(window.webpackJsonp=window.webpackJsonp||[]).push([[938],{961:function(p){p.exports={chapterTitle:null,title:"\u7b2c935\u7ae0 \u4e0e\u827e\u9ed8\u7684\u591c\u5bb4",content:"<article><h2>\u7b2c935\u7ae0 \u4e0e\u827e\u9ed8\u7684\u591c\u5bb4</h2><p>\u3000\u3000\u201c\u5f53\u4f60\u662f\u670b\u53cb\uff0c\u5c31\u4e0d\u7792\u4f60\uff0c\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u672a\u6765\u7684\u4e1a\u52a1\u53d1\u5c55\uff0c\u4e1c\u829d\u4e5f\u9887\u4e3a\u770b\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e4b\u524d\u5012\u662f\u62c5\u5fc3\u4e1c\u829d\u4e0e\u65af\u9ad8\u67cf\u5f62\u6210\u9ed8\u5951\u8054\u5408\u8d77\u6765\u7ed9\u9526\u6e56\u4ee5\u538b\u529b\uff0c\u90a3\u6837\u7684\u8bdd\uff0c\u8ba8\u4ef7\u8fd8\u4ef7\u8d77\u6765\u5c31\u76f8\u5f53\u7684\u9ebb\u70e6\uff0c\u8fd8\u8981\u638c\u63e1\u597d\u5206\u5bf8\u907f\u514d\u8fc7\u5206\u7684\u6fc0\u6012\u4e1c\u829d\u3002</p><p>\u3000\u3000\u4e1c\u829d\u6025\u4e8e\u5728\u516b\u6708\u4e2d\u65ec\u63a8\u51faiplayer\uff0c\u6240\u4ee5\u624d\u4e0d\u5f97\u4e0d\u4f9d\u8d56\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u6240\u63d0\u4f9b\u7684\u6b63\u7248\u97f3\u9891\u8d44\u6e90\uff0c\u4e1c\u829d\u4e0e\u9526\u6e56\u7684\u5408\u4f5c\u5f62\u5f0f\u662f\u6280\u672f\u4ea4\u6362\uff0c\u9526\u6e56\u53ef\u6ca1\u6709\u8d44\u683c\u62ff\u534f\u8bae\u6346\u7ed1\u4f4f\u4e1c\u829d\u4e00\u5b9a\u8981\u4f9d\u9644\u5168\u7403\u97f3\u4e50\u5728\u7ebf\uff1b\u4e1c\u829d\u5176\u5b9e\u662f\u53ef\u4ee5\u53e6\u8d77\u7089\u7076\u7684\u3002</p><p>\u3000\u3000\u65af\u9ad8\u67cf\u4e0e\u5176\u5b83\u4ece\u9526\u6e56\u83b7\u5f97\u4ea7\u54c1\u6280\u672f\u6388\u6743\u7684\u7535\u5b50\u5382\u5546\u5374\u4e0e\u9526\u6e56\u7b7e\u7f72\u4e86\u534f\u8bae\u675c\u7edd\u4e86\u53e6\u8d77\u7089\u7076\u7684\u53ef\u80fd\uff0c\u9526\u6e56\u4e5f\u56e0\u6b64\u5927\u5e45\u8c03\u4f4e\u4e86\u5bf9\u4ed6\u4eec\u7684\u6280\u672f\u6388\u6743\u8d39\u7387\u3002</p><p>\u3000\u3000\u867d\u8bf4\u9526\u6e56\u4e0e\u4e1c\u829d\u7684iplayer\u9500\u91cf\u4e3b\u8981\u5728\u4e9a\u6d32\u5e02\u573a\u5b8c\u6210\uff0c\u4f46\u662f\u5168\u7403\u97f3\u4e50\u7f51\u7ad9\u53d1\u5c55\u4ed8\u8d39\u7528\u6237\u5374\u662f\u5728\u5317\u7f8e\u5730\u533a\u6700\u4e3a\u987a\u5229\uff0c\u76ee\u524d\u4e3a\u6b64\u53ef\u4ee5\u8bf4\u662f\u65af\u9ad8\u67cf\u4e3a\u5168\u7403\u97f3\u4e50\u7f51\u7ad9\u53d1\u5c55\u4e86\u76ee\u524d\u6700\u5e9e\u5927\u7684\u4ed8\u8d39\u7528\u6237\u7fa4\u3002</p><p>\u3000\u3000\u201c\u65af\u9ad8\u67cf\u5c0f\u95e8\u5c0f\u6237\uff0c\u53ef\u4e0d\u6bd4\u4e1c\u829d\u679d\u7e41\u53f6\u8302\uff0c\u4f60\u5f00\u4ef7\u53ef\u8981\u66ff\u65af\u9ad8\u67cf\u60a0\u7740\u70b9\u3002\u201d\u827e\u9ed8\u8eab\u5b50\u503e\u8fc7\u6765\uff0c\u4e00\u8138\u7684\u5e02\u4fa9\uff0c\u602a\u8154\u7684\u6c49\u8bed\u8154\u8c03\u91cc\u8fd8\u5e26\u7740\u4e9b\u4eac\u97f5\u3002</p><p>\u3000\u3000\u5510\u5a67\u62bf\u5634\u7b11\u7740\uff0c\u5979\u5c31\u559c\u6b22\u8fd9\u6837\u9759\u9759\u7684\u5750\u5728\u5f20\u606a\u8eab\u8fb9\uff0c\u4e0d\u8bf4\u8bdd\uff0c\u53ea\u4e0d\u8fc7\u5f20\u606a\u4e0e\u827e\u9ed8\u8fd9\u4ed8\u6a21\u6837\u5c31\u8ddf\u83dc\u5e02\u573a\u91cc\u65a4\u65a4\u8ba1\u8f83\u7684\u83dc\u8d29\u5b50\u4e0e\u5bb6\u5ead\u4e3b\u5987\u53ef\u6ca1\u6709\u591a\u5c11\u533a\u522b\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u6307\u6cbe\u6c34\u5728\u684c\u4e0a\u5199\u4e86\u201c10\uff05\u201d\u3001\u201c2000\u201d\u4e24\u4e2a\u6570\u5b57\uff0c\u827e\u9ed8\u4e00\u770b\u4e4b\u4e0b\u5c31\u6fc0\u52a8\u7684\u7ad9\u8d77\u6765\uff0c\u989d\u5934\u7684\u9752\u7b4b\u8df3\u52a8\u7740\uff0c\u597d\u50cf\u597d\u4e0d\u5bb9\u6613\u624d\u538b\u4f4f\u5fc3\u5934\u6012\u706b\u4f3c\u7684\u538b\u4f4e\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u6572\u7af9\u7aff\u513f\uff0c\u5728\u5317\u7f8e\u5730\u533a\u91cd\u65b0\u5f00\u53d1\u8fd9\u4e48\u4e00\u5bb6\u7f51\u7ad9\u90fd\u4e0d\u9700\u8981\u4e24\u5343\u4e07\u7f8e\u5143\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u7684\u666e\u901a\u8bdd\u8ddf\u8c01\u5b66\u7684\uff0c\u6572\u7af9\u6760\uff0c\u4e0d\u662f\u6572\u7af9\u7aff\u513f\uff0c\u8bf4\u8fd9\u8bdd\u7684\u8bed\u6c14\u8981\u91cd\u4e00\u4e9b\uff0c\u4e0d\u80fd\u5e26\u513f\u5316\u97f3\u3002\u201d\u5f20\u606a\u772f\u7740\u773c\u7b11\u76c8\u76c8\u7684\u770b\u7740\u827e\u9ed8\uff0c\u5510\u5a67\u542c\u4e86\u5dee\u70b9\u8981\u7b11\u51fa\u58f0\uff0c\u827e\u9ed8\u8fd9\u79cd\u865a\u5f20\u58f0\u52bf\u7684\u8c08\u5224\u6280\u672f\uff0c\u5979\u4e4b\u524d\u5c31\u89c1\u8bc6\u5230\u8fc7\uff0c\u4e0d\u7136\u8fd8\u771f\u8981\u7ed9\u4ed6\u552c\u4f4f\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ac\u8d77\u624b\u5230\u534a\u7a7a\u4e2d\u5f80\u4e0b\u538b\u4e86\u538b\uff1a\u201c\u5750\u4e0b\u8bf4\u8bdd\uff0c\u65af\u9ad8\u67cf\u638f\u94b1\u51fa\u6765\u6ce8\u5165\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u83b7\u5f9710\uff05\u7684\u80a1\u6743\uff0c\u800c\u4e0d\u662f\u9526\u6e56\u8981\u5c06\u5168\u7403\u97f3\u4e50\u5728\u7ebf10\uff05\u7684\u80a1\u6743\u4ee52000\u4e07\u7684\u4ef7\u683c\u51fa\u552e\u7ed9\u65af\u9ad8\u67cf\uff0c\u8fd9\u91cc\u9762\u533a\u522b\u662f\u5f88\u5927\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u662f\u6572\u7af9\u6760\u3002\u201d\u827e\u9ed8\u8fd9\u4e0b\u5b50\u5728\u201c\u6760\u201d\u4e0a\u54ac\u91cd\u4e86\u97f3\uff0c\u201c\u6789\u6211\u5728\u8463\u4e8b\u4f1a\u9762\u524d\u6253\u4e86\u5305\u7968\uff0c\u8fd8\u8bf4\u4ee5\u606a\u5c11\u4f60\u7684\u4eba\u54c1\u4e00\u5b9a\u4f1a\u7ed9\u6211\u4eec\u4e00\u4e2a\u516c\u9053\u7684\u4ef7\u683c\u3002\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u5c06iplayer\u6280\u672f\u6388\u6743\u65af\u9ad8\u67cf\u4f7f\u7528\uff0c\u624d\u6536\u53d61.8\uff05\u7684\u4f4e\u8d39\u7387\uff0c\u5f53\u521d\u5c31\u662f\u8003\u8651\u5728\u5176\u4ed6\u65b9\u9762\u6709\u6240\u5f25\u8865\uff0c\u518d\u8bf4\u5f53\u524d\u4e92\u8054\u7f51\u4ea7\u4e1a\u4e0d\u6101\u5f97\u4e0d\u5230\u878d\u8d44\uff0c\u8fd9\u6bb5\u65f6\u95f4\u9891\u9891\u6709\u98ce\u6295\u673a\u6784\u8ddf\u6211\u4eec\u7684\u6d77\u5916\u90e8\u95e8\u63a5\u89e6\uff0c\u5f00\u51fa\u7684\u4ef7\u7801\u4e0d\u6bd4\u8fd9\u4e2a\u4f4e\uff0c\u201d\u5f20\u606a\u624b\u6307\u53e9\u4e86\u53e9\u684c\u5b50\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4f60\u8fd9\u6b21\u4eb2\u81ea\u5230\u9999\u6e2f\u6765\uff0c\u5927\u6982\u4e0d\u4f1a\u53ea\u4e3a\u7f51\u7ad9\u7684\u4e8b\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u542c\u5f20\u606a\u8fd9\u4e48\u4e00\u8bf4\uff0c\u827e\u9ed8\u5c31\u5b89\u9759\u4e0b\u6765\u4e86\uff0c\u7741\u5927\u773c\u775b\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u660e\u767d\u6211\u7684\u610f\u601d\u7684\uff0c\u603b\u4e0d\u4f1a\u8ba9\u4f60\u5728\u65af\u9ad8\u67cf\u8463\u4e8b\u4f1a\u9762\u524d\u4e22\u4e86\u8138\u9762\uff0c\u518d\u8bf4\u65af\u9ad8\u67cf\u4e0e\u9526\u6e56\u5408\u4f5c\u8fd9\u4e9b\u5e74\uff0c\u6709\u66fe\u5403\u8fc7\u4e8f\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u827e\u9ed8\u604d\u7136\u5927\u609f\uff0c\u5934\u51d1\u8fc7\u6765\u6324\u7709\u5f04\u773c\uff0c\u5c0f\u58f0\u7684\u8bf4\uff0c\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u4e86\uff0c\u603b\u4e4b\u8fd9\u4e00\u5200\u8981\u780d\u5728\u4e1c\u6d0b\u9b3c\u5b50\u8eab\u4e0a\uff0c\u4e0d\u8fc7\u4f60\u786e\u5b9a\u80fd\u8ba9\u6211\u4ea4\u5f97\u4e86\u5dee\uff1f\u201d</p><p>\u3000\u3000\u827e\u9ed8\u7684\u8868\u60c5\u771f\u662f\u5938\u5f20\uff0c\u8981\u4e0d\u662f\u5510\u5a67\u4eb2\u6635\u7684\u5750\u5728\u8eab\u8fb9\uff0c\u8ba9\u65c1\u8fb9\u4eba\u770b\u4e86\u8fd8\u771f\u4ee5\u4e3a\u4e24\u4e2a\u5927\u7537\u4eba\u6709\u4ec0\u4e48\u9690\u60c5\u5462\u3002</p><p>\u3000\u3000\u53ea\u8981\u65af\u9ad8\u67cf\u5728\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7684\u6743\u76ca\u4e0a\u63a5\u53d7\u4e86\u9526\u6e56\u7684\u62a5\u4ef7\uff0c\u7559\u7ed9\u4e1c\u829d\u7684\u8c08\u5224\u4f59\u5730\u5c31\u6781\u6709\u9650\u4e86\u3002</p><p>\u3000\u3000\u5728\u5e9e\u7136\u5927\u7269\u4e1c\u829d\u9762\u524d\uff0c\u5f20\u606a\u5b81\u53ef\u9009\u62e9\u4e0e\u89c4\u6a21\u6bd4\u9526\u6e56\u8fd8\u7565\u5c0f\u7684\u65af\u9ad8\u67cf\u7ad9\u5728\u540c\u4e00\u6761\u6218\u7ebf\u4e0a\uff0c\u4ed6\u5fc3\u91cc\u60f3\uff1a\u65af\u9ad8\u67cf\u7684\u8463\u4e8b\u4f1a\u6210\u5458\u8981\u662f\u6ca1\u6709\u6655\u5934\u7684\u8bdd\uff0c\u4e5f\u5e94\u8be5\u662f\u8fd9\u4e48\u8003\u8651\u7684\u5427\uff1f\u4ed6\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u5bf9\u6211\u6709\u4fe1\u5fc3\u554a\u3002\u201d</p><p>\u3000\u3000\u827e\u9ed8\u679c\u7136\u653e\u4e0b\u8fd9\u4e2a\u8bdd\u9898\u4e0d\u8bae\uff0c\u53ea\u8981\u5728\u5176\u4ed6\u65b9\u9762\u80fd\u83b7\u5f97\u8865\u507f\uff0c\u9526\u6e56\u7684\u5f00\u4ef7\u4e5f\u4e0d\u662f\u4e0d\u80fd\u63a5\u53d7\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8d34\u724c\u4ee3\u9500\u95ea\u5b58\u76d8\uff0c\u867d\u8bf4\u9500\u91cf\u4e0d\u5c3d\u5982\u4eba\u610f\uff0c\u4f46\u662f\u95ea\u5b58\u76d8\u7684\u6280\u672f\u5bf9\u6211\u4eec\u8fd8\u662f\u6709\u6240\u4fc3\u52a8\u7684\uff0c\u8fd9\u4e5f\u662f\u6211\u6b64\u884c\u7684\u76ee\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u95ea\u5b58\u76d8\u516d\u6708\u521d\u5c31\u5728\u4fc4\u7f57\u65af\u7684\u7535\u5b50\u5c55\u4e0a\u6b63\u5f0f\u4eae\u76f8\uff0c\u9526\u6e56\u4e03\u6708\u5c31\u5c06\u4ea7\u54c1\u63a8\u5411\u5e02\u573a\uff0c\u4e3b\u8981\u4e5f\u662f\u65e5\u97e9\u4e0e\u65b0\u52a0\u5761\u3001\u9999\u6e2f\u7b49\u4e9a\u6d32\u7ecf\u6d4e\u53d1\u8fbe\u5730\u533a\uff0c\u95ea\u5b58\u76d8\u771f\u6b63\u7684\u53d7\u5230\u4e1a\u5185\u7684\u91cd\u89c6\uff0c\u8fd8\u662f\u4e1c\u829d\u5728\u516b\u6708\u4e0b\u65ec\u63a8\u51fa\u540c\u7c7b\u578b\u7684\u95ea\u5b58\u76d8\u4ea7\u54c1\u4e4b\u540e\u2014\u2014\u4e1c\u829d\u5728\u5168\u7403\u6d88\u8d39\u7c7b\u7535\u5b50\u5e02\u573a\u7684\u5f71\u54cd\u529b\u662f\u9526\u6e56\u8fde\u63d0\u978b\u90fd\u6ca1\u6709\u8d44\u683c\u7684\u3002</p><p>\u3000\u3000\u4e1c\u829d\u4f5c\u4e3a\u5168\u7403\u7535\u5b50\u4e1a\u5de8\u5934\u4e4b\u4e00\uff0c\u65e9\u5c31\u5efa\u7acb\u8d77\u4e86\u5168\u7403\u54c1\u724c\uff0c\u4e5d\u516b\u5e74\u4e1c\u829d\u7684\u54c1\u724c\u4ef7\u503c\u5c31\u8d85\u8fc7\u4e24\u767e\u4ebf\u7f8e\u5143\uff0c\u53ef\u4ee5\u80af\u5b9a\u7684\u8bf4\uff0c\u9526\u6e56\u4e5d\u6708\u3001\u5341\u6708\u6d77\u5916\u4e1a\u52a1\u7684\u5f3a\u52b2\u589e\u6da8\u4e3b\u8981\u662f\u5f97\u76ca\u4e8e\u4e1c\u829d\u7684\u63d0\u643a\u6027\u8d28\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u76f8\u6bd4\u8f83\u8d77\u5e26\u5373\u65f6\u50a8\u5b58\u529f\u80fd\u7684iplayer\uff0c\u95ea\u5b58\u76d8\u7684\u7ed3\u6784\u5219\u8981\u7b80\u5355\u5f97\u591a\uff0c\u9664\u4e86\u51e0\u9879\u8f6f\u4ef6\u6280\u672f\u4e4b\u5916\uff0c\u63a7\u5236\u82af\u7247\u4ee5\u53ca\u8f6c\u63a5\u53e3\u7684\u786c\u4ef6\u6280\u672f\u90fd\u5728iplayer\u4e0a\u5f97\u5230\u4f53\u73b0\uff0c\u751a\u81f3\u8fd8\u8981\u7b80\u5355\uff0c\u4f46\u662f\u6280\u672f\u8bb8\u53ef\u4e5f\u662f\u4e00\u9879\u6280\u672f\u6d3b\uff0c\u4e0d\u662f\u610f\u5473\u7740\u83b7\u5f97iplayer\u4ea7\u54c1\u6280\u672f\u7684\u6388\u6743\uff0c\u5c31\u53ef\u4ee5\u5c06iplayer\u7684\u786c\u4ef6\u6280\u672f\u8f6c\u63a5\u8fc7\u6765\u751f\u4ea7\u95ea\u5b58\u76d8\uff0c\u6280\u672f\u6388\u6743\u7684\u95ee\u9898\u8fd8\u5f97\u91cd\u65b0\u8c08\u5224\u3002</p><p>\u3000\u3000\u65af\u9ad8\u67cf\u4e4b\u524d\u4e5f\u662f\u76f4\u62c9\u62ff\u9526\u6e56\u751f\u4ea7\u7684\u95ea\u5b58\u76d8\u8d34\u65af\u9ad8\u67cf\u7684\u54c1\u724c\u5728\u5317\u7f8e\u5730\u533a\u8bd5\u9500\uff0c\u8fd9\u79cd\u8d34\u724c\u9500\u552e\u53ea\u9002\u5408\u5c0f\u89c4\u6a21\u7684\u5e02\u573a\uff0c\u5e02\u573a\u5bb9\u91cf\u653e\u5927\u4e4b\u540e\uff0c\u8fd8\u662f\u76f4\u63a5\u62ff\u5230\u4ea7\u54c1\u6280\u672f\u6388\u6743\u80fd\u628a\u63e1\u66f4\u5927\u7684\u5e02\u573a\u4e3b\u52a8\u6743\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u6211\u7b54\u5e94\u4f60\uff0c\u5177\u4f53\u7684\u4f60\u627e\u9648\u4fe1\u751f\u8c08\uff0c\u201d\u5f20\u606a\u723d\u5229\u7684\u7b54\u5e94\u4e0b\u6765\uff0c\u201c\u6211\u8fd8\u5e26\u4e86\u4ef6\u793c\u7269\u7ed9\u4f60\u3002\u201d\u4ece\u8863\u515c\u91cc\u638f\u51fa\u4e00\u679aiplayer\u653e\u5230\u684c\u4e0a\u3002</p><p>\u3000\u3000\u201c\u6211\u9700\u8981\u4f60\u9001\u8fd9\u4e2a\u7ed9\u6211\uff1f\u201d\u827e\u9ed8\u7591\u60d1\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u8111\u5b50\u91cc\u7a81\u7136\u95ea\u8fc7\u4e00\u5ff5\uff0c\u610f\u8bc6\u5230\u5f20\u606a\u8981\u9001\u4ec0\u4e48\u4e1c\u897f\u7ed9\u4ed6\uff0c\u4f38\u624b\u5c06iplayer\u62ff\u5230\u624b\u91cc\u770b\u4e86\u4e24\u773c\uff0c\u4ece\u8863\u515c\u91cc\u638f\u51fa\u70df\u76d2\u5927\u5c0f\u7684\u5de5\u5177\u76d2\uff0c\u53d6\u51fa\u4e00\u6bd4\u94c1\u9489\u8fd8\u7565\u5c0f\u7684\u87ba\u4e1d\u5200\u5728\u9910\u5385\u91cc\u5c31\u5f53\u4f17\u80a2\u89e3\u8d77iplayer\u6765\u3002</p><p>\u3000\u3000\u5728\u4e07\u4e3d\u6d77\u666f\u9152\u5e97\u9876\u591a\u89c2\u5149\u9910\u5385\u7528\u9910\u7684\u5ba2\u4eba\u5927\u591a\u662f\u660e\u5929\u8981\u53c2\u52a0\u7535\u5b50\u5c55\u7684\u5ba2\u6237\uff0c\u827e\u9ed8\u7684\u884c\u4e3a\u5728\u4ed6\u4eec\u773c\u91cc\u8fd8\u4e0d\u7b97\u602a\u5f02\u3002\u827e\u9ed8\u5c06iplayer\u80a2\u89e3\u5f00\u6765\uff0c\u53d6\u51fa\u7535\u8def\u677f\u8fa9\u8bc6\u4e0a\u9762\u7684\u82af\u7247\u6807\u8bc6\uff0c\u8be7\u5f02\u4e07\u5206\u7684\u76ef\u7740\u5f20\u606a\uff1a\u201c\u6676\u5706\u5382\u53ea\u7528\u4e86\u4e0d\u5230\u4e00\u5e74\u7684\u65f6\u95f4\u5c31\u8ba9\u4f60\u4eec\u5efa\u6210\u4e86\uff1f\u8fd9\u4e2a\u8bb0\u5f55\uff0c\u53ef\u53ea\u6709\u82f1\u7279\u5c14\u4e0e\u5fb7\u4eea\u521b\u9020\u8fc7\u554a\uff0c\u96be\u602a\u4e0a\u5348\u6211\u9047\u5230\u5fb7\u4eea\u7684\u5fb7\u5c14\u6cd5\u897f\uff0c\u4ed6\u8138\u4e0a\u7684\u795e\u8272\u5f88\u602a\u5f02\u2026\u2026\u201d</p><p>\u3000\u3000\u5fb7\u4eea\u4e5f\u662f\u4e2d\u6676\u5fae\u82af\u7247\u7684\u4e3b\u8981\u80a1\u4e1c\u4e4b\u4e00\uff0c\u8eab\u4e3a\u5fb7\u4eea\u7684\u9ad8\u7ea7\u526f\u603b\u88c1\uff0c\u5fb7\u5c14\u6cd5\u897f\u5f53\u7136\u65e9\u5c31\u77e5\u9053\u4e2d\u6676\u5fae\u82af\u7247\u987a\u5229\u6295\u4ea7\u800c\u4e14\u9526\u6e56\u6210\u529f\u6d88\u5316\u4e86\u95ea\u5b58\u6280\u672f\u7684\u6d88\u606f\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u6295\u4ea7\u8fd8\u662f\u5176\u6b21\uff0c\u5173\u952e\u662f\u9526\u6e56\u6210\u529f\u6d88\u5316\u4e86\u95ea\u5b58\u6280\u672f\uff0c\u610f\u5473\u7740\u5728iplayer\u3001\u95ea\u5b58\u76d8\u751f\u4ea7\u6210\u672c\u4e2d\u5360\u4e86\u4e00\u534a\u8fd8\u591a\u7684\u95ea\u5b58\u82af\u7247\u4ef7\u683c\u5b58\u5728\u5927\u5e45\u4e0b\u964d\u7684\u53ef\u80fd\u3002\u7279\u522b\u662f\u95ea\u5b58\u76d8\uff0c\u95ea\u5b58\u76d8\u7ed3\u6784\u7b80\u5355\uff0c\u95ea\u5b58\u4e0e\u63a7\u5236\u82af\u7247\u65e2\u662f\u6838\u5fc3\u5143\u4ef6\uff0c\u4e5f\u662f\u4e3b\u8981\u5143\u4ef6\uff0c\u76f8\u6bd4\u8f83iplayer\uff0c\u53d6\u6d88\u4e86\u5fae\u5904\u7406\u5668\u4e0e\u97f3\u9891\u89e3\u7801\u5668\u82af\u7247\uff0c\u4e5f\u6ca1\u6709\u6db2\u6676\u5c4f\u3001\u5145\u653e\u7535\u4ee5\u53ca\u9502\u7535\u6c60\u7b49\u914d\u4ef6\uff0c\u76ee\u524d\u7684iplayer\u8981\u505a\u5230\u638c\u5fc3\u5927\u5c0f\uff0c\u800c\u95ea\u5b58\u76d8\u53ea\u6709\u534a\u622a\u666e\u901a\u7684\u94a2\u7b14\u5927\u5c0f\uff0c\u95ea\u5b58\u82af\u7247\u4ef7\u683c\u8981\u5360\u4e86\u95ea\u5b58\u76d8\u751f\u4ea7\u6210\u672c\u7684\u516b\u6210\u4ee5\u4e0a\u3002</p><p>\u3000\u3000\u827e\u9ed8\u53c8\u795e\u79d8\u516e\u516e\u7684\u5934\u51d1\u8fc7\u6765\u95ee\u5f20\u606a\u4e00\u53e5\uff1a\u201c\u9526\u6e56\u4f1a\u4e0d\u4f1a\u4e0e\u4e1c\u829d\u3001\u4e09\u661f\u8ba2\u7acb\u4ef7\u683c\u540c\u76df\uff1f\u201d</p><p>\u3000\u3000\u827e\u9ed8\u5f53\u7136\u4e0d\u4f1a\u60f3\u4e0d\u8d77\u6765\u65af\u9ad8\u67cf\u3001\u7231\u8fbe\u8fd8\u6709\u5fb7\u4eea\u4e09\u5bb6\u4f01\u4e1a\u8fd8\u4e00\u76f4\u5728\u5206\u4eab\u4e86\u8d85\u7ea7VCD\u3001VCD\u789f\u673a\u4e13\u7528\u89e3\u7801\u82af\u7247\u7684\u5784\u65ad\u5e02\u573a\uff0c\u867d\u8bf4\u4e5d\u516b\u5e74\u65b0\u5174\u56fd\u5bb6\u4e0e\u5730\u533a\u7684\u789f\u673a\u5e02\u573a\u589e\u6da8\u7f13\u6162\uff0c\u4ed6\u4eec\u53c8\u4e3b\u52a8\u964d\u4f4e\u89e3\u7801\u82af\u7247\u7684\u552e\u4ef7\uff0c\u7ed9\u789f\u673a\u5382\u5546\u66f4\u591a\u7684\u751f\u5b58\u7a7a\u95f4\uff0c\u5373\u4f7f\u5982\u6b64\uff0c\u65af\u9ad8\u67cf\u4eca\u5e74\u4ece\u89e3\u7801\u82af\u7247\u4e0a\u83b7\u5f97\u7684\u6536\u76ca\u4e5f\u5c06\u8d85\u8fc7\u4e00\u4ebf\u516d\u5343\u4e07\u7f8e\u5143\uff0c\u9526\u6e56\u7684\u6536\u76ca\u4e5f\u4e0d\u4f1a\u4f4e\u4e8e\u516b\u5343\u4e07\u7f8e\u5143\u3002</p><p>\u3000\u3000\u8d85\u7ea7VCD\u7684\u4ea7\u54c1\u6280\u672f\u4e13\u5229\u76ee\u524d\u7531\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u3001\u7231\u8fbe\u4e09\u5bb6\u516c\u53f8\u5171\u540c\u62e5\u6709\uff0c\u751a\u81f3\u4e0d\u7528\u62c5\u5fc3\u6709\u522b\u7684\u5382\u5546\u4f1a\u5f00\u53d1\u66ff\u4ee3\u89e3\u7801\u82af\u7247\uff0c\u552f\u4e00\u8981\u8003\u8651\u7684\u662f\u6765\u81eaDVD\u789f\u673a\u7684\u7ade\u4e89\u3002\u5f53\u524dDVD\u9635\u8425\u7684\u5382\u5546\u8981\u6025\u4e8e\u6536\u56de\u524d\u671f\u7684\u5de8\u989d\u7814\u53d1\u6295\u5165\uff0c\u52a0\u4e0a\u76f8\u4e92\u95f4\u9ad8\u6602\u7684\u6280\u672f\u6388\u6743\u8d39\u7528\uff0cDVD\u789f\u673a\u5728\u5317\u7f8e\u5730\u533a\u7684\u552e\u4ef7\u662f\u8d85\u7ea7VCD\u7684\u4e09\u500d\uff0c\u89e3\u7801\u82af\u7247\u4e0a\u7684\u8d85\u989d\u5784\u65ad\u5229\u6da6\uff0c\u4e09\u5bb6\u516c\u53f8\u8fd8\u80fd\u4eab\u7528\u597d\u4e00\u9635\u5b50\u65f6\u95f4\u3002</p><p>\u3000\u3000\u6240\u5e78\u8d85\u7ea7VCD\u7684\u4e3b\u8981\u5e02\u573a\u662f\u5728\u65b0\u5174\u56fd\u5bb6\u4e0e\u5730\u533a\uff0c\u82e5\u662f\u5728\u5317\u7f8e\u6216\u6b27\u6d32\u5730\u533a\u9500\u552e\uff0c\u4e09\u5bb6\u4f01\u4e1a\u65e0\u8bba\u662f\u5c06\u6280\u672f\u7f6e\u5165\u540c\u4e00\u5bb6\u5408\u8d44\u516c\u53f8\u638c\u63e1\u8fd8\u662f\u5206\u6563\u5728\u4e09\u5bb6\u516c\u53f8\u8ba2\u7acb\u4ef7\u683c\u540c\u76df\u7684\u5784\u65ad\u884c\u4e3a\u90fd\u662f\u8981\u53d7\u5230\u91cd\u7f5a\u7684\u3002</p><p>\u3000\u3000\u9526\u6e56\u53ef\u4e0d\u6562\u5728\u6b27\u6d32\u6216\u5317\u7f8e\u5e02\u573a\u5192\u8fd9\u4e2a\u9669\uff0c\u4e8b\u60c5\u8d25\u9732\u4e4b\u540e\uff0c\u4e1c\u829d\u3001\u4e09\u661f\u53ef\u80fd\u4ea4\u51fa\u7f5a\u91d1\u4e86\u4e8b\uff0c\u9526\u6e56\u5219\u53ef\u80fd\u7ed9\u5b8c\u5168\u9a71\u51fa\u6b27\u6d32\u4e0e\u5317\u7f8e\u5e02\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u4f1a\u3002\u201d</p><p>\u3000\u3000\u827e\u9ed8\u8fd8\u771f\u5c31\u6015\u5f20\u606a\u70b9\u5934\u8bf4\u201c\u4f1a\u201d\uff0c\u5f20\u606a\u70b9\u5934\u8bf4\u4f1a\uff0c\u6307\u4e0d\u5b9a\u65af\u9ad8\u67cf\u5c06\u662f\u7b2c\u4e00\u5bb6\u63d0\u51fa\u7533\u8bc9\u7684\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u201c\u90a3\u770b\u6765\u6211\u4eec\u7684\u8ba2\u5355\u8981\u4ece\u4e1c\u829d\u8f6c\u4ea4\u5230\u4f60\u4eec\u624b\u91cc\u4e86\uff0c\u201d\u827e\u9ed8\u5c06\u7535\u8def\u677f\u4e3e\u5230\u773c\u76ae\u5b50\u5e95\u4e0b\u53c8\u7ec6\u770b\u7247\u523b\uff0c\u201c\u53ea\u662f32M\u7684\u50a8\u5b58\u82af\u7247\u5417\uff1f\u201d</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u6b64\u65f6\u8fd8\u53ea\u62e5\u6709\u5341\u4e8c\u82f1\u5bf8\u76840.35\u5fae\u7c73\u6676\u5706\u5236\u9020\u5de5\u827a\uff0c\u6700\u9ad8\u53ea\u80fd\u52a0\u5de5\u4e0e32M\u95ea\u5b58\u9897\u7c92\u7b49\u7ea7\u7684\u96c6\u6210\u7535\u8def\uff0c\u827e\u9ed8\u5012\u4e0d\u662f\u4e0d\u6e05\u695a\u8fd9\u4e00\u70b9\uff0c\u4f46\u662f\u5bf9iplayer\u6765\u8bf4\uff0c32M\u7684\u50a8\u5b58\u5bb9\u91cf\u592a\u4f4e\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u996d\u8981\u4e00\u53e3\u4e00\u53e3\u7684\u5403\uff0c\u6211\u4e5f\u5f88\u6e05\u695a\u51fa\uff0c\u80fd\u5c06\u552e\u4ef7\u63a7\u5236\u5728400\u7f8e\u5143\u4ee5\u5185\uff0c\u5185\u7f6e\u4e24\u679a64M\u95ea\u5b58\u82af\u7247\u7684iplayer\u5728\u5317\u7f8e\u5730\u533a\u5c06\u66f4\u53d7\u6b22\u8fce\u2014\u2014\u5982\u679c\u4e0d\u8003\u8651\u5916\u5f62\u7684\u8bdd\uff0c\u65af\u9ad8\u67cf\u5012\u662f\u53ef\u4ee5\u8003\u8651\u5728iplayer\u5185\u7f6e4\u679a32M\u7684\u95ea\u5b58\u82af\u7247\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u54ea\u6709\u4f60\u60f3\u8c61\u7684\u90a3\u4e48\u597d\u7cca\u5f04\uff0c\u201d\u827e\u9ed8\u563f\u563f\u7684\u7b11\u4e86\u4e24\u58f0\uff0c\u201c\u4e5f\u4e0d\u60f3\u60f3\u6211\u8ddf\u4f60\u4eec\u6253\u4e86\u591a\u5c11\u5e74\u7684\u4ea4\u9053\uff1f\u201d</p><p>\u3000\u3000\u9526\u6e56\u62ff\u5fb7\u4eea\u7684\u4e8c\u6d41\u624b\u673a\u6280\u672f\u5728\u56fd\u5185\u624b\u673a\u5e02\u573a\u83b7\u5f97\u5f53\u524d\u7684\u6210\u529f\uff0c\u8bf4\u5230\u5e95\u8fd8\u662f\u5f02\u5e38\u91cd\u89c6\u4ea7\u54c1\u4e0e\u5de5\u827a\u8bbe\u8ba1\u7684\u7f18\u6545\uff0c\u5176\u6280\u672f\u7814\u53d1\u4ee5\u6a61\u6811\u56ed\u4e0eESS\u5b9e\u9a8c\u57fa\u5730\u4e3a\u4e3b\uff0c\u4f46\u662f\u5728\u5317\u4eac\u3001\u9999\u6e2f\u3001\u4e1c\u4eac\u3001\u6c49\u57ce\u3001\u65b0\u52a0\u5761\u90fd\u8bbe\u6709\u4ea7\u54c1\u8bbe\u8ba1\u4e0e\u5f00\u53d1\u90e8\u95e8\uff0c\u9526\u6e56\u4e0b\u534a\u5e74\u51b3\u5fc3\u5f00\u62d3\u6b27\u6d32\u4e0e\u5317\u7f8e\u5730\u533a\u7684\u5e02\u573a\uff0c\u4f46\u662f\u5728\u6b27\u6d32\u4e0e\u5317\u7f8e\u5730\u533a\u6700\u5148\u6210\u7acb\u7684\u8fd8\u662f\u4ea7\u54c1\u8bbe\u8ba1\u4e0e\u5f00\u53d1\u90e8\u95e8\uff0c\u53cd\u800c\u5b9e\u8d28\u6027\u7684\u5e02\u573a\u5f00\u62d3\u5de5\u4f5c\u5374\u8fdf\u8fdf\u6ca1\u6709\u5c55\u5f00\u2014\u2014\u8fd9\u4e9b\u60c5\u51b5\uff0c\u827e\u9ed8\u90fd\u662f\u4e00\u6e05\u4e8c\u695a\u7684\u3002</p><p>\u3000\u3000\u867d\u8bf4\u8ba9\u827e\u9ed8\u8bc6\u7834\uff0c\u5f20\u606a\u8138\u4e5f\u4e0d\u7ea2\uff0c\u4ed6\u4e5f\u6ca1\u6709\u6307\u671b\u65af\u9ad8\u67cf\u771f\u5c31\u8fd9\u4e48\u505a\u3002</p><p>\u3000\u3000\u201c\u5373\u4f7f\u5982\u6b64\uff0c\u4e5f\u8ba9\u4eba\u5174\u594b\u554a\uff0c\u201d\u827e\u9ed8\u53c8\u8bf4\u9053\uff0c\u201c\u7b49\u7535\u5b50\u5c55\u8fc7\u53bb\uff0c\u6211\u5c31\u968f\u4f60\u4eec\u76f4\u63a5\u53bb\u5efa\u90ba\uff0c\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u867d\u8bf4\u4e2d\u6676\u5fae\u82af\u7684\u8bbe\u8ba1\u4ea7\u80fd\u662f\u6708\u4ea7\u4e09\u4e07\u7247\u6676\u5706\uff0c\u521d\u6b65\u8bd5\u4ea7\u6210\u529f\uff0c\u826f\u54c1\u7387\u5f88\u4f4e\uff0c\u8981\u8fbe\u5230\u7a33\u5b9a\u7684\u4ea7\u80fd\uff0c\u4e5f\u9700\u8981\u4e09\u4e94\u4e2a\u6708\u7684\u8fc7\u6e21\u671f\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u8fc7\u4e09\u4e94\u4e2a\u6708\u518d\u6765\u4e0b\u8ba2\u5355\u4e0d\u8fdf\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u827e\u9ed8\u771f\u662f\u8981\u54ac\u7259\u5207\u9f7f\u4e86\uff0c\u4ed6\u8f6c\u5ff5\u5c31\u60f3\u660e\u767d\u5f20\u606a\u5728\u6253\u4ec0\u4e48\u4e3b\u610f\u3002</p><p>\u3000\u3000\u5c31\u7b97\u4e2d\u6676\u5fae\u82af\u521a\u6295\u4ea7\u7684\u826f\u54c1\u7387\u4f4e\u5230\u53ea\u670920\uff05\uff0c\u6708\u4ea7\u4e09\u4e94\u5343\u7247\u6676\u5706\u603b\u662f\u80fd\u505a\u5230\u7684\uff0c\u76f4\u5f84\u4e3a\u5341\u4e8c\u82f1\u5bf8\u7684\u6bcf\u7247\u6676\u5706\u5dee\u4e0d\u591a\u80fd\u591f\u751f\u4ea7\u8fd1\u56db\u767e\u679a\u95ea\u5b58\u9897\u7c92\u2014\u2014\u8fd9\u79cd\u7b80\u5355\u7684\u5e38\u8bc6\uff0c\u4ed6\u8eab\u4e3a\u65af\u9ad8\u67cf\u7684\u6267\u884c\u603b\u88c1\u8fd8\u662f\u5177\u5907\u7684\uff0c\u6b64\u65f6\u53ea\u8981\u4e2d\u6676\u5fae\u82af\u62ff\u8981\u5341\u5206\u4e4b\u4e00\u7684\u751f\u4ea7\u80fd\u529b\u6765\uff0c\u5c31\u80fd\u6ee1\u8db3\u6b64\u65f6\u7684\u5168\u7403\u5e02\u573a\u5bf932M\u95ea\u5b58\u82af\u7247\u7684\u9700\u6c42\u3002</p><p>\u3000\u3000\u4ed6\u660e\u767d\u5f20\u606a\u8981\u62d6\u4e0a\u4e09\u4e94\u4e2a\u6708\u81ea\u7136\u4e0d\u662f\u60f3\u5728\u8fd9\u4e2a\u671f\u95f4\u4e0d\u964d\u4ef7\u518d\u72e0\u72e0\u7684\u635e\u4e00\u7b14\u2014\u2014\u5373\u4f7f\u518d\u72e0\uff0c\u6bcf\u4ef6iplayer\u80fd\u589e\u52a080\u7f8e\u5143\u4e0a\u4ee5\u7684\u5229\u6da6\u7a7a\u95f4\uff0c\u4ee5\u9526\u6e56\u6b64\u65f6\u76844\u4e07\u4ef6\u7684\u6708\u9500\u91cf\uff0c\u62d6\u4e0a\u4e09\u4e2a\u6708\u4e5f\u4e0d\u8fc7\u989d\u5916\u589e\u52a0\u4e00\u5343\u4e07\u7f8e\u5143\u7684\u4ea7\u54c1\u5229\u6da6\u7f62\u4e86\uff0c\u4f46\u662f\u9526\u6e56\u5c06\u6bcf\u4ef6iplayer\u8fd9\u989d\u5916\u591a\u51fa\u6765\u768480\u7f8e\u5143\u5229\u6da6\u7a7a\u95f4\u8ba9\u7ed9\u4ee3\u7406\u5546\u3001\u7ecf\u9500\u5546\uff0c\u5374\u8db3\u4ee5\u8ba9\u7ecf\u9500\u5546\u4f7f\u51fa\u5403\u5976\u7684\u529b\u6c14\u6765\u4e3a\u9526\u6e56\u7684\u4ea7\u54c1\u5f00\u62d3\u5e02\u573a\u3002</p><p>\u3000\u3000\u4e09\u4e94\u4e2a\u6708\uff0c\u5c31\u8db3\u4ee5\u8ba9\u9526\u6e56\u7684\u6d77\u5916\u4e1a\u52a1\u4e0a\u4e24\u4e2a\u53f0\u9636\uff0c\u827e\u9ed8\u7b97\u662f\u770b\u660e\u767d\u8fc7\u6765\u4e86\uff0c\u9526\u6e56\u8fd9\u6b21\u53c2\u52a0\u7535\u5b50\u5c55\u662f\u51b2\u7740\u6b27\u6d32\u3001\u5317\u7f8e\u5730\u533a\u7684\u7535\u5b50\u4ee3\u7406\u9500\u552e\u5546\u6765\u7684\u3002</p></article>"}}}]);
