(window.webpackJsonp=window.webpackJsonp||[]).push([[939],{962:function(p){p.exports={chapterTitle:null,title:"\u7b2c756\u7ae0 \u4e0d\u9700\u8981\u56de\u907f",content:"<article><h2>\u7b2c756\u7ae0 \u4e0d\u9700\u8981\u56de\u907f</h2><p>\u3000\u3000\u5f20\u606a\u5230\u65b0\u829c\u5f53\u662f\u4f11\u5047\uff0c\u6e05\u6668\u542c\u7740\u6dc5\u6ca5\u7684\u96e8\u58f0\u9192\u6765\uff0c\u4e0d\u60f3\u8d77\u5e8a\uff0c\u62ac\u5934\u770b\u4e86\u770b\u7a97\u5916\u7684\u96e8\u4e1d\uff0c\u53c8\u5c06\u5934\u57cb\u8fdb\u88ab\u5b50\u5927\u7761\uff0c\u8ff7\u8ff7\u7cca\u7cca\u4e2d\uff0c\u5510\u5a67\u5e7d\u9999\u7684\u5a07\u8eaf\u94bb\u8fdb\u88ab\u7a9d\u91cc\u6765\uff0c\u4e4c\u9ed1\u67d4\u987a\u7684\u957f\u53d1\u6563\u5806\u5728\u9f3b\u5c16\uff0c\u6709\u7740\u597d\u95fb\u7684\u53d1\u9999\u3002\u5510\u5a67\u8138\u4e0a\u7684\u7761\u610f\u672a\u6d88\uff0c\u53ea\u7ed9\u4e86\u5f20\u606a\u4e00\u4e2a\u751c\u871c\u7684\u7b11\u5bb9\uff0c\u53cc\u624b\u8d34\u7740\u8138\u988a\u4e0b\uff0c\u95ed\u4e0a\u773c\u775b\u5f88\u5feb\u5c31\u53c8\u606c\u7136\u7684\u7761\u53bb\u3002\u9664\u4e86\u96e8\u58f0\uff0c\u518d\u542c\u4e0d\u5230\u5176\u4ed6\u58f0\u97f3\uff0c\u60f3\u5fc5\u7238\u5988\u90fd\u53bb\u5355\u4f4d\uff0c\u800c\u536b\u5a76\u4e0d\u4f1a\u8fd9\u4e48\u65e9\u8fc7\u6765\u51c6\u5907\u4e2d\u996d\uff0c\u5f20\u606a\u51dd\u89c6\u7740\u5510\u5a67\u74f7\u5668\u822c\u5149\u6ed1\u7684\u5ae9\u767d\u8138\u86cb\uff0c\u66f4\u5a07\u5a9a\u52a8\u4eba\u4e86\uff0c\u51d1\u5230\u5979\u7ea2\u6da6\u7684\u5634\u5507\u8f7b\u543b\u7740\u3002\u5510\u5a67\u957f\u957f\u7684\u776b\u6bdb\u8f7b\u98a4\u7740\uff0c\u5f85\u5f20\u606a\u60f3\u5c06\u820c\u5934\u4f38\u8fdb\u5979\u7684\u5634\u91cc\uff0c\u624d\u7741\u5f00\u773c\uff0c\u5a07\u55d4\u7740\u8bf4\uff1a\u201c\u8fd8\u6ca1\u6709\u6f31\u5634\uff0c\u201d\u4f38\u624b\u6491\u7740\u5f20\u606a\u7684\u80f8\u53e3\u8981\u5c06\u4ed6\u63a8\u5f00\uff0c\u201c\u6211\u53ea\u60f3\u5728\u4f60\u6000\u91cc\u7761\u4e00\u89c9\u554a\uff0c\u4f60\u4e0d\u80fd\u8d81\u673a\u6b3a\u8d1f\u6211\u2026\u2026\u201d\u8fd9\u79cd\u4e8b\u603b\u662f\u6108\u6323\u624e\u6108\u5feb\u4e50\uff0c\u542c\u7740\u5510\u5a67\u868a\u866b\u53eb\u58f0\u4f3c\u7684\u5fae\u541f\u6297\u8bae\uff0c\u5f20\u606a\u4efb\u5979\u7684\u5a07\u8eaf\u5728\u8eab\u4e0b\u626d\u52a8\uff0c\u52aa\u529b\u5c06\u5979\u7684\u7761\u8863\u5265\u5f00\uff0c\u53ea\u662f\u4e00\u4e0d\u5c0f\u5fc3\u820c\u5934\u9012\u8fdb\u53bb\u542e\u5438\u5510\u5a67\u7684\u9999\u820c\u5374\u7ed9\u54ac\u4f4f\u3002\u5f20\u606a\u7acb\u5373\u4e3e\u8d77\u624b\u4e0d\u6562\u52a8\u5f39\uff0c\u6574\u4e2a\u8eab\u5b50\u90fd\u6c89\u6c89\u7684\u538b\u5728\u5510\u5a67\u7684\u5a07\u8eaf\u4e0a\u3002\u8fd9\u6323\u624e\u7684\u6e38\u620f\u73a9\u4e86\u7247\u523b\uff0c\u5510\u5a67\u81ea\u5df1\u5148\u627f\u53d7\u4e0d\u4f4f\uff0c\u8eab\u5b50\u762b\u8f6f\u7684\u6a2a\u9648\u5728\u6668\u5149\u4e0e\u6dc5\u6ca5\u7684\u96e8\u58f0\u4e4b\u4e2d\uff0c\u63d0\u81c0\u7fd8\u817f\u5f20\u606a\u5c06\u5979\u7684\u5185\u88e4\u8131\u6389\uff0c\u808c\u80a4\u5728\u6668\u5149\u4e0b\u6709\u7740\u6676\u83b9\u5254\u900f\u7684\u767d\u5ae9\uff0c\u6e17\u5165\u8f7b\u7ea2\u7684\u900f\u7740\u6de1\u6de1\u7684\u7ea2\u6655\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u536b\u5170\u4e0e\u7fdf\u4e39\u9752\u4e5d\u70b9\u949f\u62d0\u8fc7\u8fd9\u91cc\uff0c\u4e4b\u524d\u63a5\u5230\u7238\u7238\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u6c5f\u4e0a\u5143\u4e2d\u5348\u8981\u5230\u5bb6\u91cc\u5403\u996d\uff0c\u5f20\u606a\u4e0d\u6653\u5f97\u6c5f\u4e0a\u5143\u4e2d\u5348\u767b\u95e8\u6709\u4ec0\u4e48\u4e8b\uff0c\u4fbf\u8ba9\u5510\u5a67\u8ddf\u7740\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u51fa\u53bb\u901b\u8857\uff0c\u4ed6\u8981\u7559\u5728\u5bb6\u91cc\u7b49\u4ed6\u7238\u966a\u6c5f\u4e0a\u5143\u56de\u6765\u5403\u4e2d\u996d\u3002</p><p>\u3000\u3000\u57ce\u9632\u6c5f\u5824\u6848\u7ed9\u6345\u51fa\u4e4b\u540e\uff0c\u7f57\u5f52\u6e90\u8fd9\u9897\u65b0\u829c\u7684\u5927\u6bd2\u7624\u7ed9\u6458\u9664\uff0c\u65b0\u829c\u5e02\u59d4\u4e66\u8bb0\u6c5f\u4e0a\u5143\u4e0e\u5e02\u957f\u5218\u7235\u90fd\u8981\u8ddf\u7740\u627f\u62c5\u4e00\u5b9a\u7684\u8d23\u4efb\u3002\u6c5f\u4e0a\u5143\u4e5f\u77e5\u9053\u81ea\u5df1\u7684\u4ed5\u9014\u5f88\u53ef\u80fd\u5c31\u7ec8\u6b62\u5728\u65b0\u829c\u7684\u4efb\u4e0a\uff0c\u5176\u4ed6\u5b98\u5458\u6216\u8bb8\u4f1a\u6d88\u6c89\u653e\u7eb5\u81ea\u5df1\uff0c\u4ece\u6b64\u5815\u843d\u8150\u5316\uff0c\u6c5f\u4e0a\u5143\u5012\u662f\u8ba4\u4e3a\u53ef\u4ee5\u653e\u5f00\u624b\u811a\u4e3a\u8fd9\u5ea7\u57ce\u5e02\u505a\u4e9b\u4ec0\u4e48\uff0c\u4e0d\u5fc5\u987e\u8651\u592a\u591a\u6f5c\u89c4\u5219\u4e0a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u5e74\u591a\u6765\uff0c\u6c5f\u4e0a\u5143\u5f00\u59cb\u94c1\u8155\u6574\u987f\u65b0\u829c\u7684\u540f\u6cbb\uff0c\u5bf9\u67e5\u51fa\u95ee\u9898\u7684\u5b98\u5458\u7edd\u4e0d\u5fc3\u6148\u624b\u8f6f\uff0c\u5c31\u7b97\u6ca1\u6709\u7ed9\u67e5\u51fa\u95ee\u9898\u3001\u4f46\u662f\u8ddf\u7f57\u5f52\u6e90\u6765\u5f80\u5bc6\u5207\u3001\u6709\u7740\u8bf4\u4e0d\u6e05\u9053\u4e0d\u660e\u5173\u7cfb\u7684\u5b98\u5458\u4e5f\u4e00\u5f8b\u5148\u8e22\u5230\u89d2\u843d\u91cc\u5f85\u67e5\u3002\u6574\u987f\u6cbb\u5b89\u3001\u63a8\u52a8\u56fd\u4f01\u4f53\u5236\u6539\u9769\uff0c\u4e0d\u754f\u60e7\u5f97\u7f6a\u65e2\u5f97\u5229\u76ca\u9636\u5c42\uff0c\u8fd9\u4e00\u5e74\u591a\u7684\u65f6\u95f4\uff0c\u5012\u8ba9\u65b0\u829c\u5448\u73b0\u51fa\u6b23\u6b23\u5411\u8363\u7684\u65b0\u9762\u8c8c\u3002</p><p>\u3000\u3000\u5728\u65b0\u829c\u5e02\u91cc\u7684\u5b98\u5458\u4e2d\uff0c\u6c5f\u4e0a\u5143\u662f\u6700\u65e9\u77e5\u9053\u5f20\u606a\u8eab\u4efd\u4e0e\u9526\u6e56\u5e95\u7ec6\u7684\u4eba\u4e4b\u4e00\uff0c\u4e5f\u662f\u6700\u575a\u5b9a\u652f\u6301\u5f20\u77e5\u884c\u5728\u65b0\u829c\u5f00\u5c55\u5de5\u4f5c\u7684\u4eba\u3002\u5f20\u77e5\u884c\u5341\u6708\u4e2d\u83b7\u4efb\u5e02\u59d4\u5e38\u59d4\u3001\u5e38\u52a1\u526f\u5e02\u957f\uff0c\u9664\u4e86\u6765\u81ea\u7701\u91cc\u7684\u76f4\u63a5\u652f\u6301\u4e4b\u5916\uff0c\u65b0\u829c\u5e02\u8fd9\u91cc\u6c5f\u4e0a\u5143\u662f\u6700\u575a\u5b9a\u7684\u652f\u6301\u8005\uff1b\u76f8\u5bf9\u800c\u8a00\uff0c\u5e02\u957f\u5218\u7235\u5219\u8981\u51b7\u6de1\u591a\u4e86\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e5f\u662f\u5728\u77ed\u77ed\u4e09\u5e74\u65f6\u95f4\u91cc\u5b8c\u6210\u4ece\u5e02\u653f\u5e9c\u526f\u79d8\u4e66\u957f\u5230\u79d8\u4e66\u957f\u5230\u526f\u5e02\u957f\u5230\u5e38\u52a1\u526f\u5e02\u957f\u7684\u4e09\u7ea7\u8df3\uff0c\u5728\u4e00\u822c\u4eba\u773c\u91cc\u7b97\u662f\u5750\u7740\u706b\u7bad\u5728\u5347\u8fc1\u3002\u5f53\u7136\uff0c\u5f20\u77e5\u884c\u5728\u65b0\u829c\u4e5f\u6709\u8db3\u591f\u8000\u773c\u7684\u653f\u7ee9\uff0c\u4e91\u6c60\u9152\u4e1a\u91cd\u7ec4\u4e4b\u540e\uff0c\u4fc3\u8fdb\u65b0\u829c\u7684\u767d\u9152\u4ea7\u4e1a\u6574\u4f53\u5d1b\u8d77\uff0c\u8fd9\u4e9b\u662f\u65b0\u829c\u5730\u65b9\u4e5d\u4e03\u5e74\u8d22\u653f\u6536\u5165\u7834\u5341\u4ebf\u7684\u4e3b\u8981\u56e0\u7d20\uff1b\u53c8\u63a8\u52a8\u90ba\u829c\u9ad8\u901f\u516c\u8def\u5de5\u7a0b\u5728\u5e74\u4e2d\u4e0a\u9a6c\uff0c\u6b64\u65f6\u53c8\u6295\u5165\u5f88\u5927\u7684\u7cbe\u529b\u63a8\u52a8\u65b0\u829c\u65c5\u6e38\u4ea7\u4e1a\u7684\u6574\u4f53\u5347\u7ea7\u3002</p><p>\u3000\u3000\u542c\u7740\u6c7d\u8f66\u9a76\u8fdb\u524d\u9662\u5b50\u7684\u58f0\u97f3\uff0c\u5f20\u606a\u8d70\u51fa\u5ba2\u5385\uff0c\u7ad9\u5728\u8d70\u5eca\u524d\u7684\u53f0\u9636\u4e0a\u770b\u7740\u7238\u7238\u4e0e\u6c5f\u4e0a\u5143\u4ece\u540c\u4e00\u8f86\u8f66\u91cc\u94bb\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u662f\u5f88\u4e45\u6ca1\u6709\u89c1\u5230\u6c5f\u4e0a\u5143\u4e86\uff0c\u8fd8\u662f\u5e74\u521d\u5728\u65b0\u829c\u65f6\u89c1\u8fc7\u9762\uff0c\u4e00\u6bb5\u65f6\u95f4\u672a\u89c1\uff0c\u6c5f\u4e0a\u5143\u7684\u4e24\u9b13\u4e4b\u95f4\u5012\u6742\u7740\u4e9b\u7070\u767d\u5934\u53d1\u3002\u6c5f\u4e0a\u5143\u770b\u7740\u7ad9\u5728\u53f0\u9636\u4e0a\u8fce\u63a5\u7684\u5f20\u606a\uff0c\u6ca1\u6709\u523b\u610f\u88c5\u51fa\u6765\u7684\u7ea8\u7ed4\u6c14\uff0c\u5112\u96c5\u800c\u95f2\u9038\uff0c\u5fc3\u60f3\uff1a\u8c01\u80fd\u60f3\u5230\u8fd9\u4e48\u4e2a\u9752\u5e74\u8981\u4ed6\u8fd9\u4e2a\u5e02\u59d4\u4e66\u8bb0\u4eb2\u81ea\u767b\u95e8\u62dc\u8bbf\uff1f\u7b11\u7740\u8ddf\u5f20\u77e5\u884c\u8bf4\u9053\uff1a\u201c\u6709\u8fd9\u6837\u51fa\u8272\u7684\u513f\u5b50\uff0c\u4f60\u8fd9\u505a\u8001\u5b50\u7684\u4f1a\u4e0d\u4f1a\u611f\u5230\u5f88\u5927\u7684\u538b\u529b\uff1f\u201d</p><p>\u3000\u3000\u201c\u603b\u4e4b\u4eab\u53d7\u4e0d\u5230\u4e00\u822c\u4e3a\u4eba\u7236\u6bcd\u7684\u5174\u8da3\u2026\u2026\u201d\u5f20\u77e5\u884c\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u4e0d\u5927\u8bf4\u8bdd\uff0c\u4e3b\u52a8\u8bf4\u4ec0\u4e48\u90fd\u4e0d\u5927\u5408\u9002\uff0c\u62db\u547c\u4e86\u6c5f\u4e0a\u5143\u4e00\u58f0\uff0c\u5c31\u966a\u7740\u8fdb\u5ba2\u5385\u5148\u5750\u4e00\u4f1a\u513f\u518d\u8fdb\u9910\u5385\u7528\u9910\u3002</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u5728\u5e02\u91cc\u4e0b\u4e86\u7981\u9152\u4ee4\uff0c\u4e2d\u5348\u5c31\u6ca1\u6709\u559d\u9152\u3002\u6c5f\u4e0a\u5143\u4eb2\u81ea\u767b\u95e8\u62dc\u8bbf\u5f53\u7136\u662f\u6709\u4ed6\u7684\u610f\u56fe\uff0c\u5348\u9910\u5c06\u8fd1\u5c3e\u58f0\u624d\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u53d1\u5c55\u5f88\u8fc5\u901f\uff0c\u6bcf\u6b21\u770b\u5230\u7535\u89c6\u91cc\u3001\u62a5\u9053\u4e0a\u6709\u5173\u9526\u6e56\u7684\u62a5\u9053\uff0c\u6211\u5fc3\u91cc\u90fd\u8981\u611f\u6168\u534a\u5929\u3002\u6709\u4e9b\u4e8b\u60c5\uff0c\u6211\u8fd8\u6ca1\u6709\u8ddf\u4f60\u7236\u4eb2\u5546\u91cf\uff0c\u542c\u8bf4\u4f60\u6628\u5929\u5230\u65b0\u829c\uff0c\u6211\u5c31\u8dd1\u8fc7\u6765\u8ddf\u4f60\u4ea4\u6d41\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u89c9\u5f97\u6211\u8fd9\u4e2a\u534a\u7cdf\u8001\u5934\u60f9\u4eba\u5fc3\u70e6\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4f1a\uff1f\u6709\u4e9b\u4e8b\u60c5\u6211\u8981\u5411\u6c5f\u4f2f\u4f2f\u4f60\u8bf7\u6559\u8fd8\u6765\u4e0d\u53ca\u5462\u3002\u201d\u5f20\u606a\u4e0d\u77e5\u9053\u6c5f\u4e0a\u5143\u627e\u4e0a\u95e8\u8981\u8bf4\u4ec0\u4e48\u4e8b\u60c5\uff0c\u7b11\u7740\u56de\u7b54\u4ed6\u3002</p><p>\u3000\u3000\u201c\u867d\u7136\u515a\u653f\u9886\u5bfc\u5e72\u90e8\u7684\u76f4\u7cfb\u5bb6\u5c5e\u5728\u5c5e\u5730\u7ecf\u5546\u6709\u56de\u907f\u7684\u8981\u6c42\uff0c\u4f46\u662f\u4e0d\u80fd\u4e3a\u4e86\u56de\u907f\u800c\u56de\u907f\u3002\u6211\u60f3\u9526\u6e56\u5728\u8003\u8651\u81ea\u8eab\u53d1\u5c55\u65f6\u523b\u610f\u907f\u5f00\u65b0\u829c\uff0c\u4e0d\u4ec5\u9526\u6e56\u4f1a\u6709\u6240\u4e0d\u65b9\u4fbf\uff0c\u90a3\u5bf9\u65b0\u829c\u6765\u8bf4\u5c31\u662f\u76f4\u63a5\u7684\u635f\u5931\u4e86\u2026\u2026\u653f\u7b56\uff0c\u6211\u53ef\u4ee5\u76f4\u63a5\u8dd1\u5230\u9676\u664b\u4e66\u8bb0\u90a3\u91cc\u53bb\u8981\uff0c\u4e0d\u4ec5\u662f\u7701\u91cc\uff0c\u5c31\u662f\u4e2d\u592e\uff0c\u90fd\u4f1a\u8003\u8651\u5230\u9526\u6e56\u5728\u884c\u4e1a\u91cc\u7684\u7279\u6b8a\u5730\u4f4d\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u6ca1\u60f3\u5230\u6c5f\u4e0a\u5143\u627e\u4e0a\u95e8\u6765\u662f\u8bf4\u8fd9\u4e2a\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u6709\u5173\u515a\u653f\u9886\u5bfc\u5e72\u90e8\u4efb\u804c\u56de\u907f\u65b9\u9762\u7684\u89c4\u5b9a\u672c\u5c31\u662f\u539f\u5219\u6027\u7684\u6761\u6b3e\uff0c\u5e76\u975e\u51b3\u7136\u4e0d\u5bb9\u8bb8\u51fa\u73b0\u7279\u4f8b\u3002</p><p>\u3000\u3000\u6b63\u6cf0\u96c6\u56e2\u4e0e\u5609\u4fe1\u7535\u5b50\u8054\u5408\u5728\u91d1\u5c71\u6295\u8d44\u5efa\u8bbe\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\u5c31\u662f\u7279\u6b8a\u7684\u4f8b\u5b50\u3002\u5468\u747e\u745c\u62c5\u4efb\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\uff0c\u800c\u5176\u5b50\u8c22\u5251\u5357\u5219\u5728\u6b63\u6cf0\u96c6\u56e2\u5185\u90e8\u76f4\u63a5\u6301\u80a1\u7684\u4efd\u989d\u8d85\u8fc710\uff05\uff0c\u800c\u552f\u4e00\u9700\u8981\u56de\u907f\u7684\u5730\u65b9\u5c31\u662f\u8c22\u5251\u5357\u6ca1\u6709\u4ee3\u8868\u6b63\u6cf0\u96c6\u56e2\u76f4\u63a5\u53c2\u4e0e\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\u7684\u5efa\u8bbe\u3002\u8fd9\u79cd\u60c5\u51b5\u4e0d\u4f1a\u7ed9\u5a92\u4f53\u5927\u8086\u62a5\u9053\uff0c\u5728\u4f53\u7cfb\u5185\u90e8\u5374\u7406\u6240\u5f53\u7136\u7684\u8ba4\u4e3a\u662f\u6b63\u5e38\u7684\u3002</p><p>\u3000\u3000\u5c31\u50cf\u6c5f\u4e0a\u5143\u6240\u8bf4\uff0c\u4ee5\u9526\u6e56\u4eca\u65e5\u7684\u5730\u4f4d\uff0c\u8981\u5230\u65b0\u829c\u6765\u6295\u8d44\uff0c\u4e5f\u53ea\u9700\u8981\u5728\u4e0a\u4e00\u7ea7\u4e3b\u7ba1\u90e8\u95e8\u90a3\u91cc\u8ba8\u8981\u653f\u7b56\uff0c\u8fd9\u4e9b\u95ee\u9898\u5c31\u53ef\u4ee5\u7406\u6240\u5f53\u7136\u7684\u7ed5\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u4eca\u5e74\u4ee5\u6765\uff0c\u914d\u5408\u7701\u91cc\u7684\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\uff0c\u9526\u6e56\u8fde\u7eed\u7684\u5927\u52a8\u4f5c\u8ba9\u4eba\u76ee\u4e0d\u6687\u63a5\uff0c\u8fdb\u519b\u624b\u673a\u4ea7\u4e1a\u3001\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\u3001\u542f\u52a8\u4e2d\u6676\u5fae\u82af\u6676\u5706\u5382\u9879\u76ee\u3001\u5728\u6d77\u5dde\u3001\u5efa\u90ba\u4e24\u5730\u6253\u9020\u5927\u89c4\u6a21\u7684\u6570\u5b57\u624b\u673a\u4ea7\u4e1a\u96c6\u7fa4\u2026\u2026</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u5fc3\u91cc\u6e05\u695a\uff0c\u65b0\u829c\u5e76\u6ca1\u6709\u8db3\u591f\u7684\u8d44\u6e90\u53bb\u627f\u63a5\u50cf\u6a61\u6811\u56ed\u3001\u4e2d\u6676\u5fae\u82af\u8fd9\u6837\u7684\u5927\u9879\u76ee\uff0c\u4f46\u662f\u6709\u4e9b\u914d\u5957\u9879\u76ee\uff0c\u65b0\u829c\u8fd8\u662f\u6709\u4e9b\u4e92\u8865\u6027\u8d28\u7684\u8d44\u6e90\u4f18\u52bf\uff0c\u7279\u522b\u7b49\u9ad8\u901f\u516c\u8def\u5efa\u6210\u4e4b\u540e\uff0c\u4e0e\u5efa\u90ba\u7684\u8def\u7a0b\u7f29\u77ed\u5230\u4e24\u4e2a\u5c0f\u65f6\u4e4b\u5185\uff0c\u4e0e\u5efa\u90ba\u56fd\u9645\u673a\u573a\u65b0\u5740\u7684\u8def\u7a0b\u66f4\u662f\u7f29\u77ed\u5230\u4e00\u4e2a\u534a\u5c0f\u65f6\u5de6\u53f3\uff0c\u65b0\u829c\u4e0e\u5efa\u90ba\u7684\u8054\u7cfb\u5c31\u53d8\u5f97\u66f4\u52a0\u7684\u7d27\u5bc6\u3002</p><p>\u3000\u3000\u9526\u6e56\u968f\u4fbf\u4ec0\u4e48\u914d\u5957\u9879\u76ee\u843d\u5728\u65b0\u829c\uff0c\u5c31\u80fd\u76f4\u63a5\u63a8\u52a8\u65b0\u829c\u7684\u5404\u9879\u7ecf\u6d4e\u6570\u636e\u4e0a\u6da8\u4e00\u5927\u622a\uff0c\u4e3a\u4e86\u65b0\u829c\u5e02\u7684\u7ecf\u6d4e\u53d1\u5c55\u3002</p><p>\u3000\u3000\u4e89\u53d6\u9526\u6e56\u5230\u65b0\u829c\u6765\u6295\u8d44\uff0c\u522b\u4eba\u770b\u65b0\u829c\u662f\u6ca1\u6709\u5176\u4ed6\u4fbf\u5229\u6761\u4ef6\uff0c\u5f20\u77e5\u884c\u4e0d\u662f\u5728\u65b0\u829c\u62c5\u4efb\u5e38\u52a1\u526f\u5e02\u957f\u5417\uff1f\u8fd9\u5c31\u662f\u4fbf\u5229\u6761\u4ef6\u3002\u4e3a\u4e86\u65b0\u829c\u7684\u7ecf\u6d4e\u80fd\u4e0a\u4e00\u4e2a\u53f0\u9636\uff0c\u6c5f\u4e0a\u5143\u4e5f\u53ea\u80fd\u539a\u7740\u8138\u76ae\u4e0a\u95e8\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u6c5f\u4e0a\u5143\u5e03\u6ee1\u76b1\u7eb9\u7684\u773c\u775b\u770b\u7740\u81ea\u5df1\uff0c\u70af\u70af\u6709\u795e\u7684\u76ee\u5149\u8ddf\u53ea\u770b\u4e2d\u730e\u7269\u7684\u72d0\u72f8\u4f3c\u7684\uff0c\u4e5f\u53ea\u80fd\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4e9b\u5929\u4f1a\u7559\u5728\u65b0\u829c\uff0c\u6b63\u597d\u8003\u8651\u8fd9\u4e9b\u4e8b\u60c5\u2026\u2026\u201d\u6c5f\u4e0a\u5143\u8fd9\u53ea\u8001\u72d0\u72f8\uff0c\u8fd8\u771f\u4e0d\u80fd\u968f\u4fbf\u5c31\u7cca\u5f04\u4ed6\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8fd9\u65f6\u5019\u4e5f\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\uff0c\u7ed9\u54ea\u8fb9\u5e2e\u8154\u90fd\u4e0d\u5bf9\u52b2\u3002\u6c5f\u4e0a\u5143\u8fd8\u5c31\u662f\u5229\u7528\u8fd9\u79cd\u5c34\u5c2c\u7684\u5173\u7cfb\u5e0c\u671b\u5f20\u606a\u8ba4\u771f\u8003\u8651\u4ed6\u7684\u5efa\u8bae\uff0c\u5f53\u7136\u4e5f\u4e0d\u80fd\u903c\u7740\u5f20\u606a\u8fd9\u65f6\u5019\u5c31\u7ed9\u7b54\u6848\u3002\u7528\u8fc7\u9910\uff0c\u5728\u5ba2\u5385\u95f2\u5750\u4e86\u7247\u523b\uff0c\u6c5f\u4e0a\u5143\u53c8\u62c9\u7740\u5f20\u77e5\u884c\u4e00\u540c\u4e58\u8f66\u79bb\u5f00\u4e86\uff0c\u5c06\u5934\u75bc\u7684\u95ee\u9898\u7559\u7ed9\u5f20\u606a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5510\u5a67\u4e0e\u7fdf\u4e39\u9752\u3001\u536b\u5170\u5979\u4eec\u901b\u8857\u5230\u4e0b\u5348\u56db\u70b9\u949f\u624d\u56de\u6765\uff0c\u63d0\u7740\u4e00\u645e\u7eb8\u888b\u7684\u6218\u5229\u54c1\uff0c\u5174\u51b2\u51b2\u7684\u51b2\u4e0a\u697c\uff0c\u4e5f\u6709\u4e70\u7ed9\u5f20\u606a\u7684\u793c\u7269\uff0c\u558a\u4ed6\u6765\u770b\uff0c\u5374\u770b\u89c1\u5f20\u606a\u5750\u5728\u4e66\u623f\u7684\u5730\u677f\u4e0a\uff0c\u6574\u4e2a\u4eba\u7ed9\u7070\u6251\u6251\u7684\u8d44\u6599\u3001\u4e66\u7c4d\u56f4\u6ee1\u3002</p><p>\u3000\u3000\u201c\u4e0d\u8bf4\u662f\u5230\u65b0\u829c\u4e4b\u540e\u7ed9\u81ea\u5df1\u653e\u5047\u3001\u4ec0\u4e48\u4e8b\u60c5\u90fd\u4e0d\u7ba1\u4e0d\u95ee\u5417\uff0c\u600e\u4e48\u53c8\u8981\u770b\u4e00\u5806\u7684\u8d44\u6599\uff1f\u201d\u5510\u5a67\u5947\u602a\u7684\u95ee\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\u7684\u82e6\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u6211\u60f3\u6d88\u505c\uff0c\u4e5f\u8981\u522b\u4eba\u8ba9\u6211\u6d88\u505c\u554a\uff0c\u4f60\u4eec\u4ee5\u4e3a\u6c5f\u4e0a\u5143\u627e\u4e0a\u95e8\u6765\u5403\u4e2d\u996d\u4f1a\u6709\u4ec0\u4e48\u597d\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u611f\u89c9\u7740\u5c31\u7b97\u9526\u6e56\u4e0d\u76f4\u63a5\u5230\u65b0\u829c\u6765\u6295\u8d44\uff0c\u4e5f\u53ef\u4ee5\u5f71\u54cd\u7535\u5b50\u4ea7\u4e1a\u96c6\u7fa4\u5728\u67d0\u4e9b\u65b9\u9762\u5411\u65b0\u829c\u503e\u659c\uff0c\u8fd9\u6837\u5c31\u80fd\u5bf9\u65b0\u829c\u7684\u7ecf\u6d4e\u6709\u4e2a\u62c9\u52a8\u4f5c\u7528\u3002\u4e5f\u4e0d\u662f\u4e3a\u4e86\u8981\u7ed9\u6c5f\u4e0a\u5143\u4e00\u4e2a\u4ea4\u4ee3\uff0c\u5c31\u5f20\u606a\u7684\u672c\u610f\u6765\u8bf4\uff0c\u4ed6\u4e5f\u5e0c\u671b\u65b0\u829c\u7684\u7ecf\u6d4e\u80fd\u66f4\u5feb\u7684\u53d1\u5c55\u8d77\u6765\u3002\u53ea\u662f\u4e4b\u524d\u592a\u62d8\u6ce5\u4e8e\u4e2d\u592e\u5728\u515a\u653f\u9886\u5bfc\u5e72\u90e8\u4efb\u804c\u56de\u907f\u65b9\u9762\u7684\u89c4\u5b9a\uff0c\u8003\u8651\u9526\u6e56\u4ee5\u53ca\u6574\u4e2a\u4ea7\u4e1a\u96c6\u7fa4\u7684\u53d1\u5c55\u65f6\uff0c\u81ea\u7136\u800c\u7136\u7684\u5c06\u65b0\u829c\u7ed9\u5254\u9664\u5728\u5916\uff0c\u6216\u8bb8\u5e76\u4e0d\u662f\u5341\u5206\u7684\u5408\u9002\uff0c\u8981\u4e0d\u662f\u7ed9\u6c5f\u4e0a\u5143\u4eca\u5929\u76f4\u63a5\u63d0\u51fa\u6765\uff0c\u5f20\u606a\u4e5f\u60f3\u4e0d\u5230\u8981\u5bf9\u6b64\u6709\u53cd\u601d\u3002</p></article>"}}}]);
