(window.webpackJsonp=window.webpackJsonp||[]).push([[94],{117:function(p){p.exports={chapterTitle:null,title:"\u7b2c1079\u7ae0 \u80cc\u540e\u7684\u7537\u4eba",content:"<article><h2>\u7b2c1079\u7ae0 \u80cc\u540e\u7684\u7537\u4eba</h2><p>\u3000\u3000\u5f20\u606a\u5750\u5728\u4e66\u684c\u524d\u6d4f\u89c8\u7535\u5b50\u90ae\u4ef6\uff0c\u5510\u5a67\u7a7f\u7740\u7761\u88d9\uff0c\u8eab\u5b50\u8db4\u5728\u67d4\u8f6f\u7684\u88ab\u8925\u4e0a\u770b\u7740\u65b0\u5a31\u4e50\u5468\u520a\u8bb0\u8005\u9001\u6765\u6253\u7b97\u6572\u8bc8\u7684\u7167\u7247\u4e0e\u62a5\u9053\u6837\u7a3f\uff0c\u8eab\u5b50\u4e0d\u5b89\u5206\u7684\u52a8\u6765\u52a8\u53bb\u7684\uff0c\u7761\u88d9\u6324\u76b1\u7740\uff0c\u9732\u51fa\u96ea\u5ae9\u7ea4\u957f\u7684\u5927\u817f\uff0c\u5ffd\u53c8\u8f6c\u8fc7\u5934\u6765\uff0c\u7075\u79c0\u7f8e\u4e3d\u7684\u7738\u5b50\u770b\u7740\u6b63\u56de\u590d\u90ae\u4ef6\u7684\u5f20\u606a\uff0c\u8bf4\u9053\uff1a</p><p>\u3000\u3000\u201c\u6211\u89c9\u5f97\u8fd9\u7bc7\u62a5\u9053\u5199\u5f97\u86ee\u6709\u9053\u7406\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u6f14\u5531\u4f1a\u8fdb\u884c\u5230\u5c3e\u58f0\u65f6\uff0c\u65b0\u5a31\u4e50\u5468\u5211\u7684\u8fd9\u540d\u6444\u5f71\u8bb0\u8005\u5b88\u5728\u6f14\u5531\u4f1a\u73b0\u573a\u7684\u540e\u53f0\u5165\u53e3\uff0c\u5c31\u662f\u60f3\u7740\u5b88\u682a\u5f85\u5154\u6316\u6398\u7eef\u95fb\u7684\u3002\u5f20\u606a\u4e58\u5750\u8c6a\u534e\u8f7f\u8f66\u51fa\u73b0\uff0c\u53c8\u6709\u591a\u540d\u4fdd\u9556\u76f8\u968f\uff0c\u76f4\u63a5\u4ece\u540e\u53f0\u5165\u53e3\u8fdb\u5165\u6f14\u5531\u4f1a\u73b0\u573a\uff0c\u5728\u9999\u6e2f\u72d7\u4ed4\u961f\u7684\u773c\u91cc\uff0c\u7b80\u76f4\u6bd4\u6253\u4e86\u63a2\u7167\u706f\u8fd8\u660e\u4eae\u3002\u6240\u5e78\u5f20\u606a\u4e0b\u8f66\u5230\u8fdb\u95e8\u4e4b\u95f4\u6ca1\u6709\u4ec0\u4e48\u505c\u987f\uff0c\u53ea\u7ed9\u4ece\u4fa7\u540e\u6293\u62cd\u4e86\u4fa7\u8138\uff0c\u5c31\u7b97\u6ca1\u6709\u56fd\u65d7\u886b\u4e8b\u60c5\uff0c\u65b0\u5a31\u4e50\u5468\u520a\u4e00\u6837\u53ef\u4ee5\u7ed9\u5a31\u4e50\u65b0\u95fb\u6253\u4e0a\u201c\u795e\u79d8\u5e74\u8f7b\u5bcc\u8c6a\u51fa\u5e2d\u65b0\u751f\u4ee3\u6b4c\u540e\u6f14\u5531\u4f1a\u73b0\u573a\u201d\u7684\u6807\u9898\u3002\u5f85\u6c5f\u9edb\u513f\u7a7f\u7740\u5f20\u606a\u7684\u886c\u886b\u51fa\u573a\uff0c\u65b0\u5a31\u4e50\u5468\u520a\u7684\u90a3\u540d\u8bb0\u8005\u5dee\u70b9\u4e50\u574f\u4e86\uff0c\u7adf\u7136\u662f\u5708\u5b50\u4ece\u6765\u90fd\u6ca1\u6709\u89c1\u5230\u8fc7\u7684\u65b0\u9762\u5b54\uff0c\u4ed6\u8981\u662f\u76f4\u63a5\u5c06\u62a5\u9053\u520a\u767b\u51fa\u6765\uff0c\u7edd\u5bf9\u662f\u5927\u5a31\u4e50\u516b\u5366\uff0c\u60b2\u5267\u7684\u662f\u4ed6\u60f3\u62ff\u8fd9\u4e9b\u7167\u7247\u6572\u8bc8\u66f4\u591a\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u8fd9\u8fb9\u901a\u77e5\u8b66\u65b9\u4ecb\u5165\uff0c\u90a3\u4e2a\u60f3\u6572\u8bc8\u4e00\u767e\u4e07\u6e2f\u5e01\u7684\u65b0\u5a31\u4e50\u5468\u520a\u8bb0\u8005\u59ff\u6001\u5c31\u8f6f\u4e86\u4e0b\u6765\uff0c\u8981\u6c42\u79c1\u4e0b\u548c\u89e3\uff0c\u5c06\u5e95\u7247\u4e0e\u62a5\u9053\u6837\u7a3f\u90fd\u4ea4\u4e86\u51fa\u6765\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u7b97\u662f\u6682\u65f6\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5f71\u6e7e\u56ed\u4f1a\u6240\u7684\u5e86\u529f\u5bb4\u4e00\u76f4\u6301\u7eed\u5230\u51cc\u6668\u4e09\u70b9\u949f\u624d\u7ed3\u675f\uff0c\u5510\u5a67\u968f\u624b\u5c06\u5e95\u7247\u4e0e\u62a5\u9053\u6837\u7a3f\u62ff\u56de\u623f\u95f4\uff0c\u8db4\u5728\u5e8a\u4e0a\u6d25\u6d25\u6709\u5473\u7684\u770b\u8d77\u6765\uff0c\u5979\u62ff\u7740\u90a3\u7bc7\u62a5\u9053\u6837\u7a3f\u5728\u8ddf\u5f20\u606a\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u5f20\u606a\u4e00\u8138\u65e0\u8f9c\u7684\u770b\u7740\u5510\u5a67\uff0c\u95ee\u9053\uff0c\u201c\u600e\u4e48\u6709\u9053\u7406\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u53c8\u6ca1\u6709\u8bf4\u4f60\u8ddf\u9edb\u513f\u59d0\u6709\u4ec0\u4e48\uff0c\u201d\u5510\u5a67\u5a07\u7b11\u7740\u8bf4\uff0c\u201c\u518d\u8bf4\u4e86\uff0c\u4f60\u4eec\u5c31\u7b97\u6709\u505a\u8d3c\u7684\u5fc3\uff0c\u6709\u505a\u8d3c\u7684\u673a\u4f1a\u554a\uff01\u4f60\u60f3\u60f3\u770b\uff0c\u6709\u591a\u5c11\u534e\u8bed\u5a92\u4f53\u7684\u72d7\u4ed4\u961f\u76ef\u7740\u4f60\u4eec\u2026\u2026\u201d\u5979\u4e3e\u7740\u5e95\u7247\u5728\u5e8a\u4e0a\u5f97\u610f\u7684\u76f4\u6253\u6eda\uff0c\u671d\u5f20\u606a\u626c\u4e86\u626c\uff0c\u8bf4\u9053\uff0c\u201c\u9edb\u513f\u59d0\u7a7f\u4f60\u7684\u886c\u886b\u771f\u7684\u5f88\u6027\u611f\uff0c\u4e4b\u524d\u5c31\u89c9\u5f97\u5979\u6f02\u4eae\uff0c\u53ef\u6ca1\u6709\u60f3\u5230\u5979\u4e5f\u6709\u8fd9\u4e48\u6027\u611f\u7684\u4e00\u9762\uff0c\u4f60\u53ef\u4ee5\u542c\u542c\u6f14\u5531\u4f1a\u7684\u5f55\u97f3\uff0c\u5c31\u77e5\u9053\u5f53\u65f6\u6709\u591a\u5c11\u6b22\u52a8\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u6765\u4f38\u4e86\u4f38\u61d2\u8170\uff0c\u8bf4\u9053\uff1a\u201c\u592a\u719f\u6089\u4e86\uff0c\u6ca1\u6709\u666e\u901a\u6b4c\u8ff7\u7684\u795e\u79d8\u611f\uff0c\u542c\u5979\u7684\u6b4c\u771f\u662f\u7f3a\u5c11\u4e00\u79cd\u8ddd\u79bb\u7684\u611f\u89c9\uff0c\u4f60\u770b\u770b\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\u4eca\u5929\u665a\u4e0a\u5c31\u62a2\u7740\u8ddf\u9edb\u513f\u98da\u6b4c\uff0c\u6211\u4e5f\u6ca1\u89c9\u5f97\u5979\u4eec\u5531\u6b4c\u6709\u591a\u5dee\u52b2\uff01\u201d</p><p>\u3000\u3000\u201c\u9edb\u513f\u59d0\u5176\u5b9e\u86ee\u53ef\u601c\u7684\uff0c\u4e00\u4e3e\u4e00\u52a8\u90fd\u5728\u72d7\u4ed4\u961f\u7684\u6ce8\u76ee\u4e4b\u4e0b\uff0c\u88ab\u5f53\u6210\u516c\u4f17\u60c5\u4eba\uff0c\u8fd8\u8981\u4fdd\u6301\u6e05\u7eaf\u7389\u5973\u7684\u5f62\u8c61\u2026\u2026\u201d\u5510\u5a67\u53c8\u7ffb\u8fc7\u6765\uff0c\u8db4\u5728\u67d4\u8f6f\u7684\u88ab\u8925\u4e0a\uff0c\u81c0\u90e8\u5fae\u6485\u7740\uff0c\u9732\u51fa\u8102\u6ed1\u82e5\u7389\u7684\u96ea\u767d\u808c\u80a4\uff0c\u5979\u7684\u957f\u817f\u6401\u5728\u6d77\u84dd\u8272\u4e00\u6837\u7684\u88ab\u8925\u4e0a\uff0c\u706f\u5149\u843d\u5728\u4e0a\u9762\uff0c\u683c\u5916\u7684\u8bf1\u5916\uff0c\u5979\u770b\u7740\u5f20\u606a\u8981\u8fc7\u6765\u6478\u5979\u7684\u81c0\u90e8\uff0c\u53c8\u7b11\u7740\u6eda\u5230\u5e8a\u7684\u53e6\u4e00\u8fb9\u8ddf\u5f20\u606a\u8bf4\u8bdd\uff0c\u201c\u5b59\u4f2f\u4f2f\u529e\u9152\u6536\u9edb\u513f\u505a\u5e72\u5973\u513f\uff0c\u4f60\u771f\u4e0d\u51fa\u5e2d\uff1f\u201d</p><p>\u3000\u3000\u201c\u7ed9\u62cd\u5230\u4e00\u6b21\u8fd8\u4e0d\u591f\u554a\uff1f\u201d\u5f20\u606a\u5750\u5728\u5e8a\u6cbf\u4e0a\uff0c\u5510\u5a67\u6eda\u8fc7\u6765\uff0c\u5934\u6795\u5728\u4ed6\u7684\u5927\u817f\u4e0a\uff0c\u4ed6\u629a\u6478\u7740\u5510\u5a67\u5ae9\u6ed1\u7684\u8138\u988a\uff0c\u53c8\u62ff\u624b\u6307\u809a\u9017\u5979\u957f\u957f\u7684\u773c\u776b\u6bdb\uff0c\u201c\u4f60\u8ddf\u76db\u590f\u8fc7\u53bb\u73a9\u5c31\u53ef\u4ee5\u4e86\uff0c\u8fd9\u79cd\u9152\u5e2d\u662f\u529e\u7ed9\u5916\u9762\u4eba\u770b\u7684\uff0c\u8fd8\u4f1a\u7279\u610f\u7684\u8bf7\u72d7\u4ed4\u961f\u51fa\u573a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u4ec5\u4e0d\u4f1a\u51fa\u5e2d\u9152\u5e2d\uff0c\u8fd8\u7279\u610f\u8ba9\u5085\u4fca\u5c06\u4ed6\u5728\u9999\u6e2f\u51fa\u884c\u7684\u8f66\u90fd\u6362\u6389\uff0c\u95f9\u51fa\u7eef\u95fb\u6765\uff0c\u7ed9\u72d7\u4ed4\u961f\u76ef\u4f4f\u7684\u611f\u89c9\u53ef\u4e0d\u597d\u53d7\uff0c\u770b\u6765\u4ee5\u536b\u5170\u7684\u540d\u4e49\u5728\u897f\u6fb3\u6d32\u8d2d\u4e70\u79c1\u4eba\u6d77\u5c9b\u7684\u51b3\u5b9a\u518d\u6b63\u786e\u4e0d\u8fc7\u4e86\u3002</p><p>\u3000\u3000\u201c\u4e0d\u662f\u8fd8\u8981\u5ba3\u5e03\u5bf9\u5b59\u59d0\u7684\u516c\u53f8\u6ce8\u8d44\u5417\uff1f\u201d\u5510\u5a67\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4ee5\u9526\u6e56\u5546\u4e8b\u7684\u540d\u4e49\u6ce8\u8d44\uff0c\u53f6\u5efa\u658c\u90fd\u4e0d\u7528\u51fa\u9762\uff0c\u6211\u53bb\u51d1\u4ec0\u4e48\u70ed\u95f9\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff0c\u5b59\u5c1a\u4e49\u662f\u9526\u6e56\u5546\u4e8b\u7684\u8054\u5408\u603b\u88c1\uff0c\u8d1f\u8d23\u9526\u6e56\u5546\u4e8b\u5411\u4e1c\u5357\u4e9a\u534e\u5546\u878d\u8d44\u4e8b\u52a1\uff0c\u4e16\u7eaa\u534e\u97f3\u5b9e\u8d28\u672c\u6765\u5c31\u662f\u4ed6\u4eec\u5b59\u5bb6\u7684\u79c1\u4eba\u516c\u53f8\uff0c\u5de6\u624b\u817e\u53f3\u624b\uff0c\u9526\u6e56\u5546\u4e8b\u76f4\u63a5\u6ce8\u8d44\uff0c\u53ea\u662f\u5c06\u4e16\u7eaa\u534e\u97f3\u516c\u53f8\u7684\u80cc\u666f\u516c\u5f00\u5316\uff0c\u89c1\u5510\u5a67\u773c\u73e0\u5b50\u4e71\u8f6c\uff0c\u95ee\u9053\uff0c\u201c\u662f\u4e0d\u662f\u89c9\u5f97\u95f9\u51fa\u8fd9\u70b9\u4e8b\u60c5\u5c31\u8981\u5bf9\u5b59\u59d0\u7684\u516c\u53f8\u6ce8\u8d44\uff0c\u6709\u4e9b\u5c0f\u9898\u5927\u505a\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u554a\uff0c\u201d\u5510\u5a67\u6291\u7740\u5934\u671d\u5f20\u606a\u5a07\u61a8\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u4e0d\u660e\u767d\uff0c\u4f60\u4e5f\u8981\u6559\u6211\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u5355\u5355\u662f\u6ce8\u8d44\uff0c\u8fd8\u8981\u5927\u4e3e\u8e0f\u5165\u9999\u6e2f\u6f14\u827a\u5a31\u4e50\u754c\u2014\u2014\u4eca\u5929\u80cc\u540e\u6307\u4f7f\u52a8\u624b\u811a\u7684\u4eba\u96be\u9053\u771f\u4e0d\u77e5\u9053\u534e\u97f3\u516c\u53f8\u80cc\u540e\u7ad9\u7740\u5b59\u5bb6\u3001\u7ad9\u7740\u9526\u6e56\uff1f\u201d\u5f20\u606a\u6478\u7740\u5510\u5a67\u7684\u8138\u988a\uff0c\u89e3\u91ca\u9053\uff0c\u201c\u4ed6\u4eec\u8fd8\u662f\u8981\u6296\u7740\u80c6\u5b50\u52a8\u624b\u811a\uff0c\u65e0\u975e\u662f\u56e0\u4e3a\u9526\u6e56\u5546\u4e8b\u7684\u603b\u90e8\u867d\u7136\u8bbe\u5728\u9999\u6e2f\u4f46\u662f\u5bf9\u9999\u6e2f\u7684\u76f4\u63a5\u5f71\u54cd\u529b\u592a\u5c0f\u4e86\uff0c\u80cc\u540e\u4e00\u4e9b\u5173\u8fde\u53c8\u4e0d\u80fd\u6446\u5f00\u6765\u7ed9\u4ed6\u4eec\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u624d\u4e0d\u4fe1\u82f1\u738b\u3001\u6c38\u4e1a\u8fd9\u4e9b\u5e55\u540e\u8d22\u4e1c\u77e5\u9053\u4e16\u7eaa\u534e\u97f3\u516c\u53f8\u80cc\u540e\u7ad9\u7740\u4e2d\u592e\u9a7b\u6e2f\u8054\u7edc\u529e\u526f\u4e3b\u4efb\u53f6\u81fb\u6c11\u4e4b\u540e\u8fd8\u6709\u80c6\u5b50\u4f7f\u8fd9\u4e9b\u4e0b\u4e09\u6ee5\u7684\u624b\u6bb5\u2014\u2014\u6709\u4e9b\u5173\u7cfb\u4e0d\u80fd\u644a\u5f00\u5230\u53f0\u9762\u4e0a\u6765\u8bf4\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u53f6\u81fb\u6c11\u5728\u9999\u6e2f\u7684\u5f71\u54cd\u529b\uff0c\u4e5f\u4f1a\u968f\u7740\u4ed6\u7684\u79bb\u4efb\u800c\u51cf\u5f31\uff0c\u4e0d\u53ef\u80fd\u957f\u671f\u7ef4\u6301\u5728\u4e00\u4e2a\u8f83\u9ad8\u7684\u6c34\u5e73\u4e0a\u3002</p><p>\u3000\u3000\u201c\u55ef\u2026\u2026\u201d\u5510\u5a67\u9887\u6709\u611f\u609f\u7684\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u5bf9\u666e\u901a\u6c11\u4f17\u5f71\u54cd\u529b\u6700\u5927\u7684\u8fd8\u662f\u6f14\u827a\u5a31\u4e50\u4ea7\u4e1a\uff0c\u9664\u4e86\u9999\u6e2f\uff0c\u5bf9\u5176\u4ed6\u5730\u533a\u7684\u5f71\u54cd\u529b\u4e5f\u5927\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5bf9\u554a\uff0c\u6295\u8d44\u5c0f\uff0c\u6709\u5229\u4e8e\u9526\u6e56\u5546\u4e8b\u5728\u9999\u6e2f\u6269\u5927\u5f71\u54cd\u529b\uff0c\u6700\u5b9e\u9645\u7684\u7528\u5904\uff0c\u5c31\u662f\u54c1\u724c\u6e17\u900f\u529b\u5f3a\uff0c\u4e0d\u8bf4\u5176\u4ed6\u7684\uff0c\u8981\u662f\u80fd\u5c06\u82f1\u738b\u5a31\u4e50\u65d7\u4e0b\u7684\u5927\u724c\u827a\u4eba\u90fd\u62a2\u8fc7\u6765\uff0c\u8ba9\u4ed6\u4eec\u90fd\u4f7f\u7528\u6211\u4eec\u7684\u624b\u673a\uff0c\u7231\u8fbe\u624b\u673a\u5728\u4e9a\u6d32\u7684\u9500\u91cf\u81f3\u5c11\u80fd\u63d0\u9ad8\u597d\u51e0\u70b9\u2026\u2026\u7535\u5f71\u3001\u6b4c\u66f2\u4ec0\u4e48\u7684\uff0c\u90fd\u53ef\u4ee5\u63d2\u8f6f\u5e7f\u544a\uff0c\u5728\u5927\u5bb6\u770b\u538c\u7535\u89c6\u3001\u62a5\u7eb8\u4e0a\u6240\u520a\u8f7d\u7684\u5e7f\u544a\u4e4b\u540e\uff0c\u8fd9\u79cd\u8f6f\u5e7f\u544a\u7684\u5f71\u54cd\u529b\u5c06\u66f4\u5927\u3002\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u4e00\u8eab\u94dc\u81ed\u5473\u2026\u2026\u201d\u5510\u5a67\u5658\u4e86\u5658\u5634\uff0c\u7ffb\u4e86\u4e2a\u8eab\uff0c\u53c8\u4e0d\u7231\u542c\u5f20\u606a\u5728\u90a3\u91cc\u778e\u5439\uff0c\u201c\u542c\u8d77\u6765\u597d\u6ca1\u52b2\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8981\u542c\u4ec0\u4e48\u554a\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u542c\u80cc\u540e\u7537\u4eba\u7684\u6545\u4e8b\uff0c\u4e5f\u6bd4\u542c\u8fd9\u4e2a\u5f3a\u554a\u3002\u201d\u5510\u5a67\u53c8\u6eda\u5230\u5f20\u606a\u7684\u5927\u817f\u6839\uff0c\u8138\u671d\u4e0a\uff0c\u770b\u7740\u5f20\u606a\u7684\u773c\u775b\u3002</p><p>\u3000\u3000\u201c\u563f\u563f\u2026\u2026\u201d\u5f20\u606a\u50bb\u7b11\u4e86\u4e24\u58f0\uff0c\u62cd\u4e86\u62cd\u5510\u5a67\u7684\u8138\u988a\uff0c\u8bf4\u9053\uff0c\u201c\u8131\u8863\u670d\u7761\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\u2026\u2026\u201d\u5510\u5a67\u6eda\u8fdb\u88ab\u5b50\u91cc\uff0c\u4e0d\u8ba9\u5f20\u606a\u94bb\u8fdb\u53bb\uff0c\u5979\u8eab\u4e0a\u5c31\u7a7f\u7740\u4e00\u4ef6\u7761\u88d9\uff0c\u518d\u8131\u5c31\u53ea\u5269\u6761\u5c0f\u5185\u88e4\u4e86\uff0c\u60f3\u5fc5\u8131\u5230\u8fd9\u4e2a\u7a0b\u5ea6\uff0c\u5f20\u606a\u4e5f\u662f\u4e0d\u4f1a\u7f62\u4f11\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5929\u4e0b\u6ca1\u6709\u4e0d\u900f\u98ce\u7684\u5899\uff0c\u56fd\u65d7\u886b\u4e8b\u4ef6\u66f4\u4e0d\u53ef\u80fd\u4e0d\u5728\u6f14\u827a\u5708\u91cc\u6d41\u4f20\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u592b\u5987\u6536\u6c5f\u9edb\u513f\u505a\u5e72\u5973\u513f\uff0c\u59ff\u6001\u4e5f\u662f\u975e\u5e38\u7684\u660e\u663e\uff0c\u8c01\u8981\u518d\u5bf9\u5979\u4f7f\u4e0b\u4e09\u6ee5\u7684\u624b\u6bb5\uff0c\u90a3\u5c31\u662f\u4e0d\u6b7b\u4e0d\u4f11\u7684\u5bf9\u6297\uff0c\u9999\u6e2f\u504f\u95e8\u52bf\u529b\u73a9\u6b6a\u95e8\u90aa\u9053\u518d\u504f\uff0c\u4e5f\u8bb2\u7a76\u4e00\u4e2a\u4e0d\u7978\u53ca\u5bb6\u4eba\u7684\u89c4\u77e9\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u56fd\u65d7\u886b\u4e8b\u4ef6\u867d\u7136\u6293\u4e0d\u5230\u82f1\u738b\u3001\u6c38\u4e1a\u5e55\u540e\u8d22\u4e1c\u7684\u628a\u67c4\uff0c\u4f46\u5ec9\u653f\u516c\u7f72\u8fd8\u662f\u627e\u5176\u4ed6\u501f\u53e3\u9080\u8bf7\u4ed6\u4eec\u8fc7\u53bb\u559d\u8336\u8b66\u9192\u4ed6\u4eec\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u5728\u5bf9\u5a92\u4f53\u534a\u516c\u5f00\u7684\u9152\u5e2d\u4e0a\uff0c\u5b59\u5c1a\u4e49\u8fd8\u4ee3\u8868\u9526\u6e56\u5546\u4e8b\u5ba3\u5e03\u5bf9\u4e16\u7eaa\u534e\u97f3\u6ce8\u8d44\u7684\u8ba1\u5212\uff0c\u4e16\u7eaa\u534e\u97f3\u518d\u4e0d\u662f\u79c1\u4eba\u6027\u8d28\u7ecf\u7eaa\u6f14\u827a\u516c\u53f8\uff0c\u8ffd\u52a0\u7684\u6295\u8d44\u867d\u7136\u5f88\u6709\u9650\uff0c\u4f46\u662f\u4ee3\u8868\u9526\u6e56\u5546\u4e8b\u901a\u8fc7\u5bf9\u4e16\u7eaa\u534e\u97f3\u7684\u6ce8\u8d44\u6b63\u5f0f\u8e0f\u5165\u5a31\u4e50\u4ea7\u4e1a\uff0c\u8fd8\u5ba3\u5e03\u8981\u4e0e\u5185\u5730\u4f20\u5a92\u4f01\u4e1a\u5408\u4f5c\uff0c\u5c06\u5185\u5730\u4f20\u5a92\u5a31\u4e50\u4ea7\u4e1a\u7684\u8d44\u672c\u5f15\u8fdb\u4e16\u7eaa\u534e\u97f3\u3002</p><p>\u3000\u3000\u53d7\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5f71\u54cd\uff0c\u4e5d\u4e5d\u5e74\u9999\u6e2f\u4ee5\u53ca\u4e1c\u5357\u4e9a\u7684\u5f71\u50cf\u5531\u7247\u5e02\u573a\u6301\u7eed\u840e\u7f29\uff0c\u9999\u6e2f\u7535\u5f71\u5df2\u7ecf\u4e0d\u518d\u6709\u5341\u5e74\u9ec4\u91d1\u65f6\u671f\u7684\u8f89\u714c\uff0c\u867d\u7136\u5185\u5730\u7248\u6743\u4fdd\u62a4\u95ee\u9898\u4e25\u91cd\uff0c\u4f46\u662f\u4e1a\u5185\u7684\u6709\u8bc6\u4e4b\u58eb\u90fd\u77e5\u9053\u9999\u6e2f\u7535\u5f71\u4ee5\u53ca\u5a31\u4e50\u4ea7\u4e1a\u8981\u590d\u82cf\uff0c\u552f\u6709\u4f9d\u6258\u5185\u5730\u5e02\u5730\u3002</p><p>\u3000\u3000\u82f1\u738b\u5a31\u4e50\u3001\u6c38\u4e1a\u5f71\u89c6\u4e24\u5bb6\u9999\u6e2f\u6f14\u827a\u5a31\u4e50\u4ea7\u4e1a\u7684\u5de8\u5934\uff0c\u6b64\u65f6\u4e5f\u6b63\u7ecf\u53d7\u5e02\u573a\u840e\u7f29\u3001\u7ecf\u6d4e\u4f4e\u9761\u7684\u9635\u75db\uff0c\u867d\u7136\u56fd\u65d7\u886b\u4e8b\u60c5\u6293\u4e0d\u5230\u4ed6\u4eec\u7684\u628a\u63e1\uff0c\u4f46\u662f\u5e76\u4e0d\u610f\u8c13\u7740\u9526\u6e56\u5546\u4e8b\u4e0d\u53ef\u4ee5\u5361\u4f4f\u4ed6\u4eec\u8fdb\u5165\u5185\u5730\u5e02\u573a\u7684\u8116\u5b50\u8ba9\u4ed6\u4eec\u6301\u7eed\u7684\u9635\u75db\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u5361\u8116\u5b50\u6700\u597d\u7684\u65b9\u6cd5\uff0c\u5c31\u662f\u4e89\u8fdb\u5165\u5185\u5730\u5e02\u573a\u7684\u6e20\u9053\u8d44\u6e90\uff0c\u82f1\u738b\u4e0e\u6c38\u4e1a\u8981\u80fd\u5728\u8fd9\u4e0a\u9762\u4e89\u8fc7\u9526\u6e56\uff0c\u90a3\u624d\u53eb\u89c1\u9b3c\u5462\u3002</p><p>\u3000\u3000\u4e0d\u8bf4\u653f\u6cbb\u4e0a\u7684\u5f71\u54cd\u529b\uff0c\u9526\u6e56\u5728\u56fd\u5185\u4e3a\u7231\u8fbe\u3001\u9999\u96ea\u6d77\u3001\u76db\u946b\u4e09\u5927\u54c1\u724c\u6492\u4e0b\u7684\u5e7f\u544a\u8d39\u7528\u5c31\u6709\u5341\u591a\u4ebf\uff0c\u4ece\u8fd9\u4e2a\u610f\u4e49\u4e0a\u6765\u8bf4\uff0c\u9526\u6e56\u5bf9\u56fd\u5185\u4f20\u5a92\u4ea7\u4e1a\u7684\u5f71\u54cd\u529b\u662f\u5c11\u6709\u4eba\u80fd\u53ca\u7684\uff0c\u66f4\u4f55\u51b5\u534e\u8bed\u7b2c\u4e00\u95e8\u6237\u7f51\u7ad9\u8fd8\u662f\u9526\u6e56\u76f4\u63a5\u63a7\u5236\u7684\u3002</p><p>\u3000\u3000\u53ea\u8981\u4e16\u7eaa\u534e\u97f3\u80fd\u5c06\u8fdb\u5165\u5185\u5730\u5e02\u573a\u7684\u6e20\u9053\u8d44\u6e90\u4e89\u5360\u4e0b\u6765\uff0c\u5176\u4ed6\u60f3\u8fdb\u5165\u5185\u5730\u5e02\u573a\u7684\u9999\u6e2f\u827a\u4eba\u3001\u6f14\u827a\u516c\u53f8\u3001\u5f71\u89c6\u5531\u7247\u6295\u8d44\u4eba\u5c31\u8981\u770b\u4e16\u7eaa\u534e\u97f3\u7684\u8138\u8272\u4e86\u3002\u5230\u65f6\u5019\u8981\u4e0d\u8981\u8e29\u82f1\u738b\u3001\u6c38\u4e1a\uff0c\u53ef\u4ee5\u770b\u5fc3\u60c5\u5982\u4f55\u4e86\u3002</p><p>\u3000\u3000\u8bf4\u8d77\u6765\uff0c\u901a\u8fc7\u4e16\u7eaa\u534e\u97f3\u5927\u89c4\u6a21\u6b63\u5f0f\u6d89\u8db3\u9999\u6e2f\u6f14\u827a\u5a31\u4e50\u4ea7\u4e1a\uff0c\u8fd8\u662f\u8003\u8651\u5230\u5b9e\u9645\u7684\u5229\u76ca\u3002\u76f4\u63a5\u6295\u8d44\u989d\u5ea6\u4e0d\u4f1a\u592a\u5927\uff0c\u518d\u52a0\u4e0a\u4f1a\u4e0e\u5185\u5730\u4f20\u5a92\u8d44\u672c\u7684\u5408\u4f5c\uff0c\u9526\u6e56\u5546\u4e8b\u7684\u76f4\u63a5\u6295\u8d44\u4f1a\u66f4\u52a0\u6709\u9650\uff0c\u4f46\u662f\u9999\u6e2f\u6f14\u827a\u5a31\u4e50\u4ea7\u4e1a\u5bf9\u5185\u5730\u3001\u5bf9\u4e1c\u5357\u4e9a\uff0c\u751a\u81f3\u65e5\u97e9\u90fd\u6709\u5f88\u5f3a\u7684\u6e17\u900f\u529b\uff0c\u4ee5\u53ca\u5728\u62b5\u6321\u65e5\u97e9\u5a31\u4e50\u4ea7\u4e1a\u5bf9\u5185\u5730\u7684\u6587\u5316\u6e17\u900f\u4e0a\uff0c\u4e5f\u53ea\u80fd\u4f9d\u91cd\u5e76\u52a0\u5f3a\u9999\u6e2f\u4e0e\u5185\u5730\u5408\u4f5c\u7684\u6a21\u5f0f\u3002</p><p>\u3000\u3000\u5341\u6708\u4e0a\u65ec\u9526\u6e56\u5546\u4e8b\u5ba3\u5e03\u5bf9\u4e16\u7eaa\u534e\u97f3\u6ce8\u8d44\u7684\u6d88\u606f\uff0c\u5728\u9999\u6e2f\u4e5f\u53ea\u6380\u8d77\u4e0d\u5927\u7684\u6ce2\u6f9c\uff0c\u4e0d\u8981\u8bf4\u4f20\u5230\u9999\u6e2f\u4e4b\u5916\u4e86\uff1b\u4f46\u662f\u9526\u6e56\u5546\u4e8b\u901a\u8fc7\u5317\u7f8e\u516c\u53f8\u5c31\u5168\u7403\u97f3\u4e50\u5728\u7ebf30\uff05\u80a1\u6743\u7684\u8f6c\u8ba9\u5411\u5fae\u8f6f\u72ee\u5b50\u5927\u5f00\u53e3\u63d0\u51fa\u4e5d\u4ebf\u7f8e\u5143\u7684\u62a5\u4ef7\u5374\u6380\u8d77\u4e86\u8f69\u7136\u5927\u6ce2\uff0c\u4e00\u4e9b\u7ecf\u6d4e\u5b66\u5bb6\u7ad9\u51fa\u6765\u76f4\u6307\u8fd9\u5c31\u662f\u5f53\u524d\u4e92\u8054\u7f51\u65b0\u7ecf\u6d4e\u6ce1\u6cab\u5316\u7684\u76f4\u89c2\u6295\u5f71\u3002</p></article>"}}}]);
