(window.webpackJsonp=window.webpackJsonp||[]).push([[942],{965:function(p){p.exports={chapterTitle:null,title:"\u7b2c939\u7ae0 \u5927\u5bb6\u90fd\u8981\u9009\u9635\u8425",content:"<article><h2>\u7b2c939\u7ae0 \u5927\u5bb6\u90fd\u8981\u9009\u9635\u8425</h2><p>\u3000\u3000\u9664\u4e86\u966a\u5510\u5a67\u53c2\u89c2\u7535\u5b50\u5c55\u4e4b\u5916\uff0c\u5f20\u606a\u5728\u4e0a\u5348\u6ca1\u6709\u5b89\u6392\u4ec0\u4e48\u884c\u7a0b\uff0c\u4ed6\u5c31\u5f88\u8212\u5fc3\u5728\u623f\u95f4\u91cc\u7761\u89c9\uff0c\u7a97\u5e18\u4e5f\u62c9\u5f97\u5bc6\u5bc6\u5b9e\u5b9e\u7684\uff0c\u4e00\u89c9\u9192\u6765\uff0c\u623f\u95f4\u91cc\u660f\u6697\u5982\u591c\uff0c\u53ea\u4e0d\u8fc7\u6000\u91cc\u591a\u4e00\u4e2a\u6e29\u9999\u6696\u7389\u7684\u4f73\u4eba\uff0c\u624b\u63e1\u4e0a\u575a\u5b9e\u5a07\u7fd8\u7684\u5c11\u5973\u6912\u4e73\uff0c\u786e\u5b9a\u8eba\u5728\u6000\u91cc\u7684\u662f\u5510\u5a67\u3002\u7fdf\u4e39\u9752\u9664\u4e86\u4e73\u623f\u9971\u6ee1\u4e30\u7855\u4e4b\u5916\uff0c\u8eab\u4f53\u4e5f\u8f83\u5510\u5a67\u7ea4\u7ec6\u7684\u5a07\u8eaf\u4e30\u76c8\u4e9b\uff0c\u6ed1\u5f39\u76ae\u8089\u5165\u624b\u6709\u4e00\u79cd\u7ef5\u8f6f\u4e4b\u611f\uff0c\u800c\u5510\u5a67\u808c\u80a4\u7684\u5f20\u529b\u66f4\u5927\u4e00\u4e9b\uff0c\u5ae9\u6ed1\u4e4b\u611f\u4e5f\u5230\u4e86\u6781\u81f4\u3002</p><p>\u3000\u3000\u5f20\u606a\u7761\u89c9\u7761\u5230\u81ea\u7136\u9192\uff0c\u81ea\u7136\u662f\u7cbe\u6c14\u5b8c\u8db3\uff0c\u4e0b\u8eab\u4e5f\u5251\u62e8\u5f29\u5f20\uff0c\u5510\u5a67\u5927\u817f\u8737\u8d77\u538b\u5728\u5f20\u606a\u7684\u5c0f\u8179\u4e0a\uff0c\u5acc\u90a3\u4e2a\u4e1c\u897f\u788d\u4e8b\uff0c\u4f38\u624b\u62e8\u5230\u4e00\u8fb9\uff0c\u201c\u55ef\u2026\u2026\u201d\u5510\u5a67\u9f3b\u8154\u91cc\u53d1\u51fa\u4e00\u58f0\u5a07\u541f\uff0c\u8eab\u5b50\u8815\u52a8\u7740\uff0c\u60f3\u627e\u4e2a\u66f4\u8212\u670d\u7684\u59ff\u52bf\u8db4\u5728\u5f20\u606a\u6000\u91cc\u3002\u5f20\u606a\u54ea\u5bb9\u5979\u8fd9\u4e48\u8212\u670d\uff0c\u5c06\u5979\u7684\u8eab\u5b50\u644a\u9648\u5728\u5e8a\u4e0a\uff0c\u501f\u7740\u5ba4\u5185\u7684\u5fae\u5149\uff0c\u51dd\u89c6\u5979\u8ff7\u4eba\u7684\u8138\u5e9e\u3002</p><p>\u3000\u3000\u201c\u7761\u89c9\u5566\uff01\u4e56\u2026\u2026\u201d\u5510\u5a67\u58f0\u97f3\u5a07\u67d4\u7684\u8bf4\u9053\uff0c\u7741\u5f00\u7684\u53cc\u773c\u53c8\u957f\u53c8\u7709\uff0c\u5728\u5e7d\u6697\u91cc\u6709\u7740\u8ff7\u79bb\u7684\u6d41\u5149\uff0c\u5f02\u5e38\u7684\u8bf1\u4eba\uff0c\u5979\u89c1\u5f20\u606a\u76ef\u7740\u5979\u7684\u7738\u5b50\u770b\u5f97\u5165\u8ff7\uff0c\u53c8\u5a07\u7f9e\u7684\u4f38\u624b\u76d6\u4f4f\u8138\uff0c\u5634\u91cc\u5a07\u55d4\u9053\uff0c\u201c\u597d\u559c\u6b22\u8eb2\u5728\u4f60\u6000\u91cc\u7684\u611f\u89c9\uff0c\u4f60\u4e0d\u8981\u60f3\u522b\u7684\u4e8b\u60c5\u54e6\uff0c\u518d\u7761\u4e00\u4f1a\u513f\uff0c\u6211\u4eec\u8fd8\u8981\u53bb\u7535\u5b50\u5c55\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u7761\u4f60\u7684\u5c31\u662f\uff0c\u6211\u73a9\u6211\u7684\u3002\u201d\u5f20\u65e0\u803b\u7684\u7b11\u7740\u8bf4\uff0c\u624b\u62d4\u5f00\u5979\u7684\u8863\u88d9\uff0c\u63c9\u629a\u7740\u5979\u633a\u79c0\u7684\u53cc\u4e73\uff0c\u4fa7\u8db4\u5728\u5979\u7684\u8eab\u4e0a\uff0c\u8138\u8d34\u7740\u5979\u4e30\u76db\u6563\u5f00\u7684\u79c0\u53d1\uff0c\u95fb\u7740\u53d1\u9645\u4f20\u6765\u7684\u9635\u9635\u5e7d\u9999\uff0c\u201c\u771f\u9999\uff0c\u95fb\u4e00\u767e\u5e74\u90fd\u4e0d\u591f\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u62e2\u8d77\u5934\uff0c\u5a07\u61a8\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u8ba9\u4f60\u8fd9\u4e2a\u5c0f\u6df7\u86cb\u95fb\uff01\u201d\u9732\u51fa\u6676\u83b9\u5982\u7389\u8033\u5eca\uff0c\u5728\u5e7d\u6697\u5149\u7ebf\u91cc\uff0c\u5fae\u5fae\u9732\u51fa\u7ec6\u8840\u7ba1\u7684\u75d5\u8ff9\u3002\u5f20\u606a\u6b63\u597d\u65b9\u4fbf\u4f38\u820c\u53bb\u8214\u5979\u7684\u8033\u5eca\uff0c\u75d2\u5f97\u5510\u5a67\u5728\u6000\u91cc\u4e71\u626d\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u6367\u4f4f\u5510\u5a67\u7cbe\u81f4\u7f8e\u4e3d\u7684\u8138\u86cb\uff0c\u543b\u4e0a\u5979\u67d4\u8f6f\u7c89\u5ae9\u7684\u6a31\u5507\uff0c\u5c06\u5979\u7684\u7259\u9f7f\u5206\u5f00\uff0c\u542e\u5438\u90a3\u8ba9\u4eba\u75f4\u72c2\u8ff7\u79bb\u7684\u4e01\u9999\u6ed1\u820c\uff0c\u76f4\u5230\u5979\u5a07\u61a8\u7ef5\u67d4\u7684\u547b\u541f\u60c5\u4e0d\u81ea\u7981\u7684\u6cc4\u51fa\u6765\uff0c\u624d\u4f38\u624b\u53bb\u6478\u5979\u7684\u4e0b\u8eab\u3002</p><p>\u3000\u3000\u5510\u5a67\u6709\u4e24\u5904\u8089\u6700\u5ae9\u6700\u6ed1\uff0c\u4e00\u662f\u5979\u7684\u4e01\u9999\u820c\u5c16\uff0c\u4e00\u662f\u5979\u5927\u817f\u5185\u4fa7\u7684\u808c\u7406\uff0c\u4e5f\u4f7f\u4ed6\u559c\u6b22\u5c06\u624b\u6307\u538b\u7740\u5510\u5a67\u7684\u79c1\u5904\u5927\u817f\u5185\u4fa7\u7684\u808c\u80a4\u8f7b\u8f7b\u7684\u6469\u6332\uff0c\u4eab\u53d7\u90a3\u8bf1\u4eba\u4e4b\u6781\u7684\u89e6\u611f\uff0c\u6478\u5f97\u5510\u5a67\u4e0b\u8eab\u6e17\u51fa\u7684\u6d25\u6db2\u6e7f\u7c98\u7c98\u7684\u6d78\u900f\u8584\u8584\u7684\u68c9\u8d28\u5185\u88e4\uff0c\u5c11\u5973\u7684\u8eab\u4f53\u5728\u5fae\u5fae\u7684\u98a4\u6296\uff0c\u5f20\u606a\u8fd9\u624d\u8131\u4e0b\u4e24\u4eba\u7684\u5185\u88e4\uff0c\u5c06\u6da8\u5f97\u96be\u53d7\u7684\u4e0b\u8eab\u4ece\u5145\u5206\u6e7f\u6da6\u7684\u5507\u4e0a\u6311\u78e8\u4e86\u4e24\u4e0b\u5c31\u76f4\u6233\u8fdb\u53bb\uff0c\u53ea\u89c9\u7ed9\u4e00\u5708\u6e29\u70ed\u6e7f\u6ed1\u7684\u5ae9\u8089\u7d27\u7d27\u7684\u7b8d\u4f4f\uff0c\u5148\u4e00\u52b2\u7684\u5c06\u5510\u5a67\u9001\u4e0a\u4e91\u7aef\uff0c\u7a0d\u6b47\u4e00\u4f1a\u513f\uff0c\u7b49\u5979\u7684\u654f\u611f\u7a0d\u7f13\u89e3\u4e9b\uff0c\u624d\u6367\u7740\u5979\u7684\u8138\u86cb\uff0c\u6df1\u60c5\u7684\u51dd\u671b\u5979\u8eab\u5904\u4e91\u7aef\u7684\u8ff7\u4eba\u5a07\u7f8e\uff0c\u808c\u80a4\u90fd\u900f\u7740\u8bf1\u4eba\u7684\u7c89\u7ea2\uff0c\u773c\u795e\u65e2\u75f4\u53c8\u5a07\uff0c\u5341\u5206\u7684\u60f9\u4eba\u601c\u7231\uff0c\u95ee\u5979\uff1a\u201c\u600e\u4e48\u4f1a\u7761\u8fc7\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u7fdf\u59d0\u5f88\u65e9\u8d77\u5e8a\u5c31\u8981\u53bb\u529e\u4e8b\uff0c\u6211\u8fd8\u6ca1\u6709\u7761\u591f\u554a\uff0c\u53ea\u80fd\u5230\u4f60\u6000\u91cc\u6765\u518d\u8865\u4e00\u89c9\u554a\u3002\u201d\u5510\u5a67\u624b\u6478\u5f20\u606a\u7684\u5fae\u523a\u624b\u7684\u4e0b\u5df4\uff0c\u8f7b\u8f7b\u7684\u5728\u80e1\u5b50\u6e23\u4e0a\u78e8\u6765\u78e8\u53bb\uff0c\u8eab\u4f53\u8fd8\u662f\u654f\u611f\u5f97\u5389\u5bb3\uff0c\u4e0d\u4ec5\u8981\u5939\u8d77\u817f\u8ba9\u8fd9\u6d51\u7403\u7684\u62bd\u52a8\u518d\u6162\u4e00\u4e9b\uff0c\u201c\u5374\u53c8\u843d\u5165\u4f60\u7684\u9b54\u638c\u4e86\uff0c\u771f\u662f\u5f97\u4e0d\u507f\u5931\u3002\u201d</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u542c\u89c1\u5916\u5c4b\u95e8\u88ab\u6253\u5f00\u7684\u58f0\u97f3\uff0c\u53ea\u6709\u7fdf\u4e39\u9752\u8fd8\u6709\u5957\u623f\u7684\u95e8\u5361\uff0c\u9ad8\u8ddf\u978b\u5728\u5927\u7406\u77f3\u94fa\u9648\u7684\u5730\u9762\u4e0a\u53d1\u51fa\u6765\u7a7a\u7a7a\u7684\u56de\u54cd\uff0c\u5510\u5a67\u7d27\u5f20\u7684\u62b1\u7d27\u5f20\u606a\u7684\u80cc\u810a\uff0c\u5728\u4ed6\u8033\u8fb9\u8f7b\u58f0\u7684\u8bf4\uff1a\u201c\u4e0d\u8981\u52a8\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6bd4\u5510\u5a67\u66f4\u62c5\u5fc3\u7ed9\u7fdf\u4e39\u9752\u542c\u5230\u58f0\u97f3\uff0c\u5c06\u5510\u5a67\u8d64\u88f8\u9999\u8273\u7684\u5a07\u8eaf\u538b\u5728\u8eab\u4e0b\uff0c\u4e5f\u4e0d\u8ba9\u5979\u6323\u624e\u4e71\u52a8\uff0c\u5c06\u90a3\u6839\u76f4\u6233\u6233\u7684\u73a9\u827a\u513f\u5374\u8fd8\u7559\u5728\u5979\u6e29\u70ed\u6e7f\u6ed1\u7684\u4f53\u5185\uff0c\u542c\u5230\u7fdf\u4e39\u9752\u5728\u5916\u95f4\u7ffb\u6587\u4ef6\u7684\u58f0\u97f3\uff0c\u5728\u5916\u95f4\u803d\u6401\u4e86\u7247\u523b\u5c31\u79bb\u5f00\u4e86\uff0c\u4e0d\u8fc7\u4ece\u9ad8\u8ddf\u978b\u8e0f\u5730\u7684\u58f0\u97f3\u91cc\u8fd8\u662f\u542c\u51fa\u6709\u4e9b\u8bb8\u72b9\u8c6b\u4e0e\u4e0d\u51b3\u6765\u3002</p><p>\u3000\u3000\u9163\u7545\u6dcb\u6f13\u7684\u6027\u4e8b\u8fc7\u540e\uff0c\u7cbe\u6c14\u6ee1\u8db3\uff0c\u5f20\u606a\u8d64\u8eab\u88f8\u8eab\u7684\u4e0b\u4e86\u5e8a\u62c9\u5f00\u7a97\u5e18\uff0c\u8ba9\u8000\u773c\u7684\u9633\u5149\u8086\u65e0\u5fcc\u60ee\u7684\u503e\u6cc4\u8fdb\u6765\uff0c\u772f\u773c\u770b\u8fd9\u65e5\u5934\uff0c\u5dee\u4e0d\u591a\u6709\u4e5d\u70b9\u949f\u4e86\uff0c\u5510\u5a67\u62ff\u7740\u88ab\u5355\u88f9\u4f4f\u5a07\u8eaf\uff0c\u7ed9\u7a81\u7136\u6d8c\u8fdb\u623f\u95f4\u91cc\u7684\u5149\u660e\u7729\u7740\u773c\u775b\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u513f\u624d\u9002\u5e94\u8fc7\u6765\uff0c\u7a97\u5916\u662f\u6ce2\u5149\u7cbc\u7cbc\u7684\u6e5b\u84dd\u5927\u6d77\uff0c\u98ce\u5149\u5947\u7f8e\u65e0\u6bd4\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5510\u5a67\u8d76\u7d27\u6d17\u6fa1\uff0c\u968f\u4fbf\u627e\u4e86\u4e9b\u4e1c\u897f\u586b\u4e86\u586b\u809a\u5b50\uff0c\u5c31\u4e00\u8d77\u4e0b\u4e86\u697c\u5230\u4e3b\u4f1a\u573a\u53bb\u770b\u7535\u5b50\u5c55\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9999\u6e2f\u79cb\u5b63\u7535\u5b50\u5c55\u662f\u4e9a\u6d32\u89c4\u6a21\u6700\u5927\u7684\u7535\u5b50\u4ea7\u54c1\u5c55\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u7275\u624b\u8d70\u8fc7\u6765\uff0c\u4e3b\u5c55\u9986\u524d\u5df2\u7ecf\u4eba\u5934\u6512\u52a8\u4e86\uff0c\u6709\u8bb8\u591a\u8863\u7740\u8003\u7a76\u3001\u795e\u60c5\u5e84\u91cd\u7684\u6b27\u7f8e\u4eba\u5728\u5176\u4e2d\u3002\u95e8\u53e3\u6709\u8bb8\u591a\u53c2\u5c55\u4f01\u4e1a\u7684\u5458\u5de5\u5728\u5206\u53d1\u5ba3\u4f20\u5355\uff0c\u4e5f\u6709\u7231\u8fbe\u7684\u5458\u5de5\u5728\u90a3\u91cc\u5206\u53d1\u5ba3\u4f20\u73af\u4fdd\u888b\u3002\u53c2\u5c55\u7684\u4e8b\u52a1\u90fd\u662f\u4ea4\u7ed9\u5e02\u573a\u90e8\u5206\u4e0e\u6d77\u5916\u516c\u53f8\u6765\u8d1f\u8d23\uff0c\u9664\u4e86\u5f20\u606a\u6e38\u624b\u597d\u95f2\u3001\u65e0\u6240\u4e8b\u4e8b\u4e4b\u5916\uff0c\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u53ea\u80fd\u5076\u5c14\u62bd\u65f6\u95f4\u8fc7\u6765\u770b\u4e24\u773c\uff0c\u5177\u4f53\u7684\u4e8b\u60c5\u662f\u4e0d\u4f1a\u8fc7\u95ee\u7684\u2014\u2014\u770b\u4e24\u773c\u4e5f\u662f\u66f4\u5173\u5fc3\u5176\u4ed6\u516c\u53f8\u63a8\u51fa\u7684\u65b0\u6280\u672f\u3001\u65b0\u4ea7\u54c1\uff0c\u9690\u85cf\u5728\u7535\u5b50\u5c55\u5e55\u540e\u7684\u5408\u7eb5\u8fde\u6a2a\u624d\u662f\u4ed6\u4eec\u8868\u6f14\u7684\u821e\u53f0\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u9999\u6e2f\u7684\u60a0\u95f2\u65f6\u5149\u4e5f\u4e0d\u591a\uff0c\u4eca\u5929\u4e0b\u5348\u5230\u660e\u5929\u4e58\u98de\u673a\u79bb\u5f00\u9999\u6e2f\u4e4b\u524d\u7684\u65e5\u7a0b\u90fd\u5b89\u6392\u5f97\u975e\u5e38\u5bc6\uff0c\u4e0d\u8fc7\u6709\u4e9b\u5b89\u6392\u53ef\u4ee5\u5e26\u5510\u5a67\u4e00\u8d77\u53c2\u52a0\uff0c\u5012\u4e0d\u81f3\u4e8e\u51b7\u843d\u4e86\u5979\u3002</p><p>\u3000\u3000\u521b\u65b0\u662f\u7535\u5b50\u5c55\u6c38\u6052\u7684\u4e3b\u9898\uff0c\u5404\u5927\u5382\u5546\u901a\u5e38\u90fd\u559c\u6b22\u5728\u5c55\u4f1a\u4e0a\u505c\u51fa\u6700\u65b0\u7684\u6280\u672f\u6210\u679c\u6765\u8d5a\u8db3\u89c2\u4f17\u4e0e\u5a92\u4f53\u7684\u773c\u7403\u3002\u8fd9\u51e0\u5e74\u662f\u6a21\u62df\u7535\u5b50\u6280\u672f\u5411\u6570\u5b57\u7535\u5b50\u6280\u672f\u6f14\u8fdb\u7684\u5173\u952e\u51e0\u5e74\uff0c\u7535\u5b50\u5c55\u4e0a\uff0c\u8bb8\u591a\u516c\u53f8\u4e5f\u5c55\u793a\u4e86\u4ed6\u4eec\u6700\u65b0\u7684\u6570\u5b57\u6280\u672f\u7814\u7a76\u6210\u679c\uff0c\u6570\u5b57\u901a\u8baf\u3001\u6570\u5b57\u89c6\u542c\u3001\u6570\u5b57\u5bb6\u7535\u7b49\u8bf8\u591a\u5728\u4e5d\u516b\u5e74\u8fd8\u53ea\u80fd\u7b97\u662f\u65b0\u6982\u5ff5\u7684\u4ea7\u54c1\u5728\u5c55\u4f1a\u4e0a\u81ea\u7136\u662f\u5927\u653e\u5149\u5f69\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u5176\u4ed6\u6d77\u5916\u7535\u5b50\u5de8\u5934\u7684\u5c55\u9986\uff0c\u7231\u8fbe\u7684\u5c55\u9986\u867d\u7136\u8bf4\u4f4d\u5b50\u4e5f\u6781\u4f73\u3001\u5c55\u533a\u7684\u5f62\u8c61\u9c9c\u4e3d\u593a\u773c\uff0c\u4f46\u662f\u80fd\u62ff\u5f97\u51fa\u624b\u7684\u4e1c\u897f\u5b9e\u5728\u662f\u592a\u6709\u9650\u4e86\u3002</p><p>\u3000\u3000\u7d22\u5c3c\u3001\u4e1c\u829d\u3001\u4e09\u661f\u7b49\u65e5\u97e9\u7535\u5b50\u4f01\u4e1a\u5c55\u793a\u51fa\u6765\u7684\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u54c1\u79cd\u5219\u53ef\u4ee5\u8bf4\u662f\u7433\u7405\u6ee1\u76ee\u3001\u7cbe\u5f69\u7eb7\u5448\u3002\u7d22\u5c3c\u5bf9DMP\u9635\u8425\u4e5f\u975e\u6ca1\u6709\u4e00\u70b9\u8b66\u60d5\uff0c\u7d22\u5c3c\u8fd9\u6b21\u501f\u7535\u5b50\u5c55\u63a8\u51fa\u597d\u51e0\u6b3e\u4fbf\u643a\u5f0f\u97f3\u4e50\u64ad\u653e\u5668\u7684\u65b0\u54c1\uff0c\u4e5f\u6709\u96c6\u6210\u4e86\u6570\u5b57\u6536\u97f3\u673a\u529f\u80fd\u7684MD-walkman\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u975e\u5e38\u6e05\u695a\u65e5\u7cfb\u7535\u5b50\u5382\u5546\u7684\u8f6f\u808b\uff0c\u7d22\u5c3c\u5b81\u53ef\u72ec\u7acb\u5f00\u53d1\u3001\u63a8\u5e7f\u8bb0\u5fc6\u68d2\u4ea7\u54c1\uff0c\u4e5f\u4e0d\u60f3\u5bf9\u4e1c\u829d\u7684\u95ea\u5b58\u6280\u672f\u4fef\u9996\u79f0\u81e3\uff0c\u8fd9\u79cd\u50b2\u6162\u7684\u4fdd\u5b88\u59ff\u6001\u53ef\u4e0d\u662f\u4e2a\u522b\u6709\u8fdc\u89c1\u7684\u7ba1\u7406\u4eba\u5458\u80fd\u591f\u626d\u8f6c\u7684\uff0c\u76f8\u53cd\u7684\uff0c\u6b64\u65f6\u770b\u4e0a\u53bb\u8fd8\u4e0d\u591f\u5f3a\u5927\u7684\u4e09\u661f\u5219\u8981\u7075\u6d3b\u591a\u53d8\u8bb8\u591a\u3001\u4e5f\u66f4\u8ba9\u4eba\u751f\u754f\u3002</p><p>\u3000\u3000\u590f\u666e\u5728\u7535\u5b50\u5c55\u4e0a\u5c55\u51fa\u4ed6\u4eec\u6700\u65b0\u7684\u7b2c\u56db\u4ee3\u8584\u819c\u6db2\u6676\u663e\u793a\u6280\u672f\uff0c\u9ad8\u6e05\u7535\u89c6\u65f6\u4ee3\u6b64\u65f6\u5df2\u7ecf\u80fd\u770b\u5230\u4e9b\u8eab\u5f71\u4e86\uff0c\u677e\u4e0b\u4e5f\u5c55\u51fa\u4ed6\u4eec\u5728\u7b49\u79bb\u5b50\u4f53\u663e\u793a\u6280\u672f\u65b9\u9762\u7684\u6700\u65b0\u6210\u679c\uff0c\u800c\u4e1c\u829d\u4e0e\u7d22\u5c3c\u5219\u5c06\u6280\u672f\u529b\u91cf\u96c6\u4e2d\u5728\u5c04\u9891\u663e\u793a\u6280\u672f\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u7136\u6e05\u695a\u9526\u6e56\u5728\u672a\u6765\u7684\u9ad8\u6e05\u663e\u793a\u6280\u672f\u5e02\u573a\u8981\u9009\u62e9\u54ea\u4e2a\u9635\u8425\uff0c\u53ef\u60dc\u8fd9\u65f6\u5019\u51e0\u5927\u9635\u8425\u90fd\u662f\u65e5\u7cfb\u7535\u5b50\u5382\u5546\u7684\u5929\u4e0b\uff0c\u4eba\u5bb6\u8fd8\u4e0d\u4e50\u610f\u5e26\u4e0a\u9526\u6e56\u4e00\u8d77\u73a9\u554a\uff0c\u5f20\u606a\u53ea\u80fd\u671f\u5f85\u5df2\u7ecf\u83b7\u5f97\u8584\u819c\u6db2\u6676\u663e\u793a\u6280\u672f\u7684\u97e9\u7cfb\u7535\u5b50\u5382\u5546\u5728\u8fd9\u6b21\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4e2d\u53d7\u632b\u4e25\u91cd\u4e00\u4e9b\u3001\u518d\u4e25\u91cd\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5e02\u573a\u90e8\u95e8\u4f1a\u4e13\u95e8\u6d3e\u4eba\u8fdb\u884c\u65b0\u6280\u672f\u3001\u65b0\u4ea7\u54c1\u7684\u60c5\u62a5\u641c\u7d22\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u53ea\u6311\u81ea\u5df1\u611f\u5174\u8da3\u7684\u53c2\u89c2\uff0c\u5728\u7433\u7405\u6ee1\u76ee\u7684\u5c55\u533a\u91cc\uff0c\u53ea\u4f1a\u89c9\u5f97\u65f6\u5149\u6613\u901d\u96be\u7559\uff0c\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u611f\u89c9\uff0c\u5df2\u7ecf\u662f\u4e2d\u5348\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5510\u5a67\u56de\u5230\u9152\u5e97\u7684\u9910\u5385\u91cc\u7528\u9910\uff0c\u8096\u664b\u6210\u8fc7\u6765\u6c47\u62a5\u4e0a\u5348\u4e0e\u6b27\u7f8e\u5ba2\u6237\u63a5\u89e6\u7684\u4e00\u4e9b\u60c5\u51b5\u3002\u6b64\u65f6\u7684\u9526\u6e56\u5728iplayer\u4e0e\u95ea\u5b58\u76d8\u7b49\u4ea7\u54c1\u6709\u5f88\u5927\u7684\u964d\u4ef7\u7a7a\u95f4\uff0c\u4f46\u4e0d\u4f1a\u6025\u7740\u964d\u4ef7\u4fc3\u9500\uff0c\u5bf9\u4e8e\u6b27\u6d32\u3001\u5317\u7f8e\u5730\u533a\u7684\u5168\u65b0\u5e02\u573a\uff0c\u4e0e\u5176\u964d\u4ef7\u4fc3\u9500\uff0c\u8fd8\u4e0d\u5982\u5c06\u5229\u6da6\u7a7a\u95f4\u8ba9\u7ed9\u7ecf\u9500\u5546\u8fd8\u6709\u6548\u4e9b\u3002</p><p>\u3000\u3000\u4e1c\u829d\u65e9\u5df2\u5728\u5168\u7403\u5e02\u573a\u5efa\u7acb\u4e86\u5b8c\u5584\u7684\u8425\u9500\u4f53\u7cfb\uff0c\u81ea\u7136\u4e0d\u9700\u8981\u8fd9\u79cd\u4f0e\u4fe9\u6765\u8ba8\u597d\u7ecf\u9500\u5546\uff0c\u9526\u6e56\u5374\u662f\u9762\u76ee\u964c\u751f\u7684\u65b0\u95ef\u5165\u8005\uff0c\u867d\u8bf4\u5728\u6b27\u6d32\u5317\u7f8e\u65b0\u8bbe\u7acb\u7684\u5e02\u573a\u90e8\u95e8\u5df2\u7ecf\u5f00\u59cb\u7740\u624b\u5efa\u5206\u9500\u4f53\u7cfb\u7684\u4e8b\u60c5\uff0c\u4f46\u8981\u90a3\u4e9b\u5206\u9500\u5546\u3001\u7ecf\u9500\u5546\u4eec\u8ba4\u771f\u7684\u534f\u52a9\u9526\u6e56\u6253\u5f00\u5f53\u5730\u7684\u5e02\u573a\uff0c\u51b3\u5b9a\u6027\u56e0\u7d20\u8fd8\u662f\u8981\u770b\u9526\u6e56\u7684\u4ea7\u54c1\u54c1\u80fd\u4e0d\u80fd\u7ed9\u4ed6\u4eec\u5e26\u53bb\u8db3\u591f\u7684\u5229\u6da6\u3002</p><p>\u3000\u3000\u597d\u5427\uff0cDMP-iplayer\u6280\u672f\u6765\u81ea\u4e8e\u7231\u8fbe\uff0c\u7231\u8fbe\u7684\u4ea7\u54c1\u8d28\u91cf\u4e0d\u6bd4\u4e1c\u829d\u3001\u65af\u9ad8\u67cf\u7a0d\u5dee\uff0c\u4e5f\u5c55\u51fa\u6bd4\u4e1c\u829d\u3001\u65af\u9ad8\u67cf\u66f4\u5177\u6280\u672f\u5b9e\u529b\u7684iplayer\u9ad8\u7aef\u4ea7\u54c1\uff0c\u800c\u4e14\u7ecf\u9500\u5546\u80fd\u4ece\u9526\u6e56\u7684\u4ea7\u54c1\u91cc\u83b7\u5f97\u6570\u500d\u4e8e\u4e1c\u829d\u3001\u65af\u9ad8\u67cf\u4ea7\u54c1\u5e26\u7ed9\u4ed6\u4eec\u7684\u5229\u6da6\uff0c\u4ed6\u4eec\u7684\u9009\u62e9\u662f\u663e\u800c\u6613\u89c1\u7684\u3002</p><p>\u3000\u3000\u4e0e\u827e\u9ed8\u7ea6\u5b9a\u4e09\u4e94\u4e2a\u6708\u7684\u7f13\u51b2\u671f\uff0c\u4e09\u4e94\u4e2a\u6708\u7684\u65f6\u95f4\u60f3\u5f7b\u5e95\u64ac\u5f00\u6b27\u6d32\u3001\u5317\u7f8e\u5730\u533a\u7684\u5e02\u573a\u662f\u5f88\u56f0\u96be\u7684\uff0c\u4f46\u662f\u6709\u8fd9\u4e09\u4e94\u4e2a\u6708\u7684\u7f13\u51b2\u671f\uff0c\u5efa\u7acb\u4e00\u4e2a\u76f8\u5bf9\u5b8c\u5584\u7684\u5206\u9500\u4f53\u7cfb\uff0c\u65f6\u95f4\u4e0a\u4e0d\u4f1a\u7279\u522b\u7684\u6025\u8feb\u2014\u2014\u8fd9\u662f\u9526\u6e56\u5728\u6b27\u6d32\u3001\u5317\u7f8e\u5e02\u573a\u7684\u521d\u6b65\u76ee\u6807\uff0c\u9526\u6e56\u7684\u5c55\u533a\u4e0a\u5348\u5438\u5f15\u4e86\u5f88\u591a\u65b0\u7684\u6b27\u7f8e\u5ba2\u6237\uff0c\u6001\u5ea6\u4e4b\u76f4\u63a5\u3001\u70ed\u60c5\uff0c\u8feb\u4f7f\u8096\u664b\u6210\u4e0b\u5348\u4e0d\u5f97\u4e0d\u4eb2\u81ea\u51fa\u9762\uff0c\u826f\u597d\u7684\u5f00\u7aef\uff0c\u9884\u793a\u7740\u8fd9\u4e2a\u76ee\u6807\u79bb\u9526\u6e56\u4e0d\u518d\u9065\u8fdc\u4e86\u3002</p><p>\u3000\u3000\u79bb\u5f00\u9910\u5385\u91cc\uff0c\u9047\u5230\u4e1c\u829d\u7684\u5c97\u6751\u6728\u7b49\u4eba\uff0c\u5c97\u6751\u6728\u6253\u62db\u547c\u7684\u59ff\u6001\u6bd4\u8d77\u6628\u65e5\u6765\u5c31\u6709\u4e9b\u751f\u786c\uff0c\u5f20\u606a\u5fc3\u91cc\u60f3\uff1a\u5927\u6982\u4ed6\u4e0a\u5348\u4e0e\u9648\u4fe1\u751f\u89c1\u9762\u77e5\u9053\u8fd9\u8fb9\u7684\u5f00\u4ef7\u4e86\uff0c\u8981\u4e3a\u5168\u7403\u97f3\u4e50\u5728\u7ebf20\uff05\u7684\u6743\u76ca\u652f\u4ed8\u56db\u5343\u4e07\u7f8e\u5143\uff0c\u5fc3\u91cc\u591a\u5c11\u4f1a\u6709\u4e9b\u4e0d\u75db\u5feb\u2014\u2014\u5373\u4f7f\u5927\u5bb6\u90fd\u662f\u6813\u5728\u540c\u4e00\u6839\u7ef3\u4e0a\u7684\u8682\u86b1\u3002</p></article>"}}}]);
