(window.webpackJsonp=window.webpackJsonp||[]).push([[944],{967:function(p){p.exports={chapterTitle:null,title:"\u7b2c760\u7ae0 \u53eb\u7834\u5589\u5499",content:"<article><h2>\u7b2c760\u7ae0 \u53eb\u7834\u5589\u5499</h2><p>\u3000\u3000\u5218\u658c\u6025\u5207\u8981\u8ddf\u5f20\u606a\u89c1\u9762\uff0c\u662f\u4ed6\u4ece\u7fdf\u4e39\u9752\u90a3\u91cc\u542c\u5230\u65b0\u829c\u4f9d\u6258\u5efa\u90ba\u7b79\u5212\u914d\u5957\u4ea7\u4e1a\u56ed\u7684\u8bbe\u60f3\u3002\u5f53\u7136\u4e86\uff0c\u5f20\u606a\u5230\u65b0\u829c\u6765\uff0c\u4ed6\u65e0\u8bba\u5982\u4f55\u90fd\u662f\u60f3\u7740\u8fc7\u6765\u89c1\u9762\u7684\uff0c\u53ea\u662f\u4e0d\u4f1a\u8fd9\u4e48\u8feb\u5207\u3002</p><p>\u3000\u3000\u7528\u9910\u65f6\uff0c\u5218\u658c\u4e5f\u6ca1\u6709\u906e\u906e\u63a9\u63a9\uff0c\u996d\u684c\u4e0a\u5bd2\u6684\u65f6\uff0c\u5c31\u76f4\u63a5\u63d0\u51fa\u65b0\u829c\u8981\u641e\u914d\u5957\u4ea7\u4e1a\u56ed\u4ed6\u5f88\u613f\u610f\u8d21\u732e\u4e00\u5206\u529b\u91cf\u3002</p><p>\u3000\u3000\u914d\u5957\u4ea7\u4e1a\u56ed\u8fd9\u65f6\u5019\u8fd8\u5904\u4e8e\u915d\u917f\u9636\u6bb5\uff0c\u5f20\u606a\u6653\u5f97\u7fdf\u4e39\u9752\u80fd\u5728\u65b0\u829c\u8fd9\u4e48\u6076\u52a3\u7684\u73af\u5883\u4e2d\u6323\u624e\u7740\u751f\u5b58\u4e0b\u6765\uff0c\u5218\u658c\u5bf9\u5979\u7684\u5e2e\u52a9\u5f88\u5927\uff0c\u8981\u6709\u4ec0\u4e48\u597d\u4e8b\uff0c\u5979\u5148\u5c06\u5218\u658c\u8003\u8651\u8fdb\u53bb\u4e5f\u662f\u5f53\u7136\u3002\u5f53\u7136\uff0c\u8981\u505a\u6210\u4e00\u4ef6\u4e8b\uff0c\u5c06\u66f4\u591a\u6709\u7740\u4e00\u81f4\u7684\u5229\u76ca\u8bc9\u6c42\u7684\u52bf\u529b\u7ea0\u96c6\u5230\u4e00\u8d77\uff0c\u65e0\u7591\u4f1a\u4e8b\u534a\u529f\u500d\uff0c\u7fdf\u4e39\u9752\u7684\u601d\u8def\u662f\u5bf9\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u4f1a\u62d2\u7edd\u5218\u658c\u7684\u8981\u6c42\uff0c\u53ea\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8fd8\u662f\u4ee5\u5e02\u91cc\u4e3a\u4e3b\uff0c\u9526\u6e56\u4e0d\u4f1a\u62a2\u4ec0\u4e48\u98ce\u5149\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u98ce\u5149\u597d\u62a2\uff0c\u5927\u5174\u96c6\u56e2\u6700\u8fd1\u5728\u65b0\u829c\u86ee\u53d7\u5e02\u91cc\u5668\u91cd\u7684\uff0c\u6211\u770b\u658c\u8001\u5927\u8fd8\u8981\u591a\u8ddf\u5218\u7235\u5e02\u957f\u6c9f\u901a\u6c9f\u901a\u2026\u2026\u201d</p><p>\u3000\u3000\u5218\u658c\u662f\u4e86\u89e3\u5f97\u8d8a\u6df1\u8d8a\u89c9\u5f97\u5f20\u606a\u7684\u5353\u8d8a\u4e0d\u51e1\uff0c\u9526\u6e56\u80cc\u540e\u7ea0\u96c6\u7684\u6743\u52bf\u662f\u5916\u4eba\u96be\u4ee5\u60f3\u8c61\u7684\uff0c\u65b0\u829c\u8981\u4f9d\u6258\u5efa\u90ba\u529e\u597d\u914d\u5957\u4ea7\u4e1a\u56ed\uff0c\u6ca1\u6709\u9526\u6e56\u5728\u80cc\u540e\u63a8\u52a8\uff0c\u5f97\u4e0d\u5230\u5efa\u90ba\u5e02\u7684\u652f\u6301\uff0c\u5f88\u96be\u60f3\u8c61\u6240\u8c13\u7684\u914d\u5957\u4ea7\u4e1a\u56ed\u8981\u71ac\u591a\u5c11\u5e74\u624d\u80fd\u6210\u89c4\u6a21\u3002\u5927\u5174\u96c6\u56e2\u60f3\u8981\u53c2\u4e0e\u5176\u4e2d\uff0c\u4e0e\u6c5f\u4e0a\u5143\u3001\u5218\u7235\u751a\u81f3\u5f20\u77e5\u884c\u6c9f\u901a\u90fd\u662f\u5fc5\u7136\u7684\uff0c\u4f46\u662f\u5148\u5f97\u5230\u5f20\u606a\u7684\u9996\u80af\uff0c\u5176\u4ed6\u590d\u6742\u7684\u4e8b\u60c5\u90fd\u4f1a\u53d8\u5f97\u7b80\u5355\u6613\u884c\u3002</p><p>\u3000\u3000\u8fd9\u4ef6\u4e8b\u4f1a\u6d89\u53ca\u5230\u65b9\u65b9\u9762\u9762\u7684\u5173\u7cfb\uff0c\u6d89\u53ca\u5230\u65b0\u829c\u5e02\u91cc\u662f\u80af\u5b9a\u7684\uff0c\u8fd8\u4f1a\u6d89\u53ca\u5230\u5efa\u90ba\u5e02\u4e0e\u76f8\u5173\u884c\u4e1a\u4e3b\u7ba1\u90e8\u95e8\u3002\u5f20\u606a\u8ba9\u7fdf\u4e39\u9752\u4ee3\u8868\u9526\u6e56\u51fa\u9762\u63a8\u52a8\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u5c31\u662f\u8bf4\u7fdf\u4e39\u9752\u4ee5\u79c1\u4eba\u52a9\u7406\u6216\u4ee3\u7406\u4eba\u7684\u8eab\u4efd\u4e0e\u65b9\u65b9\u9762\u9762\u7684\u5173\u7cfb\u7f51\u63a5\u89e6\uff0c\u90a3\u53ef\u4ee5\u8bf4\u662f\u7fdf\u4e39\u9752\u6b63\u5f0f\u5728\u9526\u6e56\u4f53\u7cfb\u5185\u83b7\u5f97\u4e00\u4e2a\u7279\u6b8a\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u5218\u658c\u8fd9\u65f6\u5019\u5374\u5728\u60f3\uff0c\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u4e4b\u95f4\u5230\u5e95\u6700\u540e\u8c01\u80fd\u5403\u6389\u8c01\u8fd9\u6837\u7684\u95ee\u9898\uff0c\u62e5\u6709\u65e0\u4e0a\u6743\u52bf\u7684\u7537\u4eba\u4e0e\u7edd\u8272\u53c8\u98ce\u60c5\u65e0\u6bd4\u7684\u5973\u4eba\u6c38\u8fdc\u662f\u8fd9\u4e2a\u4e16\u754c\u7684\u4e3b\u89d2\u3002</p><p>\u3000\u3000\u7528\u8fc7\u9910\u3001\u5f97\u5230\u60f3\u8981\u7684\u7b54\u590d\uff0c\u5218\u658c\u4e5f\u4e0d\u4f1a\u8ba8\u4eba\u5acc\u7684\u8fd8\u7559\u5728\u5c71\u4e0a\uff0c\u5c31\u5148\u4e0b\u5c71\u53bb\u3002</p><p>\u3000\u3000\u5510\u5a67\u70ed\u60c5\u7684\u5c06\u7fdf\u4e39\u9752\u633d\u7559\u4e0b\u6765\uff0c\u5f20\u606a\u53ea\u5f97\u5728\u5fc3\u4e2d\u60b2\u9e23\u3002</p><p>\u3000\u3000\u591c\u91cc\u5750\u8239\u56de\u5c9b\u4e0a\u53bb\uff0c\u7565\u6709\u4e9b\u7f3a\u7684\u6708\u4eae\u60ac\u5728\u6e5b\u84dd\u8272\u7684\u7a7a\u4e2d\uff0c\u6e56\u9762\u5c31\u50cf\u4e00\u9762\u5e7d\u6697\u7684\u955c\u5b50\u5d4c\u5728\u5c71\u5ce6\u4e4b\u95f4\uff0c\u5012\u6620\u7740\u7684\u7fa4\u661f\u4e0e\u660e\u6708\u6709\u5982\u8d34\u4e0a\u53bb\u7684\u95ea\u95ea\u53d1\u5149\u7684\u4eae\u7247\uff0c\u5750\u5728\u6c34\u53f0\u7684\u5e06\u5e03\u6905\u4e0a\uff0c\u5439\u7740\u51ac\u5b63\u5e76\u4e0d\u5bd2\u51b7\u7684\u5fae\u98ce\uff0c\u5f20\u606a\u4e5f\u5c31\u60f3\u4ec0\u4e48\u90fd\u4e0d\u60f3\u7684\u6367\u4e00\u676f\u5496\u5561\u5750\u5728\u6e56\u8fb9\u5446\u5446\u7684\u770b\u7740\u8fd9\u6e56\u5149\u5c71\u8272\uff0c\u8fdc\u5904\u7684\u5c71\u810a\u53ea\u7559\u4e0b\u9ed1\u9ee2\u9ee2\u7684\u6837\u5b50\u3002</p><p>\u3000\u3000\u201c\u5c31\u6653\u5f97\u4ed6\u8fd9\u4e2a\u4eba\u6ca1\u4e8b\u559c\u6b22\u5750\u5728\u90a3\u91cc\u53d1\u5446\uff0c\u8981\u4e0d\u662f\u4f60\u7559\u4e0b\u6765\uff0c\u5c31\u6211\u8ddf\u8fd9\u4e2a\u5bb6\u4f19\u5728\u5c9b\u4e0a\uff0c\u90a3\u4e00\u5b9a\u4f1a\u95f7\u6b7b\u3002\u201d\u5510\u5a67\u626f\u7740\u5f20\u606a\u7684\u8033\u6735\u8ddf\u7fdf\u4e39\u9752\u62b1\u6028\u3002</p><p>\u3000\u3000\u5f20\u606a\u671d\u5510\u5a67\u5472\u7259\u54a7\u5634\uff0c\u8981\u4e0d\u662f\u5c06\u7fdf\u4e39\u9752\u4e5f\u7559\u4e0b\u6765\uff0c\u5c31\u80fd\u8ba9\u8fd9\u4e2b\u5934\u5c1d\u5c1d\u53eb\u7834\u5589\u5499\u4e5f\u6ca1\u4eba\u7406\u7684\u6ecb\u5473\uff0c\u8fd9\u65f6\u5019\u5374\u4ec0\u4e48\u90fd\u505a\u4e0d\u6210\uff0c\u9664\u4e86\u770b\u6c34\u4e2d\u7684\u6708\u4eae\u53d1\u5446\uff0c\u8fd8\u80fd\u505a\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7a7f\u7740\u5e03\u62d6\u978b\u7ad9\u5728\u6c34\u53f0\u7684\u6728\u5730\u677f\u4e0a\uff0c\u8fd9\u8fb9\u7684\u6c14\u6e29\u6bd4\u5c71\u4e0b\u8981\u9ad8\u591a\u5c11\uff0c\u5979\u53ea\u7a7f\u7740\u8d34\u8eab\u7684\u7f8a\u7ed2\u886b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u6211\u4eec\u4e0d\u8981\u7406\u4ed6\u597d\u4e86\uff0c\u6211\u4eec\u53bb\u540e\u9662\u6ce1\u6e29\u6cc9\u5427\uff1f\u201d\u62c9\u7740\u5510\u5a67\u5f80\u540e\u8d70\u3002</p><p>\u3000\u3000\u5f20\u606a\u6602\u8d77\u5934\u770b\u5230\u7fdf\u4e39\u9752\u626d\u8fc7\u8eab\u53bb\u7684\u4fa7\u5f71\uff0c\u7ec6\u8170\u3001\u4e30\u80f8\u3001\u5bbd\u81c0\u7ec4\u5408\u6210\u52a8\u4eba\u5fc3\u9b44\u7684\u66f2\u7ebf\uff0c\u518d\u914d\u5408\u5979\u90a3\u5f20\u957f\u76f8\u5996\u5b7d\u7684\u7f8e\u4e3d\u8138\u86cb\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u4f1a\u8ba9\u4eba\u60f3\u8c61\u8fd9\u4e48\u4e00\u4ed8\u5a07\u8eaf\u8d64\u6761\u6761\u7684\u88f8\u9732\u51fa\u6765\u4f1a\u662f\u4f55\u7b49\u7684\u8bf1\u4eba\uff01</p><p>\u3000\u3000\u5f20\u606a\u541e\u4e86\u4e00\u53e3\u553e\u6cab\uff0c\u53c8\u4e0d\u80fd\u65e0\u803b\u5230\u8981\u6c42\u4e09\u4e2a\u4eba\u5171\u6d74\uff0c\u53ea\u80fd\u4e00\u4e2a\u4eba\u72ec\u96f6\u96f6\u7684\u5750\u5728\u6c34\u53f0\u4e0a\u53d1\u5446\u7684\u770b\u8fdc\u5904\u9ed1\u9ee2\u9ee2\u7684\u5c71\u810a\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5510\u5a67\u4e0e\u7fdf\u4e39\u9752\u5b09\u6c34\u7684\u7b11\u95f9\u58f0\u5c31\u4f20\u5230\u524d\u9762\u6765\uff0c\u5927\u6982\u5510\u5a67\u5728\u4e3a\u7fdf\u4e39\u9752\u5927\u80f8\u90e8\u611f\u6168\uff0c\u7fdf\u4e39\u9752\u8ba9\u5979\u62ff\u624b\u91cf\u4e00\u91cf\uff0c\u5510\u5a67\u4e0d\u597d\u610f\u601d\uff0c\u597d\u50cf\u53c8\u4f38\u624b\u53bb\u63e1\u4e86\uff1b\u4e24\u4e2a\u5973\u4eba\u53c8\u6bd4\u8170\u6765\u4e86\uff0c\u5510\u5a67\u8fd8\u95ee\u7fdf\u4e39\u9752\u7537\u4eba\u4f1a\u4e0d\u4f1a\u559c\u6b22\u5927\u81c0\u90e8\u7684\u5973\u4eba\uff0c\u56e0\u4e3a\u7fdf\u4e39\u9752\u7684\u81c0\u90e8\u5f88\u5bbd\u539a\uff0c\u800c\u5510\u5a67\u7684\u81c0\u90e8\u5c0f\u800c\u7fd8\uff0c\u4e24\u4e2a\u5973\u4eba\u8fd8\u4e92\u76f8\u5938\u76ae\u80a4\u597d\u3002\u5c9b\u4e0a\u591c\u6df1\u4eba\u9759\u7684\uff0c\u5f20\u606a\u90fd\u6000\u7591\u5c0f\u5c9b\u7684\u53e6\u4e00\u7aef\u6709\u4eba\u6253\u7740\u547c\u565c\u4ed6\u90fd\u80fd\u542c\u5f97\u4e00\u6e05\u4e8c\u695a\uff0c\u60f3\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48\u5973\u4eba\u5728\u4e00\u8d77\u6ce1\u6fa1\u4f1a\u8fd9\u4e48\u70ed\u95f9\uff0c\u8fd9\u611f\u89c9\u771f\u4e0d\u597d\u53d7\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u676f\u91cc\u7684\u5496\u5561\u559d\u5c3d\uff0c\u5fc3\u60f3\u5230\u5c4b\u5b50\u91cc\u518d\u51b2\u4e00\u676f\u5496\u5561\u5e94\u8be5\u7b97\u7740\u6bd4\u8f83\u6b63\u5f53\u7684\u7406\u7531\uff0c\u8fd8\u8f7b\u58f0\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u8fd8\u8981\u52a0\u70b9\u5496\u5561\uff0c\u6ca1\u5496\u5561\u559d\u5750\u5728\u5916\u9762\u53ef\u65e0\u804a\u4e86\u2026\u2026\u201d\u8fd9\u8bdd\u662f\u8bf4\u7ed9\u9b3c\u542c\u7684\u3002</p><p>\u3000\u3000\u8d70\u8fdb\u5c4b\u91cc\uff0c\u5f20\u606a\u72b9\u8c6b\u7740\u662f\u76f4\u63a5\u53bb\u653e\u6c34\u58f6\u7684\u5730\u65b9\u5012\u6c34\u51b2\u5496\u5561\uff0c\u8fd8\u662f\u518d\u5f80\u524d\u8d70\u4e24\u6b65\u3001\u8d70\u5230\u540e\u9762\u90a3\u95f4\u6253\u5f00\u7740\u95e8\u7684\u5367\u5ba4\u95e8\u53e3\u901a\u8fc7\u7a97\u6237\u5077\u5077\u6478\u6478\u7684\u770b\u4e24\u773c\u3002\u771f\u662f\u5fc3\u795e\u4ea4\u6218\u5462\uff0c\u5f80\u524d\u632a\u4e86\u4e24\u6b65\u53c8\u60f3\u7740\u8fd8\u662f\u64a4\u56de\u6765\u7b97\u4e86\uff0c\u5374\u7ed9\u4ece\u5367\u5ba4\u91cc\u8d70\u51fa\u6765\u7684\u7fdf\u4e39\u9752\u649e\u4e86\u4e2a\u6b63\u7740\u2026\u2026</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u7fdf\u4e39\u9752\u88f9\u7740\u7684\u6d74\u5dfe\u7ed9\u5f20\u606a\u649e\u677e\u8131\u4e0b\u6765\uff0c\u7fdf\u4e39\u9752\u4e5f\u6ca1\u60f3\u5230\u5f20\u606a\u65e0\u4e8b\u4f1a\u5728\u5367\u5ba4\u95e8\u53e3\u8f6c\u60a0\uff0c\u6ca1\u6709\u6765\u53ca\u5f97\u6293\u4f4f\u6d74\u5dfe\uff0c\u767d\u5f97\u8000\u773c\u7684\u5a07\u8eaf\u5c31\u8d64\u88f8\u88f8\u7684\u9732\u5728\u5f20\u606a\u7684\u9762\u524d\u3002\u7fdf\u4e39\u9752\u6700\u5148\u7684\u53cd\u5e94\u662f\u6342\u4f4f\u5634\uff0c\u786c\u751f\u751f\u7684\u5c06\u90a3\u58f0\u60ca\u547c\u6342\u5728\u5634\u91cc\u6ca1\u6709\u53d1\u51fa\u6765\uff0c\u63a5\u4e0b\u6765\u624d\u662f\u624b\u6367\u7740\u80f8\uff0c\u5c06\u80f8\u524d\u5ae3\u7ea2\u9c9c\u8273\u7684\u4e24\u70b9\u906e\u4f4f\u8e72\u4e0b\u6765\u5c06\u6d74\u5dfe\u62fe\u8d77\u6765\uff0c\u770b\u5ae3\u7ea2\u4e24\u70b9\u7684\u5c0f\u5de7\u5c31\u6653\u5f97\u7fdf\u4e39\u9752\u8fd9\u4e9b\u5e74\u8fc7\u7740\u6e05\u5fc3\u5be1\u6b32\u7684\u751f\u6d3b\uff0c\u53ea\u662f\u5f88\u597d\u5947\u4e3a\u4ec0\u4e48\u80f8\u80fd\u53d1\u80b2\u5230\u5982\u6b64\u58ee\u89c2\uff0c\u4e5f\u4e0d\u6653\u5f97\u5979\u4e0e\u97e9\u56fd\u5927\u80f8\u7f8e\u5973\u674e\u99a8\u4e88\u76f8\u6bd4\uff0c\u5230\u5e95\u54ea\u4e2a\u66f4\u64c5\u80dc\u4e00\u7b79\u3002\u7fdf\u4e39\u9752\u5f2f\u7740\u8170\u6361\u6d74\u5dfe\uff0c\u5149\u6ed1\u5982\u6708\u7684\u7ea4\u80cc\u5c55\u9732\u5728\u5f20\u606a\u7684\u773c\u524d\uff0c\u8170\u7ec6\u7ec6\u7684\u4e00\u6536\uff0c\u518d\u4e0b\u53bb\u5c31\u66f2\u7ebf\u6709\u4e9b\u5938\u5f20\u7684\u9971\u6ee1\u7684\u81c0\uff0c\u6574\u5177\u5a07\u8eaf\u51fa\u4e4e\u60f3\u8c61\u7684\u767d\u5ae9\uff0c\u5c31\u50cf\u521d\u51ac\u91ce\u5730\u5916\u7684\u7b2c\u4e00\u6367\u96ea\u3002\u6361\u8d77\u6d74\u5dfe\uff0c\u7fdf\u4e39\u9752\u7ad9\u8d77\u6765\uff0c\u6367\u5728\u624b\u91cc\u7684\u4e73\u623f\u7b80\u76f4\u8981\u6ea2\u51fa\u6765\uff0c\u4e5f\u987e\u4e0d\u5f97\u5f20\u606a\u8272\u772f\u772f\u7684\u773c\u775b\u5728\u5979\u7684\u8eab\u4e0a\u4e71\u7785\uff0c\u53ea\u662f\u8d76\u7d27\u62fe\u8d77\u6d74\u5dfe\u614c\u4e71\u7684\u5c06\u8eab\u4f53\u56f4\u4f4f\u3002\u5373\u4f7f\u56f4\u7740\u4e5f\u662f\u534a\u88f8\u7684\u7f8e\u4eba\uff0c\u8d64\u7740\u767d\u5ae9\u7684\u811a\uff0c\u811a\u8dbe\u5934\u6d82\u7740\u7c89\u7ea2\u7684\u6307\u7532\u6cb9\uff0c\u4e00\u5bf9\u633a\u7acb\u7684\u767d\u4e73\u7ed9\u80e1\u4e71\u7684\u56f4\u88f9\u7740\u547c\u4e4b\u6b32\u51fa\uff0c\u90a3\u4e00\u77a5\u5168\u88f8\u7684\u60ca\u8273\u52a0\u4e0a\u7fdf\u4e39\u9752\u5996\u5b7d\u4f3c\u7684\u8138\u86cb\u4e0a\u7f9e\u6da9\u7684\u795e\u8272\u4efb\u4f55\u7537\u4eba\u770b\u4e86\u90fd\u4f1a\u5fcd\u4e0d\u4f4f\u8981\u795e\u7ecf\u72c2\u4e71\u3002</p><p>\u3000\u3000\u660e\u660e\u542c\u5230\u5979\u8ddf\u5510\u5a67\u5728\u6d74\u6c60\u91cc\u620f\u6c34\uff0c\u600e\u4e48\u53c8\u7a81\u7136\u4ece\u5367\u5ba4\u91cc\u4e32\u51fa\u6765\u4e86\uff1f\u5f20\u606a\u5012\u662f\u6ca1\u6709\u60f3\u5230\u4ed6\u5185\u5fc3\u4ea4\u6218\u7684\u65f6\u95f4\u592a\u957f\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e3e\u4e86\u4e3e\u624b\u91cc\u7684\u5496\u5561\u676f\uff0c\u793a\u610f\u81ea\u5df1\u53ea\u662f\u8fdb\u6765\u5012\u5496\u5561\u7684\u3002\u7fdf\u4e39\u9752\u56de\u5934\u77a5\u4e86\u4e00\u773c\u5367\u5ba4\u95e8\uff0c\u610f\u6307\u5367\u5ba4\u91cc\u53ef\u6ca1\u6709\u4ec0\u4e48\u5496\u5561\u597d\u5012\u3002\u5f20\u606a\u8038\u8038\u80a9\uff0c\u8868\u793a\u81ea\u5df1\u4e2d\u4e86\u90aa\u5f80\u524d\u591a\u8d70\u4e86\u51e0\u6b65\uff0c\u5c4b\u5b50\u91cc\u6709\u4e24\u4e2a\u5343\u5a07\u767e\u5a9a\u7684\u5973\u4eba\uff0c\u60f3\u4e0d\u4e2d\u90aa\u90fd\u96be\u3002</p><p>\u3000\u3000\u542c\u5230\u5510\u5a67\u5728\u540e\u9662\u7684\u6d74\u6c60\u558a\u5979\uff0c\u7fdf\u4e39\u9752\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u8138\u4e0a\u7684\u7f9e\u7ea2\u8fd8\u6ca1\u6709\u892a\u53bb\u5c31\u8f6c\u8eab\u671d\u540e\u9662\u8d70\u53bb\u3002\u521a\u624d\u614c\u624b\u614c\u811a\u7684\u5c06\u6d74\u5dfe\u6361\u8d77\u6765\u88f9\u4e0a\uff0c\u540e\u9762\u7684\u6d74\u5dfe\u5374\u6298\u7fd8\u8d77\u6765\uff0c\u9732\u51fa\u5927\u534a\u7247\u4e30\u633a\u7684\u81c0\u90e8\u5728\u5f20\u606a\u7684\u773c\u524d\u626d\u52a8\uff0c\u5f20\u606a\u5dee\u70b9\u9f3b\u8840\u90fd\u55b7\u51fa\u6765\uff0c\u7279\u522b\u662f\u7fdf\u4e39\u9752\u4e0b\u53f0\u9636\u65f6\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u6367\u7740\u80f8\u5f2f\u7740\u8170\uff0c\u5c41\u80a1\u6485\u8d77\u6765\uff0c\u4e24\u817f\u4e4b\u95f4\u4e0d\u591a\u4f46\u5f88\u957f\u7684\u7ed2\u6bdb\u7ea4\u6beb\u6bd5\u5448\u7684\u9732\u5728\u5f20\u606a\u7684\u773c\u524d\uff0c\u771f\u53eb\u4ed6\u597d\u53d7\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6709\u76f4\u89c9\u4f3c\u7684\u56de\u5934\u770b\u5230\u4e00\u773c\uff0c\u89c1\u5f20\u606a\u50bb\u773c\u4f3c\u7684\u76ef\u7740\u81ea\u5df1\u7684\u80cc\u540e\uff0c\u610f\u8bc6\u5230\u80cc\u540e\u7684\u6d74\u5dfe\u6ca1\u6709\u906e\u597d\uff0c\u614c\u4e71\u7684\u62ff\u624b\u76d6\u4f4f\u5c41\u80a1\u671d\u540e\u9662\u8131\u8352\u800c\u9003\uff0c\u5dee\u70b9\u6454\u4e00\u8dcc\u3002</p><p>\u3000\u3000\u5f20\u606a\u6536\u56de\u76ee\u5149\uff0c\u4ed6\u8fd8\u4e00\u76f4\u4ee5\u4e3a\u7fdf\u4e39\u9752\u662f\u90a3\u79cd\u5373\u4f7f\u8d64\u8eab\u88f8\u4f53\u7ad9\u5728\u7537\u4eba\u5806\u91cc\u4e5f\u4f1a\u9547\u5b9a\u81ea\u82e5\u7684\u5973\u4eba\uff0c\u54ea\u91cc\u60f3\u8c61\u5230\u5979\u6b64\u65f6\u843d\u8352\u800c\u9003\u7684\u60c5\u5f62\uff1f\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u53c8\u51b2\u4e86\u4e00\u676f\u5496\u5561\u5750\u5230\u524d\u5ead\u7684\u5e06\u5e03\u6905\u5b50\u4e0a\u770b\u6e56\u91cc\u7684\u6708\u4eae\uff0c\u6e56\u91cc\u7684\u6708\u4eae\u4f3c\u4e4e\u53d8\u6210\u4e00\u5177\u767d\u751f\u751f\u7684\u7f8e\u5999\u52fe\u9b42\u8089\u4f53\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4e0e\u5510\u5a67\u53c8\u6ce1\u4e86\u8bb8\u4e45\u624d\u6362\u4e86\u7761\u888d\u51fa\u6765\uff0c\u5ba4\u5916\u7684\u5929\u6c14\u6bd5\u7adf\u6e05\u5bd2\uff0c\u539a\u5b9e\u7684\u7761\u888d\u5c31\u6240\u6709\u7684\u98ce\u5149\u90fd\u906e\u4f4f\u4e86\u3002\u7fdf\u4e39\u9752\u6062\u590d\u4e86\u6b63\u5e38\uff0c\u518d\u6ca1\u6709\u4ec0\u4e48\u614c\u4e71\uff0c\u5c31\u597d\u50cf\u521a\u624d\u7684\u4e8b\u60c5\u6839\u672c\u5c31\u6ca1\u6709\u53d1\u751f\u8fc7\u4e00\u6837\u3002\u5728\u5510\u5a67\u9762\u524d\uff0c\u7fdf\u4e39\u9752\u770b\u5f20\u606a\u7684\u773c\u795e\u4e0e\u5e73\u65e5\u6beb\u65e0\u5f02\u6837\u3002</p><p>\u3000\u3000\u5012\u662f\u5510\u5a67\u5a07\u67d4\u7684\u6402\u7740\u5f20\u606a\u7684\u8116\u5b50\u8ddf\u4ed6\u6492\u5a07\uff1a\u201c\u5c0f\u574f\u86cb\uff0c\u521a\u624d\u6709\u6ca1\u6709\u60f3\u7740\u8981\u6765\u5077\u770b\u554a\uff1f\u5c0f\u5fc3\u7fdf\u59d0\u5c06\u4f60\u8272\u772f\u772f\u7684\u4e24\u53ea\u8d3c\u773c\u90fd\u6233\u778e\u6389\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5510\u5a67\u8fd9\u4e48\u8bf4\uff0c\u5f20\u606a\u8fd8\u771f\u5413\u4e86\u4e00\u8eab\u51b7\u6c57\u5462\uff0c\u521a\u624d\u5c31\u7b97\u7fdf\u4e39\u9752\u6233\u4ed6\u7684\u773c\u775b\u4e5f\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u53cd\u5e94\u3002\u5f20\u606a\u4e00\u53e3\u5496\u5561\u545b\u5728\u55d3\u5b50\u773c\u91cc\uff0c\u5dee\u70b9\u7ed9\u545b\u6b7b\uff0c\u7fdf\u4e39\u9752\u4e0e\u5510\u5a67\u6ca1\u5fc3\u6ca1\u80ba\u7684\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\uff0c\u4e5f\u4e0d\u8bf4\u5e2e\u4ed6\u987a\u987a\u6c14\u3002</p><p>\u3000\u3000\u4e4b\u524d\u5173\u7cfb\u5355\u7eaf\u4e9b\uff0c\u5510\u5a67\u53ef\u80fd\u8fd8\u4f1a\u60f3\u7740\u94bb\u5230\u5f20\u606a\u7684\u6000\u91cc\u7761\u89c9\uff0c\u8fd9\u65f6\u5019\u5728\u4eba\u524d\u5c31\u7edd\u4e0d\u80af\u518d\u8ddf\u5f20\u606a\u7761\u5230\u4e00\u8d77\uff0c\u591c\u91cc\u62c9\u7740\u7fdf\u4e39\u9752\u4e00\u8d77\u7761\u3002</p><p>\u3000\u3000\u4e24\u4e2a\u5343\u5a07\u767e\u5a9a\u7684\u5973\u4eba\u5728\u8eab\u8fb9\u4e5f\u5c31\u7b97\u4e86\uff0c\u5173\u952e\u53d7\u5230\u7fdf\u4e39\u9752\u90a3\u8bf1\u4eba\u5230\u6781\u70b9\u7684\u88f8\u4f53\u7684\u523a\u6fc0\uff0c\u5f20\u606a\u5728\u5e8a\u4e0a\u7ffb\u6765\u8986\u53bb\u5230\u51cc\u6668\u624d\u7761\u7740\uff0c\u6e05\u6668\u53c8\u7ed9\u5510\u5a67\u95f9\u8d77\u6765\u770b\u65e5\u51fa\uff0c\u8d70\u5728\u6c34\u53f0\u8fb9\u6253\u778c\u7761\u5dee\u70b9\u5c31\u4e00\u5934\u683d\u5230\u6c34\u91cc\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u65f6\u5019\u66f4\u6000\u7591\u5c06\u7fdf\u4e39\u9752\u7559\u5728\u8eab\u8fb9\u505a\u7279\u522b\u52a9\u7406\u662f\u4e0d\u662f\u4e00\u9879\u6b63\u786e\u7684\u51b3\u5b9a\uff0c\u81ea\u5df1\u62b5\u6297\u6f02\u4eae\u5973\u4eba\u8bf1\u60d1\u7684\u80fd\u529b\u6ca1\u6709\u60f3\u8c61\u4e2d\u90a3\u4e48\u5f3a\u3002</p></article>"}}}]);
