(window.webpackJsonp=window.webpackJsonp||[]).push([[945],{968:function(p){p.exports={chapterTitle:null,title:"\u7b2c942\u7ae0 \u7a81\u751f\u7684\u53d8\u6545",content:"<article><h2>\u7b2c942\u7ae0 \u7a81\u751f\u7684\u53d8\u6545</h2><p>\u3000\u3000\u4e1c\u5927\u5185\u90e8\u7684\u62db\u8058\u4f1a\u4e5f\u5438\u5f15\u9644\u8fd1\u9ad8\u6821\u7684\u5b66\u751f\u8fc7\u6765\u51d1\u70ed\u95f9\uff0c\u5408\u6b22\u5c71\u897f\u7aef\u7684\u4f53\u80b2\u9986\u524d\u4eba\u5934\u6512\u52a8\uff0c\u751a\u662f\u70ed\u95f9\u3002\u4e5d\u516b\u5e74\uff0c\u5927\u5b66\u751f\uff0c\u7279\u522b\u662f\u71d5\u5f52\u6e56\u9644\u8fd1\u51e0\u6240\u9ad8\u6821\u7684\u5927\u5b66\u751f\u90fd\u8fd8\u662f\u5929\u4e4b\u5a07\u5b50\uff0c\u5373\u4f7f\u7edd\u5927\u591a\u6570\u4eba\u7ed9\u63a8\u5230\u4eba\u624d\u5e02\u573a\u4e0a\u81ea\u7531\u62e9\u4e1a\uff0c\u9752\u6625\u6d0b\u6ea2\u7684\u8138\u4e0a\u4e5f\u6ca1\u6709\u4ec0\u4e48\u60f6\u6050\u4e0d\u5b89\uff0c\u5f20\u606a\u5012\u662f\u60e6\u8bb0\u7740\u518d\u8fc7\u534a\u5e74\u597d\u4e9b\u6bd5\u4e1a\u73ed\u91cc\u7684\u9753\u4e3d\u5973\u5b69\u5b50\u4eec\u518d\u4e5f\u4e0d\u4f1a\u88c5\u70b9\u8fd9\u6821\u56ed\u4e86\uff0c\u602a\u53ef\u60dc\u7684\u3002</p><p>\u3000\u3000\u5510\u5fe0\u7a7f\u7740\u8d28\u6599\u5f88\u5dee\u7684\u897f\u670d\u6367\u7740\u4e00\u53e0\u7b80\u5386\u4ece\u4f53\u80b2\u9986\u91cc\u94bb\u51fa\u6765\uff0c\u5f20\u606a\u770b\u4ed6\u7684\u795e\u8272\u9887\u4e3a\u9893\u5e9f\uff0c\u5fc3\u91cc\u4e00\u7b11\uff1a\u5f53\u4e86\u5341\u51e0\u5e74\u7684\u4e8c\u4e16\u7956\uff0c\u6b64\u65f6\u7ec8\u4e8e\u8981\u5c1d\u4e00\u5c1d\u771f\u5b9e\u7684\u751f\u6d3b\u662f\u4f55\u7b49\u7684\u82e6\u695a\u4e86\u3002</p><p>\u3000\u3000\u4e94\u6708\u3001\u516d\u6708\uff0c\u4ee5\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u4e3a\u9996\u7684\u524d\u534e\u7a00\u77ff\u4e1a\u72af\u7f6a\u56e2\u4f19\u7ed9\u4e00\u7f51\u635e\u5c3d\uff0c\u5305\u62ec\u7ae0\u5dde\u5e02\u59d4\u4e66\u8bb0\u3001\u5e02\u957f\u3001\u7eaa\u59d4\u4e66\u8bb0\u5728\u5185\u7684\u4e03\u540d\u5e02\u5e38\u59d4\u90fd\u6d89\u6848\u88ab\u62d8\uff0c\u53ef\u4ee5\u8bf4\u8fd1\u5e74\u6765\u5c11\u6709\u7684\u3001\u9707\u60ca\u5168\u56fd\u7684\u5927\u6848\u3001\u7a9d\u6848\uff0c\u52a0\u4e0a\u4e09\u4e95\u7269\u4ea7\u6d89\u53ca\u7684516\u5927\u6848\uff0c\u7a00\u571f\u4ea7\u4e1a\u5229\u76ca\u94fe\u4e0a\u5b58\u5728\u7684\u9ed1\u5e55\u8ba9\u4eba\u89e6\u76ee\u60ca\u5fc3\uff0c\u8fd9\u4e5f\u662f\u65b0\u534e\u7a00\u77ff\u4e1a\u7531\u9526\u6e56\u51fa\u9762\u4e3b\u5bfc\u5f97\u4ee5\u7ec4\u5efa\u6574\u987f\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u7684\u5173\u952e\u63a8\u52a8\u529b\u3002</p><p>\u3000\u3000516\u6848\u6d89\u53ca\u5230\u4e09\u4e95\u7269\u4ea7\u7684\u5458\u5de5\u90fd\u5df2\u7ecf\u7ed9\u9063\u8fd4\u56de\u65e5\u672c\u4e86\uff0c\u4f46\u662f\u56fd\u5185\u7684\u8d2a\u5b98\u6c61\u540f\u5978\u5546\u4eec\u5374\u6ca1\u6709\u5982\u6b64\u6ecb\u6da6\u7684\u4eab\u53d7\u3002\u5c1a\u5b66\u4e49\u8fd8\u5728\u4efb\u4e0a\u65f6\uff0c\u7ae0\u5dde\u6848\u4e2d\u534e\u7a00\u77ff\u4e1a\u72af\u7f6a\u56e2\u4f19\u6848\u90e8\u5206\u7531\u91d1\u5c71\u5e02\u5357\u6e56\u533a\u4eba\u6c11\u6cd5\u9662\u5ba1\u7406\u5b8c\u6bd5\uff0c\u4e3b\u8981\u6848\u72af\u4e00\u5ba1\u6700\u4e25\u91cd\u7ed9\u5224\u4e86\u6b7b\u7f13\uff0c\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\u4e0d\u670d\u4e00\u5ba1\u5224\u51b3\u63d0\u524d\u4e0a\u8bc9\uff0c\u4ed6\u4eec\u6240\u6599\u60f3\u4e0d\u5230\u7684\uff0c\u4e8c\u5ba1\u7b49\u5f85\u4ed6\u4eec\u7684\u5374\u662f\u51b3\u5fc3\u94c1\u8155\u6574\u6cbb\u540f\u573a\u7684\u5f90\u5b66\u5e73\u51fa\u4efb\u6c5f\u5357\u7701\u59d4\u4e66\u8bb0\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u867d\u8bf4\u5f90\u5b66\u5e73\u6709\u51b3\u5fc3\u8981\u6740\u51e0\u4e2a\u8d2a\u5b98\u6c61\u540f\u7acb\u5a01\uff0c\u4e0d\u8fc7\u8fd9\u65f6\u5019\u5404\u65b9\u9762\u7684\u538b\u529b\u90fd\u9aa4\u7136\u6d8c\u6765\u3002\u7ae0\u5dde\u843d\u9a6c\u5b98\u5458\u7684\u4e00\u5ba1\u662f\u5728\u5f90\u5b66\u5e73\u4e0a\u4efb\u540e\u8fdb\u884c\u7684\uff0c\u4f46\u662f\u5f90\u5b66\u5e73\u521a\u5230\u6c5f\u5357\uff0c\u8fd8\u65e0\u6cd5\u76f4\u63a5\u5bf9\u76f8\u5bf9\u72ec\u7acb\u7684\u653f\u6cd5\u7cfb\u7edf\u65bd\u52a0\u5f71\u54cd\uff0c\u7279\u522b\u662f\u4e0b\u9762\u7684\u653f\u6cd5\u7cfb\u7edf\u65e9\u5c31\u5f62\u6210\u4e00\u5f20\u7f51\uff0c\u77ed\u65f6\u95f4\u5185\u662f\u65e0\u6cd5\u6495\u7834\u7684\uff0c\u4e00\u5ba1\u4e2d\u7ae0\u5dde\u524d\u5e02\u59d4\u4e66\u8bb0\u83b7\u5211\u6700\u9ad8\uff0c\u53ea\u7ed9\u5224\u4e86\u6b7b\u7f13\uff0c\u8fd9\u4e9b\u843d\u9a6c\u5b98\u5458\u90fd\u670d\u4ece\u5224\u51b3\u653e\u5f03\u4e0a\u8bc9\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5c31\u53ea\u80fd\u8fd9\u6837\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\u6b64\u65f6\u4e00\u5b9a\u4f1a\u5f88\u540e\u6094\u5728\u4e00\u5ba1\u540e\u63d0\u8bf7\u4e0a\u8bc9\uff0c\u56e0\u4e3a\u4ed6\u4eec\u80fd\u54ac\u7684\u4eba\u90fd\u5df2\u7ecf\u7ed9\u7275\u6d89\u5230\u6848\u4e2d\u6765\u4e86\uff0c\u4e8c\u5ba1\u4f1a\u7531\u91d1\u5c71\u5e02\u6cd5\u9662\u5728\u5341\u4e00\u6708\u4e0b\u65ec\u5f00\u5ead\u5ba1\u7406\uff0c\u7b49\u5f85\u4ed6\u4eec\u7684\u547d\u8fd0\u5c31\u672a\u5fc5\u5982\u4ed6\u4eec\u6240\u60f3\u7684\u90a3\u4e48\u4e50\u89c2\u4e86\u3002</p><p>\u3000\u3000\u5510\u6c0f\u72af\u7f6a\u56e2\u4f53\u5206\u5d29\u79bb\u6790\uff0c\u4e3b\u8981\u6210\u5458\u5728\u4e00\u5ba1\u4e2d\u90fd\u83b7\u91cd\u5211\uff0c\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\u4e5f\u751a\u662f\u56a3\u5f20\u72c2\u623e\uff0c\u5165\u72f1\u524d\u90fd\u6ca1\u6709\u8fc7\u8f6c\u79fb\u8d44\u4ea7\u7684\u5ff5\u5934\uff0c\u72af\u7f6a\u6240\u5f97\u6089\u906d\u6284\u6ca1\uff0c\u5f53\u521d\u4e00\u63b7\u5343\u91d1\u7ea0\u7f20\u4f55\u5f26\u7684\u5510\u5fe0\u81ea\u7136\u4e5f\u4ece\u4e8c\u4e16\u7956\u7684\u4e91\u7aef\u5760\u843d\u4e0b\u6765\uff0c\u751f\u6d3b\u6ca1\u6709\u534a\u70b9\u7740\u843d\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u4ee5\u4e3a\u5510\u5fe0\u79bb\u5f00\u5e08\u5927\u4e86\uff0c\u66f4\u6ca1\u6709\u60f3\u5230\u4ed6\u8fd9\u65f6\u5019\u7adf\u7136\u8fd8\u6367\u7740\u7b80\u5386\u5230\u62db\u8058\u4f1a\u73b0\u573a\u6765\u627e\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u5510\u5fe0\u4e5f\u770b\u5230\u5f20\u606a\uff0c\u773c\u775b\u95ea\u8fc7\u67ad\u5533\u6028\u6068\u7684\u795e\u8272\uff0c\u522b\u8fc7\u8138\u53bb\uff0c\u4e0e\u540c\u5b66\u4ece\u53e6\u4e00\u4fa7\u79bb\u5f00\u4e86\u62db\u8058\u4f1a\u73b0\u573a\u3002\u5f20\u606a\u5c06\u5510\u5fe0\u6028\u6bd2\u7684\u773c\u795e\u3001\u9634\u51b7\u7684\u8138\u8272\u770b\u5728\u773c\u91cc\uff0c\u5fc3\u91cc\u60f3\uff1a\u96be\u9053\u8fd9\u5bb6\u4f19\u5bf9\u4f55\u5f26\u8fd8\u6ca1\u6709\u7edd\u4e86\u5ff5\u60f3\uff1f\u5012\u662f\u63d0\u9192\u4f55\u5f26\u5c0f\u5fc3\u4e00\u4e9b\u3002\u53c8\u770b\u89c1\u51e0\u540d\u5e08\u5927\u7684\u5b66\u751f\u4e0e\u5510\u5fe0\u4e00\u8d77\u79bb\u5f00\uff0c\u5fc3\u91cc\u53c8\u60f3\uff1a\u5510\u5bb6\u7ecf\u5386\u5982\u6b64\u4e4b\u5927\u7684\u53d8\u6545\uff0c\u5510\u5fe0\u7adf\u7136\u8fd8\u80fd\u7792\u4f4f\u4ed6\u7684\u540c\u5b66\uff0c\u4e5f\u771f\u662f\u4e0d\u7b80\u5355\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0b\u5348\u7167\u4f8b\u53bb\u56fe\u4e66\u9986\u6e29\u4e66\uff0c\u9ec4\u660f\u65f6\u53bb\u4e86\u4e00\u8d9f\u9152\u5427\uff0c\u8ddf\u7ad9\u5728\u5427\u53f0\u540e\u7684\u4f55\u5f26\u63d0\u8d77\u5510\u5fe0\u3002</p><p>\u3000\u3000\u201c\u4ed6\u554a\uff0c\u201d\u4f55\u5f26\u5bf9\u5510\u5fe0\u7684\u5370\u8c61\u90fd\u6709\u4e9b\u6a21\u7cca\u4e86\uff0c\u201c\u4e0a\u6b21\u4f60\u9a97\u4ed6\u7838\u4e86\u8868\u4e4b\u540e\uff0c\u6211\u5c31\u518d\u6ca1\u6709\u770b\u5230\u8fc7\u4ed6\u4e86\uff0c\u5f97\u6709\u597d\u4e45\u4e86\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u6709\u534a\u5e74\u591a\u65f6\u95f4\u4e86\u3002\u201d\u5f20\u606a\u60f3\u4e86\u60f3\uff0c\u5e08\u5927\u8bf4\u5927\u4e0d\u5927\uff0c\u6821\u56ed\u91cc\u4e5f\u585e\u4e86\u8fd1\u4e24\u4e07\u5e08\u751f\uff0c\u8981\u4e0d\u662f\u523b\u610f\u7684\u5b88\u5019\uff0c\u8981\u60f3\u5076\u7136\u7684\u9047\u4e0a\u4ec0\u4e48\u4eba\u4e5f\u56f0\u96be\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u5f20\u606a\u4e5f\u4e0d\u591a\u8bf4\u4ec0\u4e48\uff0c\u514d\u5f97\u7ed9\u4f55\u5f26\u5fc3\u91cc\u7559\u4e0b\u4ec0\u4e48\u9634\u5f71\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e60\u60ef\u56fe\u4e66\u9986\u7684\u6c1b\u56f4\uff0c\u6216\u8bb8\u4e60\u60ef\u9648\u5983\u84c9\u5728\u4e00\u65c1\u6e29\u4e66\u7684\u611f\u89c9\uff0c\u53ea\u8981\u5728\u5efa\u90ba\u6ca1\u6709\u5176\u4ed6\u4e8b\u60c5\uff0c\u4ed6\u591a\u534a\u4f1a\u7a9d\u5728\u9605\u89c8\u5ba4\u91cc\u770b\u4e1c\u897f\uff0c\u518d\u8bf4\u6700\u8fd1\u9700\u8981\u770b\u7684\u6750\u6599\u4e5f\u592a\u591a\u4e86\u4e00\u4e9b\uff0c\u8fd9\u51e0\u5929\u4eba\u4e00\u76f4\u6ce1\u5728\u56fe\u4e66\u9986\u91cc\u770b\u6750\u6599\u3002</p><p>\u3000\u3000\u4e1c\u5927\u56fe\u4e66\u9986\u662f\u71d5\u5f52\u6e56\u9644\u8fd1\u51e0\u6240\u9ad8\u6821\u95ed\u9986\u6700\u665a\u7684\u56fe\u4e66\u9986\uff0c\u4f46\u662f\u5728\u4e5d\u70b9\u534a\u949f\u4e4b\u524d\u4e5f\u8981\u6e05\u573a\u95ed\u9986\u4e86\uff0c\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u603b\u8981\u7b49\u5230\u95ed\u9986\u91cc\u624d\u79bb\u5f00\u56fe\u4e66\u9986\u3002</p><p>\u3000\u3000\u5f20\u606a\u5939\u7740\u4e00\u53e0\u8d44\u6599\u5148\u9001\u9648\u5983\u84c9\u56de\u5bbf\u820d\uff0c\u7ed5\u8fc7\u5408\u6b22\u5c71\u4e1c\u9e93\u800c\u8d70\uff0c\u867d\u7136\u5929\u6c14\u6e10\u5bd2\uff0c\u8349\u4e1b\u91cc\u8fd8\u4f20\u6765\u8bb8\u591a\u866b\u9e23\u58f0\uff0c\u5c71\u810a\u4e0a\u706f\u706b\u660e\u4eae\uff0c\u8fd8\u6709\u4e9b\u60c5\u4fa3\u5728\u5c71\u4e0a\u8c08\u60c5\u8bf4\u7231\uff0c\u770b\u89c1\u6709\u4e00\u5bf9\u7537\u5973\u7275\u624b\u4e0a\u5c71\u7684\u80cc\u666f\u50cf\u8499\u4e50\u4e0e\u5e2d\u82e5\u7433\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u60f3\u7740\u8981\u6253\u6270\u4ed6\u4eec\u4fe9\uff0c\u9648\u5983\u84c9\u4e5f\u8e51\u624b\u8e51\u8db3\u7684\u6015\u6253\u6270\u5230\u5979\u4eec\u3002\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u4e00\u76f4\u7ed9\u4e1c\u5927\u63d0\u4f9b\u8d44\u52a9\uff0c\u5408\u6b22\u5c71\u4eae\u5316\u5de5\u7a0b\u4e5f\u8d44\u52a9\u4e86\u90e8\u5206\u7ecf\u8d39\uff0c\u8499\u4e50\u5634\u91cc\u6ca1\u6709\u8bf4\uff0c\u809a\u5b50\u91cc\u4e00\u5b9a\u4f1a\u9a82\u6211\u4eec\u7ba1\u8fd9\u4e8b\u6700\u64cd\u86cb\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9648\u5983\u84c9\u4e5f\u77e5\u9053\u5408\u6b22\u5c71\u547d\u540d\u7684\u7531\u6765\uff0c\u9677\u5165\u70ed\u604b\u4e2d\u7684\u9752\u5e74\u7537\u5973\u53ea\u60f3\u7740\u5f80\u66f4\u9ed1\u6697\u7684\u5730\u65b9\u94bb\uff0c\u8fd9\u5c71\u810a\u4e0a\u7480\u74a8\u7684\u706f\u5149\u4f7f\u5f97\u6821\u56ed\u5185\u7684\u591c\u666f\u66f4\u8ff7\u4eba\u4e9b\uff0c\u5374\u4e00\u5b9a\u8ba8\u4e0d\u4e86\u7537\u5973\u604b\u4eba\u7684\u6b22\u559c\uff0c\u9648\u5983\u84c9\u7c89\u8138\u5fae\u7ea2\uff0c\u5047\u88c5\u542c\u4e0d\u61c2\u5f20\u606a\u7684\u8bdd\uff0c\u6ca1\u6709\u5e94\u4ed6\u3002</p><p>\u3000\u3000\u5f20\u606a\u6de1\u6de1\u4e00\u7b11\uff0c\u4ed6\u53ea\u662f\u4e0d\u60f3\u8fd9\u5408\u6b22\u5c71\u4e0a\u6709\u4ec0\u4e48\u6076\u6027\u4e8b\u4ef6\u53d1\u751f\uff0c\u4e5f\u4e0d\u60f3\u5b66\u751f\u4f1a\u591c\u91cc\u6ca1\u4e8b\u5c31\u60f3\u7740\u8fc7\u6765\u6253\u6563\u91ce\u9e33\u9e2f\uff0c\u8ba9\u8fd9\u4e9b\u9677\u5165\u70ed\u604b\u4e2d\u7684\u7537\u5973\u75db\u6068\u90a3\u5c31\u75db\u75db\u6068\u5427\u3002</p><p>\u3000\u3000\u98ce\u4ece\u6797\u68a2\u95f4\u7a7f\u68f1\u800c\u6765\uff0c\u9648\u5983\u84c9\u7a7f\u7740\u5355\u8584\u7684\u7f69\u886b\uff0c\u6709\u4e9b\u5fae\u5bd2\uff0c\u5979\u5374\u7edd\u4e0d\u5e0c\u671b\u5f20\u606a\u5b66\u7ec5\u58eb\u4f3c\u7684\u8131\u4e0b\u5916\u5957\u7ed9\u5979\uff0c\u5c31\u597d\u50cf\u6709\u9053\u754c\u7ebf\u6e05\u6670\u7684\u753b\u5728\u90a3\u91cc\u2014\u2014\u5728\u5f20\u606a\u5728\u5efa\u90ba\u7684\u65e5\u5b50\u80fd\u4e00\u8d77\u5728\u56fe\u4e66\u9986\u91cc\u6e29\u4e66\uff0c\u591c\u665a\u518d\u8ba9\u4ed6\u9001\u56de\u5bbf\u820d\uff0c\u8fd9\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u3000\u3000\u8d70\u5230\u5bbf\u820d\u697c\u524d\u7684\u7bee\u7403\u573a\uff0c\u8fd8\u6709\u597d\u4e9b\u4eba\u5728\u706f\u5149\u4e0b\u6253\u7bee\u7403\uff0c\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u51fa\u73b0\u5728\u5bbf\u820d\u697c\u524d\uff0c\u987f\u65f6\u5c31\u6709\u4eba\u7ed9\u7bee\u7403\u7838\u4e2d\u8138\u4e86\u3002</p><p>\u3000\u3000\u8d76\u5de7\u5bbf\u820d\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\u4e5f\u56de\u5bbf\u820d\uff0c\u9648\u5983\u84c9\u5c31\u5728\u53c9\u8def\u53e3\u8ddf\u5f20\u606a\u6325\u624b\u544a\u522b\uff0c\u4e0e\u5bbf\u820d\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\u5f80\u91cc\u8d70\uff0c\u53ef\u662f\u5f85\u5f20\u606a\u8f6c\u8fc7\u8eab\u53bb\uff0c\u4e24\u4e2a\u5973\u5b69\u5b50\u90fd\u4e00\u5de6\u4e00\u53f3\u7684\u67b6\u4f4f\u9648\u5983\u84c9\u8f6c\u8fc7\u8eab\u6765\uff0c\u82b1\u75f4\u4e00\u6837\u7684\u770b\u7740\u8def\u706f\u5149\u4e0b\u7684\u8eab\u6750\u524a\u7626\u5374\u633a\u62d4\u7684\u5f20\u606a\uff0c\u95ee\u5979\uff1a\u201c\u5929\u5929\u9001\u4f60\u56de\u5bbf\u820d\uff0c\u4f60\u4eec\u5c31\u6ca1\u6709\u70b9\u5b9e\u8d28\u6027\u7684\u8fdb\u5c55\uff1f\u8fd9\u5c0f\u5b50\u5c31\u662f\u770b\u80cc\u5f71\u4e5f\u89c9\u5f97\u597d\u5e05\u554a\uff0c\u4f60\u5929\u5929\u8ddf\u4ed6\u7c98\u5728\u4e00\u8d77\u4e0d\u89c9\u5f97\u5634\u998b\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\u554a\uff0c\u6211\u4eec\u662f\u670b\u53cb\uff0c\u54ea\u91cc\u50cf\u4f60\u4eec\u60f3\u7684\u90a3\u4e48\u4e4c\u4e03\u516b\u7cdf\uff1f\u6211\u4eec\u53ea\u662f\u4e00\u8d77\u5728\u56fe\u4e66\u9986\u91cc\u5b66\u4e60\u7f62\u4e86\u3002\u201d\u9648\u5983\u84c9\u626f\u7740\u5bbf\u820d\u91cc\u4e24\u4e2a\u5973\u5b69\u5b50\u6765\u8f6c\u8fc7\u8eab\u53bb\uff0c\u8981\u8ba9\u5f20\u606a\u770b\u89c1\u5979\u4eec\u8fd9\u6a21\u6837\uff0c\u771f\u662f\u53eb\u4eba\u96be\u4e3a\u60c5\u554a\u3002</p><p>\u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u4e16\u754c\u4e0a\u6709\u7eaf\u51c0\u7684\u7537\u5973\u670b\u53cb\u5173\u7cfb\u5417\uff1f\u201d\u4e00\u4e2a\u5973\u5b69\u5b50\u4e00\u672c\u6b63\u7ecf\u7684\u770b\u7740\u9648\u5983\u84c9\uff0c\u53c8\u634f\u4e86\u634f\u9648\u5983\u84c9\u7684\u8138\u86cb\uff0c\u201c\u4f60\u8fd9\u4e48\u6f02\u4eae\uff0c\u6211\u770b\u4e86\u90fd\u5fcd\u4e0d\u4f4f\u60f3\u54ac\u4e00\u53e3\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u6ca1\u6709\u5566\uff1f\u4f60\u4eec\u7684\u8111\u5b50\u90fd\u80ae\u810f\u4e86\uff0c\u5f53\u7136\u6ca1\u6709\u3002\u201d\u9648\u5983\u84c9\u7b11\u7740\u53cd\u9a73\u8bf4\uff0c\u770b\u7740\u706f\u5149\u4e0b\u6e10\u884c\u6e10\u8fdc\u7684\u5f20\u606a\uff0c\u80cc\u5f71\u5012\u771f\u662f\u86ee\u5438\u5f15\u4eba\u7684\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u6211\u4eec\u7ea6\u4f60\u660e\u5929\u53bb\u901b\u8857\uff0c\u4e0d\u8ba9\u4f60\u53bb\u56fe\u4e66\u9986\u6e29\u4e66\uff0c\u4f60\u7b54\u5e94\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u52c9\u5f3a\uff1f\u201d\u53e6\u4e00\u4e2a\u5973\u5b69\u5b50\u76ef\u7740\u9648\u5983\u84c9\u7684\u773c\u775b\uff0c\u60f3\u4ece\u5979\u773c\u775b\u91cc\u770b\u51fa\u4ec0\u4e48\u7834\u7efd\u6765\u3002</p><p>\u3000\u3000\u201c\u597d\u5566\uff0c\u6211\u660e\u5929\u4e5f\u60f3\u53bb\u8d2d\u7269\u554a\uff01\u201d\u9648\u5983\u84c9\u72e1\u9ee0\u7684\u4e00\u7b11\uff0c\u201c\u4f60\u4eec\u60f3\u8bd5\u63a2\u4ec0\u4e48\u554a\uff0c\u8981\u4e0d\u6211\u5c06\u5f20\u606a\u4ecb\u7ecd\u7ed9\u4f60\u4eec\u4fe9\u624d\u7518\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u201c\u5509\uff0c\u6211\u4e0e\u4ed6\u89c1\u8fc7\u4e09\u6b21\u9762\uff0c\u4ed6\u773c\u795e\u5728\u6211\u8138\u4e0a\u505c\u7559\u7684\u65f6\u95f4\u4ece\u6765\u90fd\u4e0d\u8d85\u8fc70.3\u79d2\uff0c\u4f60\u89c9\u5f97\u4f60\u8fd9\u53e5\u8bdd\u4f1a\u7ed9\u6211\u7edd\u671b\u7684\u5fc3\u4ec0\u4e48\u5e0c\u671b\u4e0d\u6210\uff1f\u201d\u90a3\u5973\u5b69\u5b50\u5e7d\u6028\u7684\u8f7b\u53f9\u4e86\u4e00\u58f0\u3002</p><p>\u3000\u3000\u201c\u4e0d\u8981\u641e\u5f97\u8ddf\u9965\u6e34\u7684\u6028\u5987\u4e00\u6837\uff0c\u6628\u5929\u591c\u4e0a\u624d\u770b\u89c1\u4f60\u8ddf316\u7684\u9648\u6653\u94bb\u5408\u6b22\u5c71\u7684\u7af9\u6797\u5b50\uff0c\u90a3\u4e48\u4eae\u7684\u706f\u5149\uff0c\u4f60\u4eec\u4fe9\u7684\u52a8\u4f5c\u5c31\u4e0d\u80fd\u542b\u84c4\u4e00\u70b9\uff1f\u201d\u53e6\u4e00\u4e2a\u5973\u5b69\u5b50\u65e0\u60c5\u6233\u7a7f\u5979\u7684\u5047\u75f4\u60c5\uff0c\u5979\u7acb\u65f6\u5472\u7259\u51bd\u5634\u7684\u8981\u53bb\u6495\u90a3\u4e2a\u5973\u5b69\u5b50\u7684\u5634\uff0c\u7b11\u95f9\u7740\u7ea0\u7f20\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u60f3\u8d77\u5f20\u606a\u7684\u8bdd\uff0c\u56e0\u4e3a\u8d44\u52a9\u5408\u6b22\u5c71\u4eae\u5316\u5de5\u7a0b\u7684\u4e8b\uff0c\u75db\u6068\u9526\u6e56\u7684\u4eba\u5e94\u8be5\u4e0d\u5c11\uff0c\u76f4\u5230\u5f20\u606a\u7684\u8eab\u5f71\u6d88\u5931\u5728\u62d0\u89d2\u5904\uff0c\u5979\u4eec\u624d\u8f6c\u8fc7\u8eab\u5f80\u5bbf\u820d\u697c\u91cc\u8d70\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u53ef\u4e0d\u77e5\u9053\u7ed9\u9648\u5983\u84c9\u5bbf\u820d\u7684\u5973\u5b69\u5b50\u8bae\u8bba\u4e86\u534a\u5929\uff0c\u4ed6\u95f2\u5ead\u4fe1\u6b65\u8d70\u51fa\u6821\u4e1c\u95e8\u3002\u867d\u8bf4\u51e0\u767e\u7c73\u5916\u7684\u5b66\u5e9c\u5df7\u8fd8\u6b63\u9677\u5165\u706f\u7ea2\u9152\u7eff\u7684\u591c\u751f\u6d3b\u4e4b\u4e2d\uff0c\u4f46\u662f\u63a5\u8fd1\u5b66\u751f\u5bbf\u820d\u7184\u706f\u65f6\u95f4\uff0c\u6821\u4e1c\u95e8\u5916\u7684\u6797\u836b\u9053\u4e0a\u5df2\u7ecf\u770b\u4e0d\u5230\u6709\u4ec0\u4e48\u4eba\u3002\u5f20\u606a\u6ce8\u610f\u5230\u524d\u9762\u6709\u597d\u51e0\u76cf\u8def\u706f\u90fd\u7184\u4e86\uff0c\u5317\u9762\u7684\u9752\u5e74\u516c\u5bd3\u6574\u680b\u697c\u90fd\u7a7a\u7740\uff0c\u6797\u836b\u53c8\u5bc6\uff0c\u4e2d\u95f4\u4e00\u6bb5\u8def\u9ed1\u9ee2\u9ee2\u7684\u6ca1\u6709\u4ec0\u4e48\u5149\u7ebf\uff0c\u5f20\u606a\u8d77\u521d\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5fc3\u60f3\u7740\u7ecf\u8fc7\u4fdd\u5b89\u5ba4\u65f6\u63d0\u9192\u4e00\u58f0\uff0c\u8d70\u5230\u8fd1\u5904\uff0c\u501f\u7740\u8fdc\u5904\u4f20\u6765\u7684\u5149\u7ebf\uff0c\u770b\u7740\u8def\u706f\u67f1\u4e0b\u7684\u8def\u9762\u6709\u4e9b\u73bb\u7483\u6e23\u5b50\uff0c\u5f20\u606a\u7709\u5934\u5fae\u76b1\uff0c\u62ac\u5934\u770b\u4e86\u770b\u5934\u9876\u4e0a\u7684\u8def\u706f\uff0c\u662f\u7ed9\u4eba\u62ff\u4e1c\u897f\u7838\u788e\u7684\uff0c\u6ca1\u7b49\u4ed6\u5c06\u5fc3\u601d\u60f3\u5230\u522b\u5904\u53bb\uff0c\u9661\u7136\u611f\u89c9\u6709\u4eba\u4ece\u80cc\u540e\u7f00\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u521a\u4ece\u515c\u91cc\u638f\u51fa\u624b\u673a\u6309\u4e86\u4e00\u4e2a\u952e\uff0c\u5c31\u542c\u89c1\u8eab\u540e\u4e00\u58f0\u4f4e\u6c89\u7684\u65ad\u559d\uff1a\u201c\u627e\u6b7b\uff01\u201d\u624b\u8155\u5c31\u7ed9\u4ece\u80cc\u540e\u8e39\u4e2d\uff0c\u624b\u673a\u6454\u843d\u5230\u4e00\u8fb9\uff0c\u5f20\u606a\u626d\u5934\u770b\u89c1\u80cc\u540e\u662f\u4e24\u4e2a\u9762\u76ee\u72f0\u72de\u7684\u9752\u5e74\uff0c\u6b63\u8981\u6492\u8def\u5f00\u6e9c\uff0c\u524d\u9762\u6811\u540e\u9634\u5f71\u91cc\u53c8\u8d70\u51fa\u4e24\u4eba\uff0c\u624b\u91cc\u62ff\u7740\u8e6d\u4eae\u7684\u5f39\u7c27\u5200\uff0c\u5305\u6284\u8fc7\u6765\uff0c\u5c06\u4ed6\u7684\u9000\u8def\u5835\u4f4f\u3002</p><p>\u3000\u3000\u8fd8\u771f\u662f\u75db\u82e6\uff0c\u4ed6\u4eec\u9009\u62e9\u4e1c\u5927\u6821\u95e8\u4fdd\u5b89\u5ba4\u4e0e\u9752\u5e74\u516c\u5bd3\u4fdd\u5b89\u5ba4\u4e2d\u95f4\u7684\u8def\u6bb5\uff0c\u9644\u8fd1\u7684\u8def\u706f\u90fd\u7ed9\u7838\u574f\uff0c\u4ece\u4e24\u8fb9\u90fd\u770b\u4e0d\u5230\u8fd9\u91cc\u7684\u60c5\u5f62\u3002\u5f20\u606a\u7ed9\u903c\u8fdb\u6b7b\u89d2\u540e\uff0c\u5510\u5fe0\u624d\u8d70\u51fa\u6765\uff0c\u6361\u5730\u4e0a\u7684\u624b\u673a\uff0c\u67e5\u770b\u4e86\u4e00\u4e0b\uff0c\u5c06\u624b\u673a\u9012\u7ed9\u4e00\u540d\u9752\u5e74\u8bf4\u9053\uff1a\u201c\u6ca1\u4e8b\uff0c\u4ed6\u6ca1\u6709\u6765\u5f97\u62d4\u53f7\u2026\u2026\u5c31\u6309\u4e86\u4e00\u4e2a\u952e\u3002\u201d</p></article>"}}}]);
