(window.webpackJsonp=window.webpackJsonp||[]).push([[947],{970:function(p){p.exports={chapterTitle:null,title:"\u7b2c763\u7ae0 \u9152\u5427\u730e\u8273",content:"<article><h2>\u7b2c763\u7ae0 \u9152\u5427\u730e\u8273</h2><p>\u3000\u30001\u670820\u65e5\u523022\u65e5\u5f00\u5c55\u7684\u4e1c\u4eac\u56fd\u9645\u7535\u5b50\u4ea7\u54c1\u5c55\u89c8\u4f1a\u662f\u4e16\u754c\u6700\u8457\u540d\u7684\u7535\u5b50\u5c55\u4f1a\u4e4b\u4e00\uff0c\u81ea\u4e3e\u529e\u4ee5\u6765\uff0c\u6bcf\u5e74\u4e00\u5c4a\uff0c\u5df2\u7ecf\u4e3e\u529e\u4e8626\u5c4a\uff0c\u4eca\u5e74\u5c06\u8fd9\u4e2a\u5c55\u4f1a\u7684\u7b2c27\u5c4a\uff0c\u53bb\u5e74\u5c55\u4f1a\u805a\u96c6\u5168\u7403\u63a5\u8fd1\u4e5d\u767e\u5bb6\u7535\u5b50\u4f01\u4e1a\u53c2\u5c55\uff0c\u5438\u5f15\u4e86\u8d85\u8fc7\u516d\u4e07\u540d\u5f53\u5730\u53ca\u56fd\u9645\u77e5\u540d\u7535\u5b50\u5382\u5546\u4ee3\u8868\u524d\u6765\u53c2\u89c2\uff0c\u4e1c\u4eac\u7535\u5b50\u5c55\u7684\u4e70\u5bb6\u7edd\u5927\u591a\u6570\u4e5f\u662f\u8fd9\u4e9b\u6765\u81ea\u884c\u5185\u7684\u9876\u5c16\u4e13\u4e1a\u4eba\u58eb\u3002</p><p>\u3000\u3000\u8bb8\u591a\u7535\u5b50\u5382\u5546\u90fd\u4f1a\u501f\u4e1c\u4eac\u5c55\u7684\u65f6\u673a\u53d1\u5e03\u65b0\u4ea7\u54c1\uff0c\u524d\u6765\u53c2\u89c2\u7684\u4eba\u7fa4\u91cc\u65e0\u7591\u6709\u76f8\u5f53\u4e00\u90e8\u5206\u90fd\u662f\u8fc7\u6765\u6536\u96c6\u65b0\u4ea7\u54c1\u8d44\u6599\u7684\u4e13\u4e1a\u4eba\u58eb\u3002</p><p>\u3000\u3000\u81ea\u7136\u4e5f\u6709\u6765\u81ea\u5168\u7403\u5404\u5730\u7684\u5a92\u4f53\u8bb0\u8005\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u4e5f\u5c06\u53c2\u52a0\u4e1c\u4eac\u7535\u5b50\u5c55\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u5728\u65e5\u672c\u8bbe\u6709\u5b50\u516c\u53f8\uff0c\u5fb7\u4eea\u7684\u4e8b\u5b9c\u5747\u7531\u65e5\u672c\u5b50\u516c\u53f8\u8d1f\u8d23\uff0c\u5728\u4e1c\u4eac\u7535\u5b50\u5c55\u4e0a\uff0c\u5fb7\u4eea\u9664\u4e86\u6b63\u5f0f\u63a8\u51fa\u4e0e\u9526\u6e56\u8054\u5408\u7684DMP\u6280\u672f\u4e4b\u5916\uff0c\u8fd8\u5c06\u63a8\u51fa\u5176\u4ed6\u591a\u6b3e\u7535\u5b50\u5143\u5668\u4ef6\u65b0\u54c1\u3002</p><p>\u3000\u3000\u65af\u9ad8\u67cf\u65e9\u5c31\u6709\u5de5\u4f5c\u4eba\u5458\u62b5\u5728\u4e1c\u4eac\u7b79\u5907\u4e0e\u9526\u6e56\u914d\u5408\u53c2\u5c55\u4e8b\u5b9c\uff0c\u827e\u9ed8\u5219\u523019\u65e5\u4e2d\u5348\u624d\u4ece\u7f8e\u56fd\u52a0\u6d32\u76f4\u63a5\u98de\u62b5\u4e1c\u4eac\u3002</p><p>\u3000\u3000\u827e\u9ed8\u7684\u7cbe\u529b\u5145\u6c9b\uff0c\u4ed6\u4e58\u5750\u7684\u98de\u673a\u4e2d\u5348\u62b5\u8fbe\u4e1c\u4eac\uff0c\u5728\u4ed6\u5230\u4e1c\u4eac\u540e\u5904\u7406\u5b8c\u624b\u5934\u7684\u5de5\u4f5c\u4e0a\uff0c\u7cbe\u529b\u72b9\u6709\u4f59\u88d5\uff0c\u5929\u8272\u669d\u9eef\u4e4b\u540e\u5c31\u76f4\u63a5\u62c9\u5f20\u606a\u5230\u65b0\u5bbf\u7684\u6b4c\u821e\u753a\u53bb\u6d88\u9063\u3002</p><p>\u3000\u3000\u201c\u5e94\u8be5\u8ba9\u6211\u6765\u6b63\u5f0f\u6b22\u8fce\u4f60\u5230\u4e1c\u4eac\u6765\uff0c\u201d\u827e\u9ed8\u9886\u7740\u5f20\u606a\u8d70\u4e00\u5bb6\u7235\u58eb\u4e50\u9707\u5f97\u6574\u4e2a\u5c4b\u5b50\u90fd\u5728\u6447\u6643\u7684\u9152\u5427\uff0c\u201c\u8096\u664b\u6210\u3001\u9648\u4fca\u8f89\u90a3\u7fa4\u5bb6\u4f19\u53c8\u600e\u4e48\u6562\u5728\u4f60\u9762\u524d\u9732\u51fa\u4ed6\u4eec\u672c\u6765\u7684\u9762\u76ee\uff1f\u5728\u6211\u8fc7\u6765\u4e4b\u524d\uff0c\u4f1a\u4e0d\u4f1a\u611f\u5230\u5f88\u82e6\u95f7\uff1f\u201d\u827e\u9ed8\u5c06\u5176\u4ed6\u4eba\u90fd\u8e22\u5f00\uff0c\u751a\u81f3\u8ba9\u5085\u4fca\u4e0e\u65e5\u672c\u5b50\u516c\u53f8\u6d3e\u51fa\u7684\u53f8\u673a\u7559\u5728\u9152\u5427\u5916\u7684\u8f66\u4e0a\uff0c\u4e0d\u5f71\u54cd\u4ed6\u4e0e\u5f20\u606a\u5728\u9152\u5427\u91cc\u5bfb\u6b22\u4f5c\u4e50\u3002</p><p>\u3000\u3000\u201c\u7684\u786e\u591f\u95f7\u7684\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u6307\u671b\u4f60\u80fd\u63d0\u5230\u4e1c\u4eac\u6765\u3002\u201d\u5728\u9152\u5427\u91cc\u8bf4\u8bdd\u8981\u626f\u8d77\u55d3\u5b50\uff0c\u5f20\u606a\u66f4\u591a\u65f6\u95f4\u5b81\u53ef\u95ed\u4e0a\u5634\uff0c\u5750\u5728\u6a61\u6728\u6253\u5236\u7684\u5427\u53f0\u8fb9\u7684\u7329\u7ea2\u5ea7\u6905\u4e0a\u559d\u9152\uff0c\u9ed8\u9ed8\u5fcd\u53d7\u80fd\u8ba9\u6574\u95f4\u5c4b\u5b50\u6447\u6643\u4f3c\u7684\u5de8\u5927\u97f3\u4e50\u58f0\u54cd\uff0c\u6253\u91cf\u7740\u9152\u5427\u91cc\u6f02\u4eae\u7684\u65e5\u672c\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u5149\u7ebf\u660f\u6697\u7684\u9152\u5427\u91cc\uff0c\u7edd\u5927\u90e8\u5206\u7684\u5973\u6027\u987e\u5ba2\u5e74\u7eaa\u90fd\u5f88\u8f7b\uff0c\u6ca1\u6709\u5ba4\u5916\u7684\u5bd2\u51b7\uff0c\u5979\u4eec\u8131\u6389\u5916\u5957\u3001\u7a7f\u7740\u5355\u8584\u7684\u8863\u886b\uff0c\u90fd\u6709\u7740\u59e3\u597d\u7684\u66f2\u7ebf\uff0c\u9886\u895f\u9732\u51fa\u6027\u611f\u7ea4\u7ec6\u7684\u7410\u9aa8\u4e0e\u80f8\u524d\u7684\u4e73\u6c9f\u3002\u8fd9\u662f\u4e00\u5bb6\u5939\u6742\u5728\u6309\u6469\u9662\u4e0e\u60c5\u4eba\u9152\u5e97\u4e4b\u95f4\u7684\u7235\u58eb\u4e50\u9152\u5427\uff0c\u65e5\u672c\u4eba\u70ed\u7231\u7235\u58eb\u4e50\u7684\u7a0b\u5ea6\u751a\u81f3\u8d85\u8fc7\u7235\u58eb\u4e50\u7684\u53d1\u6e90\u5730\uff0c\u9152\u5427\u91cc\u7edd\u5927\u591a\u6570\u662f\u65e5\u672c\u4eba\uff0c\u65e5\u672c\u7537\u6027\u5927\u591a\u504f\u7626\uff0c\u4e2a\u4eba\u5e76\u4e0d\u77ee\uff0c\u5f20\u606a\u6df7\u8ff9\u5176\u4e2d\u4e0d\u4f1a\u7ed9\u533a\u522b\u51fa\u6765\uff0c\u827e\u9ed8\u5012\u662f\u8fd9\u95f4\u9152\u5427\u91cc\u60df\u4e00\u7684\u9ad8\u4e2a\u5b50\u767d\u4eba\uff0c\u4e0d\u8fc7\u4e5f\u53ea\u662f\u521a\u8fdb\u9152\u5427\u91cc\u5f15\u8d77\u522b\u4eba\u7684\u6ce8\u610f\uff0c\u4e4b\u540e\u5927\u5bb6\u90fd\u5404\u81ea\u559d\u9152\u3002</p><p>\u3000\u3000\u201c\u4e1c\u6d0b\u9b3c\u5b50\u8fd8\u662f\u6ca1\u6709\u4ec0\u4e48\u52a8\u9759\uff1f\u201d\u827e\u9ed8\u62ff\u65e5\u8bed\u51d1\u5230\u5f20\u606a\u8033\u6735\u5927\u58f0\u558a\uff0c\u5373\u4f7f\u662f\u8bf4\u65e5\u8bed\uff0c\u4ed6\u4e5f\u559c\u6b22\u62ff\u6c49\u8bed\u7684\u60ef\u7528\u8bed\uff0c\u4e1c\u6d0b\u5e94\u8be5\u662f\u4e2d\u56fd\u5bf9\u65e5\u672c\u7279\u6709\u7684\u79f0\u8c13\u3002</p><p>\u3000\u3000\u201cXeus\u901a\u8fc7\u77f3\u4e38\u7535\u5668\u63d0\u51fa\u8981\u4e70\u65adiplayer\u5728\u65e5\u672c\u7684\u9500\u552e\uff0c\u5355\u7b14\u5c31\u5f00\u51fa\u5341\u4e07\u53ea\u7684\u8ba2\u5355\uff0c\u6211\u4eec\u62d2\u7edd\u4e86\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u518d\u6ca1\u6709\u5176\u4ed6\u52a8\u9759\u3002\u201d\u5f20\u606a\u8981\u56de\u7b54\u827e\u9ed8\uff0c\u4e00\u6837\u8981\u51d1\u5230\u827e\u9ed8\u8033\u6735\u626f\u7740\u55d3\u5b50\u558a\uff0c\u4ed6\u8003\u8651\u7740\u8981\u8ba8\u8bba\u8fd9\u4e9b\u95ee\u9898\u662f\u4e0d\u662f\u53bb\u4e00\u4e2a\u5b89\u9759\u4e9b\u7684\u5730\u65b9\u5408\u9002\u3002</p><p>\u3000\u3000\u65e9\u5728\u4e00\u6708\u521d\u9526\u6e56\u5c31\u8054\u5408\u5fb7\u4eea\u5728\u5efa\u90ba\u5ba3\u5e03\u8981\u5171\u540c\u63a8\u5e7f\u4fbf\u643a\u5f0f\u6570\u5b57\u97f3\u9891\u64ad\u653e\u4ea7\u54c1\u7684DMP\u6280\u672f\uff0c\u65af\u9ad8\u67cf\u4e5f\u5c06\u662f\u9526\u6e56\u63a8\u5e7f\u4ea7\u54c1\u6280\u672f\u7684\u5408\u4f5c\u4f19\u4f34\u3002\u7531\u4e8eDMP\u6280\u672f\u5728\u56fd\u5185\u7684\u5e94\u7528\u5e02\u573a\u6709\u9650\u5236\uff0c\u8fd9\u5219\u6d88\u606f\u5728\u56fd\u5185\u6ca1\u6709\u9020\u6210\u591a\u5c11\u54cd\u5e94\uff0c\u5012\u662f\u591a\u5bb6\u6b27\u7f8e\u7535\u5b50\u5382\u5546\u4ece\u65b0\u95fb\u8f6c\u8f7d\u4e2d\u6316\u6398\u51fa\u8fd9\u6761\u654f\u611f\u7684\u6d88\u606f\uff0c\u81f4\u51fd\u9526\u6e56\u8981\u6c42\u83b7\u5f97\u66f4\u591a\u66f4\u8be6\u7ec6\u7684\u8d44\u6599\uff0c\u4e5f\u6709\u6b27\u7f8e\u7535\u5b50\u5382\u5546\u76f4\u63a5\u6d3e\u9063\u9a7b\u534e\u529e\u4e8b\u673a\u6784\u7684\u5de5\u4f5c\u4eba\u5458\u4e0a\u95e8\u54a8\u8be2\u76f8\u5173\u4e8b\u5b9c\u3002\u6216\u8bb8\u65e5\u7cfb\u7535\u5b50\u5382\u5546\u7684\u5e02\u573a\u60c5\u51b5\u90e8\u95e8\u9057\u6f0f\u4e86\u76f8\u5173\u6d88\u606f\uff0c\u4e5f\u6216\u8bb8\u65e5\u7cfb\u7535\u5b50\u5382\u5546\u5bf9\u9526\u6e56\u4e0e\u5fb7\u4eea\u8054\u5408\u63a8\u51fa\u7684DMP\u6280\u672f\u4e0d\u5c51\u4e00\u987e\uff0c\u9664\u4e86\u65e5\u672c\u4e8c\u6d41\u89c6\u542c\u7535\u5b50\u5382\u5546Xeus\u901a\u8fc7\u77f3\u4e38\u7535\u5668\u63d0\u51fa\u8981\u4e70\u65ad\u9526\u6e56\u5728\u65e5\u672c\u7684iplayer\u7684\u4ea7\u54c1\u9500\u552e\uff0c\u5176\u4ed6\u8bf8\u5982\u7d22\u5c3c\u3001\u4e1c\u829d\u3001\u677e\u4e0b\u3001\u590f\u666e\u7b49\u65e5\u672c\u4e3b\u8981\u7535\u5b50\u5382\u5546\u90fd\u4fdd\u6301\u4e86\u6c89\u9ed8\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e9b\u65e5\u672c\u4eba\u72e1\u733e\u5f97\u5f88\uff0c\u4f60\u4eec\u8054\u5408\u5fb7\u4eea\u5728\u5efa\u90ba\u5ba3\u5e03\u6d88\u606f\u90fd\u6709\u534a\u4e2a\u6708\u4e86\uff0c\u800c\u4e14\u4f60\u4eec\u5728\u65e5\u672c\u6536\u8d2d\u7684\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u8fd8\u4ece\u7d22\u5c3c\u83b7\u5f97\u97f3\u50cf\u4ea7\u54c1\u7f51\u4e0a\u9500\u552e\u7684\u552e\u6743\uff0c\u4e5f\u5927\u91cf\u91c7\u8d2d\u7d22\u5c3c\u7684\u7535\u6c60\uff0c\u6211\u60f3\u4ed6\u4eec\u641c\u96c6\u5230\u7684\u60c5\u62a5\u5df2\u7ecf\u76f8\u5f53\u591a\u4e86\uff0c\u4ed6\u4eec\u5927\u6982\u8981\u7b49\u5230\u660e\u5929\u770b\u7535\u5b50\u5c55\u4e0a\u9526\u6e56\u6b63\u5f0f\u63a8\u51faiplayer\u4e4b\u540e\u518d\u62ff\u51fa\u76f8\u5e94\u7684\u5bf9\u7b56\u2026\u2026Xeus\u770b\u4e0a\u53bb\u86ee\u5927\u65b9\u7684\uff0c\u51fa\u624b\u5c31\u662f\u56db\u5343\u4e07\u7684\u5355\u5b50\uff0c\u5c31\u8fd9\u6837\uff0c\u7d22\u5c3c\u3001\u677e\u4e0b\u3001\u590f\u666e\u90fd\u8fd8\u80fd\u5750\u7740\u4e0d\u52a8\uff0c\u770b\u6765\u4ed6\u4eec\u8fd8\u771f\u4e0d\u662f\u4e00\u822c\u7684\u72e1\u733e\u3002\u201d\u827e\u9ed8\u5f53\u7136\u4e0d\u4f1a\u5728\u201c\u56db\u5343\u4e07\u201d\u540e\u9762\u753b\u86c7\u6dfb\u8db3\u7684\u6dfb\u4e0a\u201c\u7f8e\u5143\u201d\u8fd9\u4e2a\u8bcd\uff0c\u65e0\u8bba\u662f\u9526\u6e56\u5728\u65e5\u672c\u5e02\u573a\u8fd8\u662f\u65af\u9ad8\u67cf\u5728\u5317\u7f8e\u5e02\u573a\u6700\u65e9\u63a8\u51fa\u7684\u4e24\u6b3e\u4e0d\u540c\u50a8\u5b58\u5bb9\u6613\u7684MP3\u64ad\u653e\u5668\uff0c\u552e\u4ef7\u90fd\u5b9a\u5728299\u7f8e\u5143\u3001399\u7f8e\u5143\uff0c\u552e\u4ef7\u4e0eMD\u76f8\u6bd4\uff0c\u5e76\u6ca1\u6709\u4ec0\u4e48\u4f18\u52bf\uff0c\u4e3b\u8981\u662f\u50a8\u5b58\u5143\u4ef6\u7684\u552e\u4ef7\u5c45\u9ad8\u4e0d\u5c11\uff0c\u6210\u672c\u65e0\u6cd5\u964d\u4f4e\u3002</p><p>\u3000\u3000\u201c\u8c01\u6653\u5f97\uff0c\u5728\u7d22\u5c3c\u8fd9\u4e2a\u5e9e\u7136\u5927\u7269\u9762\u524d\uff0c\u9526\u6e56\u6839\u672c\u5c31\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u4ed6\u4eec\u7684\u786e\u6ca1\u6709\u5fc5\u8981\u4e00\u672c\u6b63\u7ecf\u7684\u7406\u4f1a\u6211\u4eec\u3002\u201d\u5f20\u606a\u6ce8\u610f\u5230\u5427\u53f0\u7684\u53e6\u4e00\u4fa7\u6709\u4e24\u4e2a\u6253\u626e\u65f6\u9ae6\u7684\u65e5\u672c\u5973\u5b69\u5b50\u5077\u770b\u4ed6\u8ddf\u827e\u9ed8\u5728\u8bf4\u7b11\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\u66a7\u6627\u4e0d\u660e\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u8fdf\u65e9\u4f1a\u53d7\u5230\u6559\u8bad\u7684\uff0c\u8fd9\u5c31\u662f\u6211\u8d76\u5230\u4e1c\u4eac\u7684\u7f18\u6545\uff0c\u6211\u5e0c\u671b\u770b\u5230\u4ed6\u4eec\u5927\u68a6\u521d\u9192\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u6b63\u52aa\u529b\u63a8\u5e7f\u7684MD\u3001CD\u3001Walkman\u5374\u65e9\u5c31\u7ed9iplayer\u6253\u5f97\u843d\u82b1\u6d41\u6c34\u2026\u2026\u201d\u827e\u9ed8\u5bf9\u9526\u6e56\u5012\u6bd4\u5f20\u606a\u672c\u4eba\u8fd8\u6709\u4fe1\u5fc3\uff0c\u63a2\u8fc7\u8eab\u5b50\u671d\u53e6\u4e00\u4fa7\u90a3\u4e24\u4e2a\u65e5\u672c\u5973\u5b69\u5b50\u4f38\u624b\u6253\u62db\u547c\u3002\u90a3\u4e24\u4e2a\u5973\u5b69\u5b50\u7b11\u4e86\u7b11\uff0c\u6709\u4e9b\u52c9\u5f3a\uff0c\u8f6c\u8138\u770b\u5411\u522b\u5904\uff0c\u827e\u9ed8\u4e5f\u6ca1\u6709\u8fdb\u4e00\u6b65\u8868\u793a\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u827e\u9ed8\u663e\u7136\u662f\u5bf9\u8fd9\u4e24\u4e2a\u5973\u5b69\u5b50\u7684\u59ff\u8272\u4e0d\u591f\u6ee1\u610f\uff0c\u5b9e\u5728\u4e0d\u6e05\u695a\u6b27\u7f8e\u4eba\u5bf9\u4e1c\u65b9\u5973\u751f\u7684\u5ba1\u7f8e\u89c2\uff0c\u5f20\u606a\u5012\u662f\u89c9\u5f97\u90a3\u4e24\u4e2a\u5973\u5b69\u5b50\u8eab\u6750\u66f2\u7ebf\u4e0d\u5dee\u3001\u6837\u8c8c\u79c0\u7f8e\u53ef\u7231\uff0c\u827e\u9ed8\u8f6c\u8138\u5374\u8ddf\u5427\u53f0\u4e0b\u53bb\u7b2c\u4e8c\u5ea7\u7684\u4e09\u4e2a\u5973\u5b69\u5b50\u5728\u5750\u65e0\u58f0\u7684\u4ea4\u6d41\u3002\u54e6\uff0c\u90a3\u4e09\u4e2a\u5973\u5b69\u5b50\uff0c\u5f20\u606a\u6ca1\u6709\u4e00\u4e2a\u4e2d\u610f\u7684\u3002</p><p>\u3000\u3000\u201c\u4e1c\u829d\uff0c\u201d\u827e\u9ed8\u7684\u773c\u795e\u53c8\u4ece\u4e0e\u5973\u5b69\u5b50\u7684\u8c03\u60c5\u4e2d\u6536\u56de\u6765\uff0c\u51d1\u5230\u5f20\u606a\u7684\u8033\u6735\u5927\u58f0\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u5e94\u8be5\u627e\u4e1c\u829d\uff0c\u4e1c\u829d\u624b\u91cc\u6709\u9526\u6e56\u6240\u6025\u9700\u7684\u95ea\u5b58\u6280\u672f\uff0c\u800c\u5728\u4fbf\u643a\u5f0f\u97f3\u9891\u4ea7\u54c1\u5e02\u573a\u751a\u81f3\u6574\u4e2a\u89c6\u542c\u4ea7\u54c1\u5e02\u573a\uff0c\u4e1c\u829d\u7ed9\u677e\u4e0b\u3001\u7d22\u5c3c\u3001\u590f\u666e\u4e09\u5bb6\u516c\u53f8\u538b\u5f97\u5598\u4e0d\u8fc7\u6c14\uff0c\u522b\u770b\u8fd9\u4e9b\u65e5\u672c\u4eba\u90fd\u4e00\u81f4\u7684\u4fdd\u6301\u6c89\u9ed8\uff0c\u4f46\u662f\u6211\u60f3\u4e1c\u829d\u4e00\u5b9a\u4f1a\u5bf9\u9526\u6e56\u7684iplayer\u4ea7\u54c1\u6280\u672f\u6709\u7740\u4e0d\u540c\u7684\u60f3\u6cd5\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u540c\u610f\u827e\u9ed8\u7684\u770b\u6cd5\uff0c\u4e0d\u8fc7\u770b\u7740\u8ddf\u827e\u9ed8\u8c03\u60c5\u7684\u4e09\u4e2a\u59ff\u8272\u666e\u901a\u7684\u5973\u5b69\u5b50\u6709\u8981\u8d70\u8fc7\u6765\u7684\u610f\u601d\uff0c\u5373\u4f7f\u627e\u4eba\u966a\u559d\u9152\uff0c\u5f20\u606a\u4e5f\u4e0d\u60f3\u59d4\u5c48\u4e86\u81ea\u5df1\uff0c\u773c\u795e\u7acb\u5373\u77a5\u5230\u5427\u53f0\u53e6\u4e00\u4fa7\u7684\u4e24\u4e2a\u957f\u76f8\u751c\u7f8e\u7684\u5973\u5b69\u5b50\u8138\u4e0a\uff0c\u7ed9\u4e86\u4e00\u4e2a\u66a7\u6627\u4e0d\u660e\u7684\u773c\u795e\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\u7ad9\u4e86\u8d77\u6765\uff0c\u53e6\u4e00\u4e2a\u5973\u5b69\u5b50\u6709\u4e9b\u4e0d\u5927\u613f\u610f\uff0c\u8138\u4e0a\u7684\u795e\u8272\u6709\u72b9\u8c6b\u3001\u6709\u96be\u582a\u3001\u6709\u6323\u624e\uff0c\u5f88\u96be\u60f3\u8c61\u5728\u9152\u5427\u91cc\u559d\u9152\u600e\u4e48\u4f1a\u8fd9\u4e48\u590d\u6742\u7684\u5fc3\u60c5\uff0c\u5f20\u606a\u53ea\u662f\u8ba9\u5973\u5f85\u8005\u5728\u5427\u53f0\u4e0a\u52a0\u4e86\u4e24\u53ea\u9152\u676f\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e24\u4e2a\u5973\u5b69\u5b50\u4eca\u665a\u672a\u5fc5\u5c31\u80fd\u591f\u641e\u5b9a\uff0c\u8981\u662f\u4eca\u591c\u80fd\u641e\u5b9a\u7684\u8bdd\uff0c\u591a\u534a\u662f\u4ed8\u8d39\u670d\u52a1\u2014\u2014\u6211\u4eec\u53c8\u4e0d\u80fd\u5728\u4e1c\u4eac\u803d\u6401\u592a\u957f\u7684\u65f6\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u539f\u6765\u827e\u9ed8\u8fd9\u5bb6\u4f19\u662f\u6839\u636e\u8fd9\u4e2a\u6765\u9009\u62e9\u5728\u9152\u5427\u730e\u8273\u7684\u5bf9\u8c61\uff0c\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u8ffd\u6c42\u5f88\u6d6e\u6d45\uff0c\u5b81\u53ef\u4ed8\u8d39\u4e5f\u8981\u76f8\u8c8c\u987a\u773c\u7684\u5973\u5b69\u5b50\u2026\u2026\u4e00\u8d77\u559d\u9152\u3002\u201d</p><p>\u3000\u3000\u90a3\u4e2a\u795e\u8272\u95f4\u6709\u4e9b\u4e0d\u60c5\u4e0d\u613f\u7684\u5973\u5b69\u5b50\u4e5f\u7ad9\u8d77\u6765\uff0c\u521a\u624d\u7ed9\u5427\u53f0\u7684\u4e00\u89d2\u6321\u7740\uff0c\u8fd9\u65f6\u5019\u624d\u770b\u89c1\u5979\u88d9\u4e0b\u4fee\u957f\u7684\u7f8e\u817f\u6ca1\u6709\u88f9\u4e1d\u889c\uff0c\u5728\u5e7d\u6697\u7684\u5149\u7ebf\u6709\u7740\u8ff7\u4eba\u7684\u5ae9\u767d\uff0c\u8fd8\u4ee5\u4e3a\u65e5\u672c\u5c31\u9ad8\u4e2d\u751f\u559c\u6b22\u5728\u51ac\u5929\u9732\u51fa\u767d\u751f\u751f\u7684\u5927\u817f\uff0c\u8fd9\u53cc\u7f8e\u817f\u4e5f\u6ca1\u6709\u65e5\u672c\u5c11\u5973\u5e38\u89c1\u7684\u7c97\u58ee\uff0c\u4e0d\u5927\u5b9e\u7528\uff0c\u4f46\u662f\u7ea4\u7ec6\u65e0\u6687\u3002</p><p>\u3000\u3000\u4e24\u4e2a\u5973\u4eba\u81ea\u62a5\u5bb6\u95e8\uff0c\u90fd\u662f\u5f88\u5e38\u89c1\u7684\u65e5\u672c\u5973\u6027\u540d\u5b57\uff0c\u5f20\u606a\u751a\u81f3\u60f3\u5f00\u73a9\u7b11\u95ee\u90a3\u4e2a\u7f8e\u817f\u5973\u5b69\u53eb\u5979\u5c0f\u6cfd\u5706\u53ef\u4e0d\u53ef\u4ee5\uff0c\u5979\u7684\u957f\u76f8\u4e0e\u5c0f\u6cfd\u5706\u6709\u51e0\u5206\u76f8\u8096\u3002\u4e0d\u8bba\u662f\u5230\u9152\u5427\u730e\u8273\u7684\u5973\u6027\u767d\u9886\uff0c\u8fd8\u662f\u6253\u7b97\u6536\u8d39\u966a\u9152\u7684\u804c\u4e1a\u5973\u5b69\uff0c\u5728\u9152\u5427\u91cc\u90fd\u4e0d\u53ef\u80fd\u7b2c\u4e00\u6b21\u5c31\u8ddf\u964c\u751f\u8bf4\u51fa\u81ea\u5df1\u7684\u540d\u5b57\u3002</p><p>\u3000\u3000\u827e\u9ed8\u7684\u773c\u5149\u5f88\u51c6\uff0c\u5f20\u606a\u4e0e\u827e\u9ed8\u79bb\u5f00\u65f6\u652f\u4ed8\u5c0f\u8d39\uff0c\u4e24\u4e2a\u5973\u5b69\u5b50\u6ca1\u6709\u62d2\u7edd\uff0c\u660e\u5929\u9700\u8981\u65e9\u8d77\uff0c\u827e\u9ed8\u5927\u4e3a\u540e\u6094\u4e0d\u80fd\u5c06\u5973\u5b69\u5b50\u5e26\u56de\u9152\u5e97\u53bb\uff0c\u5373\u4f7f\u5982\u6b64\u8fd8\u7559\u4e0b\u4e24\u4e2a\u5973\u5b69\u5b50\u7684\u8054\u7cfb\u65b9\u5f0f\u3002\u7ad9\u8d77\u6765\u79bb\u5f00\u5427\u53f0\uff0c\u8ddf\u4e24\u4e2a\u5973\u5b69\u5b50\u9053\u522b\uff1a\u201c\u795d\u4f60\u4eec\u665a\u4e0a\u8fd8\u80fd\u627e\u5230\u5176\u4ed6\u6177\u6168\u7684\u5ba2\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u62ff\u8d77\u5916\u5957\u8981\u8d70\uff0c\u5374\u610f\u5916\u7684\u542c\u5230\u90a3\u4e2a\u7f8e\u817f\u5973\u5b69\u5b50\u8bf4\u4e86\u4e00\u53e5\u4e0a\u6d77\u8bdd\uff0c\u4ed6\u6123\u4e86\u4e00\u4e0b\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8868\u793a\uff0c\u5c31\u4e0e\u827e\u9ed8\u79bb\u5f00\u9152\u5427\uff1b\u5085\u4fca\u4e0e\u53f8\u673a\u90fd\u5728\u9152\u5427\u5916\u7684\u8f66\u4e0a\u7b49\u5019\uff0c\u4ed6\u4eec\u591f\u6ca1\u6709\u826f\u5fc3\u7684\u3002</p></article>"}}}]);
