(window.webpackJsonp=window.webpackJsonp||[]).push([[948],{971:function(p){p.exports={chapterTitle:null,title:"\u7b2c945\u7ae0 \u4e00\u4e2a\u90fd\u4e0d\u653e\u8fc7",content:"<article><h2>\u7b2c945\u7ae0 \u4e00\u4e2a\u90fd\u4e0d\u653e\u8fc7</h2><p>\u3000\u3000\u6bd4\u8d77\u521d\u65f6\u631f\u6301\u5f20\u606a\u65f6\u7684\u58f0\u8272\u4ff1\u5389\uff0c\u6b64\u65f6\u7684\u5510\u5fe0\u66f4\u50cf\u4e27\u5bb6\u4e4b\u72ac\uff0c\u795e\u60c5\u9893\u9761\uff0c\u8138\u8272\u7070\u8d25\uff0c\u6574\u4e2a\u4eba\u4e00\u526f\u5f7b\u5e95\u57ae\u6389\u7684\u6a21\u6837\uff0c\u5750\u5728\u5ba1\u8baf\u684c\u524d\u770b\u7740\u5f20\u606a\u5728\u5085\u4fca\u7684\u966a\u540c\u4e0b\u8fdb\u6765\u5ba1\u8baf\u684c\uff0c\u770b\u7740\u5f20\u606a\u6ca1\u6709\u4ec0\u4e48\u611f\u60c5\u7684\u51b0\u51b7\u773c\u795e\uff0c\u5bb3\u6015\u5f97\u8eab\u5b50\u7981\u4e0d\u4f4f\u7684\u53d1\u98a4\uff0c\u4ed6\u4e0d\u77e5\u9053\u7fdf\u4e39\u9752\u8f6c\u5371\u4e3a\u5b89\u4e86\uff0c\u4ed6\u518d\u7b28\u4e5f\u77e5\u9053\u7ed9\u523a\u4e2d\u80f8\u53e3\u7684\u90a3\u4e2a\u5973\u4eba\u4e0e\u5f20\u606a\u6709\u7740\u4e0d\u4e00\u822c\u7684\u5173\u7cfb\uff0c\u4e0d\u7136\u8c01\u4f1a\u4e3a\u4ed6\u4eba\u594b\u4e0d\u987e\u8eab\u7684\u6321\u4e00\u5200\u3002</p><p>\u3000\u3000\u201c\u6211\u60f3\u77e5\u9053\u4f60\u4ece\u54ea\u91cc\u77e5\u9053\u662f\u6211\u8981\u81f4\u4f60\u7236\u4eb2\u4e0e\u4f60\u53d4\u53d4\u4ee5\u6b7b\u5730\u7684\uff0c\u6211\u4e0e\u4f60\u7236\u4eb2\u3001\u4f60\u53d4\u53d4\u65e0\u6028\u65e0\u4ec7\uff0c\u6211\u4e3a\u4ec0\u4e48\u975e\u8981\u81f4\u4ed6\u4eec\u4ee5\u6b7b\u5730\uff1f\u201d\u5f20\u606a\u795e\u8272\u51b7\u5cfb\u7684\u5750\u5230\u5510\u5fe0\u7684\u9762\u524d\uff0c\u5c06\u4e00\u53e0\u539a\u539a\u7684\u7ae0\u5dde\u6848\u5ba1\u7406\u5377\u6848\u62e8\u5230\u5510\u5fe0\u9762\u524d\uff0c\u8bf4\u9053\uff0c\u201c\u8981\u81f4\u4ed6\u4eec\u4ee5\u6b7b\u5730\u7684\uff0c\u662f\u4ed6\u4eec\u81ea\u5df1\u8fd9\u5341\u5e74\u6765\u4f5c\u7684\u5b7d\uff0c\u81f3\u4e8e\u68c0\u5bdf\u9662\u5728\u4e8c\u5ba1\u65f6\u4f1a\u4e0d\u4f1a\u8865\u5145\u65b0\u7684\u7f6a\u8bc1\uff0c\u8981\u770b\u4ed6\u4eec\u81ea\u5df1\u7684\u9020\u5316\u2014\u2014\u800c\u4f60\uff0c\u6211\u53ef\u4ee5\u5f88\u660e\u786e\u7684\u544a\u8bc9\u4f60\uff0c\u6211\u8ddf\u4f60\u4ece\u6b64\u5c31\u6709\u4ec7\u6709\u6028\u7684\uff0c\u6211\u4e0d\u662f\u4e00\u4e2a\u5bbd\u5bb9\u7684\u4eba\u2014\u2014\u6211\u73b0\u5728\u53ea\u662f\u5f88\u597d\u5947\uff1a\u8fd9\u4e00\u5207\u90fd\u662f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u201d</p><p>\u3000\u3000\u5510\u5fe0\u5934\u57cb\u5230\u684c\u5b50\u4e0a\uff0c\u4e0d\u6562\u4e0e\u5f20\u606a\u76f4\u89c6\uff0c\u5927\u817f\u63a7\u5236\u4e0d\u4f4f\u7684\u6253\u98a4\uff0c\u5374\u54ac\u53e3\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u65f6\u95f4\u8ddf\u5510\u5fe0\u5728\u5ba1\u8baf\u5ba4\u91cc\u78e8\u65f6\u95f4\uff0c\u5ba1\u8baf\u5de5\u4f5c\u81ea\u7136\u7531\u8b66\u65b9\u6765\u8d1f\u8d23\uff0c\u89c1\u5510\u5fe0\u8fd9\u6a21\u6837\uff0c\u5f20\u606a\u7a0d\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u5c31\u7ad9\u8d77\u6765\uff0c\u4e0e\u5085\u4fca\u51fa\u4e86\u5ba1\u8baf\u5ba4\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u4e0d\u662f\u610f\u5fd7\u575a\u5b9a\u7684\u4eba\uff0c\u5982\u6b64\u6709\u8010\u5fc3\u7684\u4e8b\u60c5\u4e5f\u4e0d\u662f\u4ed6\u80fd\u7b56\u5212\u51fa\u6765\u7684\uff0c\u4f60\u8ddf\u91d1\u5c71\u8b66\u65b9\u8054\u7cfb\u4e00\u4e0b\uff0c\u8c03\u67e5\u4e00\u4e0b\u6709\u6ca1\u6709\u4eba\u53ef\u80fd\u66ff\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u6697\u4e2d\u4f20\u9012\u6d88\u606f\uff0c\u201d\u53c8\u8f7b\u53f9\u4e86\u4e00\u58f0\uff0c\u201c\u9664\u6076\u52a1\u5c3d\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u6c0f\u5144\u5f1f\u7684\u5229\u7528\u4ef7\u503c\u5df2\u7ecf\u4e0d\u5927\uff0c\u5510\u5fe0\u53c8\u4e0d\u662f\u80fd\u5e72\u6210\u4e8b\u7684\u6837\u5b50\uff0c\u867d\u8bf4\u6709\u53ef\u80fd\u6709\u5fc3\u4eba\u5728\u80cc\u540e\u63a8\u6ce2\u52a9\u6f9c\u4e86\u4e00\u628a\uff0c\u4f46\u4e0d\u4f1a\u5c06\u4e00\u628a\u5927\u7684\u8d4c\u5510\u5fe0\u8eab\u4e0a\u2014\u2014\u8fd9\u6837\u5c31\u592a\u4e0d\u667a\u4e86\uff0c\u5f88\u53ef\u80fd\u662f\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\u5728\u5782\u6b7b\u4e4b\u65f6\u7684\u6323\u624e\u3002\u8fd9\u5e74\u5934\uff0c\u6709\u94b1\u6536\uff0c\u80c6\u5b50\u662f\u8d8a\u6765\u8d8a\u5927\uff0c\u867d\u8bf4\u5510\u6c0f\u5144\u5f1f\u4e00\u5ba1\u7ed9\u5224\u4e86\u6b7b\u7f13\uff0c\u4f46\u662f\u6709\u80c6\u91cf\u5e2e\u4ed6\u4eec\u6697\u4e2d\u4f20\u9012\u6d88\u606f\u7684\u4eba\u8fd8\u662f\u6709\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u79bb\u5f00\u5e02\u516c\u5b89\u5c40\uff0c\u5929\u8fb9\u5df2\u6cdb\u5fae\u660e\u6e05\u4eae\uff0c\u9646\u6587\u592b\u3001\u738b\u7ef4\u5747\u4e5f\u90fd\u76f8\u7ee7\u79bb\u5f00\uff0c\u5f20\u606a\u5750\u8f66\u56de\u533b\u9662\u53bb\uff0c\u4ed6\u7238\u5988\u90fd\u4ece\u65b0\u829c\u6025\u51b2\u51b2\u7684\u8d76\u5230\u5efa\u90ba\u6765\uff0c\u5c31\u5728\u533b\u9662\u91cc\u7b49\u4ed6\u3002</p><p>\u3000\u3000\u5728\u8f66\u4e0a\u5f20\u606a\u8ba9\u5085\u4fca\u6253\u7535\u8bdd\u627e\u8f86\u8f6e\u6905\u8f66\u5077\u5077\u6478\u6478\u7684\u5230\u505c\u8f66\u573a\u6765\u63a5\u4ed6\u3002\u5085\u4fca\u521d\u65f6\u4e0d\u89e3\uff0c\u6309\u5f20\u606a\u7684\u5429\u5490\u901a\u77e5\u7559\u5b88\u5728\u533b\u9662\u91cc\u7684\u5de5\u4f5c\u4eba\u5458\u627e\u6765\u8f6e\u6905\u5230\u505c\u8f66\u573a\u5916\u6765\u63a5\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u4e0a\u8f6e\u6905\u7531\u5de5\u4f5c\u4eba\u5458\u63a8\u7740\u8fdb\u533b\u9662\uff1b\u6ee1\u8138\u6012\u6c14\u7684\u6881\u683c\u73cd\u4ece\u8fc7\u9053\u91cc\u51b2\u5c06\u51fa\u6765\uff0c\u770b\u7740\u513f\u5b50\u5750\u8f6e\u6905\u4e0a\uff0c\u8fd8\u4ee5\u4e3a\u9664\u4e86\u624b\u81c2\u7ed9\u523a\u4f24\u4e4b\u5916\uff0c\u54ea\u91cc\u53c8\u51fa\u4e86\u95ee\u9898\uff0c\u6ee1\u8154\u6012\u6c14\u6d88\u5f97\u4e00\u5e72\u4e8c\u51c0\uff0c\u7126\u6025\u7684\u95ee\uff1a\u201c\u4e0d\u662f\u8bf4\u53ea\u6709\u624b\u81c2\u7ed9\u523a\u4f24\u4e86\u5417\uff0c\u4f60\u4eec\u600e\u4e48\u90fd\u4e0d\u8ddf\u6211\u8bf4\u5b9e\u8bdd\u554a\uff1f\u201d\u773c\u6cea\u7c0c\u7c0c\u7684\u5c31\u5f80\u4e0b\u6d41\uff0c\u62b9\u7740\u6cea\u6251\u8fc7\u6765\u770b\u5f20\u606a\u817f\u4e0a\u5230\u5e95\u54ea\u91cc\u51fa\u4e86\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u5c31\u624b\u81c2\u53d7\u4e86\u4f24\uff0c\u5931\u8840\u6709\u4e9b\u591a\uff0c\u96be\u514d\u5934\u6655\u4e4f\u529b\uff0c\u5c31\u5750\u8f6e\u6905\u8ba9\u4ed6\u4eec\u63a8\u7740\u8d70\uff0c\u6ca1\u6709\u4f60\u60f3\u8c61\u4e25\u91cd\uff0c\u201d\u5f20\u606a\u89e3\u91ca\u9053\uff0c\u201c\u4e39\u9752\u6709\u6ca1\u6709\u9192\u8fc7\u6765\uff1f\u6211\u521a\u521a\u5f97\u53bb\u516c\u5b89\u5c40\u76ef\u7740\u4ed6\u4eec\u6293\u6355\u51f6\u624b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u4e8b\u5c31\u597d\uff0c\u5413\u6b7b\u6211\u4e86\uff0c\u5931\u8840\u8fc7\u591a\uff0c\u5c31\u8981\u4e71\u8d70\u554a\uff0c\u201d\u6881\u683c\u73cd\u62b9\u7740\u773c\u6cea\uff0c\u4e5f\u6ca1\u6709\u8fc7\u5206\u8d23\u602a\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u7fdf\u4e39\u9752\u521a\u521a\u9192\u8fc7\u6765\uff0c\u5979\u7238\u5988\u5728\u8d76\u6765\u7684\u8def\u4e0a\uff0c\u8fd8\u8981\u8fc7\u4f1a\u513f\u65f6\u95f4\u624d\u8d76\u5230\u3002\u201d</p><p>\u3000\u3000\u5085\u4fca\u8fd9\u624d\u77e5\u9053\u5f20\u606a\u8981\u5de5\u4f5c\u4eba\u5458\u5e26\u8f6e\u6905\u5230\u505c\u8f66\u573a\u6765\u63a5\u4ed6\u7684\u610f\u56fe\uff0c\u8981\u662f\u8ba9\u6881\u683c\u73cd\u770b\u5230\u4ed6\u6d3b\u8e66\u4e71\u8df3\u7684\u5c06\u53d7\u4f24\u7684\u624b\u81c2\u540a\u5728\u8116\u5b50\u4e0a\u4e71\u8d70\uff0c\u94c1\u5b9a\u8981\u6328\u4e00\u901a\u65a5\u9a82\u3002\u4ed6\u53c8\u4e0d\u80fd\u7b11\u51fa\u58f0\u6765\uff0c\u8ddf\u6881\u683c\u73cd\u3001\u5f20\u77e5\u884c\u8ba4\u771f\u7684\u68c0\u8ba8\u8bf4\u9053\uff1a\u201c\u51fa\u4e86\u8fd9\u6837\u7684\u7bd3\u5b50\uff0c\u90fd\u662f\u6211\u4eec\u5de5\u4f5c\u5931\u804c\u2026\u2026\u201d</p><p>\u3000\u3000\u6881\u683c\u73cd\u62bf\u7740\u5634\u4e0d\u7f6e\u53ef\u5426\uff0c\u5fc3\u91cc\u8fd8\u662f\u6709\u4e9b\u62b1\u6028\u7684\uff0c\u5f53\u7136\u4e86\uff0c\u4eba\u90fd\u6ca1\u6709\u5927\u4e8b\uff0c\u6709\u4e9b\u4e0d\u6ee1\u610f\u7684\u5730\u65b9\uff0c\u4e5f\u4f1a\u5fcd\u4f4f\u4e0d\u8bf4\u51fa\u53e3\u6765\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8d70\u8fc7\u6765\u62cd\u4e86\u62cd\u5085\u4fca\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u603b\u4e0d\u53ef\u80fd\u675c\u7edd\u6240\u6709\u7684\u610f\u5916\uff0c\u65e0\u9700\u4e3a\u8fd9\u6b21\u610f\u5916\u81ea\u8d23\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u6709\u4e9b\u610f\u5916\u662f\u96be\u4ee5\u9884\u9632\u7684\uff0c\u518d\u8bf4\u4e5f\u4e0d\u53ef\u80fd\u6709\u4e07\u65e0\u4e00\u5931\u7684\u4fdd\u5168\u7cfb\u7edf\uff1b\u8981\u8bf4\u6709\u5931\uff0c\u90a3\u4e5f\u662f\u9752\u5e74\u516c\u5bd3\u5c0f\u533a\u5927\u95e8\u4e0e\u4e1c\u5927\u4e1c\u95e8\u4e4b\u95f4\u7684\u51e0\u76cf\u8def\u706f\u5f53\u65f6\u7ed9\u4eba\u6253\u788e\u6389\u6ca1\u6709\u5f15\u8d77\u8db3\u591f\u7684\u8b66\u89c9\u3002\u5f20\u606a\u8fd9\u65f6\u5019\u5934\u8111\u51b7\u9759\u4e0b\u6765\uff0c\u66f4\u4e0d\u4f1a\u8fc1\u6012\u522b\u4eba\uff0c\u95ee\u5085\u4fca\uff1a\u201c\u90a3\u4e2a\u52a9\u7406\u59d3\u6613\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u5085\u4fca\u77e5\u9053\u5f20\u606a\u662f\u8bf4\u90a3\u4e2a\u4e00\u811a\u5c06\u6301\u5200\u51f6\u624b\u8e39\u6389\u534a\u6761\u547d\u3001\u8e39\u5f97\u751f\u6b7b\u4e0d\u77e5\u7684\u5b89\u5168\u4e8b\u52a1\u52a9\u7406\uff0c\u201c\u5e73\u65f6\u5de5\u4f5c\u5f88\u8ba4\u771f\u8e0f\u5b9e\uff0c\u4f60\u4f20\u51fa\u6765\u7684\u4fe1\u53f7\uff0c\u4ed6\u7b2c\u4e00\u4e2a\u505a\u51fa\u53cd\u5e94\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u5e74\u5e95\u65f6\uff0c\u8bb0\u5f97\u63d0\u9192\u6211\u4e00\u58f0\u3002\u201d\u5f20\u606a\u5429\u5490\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5085\u4fca\u70b9\u70b9\u5934\uff0c\u5e74\u5e95\u65f6\u63d0\u9192\u81ea\u7136\u662f\u63d0\u9192\u5e74\u7ec8\u5956\u52b1\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u5f80\u7fdf\u4e39\u9752\u7684\u7279\u62a4\u75c5\u623f\u8d70\u53bb\uff0c\u8d70\u5230\u75c5\u623f\u524d\u7684\u8fc7\u9053\uff0c\u5f20\u606a\u53c8\u4ece\u8f6e\u6905\u4e0a\u7ad9\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u5931\u8840\u8fc7\u591a\u5934\u6655\u5417\uff1f\u5feb\u5750\u5230\u8f6e\u6905\u4e0a\u3002\u201d\u6881\u683c\u73cd\u6309\u7740\u5f20\u606a\u7684\u80a9\u8180\uff0c\u8981\u4ed6\u5750\u56de\u5230\u8f6e\u6905\u4e0a\u3002</p><p>\u3000\u3000\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8b\uff0c\u4e0d\u8981\u8ba9\u91cc\u9762\u4eba\u770b\u5230\u5927\u60ca\u5c0f\u602a\u7684\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5012\u662f\u5931\u8840\u8fc7\u591a\uff0c\u659c\u8eba\u5728\u75c5\u5e8a\u4e0a\uff0c\u9762\u5bb9\u82cd\u767d\uff0c\u773c\u795e\u4e5f\u6ca1\u6709\u529b\u6c14\uff0c\u8f6f\u7ef5\u7ef5\u7684\u770b\u7740\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u770b\u5230\u4ed6\u53ea\u662f\u624b\u81c2\u540a\u6302\u5728\u80f8\u524d\uff0c\u5012\u771f\u662f\u5fc3\u5b89\u4e86\uff0c\u4e24\u4eba\u5bf9\u671b\u4e86\u6709\u4e24\u79d2\u949f\uff0c\u7fdf\u4e39\u9752\u5c31\u5c06\u89c6\u7ebf\u79fb\u5f00\uff0c\u5fae\u4fa7\u7740\u5934\u65e0\u529b\u7684\u8ddf\u536b\u5170\u8bf4\u7740\u8bdd\uff0c\u62b1\u6028\u4e0d\u8be5\u5c06\u6d88\u606f\u544a\u8bc9\u5979\u7238\u5988\uff0c\u4f1a\u60f9\u5f97\u5bb6\u4eba\u62c5\u5fc3\u53d7\u6015\u7684\u3002</p><p>\u3000\u3000\u201c\u6ca1\u5173\u7cfb\u7684\uff0c\u7279\u610f\u5429\u5490\u5e02\u91cc\u6d3e\u4e86\u8f66\uff0c\u8fd8\u8c03\u4e86\u8b66\u8f66\u5f00\u9053\uff0c\u7535\u8bdd\u91cc\u90fd\u8ddf\u4f60\u7238\u5988\u90fd\u8bf4\u6e05\u695a\u4e86\u7684\u60c5\u51b5\uff0c\u8ba9\u4ed6\u4eec\u653e\u5bbd\u5fc3\u624d\u5c06\u4ed6\u4eec\u63a5\u8fc7\u6765\u7684\uff0c\u201d\u6881\u683c\u73cd\u62ff\u51fa\u624b\u673a\uff0c\u8d70\u5230\u75c5\u69bb\u524d\u5f02\u5e38\u4eb2\u5207\u7684\u8bf4\u9053\uff0c\u201c\u8981\u4e0d\u6211\u8fd9\u5c31\u5e2e\u4f60\u62d4\u7535\u8bdd\uff0c\u8ba9\u4f60\u8ddf\u4f60\u7238\u5988\u901a\u4e00\u4e0b\u7535\u8bdd\uff0c\u4ed6\u4eec\u4e5f\u5c31\u66f4\u653e\u5bbd\u5fc3\u4e86\u2026\u2026\u518d\u8fc7\u4e00\u4e2a\u5c0f\u65f6\u4e5f\u8be5\u5230\u5efa\u90ba\u4e86\u3002\u201d</p><p>\u3000\u3000\u536b\u5170\u4e0e\u5b59\u9759\u6aac\u7ad9\u8d77\u6765\u5c06\u6905\u5b50\u8ba9\u7ed9\u6881\u683c\u73cd\u5750\uff0c\u7fdf\u4e39\u9752\u6323\u624e\u7740\u8981\u6b20\u8d77\u8eab\u5b50\uff0c\u7275\u52a8\u80f8\u53e3\u7684\u4f24\u53e3\uff0c\u7709\u5934\u75db\u76b1\u4e86\u8d77\u6765\uff0c\u6881\u683c\u73cd\u5fd9\u6309\u4f4f\u5979\u7684\u80a9\u8180\uff1a\u201c\u4f60\u4e0d\u8981\u52a8\uff0c\u6211\u6765\u5e2e\u4f60\u62d4\u53f7\uff0c\u4f60\u8f7b\u8f7b\u7684\u8bf4\u4e24\u53e5\u8bdd\u8ba9\u4ed6\u4eec\u5b89\u5fc3\u5c31\u53ef\u4ee5\u4e86\u2014\u2014\u90fd\u602a\u5f20\u606a\u8fd9\u4e2a\u60f9\u7978\u7cbe\uff0c\u504f\u751f\u751f\u7684\u8981\u4f60\u66ff\u4ed6\u767d\u6328\u8fd9\u4e00\u5200\u2026\u2026\u201d\u8bf4\u7740\u5c31\u54fd\u54bd\u8d77\u6765\uff0c\u5bf9\u594b\u4e0d\u987e\u8eab\u66ff\u5f20\u606a\u6321\u4e00\u5200\u7684\u7fdf\u4e39\u9752\uff0c\u6881\u683c\u73cd\u4ece\u5185\u5fc3\u6df1\u5904\u5145\u6ee1\u7684\u611f\u6fc0\u4e4b\u60c5\uff0c\u4e4b\u524d\u5bf9\u7fdf\u4e39\u9752\u7684\u90a3\u4e9b\u504f\u89c1\u81ea\u7136\u4e5f\u5c31\u5f7b\u5f7b\u5e95\u5e95\u7684\u70df\u6d88\u4e91\u6563\u4e86\uff0c\u53ea\u662f\u6ca1\u597d\u610f\u601d\u5f00\u51fa\u53e3\uff0c\u8981\u4e0d\u7136\u8fd9\u5f53\u573a\u53c8\u8981\u5c06\u7fdf\u4e39\u9752\u8ba4\u4f5c\u5e72\u5973\u513f\u4e86\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u6324\u5728\u75c5\u623f\u91cc\uff0c\u5f20\u606a\u4e5f\u4e0d\u4fbf\u8ddf\u7fdf\u4e39\u9752\u8bf4\u4ec0\u4e48\u3002\u6298\u817e\u4e86\u4e00\u591c\uff0c\u6b64\u65f6\u5929\u5149\u5fae\u660e\u4e86\uff0c\u4ed6\u7684\u624b\u81c2\u51e0\u4e4e\u7ed9\u624e\u7a7f\uff0c\u4e5f\u6d41\u4e86\u4e0d\u5c11\u8840\uff0c\u5012\u771f\u662f\u611f\u5230\u6709\u4e9b\u4e4f\u529b\u5934\u6655\u4e86\uff0c\u624b\u81c2\u7684\u4f24\u53e3\u6ca1\u6709\u9ebb\u9189\uff0c\u8fd8\u662f\u5f88\u75db\uff0c\u4ed6\u5988\u8ba9\u4ed6\u53bb\u75c5\u623f\u4f11\u606f\uff0c\u4ed6\u4e5f\u5c31\u5148\u53bb\u9694\u58c1\u7684\u75c5\u623f\u6362\u4e86\u75c5\u53f7\u7a7f\u7684\u8863\u670d\u4f11\u606f\uff0c\u4ed6\u539f\u6253\u7b97\u7b49\u5230\u7fdf\u4e39\u9752\u7236\u6bcd\u8fc7\u6765\u518d\u4f11\u606f\u7684\uff0c\u4e0e\u7236\u4eb2\u5728\u623f\u95f4\u91cc\u8bf4\u7740\u8bdd\uff0c\u8bf4\u7740\u8bdd\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5c31\u7761\u7740\u4e86\u3002</p><p>\u3000\u3000\u518d\u9192\u8fc7\u6765\uff0c\u5c31\u770b\u89c1\u5510\u5a67\u5750\u5728\u5e8a\u524d\u624b\u81c2\u5c48\u5728\u5e8a\u6cbf\u4e0a\u652f\u7740\u4e0b\u5df4\u770b\u4e66\uff0c\u90fd\u4e0d\u6653\u5f97\u8c01\u5c06\u6d88\u606f\u544a\u8bc9\u5979\u7684\uff0c\u4f38\u624b\u6478\u4e86\u6478\u5979\u5149\u6ed1\u7684\u8138\u86cb\u3002</p><p>\u3000\u3000\u201c\u4f60\u9192\u8fc7\u6765\u4e86\u2026\u2026\u201d\u5510\u5a67\u6b23\u559c\u7684\u5750\u76f4\u8eab\u5b50\uff0c\u53c8\u89c9\u5f97\u624b\u81c2\u6709\u4e9b\u53d1\u9ebb\uff0c\u624b\u8f7b\u63c9\u7740\u80f3\u818a\u8098\uff0c\u8138\u6b6a\u8fc7\u6765\uff0c\u5c06\u5f20\u606a\u7684\u624b\u638c\u5939\u5728\u8138\u4e0e\u80a9\u4e4b\u95f4\uff0c\u611f\u53d7\u4ed6\u7684\u4f53\u6e29\u3002</p><p>\u3000\u3000\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fd8\u4e0d\u60f3\u8ba9\u4f60\u62c5\u5fc3\u7684\u5462\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u7761\u8db3\u4e86\u4e00\u89c9\uff0c\u611f\u89c9\u5df2\u7ecf\u597d\u591a\u4e86\uff0c\u4f24\u53e3\u4e5f\u6536\u4f4f\u4e86\uff0c\u4e0d\u600e\u4e48\u75bc\u75db\uff0c\u8f7b\u637b\u7740\u5510\u5a67\u7684\u8033\u5ed3\uff0c\u95ee\u9053\uff0c\u201c\u6211\u7761\u4e86\u591a\u4e45\uff1f\u201d\u7a97\u5e18\u62c9\u7740\uff0c\u5ba4\u5185\u706f\u5149\u67d4\u548c\uff0c\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u4e00\u89c9\u7761\u4e86\u591a\u4e45\u3002</p><p>\u3000\u3000\u201c\u592a\u9633\u5feb\u843d\u5c71\u4e86\u2026\u2026\u201d\u5510\u5a67\u7ad9\u8d77\u6765\u5e2e\u5f20\u606a\u5c06\u7a97\u5e18\u62c9\u5f00\uff0c\u8ba9\u843d\u65e5\u4f59\u6656\u503e\u6cc4\u5230\u623f\u95f4\u91cc\u6765\uff0c\u6d01\u767d\u7684\u623f\u95f4\u50cf\u9aa4\u7136\u67d3\u4e0a\u4e00\u5c42\u971e\u5149\u3002</p><p>\u3000\u3000\u5916\u95f4\u7684\u4eba\u542c\u5230\u91cc\u9762\u7684\u8bf4\u8bdd\u58f0\uff0c\u95e8\u7ed9\u4eba\u4ece\u5916\u95f4\u63a8\u5f00\u6765\uff0c\u9759\u6aac\u6400\u7740\u5979\u7236\u4eb2\u5b59\u5c1a\u4e49\u7684\u624b\u81c2\u63a2\u5934\u5f80\u91cc\u770b\uff0c\u4e00\u8138\u7684\u5173\u5207\uff0c\u95ee\u5019\u7684\u8bed\u6c14\u523b\u610f\u7684\u6de1\u4e86\u4e9b\uff1a\u201c\u4f60\u9192\u4e86\uff0c\u6211\u7238\u8ddf\u5510\u5a67\u4e58\u4e2d\u5348\u7684\u98de\u673a\u8d76\u5230\u5efa\u90ba\u7684\u3002\u201d\u63a8\u95e8\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u89c1\u5916\u95f4\u7684\u6c99\u53d1\u4e0a\u8fd8\u5750\u7740\u4ed6\u4e0d\u8ba4\u8bc6\u7684\u4eba\uff0c\u516d\u5341\u5c81\u5de6\u53f3\uff0c\u8138\u4e0a\u7684\u76b1\u7eb9\u5f88\u6df1\uff0c\u8eab\u4e0a\u7684\u4e2d\u5c71\u88c5\u6d17\u5f97\u53d1\u767d\uff0c\u4f46\u71a8\u5f97\u670d\u5e16\uff0c\u8fd9\u65f6\u5019\u4e5f\u8ddf\u7740\u7ad9\u8d77\u6765\uff0c\u5728\u95e8\u5916\u4e0d\u6653\u5f97\u8981\u4e0d\u8981\u8ddf\u7740\u8d70\u8fdb\u6765\u3002</p><p>\u3000\u3000\u201c\u7fdf\u4f2f\u4f2f\u5427\uff1f\u201d\u5f20\u606a\u6380\u88ab\u5750\u4e86\u8d77\u6765\uff0c\u201c\u4e39\u9752\u59d0\u597d\u4e9b\u4e86\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7684\u7236\u4eb2\u662f\u8001\u9000\u4f0d\u519b\u4eba\uff0c\u867d\u8bf4\u53ea\u662f\u5c0f\u5b66\u6bd5\u4e1a\u7684\u6587\u5316\uff0c\u9000\u4f0d\u540e\u5728\u5f53\u5730\u7684\u6751\u5c0f\u5f53\u8bed\u6587\u8001\u5e08\uff0c\u4e5f\u662f\u76f8\u5f53\u7684\u5c3d\u804c\uff0c\u662f\u4e2a\u6027\u5b50\u3001\u813e\u6c14\u5f88\u786c\u7684\u4eba\uff0c\u4e0d\u8fc7\u4eca\u5929\u770b\u5230\u5982\u6b64\u4e4b\u591a\u7684\u3001\u4e4b\u524d\u5728\u4ed6\u4e16\u754c\u4e4b\u5916\u7684\u5de8\u5bcc\u6743\u5ba6\u4eec\u90fd\u6001\u5ea6\u8c26\u548c\u7684\u8ddf\u4ed6\u8bf4\u8bdd\uff0c\u5fc3\u91cc\u591a\u5c11\u8fd8\u662f\u6709\u4e9b\u60f6\u7136\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u7ad9\u8d77\u6765\uff0c\u6276\u4e86\u7fdf\u4e39\u9752\u7684\u7236\u4eb2\u624b\u81c2\u4e00\u628a\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u54e5\u4e00\u8d77\u8fdb\u6765\u5750\u4e00\u5750\uff0c\u201d\u8d70\u8fdb\u6765\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u9676\u664b\u4e66\u8bb0\u521a\u521a\u8d70\uff0c\u6ca1\u6709\u8ba9\u53eb\u9192\u4f60\uff0c\u4f60\u611f\u89c9\u597d\u4e9b\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u597d\u591a\u4e86\uff0c\u201d\u5f20\u606a\u5fc3\u60f3\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u8fc7\u6765\u4e4b\u524d\u5e94\u8be5\u6ca1\u6709\u62db\u547c\u4e00\u58f0\uff0c\u8fd9\u4e8b\u538b\u540e\u518d\u8bf4\uff0c\u4ed6\u8fd9\u4e00\u89c9\u7761\u5230\u592a\u9633\u843d\u5c71\uff0c\u4e0d\u6653\u5f97\u9519\u8fc7\u591a\u5c11\u4eba\u6765\u63a2\u8bbf\uff0c\u4e5f\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u4ed6\u7ad9\u8d77\u6765\u63e1\u4f4f\u7fdf\u7236\u67af\u6811\u76ae\u4f3c\u7684\u7c97\u7cd9\u7684\u624b\uff0c\u95ee\u9053\uff0c\u201c\u4f2f\u6bcd\u5728\u4e39\u9752\u59d0\u90a3\u91cc\u5427\uff1f\u6211\u4eec\u53bb\u4e39\u9752\u59d0\u623f\u91cc\u770b\u4e00\u770b\u3002\u201d</p><p>\u3000\u3000\u8d70\u5230\u9694\u58c1\u7684\u75c5\u623f\uff0c\u5927\u5bb6\u90fd\u5728\u5916\u95f4\u7b49\u7740\uff0c\u539f\u6765\u7fdf\u4e39\u9752\u6bcd\u4eb2\u4e0e\u536b\u5170\u5728\u91cc\u95f4\u5e2e\u7fdf\u4e39\u9752\u64e6\u6d17\u8eab\u5b50\uff0c\u5085\u4fca\u8fd9\u65f6\u5019\u8d70\u8fdb\u6765\uff0c\u544a\u8bc9\u5f20\u606a\uff1a\u201c\u5e02\u5c40\u6253\u6765\u7535\u8bdd\uff0c\u6628\u5929\u9003\u8131\u7684\u90a3\u4e2a\u4eba\u521a\u521a\u5411\u8b66\u65b9\u81ea\u9996\u4e86\u2026\u2026\u201d</p></article>"}}}]);
