(window.webpackJsonp=window.webpackJsonp||[]).push([[948],{971:function(p){p.exports={chapterTitle:null,title:"\u7b2c764\u7ae0 \u5f02\u4e61\u76f8\u9047",content:"<article><h2>\u7b2c764\u7ae0 \u5f02\u4e61\u76f8\u9047</h2><p>\u3000\u3000\u4f4d\u4e8e\u4e1c\u4eac\u5343\u53f6\u53bf\u6d77\u6ee8\u7684\u65e5\u672c\u4f1a\u5c55\u4e2d\u5fc3\uff0c\u7eff\u6811\u73af\u62b1\uff0c\u666f\u8272\u6021\u4eba\uff0c\u4ece\u5efa\u7b51\u4e0a\u770b\uff0c\u4e5f\u53ef\u8c13\u6c14\u52bf\u6062\u5b8f\u3002\u4f46\u662f\u7531\u4e8e\u8fd1\u5e74\u6765\uff0c\u65e5\u672c\u56fd\u5185\u7ecf\u6d4e\u4e0d\u666f\u6c14\u3001\u80a1\u5e02\u4f4e\u8ff7\u3001\u8ba1\u7b97\u673a\u53ca\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u7684\u5e02\u573a\u9700\u6c42\u589e\u957f\u7f13\u6162\uff0c\u5bfc\u81f4\u65e5\u672c\u7684\u4f1a\u5c55\u4e1a\u4e5f\u968f\u4e4b\u4e0b\u6ed1\uff0c\u5343\u53f6\u7684\u4f1a\u5c55\u4e2d\u5fc3\u6bcf\u5e74\u7684\u5927\u591a\u6570\u65f6\u95f4\u90fd\u4e00\u76f4\u95e8\u5ead\u51b7\u843d\uff0c\u4e1c\u4eac\u7535\u5b50\u5c55\u4f1a\u671f\u95f4\u662f\u5343\u53f6\u4f1a\u5c55\u5343\u4e2d\u5fc3\u4e00\u5e74\u5f53\u4e2d\u5c11\u6709\u7684\u70ed\u95f9\u65e5\u5b50\u3002</p><p>\u3000\u3000\u867d\u7136\u4e1c\u4eac\u7535\u5b50\u5c55\u53f7\u79f0\u662f\u65e5\u672c\u6743\u5a01\u3001\u4e9a\u6d32\u4e00\u6d41\u7684\u56fd\u9645\u4f1a\u5c55\uff0c\u5c31\u5176\u516d\u4e07\u5e73\u65b9\u7c73\u5c55\u51fa\u89c4\u6a21\u6765\u8bf4\uff0c\u53ef\u4ee5\u8bf4\u662f\u4e9a\u6d32\u7535\u5b50\u5c55\u4e2d\u7684\u8001\u5927\uff0c\u4f46\u662f\u56fd\u9645\u5f71\u54cd\u529b\u5374\u8981\u6bd4\u9999\u6e2f\u7535\u5b50\u5c55\u900a\u8272\u8bb8\u591a\uff0c\u8fd1\u51e0\u5e74\u6bcf\u5e74\u6d77\u5916\u53c2\u89c2\u4eba\u6570\u8fd8\u4e0d\u8db3\u4e24\u5343\uff0c\u56fd\u9645\u5316\u7a0b\u5ea6\u8fd8\u5f88\u4e0d\u8db3\u3002\u4e3a\u4e86\u8fdb\u4e00\u6b65\u6269\u5927\u4e1c\u4eac\u7535\u5b50\u5c55\u7684\u56fd\u9645\u5316\u5f71\u54cd\uff0c\u4e3e\u529e\u5355\u4f4d\u4ece\u4e5d\u4e94\u5e74\u8d77\u5c31\u5c06\u4f1a\u5c55\u65f6\u95f4\u4ece\u6bcf\u5e74\u7684\u91d1\u79cb\u5341\u6708\u63a8\u5ef6\u5230\u6b21\u5e74\u7684\u4e00\u6708\uff0c\u66f4\u5927\u8303\u56f4\u7684\u7f34\u8bf7\u66f4\u591a\u77e5\u540d\u7535\u5b50\u5382\u5546\u53c2\u52a0\u3002\u53c2\u5c55\u7684\u5382\u5546\u9664\u65e5\u672c\u672c\u58eb\u7535\u5b50\u5382\u5546\u4e4b\u5916\uff0c\u6d77\u5916\u5382\u5546\u8fd8\u662f\u4ee5\u97e9\u56fd\u3001\u65b0\u52a0\u5761\u3001\u53f0\u6e7e\u3001\u9999\u6e2f\u7684\u7535\u5b50\u5382\u5546\u4e3a\u4e3b\uff0c\u4f1a\u5c55\u65f6\u95f4\u8c03\u6574\u4e4b\u540e\u4e5f\u591a\u4e86\u8bb8\u591a\u5bf9\u4e9a\u6d32\u5e02\u573a\u611f\u5174\u8da3\u7684\u6b27\u7f8e\u7535\u5b50\u5382\u5546\uff0c\u5927\u9646\u7684\u5382\u5546\u5c31\u5f20\u606a\u76ee\u524d\u6240\u77e5\u8fd8\u4ec5\u6709\u4ed6\u4eec\u4e00\u5bb6\u3002</p><p>\u3000\u3000\u97e9\u56fd\u7535\u5b50\u5382\u5546\u4e00\u5411\u4ee5\u65e5\u7cfb\u5382\u5546\u4e3a\u8ffd\u8d76\u76ee\u6807\uff0c\u5bf9\u4e1c\u4eac\u7535\u5b50\u5c55\u975e\u5e38\u91cd\u89c6\uff0c\u5f20\u606a\u5f88\u65e9\u5c31\u770b\u5230\u4e09\u661f\u72ec\u7acb\u5c55\u5385\u7684\u5916\u90e8\u7167\u7247\uff0c\u6bd4\u9526\u6e56\u8981\u663e\u773c\u5f97\u591a\u3002\u6240\u6709\u53c2\u5c55\u5382\u5546\u90fd\u80fd\u4ece\u4f1a\u5c55\u4e2d\u5fc3\u7684\u7f51\u7ad9\u83b7\u5f97\u4e00\u9875\u4e13\u95e8\u7684\u5ba3\u4f20\u7f51\u9875\uff0c\u9664\u4e86\u5c06\u6700\u57fa\u672c\u7684\u8054\u7edc\u65b9\u5f0f\u8f93\u5165\u4e4b\u5916\uff0c\u8fd8\u53ef\u4ee5\u5c06\u81ea\u5df1\u7684\u4f01\u4e1a\u7b80\u4ecb\u3001\u4ea7\u54c1\u3001\u56fe\u7247\u4e0a\u4f20\u5230\u7f51\u9875\u4e0a\uff0c\u76f8\u5f53\u4e8e\u4e00\u6b21\u7f51\u7ad9\u65b0\u95fb\u53d1\u5e03\uff0c\u4e5f\u65b9\u4fbf\u67e5\u5bfb\u53c2\u5c55\u5382\u5546\u7684\u4fe1\u606f\u3002\u5f20\u606a\u5c31\u4ece\u7f51\u7ad9\u4e0a\u67e5\u5bfb\u53c2\u5c55\u516c\u53f8\u540d\u5f55\u8fd8\u770b\u5230\u5609\u4fe1\u7535\u5b50\u8d6b\u7136\u5728\u5217\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e09\u661f\u72ec\u7acb\u5c55\u5385\u5916\u90e8\u8d34\u7740\u84dd\u8272\u7684\u8367\u5149\u724c\uff0c\u663e\u793a\u7740\u9887\u5927\u7684\u4e09\u661f\u6807\u8bc6\uff0c\u5f20\u606a\u4e0e\u8096\u664b\u6210\u8fdb\u4f1a\u5c55\u4e2d\u5fc3\u4e4b\u540e\u6ca1\u6709\u76f4\u63a5\u53bb\u9526\u6e56\u7684\u5c55\u53f0\uff0c\u800c\u662f\u5148\u8dd1\u5230\u4e09\u661f\u7684\u5c55\u5385\u6765\u3002\u9664\u4e86\u4e09\u661f\u4f20\u7edf\u7684\u4f18\u52bf\u767d\u7535\u4ea7\u54c1\u4e4b\u5916\uff0c\u5728\u90a3\u91cc\u8fd8\u80fd\u770b\u5230\u4e09\u661f\u5728\u53d1\u5c55\u6570\u5b57\u6280\u672f\u4e0a\u7684\u52aa\u529b\uff0c\u9664\u4e86\u7cbe\u81f4\u5c0f\u5de7\u7684\u6570\u5b57\u624b\u673a\u3001\u6db2\u6676\u663e\u793a\u5668\u3001\u6db2\u6676\u7535\u89c6\u3001\u7b49\u79bb\u5b50\u7535\u89c6\u7b49\u4e09\u661f\u65b0\u5f00\u53d1\u51fa\u6765\u7684\u65b0\u4ea7\u54c1\u4e4b\u5916\uff0c\u4e09\u661f\u8fd8\u5728\u8fd9\u6b21\u5c55\u4f1a\u4e0a\u63a8\u51fa\u4ed6\u4eec\u7684DVD\u789f\u673a\u4ea7\u54c1\uff1b\u4e09\u661f\u7684\u786e\u6709\u592a\u591a\u9700\u8981\u9526\u6e56\u536f\u8db3\u52b2\u624d\u80fd\u8ffd\u8d76\u4e0a\u7684\u65b0\u4ea7\u54c1\u6280\u672f\u3002</p><p>\u3000\u3000DVD\u789f\u673a\u8981\u5728\u4e2d\u56fd\u4e0e\u5176\u4ed6\u65b0\u5174\u5730\u533a\u63a8\u5e7f\uff0c\u6709\u4e00\u4e2a\u6700\u57fa\u672c\u7684\u6761\u4ef6\u5c31\u662f\u8981\u80fd\u517c\u5bb9\u64ad\u653eVCD\u3001SVCD\u7684\u789f\u7247\uff0c\u8fd9\u6837\u624d\u80fd\u89e3\u51b3\u51e0\u79cd\u4e0d\u540c\u683c\u5f0f\u7247\u6e90\u4e0a\u7684\u77db\u76fe\u3002VCD\u662f\u516c\u5f00\u7684\u6280\u672f\uff0cSVCD\u7684\u591a\u9879\u4e13\u5229\u6280\u672f\u63e1\u5728\u9526\u6e56\u624b\u91cc\uff0c\u5176\u4ed6\u5c0f\u5382\u4e5f\u5c31\u7f62\u4e86\uff0c\u4e09\u661f\u7684DVD\u789f\u673a\u60f3\u8981\u8fdb\u5165\u65b0\u5174\u5e02\u573a\uff0c\u9526\u6e56\u662f\u4ed6\u4eec\u7ed5\u4e0d\u8fc7\u53bb\u7684\u969c\u788d\uff0c\u9664\u975e\u4ed6\u4eec\u80fd\u8ba9DVD\u76d7\u7248\u789f\u5f7b\u5e95\u7684\u8986\u76d6\u7247\u6e90\u5e02\u573a\u3002</p><p>\u3000\u3000\u9526\u6e56\u6682\u65f6\u8fd8\u4e0d\u60f3\u5728\u56fd\u5185\u53ca\u5176\u4ed6\u65b0\u5174\u56fd\u5bb6\u63a8\u5e7fDVD\u789f\u673a\uff0c\u4e09\u661f\u4e5f\u65e0\u6cd5\u7406\u76f4\u6c14\u58ee\u7684\u8ddf\u9526\u6e56\u8c08SVCD\u6280\u672f\u7684\u6388\u6743\u95ee\u9898\uff0c\u773c\u4e0b\u5927\u5bb6\u4e00\u8d77\u5c06DVD\u789f\u673a\u63a8\u5411\u6d77\u5916\u53d1\u8fbe\u5730\u533a\u5e02\u573a\u624d\u662f\u6b63\u9014\u3002</p><p>\u3000\u3000\u5728\u4e09\u661f\u7684\u5c55\u5385\u91cc\u8f6c\u60a0\u4e86\u5927\u534a\u4e2a\u5c0f\u65f6\uff0c\u5f20\u606a\u4e0e\u8096\u664b\u6210\u624d\u60f3\u5230\u8981\u56de\u7231\u8fbe\u7535\u5b50\u7684\u5c55\u53f0\u53bb\uff0c\u5374\u610f\u5916\u770b\u5230\u674e\u99a8\u4e88\u6b63\u7ad9\u5728\u7231\u8fbe\u7684\u5c55\u53f0\u524d\u3002\u5979\u7a7f\u7740\u725b\u4ed4\u88e4\uff0c\u886c\u51fa\u4fee\u957f\u7f8e\u817f\uff0c\u767d\u5462\u5916\u5957\u7ee3\u6709\u9ed1\u8272\u5341\u5b57\u7eb9\uff0c\u534a\u622a\u5bbd\u5982\u8377\u53f6\u4f3c\u7684\u5916\u5957\u8896\u53e3\u9732\u51fa\u9ed1\u8272\u7684\u7ed2\u7ebf\u8896\u6765\uff0c\u80f3\u818a\u8098\u513f\u6302\u7740\u4e00\u53ea\u767d\u8272\u9ed1\u6761\u7eb9\u7684\u5973\u5f0f\u62ce\u5305\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u6b63\u97a0\u7740\u8eab\u5b50\u770b\u65e5\u672c\u5206\u516c\u53f8\u7684\u5de5\u4f5c\u4eba\u5458\u6f14\u793aiplayer\u7684\u529f\u80fd\uff0c\u5916\u5957\u540e\u6446\u5fae\u5fae\u540a\u8d77\uff0c\u9732\u51fa\u7ed9\u725b\u4ed4\u88e4\u7ef7\u5f97\u6d51\u5706\u7684\u81c0\u90e8\uff1b\u5f20\u606a\u6562\u80af\u5b9a\u6bcf\u4e00\u4e2a\u4eba\u8d70\u8fc7\u6765\u7684\u7537\u4eba\u90fd\u4f1a\u6709\u5728\u8fd9\u6d51\u5706\u81c0\u90e8\u4e0a\u6478\u4e00\u628a\u7684\u60f3\u6cd5\u3002</p><p>\u3000\u3000\u201c\u4e09\u661f\u5373\u4f7f\u8981\u523a\u63a2\u6211\u4eec\u7684\u5546\u4e1a\u60c5\u62a5\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u9700\u8981\u4f60\u51fa\u9762\u5427\uff1f\u201d\u5f20\u606a\u6487\u5634\u7b11\u7740\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u674e\u99a8\u4e88\u9661\u7136\u770b\u5230\u5f20\u606a\u7ad9\u5230\u8eab\u8fb9\u62ff\u97e9\u8bed\u8ddf\u5979\u8bf4\u8bdd\uff0c\u7ed9\u5413\u4e86\u4e00\u8df3\uff0c\u60ca\u53eb\u58f0\u60f9\u5f97\u5468\u56f4\u4eba\u4fa7\u76ee\u800c\u89c6\uff1b\u5979\u5373\u4f7f\u53d7\u60ca\u540e\u7684\u60ca\u53eb\u58f0\u4e5f\u662f\u6e05\u67d4\u8106\u7f8e\uff0c\u8ba9\u4eba\u542c\u8fc7\u4e4b\u540e\u96be\u4ee5\u5fd8\u6000\u3002\u674e\u99a8\u4e88\u624b\u63a9\u7740\u8138\u9762\uff0c\u6b20\u7740\u8eab\u5b50\u81f4\u6b49\u4e5f\u662f\u4e00\u8138\u7684\u5c34\u5c2c\uff0c\u7cbe\u81f4\u7684\u8138\u988a\u4e5f\u56e0\u4e3a\u5c34\u5c2c\u4e0e\u53d7\u5413\u800c\u6da8\u5f97\u901a\u7ea2\uff0c\u5c31\u50cf\u6e17\u4e86\u8840\u4f3c\u7684\u9c9c\u8273\uff0c\u5012\u662f\u8ba9\u5979\u770b\u4e0a\u53bb\u66f4\u52a0\u7684\u5a07\u5a9a\u3002</p><p>\u3000\u3000\u201c\u6ca1\u6709\u542c\u8bf4\u4f60\u4f1a\u5230\u65e5\u672c\u6765\u554a\uff1f\u201d\u674e\u99a8\u4e88\u5fc3\u7eea\u7a0d\u5b9a\u7591\u60d1\u7684\u62ff\u97e9\u8bed\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u621117\u65e5\u4ece\u5317\u4eac\u4e58\u98de\u673a\u5230\u540d\u53e4\u5c4b\u7684\uff0c\u67e5\u5bfb\u5efa\u90ba\u6216\u4e1c\u4eac\u7684\u822a\u73ed\uff0c\u7684\u786e\u4e0d\u4f1a\u77e5\u9053\u6211\u5230\u65e5\u672c\u6765\u3002\u201d\u5f20\u606a\u6700\u8fd1\u5f88\u8ba4\u771f\u7684\u52a0\u5f3a\u8fc7\u4e00\u6bb5\u65f6\u95f4\u7684\u97e9\u8bed\u5b66\u4e60\uff0c\u6bd4\u82f1\u8bed\u3001\u65e5\u8bed\u8981\u5dee\u4e00\u70b9\uff0c\u4f46\u662f\u7b80\u5355\u7684\u5bf9\u8bdd\u4e0d\u6210\u4ec0\u4e48\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u8d35\u516c\u53f8\u7684\u9648\u4fe1\u751f\u6628\u5929\u8fd8\u5728\u56fd\u5185\u7684\u5a92\u4f53\u524d\u9732\u9762\uff0c\u624d\u63a8\u60f3\u4f60\u6ca1\u6709\u5230\u65e5\u672c\u6765\uff0c\u53ef\u6ca1\u6709\u4eba\u4f1a\u4e13\u7a0b\u7684\u53bb\u67e5\u4f60\u7684\u884c\u7a0b\uff0c\u201d\u674e\u99a8\u4e88\u94f6\u7259\u5fae\u54ac\u7740\u4e0b\u5507\uff0c\u6709\u4e9b\u6c14\u9f13\u9f13\u7684\u8bf4\u9053\uff0c\u201c\u6211\u5728\u65e5\u672c\u4f11\u5047\uff0c\u60f3\u7740\u5230\u4f1a\u5c55\u4e2d\u5fc3\u6765\u6253\u53d1\u65f6\u5149\uff0c\u4e5f\u6ca1\u6709\u8981\u523a\u63a2\u8d35\u516c\u53f8\u5546\u4e1a\u60c5\u62a5\u7684\u610f\u601d\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u610f\u601d\uff0c\u53ea\u662f\u4e24\u4e2a\u4eba\u90fd\u7b97\u662f\u5728\u5f02\u4e61\u9047\u5230\u611f\u89c9\u5f88\u610f\u5916\uff0c\u4f60\u82e5\u662f\u4ecb\u610f\uff0c\u90a3\u6211\u6536\u56de\u521a\u624d\u7684\u8bdd\uff0c\u6211\u521a\u521a\u4e5f\u624d\u53bb\u4e09\u661f\u7684\u5c55\u5385\u770b\u8fc7\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u5012\u4e0d\u5fc5\u4e86\u2026\u2026\u201d\u674e\u99a8\u4e88\u4e0d\u662f\u90a3\u79cd\u5484\u5484\u903c\u4eba\u7684\u5973\u6027\uff0c\u5f20\u606a\u7a0d\u9000\u6b65\uff0c\u5979\u5c31\u4f1a\u4e3a\u81ea\u5df1\u521a\u624d\u7a0d\u7a0d\u5f3a\u786c\u7684\u8bdd\u89c9\u5f97\u5185\u759a\uff0c\u201c\u6211\u8bf4\u8bdd\u4e5f\u6709\u4e0d\u59a5\u3002\u201d\u5fc3\u91cc\u5374\u662f\u5947\u602a\uff1a\u600e\u4e48\u4f1a\u4e09\u756a\u4e94\u6b21\u7684\u9047\u5230\u8fd9\u4e2a\u6709\u4e9b\u65e0\u8d56\u6a21\u6837\u7684\u5bb6\u4f19\uff1f</p><p>\u3000\u3000\u201c\u5c55\u4f1a\u624d\u5f00\u59cb\u4e0d\u4e45\uff0c\u4f60\u4e0d\u4f1a\u770b\u8fc7\u6211\u4eec\u7684\u5c55\u53f0\u5c31\u6025\u7740\u79bb\u5f00\u5427\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4e0d\u554a\uff0c\u597d\u591a\u516c\u53f8\u90fd\u4f1a\u63a8\u51fa\u65b0\u5947\u7684\u65b0\u4ea7\u54c1\uff0c\u4f60\u4e5f\u53ef\u4ee5\u56db\u5904\u95f2\u901b\u5417\uff1f\u201d\u674e\u99a8\u4e88\u5e76\u4e0d\u4ecb\u610f\u5728\u4f1a\u5c55\u4e2d\u5fc3\u91cc\u4e34\u65f6\u591a\u4e00\u4e2a\u4f34\uff0c\u53ea\u662f\u6000\u7591\u5f20\u606a\u80fd\u8131\u5f00\u8eab\u3002</p><p>\u3000\u3000\u201c\u4f60\u90fd\u8bf4\u4e86\u9648\u4fe1\u751f\u8fd8\u5728\u56fd\u5185\u6211\u5c31\u4e0d\u5e94\u8be5\u51fa\u73b0\u5728\u65e5\u672c\uff1b\u6211\u5f53\u7136\u53ef\u4ee5\u966a\u4f60\u56db\u5904\u8d70\u4e00\u8d70\u2026\u2026\u201d\u5f20\u606a\u8ba9\u8096\u664b\u6210\u4ece\u5c55\u53f0\u5de5\u4f5c\u4eba\u5458\u90a3\u91cc\u62ff\u4e86\u4e00\u53eaiplayer\uff0c\u9012\u7ed9\u674e\u99a8\u4e88\uff0c\u8bf4\u9053\uff0c\u201c\u97e9\u56fd\u4eba\u4e5f\u6709\u8fc7\u6625\u8282\u7684\u4e60\u60ef\uff0c\u8fd9\u7b97\u662f\u6211\u9001\u7ed9\u4f60\u7684\u65b0\u5e74\u793c\u7269\uff0c\u521a\u624d\u770b\u4f60\u8fd9\u4e48\u8ba4\u771f\u7684\u5728\u542c\u5de5\u4f5c\u4eba\u5458\u7684\u8bb2\u89e3\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u614c\u4e71\u7684\u53cc\u624b\u63a5\u8fc7iplayer\uff0c\u5f88\u4e00\u672c\u6b63\u7ecf\u7684\u97a0\u8eac\u9053\u8c22\uff0c\u5012\u662f\u4e0e\u5f71\u89c6\u5267\u91cc\u7684\u97e9\u56fd\u4f20\u7edf\u5973\u6027\u4e00\u6837\uff0c\u867d\u7136\u5979\u7684\u7a7f\u7740\u6253\u626e\u76f8\u5f53\u5165\u65f6\u3002</p><p>\u3000\u3000\u5f20\u606a\u5230\u5c55\u4f1a\u4e0a\u6765\u7684\u610f\u56fe\u4e5f\u662f\u56db\u5904\u95f2\u901b\uff0c\u5bfb\u627e\u4ed6\u6ca1\u6709\u610f\u8bc6\u5230\u5374\u4f1a\u5728\u672a\u6765\u4e0d\u4e45\u5d1b\u8d77\u7684\u65b0\u4ea7\u54c1\u6280\u672f\uff0c\u53c2\u89c2\u7d22\u5c3c\u3001\u677e\u4e0b\u3001\u4e1c\u829d\u3001\u590f\u666e\u7b49\u516c\u53f8\u7684\u5c55\u5385\u4e5f\u662f\u5fc5\u9700\u7684\u3002\u5728\u7d22\u5c3c\u7684\u5c55\u53f0\uff0c\u5f20\u606a\u8fd8\u770b\u5230\u201c\u8001\u670b\u53cb\u201d\uff1a\u7d22\u5c3c\u539f\u5143\u5668\u4ef6\u90e8\u526f\u603b\u88c1\u6e21\u8fb9\u656c\u4e00\u3002</p><p>\u3000\u3000\u6e21\u8fb9\u656c\u4e00\u66fe\u8d1f\u8d23\u7d22\u5c3c\u5143\u5668\u4ef6\u90e8\u7684\u789f\u673a\u673a\u82af\u4e8b\u52a1\uff0c\u7531\u4e8e\u9526\u6e56\u4e0e\u98de\u5229\u6d66\u3001\u65af\u9ad8\u67cf\u3001\u5fb7\u4eea\u79d8\u5bc6\u7ed3\u76df\uff0c\u5c06\u7d22\u5c3c\u6392\u9664\u51faSVCD\u789f\u673a\u673a\u82af\u7684\u5236\u9020\uff0c\u8fd9\u662f\u7d22\u5c3c\u8fd1\u5e74\u6765\u5c11\u6709\u7684\u5728\u4ea7\u4e1a\u4e0a\u7684\u6e83\u8d25\uff0c\u5176\u540e\u6e21\u8fb9\u656c\u4e00\u7ed9\u8c03\u56de\u65e5\u672c\uff0c\u4e5f\u4e0d\u6653\u5f97\u4ed6\u8fd4\u56de\u7d22\u5c3c\u603b\u90e8\u7684\u60c5\u51b5\uff0c\u770b\u4ed6\u51fa\u73b0\u5728\u7d22\u5c3c\u5c55\u5385\u91cc\uff0c\u8bf4\u4e0d\u5b9a\u804c\u4f4d\u8c03\u5230\u7d22\u5c3c\u7684\u65b0\u4ea7\u54c1\u5f00\u53d1\u90e8\u95e8\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u7d22\u5c3c\u7684\u5168\u7403\u9500\u552e\u5c06\u8d85\u8fc7\u4e94\u767e\u4ebf\u7f8e\u5143\uff0c\u662f\u7231\u8fbe\u96c6\u56e2\u7684\u4e00\u767e\u591a\u500d\u3002</p><p>\u3000\u3000\u4f46\u662f\u786e\u786e\u5b9e\u5b9e\u7684\uff0c\u7d22\u5c3c\u66fe\u5728\u67d0\u6b64\u5c0f\u6218\u573a\u4e0a\u7ed9\u9526\u6e56\u5f7b\u5e95\u51fb\u6e83\u3002</p><p>\u3000\u3000\u6e21\u8fb9\u656c\u4e00\u66fe\u5230\u8fc7\u6d77\u5dde\uff0c\u4f46\u5bf9\u5f20\u606a\u4e0d\u751a\u719f\u6089\uff0c\u77e5\u9053\u8fd9\u4e48\u4e2a\u4eba\uff0c\u4f46\u662f\u5bf9\u4ed6\u7684\u76f8\u8c8c\u6ca1\u6709\u7559\u4e0b\u4efb\u4f55\u5370\u8c61\u3002\u6e21\u8fb9\u656c\u4e00\u5de1\u89c6\u8fc7\u7d22\u5c3c\u7684\u5c55\u5385\uff0c\u5e76\u6ca1\u6709\u610f\u8bc6\u5230\u5728\u7d22\u5c3c\u5c55\u5385\u91cc\u7684\u4e00\u5bf9\u4fca\u7537\u7f8e\u5973\u8eab\u4efd\u4f1a\u5982\u6b64\u602a\u5f02\u3002</p><p>\u3000\u3000\u8d70\u51fa\u7d22\u5c3c\u7684\u5c55\u5385\uff0c\u6709\u4e24\u540d\u4fc3\u9500\u5c0f\u59d0\u8dd1\u8fc7\u6765\u6563\u53d1\u4f20\u5355\uff0c\u7a7f\u7740\u4e0a\u8eab\u94f6\u7c89\u95ea\u95ea\u4e0e\u592a\u7a7a\u670d\u7c7b\u4f3c\u3001\u4e0b\u8eab\u5219\u662f\u9ad8\u5f00\u53c9\u7684\u6bd4\u57fa\u5c3c\uff0c\u4e0d\u6653\u5f97\u662f\u8c01\u8bbe\u8ba1\u4e86\u8fd9\u6837\u7684\u5ba3\u4f20\u670d\u88c5\uff0c\u5f20\u606a\u8d77\u521d\u6ca1\u6709\u610f\u8bc6\u5230\u4ec0\u4e48\uff0c\u4ed6\u4e0e\u674e\u99a8\u4e88\u8c08\u7b11\u98ce\u751f\uff0c\u8fd8\u4f38\u624b\u53bb\u4e3b\u52a8\u63a5\u4f20\u5355\uff0c\u76f4\u5230\u6709\u540d\u4fc3\u9500\u5c0f\u59d0\u50bb\u773c\u4f3c\u7684\u7ad9\u5728\u90a3\u91cc\u76ef\u7740\u5f20\u606a\uff0c\u5f20\u606a\u624d\u60f3\u8d77\u6765\u8fd9\u4e2a\u5973\u5b69\u5b50\u662f\u6628\u5929\u5728\u65b0\u5bbf\u7235\u58eb\u4e50\u9152\u5427\u91cc\u9047\u5230\u7684\u90a3\u4e2a\u4e0a\u6d77\u5973\u5b69\u5b50\uff0c\u6ca1\u60f3\u5230\u5979\u767d\u5929\u53c8\u5230\u5c55\u4f1a\u4e0a\u6765\u5f53\u4fc3\u9500\u5c0f\u59d0\u8fd8\u6253\u96f6\u5de5\u3002</p><p>\u3000\u3000\u90a3\u5973\u5b69\u5b50\u4f3c\u4e4e\u5f88\u5bb3\u6015\u5f20\u606a\u63ed\u7a7f\u5979\u5728\u591c\u5e97\u966a\u9152\u6253\u5de5\u7684\u4e8b\u60c5\uff0c\u7741\u773c\u770b\u7740\u5f20\u606a\uff0c\u8138\u8272\u90fd\u6709\u4e9b\u53d1\u767d\uff0c\u5979\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u5374\u4e0d\u662f\u6628\u591c\u4e00\u540c\u51fa\u73b0\u5728\u9152\u5427\u7684\u90a3\u4f4d\u3002\u5f20\u606a\u6628\u5929\u5c31\u6ce8\u610f\u5230\u8fd9\u5973\u5b69\u5b50\u6709\u4e00\u53cc\u4fee\u957f\u7684\u5927\u817f\uff0c\u867d\u7136\u4e0d\u53ca\u8eab\u8fb9\u7684\u674e\u99a8\u4e88\u5343\u5a07\u767e\u5a9a\uff0c\u8138\u86cb\u4e5f\u7edd\u5bf9\u53ef\u4ee5\u5f52\u5165\u5230\u6f02\u4eae\u7684\u4e00\u7c7b\uff0c\u770b\u5979\u6628\u5929\u7684\u8868\u73b0\uff0c\u4f3c\u4e4e\u4e5f\u624d\u521a\u521a\u7ed9\u4eba\u62c9\u4e0b\u6c34\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u5979\u624b\u91cc\u6367\u7684\u4e00\u5927\u6367\u4f20\u5355\u62ff\u51fa\u4e00\u5f20\uff0c\u5fae\u5fae\u6b20\u7740\u8eab\u4e0a\u9053\u8c22\u3002\u5f20\u606a\u5e76\u4e0d\u662f\u540c\u60c5\u5fc3\u6cdb\u6ee5\u6210\u707e\u7684\u4eba\uff0c\u89c1\u5979\u4e00\u526f\u60f6\u6050\u4e0d\u5b89\u7684\u6837\u5b50\uff0c\u5f20\u606a\u6ca1\u6709\u8bf4\u4ec0\u4e48\u5c31\u4e0e\u674e\u99a8\u4e88\u8f6c\u5230\u53e6\u5916\u7684\u5c55\u53f0\u53bb\u4e86\u3002</p></article>"}}}]);
