(window.webpackJsonp=window.webpackJsonp||[]).push([[949],{972:function(p){p.exports={chapterTitle:null,title:"\u7b2c946\u7ae0 \u63a2\u89c6\u4e0e\u6c47\u62a5",content:"<article><h2>\u7b2c946\u7ae0 \u63a2\u89c6\u4e0e\u6c47\u62a5</h2><p>\u3000\u3000\u4e8b\u4ef6\u53d1\u751f\u540e\uff0c\u4f55\u7eaa\u4e91\u6beb\u4e0d\u72b9\u8c6b\u7684\u5c31\u8c03\u52a8\u9a6c\u83b2\u8857\u3001\u6e56\u4e1c\u8857\u7b49\u5730\u6d3e\u51fa\u6240\u4ee5\u53ca\u533a\u5206\u5c40\u7684\u8b66\u529b\u6293\u6355\u9003\u72af\uff0c\u53ca\u65f6\u5c01\u9501\u4e86\u8fdb\u51fa\u71d5\u5f52\u6e56\u533a\u57df\u7684\u9053\u8def\uff0c\u6df1\u591c\u8fc7\u5883\u8f66\u91cc\u90fd\u4e25\u52a0\u76d8\u67e5\uff0c\u5c31\u6015\u8fd9\u8fb9\u771f\u95f9\u51fa\u4eba\u547d\u518d\u7ed9\u4eba\u9003\u8131\u5c31\u5f88\u96be\u4ea4\u4ee3\u4e86\u3002\u7f57\u541b\u6307\u793a\u5e02\u5c40\u63a5\u624b\u540e\uff0c\u5e02\u5c40\u66f4\u662f\u4ece\u4e1c\u534e\u533a\u5206\u5c40\u4ee5\u53ca\u5e02\u5c40\u8c03\u52a8\u8b66\u529b\uff0c\u66f4\u5927\u8303\u56f4\u7684\u52a0\u5f3a\u5bf9\u706b\u8f66\u7ad9\u3001\u957f\u9014\u8f66\u7ad9\u3001\u7801\u5934\u3001\u673a\u573a\u4ee5\u53ca\u9ad8\u901f\u8def\u3001\u56fd\u9053\u7b49\u8def\u53e3\u901a\u884c\u7684\u76d8\u67e5\u529b\u5ea6\uff0c\u71d5\u5f52\u6e56\u9644\u8fd1\u5404\u5927\u9ad8\u6821\u7684\u4fdd\u5b89\u5904\u4e5f\u90fd\u8c03\u52a8\u8d77\u6765\u6e05\u67e5\u6821\u56ed\u91cc\u5bb9\u6613\u85cf\u4eba\u7684\u89d2\u843d\u3002</p><p>\u3000\u3000\u9003\u8131\u7684\u90a3\u4eba\u5728\u8eb2\u85cf\u8fd1\u4e8c\u5341\u5c0f\u65f6\u4e4b\u540e\uff0c\u770b\u5230\u6ca1\u6709\u9003\u51fa\u5efa\u90ba\u7684\u5e0c\u671b\uff0c\u8feb\u4e8e\u641c\u6355\u7684\u538b\u529b\uff0c\u4e3b\u52a8\u5411\u641c\u67e5\u7684\u6b66\u8b66\u6295\u6848\u81ea\u9996\u2014\u2014\u53c2\u4e0e\u631f\u6301\u7684\u4e94\u4eba\u65e0\u4e00\u9003\u8131\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u5085\u4fca\u6c47\u62a5\u8fc7\u6293\u6355\u7684\u60c5\u51b5\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u65e2\u7136\u4eba\u90fd\u6293\u4f4f\u4e86\uff0c\u63a5\u4e0b\u6765\u7684\u4e8b\u60c5\u4e5f\u4e0d\u60f3\u76f4\u63a5\u63d2\u624b\u4e86\uff0c\u603b\u8981\u7ed9\u5404\u65b9\u9762\u7559\u4e9b\u9762\u5b50\uff0c\u5ba1\u8baf\u7684\u5de5\u4f5c\uff0c\u5efa\u90ba\u5e02\u8b66\u65b9\u3001\u68c0\u5bdf\u673a\u5173\u4e0d\u4f1a\u4e0d\u5c3d\u529b\u3002</p><p>\u3000\u3000\u5176\u5b9e\uff0c\u5728\u5f20\u606a\u7761\u9192\u8fc7\u4e4b\u524d\uff0c\u6301\u5200\u523a\u4e2d\u7fdf\u4e39\u9752\u3001\u5f20\u606a\u90a3\u540d\u51f6\u624b\u4e5f\u6ca1\u6709\u80fd\u4fdd\u4f4f\u4e00\u6761\u547d\uff0c\u5c31\u5728\u5e02\u7b2c\u4e00\u4eba\u6c11\u533b\u9662\u7684\u7279\u522b\u6551\u6cbb\u5ba4\u91cc\u3001\u5728\u8b66\u65b9\u7684\u4e25\u5bc6\u76d1\u63a7\u4e0b\u505c\u6b62\u4e86\u547c\u5438\uff0c\u5374\u5f97\u4e0d\u5230\u4efb\u4f55\u4eba\u7684\u540c\u60c5\u2014\u2014\u5085\u4fca\u89c9\u5f97\u8fd9\u65f6\u5019\u6ca1\u6709\u5fc5\u8981\u5927\u715e\u98ce\u666f\u7684\u5f53\u7740\u4f17\u4eba\u7684\u9762\u5c06\u8fd9\u4e8b\u8bf4\u51fa\u6765\uff0c\u4e5f\u5c31\u6682\u4e14\u4e0d\u63d0\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u5915\u9633\u659c\u6656\u4ece\u7a97\u5916\u503e\u6cc4\u8fdb\u6765\uff0c\u5ba4\u5185\u7684\u4e00\u5207\u90fd\u67d3\u7740\u6de1\u6de1\u7684\u971e\u5149\u5f02\u5f69\u3002</p><p>\u3000\u3000\u64e6\u6d17\u8fc7\u8eab\u5b50\u7684\u7fdf\u4e39\u9752\u8fd8\u53ea\u80fd\u5e73\u8eba\u5728\u75c5\u5e8a\u4e0a\uff0c\u76d6\u7740\u533b\u9662\u91cc\u5e26\u6d88\u6bd2\u6c34\u5473\u7684\u8584\u88ab\uff0c\u8584\u8584\u7684\u88ab\u5b50\u51f8\u51fa\u73b2\u73d1\u7684\u8eab\u4f53\u66f2\u7ebf\u6765\uff0c\u767d\u7699\u7684\u53cc\u624b\u653e\u5728\u88ab\u5b50\u4e0a\uff0c\u4e00\u53ea\u624b\u8fd8\u5728\u6302\u540a\u6ef4\uff0c\u8138\u8272\u6bd4\u6e05\u6668\u8981\u597d\u4e00\u4e9b\uff0c\u773c\u775b\u4e5f\u6062\u590d\u4e86\u4e9b\u795e\u91c7\uff0c\u53ea\u662f\u773c\u795e\u6709\u610f\u65e0\u610f\u7684\u907f\u5f00\u53bb\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u53ea\u662f\u8ddf\u7fdf\u4e39\u9752\u7684\u6bcd\u4eb2\u8bf4\u4e86\u4e9b\u5bbd\u6170\u7684\u8bdd\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7684\u6bcd\u4eb2\u662f\u4e2a\u5178\u578b\u7684\u519c\u6751\u5987\u5973\uff0c\u8138\u5f62\u5468\u6b63\uff0c\u4e0d\u8fc7\u4ece\u5979\u7ed9\u5c81\u6708\u6467\u6b8b\u7684\u8138\u4e0a\u53ef\u770b\u4e0d\u51fa\u80fd\u751f\u51fa\u5982\u6b64\u82b1\u5bb9\u6708\u8c8c\u7684\u5973\u513f\u6765\uff0c\u5728\u7fdf\u4e39\u9752\u75c5\u623f\u91cc\u95f2\u804a\u4e86\u7247\u523b\uff0c\u5085\u4fca\u53c8\u8d70\u8fdb\u6765\u544a\u8bc9\u4ed6\uff1a\u201c\u9a6c\u6d77\u9f99\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u8bf4\u662f\u4f59\u4e66\u8bb0\u3001\u9648\u526f\u5385\u957f\u4e0e\u9648\u5c40\u957f\u4e00\u4f1a\u513f\u5230\u533b\u9662\u6765\u7814\u7a76\u6848\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9a6c\u6d77\u9f99\u4e5f\u6765\u4e86\uff1f\u201d\u95ee\u51fa\u8fd9\u53e5\u8bdd\uff0c\u5f20\u606a\u5c31\u89c9\u5f97\u4ed6\u95ee\u8fd9\u8bdd\u771f\u662f\u591a\u4f59\uff0c\u8fd9\u91cc\u53d1\u751f\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u9a6c\u6d77\u9f99\u600e\u4e48\u53ef\u80fd\u5728\u6d77\u5dde\u5750\u5f97\u4f4f\uff1f</p><p>\u3000\u3000\u201c\u6e05\u6668\u5c31\u5230\u4e86\uff0c\u4f60\u90a3\u65f6\u521a\u7761\u3002\u4ed6\u672c\u6765\u8fd8\u80fd\u518d\u65e9\u4e9b\uff0c\u53ea\u662f\u5bf9\u6d77\u5dde\u90a3\u8fb9\u7684\u4fdd\u5168\u5de5\u4f5c\u53c8\u6709\u4e9b\u4e0d\u5927\u653e\u5fc3\uff0c\u4ed4\u7ec6\u76ef\u8fc7\u4e00\u904d\u4e4b\u540e\u624d\u5750\u8f66\u8fc7\u6765\u2014\u2014\u5230\u5efa\u90ba\u540e\uff0c\u4ed6\u4e00\u76f4\u5728\u5e02\u516c\u5b89\u5c40\u90a3\u91cc\u3002\u201d\u5085\u4fca\u89e3\u91ca\u9053\uff0c\u5e02\u516c\u5b89\u5c40\u90a3\u8fb9\u8981\u4e2a\u4eba\u76ef\u7740\uff0c\u6ca1\u6709\u6bd4\u9a6c\u6d77\u9f99\u66f4\u5408\u9002\u7684\u4eba\u4e86\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u653f\u6cd5\u59d4\u4e66\u8bb0\u4f59\u5e86\u6797\u3001\u6c5f\u5357\u7701\u516c\u5b89\u5385\u526f\u5385\u957f\u9648\u99a5\u6770\u3001\u5efa\u90ba\u5e02\u516c\u5b89\u5c40\u5c40\u957f\u9648\u79c9\u5fb7\u8dd1\u5230\u533b\u9662\u91cc\u6765\u7814\u7a76\u6848\u60c5\uff0c\u8bf4\u7684\u597d\u542c\u662f\u7814\u7a76\u6848\u60c5\uff0c\u5176\u5b9e\u662f\u6765\u6c47\u62a5\u6848\u60c5\u8fdb\u5c55\u7684\uff0c\u6240\u4ee5\u674e\u8fdc\u6e56\u4e0d\u6765\u3001\u7f57\u541b\u4e0d\u6765\u3002</p><p>\u3000\u3000\u4e0d\u6253\u6270\u7fdf\u4e39\u9752\u4f11\u606f\uff0c\u5f20\u606a\u4e0e\u7236\u4eb2\u8fd8\u662f\u6709\u5b59\u5c1a\u4e49\u5148\u56de\u5230\u81ea\u5df1\u7684\u75c5\u623f\u91cc\u7b49\u4f59\u5e86\u6797\u7b49\u4eba\u8fc7\u6765\uff0c\u7fdf\u7236\u77e5\u9053\u8fd9\u8fb9\u53c2\u4e0e\u4e0d\u4e0a\uff0c\u4e0d\u8981\u8bf4\u5f20\u77e5\u884c\u662f\u65b0\u829c\u7684\u5e38\u52a1\u526f\u5e02\u957f\uff0c\u4eca\u5929\u6765\u63a2\u89c6\u7684\u5b98\u5458\u91cc\uff0c\u5927\u591a\u6570\u4eba\u7ea7\u522b\u6bd4\u5f20\u77e5\u884c\u8fd8\u8981\u9ad8\uff0c\u4ed6\u4e3b\u52a8\u7559\u5728\u90a3\u8fb9\uff0c\u4e0d\u5f71\u54cd\u4ed6\u4eec\u8bf4\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u867d\u8bf4\u662f\u6295\u6848\u81ea\u9996\uff0c\u4f46\u81f3\u5c11\u5c06\u6848\u72af\u90fd\u63a7\u5236\u4f4f\u4e86\uff0c\u9648\u79c9\u5fb7\u8eab\u4e0a\u7684\u538b\u529b\u8f7b\u4e86\u4e00\u534a\uff0c\u5ba1\u8baf\u7684\u5de5\u4f5c\u5012\u662f\u7b80\u5355\uff0c\u5e94\u8be5\u8fd8\u662f\u8981\u4e0e\u5510\u82f1\u80b2\u3001\u5510\u82f1\u57f9\u5144\u5f1f\u7684\u6848\u5b50\u8054\u7cfb\u8d77\u6765\u5ba1\uff0c\u4e0d\u8fc7\u8fd9\u5c31\u662f\u6c5f\u5357\u7701\u8b66\u65b9\u7684\u4e8b\u60c5\u4e86\u3002\u4ed6\u6068\u4e0d\u5f97\u8d76\u7d27\u5c06\u6848\u5b50\u79fb\u4ea4\u7ed9\u9648\u99a5\u6770\uff0c\u53ea\u662f\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u6ca1\u6709\u5f00\u53e3\uff0c\u4ed6\u8fd8\u5f97\u786c\u7740\u5934\u76ae\u5c06\u8d23\u4efb\u5148\u62c5\u4e0b\u6765\u3002\u4ed6\u5fc3\u91cc\u60f3\u9648\u99a5\u6770\u8eab\u4e0a\u7684\u538b\u529b\u5927\u6982\u4e0d\u5c11\uff0c\u867d\u7136\u6c5f\u5357\u662f\u7701\u957f\u6881\u4f1f\u6cd5\u76f4\u63a5\u7ed9\u7701\u516c\u5b89\u5385\u4e0b\u4e86\u6307\u793a\uff0c\u4f46\u662f\u8c01\u90fd\u77e5\u9053\u771f\u6b63\u4f1a\u5389\u773c\u76ef\u7740\u516c\u5b89\u5385\u7684\u662f\u7701\u59d4\u4e66\u8bb0\u5f90\u5b66\u5e73\u3002</p><p>\u3000\u3000\u5e02\u516c\u5b89\u5c40\u5c40\u957f\u9648\u79c9\u5fb7\u6ca1\u6709\u4e0e\u5f20\u606a\u600e\u4e48\u63a5\u89e6\u8fc7\uff0c\u6700\u591a\u7684\u63a5\u89e6\uff0c\u4e5f\u662f\u51e0\u6b21\u91cd\u5927\u7684\u6d3b\u52a8\u4ed6\u8d1f\u8d23\u4fdd\u5168\u5de5\u4f5c\u7167\u8fc7\u9762\uff0c\u90fd\u6ca1\u6709\u8bf4\u8bdd\u7684\u673a\u4f1a\uff0c\u4ed6\u966a\u4f59\u5e86\u6797\u3001\u9648\u99a5\u6770\u5728\u7701\u4eba\u6c11\u533b\u9662\u4f4f\u9662\u90e8\u5927\u697c\u524d\u4e0b\u4e86\u8f66\uff0c\u9a6c\u6d77\u9f99\u81ea\u7136\u8ddf\u7740\u4e00\u8d77\u56de\u533b\u9662\uff0c\u9648\u79c9\u5fb7\u8fd8\u7279\u610f\u8ba9\u533a\u5206\u5c40\u7684\u526f\u5c40\u957f\u4f55\u7eaa\u4e91\u966a\u540c\u7740\u4e00\u8d77\u8fc7\u6765\u3002\u4ed6\u4eec\u4e0b\u4e86\u8f66\u5c31\u76f4\u63a5\u5f80\u4f4f\u9662\u90e8\u5927\u697c\u5927\u5385\u91cc\u94bb\uff0c\u770b\u89c1\u4ed6\u59bb\u5144\u9b4f\u51a0\u534e\u7a7f\u7740\u767d\u5927\u8902\u8fce\u8fc7\u6765\uff0c\u4ecb\u7ecd\u7ed9\u4f59\u5e86\u6797\u3001\u9648\u99a5\u6770\u7b49\u4eba\u8ba4\u8bc6\uff1a\u201c\u8fd9\u4f4d\u662f\u7701\u4eba\u6c11\u533b\u9662\u526f\u9662\u957f\u9b4f\u51a0\u534e\uff0c\u662f\u5fc3\u8111\u5916\u79d1\u7684\u6743\u5a01\u4e13\u5bb6\u2026\u2026\u201d\u6ca1\u6709\u4ecb\u7ecd\u79c1\u4eba\u5173\u7cfb\uff0c\u95ee\u59bb\u5144\u9b4f\u51a0\u534e\uff0c\u201c\u6211\u4eec\u8fc7\u6765\u627e\u6628\u5929\u591c\u91cc\u5165\u9662\u7684\u4e24\u4f4d\u75c5\u4eba\uff0c\u6211\u4e0e\u4f59\u4e66\u8bb0\u8fd8\u6709\u6c5f\u5357\u7701\u516c\u5b89\u5385\u7684\u9648\u5385\u957f\u8fc7\u6765\u627e\u4ed6\u6709\u4e8b\u60c5\uff0c\u4f60\u77e5\u9053\u4ed6\u4eec\u4f4f\u54ea\u91cc\u5c31\u5e26\u6211\u4eec\u53bb\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u9a6c\u6d77\u9f99\u8ddf\u7740\u9648\u79c9\u5fb7\u4e00\u8d77\u5230\u533b\u9662\u91cc\u6765\uff0c\u4ed6\u62ac\u773c\u770b\u4e86\u770b\u9b4f\u51a0\u534e\uff0c\u770b\u5e74\u7eaa\u4e0d\u5927\uff0c\u4e94\u5341\u591a\u5c81\uff0c\u4e24\u9b13\u7684\u5934\u53d1\u5374\u90fd\u6709\u4e9b\u971c\u767d\u4e86\uff0c\u81ea\u5df1\u5728\u8fd9\u91cc\uff0c\u9648\u79c9\u5fb7\u627e\u8fd9\u4e2a\u9b4f\u51a0\u534e\u5e26\u8def\u662f\u4e0d\u662f\u591a\u6b64\u4e00\u4e3e\uff1f\u8bf4\u662f\u5fc3\u8111\u5916\u79d1\u7684\u4e13\u5bb6\uff0c\u9a6c\u6d77\u9f99\u6e05\u65e9\u8d76\u5230\u533b\u9662\u65f6\u53c2\u52a0\u6cbb\u7597\u7684\u533b\u751f\u90fd\u5728\u573a\uff0c\u6ca1\u6709\u89c1\u5230\u8fd9\u4e2a\u6240\u8c13\u4e13\u5bb6\u7684\u8eab\u5f71\uff0c\u4e0d\u6653\u5f97\u9648\u79c9\u5fb7\u6253\u4ec0\u4e48\u5fc3\u601d\uff0c\u4e5f\u4e0d\u4fbf\u62e6\u4f4f\u4e0d\u8ba9\u9b4f\u51a0\u534e\u5e26\u8def\u3002</p><p>\u3000\u3000\u9b4f\u51a0\u534e\u5f53\u7136\u77e5\u9053\u6628\u5929\u6df1\u591c\u5165\u9662\u7684\u90a3\u4e24\u540d\u88ab\u9510\u5668\u523a\u4f24\u7537\u5973\u4f4f\u54ea\u91cc\uff0c\u4eca\u5929\u8fd8\u60f3\u627e\u673a\u4f1a\u63a5\u89e6\u4e00\u4e0b\uff1b\u4f46\u4ed6\u51ff\u5b9e\u4e0d\u77e5\u9053\u5f20\u606a\u7684\u8eab\u4efd\uff0c\u6574\u4e2a\u7701\u4eba\u6c11\u533b\u9662\u91cc\u5c31\u9662\u957f\u845b\u6c11\u4fca\u4e00\u4eba\u77e5\u9053\u3002\u845b\u6c11\u4fca\u795e\u795e\u53e8\u53e8\u7684\u4ece\u4ed6\u5206\u7ba1\u7684\u5916\u79d1\u8c03\u4e86\u4e24\u540d\u4e13\u5bb6\u518d\u52a0\u4e0a\u4f4f\u9662\u90e8\u4e3b\u4efb\u4e34\u65f6\u6210\u7acb\u4e86\u4e00\u4e2a\u533b\u5e08\u5c0f\u7ec4\uff0c\u845b\u6c11\u4fca\u4eb2\u81ea\u62c5\u4efb\u533b\u5e08\u5c0f\u7ec4\u7684\u7ec4\u957f\uff0c\u8bf4\u662f\u8981\u7edd\u5bf9\u4fdd\u969c\u4e24\u540d\u75c5\u4eba\u7684\u751f\u547d\u5b89\u5168\uff0c\u4e0d\u8ba9\u4ed6\u8fd9\u4e2a\u5206\u7ba1\u7684\u526f\u9662\u957f\u63d2\u624b\uff1b\u8981\u4e0d\u662f\u5165\u4f4f\u75c5\u4eba\u7684\u5bb6\u5c5e\u53cd\u5bf9\uff0c\u845b\u6c11\u4fca\u80fd\u5c06\u4f4f\u9662\u90e8\u9876\u697c\u6574\u6574\u4e00\u5c42\u697c\u7684\u9ad8\u5e72\u7279\u62a4\u75c5\u623f\u90fd\u6e05\u51fa\u6765\u7ed9\u4eba\u5bb6\u3002</p><p>\u3000\u3000\u9b4f\u51a0\u534e\u4e5f\u4e0d\u662f\u4e0d\u61c2\u4e16\u6545\uff0c\u4f46\u4e5f\u505a\u4e0d\u5230\u8fd9\u6837\u7684\u5730\u6b65\u3002</p><p>\u3000\u3000\u653f\u6cd5\u59d4\u4e66\u8bb0\u4f59\u5e86\u6797\u5fc3\u91cc\u6e05\u695a\uff0c\u5c31\u7b97\u77e5\u9053\u7a81\u7136\u5192\u51fa\u8fd9\u4e48\u4e2a\u4eba\u800c\u4e14\u9648\u79c9\u5fb7\u8bed\u6c14\u91cc\u4e5f\u6709\u660e\u663e\u7684\u501f\u673a\u63a8\u8350\u7684\u610f\u601d\uff0c\u8fd8\u662f\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u9b4f\u526f\u9662\u957f\u6628\u5929\u591c\u91cc\u4e5f\u8f9b\u82e6\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9b4f\u51a0\u534e\u5fae\u5fae\u4e00\u6123\uff0c\u624d\u8bf4\u9053\uff0c\u201c\u6628\u5929\u4e0b\u5348\u8fde\u505a\u4e86\u4e24\u53f0\u624b\u672f\uff0c\u8fd9\u91cc\u5c31\u6ca1\u6709\u80fd\u5e2e\u4e0a\u5fd9\u3002\u201d</p><p>\u3000\u3000\u6628\u5929\u591c\u91cc\u53d7\u4f24\u7537\u5973\u7ed9\u9001\u6765\u6025\u6551\u65f6\uff0c\u7701\u653f\u5e9c\u79d8\u4e66\u957f\u9646\u6587\u592b\u4e5f\u4eb2\u81ea\u8d76\u8fc7\u6765\u6253\u62db\u547c\uff0c\u9b4f\u51a0\u534e\u867d\u662f\u5916\u8058\u7684\u526f\u9662\u957f\uff0c\u4ed6\u624d\u662f\u7701\u4eba\u6c11\u533b\u9662\u6700\u4f18\u79c0\u7684\u5fc3\u80f8\u3001\u5fc3\u8111\u5916\u79d1\u9886\u57df\u7684\u4e13\u5bb6\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u6628\u5929\u4e0b\u5348\u8fde\u7eed\u52a8\u4e86\u4e24\u53f0\u5927\u624b\u672f\uff0c\u76f4\u5230\u591c\u91cc\u4e5d\u70b9\u949f\u624d\u7ed3\u675f\u624b\u672f\u56de\u5230\u5bb6\u91cc\uff0c\u63a5\u5230\u7535\u8bdd\u65f6\u6574\u4e2a\u4eba\u7cbe\u75b2\u529b\u7aed\uff0c\u542c\u4f4f\u9662\u533b\u5e08\u6c47\u62a5\u6ca1\u6709\u7279\u522b\u7684\u5371\u9669\uff0c\u5373\u4f7f\u7701\u653f\u5e9c\u79d8\u4e66\u957f\u8d76\u8fc7\u6765\u6253\u62db\u547c\uff0c\u5373\u4f7f\u77e5\u9053\u4f24\u8005\u7684\u8eab\u4efd\u5f88\u91cd\u8981\uff0c\u4ed6\u4e5f\u6ca1\u6709\u8d76\u56de\u533b\u9662\u6765\u3002\u65e2\u7136\u6ca1\u6709\u751f\u547d\u5371\u9669\uff0c\u4ed6\u751a\u81f3\u6ca1\u6709\u518d\u901a\u77e5\u8c01\u523b\u610f\u4ece\u5bb6\u91cc\u8d76\u56de\u533b\u9662\uff0c\u5c31\u8ba9\u4f4f\u9662\u533b\u5e08\u8d1f\u8d23\u4e86\u62a2\u6551\uff0c\u5012\u662f\u9519\u8fc7\u4e86\u4e0e\u5f20\u606a\u63a5\u89e6\u7684\u673a\u4f1a\uff0c\u65e9\u6668\u4e4b\u540e\uff0c\u9662\u957f\u845b\u6c11\u4fca\u5c31\u4eb2\u81ea\u63a5\u624b\u4e86\u6cbb\u7597\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u8fd9\u6837\u554a\uff0c\u201d\u4f59\u5e86\u6797\u5012\u4e0d\u662f\u8981\u963b\u6b62\u9648\u79c9\u5fb7\u8bf7\u9b4f\u51a0\u534e\u5e26\u8def\uff0c\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e5f\u662f\u8ba9\u9648\u79c9\u5fb7\u77e5\u9053\u4ed6\u5fc3\u91cc\u900f\u4eae\uff0c\u201c\u8fd8\u8981\u9ebb\u70e6\u9b4f\u526f\u9662\u957f\u5e26\u8def\u5462\u3002\u201d</p><p>\u3000\u3000\u9648\u99a5\u6770\u4e0d\u542d\u58f0\uff0c\u4f55\u7eaa\u4e91\u5012\u662f\u89c1\u8bc6\u4e86\u8fd9\u5185\u4e2d\u85cf\u7740\u8fd9\u4e48\u591a\u7684\u5fc3\u601d\uff0c\u5fc3\u91cc\u60f3\u9648\u79c9\u5fb7\u4e0e\u9b4f\u51a0\u534e\u5173\u7cfb\u5e94\u8be5\u5f88\u8fd1\uff0c\u4ed6\u8eab\u5904\u516c\u5b89\u4f53\u7cfb\u5185\u90e8\uff0c\u81ea\u7136\u4ef0\u5e02\u5c40\u5c40\u957f\u9648\u79c9\u5fb7\u7684\u9f3b\u606f\uff0c\u4f46\u662f\u542c\u8bf4\u9b4f\u51a0\u534e\u6ca1\u6709\u53c2\u4e0e\u6628\u5929\u591c\u91cc\u7684\u62a2\u6551\uff0c\u770b\u4ed6\u8fd9\u6837\u5b50\u5927\u6982\u4e4b\u540e\u4e5f\u6ca1\u6709\u4e0e\u5f20\u606a\u6216\u5f20\u77e5\u884c\u63a5\u89e6\u8fc7\uff0c\u5c31\u4e0d\u60f3\u591a\u4e8b\u63fd\u4ec0\u4e48\u4e8b\u60c5\u5230\u81ea\u5df1\u5934\u4e0a\u6765\uff0c\u4e5f\u6ca1\u6709\u542d\u58f0\u3002</p><p>\u3000\u3000\u9b4f\u51a0\u534e\u4e00\u8fb9\u5e26\u7740\u9886\u7740\u4f59\u5e86\u6797\u3001\u9648\u79c9\u5fb7\u5f80\u91cc\u8d70\uff0c\u4e00\u8fb9\u62ff\u51fa\u624b\u673a\u6765\u6253\u7535\u8bdd\uff1a\u201c\u2026\u2026\u5c0f\u738b\uff0c\u6211\u7ea6\u89c1\u7684\u4e00\u4e2a\u75c5\u4eba\u5927\u6982\u5728\u534a\u4e2a\u5c0f\u65f6\u5185\u5230\u533b\u9662\uff0c\u4ed6\u4eec\u8981\u662f\u63d0\u524d\u8fc7\u6765\uff0c\u4f60\u5e2e\u6211\u63a5\u5f85\u4e00\u4e0b\u2026\u2026\u5bf9\uff0c\u5c31\u662f\u4e4b\u524d\u7ec4\u7ec7\u5927\u5bb6\u5206\u6790\u7684\u90a3\u4e2a\u7279\u6b8a\u75c5\u4f8b\u3002\u201d\u6302\u4e86\u7535\u8bdd\uff0c\u62b1\u6b49\u7684\u8ddf\u4f59\u5e86\u6797\u7b49\u4eba\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u5230\u4f4f\u9662\u90e8\u5927\u697c\u9876\u697c\uff0c\u7535\u68af\u95e8\u6253\u5f00\uff0c\u6f02\u4eae\u3001\u7ed9\u4eba\u5f88\u5e72\u51c0\u611f\u89c9\u7684\u536b\u5170\u62ff\u7740\u624b\u673a\u7ad9\u5728\u7535\u68af\u95e8\u5916\u7b49\u7535\u68af\uff0c\u770b\u5230\u9a6c\u6d77\u9f99\u8ddf\u9b4f\u51a0\u534e\u7b49\u4eba\u5728\u7535\u68af\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u9a6c\u53d4\u9886\u7740\u4eba\u8fc7\u6765\u4e86\uff1f\u201d\u4e0e\u4f59\u5e86\u6797\u7b49\u4eba\u6d45\u6d45\u7684\u4e00\u7b11\uff0c\u7b97\u662f\u62db\u547c\uff0c\u53c8\u8ddf\u9a6c\u6d77\u9f99\u8bf4\u9053\uff0c\u201c\u5f20\u606a\u57281218\u7b49\u4f60\u4eec\u5462\uff0c\u6211\u8981\u4e0b\u53bb\u63a5\u4e2a\u4eba\uff0c\u5c31\u4e0d\u966a\u4f60\u4eec\u8fc7\u53bb\u4e86\u2026\u2026\u201d\u8ba9\u4ed6\u4eec\u5148\u51fa\u4e86\u7535\u68af\u624d\u8fdb\u53bb\uff0c\u5979\u4e58\u7535\u68af\u4e0b\u4e86\u697c\uff0c\u53bb\u63a5\u5f20\u606a\u7684\u5c0f\u59e8\u3001\u5f20\u77e5\u975e\u7684\u59bb\u5b50\u3002</p><p>\u3000\u3000\u536b\u5170\u7ad9\u5728\u4f4f\u9662\u90e8\u5927\u697c\u524d\u7b49\u5f20\u606a\u7684\u5c0f\u5a76\uff0c\u770b\u7740\u6709\u4e00\u7537\u4e00\u5973\u4ece\u5927\u95e8\u53e3\u8d70\u4e86\u8fdb\u6765\uff0c\u9006\u7740\u6709\u4e9b\u8000\u773c\u7684\u5915\u9633\u5149\u8f89\uff0c\u770b\u4e0d\u6e05\u695a\u4e24\u4eba\u7684\u957f\u76f8\u3002\u90a3\u7537\u5b69\u5b50\u8d70\u4e86\u8fc7\u6765\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u5f20\u7eb8\u6761\u627e\u5979\u95ee\u8def\uff1a\u201c\u62a4\u58eb\u59d0\u59d0\uff0c\u4f60\u597d\uff0c\u8bf7\u95ee\u4e00\u4e0b\uff0c\u9b4f\u51a0\u534e\u9662\u957f\u7684\u529e\u516c\u5ba4\u600e\u4e48\u8d70\uff1f\u201d</p><p>\u3000\u3000\u536b\u5170\u89c9\u5f97\u773c\u524d\u8fd9\u5927\u7537\u5b69\u513f\u6709\u4e9b\u8138\u719f\uff0c\u53ea\u662f\u60f3\u4e0d\u8d77\u6765\u5728\u54ea\u91cc\u89c1\u8fc7\uff0c\u5973\u5b69\u5b50\u7ad9\u5728\u8fdc\u5904\uff0c\u5fae\u6602\u7740\u5934\u5728\u4e13\u6ce8\u7684\u770b\u7740\u8fdc\u5904\u7684\u5915\u9633\uff0c\u6574\u4e2a\u4eba\u7ed9\u5915\u9633\u5149\u6656\u7b3c\u7f69\u7740\uff0c\u5373\u4f7f\u770b\u4e0d\u6e05\u695a\u76f8\u8c8c\uff0c\u4e5f\u77e5\u9053\u662f\u4e2a\u5f88\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u4fbf\u662f\u8fd9\u6837\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u536b\u5170\u624b\u63d2\u7740\u62a4\u58eb\u670d\u7684\u8863\u515c\u91cc\uff0c\u671d\u7537\u5b69\u5b50\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u5df1\u7684\u8863\u670d\u6cfc\u810f\u4e86\uff0c\u8fd9\u5957\u8863\u670d\u662f\u501f\u8fc7\u6765\u7a7f\u7684\uff0c\u4f60\u4eec\u8981\u4e0d\u53bb\u5927\u5385\u91cc\u627e\u771f\u6b63\u7684\u62a4\u58eb\u59d0\u59d0\u95ee\u8def\uff1f\u201d</p><p>\u3000\u3000\u7537\u5b69\u5b50\u817c\u8146\u7684\u7b11\u4e86\u7b11\uff0c\u5c31\u8d70\u5f00\u4e0e\u90a3\u4e2a\u5973\u5b69\u5b50\u8d70\u8fdb\u5927\u697c\u91cc\uff0c\u8fd9\u4f1a\u513f\u5f20\u77e5\u975e\u7684\u59bb\u5b50\u4e0e\u5f20\u606a\u7684\u5976\u5976\u8d70\u4e86\u8fc7\u6765\uff0c\u536b\u5170\u8d76\u7d27\u8fce\u4e0a\u53bb\u5c06\u5979\u4eec\u63a5\u5230\u75c5\u623f\u53bb\u3002</p></article>"}}}]);
