(window.webpackJsonp=window.webpackJsonp||[]).push([[956],{979:function(p){p.exports={chapterTitle:null,title:"\u7b2c953\u7ae0 \u4e09\u661f\u7684\u6218\u7565\u8c03\u6574",content:"<article><h2>\u7b2c953\u7ae0 \u4e09\u661f\u7684\u6218\u7565\u8c03\u6574</h2><p>\u3000\u3000\u8981\u662f\u9075\u5faa\u5386\u53f2\u7684\u8f68\u8ff9\uff0c\u56fd\u5185\u7684CDMA\u7f51\u7edc\u5efa\u8bbe\u5c06\u56e0\u4e3a\u4e00\u7eb8\u519b\u961f\u7981\u5546\u4ee4\u800c\u505c\u987f\uff0c\u4e09\u661f\u5bf9\u4e2d\u56fd\u901a\u8baf\u5e02\u573a\u7684\u5f00\u53d1\u4e5f\u5c06\u968f\u4e4b\u9677\u5165\u56f0\u5883\uff0c\u4e0d\u4ec5\u4ec5\u4e09\u661f\u4e00\u5bb6\u516c\u53f8\uff0c\u97e9\u56fd\u53e6\u4e00\u5bb6\u7535\u5b50\u5de8\u5934LG\u7684\u5bf9\u4e2d\u56fd\u901a\u8baf\u5e02\u573a\u7684\u5f00\u53d1\u4e5f\u5c06\u56e0\u6b64\u9677\u5165\u56f0\u5883\u3002</p><p>\u3000\u3000\u90e8\u5206\u4e1a\u52a1\u5728\u533a\u57df\u5e02\u573a\u53d7\u632b\uff0c\u6bd5\u7adf\u4e0d\u80fd\u7b97\u4ec0\u4e48\u81f4\u547d\u4f24\u3002\u5373\u4f7f\u4e09\u661f\u5bf9\u4e2d\u56fd\u901a\u8baf\u5e02\u573a\u7684\u5f00\u53d1\u53d7\u632b\uff0c\u968f\u540e\u7684\u7b56\u7565\u8c03\u6574\u4e5f\u8c08\u4e0d\u4e0a\u975e\u5e38\u7684\u53ca\u65f6\uff0c\u4e00\u76f4\u62d6\u5ef6\u5230\u96f6\u4e00\u5e74\u8054\u901a\u91cd\u65b0\u67b6\u8bbe\u5168\u56fd\u6027\u7684CDMA\u5546\u7528\u7f51\uff0c\u4e09\u661f\u624d\u5728\u4e2d\u56fd\u7684\u624b\u673a\u5e02\u573a\u4f1a\u6709\u8d77\u8272\uff0c\u4f46\u662f\u4e09\u661f\u624b\u673a\u5728\u96f6\u4e00\u5e74\u4e4b\u540e\u8fd8\u662f\u5f3a\u52bf\u5d1b\u8d77\u4e86\uff0c\u5728\u5168\u7403\u5e02\u573a\u6210\u4e3a\u4ec5\u6b21\u4e8e\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u7684\u7b2c\u4e09\u5927\u624b\u673a\u751f\u4ea7\u5546\u3002\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u82e5\u662f\u80fd\u9075\u5faa\u5386\u53f2\u7684\u8f68\u8ff9\uff0c\u9526\u6e56\u81f3\u5c11\u5728\u96f6\u4e00\u5e74\u4e4b\u524d\u4e0d\u7528\u62c5\u5fe7\u56fd\u5185\u624b\u673a\u5e02\u573a\u6709\u6765\u81ea\u4e09\u661f\u7684\u5a01\u80c1\u3002</p><p>\u3000\u3000\u53ef\u60dc\u554a\uff0c\u9526\u6e56\u5bf9\u56fd\u5185\u624b\u673a\u5e02\u573a\u6765\u8bf4\uff0c\u662f\u4e00\u53ea\u975e\u5e38\u975e\u5e38\u5927\u7684\u8774\u8776\uff1b\u9526\u6e56\u7684\u5f3a\u52bf\u5d1b\u8d77\uff0c\u4f7f\u5f97\u4e09\u661f\u63d0\u524d\u653e\u5f03\u5bf9CDMA\u5b9e\u9a8c\u7f51\u5927\u89c4\u6a21\u5546\u7528\u5316\u7684\u5b8c\u5168\u4f9d\u8d56\uff0c\u5f00\u59cb\u52a0\u5927\u5bf9GSM\u6570\u5b57\u624b\u673a\u7684\u5f00\u53d1\u529b\u5ea6\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u91cc\u5f88\u6e05\u695a\uff0c\u9526\u6e56\u5728\u624b\u673a\u4e1a\u52a1\u4e0a\u7684\u4ea7\u54c1\u5f00\u53d1\u4e0e\u5e02\u573a\u5f00\u53d1\u7b56\u7565\u5176\u5b9e\u662f\u501f\u9274\u4e86\u4e09\u661f\u5728\u96f6\u4e00\u3001\u96f6\u4e8c\u5e74\u4e4b\u540e\u7684\u7ecf\u9a8c\uff0c\u4e09\u661f\u6b64\u65f6\u5c31\u8c03\u6574\u4e86\u4ed6\u4eec\u5728\u6570\u5b57\u624b\u673a\u4e0a\u7684\u7ecf\u8425\u7b56\u7565\uff0c\u9526\u6e56\u7684\u624b\u673a\u4e1a\u52a1\u5c31\u4e0d\u5f97\u4e0d\u8feb\u5207\u7684\u8003\u8651\u5373\u5c06\u6765\u81ea\u4e8e\u4e09\u661f\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u7a7f\u7740\u4e00\u4ef6\u886c\u886b\uff0c\u867d\u7136\u8bf4\u4fdd\u6696\u6027\u80fd\u6781\u597d\u7684\u6ee9\u7f8a\u6bdb\u886c\u886b\uff0c\u4f46\u662f\u5341\u4e00\u6708\u5e95\u3001\u5341\u4e8c\u6708\u5934\u4e0a\u7684\u591c\u665a\u7a7f\u4e00\u4ef6\u886c\u886b\u4e5f\u771f\u53eb\u4eba\u597d\u53d7\u3002\u62ac\u5934\u770b\u4e86\u770b\u7ed9\u591c\u98ce\u5439\u62c2\u52a8\u7684\u6811\u68a2\uff0c\u5f20\u606a\u7b11\u7740\u8ddf\u674e\u99a8\u4e88\u8bf4\u9053\uff1a\u201c\u771f\u662f\u6ca1\u6709\u611f\u89c9\uff0c\u51ac\u5929\u5c31\u6765\u4e86\uff0c\u771f\u662f\u53eb\u4eba\u611f\u6168\u4e07\u5206\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u53ef\u6bd4\u4f60\u7a7a\u95f2\u591a\u4e86\uff0c\u8fd8\u8bb0\u5f97\u7b2c\u4e00\u6b21\u770b\u5230\u68a7\u6850\u6811\u53f6\u98d8\u843d\u7684\u60c5\u666f\uff0c\u201d\u674e\u99a8\u4e88\u7b11\u7740\u8bf4\uff0c\u201c\u53ea\u662f\u6ca1\u6709\u60f3\u5230\u4f60\u8fd8\u4f1a\u4e3a\u5b63\u8282\u7684\u53d8\u6362\u611f\u6168\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5012\u4e0d\u662f\u6709\u95f2\u60c5\u5728\u8fd9\u91cc\u611f\u6625\u4f24\u79cb\uff0c\u800c\u662f\u60f3\u5230\u4e5d\u516b\u5e74\u5c31\u5269\u4e0b\u4e2a\u628a\u6708\u5c31\u8981\u8fc7\u53bb\u4e86\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u6700\u96be\u71ac\u7684\u65f6\u523b\u5373\u5c06\u7ffb\u8fc7\u53bb\u4e86\uff0c\u8fd9\u65f6\u5019\u624d\u89c9\u5f97\u5386\u65f6\u4e00\u5e74\u534a\u4e4b\u524d\u7684\u7ecf\u6d4e\u5371\u673a\u3001\u65f6\u95f4\u592a\u77ed\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u771f\u662f\u592a\u77ed\u4e86\u3002</p><p>\u3000\u3000\u867d\u7136\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4f7f\u5f97\u65e5\u97e9\u7535\u5b50\u5de8\u5934\u66f4\u52a0\u6e05\u9192\u7684\u8ba4\u8bc6\u5230\u4e2d\u56fd\u5e02\u573a\u7684\u91cd\u8981\u6027\uff0c\u4f46\u662f\u540c\u6837\u7684\u4e5f\u4f7f\u8fd9\u4e9b\u65e5\u97e9\u7535\u5b50\u5de8\u5934\u5728\u65b0\u5e02\u573a\u4e0a\u7684\u52a8\u4f5c\u53d8\u5f97\u8d8a\u53d1\u7684\u8c28\u614e\uff0c\u65e5\u76ca\u4e25\u5cfb\u7684\u8d22\u52a1\u72b6\u51b5\u4e5f\u4f7f\u8425\u9500\u9884\u7b97\u88ab\u66f4\u4e25\u683c\u7684\u63a7\u5236\u2014\u2014\u540c\u65f6\uff0c\u4e2d\u56fd\u5e02\u573a\u8425\u9500\u7ecf\u6d4e\u5728\u4e5d\u5341\u5e74\u4ee3\u4e2d\u540e\u671f\u7206\u53d1\u51fa\u8000\u773c\u7684\u5149\u8292\uff0c\u8425\u9500\u6210\u672c\u5927\u5e45\u6500\u5347\uff0c\u8fd9\u5c31\u4f7f\u5f97\u65e5\u97e9\u7535\u5b50\u5382\u5546\u539f\u5148\u7684\u8425\u9500\u9884\u7b97\u589e\u52a0\u62e8\u4ed8\u5374\u4e0d\u80fd\u53ca\u65f6\u8ddf\u4e0a\u6210\u672c\u7684\u589e\u6da8\u3002</p><p>\u3000\u3000\u6700\u4e3a\u5178\u4f8b\u7684\u6848\u4f8b\u5c31\u662f\u592e\u89c6\u6807\u738b\u5e7f\u544a\u4e5d\u4e94\u5e74\u6700\u521d\u767b\u4e0a\u5386\u53f2\u821e\u53f0\u65f6\u7ade\u4ef7\u624d\u4e09\u5343\u4e07\uff0c\u4e5d\u516d\u5e74\u7231\u8fbe\u5f00\u51fa\u516b\u5343\u4e07\u7684\u7ade\u4ef7\uff0c\u5960\u57fa\u6b64\u65f6\u9526\u6e56\u7684\u6839\u57fa\uff0c\u4e5d\u4e03\u5e74\u592e\u89c6\u6807\u738b\u7ed9\u79d1\u738b\u7092\u5230\u4e09\u4ebf\u4e94\u5343\u4e07\u7684\u5929\u4ef7\uff0c\u5f53\u5e74\u79d1\u738b\u5de8\u4e8f\u4f7f\u5f97\u6807\u738b\u73b0\u8c61\u6709\u6240\u9000\u70ed\u7ed9\u9999\u96ea\u6d77\u4ee5\u4e00\u4ebf\u516b\u5343\u4e07\u593a\u5f97\u4eca\u5e74\u7684\u6807\u738b\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u4f4f\u9662\u671f\u95f4\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u53ec\u5f00\u7b2c\u4e94\u5c4a\u5e7f\u544a\u62db\u5546\u5927\u4f1a\uff0c\u8fd9\u4e00\u6b21\u9999\u96ea\u6d77\u4e0e\u7231\u8fbe\u90fd\u5043\u65d7\u606f\u9f13\uff0c\u660e\u5e74\u7684\u592e\u89c6\u6807\u738b\u7ed9\u6b65\u6b65\u9ad8\u4ee5\u4e24\u4ebf\u516d\u5343\u4e07\u7684\u9ad8\u4ef7\u7ade\u8d70\u3002</p><p>\u3000\u3000\u867d\u7136\u7231\u8fbe\u6ca1\u6709\u53bb\u4e89\u4ec0\u4e48\u6807\u738b\uff0c\u4f46\u662f\u6574\u6574\u4e5d\u516b\u5e74\u7231\u8fbe\u4e3a\u65d7\u4e0b\u4ea7\u54c1\u5728\u56fd\u5185\u5e02\u573a\u7684\u8425\u9500\u603b\u82b1\u8d39\u63a5\u8fd1\u4e94\u4e2a\u4ebf\u3002\u4e0e\u6b64\u540c\u671f\uff0c\u4e09\u661f\u4eca\u5e74\u5728\u4e2d\u56fd\u5e02\u573a\u7684\u8425\u9500\u82b1\u8d39\u624d\u4e94\u5343\u4e07\u7f8e\u5143\u2014\u2014\u8fd9\u4e2a\u6570\u636e\u7edd\u5bf9\u662f\u4e09\u661f\u7684\u5546\u4e1a\u673a\u5bc6\uff0c\u4f46\u662f\u8fc7\u4e86\u660e\u5e74\u5c31\u4e0d\u518d\u662f\u4ec0\u4e48\u5546\u4e1a\u673a\u5bc6\uff0c\u6240\u4ee5\u5f20\u606a\u77e5\u9053\u8fd9\u4e2a\u6570\u636e\u4e5f\u4e0d\u662f\u4ec0\u4e48\u597d\u5947\u602a\u7684\u4e8b\u60c5\u2014\u2014\u8fd9\u8fd8\u662f\u4e09\u661f\u4e0b\u5b9a\u51b3\u5fc3\u5927\u529b\u5f00\u62d3\u4e2d\u56fd\u5e02\u573a\u540e\u7684\u8425\u9500\u6295\u5165\u3002</p><p>\u3000\u3000\u4e09\u661f\u5728\u4e2d\u56fd\u5e02\u573a\u6295\u653e\u7684\u4ea7\u54c1\u79cd\u7c7b\u6bd4\u7231\u8fbe\u8981\u591a\u5f97\u591a\uff0c\u8425\u9500\u6295\u5165\u5374\u5dee\u7231\u8fbe\u4e00\u622a\uff0c\u8fd9\u5f53\u4e2d\u8fd8\u5b58\u5728\u4e00\u4e2a\u672c\u571f\u5316\u9002\u5e94\u7684\u95ee\u9898\uff0c\u4e09\u661f\u4e5d\u516b\u5e74\u5728\u4e2d\u56fd\u5e02\u573a\u7684\u8868\u73b0\u8fdc\u4e0d\u5982\u7231\u8fbe\u6770\u51fa\u4e5f\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u8fd9\u662f\u4e5d\u516b\u5e74\u7684\u60c5\u51b5\u2014\u2014\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5373\u5c06\u70df\u6d88\u4e91\u6563\uff0c\u8d1f\u8d23\u4e00\u767e\u4e03\u5341\u4ebf\u7f8e\u5143\u7684\u4e09\u661f\u4e5f\u5373\u5c06\u71ac\u8fc7\u51e0\u5341\u5e74\u6765\u6700\u4e25\u91cd\u7684\u5371\u673a\uff0c\u4ece\u6ce5\u7ef0\u822c\u7684\u56f0\u5883\u4e2d\u8d70\u51fa\u6765\uff0c\u90a3\u4e09\u661f\u660e\u5e74\u5bf9\u4e2d\u56fd\u5e02\u573a\u7684\u8425\u9500\u6295\u5165\u4f1a\u8fbe\u5230\u591a\u5c11\uff1f\u5bf9\u5168\u7403\u5e02\u573a\u7684\u8425\u9500\u603b\u6295\u5165\u4f1a\u8fbe\u5230\u591a\u5c11\uff1f</p><p>\u3000\u3000\u9664\u4e86\u4e09\u661f\uff0c\u97e9\u56fdLG\u4ee5\u53ca\u65e5\u7cfb\u7535\u5b50\u5382\u5546\u5728\u6e21\u8fc7\u6700\u8270\u96be\u7684\u5371\u673a\u4e4b\u540e\uff0c\u5bf9\u6b23\u6b23\u5411\u8363\u7684\u4e2d\u56fd\u5e02\u573a\u7684\u8425\u9500\u6295\u5165\u4f1a\u589e\u52a0\u591a\u5c11\uff1f</p><p>\u3000\u3000\u5bf9\u4e8e\u90a3\u4e9b\u7ecf\u8425\u98ce\u683c\u4fdd\u5b88\u4e14\u4f20\u7edf\u7684\u7535\u5b50\u5382\u5546\uff0c\u5f20\u606a\u53ef\u4ee5\u4f9d\u8d56\u524d\u5c18\u5f80\u4e16\u91cc\u7684\u7ecf\u9a8c\u53bb\u63a8\u6d4b\uff0c\u5bf9\u4e09\u661f\u3001\u4e1c\u829d\u8fd9\u6837\u6709\u7740\u521b\u65b0\u4e0e\u6539\u53d8\u52c7\u6c14\u3001\u9002\u5e94\u6027\u66f4\u5f3a\u7684\u4f01\u4e1a\uff0c\u5f20\u606a\u53ea\u80fd\u611f\u6168\u9526\u6e56\u5df2\u7ecf\u662f\u4e00\u53ea\u5927\u8774\u8776\u4e86\uff0c\u524d\u5c18\u5f80\u4e16\u91cc\u7684\u7ecf\u9a8c\u5df2\u7ecf\u4e0d\u8db3\u4ee5\u501f\u9274\u3002</p><p>\u3000\u3000\u771f\u662f\u5934\u75bc\u554a\uff0c\u7ecf\u6d4e\u5371\u673a\u600e\u4e48\u7728\u773c\u95f4\u5c31\u8fc7\u53bb\u4e86\uff1f\u5f20\u606a\u5df2\u7ecf\u9884\u611f\u5230\u660e\u5e74\u7684\u7ade\u4e89\u4f1a\u66f4\u52a0\u7684\u6fc0\u70c8\uff0c\u9526\u6e56\u4f3c\u4e4e\u4e5f\u8981\u66f4\u79ef\u6781\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u966a\u674e\u99a8\u4e88\u5230\u56fe\u4e66\u9986\u5bc4\u5b58\u5904\u62ff\u56de\u5916\u5957\uff0c\u8eab\u4f53\u65e9\u56e0\u8dd1\u6b65\u800c\u6696\u548c\u8d77\u6765\uff0c\u674e\u99a8\u4e88\u53c8\u5c06\u6e56\u5170\u8272\u7684\u5916\u5957\u7a7f\u5728\u8eab\u4e0a\uff0c\u7ad9\u5728\u56fe\u4e66\u9986\u5927\u5385\u524d\u7684\u8d70\u5eca\u4e0a\u770b\u7740\u524d\u9762\u7684\u5e7f\u573a\uff0c\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\u7f18\u6545\uff0c\u5e7f\u573a\u4e0a\u7684\u55b7\u6c34\u6c60\u8fd9\u65f6\u95f4\u55b7\u8d77\u51b2\u5929\u7684\u6c34\u67f1\u6765\uff0c\u6d01\u767d\u6676\u83b9\u7684\u6c34\u67f1\u7ec7\u6210\u4e00\u7247\u5e55\u5899\u3002</p><p>\u3000\u3000\u201c\u4f60\u662f\u600e\u4e48\u770b\u5f85\u4e09\u661f\uff1f\u201d\u674e\u99a8\u4e88\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6211\u554a\uff0c\u201d\u5f20\u606a\u770b\u4e86\u674e\u99a8\u4e88\u4e00\u773c\uff0c\u6e05\u7eaf\u59a9\u5a9a\u7684\u8138\u4e0a\uff0c\u773c\u775b\u5c31\u50cf\u5934\u9876\u7684\u591c\u7a7a\u4e00\u822c\u5e7d\u9759\uff0c\u8bf4\u9053\uff0c\u201c\u4e09\u661f\u662f\u6211\u4eec\u5b66\u4e60\u7684\u5bf9\u8c61\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u5b66\u4e60\u4e09\u661f\uff0c\u5728\u4e2d\u56fd\u5e02\u573a\uff0c\u4f60\u4eec\u4e0d\u662f\u66f4\u51fa\u8272\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u4f60\u60f3\u8c61\u7684\u90a3\u4e48\u4e0d\u77e5\u8f7b\u91cd\u2014\u2014\u4e09\u5e74\u8d76\u82f1\u3001\u4e94\u5e74\u8d85\u7f8e\uff0c\u90a3\u662f\u4e2d\u56fd\u5927\u8dc3\u8fdb\u65f6\u671f\u7684\u53e3\u53f7\uff0c\u6b64\u65f6\u7684\u4e2d\u56fd\u4eba\u90fd\u8fd8\u633a\u52a1\u5b9e\u7684\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u4e0e\u674e\u99a8\u4e88\u8bf4\u9053\uff0c\u201c\u9996\u5148\uff0c\u9ad8\u7aef\u4ea7\u54c1\u5e02\u573a\u80cc\u540e\u5fc5\u987b\u4f9d\u8d56\u4e8e\u5c16\u7aef\u79d1\u6280\uff0c\u8fd9\u65f6\u6211\u4eec\u8fdc\u4e0d\u5982\u4e09\u661f\u7684\u5730\u65b9\uff0c\u5f53\u7136\u4e86\uff0c\u6bd4\u65e5\u7cfb\u4f01\u4e1a\u7684\u8ddd\u5dee\u66f4\u5927\uff0c\u800c\u4e14\u6211\u4eec\u5728\u6280\u672f\u4e0a\u7684\u6295\u5165\u6b64\u65f6\u8fd8\u8fdc\u8fdc\u4e0d\u80fd\u8ddf\u4e09\u661f\u6bd4\uff1b\u53e6\u5916\uff0c\u4e09\u661f\u5bf9\u5e02\u573a\u7684\u9002\u5e94\u6027\u5f88\u5f3a\uff0c\u6211\u4eec\u4e0d\u6562\u8bf4\u6bd4\u4e09\u661f\u505a\u5f97\u66f4\u597d\uff0c\u4f46\u662f\u80fd\u591f\u80af\u5b9a\u65e5\u7cfb\u4f01\u4e1a\u5728\u8fd9\u4e00\u70b9\u8981\u6bd4\u4e09\u661f\u5dee\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4f60\u4e5f\u8ba4\u4e3a\u4e09\u661f\u6709\u8d85\u8d8a\u7d22\u5c3c\u7684\u53ef\u80fd\u554a\u2026\u2026\u201d\u674e\u99a8\u4e88\u8f7b\u53f9\u4e86\u4e00\u53e5\uff0c\u5979\u6ca1\u6709\u610f\u8bc6\u5230\u5979\u7684\u53ea\u8a00\u7247\u8bed\u4f1a\u7ed9\u5f20\u606a\u5e26\u53bb\u600e\u6837\u7684\u4fe1\u606f\u91cf\uff0c\u5f53\u7136\uff0c\u5979\u5bf9\u5bb6\u65cf\u5c31\u6ca1\u6709\u592a\u591a\u7684\u8ba4\u540c\u611f\uff0c\u5bf9\u5f20\u606a\u4eb2\u8fd1\uff0c\u4e5f\u6ca1\u6709\u592a\u591a\u7684\u8b66\u60d5\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u6f5c\u610f\u8bc6\u7684\u671f\u671b\u5f20\u606a\u80fd\u9886\u5bfc\u9526\u6e56\u7ed9\u4e09\u661f\u4e00\u4e2a\u72e0\u72e0\u7684\u3001\u95ea\u4eae\u7684\u6559\u8bad\uff0c\u8ba9\u5bb6\u65cf\u7684\u7537\u4eba\u6c14\u7130\u7a0d\u6536\u655b\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bfb\u8fc7\u597d\u51e0\u904d\u6709\u5173\u674e\u5065\u7199\u7684\u4e00\u672c\u4f20\u8bb0\uff0c\u90a3\u4e5f\u7ecf\u674e\u5065\u7199\u672c\u4eba\u552f\u4e00\u786e\u8ba4\u8fc7\u7684\u4f20\u8bb0\uff1a\u674e\u5065\u7199\u5728\u4e09\u661f\u5185\u90e8\u6b63\u5f0f\u63d0\u51fa\u4ee5\u8d85\u8d8a\u7d22\u5c3c\u4e3a\u76ee\u6807\u7684\u6570\u7801\u878d\u5408\u6218\u7565\u662f\u5728\u7ecf\u6d4e\u5371\u673a\u7f13\u548c\u540e\u7684\u4e5d\u4e5d\u5e74\u6625\u5929\uff0c\u6570\u7801\u878d\u5408\u6218\u7565\u662f\u5c06\u4e09\u661f\u7535\u5b50\u5f15\u5bfc\u8d70\u5411\u201c\u6570\u5b57\u878d\u5408\u9769\u547d\u9886\u5bfc\u8005\u4e4b\u4e00\u201d\u7684\u6218\u7565\u8ba1\u5212\uff0c\u8fd9\u4e2a\u6218\u7565\u8ba1\u5212\u7684\u76ee\u6807\u5c31\u662f\u8981\u5c06\u4e09\u661f\u7535\u5b50\u65d7\u4e0b\u7684\u6d88\u8d39\u7535\u5b50\u3001\u4fe1\u606f\u901a\u8baf\u4ea7\u54c1\u3001\u7535\u89c6\u673a\u3001\u4e2a\u4eba\u7535\u8111\u4ee5\u53ca\u5728\u7ebf\u4e0e\u79bb\u7ebf\u7684\u4e16\u754c\u878d\u5408\u5728\u4e00\u8d77\uff0c\u63a8\u52a8\u6570\u5b57\u96c6\u6210\u7684\u9769\u547d\u3002\u4e3a\u4e86\u914d\u5408\u8fd9\u4e2a\u6218\u7565\u8ba1\u5212\uff0c\u4e09\u661f\u8fd8\u542f\u52a8\u4e86\u201c\u91d1\u8425\u9500\u201d\u65b9\u6848\u3002\u4e5d\u4e5d\u5e74\u4e4b\u524d\uff0c\u4e09\u661f\u54c1\u724c\u5728\u5e02\u573a\u4e0a\u7684\u77e5\u540d\u5ea6\u4e0e\u53f7\u53ec\u529b\u8fd8\u8981\u8fdc\u8fdc\u5f31\u4e8e\u65e5\u7cfb\u7535\u5b50\u5382\u5546\uff0c\u4e3a\u4e86\u6253\u9020\u6210\u4e0e\u65e5\u672c\u7d22\u5c3c\u76f8\u5ab2\u7f8e\u7684\u4e16\u754c\u540d\u724c\uff0c\u4e09\u661f\u7535\u5b50\u5c06\u4e3a\u4e4b\u540e\u4e94\u5e74\u65b0\u589e\u52a0\u4e86\u5341\u4ebf\u7f8e\u5143\u7684\u8425\u9500\u9884\u7b97\u3002</p><p>\u3000\u3000\u5982\u6b64\u770b\u6765\uff0c\u5373\u4f7f\u4e09\u661f\u7684\u6574\u4e2a\u6570\u7801\u878d\u5408\u6218\u7565\u8ba1\u5212\u6ca1\u6709\u5168\u9762\u63d0\u524d\u5b9e\u65bd\uff0c\u4e5f\u81f3\u5c11\u6709\u90e8\u5206\u6218\u7565\u8ba1\u5212\u63d0\u524d\u5b9e\u65bd\u4e86\uff0c\u800c\u4e14\u66f4\u4fa7\u91cd\u4e2d\u56fd\u5e02\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c9\u5f97\u5728\u8fd9\u4e48\u51b7\u7684\u591c\u665a\u80a9\u8180\u90fd\u60c5\u4e0d\u81ea\u7981\u7684\u6536\u7d27\u4e86\u4e9b\u8bb8\uff0c\u4e09\u661f\u7684\u5e94\u53d8\u6bd4\u66fe\u7ecf\u53d1\u751f\u7684\u5386\u53f2\u63d0\u524d\u4e86\u3002</p><p>\u3000\u3000\u4e0e\u674e\u99a8\u4e88\u56de\u5230\u516c\u5bd3\uff0c\u5f20\u606a\u770b\u89c1\u536b\u5170\u679c\u7136\u5c06\u79c1\u4eba\u7269\u54c1\u90fd\u642c\u5230\u697c\u4e0b\u4e39\u9752\u623f\u91cc\u53bb\u4e86\uff0c\u4ed6\u53ea\u80fd\u62a5\u4e86\u82e6\u7b11\uff0c\u4ed6\u5230\u7fdf\u4e39\u9752\u623f\u91cc\u62ff\u5916\u5957\uff0c\u5b59\u9759\u6aac\u4e5f\u5728\u90a3\u91cc\uff0c\u4ed6\u5bf9\u7fdf\u4e39\u9752\u8bf4\u9053\uff1a\u201c\u4f60\u8ba1\u5212\u4e00\u4e0b\uff0c\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u6211\u60f3\u63d0\u524d\u5230\u5341\u4e8c\u6708\u4e0a\u65ec\u53ec\u5f00\uff0c\u8fd9\u6837\u80fd\u8ba9\u4e0b\u9762\u6210\u5458\u4f01\u4e1a\u5bf9\u660e\u5e74\u7684\u8ba1\u5212\u80fd\u6709\u65f6\u95f4\u8c03\u6574\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u8bf8\u591a\u6210\u5458\u4f01\u4e1a\u4ee5\u4ea4\u53c9\u6301\u80a1\u7684\u5f62\u5f0f\u5f62\u6210\u9526\u6e56\u4f53\u7cfb\u7684\u6574\u4f53\uff0c\u4f20\u7edf\u7684\u80a1\u4efd\u5236\u516c\u53f8\u8463\u4e8b\u4f1a\u96c6\u4f53\u51b3\u7b56\u673a\u5236\u4e0d\u518d\u9002\u5408\u8fd9\u4e2a\u590d\u6742\u7684\u4f53\u7cfb\uff0c\u5f20\u606a\u51b3\u5b9a\u5728\u539f\u7231\u8fbe\u96c6\u56e2\u5927\u9879\u76ee\u90e8\u7684\u57fa\u7840\u4e0a\u53ec\u96c6\u9ad8\u7ea7\u7ecf\u7406\u53c2\u52a0\u7684\u4f1a\u8bae\u6765\u5171\u540c\u78cb\u5546\u3001\u51b3\u7b56\u6574\u4e2a\u4f53\u7cfb\u7684\u672a\u6765\u53d1\u5c55\u65b9\u5411\u2014\u2014\u5728\u9526\u6e56\u4f53\u7cfb\u91cc\uff0c\u9ad8\u7ea7\u7ecf\u7406\u5bf9\u65e5\u5e38\u4e8b\u52a1\u7684\u7ba1\u7406\u6743\u9650\u4e0e\u51b3\u7b56\u5730\u4f4d\u4e5f\u5c06\u66f4\u9ad8\u3002</p><p>\u3000\u3000\u4e00\u65e6\u8fd9\u4e2a\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u5f62\u6210\u60ef\u4f8b\u3001\u5f62\u6210\u884c\u4e4b\u6709\u6548\u7684\u673a\u5236\uff0c\u9526\u6e56\u4e5f\u5c06\u521d\u6b65\u5177\u5907\u4f5c\u4e3a\u4e00\u4e2a\u8d22\u56e2\u7684\u57fa\u7840\u4e86\uff0c\u4e5f\u5c06\u4e0d\u4f1a\u56e0\u4e3a\u4e2a\u4f53\u6b7b\u4ea1\u7684\u5076\u7136\u6027\u56e0\u7d20\u800c\u9677\u5165\u6fd2\u4e34\u5d29\u6e83\u7684\u56f0\u5883\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u7684\u610f\u5916\uff0c\u5c24\u5176\u8ba9\u5f20\u606a\u89c9\u5f97\u6709\u5c3d\u5feb\u63a8\u52a8\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u53ec\u5f00\u4e0e\u5f62\u6210\u56fa\u5b9a\u673a\u5236\u7684\u5fc5\u8981\u6027\uff0c\u8c01\u6709\u8fc7\u5f20\u606a\u8fd9\u6837\u7684\u7ecf\u5386\uff0c\u5bf9\u751f\u6b7b\u4e4b\u4e8b\u5012\u662f\u80fd\u770b\u6de1\u4e9b\uff0c\u4f46\u662f\u9526\u6e56\u7684\u672a\u6765\u4e0d\u80fd\u4e0d\u91cd\u89c6\u3002</p><p>\u3000\u3000\u53ec\u5f00\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u4e0d\u662f\u7b80\u5355\u7684\u5c06\u6709\u8d44\u683c\u53c2\u52a0\u4f1a\u8bae\u7684\u9ad8\u7ea7\u7ecf\u7406\u4eec\u53ec\u96c6\u9f50\u5c31\u53ef\u4ee5\u4e86\uff0c\u7b2c\u4e00\u6b21\u53ef\u4ee5\u7c97\u964b\u4e00\u4e9b\uff0c\u4f46\u662f\u518d\u7c97\u964b\uff0c\u53ec\u5f00\u4f1a\u8bae\u7684\u8bae\u9898\u4ee5\u53ca\u8bae\u9898\u76f8\u5173\u8d44\u6599\u90fd\u8981\u63d0\u524d\u51c6\u5907\u597d\u4ea4\u5230\u9ad8\u7ea7\u7ecf\u7406\u4eec\u7684\u624b\u91cc\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8fd8\u6709\u4e00\u4e2a\u91cd\u8981\u7684\u5de5\u4f5c\u5c31\u662f\u786e\u5b9a\u8c01\u6709\u8d44\u683c\u53c2\u52a0\u8fd9\u4e2a\u4f1a\u8bae\uff0c\u53c2\u52a0\u8fd9\u4e2a\u4f1a\u8bae\u7684\u9ad8\u7ea7\u7ecf\u7406\u4eec\u5c06\u662f\u9526\u6e56\u4f53\u7cfb\u7684\u6838\u5fc3\u7ba1\u7406\u5c42\uff0c\u5c06\u5171\u540c\u6267\u638c\u8fd9\u4e2a\u7ecf\u6d4e\u5e1d\u56fd\u3002</p></article>"}}}]);
