(window.webpackJsonp=window.webpackJsonp||[]).push([[957],{980:function(p){p.exports={chapterTitle:null,title:"\u7b2c954\u7ae0 \u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae",content:"<article><h2>\u7b2c954\u7ae0 \u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae</h2><p>\u3000\u3000\u5f20\u4e9a\u5e73\u4e5f\u662f\u6709\u6bb5\u65f6\u95f4\u6ca1\u6709\u5230\u5efa\u90ba\u6765\u4e86\uff0c\u98de\u5229\u6d66\u7684\u4e1a\u52a1\u91cd\u70b9\u4e0d\u5728\u5efa\u90ba\uff0c\u518d\u6b21\u5230\u5efa\u90ba\u6765\uff0c\u4ed6\u4e5f\u4e0d\u518d\u662f\u4ee5\u98de\u5229\u6d66\u5143\u5668\u4ef6\u90e8\u5927\u4e2d\u534e\u533a\u603b\u88c1\u7684\u8eab\u4efd\uff0c\u65b0\u7684\u8eab\u4efd\u662f\u7231\u8fbe\u96c6\u56e2\u9ad8\u7ea7\u526f\u603b\u88c1\u517c\u5143\u5668\u4ef6\u90e8\u603b\u88c1\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u5230\u5efa\u90ba\u6765\uff0c\u624d\u53d1\u73b0\u5efa\u90ba\u5728\u4e0d\u957f\u7684\u65f6\u95f4\u91cc\u53d1\u751f\u7684\u53d8\u5316\u662f\u5982\u6b64\u4e4b\u5927\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u4e0d\u6562\u60f3\u8c61\uff0c\u201d\u5f20\u4e9a\u5e73\u7ad9\u5728\u71d5\u5f52\u5c71\u5317\u5761\u4e0a\u7684\u9675\u56ed\u5e7f\u573a\u4e0a\u773a\u671b\u6a61\u6811\u56ed\u3001\u5b66\u5e9c\u5df7\u65b9\u5411\uff0c\u201c\u524d\u5e74\u51ac\u5929\u5230\u5efa\u90ba\u51fa\u5dee\u65f6\uff0c\u6211\u5750\u8f66\u7ecf\u8fc7\u8fd9\u4e00\u7247\uff0c\u9664\u4e86\u5e08\u5927\u4e0e\u4e1c\u5927\u7684\u6821\u820d\u4e4b\u5916\uff0c\u71d5\u5f52\u6e56\u5317\u5cb8\u5b8c\u5b8c\u5168\u5168\u4e0e\u666e\u901a\u7684\u51cc\u4e71\u5c0f\u57ce\u9547\u6ca1\u6709\u4ec0\u4e48\u533a\u522b\uff0c\u90a3\u65f6\u5019\u4e1c\u534e\u5927\u9053\u4e5f\u521a\u521a\u5efa\u6210\uff0c\u6cbf\u8857\u90fd\u662f\u51cc\u4e71\u7684\u623f\u5c4b\uff0c\u77ed\u77ed\u4e00\u5e74\u591a\u65f6\u95f4\uff0c\u8c01\u80fd\u60f3\u5230\u4f1a\u6709\u5982\u6b64\u7470\u7f8e\u7684\u666f\u8c61\uff0c\u6211\u770b\u4e0d\u4e45\u7684\u5c06\u6765\uff0c\u6211\u4eec\u7ad9\u7684\u5730\u65b9\u5c06\u6210\u4e3a\u5efa\u90ba\u4e00\u5904\u8457\u540d\u7684\u89c2\u666f\u53f0\u4e5f\u8bf4\u4e0d\u5b9a\u2026\u2026\u201d</p><p>\u3000\u3000\u71d5\u5f52\u6e56\u5317\u5cb8\u4e0e\u5e08\u5927\u3001\u4e1c\u5927\u4e4b\u95f4\u7684\u8fd1\u5343\u4ea9\u571f\u5730\u90fd\u5df2\u7ecf\u5f00\u53d1\u5b8c\u6bd5\uff0c\u5e08\u5927\u4e0e\u4e1c\u5927\u4e4b\u95f4\u7684\u5b66\u5e9c\u5df7\u4e5f\u5b8c\u5168\u63a8\u5012\u91cd\u5efa\uff0c\u5e74\u521d\u542f\u52a8\u6e56\u4e1c\u5546\u5708\u5efa\u8bbe\uff0c\u6b64\u65f6\u71d5\u5f52\u6e56\u4e1c\u5317\u9685\u4e24\u767e\u4ea9\u5730\u7684\u71d5\u6e56\u522b\u82d1\u522b\u5885\u533a\u4e5f\u5b8c\u5168\u5efa\u6210\uff0c\u8fd9\u5927\u7247\u7684\u5efa\u7b51\u9664\u4e86\u9ad8\u6863\u516c\u5bd3\u3001\u5546\u4e1a\u8857\u4e4b\u5916\uff0c\u4e3b\u8981\u8fd8\u662f\u4ee5\u6a61\u6811\u56ed\u4e3a\u4e3b\u9898\u7684\u5b9e\u9a8c\u56ed\u4e0e\u521b\u4e1a\u56ed\uff0c\u5927\u9762\u7684\u6e56\u6ee8\u4f11\u95f2\u7eff\u5730\u4ee5\u53ca\u4f4f\u5b85\u533a\u91cc\u7684\u690d\u88ab\u90fd\u4ee5\u6a61\u6811\u4e3a\u4e3b\uff0c\u7ad9\u5728\u71d5\u5f52\u5c71\u5761\u4e0a\u5f80\u8fd9\u7247\u773a\u671b\uff0c\u6b63\u503c\u521d\u51ac\u6a61\u6811\u53f6\u7247\u8272\u5f69\u53d8\u5e7b\u4e4b\u9645\uff0c\u6574\u6574\u4e00\u5927\u7247\u7684\u571f\u5730\u6cdb\u51fa\u91d1\u7ea2\u3001\u91d1\u9ec4\u3001\u6ce5\u9ec4\u3001\u6d45\u8910\u7b49\u4e30\u5bcc\u7684\u8272\u5f69\uff0c\u7f8e\u8f6e\u7f8e\u5942\uff0c\u4eff\u4f5b\u7ed9\u6e56\u5317\u6ee8\u88f9\u4e0a\u4e00\u6761\u4e94\u5f69\u7f24\u7eb7\u7684\u62ab\u5dfe\uff0c\u71d5\u5f52\u6e56\u7684\u6e56\u6c34\u6f84\u6f88\u5982\u4e00\u6574\u5757\u5de8\u5927\u7684\u78a7\u7389\u5d4c\u5728\u5c71\u811a\u4e0b\uff0c\u518d\u4e0e\u7eff\u6811\u8471\u90c1\u7684\u9ad8\u6821\u6821\u56ed\u8fde\u7ef5\u6210\u4e00\u7247\uff0c\u7edd\u6ca1\u6709\u7279\u522b\u5f3a\u70c8\u7684\u73b0\u4ee3\u90fd\u5e02\u4e4b\u611f\uff0c\u6574\u4f53\u5c31\u50cf\u4e00\u5ea7\u8d85\u5927\u578b\u7684\u6e56\u6ee8\u6a61\u6811\u516c\u56ed\u3002</p><p>\u3000\u3000\u5728\u6a61\u6811\u56ed\u533a\u57df\u4ee5\u4e1c\u3001\u6e56\u4e1c\u5546\u5708\u7684\u4e3b\u5efa\u8bbe\u5730\uff0c\u9ad8\u697c\u5927\u53a6\u62e8\u5730\u800c\u8d77\uff0c\u867d\u7136\u5927\u90e8\u5206\u5efa\u7b51\u90fd\u5728\u7d27\u5f20\u7684\u65bd\u5de5\u5f53\u4e2d\uff0c\u4f46\u4e5f\u80fd\u611f\u53d7\u5230\u5f3a\u70c8\u73b0\u4ee3\u90fd\u5e02\u6c14\u606f\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u82cf\u6d25\u4e1c\u7b11\u4e86\u7b11\uff0c\u4ed6\u8fd9\u4e9b\u5929\u5fd9\u788c\u5f97\u5f88\uff0c\u542c\u8bf4\u4ece\u8fd9\u91cc\u770b\u4e0a\u53bb\u98ce\u666f\u7edd\u7f8e\uff0c\u518d\u6ca1\u6709\u65f6\u95f4\u4e0a\u6765\u770b\u4e00\u773c\uff0c\u8bf4\u9053\uff0c\u201c\u6709\u95f2\u7a7a\u592b\u7684\u4eba\uff0c\u4ece\u4e5d\u6708\u4e0b\u65ec\u5c31\u5f00\u59cb\u767b\u5c71\u4e86\uff0c\u8fd9\u6a61\u6811\u56ed\u679c\u7136\u662f\u540d\u526f\u5176\u5b9e\u2026\u2026\u4e0d\u8981\u8bf4\u4ece\u5c71\u4e0a\u770b\u4e0b\u53bb\uff0c\u5750\u8f66\u7ecf\u8fc7\u9ad8\u5927\u7684\u6a61\u6811\u4e1b\u6797\uff0c\u611f\u89c9\u4e5f\u76f8\u5f53\u7684\u8212\u9002\uff0c\u4e0d\u8fc7\u5728\u606a\u5c11\u770b\u6765\uff0c\u8fd8\u662f\u89c4\u6a21\u8fd8\u662f\u592a\u5c0f\u4e86\u4e9b\uff0c\u6211\u770b\u4ed6\u662f\u60e6\u8bb0\u7740\u7f8e\u56fd\u897f\u90e8\u7684\u90a3\u4e9b\u52a8\u8f84\u6570\u4e07\u4ea9\u3001\u5341\u6570\u4e07\u4ea9\u7684\u5927\u6a61\u6811\u56ed\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u82cf\u6d25\u4e1c\u8fd9\u4e48\u8bf4\u5f20\u606a\u7684\u5fd7\u5411\uff0c\u5f20\u4e9a\u5e73\u4e5f\u662f\u6d45\u6d45\u7684\u4e00\u7b11\uff0c\u4e2d\u56fd\u5730\u867d\u5e7f\uff0c\u4eba\u66f4\u7a20\uff0c\u5728\u4eba\u53e3\u7a20\u5bc6\u7684\u5efa\u90ba\u5e02\uff0c\u80fd\u6709\u8fd9\u4e48\u4e00\u7247\u51c0\u571f\uff0c\u5df2\u7ecf\u8ba9\u80fd\u8ba9\u4eba\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002</p><p>\u3000\u3000\u60f3\u8d77\u524d\u5e74\u5750\u8f66\u7ecf\u8fc7\u71d5\u5f52\u6e56\u7554\u8fd8\u80fd\u95fb\u5f97\u6e56\u6c34\u7684\u6076\u81ed\uff0c\u624d\u4e24\u5e74\u65f6\u95f4\uff0c\u71d5\u5f52\u6e56\u6c34\u6e05\u6f88\u82e5\u5d4c\u5728\u5927\u5730\u4e0a\u7684\u4e00\u6574\u5757\u5de8\u5927\u78a7\u7389\uff0c\u8fd9\u4e9b\u90fd\u662f\u9526\u6e56\u5927\u4e3e\u633a\u8fdb\u5efa\u90ba\u7684\u8fd9\u4e00\u5e74\u591a\u65f6\u95f4\u91cc\u7ed9\u8fd9\u5ea7\u57ce\u5e02\u5e26\u6765\u7684\u65b0\u666f\u8c61\uff0c\u5f20\u4e9a\u5e73\u770b\u7740\u82cf\u6d25\u4e1c\u53cc\u624b\u53c9\u8170\u773a\u671b\u5c71\u4e0b\u98ce\u666f\u7684\u6021\u7136\u795e\u6001\uff0c\u5fc3\u60f3\uff1a\u4f5c\u4e3a\u5e55\u540e\u63a8\u52a8\u8fd9\u4e9b\u53d8\u5316\u7684\u4eba\u7fa4\u4e2d\u7684\u4e00\u5458\uff0c\u7ad9\u5728\u5c71\u5761\u4e0a\u770b\u7740\u8fdc\u5904\u7684\u7f8e\u666f\uff0c\u5fc3\u91cc\u7684\u81ea\u8c6a\u611f\u5927\u6982\u662f\u4e0d\u8a00\u800c\u55bb\u7684\u5427\u3002\u53c8\u6de1\u6de1\u4e00\u7b11\uff0c\u8fd9\u65f6\u5019\u4ed6\u4e5f\u80fd\u66f4\u771f\u5207\u7684\u611f\u53d7\u5230\u878d\u5165\u9526\u6e56\u4f1a\u6709\u600e\u6837\u7684\u672a\u6765\u5728\u7b49\u5f85\u7740\u81ea\u5df1\uff0c\u73af\u987e\u5de6\u53f3\uff0c\u90fd\u662f\u4e3a\u4e86\u53c2\u4e0e\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u63d0\u524d\u4e00\u5929\u62b5\u8fbe\u5efa\u90ba\u7684\u9526\u6e56\u9ad8\u5c42\uff0c\u5728\u573a\u7684\u8fd9\u4e9b\u4eba\u53ef\u4ee5\u8bf4\u65e0\u4e00\u4e0d\u662f\u5f53\u4e16\u4e4b\u9009\uff0c\u5728\u77ed\u77ed\u4e09\u56db\u5e74\u7684\u65f6\u95f4\u91cc\u90fd\u805a\u96c6\u5230\u9526\u6e56\u7684\u65d7\u4e0b\u3002</p><p>\u3000\u3000\u9526\u6e56\u4f53\u7cfb\u4ee5\u7231\u8fbe\u3001\u9526\u6e56\u5546\u4e8b\u4e3a\u6838\u5fc3\u67b6\u6784\u4e86\u6574\u4e2a\u4f53\u7cfb\uff0c\u6709\u8d44\u683c\u53c2\u52a0\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u7684\u9ad8\u5c42\u4e5f\u4ee5\u8fd9\u4e24\u5bb6\u6210\u5458\u4f01\u4e1a\u4e3a\u591a\uff0c\u7231\u8fbe\u96c6\u56e2\u6709\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u6210\u6c49\u748b\u3001\u6768\u4e91\u3001\u9c81\u5e86\u751f\u3001\u8c2d\u4e91\u677e\u7b49\u4eba\u4ee3\u8868\u5217\u5e2d\uff0c\u5f53\u7136\u8fd8\u8981\u7b97\u4e0a\u65b0\u52a0\u5165\u9526\u6e56\u5c31\u51fa\u4efb\u8981\u804c\u7684\u5f20\u4e9a\u5e73\uff0c\u9526\u6e56\u5546\u4e8b\u867d\u8bf4\u53ea\u662f\u521d\u8bbe\u7acb\uff0c\u4e5f\u6709\u5305\u62ec\u5b59\u5c1a\u4e49\u3001\u53f6\u5efa\u658c\u3001\u845b\u660e\u5fb7\u3001\u8096\u664b\u6210\u3001\u848b\u8587\u3001\u5468\u4e00\u5e73\u7b49\u4eba\u4ee3\u8868\u5217\u5e2d\uff0c\u6b64\u5916\uff0c\u65b0\u5149\u7eb8\u4e1a\u7531\u5468\u6e38\u3001\u5468\u590d\u4ee3\u8868\u5217\u5e2d\uff0c\u65b0\u5149\u6797\u4e1a\u7531\u5b8b\u5fd7\u4f51\u4ee3\u8868\u5217\u5e2d\uff0c\u4e2d\u6676\u5fae\u82af\u7531\u67f3\u5fd7\u6210\u4ee3\u8868\u5217\u5e2d\u3001\u9999\u96ea\u6d77\u7531\u9676\u884c\u5065\u3001\u9648\u6cab\u3001\u53f6\u5c0f\u6850\u4ee3\u8868\u5217\u5e2d\u3001\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u7531\u76db\u9752\u4ee3\u8868\u5217\u5e2d\uff0c\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u7531\u90b5\u81f3\u521a\u4ee3\u8868\u5217\u5e2d\uff0c\u5b8f\u8fdc\u5b9e\u4e1a\u7531\u5f20\u77e5\u975e\u4ee3\u8868\u5217\u5e2d\u3001\u8d8a\u79c0\u63a7\u80a1\u3001\u9526\u6e56\u63a7\u80a1\u7531\u8521\u7eef\u5a1f\u4ee3\u8868\u4ee3\u8868\u5217\u5e2d\u3001\u7231\u8fbe\u7535\u5b50\u7531\u5085\u5bb6\u4fca\u4ee3\u8868\u5217\u5e2d\uff0c\u76db\u946b\u7535\u5668\u7531\u90b5\u5fc3\u6587\u4ee3\u8868\u5217\u5e2d\uff0c\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u7531\u5d14\u56fd\u6052\u4ee3\u8868\u5217\u5e2d\u2026\u2026\u6b64\u5916\u8fd8\u8981\u518d\u52a0\u4e0a\u4ee3\u8868\u6d77\u88d5\u516c\u53f8\u5217\u5e2d\u7684\u8c22\u665a\u6674\u3002\u9664\u4e86\u5f20\u606a\u4e4b\u5916\uff0c\u6b63\u5f0f\u6709\u8d44\u683c\u53c2\u52a0\u8fd9\u6b21\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u7684\u4eba\u5458\u5171\u4e8c\u5341\u4e5d\u4eba\uff0c\u53c2\u52a0\u8fd9\u6b21\u4f1a\u8bae\uff0c\u4e5f\u786e\u8ba4\u4e86\u4ed6\u4eec\u5728\u9526\u6e56\u4f53\u7cfb\u5185\u7684\u6838\u5fc3\u7ba1\u7406\u8005\u7684\u8eab\u4efd\u4e0e\u5730\u4f4d\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u770b\u4e86\u770b\u5de6\u53f3\u7684\u4eba\u7fa4\uff0c\u5fc3\u60f3\u8fd9\u91cc\u7684\u4e8c\u4e09\u5341\u53f7\u968f\u4fbf\u8c01\u6311\u51fa\u6765\u90fd\u582a\u79f0\u4e00\u65f6\u4e4b\u9009\u5427\uff0c\u4f46\u662f\u5c06\u4ed6\u4eec\u805a\u96c6\u5728\u4e00\u8d77\u7684\u7075\u9b42\u4eba\u7269\u5374\u662f\u90a3\u4e2a\u4e0d\u8db3\u4e8c\u5341\u4e00\u5c81\u7684\u9752\u5e74\u3002\u5f20\u4e9a\u5e73\u56de\u5934\u627e\u5f20\u606a\u7684\u8eab\u5f71\uff0c\u770b\u89c1\u5f20\u606a\u4e0e\u8c22\u665a\u6674\u7ad9\u5728\u4e00\u8d77\u8bf4\u8bdd\uff0c\u54e6\uff0c\u8c22\u665a\u6674\u5e73\u65e5\u4f4e\u8c03\u60ef\u4e86\uff0c\u4e0d\u60f9\u4eba\u77a9\u76ee\uff0c\u4f46\u4e0d\u53ef\u5426\u8ba4\uff0c\u8981\u6ca1\u6709\u5979\u575a\u5b9a\u7684\u652f\u6301\uff0c\u9526\u6e56\u5c31\u6ca1\u6709\u817e\u98de\u7684\u575a\u5b9a\u6839\u57fa\uff0c\u8fd8\u6709\u53f6\u5efa\u658c\uff0c\u53f6\u5efa\u658c\u80cc\u540e\u7684\u53f6\u5bb6\u53ef\u4ee5\u8bf4\u662f\u9526\u6e56\u817e\u98de\u7684\u53e6\u4e00\u4e2a\u575a\u5b9a\u6839\u57fa\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7684\u9526\u6e56\u5230\u5e95\u6709\u591a\u5e9e\u5927\uff1f</p><p>\u3000\u3000\u4eca\u5e74\u590f\u5b63\u6c34\u707e\u540e\uff0c\u4e3a\u7ed9\u6c5f\u5357\u7701\u7b79\u63aa\u707e\u540e\u91cd\u5efa\u8d44\u91d1\uff0c\u5f20\u606a\u4e0e\u8c22\u665a\u6674\u8054\u5408\u5411\u90ed\u3001\u4f55\u3001\u9a6c\u3001\u9648\u7b49\u4e1c\u5357\u4e9a\u534e\u5546\u5bb6\u65cf\u8f6c\u8ba9\u7231\u8fbe\u96c6\u56e26\uff05\u7684\u80a1\u6743\u4f5c\u4ef7\u4e00\u4ebf\u4e24\u5343\u4e07\u7f8e\u5143\uff0c\u8f6c\u8ba9\u8d8a\u79c0\u63a7\u80a16\uff05\u7684\u80a1\u6743\u4f5c\u4ef7\u516b\u5343\u4e07\u7f8e\u5143\uff0c\u4ee5\u6b64\u6b21\u80a1\u4ef7\u8f6c\u8ba9\u7684\u4ef7\u683c\u4e3a\u57fa\u7840\u8ba1\u7b97\u7231\u8fbe\u96c6\u56e2\u7684\u5e02\u503c\u8d85\u8fc720\u4ebf\u7f8e\u5143\uff0c\u8d8a\u79c0\u63a7\u80a1\u7684\u5e02\u503c\u8d85\u8fc7\u53bb13\u4ebf\u7f8e\u5143\u3002\u8d8a\u79c0\u63a7\u80a1\u662f\u9526\u6e56\u4f53\u7cfb\u5185\u7684\u8d44\u4ea7\u7ba1\u7406\u516c\u53f8\uff0c\u8fd9\u6b21\u4f1a\u8bae\u5c06\u51b3\u5b9a\u5c06\u8d8a\u79c0\u63a7\u80a1\u6574\u5408\u5e76\u5165\u9526\u6e56\u5546\u4e8b\u7684\u6295\u8d44\u4e0e\u8d44\u4ea7\u7ba1\u7406\u90e8\u95e8\uff0c\u4f46\u662f\u4ee5\u8fd9\u4e24\u4e2a\u7b80\u5355\u7684\u6570\u636e\u8fd8\u8fdc\u8fdc\u4e0d\u80fd\u53cd\u5e94\u51fa\u4ee5\u7231\u8fbe\u96c6\u56e2\u3001\u9526\u6e56\u5546\u4e8b\u4e24\u5bb6\u6210\u5458\u4f01\u4e1a\u4e3a\u6838\u5fc3\u7684\u9526\u6e56\u4f53\u7cfb\u7684\u5e9e\u5927\u67b6\u6784\u4e0e\u89c4\u6a21\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u662f\u5341\u4e8c\u6708\u4e94\u65e5\u6b63\u5f0f\u8131\u79bb\u98de\u5229\u6d66\uff0c\u4ed6\u6ca1\u6709\u7ed9\u81ea\u5df1\u4f11\u5047\u7684\u65f6\u95f4\uff0c\u5728\u6d77\u5dde\u9017\u7559\u4e86\u56db\u5929\u7684\u65f6\u95f4\uff0c\u53c8\u63d0\u524d\u4e09\u5929\u5c31\u5230\u5efa\u90ba\u4e86\uff0c\u5c31\u7b97\u662f\u6b63\u5f0f\u51fa\u4efb\u7231\u8fbe\u96c6\u56e2\u9ad8\u7ea7\u526f\u603b\u88c1\u517c\u5143\u5668\u4ef6\u603b\u88c1\u7684\u804c\u52a1\u3002\u5728\u8fd9\u4e03\u5929\u7684\u65f6\u95f4\u91cc\uff0c\u4ed6\u6700\u4e3b\u8981\u7684\u5de5\u4f5c\u5c31\u662f\u5c06\u9526\u6e56\u7684\u6574\u4e2a\u67b6\u6784\u90fd\u719f\u6089\u8d77\u6765\u3002</p><p>\u3000\u3000\u5916\u4eba\u5bf9\u9526\u6e56\u6700\u4e3a\u719f\u6089\u7684\u81ea\u7136\u662f\u7231\u8fbe\u7535\u5b50\u3002</p><p>\u3000\u3000\u501f\u58f3\u4e0a\u5e02\u540e\u7684\u7231\u8fbe\u7535\u5b50\u4f9d\u65e7\u4ee5\u789f\u673a\u4e1a\u52a1\u4e3a\u4e3b\uff0c\u4e5d\u4e03\u5e74\u521d\u4e0e\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u8054\u5408\u63a8\u51faSVCD\u789f\u673a\u6280\u672f\uff0c\u968f\u540e\u7231\u8fbe\u7535\u5b50\u53c8\u5355\u72ec\u9646\u7eed\u63a8\u51faSVCD\u89c6\u76d8\u6280\u672f\u4e0e\u8d85\u5bc6\u5ea6SVCD\u89c6\u76d8\u6280\u672f\uff0c\u786e\u5b9a\u4e86SVCD\u5728\u77e5\u8bc6\u4ea7\u6743\u4fdd\u62a4\u76f8\u5bf9\u5bbd\u677e\u7684\u65b0\u5174\u56fd\u5bb6\u4e0e\u5730\u533a\u789f\u673a\u5e02\u573a\u7684\u5730\u4f4d\uff1b\u7231\u8fbe\u7535\u5b50\u9884\u8ba1\u4e5d\u516b\u5e74\u5168\u5e74\u751f\u4ea7\u4ee5SVCD\u4e3a\u4e3b\u7684\u5404\u7c7b\u578b\u789f\u673a\u5c06\u7a81\u7834\u4e03\u767e\u4e07\u53f0\uff0c\u4ea7\u503c\u7a81\u7834\u4e00\u767e\u4ebf\u5143\u3002\u7531\u4e8e\u7231\u8fbe\u7535\u5b50\u6210\u7acb\u7684\u65f6\u95f4\u6700\u65e9\uff0c\u4ece\u4e5d\u516b\u5e74\u8d77\u5c06\u9010\u6e10\u4eab\u53d7\u4e0d\u5230\u7a0e\u8d39\u51cf\u514d\u7684\u4f18\u60e0\u653f\u7b56\uff0c\u4e5d\u516b\u5e74\u5c06\u5411\u6d77\u5dde\u5e02\u8d22\u653f\u8d21\u732e\u7a0e\u6536\u8fd1\u516d\u4e2a\u4ebf\u3002\u867d\u8bf4\u4e5d\u516b\u5e74\u4e1c\u5357\u4e9a\u789f\u673a\u5e02\u573a\u4ecd\u906d\u91cd\u632b\uff0c\u56fd\u5185\u789f\u673a\u5e02\u573a\u7684\u7ade\u4e89\u4e5f\u5341\u5206\u7684\u6fc0\u70c8\uff0c\u4f5c\u4e3a\u7b2c\u4e00\u789f\u673a\u54c1\u724c\u7684\u7231\u8fbe\u7535\u5b50\u4e5d\u516b\u5e74\u4ecd\u7136\u5b8c\u6210\u4e5d\u4e2a\u4ebf\u7684\u4e3b\u8425\u4e1a\u52a1\u5e74\u51c0\u5229\u6da6\u76ee\u6807\u3002</p><p>\u3000\u3000\u7531\u4e8e\u7231\u8fbe\u96c6\u56e2\u4e0e\u8d8a\u79c0\u63a7\u80a1\u5bf9\u7231\u8fbe\u7535\u5b50\u4e00\u76f4\u5b9e\u65bd\u79ef\u6781\u7684\u589e\u6301\u8ba1\u5212\uff0c\u52a0\u4e0a\u7231\u8fbe\u7535\u5b50\u7684\u789f\u673a\u4e1a\u52a1\u5728\u6de1\u5e02\u7684\u8868\u73b0\u4f9d\u65e7\u4e0d\u4fd7\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4e0a\u7684\u8868\u73b0\u8981\u8fdc\u8fdc\u4f18\u4e8e\u5176\u4ed6\u4e0a\u5e02\u516c\u53f8\u3002\u867d\u7136\u7ef4\u6301\u5728\u4e03\u5341\u4ebf\u6e2f\u5143\u5de6\u53f3\u7684\u5e02\u503c\u8ddd\u79bb\u5cf0\u503c\u4e0b\u964d\u670930\uff05\uff0c\u4f46\u662f\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4e0a\uff0c\u6bd4\u6bd4\u7686\u662f\u5e02\u503c\u53d7\u632b\u8fc7\u534a\u3001\u751a\u81f3\u8d85\u8fc7\u4e09\u5206\u4e4b\u4e8c\u7684\u516c\u53f8\u3002</p><p>\u3000\u3000\u4e00\u76f4\u5b9e\u65bd\u79ef\u6781\u7684\u589e\u6301\u8ba1\u5212\uff0c\u7231\u8fbe\u96c6\u56e2\u4e0e\u8d8a\u79c0\u63a7\u80a1\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u6301\u80a1\u6bd4\u4f8b\u5728\u8fc7\u53bb\u4e00\u5e74\u65f6\u95f4\u91cc\u7531\u4e4b\u524d\u7684\u4e0d\u8db330\uff05\u63d0\u9ad8\u523048\uff05\u3002</p><p>\u3000\u3000\u8fd9\u5927\u6982\u662f\u5916\u4eba\u80fd\u591f\u76f4\u63a5\u4e86\u89e3\u5230\u7684\u9526\u6e56\uff0c\u9526\u6e56\u6301\u80a1\u7231\u8fbe\u7535\u5b50\u7684\u80a1\u4efd\u5e02\u503c\u7ea634\u4ebf\u6e2f\u5143\uff1b\u50cf\u9526\u6e56\u4e5d\u516b\u5e74\u8fd8\u80fd\u989d\u5916\u5206\u4eab\u789f\u673a\u89e3\u7801\u82af\u7247\u516b\u5343\u4e07\u7f8e\u5143\u7684\u5784\u65ad\u66b4\u5229\uff0c\u662f\u5916\u4eba\u6240\u4e0d\u77e5\u7684\u3002</p><p>\u3000\u3000\u8fd9\u4ec5\u4ec5\u662f\u51b0\u5c71\u4e00\u89d2\uff0c\u8981\u662f\u80fd\u66f4\u7ec6\u5fc3\u4e00\u4e9b\uff0c\u4ece\u7231\u8fbe\u7535\u5b50\u516c\u5e03\u7684\u7b2c\u4e09\u5b63\u5ea6\u8d22\u62a5\u91cc\u53ef\u4ee5\u770b\u5230\u60e0\u5c71\u9999\u96ea\u6d77\u7535\u5668\u4eca\u5e74\u7684\u8d22\u52a1\u72b6\u51b5\uff0c\u867d\u7136\u9999\u96ea\u6d77\u4eca\u5e74\u6ca1\u6709\u5206\u7ea2\u7684\u8ba1\u5212\uff0c\u4e0d\u4f1a\u7ed9\u7231\u8fbe\u7535\u5b50\u7684\u5e74\u51c0\u5229\u6da6\u505a\u51fa\u76f4\u63a5\u7684\u8d21\u732e\uff0c\u4f46\u662f\u4ecd\u7136\u80fd\u4ece\u7231\u8fbe\u7535\u5b50\u7684\u8d22\u62a5\u91cc\u770b\u51fa\u9999\u96ea\u6d77\u501f\u52a9\u592e\u89c6\u5e7f\u544a\u6807\u738b\u4e4b\u540e\u7684\u817e\u98de\u59ff\u6001\u3002</p><p>\u3000\u3000\u9526\u6e56\u4ece\u4e5d\u516d\u5e74\u6625\u672b\u6b63\u5f0f\u4ecb\u5165\u9999\u96ea\u6d77\uff0c\u901a\u8fc7\u7231\u8fbe\u7535\u5b50\u3001\u8d8a\u79c0\u63a7\u80a1\u4e24\u6b21\u5bf9\u9999\u96ea\u6d77\u8fdb\u884c\u6ce8\u8d44\uff0c\u4f7f\u5f97\u5728\u56fd\u5185\u6709\u8fd1\u4e8c\u5341\u5e74\u5386\u53f2\u7684\u6c11\u65cf\u54c1\u724c\u9999\u96ea\u6d77\u83b7\u5f97\u5145\u8db3\u7684\u53d1\u5c55\u8d44\u91d1\uff0c\u51c0\u8d44\u4ea7\u5728\u77ed\u65f6\u95f4\u91cc\u589e\u6da8\u5230\u5341\u4e94\u4ebf\uff0c\u8d44\u4ea7\u89c4\u6a21\u5728\u4e5d\u4e03\u5e74\u5e74\u672b\u65f6\u5c31\u8fbe\u5230\u4e09\u5341\u4ebf\uff0c\u5728\u4e5d\u4e03\u5e74\u5e74\u672b\u9664\u4e86\u5efa\u6210\u65b0\u7684\u7535\u51b0\u7bb1\u3001\u51b0\u67dc\u751f\u4ea7\u7ebf\u4e4b\u5916\uff0c\u8fd8\u6295\u8d44\u5efa\u9020\u4e86\u6d17\u8863\u673a\u4e0e\u7a7a\u8c03\u751f\u4ea7\u7ebf\uff0c\u4e5d\u516b\u5e74\u9999\u96ea\u6d77\u7684\u54c1\u724c\u5e7f\u544a\u6b63\u5f0f\u8fdb\u5165\u4e2d\u592e\u7535\u89c6\u53f0\u9ec4\u91d1\u65f6\u6bb5\u64ad\u51fa\uff0c\u9999\u96ea\u6d77\u7684\u4ea7\u54c1\u9500\u552e\u4e00\u6539\u5f80\u5e74\u7684\u9893\u7136\uff0c\u4e00\u4e3e\u593a\u56de\u201c\u5317\u6d77\u5c14\u3001\u5357\u9999\u96ea\u6d77\u201d\u7684\u5730\u4f4d\uff0c\u4e3b\u8425\u4e1a\u52a1\u2014\u2014\u7535\u51b0\u7bb1\u53ca\u51b7\u67dc\u7684\u9500\u552e\u989d\u9884\u8ba1\u4e5d\u516b\u5e74\u5168\u5e74\u80fd\u8fbe\u523036\u4ebf\uff0c\u5408\u5e76\u6d17\u8863\u673a\u3001\u7a7a\u8c03\u7b49\u4e1a\u52a1\uff0c\u5168\u5e74\u9500\u552e\u603b\u989d\u5c06\u8fbe\u523050\u4ebf\uff0c\u6263\u9664\u4e0a\u7f34\u7684\u7a0e\u91d1\u4e4b\u540e\uff0c\u5e74\u7eaf\u5229\u4ecd\u5c06\u8fbe\u52305\u4ebf\u5143\u5de6\u53f3\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c50\u4ebf\u7684\u9500\u552e\u989d\u4e0e5\u4ebf\u7684\u5e74\u7eaf\u5229\u8fd8\u4ec5\u4ec5\u662f\u9999\u96ea\u6d77\u7684\u521d\u671f\u76ee\u6807\uff0c\u4e5d\u4e5d\u5e74\u9999\u96ea\u6d77\u5c06\u52a0\u5927\u5f00\u53d1\u56fd\u5185\u6d17\u8863\u673a\u3001\u7a7a\u8c03\u5e02\u573a\u7684\u529b\u5ea6\u4e0e\u54c1\u724c\u5f00\u53d1\u6df1\u5ea6\uff0c\u540c\u65f6\u8fd8\u8ba1\u5212\u5411\u4e0a\u6e38\u7684\u51b7\u51dd\u5668\u4e0e\u538b\u7f29\u673a\u4ea7\u4e1a\u5ef6\u4f38\uff0c\u8fd8\u5c06\u542f\u52a8\u6d77\u5916\u5e02\u573a\u7684\u5f00\u53d1\uff0c\u8ba1\u5212\u5728\u4e5d\u4e5d\u5e74\u5e95\u5b9e\u73b0\u4e0a\u8ff0\u7684\u4e24\u4e2a\u6570\u5b57\u5404\u7ffb\u4e00\u7ffb\u7684\u76ee\u6807\u3002</p><p>\u3000\u3000\u9664\u4e86\u7231\u8fbe\u7535\u5b50\u6301\u6709\u9999\u96ea\u6d7725\uff05\u7684\u80a1\u4efd\u4e4b\u5916\uff0c\u9526\u6e56\u8fd8\u901a\u8fc7\u8d8a\u79c0\u63a7\u80a1\u76f4\u63a5\u6301\u6709\u9999\u96ea\u6d7751\uff05\u7684\u80a1\u4efd\u3002</p><p>\u3000\u3000\u8fd9\u4ec5\u4ec5\u662f\u5916\u4eba\u901a\u8fc7\u516c\u5f00\u8d44\u6599\u6240\u80fd\u4e86\u89e3\u5230\u7684\u9526\u6e56\u7684\u51b0\u5c71\u4e00\u89d2\u3002</p><p>\u3000\u3000\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u53ec\u5f00\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\uff0c\u9664\u4e86\u8ba8\u8bba\u5c06\u8d8a\u79c0\u63a7\u80a1\u5e76\u5165\u9526\u6e56\u5546\u4e8b\u7684\u6295\u8d44\u4e0e\u8d44\u4ea7\u7ba1\u7406\u90e8\u95e8\u4e4b\u5916\uff0c\u4e5f\u5bf9\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\u9526\u6e56\u4f53\u7cfb\u878d\u5408\u8ba1\u5212\u7684\u5b9e\u65bd\u8fc7\u7a0b\u8fdb\u884c\u68c0\u8ba8\u5e76\u786e\u8ba4\u4e0b\u4e00\u6b65\u7684\u4ea4\u53c9\u6301\u80a1\u65b9\u6848\uff0c\u8fd9\u6b21\u8bae\u9898\u662f\u4e8b\u5148\u5c31\u786e\u5b9a\u4e0b\u6765\u7684\uff0c\u76f8\u5173\u6750\u6599\u4e5f\u63d0\u524d\u9012\u4ea4\u5230\u4e0e\u4f1a\u8005\u7684\u624b\u91cc\u3002</p><p>\u3000\u3000\u4ee5\u52a0\u5f3a\u6570\u5b57\u624b\u673a\u7ec8\u7aef\u9500\u552e\u7684\u201c\u767e\u57ce\u767e\u5e97\u201d\u8ba1\u5212\u4e5f\u6b63\u5f0f\u542f\u52a8\uff0c\u7231\u8fbe\u96c6\u56e2\u4e0e\u76db\u946b\u7535\u5668\u5171\u6295\u8d445\u4ebf\u5143\u6210\u7acb\u76db\u946b\u7535\u8baf\u5f00\u59cb\u6570\u5b57\u624b\u673a\u4e13\u8425\u4e1a\u52a1\uff0c\u7231\u8fbe\u96c6\u56e2\u63d0\u4f9b3\u4ebf\u5143\u8d44\u91d1\uff0c\u6362\u53d6\u76db\u946b\u7535\u566812\uff05\u7684\u80a1\u4efd\u4e0e\u76db\u946b\u7535\u8baf40\uff05\u7684\u80a1\u4efd\uff0c\u53e6\u5916\uff0c\u53f6\u5bb6\u8fd8\u5c06\u8fdb\u4e00\u6b65\u4e0e\u5f20\u606a\u3001\u8c22\u665a\u6674\u7b49\u4eba\u4ea4\u6362\u5bf9\u7231\u8fbe\u96c6\u56e2\u3001\u9526\u6e56\u5546\u4e8b\u4e0e\u76db\u946b\u7535\u5668\u7684\u6301\u80a1\u6743\uff0c\u4ee5\u4fdd\u8bc1\u5c06\u76db\u946b\u7535\u5668\u9010\u6b65\u7684\u878d\u5408\u5230\u9526\u6e56\u4f53\u7cfb\u4e4b\u4e2d\uff0c\u53e6\u5916\u6b63\u4e0e\u60e0\u5c71\u5e02\u653f\u5e9c\u8c08\u5224\uff0c\u5c06\u7531\u7231\u8fbe\u96c6\u56e2\u6536\u8d2d\u60e0\u5c71\u5e02\u8f7b\u5de5\u5c40\u6240\u6301\u6709\u7684\u9999\u96ea\u6d77\u4f59\u4e0b\u768424\uff05\u7684\u80a1\u4efd\uff0c\u5c06\u9999\u96ea\u6d77\u7535\u5668\u5b8c\u5168\u7684\u79c1\u6709\u5316\uff0c\u8fd9\u4e5f\u662f\u4e3a\u9999\u96ea\u6d77\u4e0b\u4e00\u6b65\u7684\u4e0a\u5e02\u8ba1\u5212\u505a\u51c6\u5907\u3002</p><p>\u3000\u3000\u9664\u4e86\u8981\u53d1\u5c55\u56fd\u5185\u6700\u5927\u89c4\u6a21\u7684\u624b\u673a\u4e13\u8425\u4f01\u4e1a\u4e4b\u5916\uff0c\u6b64\u65f6\u7684\u76db\u946b\u7535\u5668\u62e5\u6709\u516b\u5341\u516b\u5bb6\u5bb6\u7535\u5356\u573a\uff0c\u5176\u4e2d\u4e8c\u5341\u516b\u5bb6\u90fd\u662f\u8425\u4e1a\u9762\u79ef\u63a5\u8fd1\u6216\u8d85\u8fc7\u4e0a\u4e07\u5e73\u7c73\u7684\u65d7\u8230\u5356\u573a\uff0c\u96c7\u7528\u5458\u5de5\u603b\u6570\u8d85\u8fc7\u4e00\u4e07\u4e8c\u5343\u4eba\uff0c\u9500\u552e\u989d\u7a81\u7834\u4e00\u767e\u4e8c\u5341\u4ebf\uff0c\u5f53\u4e4b\u65e0\u6127\u662f\u56fd\u5185\u6700\u5927\u7684\u5546\u4e1a\u4f01\u4e1a\uff0c\u867d\u8bf4\u5728\u6269\u5f20\u671f\u91c7\u53d6\u4e86\u7c97\u653e\u5f0f\u7684\u7ba1\u7406\u6a21\u5f0f\uff0c\u5e74\u51c0\u5229\u6da6\u9ad8\u8fbe\u4e03\u4ebf\u3002</p><p>\u3000\u3000\u4e00\u65e6\u5f62\u6210\u4ee5\u7231\u8fbe\u96c6\u56e2\u3001\u9526\u6e56\u5546\u4e8b\u4e3a\u6838\u5fc3\u7684\u9526\u6e56\u4f53\u7cfb\uff0c\u8fd9\u4e2a\u4f53\u7cfb\u5c06\u76f4\u63a5\u63a7\u5236\u4e16\u754c\u6700\u5927\u7684\u789f\u673a\u4f01\u4e1a\u7231\u8fbe\u7535\u5b50\u3001\u56fd\u5185\u7b2c\u56db\u5927\u624b\u673a\u5382\u5546\u3001\u5168\u7403\u7b2c\u4e8c\u5927\u4fbf\u643a\u5f0f\u97f3\u4e50\u64ad\u653e\u5668\u5382\u5546\u3001\u7b2c\u4e8c\u5927\u5373\u65f6\u50a8\u5b58\u76d8\u5382\u5546\u7231\u8fbe\u96c6\u56e2\u3001\u534e\u4e1c\u6700\u5927\u7684\u9020\u7eb8\u4f01\u4e1a\u65b0\u5149\u7eb8\u4e1a\u3001\u56fd\u5185\u6700\u5927\u7684\u6797\u4f01\u65b0\u5149\u6797\u4e1a\uff0c\u56fd\u5185\u6700\u5927\u7684\u5bb6\u7535\u8fde\u9501\u4f01\u4e1a\u76db\u946b\u7535\u5668\u3001\u76ee\u524d\u56fd\u5185\u552f\u4e00\u7684\u6676\u5706\u5382\u4e2d\u6676\u5fae\u82af\uff0c\u56fd\u5185\u7b2c\u4e8c\u5927\u7535\u51b0\u7bb1\u3001\u51b7\u67dc\u751f\u4ea7\u4f01\u4e1a\u9999\u96ea\u6d77\uff0c\u6bd4\u4e0a\u8ff0\u6210\u5458\u4f01\u4e1a\uff0c\u95f4\u63a5\u63a7\u5236\u7684\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u3001\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u3001\u5b8f\u8fdc\u5b9e\u4e1a\u5012\u663e\u5f97\u65e0\u5173\u7d27\u8981\u4e86\u3002</p><p>\u3000\u3000\u8981\u60f3\u7406\u6e05\u9526\u6e56\u4f53\u7cfb\u5185\u90e8\u590d\u6742\u7684\u6743\u76ca\u5173\u7cfb\uff0c\u5f20\u4e9a\u5e73\u5373\u4f7f\u62ff\u5230\u6700\u76f4\u63a5\u7684\u8d44\u6599\uff0c\u4e5f\u4f1a\u611f\u5230\u5341\u5206\u7684\u5934\u75bc\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u5fc3\u91cc\u4e5f\u5f88\u597d\u5947\uff1a\u5f20\u606a\u8fd9\u4e48\u4e00\u4e2a\u8fd8\u4e0d\u6ee1\u4e8c\u5341\u4e00\u5c81\u7684\u9752\u5e74\u6b64\u65f6\u540d\u4e0b\u7684\u8d44\u4ea7\u5230\u5e95\u6709\u591a\u5c11\uff1f\u4ee5\u4e5d\u6708\u4e2d\u65ec\u5411\u90ed\u3001\u9648\u3001\u9a6c\u3001\u4f55\u7b49\u4e1c\u5357\u4e9a\u534e\u5546\u5bb6\u65cf\u8f6c\u8ba9\u80a1\u6743\u7684\u4ef7\u683c\u4e3a\u57fa\u7840\u8ba1\u7b97\uff0c\u5f20\u606a\u540d\u4e0b\u7684\u8d44\u4ea7\u603b\u503c\u5dee\u4e0d\u591a\u6709\u4e8c\u5341\u4e94\u516d\u4ebf\u7f8e\u5143\uff0c\u4ee5\u53f6\u5efa\u658c\u4e3a\u4ee3\u8868\u7684\u53f6\u5bb6\u8d44\u4ea7\u5dee\u4e0d\u591a\u6709\u516b\u4ebf\u7f8e\u5143\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u745c\u592b\u5987\u8d44\u4ea7\u5dee\u4e0d\u591a\u56db\u4ebf\u7f8e\u5143\uff0c\u8c22\u665a\u6674\u5dee\u4e0d\u591a\u62e5\u6709\u56db\u4ebf\u7f8e\u5143\uff0c\u845b\u660e\u4fca\u5dee\u4e0d\u591a\u4e24\u4ebf\u7f8e\u5143\uff0c\u8bb8\u601d\u867d\u7136\u6ca1\u6709\u53c2\u52a0\u6b64\u6b21\u7684\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\uff0c\u540d\u4e0b\u7684\u8d44\u4ea7\u4e5f\u8981\u6709\u4e00\u4ebf\u4e94\u5343\u7f8e\u5143\u4e4b\u591a\u3002\u6709\u8d44\u683c\u53c2\u52a0\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u7684\u6838\u5fc3\u7ba1\u7406\u4eba\u5458\uff0c\u9664\u4e86\u5f20\u4e9a\u5e73\u4ed6\u81ea\u5df1\u662f\u65b0\u52a0\u5165\u9526\u6e56\u7684\u3001\u8fd8\u6ca1\u6709\u83b7\u5f97\u9526\u6e56\u7684\u80a1\u6743\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u7684\u8eab\u5bb6\u4e5f\u81ea\u662f\u4e0d\u83f2\uff0c\u5f53\u4e2d\u53c8\u4ee5\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4e24\u4e2a\u5bf9\u9526\u6e56\u8d21\u732e\u6700\u5927\u7684\u5143\u8001\u8eab\u5bb6\u6700\u4e3a\u4e30\u539a\uff0c\u8d44\u4ea7\u90fd\u5728\u4e00\u4ebf\u7f8e\u5143\u4ee5\u4e0a\u3002</p><p>\u3000\u3000\u4ee5\u6b64\u4e3a\u57fa\u7840\u7684\u8ba1\u7b97\uff0c\u9526\u6e56\u4f53\u7cfb\u7684\u6838\u5fc3\u8d44\u4ea7\u89c4\u6a21\u521a\u521a\u8fbe\u5230\u4e94\u5341\u4ebf\u7f8e\u5143\uff0c\u8fd9\u8981\u4e22\u5230\u56fd\u9645\u80cc\u666f\u4e0b\u6bd4\u8f83\uff0c\u9526\u6e56\u8fd8\u771f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4e09\u661f\u96c6\u56e2\u4e5d\u516b\u5e74\u8d1f\u503a\u89c4\u6a21\u5c31\u9ad8\u8fbe\u4e00\u767e\u4e03\u5341\u4ebf\u7f8e\u5143\uff0c\u7d22\u5c3c\u7684\u5e02\u503c\u5373\u4f7f\u5728\u6700\u4e25\u91cd\u7684\u7ecf\u6d4e\u5371\u673a\u65f6\u671f\u4ecd\u7ef4\u6301\u4e09\u767e\u4ebf\u7f8e\u5143\u4e4b\u4e0a\uff0c\u7d22\u5c3c\u8fd8\u4ec5\u4ec5\u662f\u4e09\u4e95\u8d22\u9600\u63a7\u5236\u7684\u4e00\u5bb6\u65e5\u7cfb\u7535\u5b50\u4f01\u4e1a\uff1b\u4e0d\u8fc7\u8fd9\u6b21\u53c2\u52a0\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u7684\u9ad8\u5c42\u5fc3\u91cc\u90fd\u5f88\u6e05\u695a\uff0c\u6574\u4e2a\u9526\u6e56\u4f53\u7cfb\u7684\u6838\u5fc3\u4ef7\u503c\u7edd\u5bf9\u8981\u8fdc\u8fdc\u8d85\u8fc7\u4e94\u5341\u4ebf\u7f8e\u5143\uff0c\u751a\u81f3\u80fd\u5bf9\u5c0f\u6c5f\u6d41\u57df\u7684\u7ecf\u6d4e\u3001\u653f\u6cbb\u52bf\u6001\u4ea7\u751f\u663e\u8457\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u5468\u6e38\u8d70\u5230\u5f20\u4e9a\u5e73\u3001\u82cf\u6d25\u4e1c\u8eab\u8fb9\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u518d\u8fc7\u51e0\u5341\u3001\u4e0a\u767e\u5e74\uff0c\u540e\u4eba\u7814\u7a76\u4e2d\u56fd\u6216\u4e16\u754c\u8fd1\u73b0\u4ee3\u7ecf\u6d4e\u53f2\uff0c\u4f1a\u4e0d\u4f1a\u5c06\u8fd9\u6b21\u4f1a\u8bae\u53d6\u4e2a\u7c7b\u4f3c\u2018\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u2019\u8fd9\u4e48\u95ea\u4eae\u7684\u79f0\u8c13\uff1f\u8981\u662f\u53ef\u4ee5\uff0c\u6211\u4eec\u8fd9\u4e9b\u4eba\u90fd\u8981\u9752\u53f2\u7559\u540d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u6e38\u8bf4\u5f97\u98ce\u8da3\uff0c\u795e\u6001\u95f4\u4e5f\u9887\u4e3a\u81ea\u8c6a\uff0c\u5f20\u4e9a\u5e73\u7b11\u4e86\u8d77\u6765\uff0c\u6b64\u65f6\u7684\u9526\u6e56\u4e5f\u65e0\u9700\u63a9\u9970\u91ce\u5fc3\u4e86\uff0c\u7279\u522b\u662f\u8fd9\u6b21\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u7684\u53ec\u5f00\uff0c\u5df2\u7ecf\u6807\u5fd7\u7740\u9526\u6e56\u521d\u6b65\u5177\u5907\u4e86\u7efc\u5408\u6027\u4ea7\u4e1a\u8d22\u56e2\u7684\u7279\u5f81\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u53eb\u4f60\u8fc7\u53bb\u5462\uff1f\u201d\u82cf\u6d25\u4e1c\u63d0\u9192\u5f20\u4e9a\u5e73\u6ce8\u610f\u770b\u5f20\u606a\u5728\u8fdc\u5904\u671d\u8fd9\u8fb9\u62db\u624b\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u3001\u8096\u664b\u6210\u4ee5\u53ca\u9999\u96ea\u6d77\u7535\u5668\u96c6\u56e2\u603b\u88c1\u9676\u884c\u5065\u7b49\u4eba\u7ad9\u5728\u4e00\u8d77\uff0c\u5468\u6e38\u53e6\u627e\u4eba\u8bf4\u8bdd\u53bb\uff0c\u82cf\u6d25\u4e1c\u966a\u5f20\u4e9a\u5e73\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u9999\u96ea\u6d77\u8981\u542f\u52a8\u6570\u5b57\u5bb6\u7535\u8ba1\u5212\u5b9c\u65e9\u4e0d\u5b9c\u8fdf\u7684\u2026\u2026\u201d\u5f20\u606a\u770b\u7740\u82cf\u6d25\u4e1c\u4e0e\u5f20\u4e9a\u5e73\u8d70\u8fc7\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u867d\u7136\u6211\u4eec\u8981\u540c\u65f6\u8fd0\u8425\u7231\u8fbe\u4e0e\u9999\u96ea\u6d77\u53cc\u54c1\u724c\uff0c\u4f46\u662f\u5185\u90e8\u7684\u878d\u5408\u4e0d\u80fd\u56e0\u4e3a\u54c1\u724c\u4e0d\u540c\u800c\u751f\u969c\u788d\uff0c\u4e0e\u60e0\u5c71\u5e02\u653f\u5e9c\u7684\u8c08\u5224\u4e5f\u8981\u5c3d\u5feb\u5b8c\u6210\uff0c\u7531\u7231\u8fbe\u6536\u8d2d\u60e0\u5c71\u5e02\u8f7b\u5de5\u5c40\u624b\u91cc\u5269\u4f59\u7684\u9999\u96ea\u6d77\u7684\u80a1\u4efd\uff0c\u53e6\u5916\uff0c\u9999\u96ea\u6d77\u7684\u6d77\u5916\u5e02\u573a\u5f00\u53d1\u4e5f\u8981\u8fc5\u901f\u542f\u52a8\u8d77\u6765\uff0c\u8fd9\u90e8\u5206\u7684\u5de5\u4f5c\uff0c\u5f53\u7136\u4e5f\u8981\u4ea4\u7ed9\u9526\u6e56\u5546\u4e8b\u65d7\u4e0b\u7684\u6d77\u5916\u8fd0\u8425\u90e8\u53bb\u8fd0\u4f5c\u2026\u2026\u201d\u5f20\u606a\u62cd\u4e86\u62cd\u8096\u664b\u6210\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff0c\u201c\u660e\u5e74\u4f60\u8eab\u4e0a\u7684\u538b\u529b\u4e0d\u5c11\u2026\u2026\u9526\u6e56\u4f9d\u6258\u56fd\u5185\u5e02\u573a\u4e3a\u6839\u57fa\uff0c\u4f46\u80fd\u5426\u5b9e\u73b0\u771f\u6b63\u7684\u817e\u98de\uff0c\u8fd8\u662f\u8981\u6709\u5168\u7403\u5e02\u573a\u7684\u7ade\u4e89\u610f\u8bc6\uff0c\u53ea\u6709\u5728\u5168\u7403\u5e02\u573a\u4e0e\u4e09\u661f\u3001\u7d22\u5c3c\u3001\u677e\u4e0b\u3001\u8bfa\u57fa\u4e9a\u7b49\u6d77\u5916\u7535\u5b50\u5de8\u5934\u8089\u640f\u8fc7\u8fd8\u80fd\u5c79\u7acb\u4e0d\u5012\uff0c\u624d\u80fd\u7b97\u83b7\u5f97\u6210\u529f\uff0c\u6211\u4eec\u6210\u529f\u7684\u6807\u51c6\u662f\u8981\u6253\u9020\u4e00\u4e2a\u8ba9\u56fd\u4eba\u9a84\u50b2\u7684\u4f01\u4e1a\u3002\u201d</p><p>\u3000\u3000\u5915\u9633\u5c06\u5782\uff0c\u5927\u5bb6\u90fd\u4f9d\u6b21\u5750\u4e0a\u8f66\u4e0b\u5c71\u53bb\uff0c\u665a\u4e0a\u7684\u805a\u9910\u5c31\u5b89\u6392\u5728\u521b\u4e1a\u56ed\u7684\u5458\u5de5\u9910\u5385\u91cc\u3002</p></article>"}}}]);
