(window.webpackJsonp=window.webpackJsonp||[]).push([[960],{983:function(p){p.exports={chapterTitle:null,title:"\u7b2c957\u7ae0 \u6821\u56ed\u804a\u5929\u5ba4",content:"<article><h2>\u7b2c957\u7ae0 \u6821\u56ed\u804a\u5929\u5ba4</h2><p>\u3000\u3000\u8096\u664b\u6210\u8ddf\u5f20\u606a\u3001\u9648\u4fe1\u751f\u3001\u9676\u884c\u5065\u3001\u53f6\u5c0f\u6850\u7b49\u4eba\u901a\u62a5\u4e86\u4e00\u4e9b\u4e0e\u65b0\u52a0\u5761\u7535\u4fe1\uff08Singtel\uff09\u5728\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u5408\u4f5c\u4e0a\u8c08\u5224\u7684\u4e00\u4e9b\u4e8b\u9879\u3002</p><p>\u3000\u3000\u9526\u6e56\u624b\u673a\u6700\u521d\u8fdb\u5165\u65b0\u52a0\u5761\u5e02\u573a\uff0c\u9009\u62e9\u7684\u8fd0\u8425\u5546\u5408\u4f5c\u4f19\u4f34\u5c31\u662f\u65b0\u52a0\u5761\u7535\u4fe1\u516c\u53f8\uff0c\u5728\u8fc7\u53bb\u4e00\u5e74\u65f6\u95f4\u91cc\u4e3a\u65b0\u52a0\u5761\u7535\u4fe1\u516c\u53f8\u63d0\u4f9b\u603b\u91cf\u8fbe\u56db\u4e07\u4ef6\u7684\u5b9a\u5236\u624b\u673a\uff0c\u4ec5\u8fd9\u7b14\u4e1a\u52a1\uff0c\u5c31\u5360\u4e86\u65b0\u52a0\u5761\u56fd\u5185\u624b\u673a\u5e02\u573a\u76845\uff05\uff0c\u4e5f\u4e3a\u9526\u6e56\u6210\u4e3a\u65b0\u52a0\u5761\u56fd\u5185\u624b\u673a\u5e02\u573a\u5360\u6709\u7387\u6392\u540d\u7b2c\u4e94\u7684\u5382\u5546\u5960\u5b9a\u4e86\u57fa\u7840\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u7684\u5e02\u573a\u770b\u4e0a\u53bb\u6709\u4e9b\u5fae\u4e0d\u8db3\u9053\uff0c\u5373\u4f7f\u5168\u6c11\u4eba\u624b\u4e00\u53ea\u624b\u673a\uff0c\u624d340\u4e07\u4ef6\u7684\u5e02\u573a\u5bb9\u91cf\uff0c\u5e02\u573a\u9971\u548c\u540e\u7684\u6362\u673a\u5e02\u573a\u5bb9\u91cf\u6bcf\u5e74\u4e5f\u6700\u591a100\u4e07\u4ef6\uff0c\u5c31\u7b97\u5168\u90e8\u5360\u4e0b\u6765\uff0c\u8fd8\u4e0d\u591f\u56fd\u9645\u624b\u673a\u5de8\u5934\u586b\u7259\u7f1d\u7684\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u65e5\u540e\u4e9a\u6d32\u7b2c\u4e8c\u5927\u7684\u79fb\u52a8\u901a\u8baf\u7f51\u7edc\u8fd0\u8425\u5546\uff08\u4ec5\u6b21\u4e8e\u4e2d\u56fd\u79fb\u52a8\uff09\u65b0\u52a0\u5761\u7535\u4fe1\u516c\u53f8\u6b64\u65f6\u8fd8\u6ca1\u6709\u9732\u51fa\u65e5\u540e\u7684\u5a01\u6b66\u96c4\u59ff\uff0c\u4e3b\u8425\u4e1a\u52a1\u8fd8\u5c40\u9650\u5728\u72ed\u7a84\u7684\u3001\u53ea\u6709340\u4e07\u4eba\u53e3\u7684\u65b0\u52a0\u5761\u56fd\u5185\u5e02\u573a\u6fc0\u70c8\u640f\u6740\u3002\u4e0d\u8fc7\u5728\u56fd\u5185\u72ed\u7a84\u53d1\u5c55\u7a7a\u95f4\u7684\u4fc3\u4f7f\u4e0b\uff0c\u9009\u62e9\u6218\u7565\u8f6c\u578b\u3001\u5b9e\u884c\u5168\u7403\u5316\u6269\u5f20\u4e5f\u662f\u65b0\u52a0\u5761\u7535\u4fe1\u516c\u53f8\u8fd9\u4e24\u5e74\u79ef\u6781\u5728\u505a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u7535\u4fe1\u516c\u53f8\u5168\u7403\u5316\u8fdb\u7a0b\u4ee5\u53ca\u5728\u4e9a\u592a\u5730\u533a\u79fb\u8fd0\u901a\u8baf\u8fd0\u8425\u4e1a\u52a1\u4e0a\u7684\u53d1\u5c55\u524d\u666f\u5373\u4f7f\u6709\u4eba\u80fd\u770b\u51fa\u4e9b\u7aef\u502a\u6765\uff0c\u56fd\u9645\u624b\u673a\u5de8\u5934\u4e5f\u4e0d\u4f1a\u5c06\u4e9a\u592a\u5730\u533a\u4e1a\u52a1\u7684\u5e0c\u671b\u8fc7\u591a\u7684\u5bc4\u6258\u5728\u65b0\u52a0\u5761\u7535\u4fe1\u516c\u53f8\u8eab\u4e0a\uff0c\u552f\u6709\u9526\u6e56\u8fd9\u6837\u89c4\u6a21\u7684\u65b0\u5174\u516c\u53f8\u624d\u4f1a\u4e0e\u65b0\u5174\u7684\u8fd0\u8425\u5546\u516c\u53f8\u5408\u4f5c\u8054\u5408\u5f00\u62d3\u65b0\u7684\u5e02\u573a\u3002</p><p>\u3000\u3000\u826f\u597d\u7684\u5408\u4f5c\u5f00\u7aef\uff0c\u4f7f\u5f97\u53cc\u65b9\u90fd\u6709\u610f\u613f\u66f4\u6df1\u5165\u7684\u5f00\u5c55\u5168\u9762\u5408\u4f5c\u5173\u7cfb\uff0c\u65b0\u52a0\u5761\u7535\u4fe1\u5e0c\u671b\u9526\u6e56\u80fd\u63d0\u4f9b\u66f4\u4e30\u5bcc\u591a\u6837\u7684\u3001\u4ef7\u683c\u76f8\u5bf9\u4f4e\u5ec9\u7684\u624b\u673a\uff0c\u5f20\u606a\u4e5f\u5bc4\u5e0c\u671b\u65b0\u52a0\u5761\u7535\u4fe1\u80fd\u6210\u4e3a\u9526\u6e56\u7684\u624b\u673a\u4e1a\u52a1\u6253\u5165\u6fb3\u5927\u5229\u4e9a\u3001\u5370\u5c3c\u3001\u83f2\u5f8b\u5bbe\u3001\u9a6c\u6765\u897f\u4e9a\u3001\u6cf0\u56fd\u3001\u6bd4\u5229\u65f6\u7b49\u5730\u5e02\u573a\u7684\u63a8\u8fdb\u5242\u3002</p><p>\u3000\u3000\u53e6\u5916\uff0c\u7231\u8fbe\u789f\u673a\u4e1a\u52a1\u5168\u9762\u8fdb\u5165\u4e1c\u5357\u4e9a\u5e02\u573a\u5df2\u6709\u4e24\u5e74\u591a\u65f6\u95f4\u4e86\uff0c\u867d\u7136\u53d7\u91d1\u878d\u5371\u673a\u62d6\u7d2f\uff0c\u4e1c\u5357\u4e9a\u5e02\u573a\u4e00\u76f4\u75b2\u8f6f\u65e0\u529b\uff0c\u6c47\u7387\u635f\u5931\u53c8\u6781\u4e3a\u4e25\u91cd\uff0c\u6d77\u5916\u90e8\u95e8\u4e00\u76f4\u575a\u6301\u5bf9\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u7ecf\u9500\u5546\u8fdb\u884c\u5de8\u989d\u8865\u8d34\uff0c\u53c8\u52a0\u5927\u5e02\u573a\u5ba3\u4f20\u7684\u529b\u5ea6\uff0c\u867d\u8bf4\u4e1c\u5357\u4e9a\u5e02\u573a\u5728\u789f\u673a\u4e1a\u52a1\u4e0a\u8d21\u732e\u7684\u5229\u6da6\u5341\u5206\u6709\u9650\uff0c\u4f46\u662f\u8425\u9500\u6e20\u9053\u4e0e\u5e02\u573a\u7684\u5efa\u8bbe\u5df2\u7ecf\u5341\u5206\u5b8c\u5584\uff0c\u9999\u96ea\u6d77\u7684\u5bb6\u7535\u4ea7\u54c1\u4e5f\u662f\u5b8c\u5168\u53ef\u4ee5\u501f\u52a9\u8fd9\u4e2a\u8425\u9500\u6e20\u9053\u8fdb\u5165\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u5c06\u6700\u5927\u9650\u5ea6\u7684\u8282\u51cf\u8425\u9500\u6210\u672c\u3002</p><p>\u3000\u3000\u5750\u5728\u5b66\u5e9c\u5df7\u6cbf\u8857\u7684\u7cbe\u81f4\u4f46\u4e0d\u5962\u534e\u7684\u9910\u5385\u91cc\uff0c\u8c08\u8bba\u4e86\u4e00\u4e9b\u5f00\u62d3\u4e1c\u5357\u4e9a\u5e02\u573a\u7684\u95ee\u9898\uff0c\u4e00\u987f\u9152\u559d\u4e86\u5c06\u8fd1\u4e09\u4e2a\u5c0f\u65f6\uff0c\u624d\u653e\u9648\u4fe1\u751f\u3001\u9676\u884c\u5065\u3001\u8096\u664b\u6210\u7b49\u4eba\u79bb\u5f00\uff0c\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u4e5f\u8d70\u51fa\u9910\u5385\uff0c\u5728\u5b66\u5e9c\u5df7\u4e0a\u6563\u6b65\u3002</p><p>\u3000\u3000\u201c\u660e\u5929\u5c31\u4e0d\u80fd\u966a\u4f60\u53bb\u65b0\u4ead\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u7480\u74a8\u7684\u8def\u706f\u5149\u5c06\u5b66\u5e9c\u5df7\u7167\u5f97\u901a\u660e\u5982\u663c\uff0c\u7fdf\u4e39\u9752\u5fae\u5fae\u4fa7\u8fc7\u5934\uff0c\u51dd\u89c6\u7740\u5f20\u606a\u8eab\u524d\u8eab\u540e\u7ed9\u8def\u706f\u5149\u62d6\u51fa\u6765\u4e24\u9053\u6781\u6de1\u7684\u5f71\u5b50\uff0c\u8ddf\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u5c06\u4e3a\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u63d0\u4f9b\u667a\u529b\u652f\u6301\uff0c\u7fdf\u4e39\u9752\u4e4b\u524d\u51fa\u9762\u7ec4\u5efa\u7684\u72ec\u7acb\u529e\u516c\u5ba4\u5c06\u4e3a\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u63d0\u4f9b\u884c\u653f\u4e8b\u52a1\u4e0a\u7684\u652f\u6301\uff0c\u4e5f\u662f\u5230\u8fd9\u65f6\u5019\u7fdf\u4e39\u9752\u5728\u9526\u6e56\u4f53\u7cfb\u5185\u90e8\u624d\u6709\u6b63\u5f0f\u7684\u804c\u52a1\uff0c\u5c31\u662f\u62c5\u4efb\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u884c\u653f\u90e8\u603b\u88c1\u4e00\u804c\uff0c\u5979\u9664\u4e86\u8d1f\u8d23\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u7684\u884c\u653f\u4e8b\u52a1\u4e4b\u5916\uff0c\u6700\u4e3b\u8981\u7684\u8fd8\u662f\u8d1f\u8d23\u6bcf\u5b63\u5ea6\u5b9a\u671f\u4e3e\u884c\u7684\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u7684\u7b79\u5907\u5de5\u4f5c\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u7e41\u91cd\u7684\u4e8b\u52a1\u5c31\u8981\u5c06\u5979\u7cfb\u5728\u5efa\u90ba\u8fd9\u8fb9\uff0c\u4e0d\u80fd\u8ddf\u5f20\u606a\u5230\u5404\u5730\u95f2\u901b\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u51dd\u89c6\u7740\u7fdf\u4e39\u9752\u5728\u8def\u706f\u5149\u4e0b\u6781\u5177\u8bf1\u60d1\u7684\u7f8e\u7738\uff0c\u9887\u4e3a\u53ef\u60dc\u7684\u8bf4\u9053\uff1a\u201c\u4e0a\u56de\u53bb\u65b0\u4ead\uff0c\u4f60\u5c31\u6ca1\u80fd\u8ddf\u6211\u8fc7\u53bb\u2026\u2026\u201d\u4f38\u624b\u8981\u53bb\u7275\u7fdf\u4e39\u9752\u7684\u624b\u3002</p><p>\u3000\u3000\u201c\u4f60\u5012\u662f\u4e0d\u6015\u522b\u4eba\u770b\u5230\u4f24\u5fc3\uff0c\u201d\u7fdf\u4e39\u9752\u624b\u5f80\u540e\u7f29\u4e86\u7f29\uff0c\u4e0d\u8ba9\u5f20\u606a\u5728\u5b66\u5e9c\u5df7\u91cc\u7275\u5979\u7684\u624b\uff0c\u53c8\u66a7\u6627\u4e0d\u660e\u7684\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u8bf4\u9053\uff0c\u201c\u65b0\u4ead\u5012\u662f\u6709\u4eba\u4e0d\u6015\u8ba9\u4eba\u7cdf\u8e4b\u4e86\uff0c\u4f60\u4e0d\u6015\u6211\u8ddf\u7740\u4f60\u8fc7\u53bb\u788d\u4f60\u7684\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8146\u7740\u8138\u7b11\u4e86\u7b11\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u5acc\u4f60\u788d\u4e8b\u8fc7\uff1f\u4e60\u60ef\u4f60\u5728\u8eab\u8fb9\uff0c\u4f60\u4e0d\u8ddf\u7740\uff0c\u6709\u65f6\u5019\u4f1a\u89c9\u5f97\u6709\u4e9b\u4e0d\u65b9\u4fbf\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u56de\u5934\u770b\u4e86\u770b\uff0c\u770b\u4e0d\u89c1\u6709\u719f\u8bc6\u7684\u4eba\u5728\u8857\u4e0a\uff0c\u8f6c\u56de\u5934\u6765\u4eb2\u6635\u7684\u62cd\u4e86\u62cd\u5f20\u606a\u7684\u8138\u988a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u5c31\u7b97\u4e86\uff0c\u660e\u5929\u5b89\u6392\u4e86\u4e8b\u60c5\uff0c\u603b\u4e0d\u80fd\u4efb\u6027\u7684\u4e22\u4e0b\u4e0d\u7ba1\uff0c\u4e0b\u56de\u966a\u4f60\u53bb\u65b0\u4ead\u2026\u2026\u6211\u957f\u8fd9\u4e48\u5927\uff0c\u8fd8\u6ca1\u6709\u8d64\u817f\u5728\u6d77\u6c34\u91cc\u8d70\u8fc7\u5462\uff0c\u5e0c\u671b\u4f60\u80fd\u966a\u6211\u5230\u6d77\u8fb9\u8d70\u4e00\u8d70\u3002\u201d</p><p>\u3000\u3000\u8d70\u5230\u8def\u53e3\uff0c\u536b\u5170\u8fd9\u65f6\u5019\u521a\u4ece\u6821\u4e1c\u95e8\u8d70\u51fa\u6765\uff0c\u5979\u770b\u89c1\u5f20\u606a\u4ed6\u4eec\uff0c\u96c0\u8dc3\u7684\u8d70\u8fc7\u6765\uff0c\u633d\u8fc7\u7fdf\u4e39\u9752\u7684\u80f3\u818a\uff0c\u95ee\u9053\uff1a\u201c\u4e09\u5929\u7684\u4f1a\u8bae\uff0c\u4eca\u5929\u5c31\u5168\u90e8\u7ed3\u675f\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u7fdf\u4e39\u9752\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u7136\u54ea\u80fd\u8fd9\u4e48\u65e9\u8131\u8eab\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u8155\u8868\uff0c\u89c1\u65f6\u95f4\u8fd8\u65e9\uff0c\u8fd9\u65f6\u5019\u56de\u516c\u5bd3\u4e5f\u4e0d\u80fd\u5c06\u7fdf\u4e39\u9752\u7559\u5728\u8eab\u8fb9\u966a\u81ea\u5df1\uff0c\u4fbf\u60f3\u7740\u53bb\u521b\u57df\u7f51\u5427\u4e0a\u4f1a\u513f\uff0c\u8ddf\u7fdf\u4e39\u9752\u3001\u536b\u5170\u8bf4\u4e86\u4e00\u58f0\uff0c\u53c8\u6298\u4e86\u56de\u6765\u3002</p><p>\u3000\u3000\u4e66\u623f\u91cc\u7684\u90a3\u53f0\u8ba1\u7b97\u673a\uff0c\u51e0\u4e4e\u6bcf\u4e24\u4e09\u4e2a\u6708\u5c31\u8981\u66f4\u65b0\u4e00\u4e0b\u786c\u4ef6\uff0c\u6b64\u65f6\u7684\u5962\u534e\u914d\u7f6e\u52a0\u4e0a\u5149\u7ea4\u4e13\u7ebf\u4e0a\u7f51\uff0c\u5dee\u4e0d\u591a\u4e5f\u80fd\u8ba9\u5f20\u606a\u6ee1\u610f\u4e86\u3002\u4f46\u662f\u4e0d\u4e3a\u4e86\u5de5\u4f5c\u3001\u72ec\u81ea\u4e00\u4eba\u5728\u4e66\u623f\u91cc\u4e0a\u7f51\uff0c\u6bd4\u5728\u7f51\u5427\u91cc\u4e0a\u7f51\u7684\u611f\u89c9\u603b\u662f\u8981\u5dee\u5f88\u591a\u3002</p><p>\u3000\u3000\u51ac\u591c\u7684\u8857\u5934\u8fd8\u6709\u4e9b\u6e05\u5bd2\uff0c\u5f20\u606a\u63a8\u95e8\u8d70\u8fdb\u7f51\u5427\uff0c\u624d\u89c9\u5f97\u6709\u4e9b\u6696\u610f\uff0c\u6413\u7740\u624b\uff0c\u89c1\u5e95\u697c\u5927\u5385\u91cc\u90fd\u5750\u6ee1\u4e86\u4eba\uff0c\u8fd8\u6709\u4eba\u5728\u4f11\u606f\u533a\u7b49\u5f85\u673a\u4f4d\u3002\u6536\u94f6\u53f0\u540e\u9762\u7ad9\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\u90fd\u662f\u964c\u751f\u8138\u5b54\uff0c\u6709\u4e2a\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u7684\u6210\u5458\u5728\u5e95\u697c\u5927\u5385\u91cc\u503c\u73ed\u6307\u5bfc\u4e00\u4e9b\u4e0d\u719f\u6089\u8ba1\u7b97\u673a\u7684\u65b0\u751f\u4e0a\u7f51\uff0c\u4ed6\u770b\u89c1\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u70ed\u7edc\u7684\u8fce\u4e0a\u53bb\uff1a\u201c\u606a\u5c11\u6765\u627e\u675c\u98de\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u6709\u7a7a\u7406\u4ed6\uff1f\u6211\u8fc7\u6765\u4e0a\u7f51\u7684\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u6211\u4e0d\u53bb\u4f60\u4eec\u5de5\u4f5c\u5ba4\uff0c\u4e4c\u70df\u7634\u6c14\u7684\uff0c\u4e8c\u697c\u8fd8\u6709\u6ca1\u6709\u673a\u4f4d\u7a7a\u7740\uff1f\u201d</p><p>\u3000\u3000\u5373\u4f7f\u5230\u4e5d\u516b\u5e74\u5e74\u5e95\u4e86\uff0c\u521b\u57df\u7f51\u5427\u7684\u6536\u8d39\u4f9d\u65e7\u7ef4\u6301\u5728\u53bb\u5e74\u5e74\u4e2d\u65f6\u7684\u9ad8\u4f4d\uff0cVIP\u533a\u6bcf\u53f0\u673a\u4f4d\u6bcf\u5c0f\u65f6\u8981\u6536\u53d6\u5341\u4e8c\u5143\u7684\u4e0a\u7f51\u8d39\u7528\uff0c\u8fd8\u6709\u4e9b\u7a7a\u4f4d\u2014\u2014\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u5df2\u7ecf\u6536\u56de\u5f53\u521d\u4e09\u500d\u591a\u7684\u6295\u8d44\uff0c\u53e6\u5916\u8fde\u9501\u52a0\u76df\u53ca\u7ba1\u7406\u8f6f\u4ef6\u7684\u6536\u5165\u5c31\u8d85\u8fc7\u4e03\u516b\u767e\u4e07\u2014\u2014\u81f3\u5c11\u5728\u71d5\u5f52\u6e56\u533a\u57df\u8fd8\u6ca1\u6709\u4e00\u5bb6\u7f51\u5427\u7684\u73af\u5883\u3001\u8ba1\u7b97\u673a\u914d\u7f6e\u3001\u670d\u52a1\u80fd\u6bd4\u8fd9\u91cc\u66f4\u597d\uff0c\u8981\u8bf4\u5176\u4ed6\u57ce\u533a\u6709\u66f4\u6982\u5ff5\u6027\u7684\u3001\u65f6\u5c1a\u7f51\u5427\u51fa\u73b0\uff0c\u90a3\u4e5f\u662f\u521b\u57df\u7f51\u5427\u5728\u5efa\u90ba\u76f4\u63a5\u7ecf\u8425\u7684\u8fde\u9501\u5206\u5e97\u3002\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u5927\u89c4\u6a21\u7684\u8d44\u672c\u6295\u5165\u65b0\u5174\u7684\u7f51\u5427\u4ea7\u4e1a\uff0c\u675c\u98de\u4ed6\u4eec\u6240\u62a2\u5360\u7684\u5148\u673a\u8fd8\u6ca1\u6709\u9047\u5230\u5f3a\u6709\u529b\u7684\u7ade\u4e89\u8005\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u5230\u4e8c\u697cVIP\u533a\u7684\u4e00\u95f4\u534a\u5f00\u653e\u5f0f\u7684\u9694\u95f4\u91cc\uff0c\u9694\u65ad\u662f\u7528\u9f50\u80f8\u9ad8\u7684\u9694\u677f\u52a0\u4e09\u5341\u516c\u5206\u9ad8\u7684\u94a2\u5316\u73bb\u7483\u505a\u6210\uff0c\u9694\u58c1\u662f\u5973\u751f\u4e13\u533a\uff0c\u597d\u4e9b\u5e08\u5927\u6216\u97f3\u4e50\u5b66\u9662\u7684\u5973\u5b69\u5b50\u90fd\u559c\u6b22\u5230\u521b\u57df\u7f51\u5427\u7684\u5973\u751f\u4e13\u533a\u4e0a\u7f51\uff0c\u5f20\u606a\u770b\u4e86\u51e0\u773c\u90a3\u51e0\u4e2a\u76f8\u8c8c\u6e05\u79c0\u7684\u5973\u5b69\u5b50\uff0c\u624d\u80cc\u9760\u7740\u76ae\u6c99\u53d1\u5f80\u7a97\u5916\u671b\u53bb\u3002</p><p>\u3000\u3000\u7a97\u5916\u7684\u5b66\u5e9c\u5df7\u5185\u5df7\u8def\u706f\u7a0d\u6697\u4e00\u4e9b\uff0c\u6709\u7740\u706f\u706b\u8ff7\u79bb\u4e4b\u611f\uff0c\u79d1\u738b\u9ad8\u79d1\u8bbe\u5728\u5185\u5df7\u4e09\u697c\u7684\u624b\u673a\u8bbe\u8ba1\u5de5\u4f5c\u5ba4\u91cc\u8fd9\u65f6\u5019\u6b63\u706f\u706b\u901a\u660e\u3001\u5ba4\u5185\u4eba\u5f71\u6512\u52a8\u3002\u79d1\u738b\u9ad8\u79d1\u5bf9\u624b\u673a\u8bbe\u8ba1\u5de5\u4f5c\u4e5f\u751a\u4e3a\u91cd\u89c6\uff0c\u6b64\u65f6\u79d1\u738b\u9ad8\u79d1\u7684\u624b\u673a\u8bbe\u8ba1\u5de5\u4f5c\u5ba4\u5df2\u7ecf\u6709\u4e8c\u5341\u51e0\u540d\u5458\u5de5\uff0c\u4ea7\u54c1\u8bbe\u8ba1\u5de5\u4f5c\u4e5f\u4e0d\u4ec5\u4ec5\u505c\u7559\u5728\u8868\u5c42\uff0c\u8fd8\u662f\u8c22\u5b50\u77bb\u8d1f\u8d23\u8fd9\u91cc\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u8ba1\u7b97\u673a\u542f\u52a8\u5b8c\u6bd5\uff0c\u5f20\u606a\u770b\u4e86\u770b\u5c4f\u5e55\u53f3\u4e0b\u89d2\u7684\u65f6\u95f4\uff0c\u90fd\u5feb\u4e5d\u70b9\u949f\u4e86\uff0c\u770b\u60c5\u51b5\u5de5\u4f5c\u5ba4\u91cc\u8fd8\u6709\u597d\u4e9b\u4eba\u5728\u52a0\u73ed\uff0c\u5fc3\u91cc\u60f3\uff1a\u79d1\u738b\u9ad8\u79d1\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u4ea7\u54c1\u95ee\u4e16\uff0c\u8fd9\u91cc\u624d\u4f1a\u8fd9\u4e48\u7684\u5fd9\u788c\uff1f\u60f3\u9526\u6e56\u521a\u7814\u5236\u7b2c\u4e00\u53f0\u789f\u673a\u6210\u529f\u4e4b\u524d\uff0c\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4ed6\u4eec\u90fd\u51e0\u4e4e\u6ca1\u6709\u79bb\u5f00\u8fc7\u5b9e\u9a8c\u5ba4\uff0c\u8bf4\u5b9e\u8bdd\uff0c\u5f20\u606a\u5bf9\u8c22\u5b50\u77bb\u7684\u8868\u73b0\u8fd8\u662f\u5f88\u671f\u5f85\u7684\u3002</p><p>\u3000\u3000\u770b\u5230\u8ba1\u7b97\u673a\u684c\u9762\u4e0a\u6709\u4e2a\u719f\u6089\u7684\u56fe\u6807\uff0c\u5f20\u606a\u70b9\u51fb\u8fdb\u53bb\uff0c\u679c\u7136\u662f\u4e1c\u5927\u6821\u56ed\u804a\u5929\u5ba4\u7684\u754c\u9762\u3002\u524d\u5c18\u5f80\u4e16\u91cc\uff0c\u866b\u4ff1\u4e50\u90e8\u8981\u7b49\u5230\u4e0b\u5b66\u671f\u624d\u4f1a\u5c06\u6821\u56ed\u804a\u5929\u5ba4\u67b6\u6784\u5728BBS\u7684\u7f51\u9875\u4e0a\uff0c\u6ca1\u60f3\u5230\u8fd9\u8f88\u5b50\u63d0\u524d\u51fa\u73b0\u4e86\uff0c\u4e0e\u8bb0\u5fc6\u4e2d\u7684\u754c\u9762\u4e5f\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u533a\u522b\uff0c\u8ba9\u5fc3\u5934\u6d8c\u51fa\u4e00\u4e9b\u7269\u662f\u4eba\u975e\u7684\u611f\u6168\u6765\uff0c\u968f\u624b\u70b9\u5f00\u6ce8\u518c\u754c\u9762\uff0c\u5728\u6ce8\u518c\u7528\u6237\u540d\u91cc\u8f93\u5165\uff1a\u4ef0\u5367\u5728\u6d77\u5e95\u5410\u6ce1\u6ce1\u7684\u9c7c\uff0c\u53c8\u8f93\u5165\u5bc6\u7801\u7b49\u6ce8\u518c\u8d44\u6599\uff0c\u70b9\u51fb\u786e\u8ba4\u952e\u8fc7\u4e86\u6709\u4e09\u56db\u79d2\u949f\uff0c\u754c\u9762\u63d0\u793a\u7528\u6237\u540d\u5df2\u7ecf\u88ab\u6ce8\u518c\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5b89\u9759\u7684\u51dd\u89c6\u7740\u754c\u9762\u4e0a\u7684\u63d0\u793a\u4fe1\u606f\u6709\u51e0\u79d2\u949f\uff0c\uff1a\u8be5\u7528\u6237\u540d\u5df2\u7ecf\u88ab\u6ce8\u518c\uff0c\u5efa\u8bae\u4f7f\u7528\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u6478\u4e86\u6478\u53e3\u888b\uff0c\u6ca1\u6709\u70df\uff0c\u7a81\u7136\u6709\u79cd\u60f3\u5927\u5438\u4e00\u53e3\u70df\u7684\u611f\u89c9\uff0c\u7ad9\u8d77\u6765\uff0c\u60f3\u5728\u4e8c\u697c\u627e\u5230\u4e2a\u6b63\u5728\u62bd\u70df\u53c8\u8ba4\u8bc6\u7684\u4eba\uff0c\u5c0f\u62c7\u6307\u6309\u5728\u7535\u8111\u684c\u53f0\u9762\u4e0a\u4e0d\u610f\u8bc6\u7684\u6709\u4e9b\u8f7b\u98a4\u3002</p><p>\u3000\u3000\u6536\u94f6\u53f0\u540e\u9762\u7684\u4e00\u4e2a\u5973\u5b69\u5b50\u7aef\u8fc7\u5496\u5561\u8d70\u8fc7\u6765\uff0c\u5979\u770b\u89c1\u5f20\u606a\u7684\u8111\u888b\u4ece\u9694\u65ad\u540e\u4f38\u51fa\u6765\uff0c\u8fd8\u4ee5\u4e3a\u662f\u5728\u7b49\u5979\u9001\u6765\u7684\u5496\u5561\uff0c\u751c\u751c\u7684\u4e00\u7b11\uff0c\u5c06\u624b\u91cc\u7684\u5496\u5561\u676f\u7565\u4e3e\u9ad8\u4e9b\uff0c\u793a\u610f\u8fd9\u662f\u7ed9\u4ed6\u7aef\u6765\u7684\u5496\u5561\u3002</p><p>\u3000\u3000\u201c\u6709\u6ca1\u6709\u70df\uff1f\u201d\u5f20\u606a\u9694\u7740\u9694\u65ad\u95ee\u9053\uff0c\u58f0\u97f3\u6709\u4e9b\u5927\uff0c\u4ed6\u81ea\u5df1\u90fd\u6ca1\u6709\u611f\u89c9\u5230\u3002</p><p>\u3000\u3000\u5973\u751f\u4e13\u533a\u4e0a\u7f51\u7684\u51e0\u4e2a\u5973\u5b69\u5b50\u90fd\u8f6c\u8fc7\u5934\u6765\uff0c\u8be7\u5f02\u7684\u770b\u4e86\u5f20\u606a\uff0c\u6709\u4e2a\u957f\u76f8\u6e05\u79c0\u7684\u5973\u5b69\u5b50\u8fd8\u4e3b\u52a8\u671d\u5899\u58c1\u4e0a\u7684\u7981\u70df\u6807\u8bc6\u6307\u4e86\u6307\uff0c\u793a\u610f\u8fd9\u91cc\u662f\u975e\u5438\u70df\u533a\u3002</p><p>\u3000\u3000\u5f20\u606a\u514b\u5236\u7740\u4e0d\u53bb\u770b\u7535\u8111\u5c4f\u5e55\uff0c\u8d70\u51fa\u9694\u95f4\uff0c\u793a\u610f\u5973\u5b69\u5b50\u5c06\u5496\u5561\u653e\u684c\u4e0a\u5c31\u884c\uff0c\u4ed6\u5230\u4e09\u697c\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u770b\u5230\u65bd\u65b0\u98de\u7684\u684c\u4e0a\u653e\u7740\u4e00\u5305\u70df\uff0c\u4ed6\u62ff\u8d77\u70df\u4e0e\u6253\u706b\u673a\u671d\u65bd\u65b0\u98de\u626c\u4e86\u626c\uff0c\u4e5f\u6ca1\u6709\u8bf4\u4ec0\u4e48\u5c31\u4e0b\u4e86\u697c\uff0c\u5230\u540e\u5df7\u627e\u4e86\u4e00\u5904\u5b89\u9759\u7684\u89d2\u843d\uff0c\u8e72\u4e0b\u6765\u80cc\u7740\u98ce\u70b9\u71c3\u4e00\u652f\u70df\u62bd\u8d77\u6765\u3002</p></article>"}}}]);
