(window.webpackJsonp=window.webpackJsonp||[]).push([[963],{986:function(p){p.exports={chapterTitle:null,title:"\u7b2c960\u7ae0 \u5f20\u606a\u7684\u5fc3\u601d",content:"<article><h2>\u7b2c960\u7ae0 \u5f20\u606a\u7684\u5fc3\u601d</h2><p>\u3000\u3000\u201c\u65b9\u4fbf\u53d6\u4e0b\u6765\uff1f\u201d\u5f20\u606a\u6307\u4e86\u6307\u9648\u9759\u60ac\u6302\u5728\u6676\u83b9\u5254\u900f\u7684\u8033\u5782\u4e0a\u9576\u7ea2\u739b\u7459\u77f3\u7684\u8033\u5760\uff0c\u89c1\u5979\u5c06\u6ce2\u6d6a\u5377\u7684\u957f\u53d1\u634b\u5230\u4e00\u4fa7\uff0c\u9732\u51fa\u6676\u83b9\u82e5\u7389\u7684\u8116\u6897\u5b50\uff0c\u5934\u53d1\u8fc7\u4e8e\u4e30\u76db\u4e86\uff0c\u4f3c\u4e4e\u624b\u4e00\u653e\u5c31\u8981\u677e\u5f00\uff0c\u5c31\u4f38\u624b\u53bb\u5e2e\u5979\u5c06\u8033\u5760\u6458\u4e0b\u6765\u3002</p><p>\u3000\u3000\u7ed9\u5f20\u606a\u4f38\u624b\u6458\u8033\u5760\u65f6\uff0c\u8033\u5782\u7ed9\u4ed6\u7684\u624b\u6307\u8f7b\u8f7b\u7684\u78b0\u7740\uff0c\u6709\u4e9b\u75d2\uff0c\u5fc3\u95f4\u4e5f\u8361\u7740\u5947\u602a\u7684\u611f\u89c9\uff0c\u9648\u9759\u5c31\u6015\u8fd9\u65f6\u5019\u8033\u6839\u4f1a\u7ea2\u9732\u4e86\u5fc3\u602f\uff0c\u611f\u89c9\u8033\u5760\u79bb\u8033\u800c\u53bb\uff0c\u62ff\u8d77\u9ad8\u811a\u73bb\u7483\u676f\u6545\u4f5c\u9547\u5b9a\u7684\u62bf\u4e86\u4e00\u53e3\u9152\uff0c\u8fd8\u4e0e\u5b59\u9759\u6aac\u76f8\u89c6\u4e00\u7b11\uff0c\u63a9\u9970\u5fc3\u95f4\u90a3\u5947\u5f02\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u90a3\u679a\u9576\u739b\u7459\u77f3\u7684\u8033\u5760\u653e\u5728\u65f6\u5b66\u658c\u7684\u90a3\u53eai08\u624b\u673a\u4e0a\uff0c\u4e5f\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u53ea\u662f\u671d\u9648\u9759\u3001\u8c22\u5b50\u5609\u671b\u8fc7\u6765\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u707f\u70c2\u7684\u7b11\u5bb9\uff0c\u9648\u9759\u53ea\u89c9\u5f97\u5fc3\u810f\u6709\u4e9b\u6536\u7d27\uff0c\u6709\u79cd\u901a\u4e0d\u8fc7\u6c14\u6765\u7684\u611f\u89c9\uff0c\u53ea\u89c9\u5f97\u8138\u6709\u4e9b\u53d1\u70eb\uff0c\u5012\u6ca1\u6709\u5fc3\u601d\u53bb\u8003\u8651\u5f20\u606a\u8fd9\u4e9b\u4e3e\u52a8\u80cc\u540e\u7684\u542b\u4e49\u3002</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u8c22\u5b50\u5609\u51b0\u96ea\u806a\u660e\uff0c\u5728\u5f20\u606a\u4f38\u624b\u6458\u9648\u9759\u7684\u90a3\u679a\u8033\u5760\u65f6\uff0c\u5c31\u660e\u767d\u5f20\u606a\u8981\u544a\u8bc9\u5979\u4eec\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u660e\u5e74\u6625\u5b63\u80fd\u62ff\u5230\u624b\u673a\u751f\u4ea7\u724c\u7167\uff0c\u5c31\u7b97\u51a0\u4ee5\u79d1\u738b\u7684\u54c1\u724c\u9500\u552e\uff0c\u5728\u56fd\u5185\u624b\u673a\u5e02\u573a\u4f9d\u65e7\u662f\u5f31\u52bf\u54c1\u724c\u3002\u5bf9\u5f31\u52bf\u54c1\u724c\u6765\u8bf4\uff0c\u4ea7\u54c1\u65e0\u7591\u662f\u6700\u6709\u6548\u7684\u7a81\u7834\u53e3\uff0c\u5c31\u50cf\u770b\u5230\u5f71\u789f\u673a\u5bf9\u9526\u6e56\u7684\u5d1b\u8d77\u4f5c\u7528\uff0c\u8c22\u5b50\u5609\u4e5f\u4e00\u76f4\u60f3\u5728\u4ea7\u54c1\u4e0a\u5bfb\u6c42\u7a81\u7834\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u4ee5\u79d1\u738b\u9ad8\u79d1\u6b64\u65f6\u7684\u6280\u672f\u5e95\u5b50\uff0c\u80fd\u5728\u660e\u5e74\u6625\u5b63\u4e4b\u524d\u6210\u529f\u5f00\u53d1\u51fa\u4e00\u6b3e\u6027\u80fd\u3001\u54c1\u8d28\u90fd\u4f4d\u4e8e\u4e2d\u7b49\u5c42\u6b21\u7684\u624b\u673a\u5c31\u8981\u8c22\u5929\u8c22\u5730\u4e86\u3002\u6682\u4e0d\u7528\u53bb\u60f3\u9526\u6e56\u4e0e\u90a3\u4e9b\u6d77\u5916\u624b\u673a\u5de8\u5934\uff0c\u5c31\u662f\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u56fd\u4ea7\u624b\u673a\u5382\u5546\u65d7\u4e0b\u5230\u660e\u5e74\u6625\u5b63\u90fd\u80fd\u65b0\u63a8\u51fa\u4e24\u4e09\u6b3e\u65b0\u673a\u578b\u51fa\u6765\uff0c\u79d1\u738b\u9ad8\u79d1\u53ea\u6709\u4e00\u6b3e\u65b0\u54c1\uff0c\u5982\u4f55\u4e0e\u7ade\u4e89\u8005\u6297\u8861\u3001\u8fc5\u901f\u5728\u56fd\u5185\u5e02\u573a\u53d6\u5f97\u7a81\u7834\uff1f</p><p>\u3000\u3000\u501f\u9274\u5b9d\u77f3\u52a0\u949f\u8868\u7684\u7ecf\u5178\u521b\u9020\u7406\u5ff5\uff0c\u8c22\u5b50\u5609\u5c31\u8bbe\u8ba1\u4e86\u5f00\u53d1\u5b9d\u77f3\u624b\u673a\u7cfb\u5217\u7684\u65b9\u6848\uff0c\u53ea\u9700\u8981\u6210\u529f\u5f00\u53d1\u51fa\u4e00\u6b3e\u65b0\u673a\u578b\uff0c\u5c31\u53ef\u4ee5\u901a\u8fc7\u9576\u5d4c\u7389\u77f3\u7684\u65b9\u5f0f\uff0c\u5206\u6210\u975e\u5b9d\u77f3\u624b\u673a\u3001\u5b9d\u77f3\u624b\u673a\u4e24\u5927\u7c7b\uff0c\u6839\u636e\u5b9d\u77f3\u7684\u54c1\u8d28\u7b49\u7ea7\u4e0d\u540c\uff0c\u5b9d\u77f3\u624b\u673a\u53c8\u53ef\u4ee5\u5206\u51fa\u4e0d\u540c\u7684\u6863\u6b21\u6765\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u4e5f\u5c31\u7b49\u540c\u4e8e\u62e5\u6709\u5c42\u6b21\u4e30\u5bcc\u7684\u4ea7\u54c1\u7cfb\u5217\u3002\u53e6\u5916\uff0c\u4e2d\u56fd\u4eba\u5bf9\u7389\u77f3\u6709\u7740\u6781\u9ad8\u7684\u4ef7\u503c\u8ba4\u540c\u611f\uff0c\u5373\u4f7f\u79d1\u738b\u9ad8\u79d1\u53ea\u80fd\u5f00\u53d1\u4e2d\u4f4e\u7aef\u624b\u673a\u4ea7\u54c1\uff0c\u4e5f\u80fd\u51ed\u501f\u9576\u5d4c\u4e00\u7c92\u7480\u74a8\u593a\u76ee\u7684\u7389\u77f3\u63d0\u5347\u4ea7\u54c1\u7684\u6863\u6b21\u4e0e\u5f62\u8c61\u800c\u76f4\u63a5\u6324\u5165\u4e2d\u9ad8\u7aef\u5e02\u573a\u3002</p><p>\u3000\u3000\u9526\u6e56\u773c\u4e0b\u4e5f\u662f\u51ed\u501f\u6781\u5176\u51fa\u8272\u7684\u4ea7\u54c1\u8bbe\u8ba1\u3001\u6750\u8d28\u9009\u62e9\u4e0e\u54c1\u8d28\u7ba1\u7406\uff0c\u4ee5\u4e8c\u6d41\u6280\u672f\u5236\u9020\u51fa\u4e0d\u900a\u4e00\u6d41\u7684\u624b\u673a\u4ea7\u54c1\u6765\uff0c\u5728\u56fd\u5185\u83b7\u5f97\u4ec5\u6b21\u4e8e\u7231\u7acb\u4fe1\u3001\u6469\u6258\u7f57\u62c9\u4e0e\u8bfa\u57fa\u4e9a\u7684\u5e02\u573a\u5730\u4f4d\uff0c\u8c22\u5b50\u5609\u7684\u65b9\u6848\u53ea\u662f\u5c06\u9526\u6e56\u7684\u8fd9\u4e00\u8425\u9500\u601d\u8def\u53d1\u6325\u5230\u6781\u81f4\u7f62\u4e86\u2014\u2014\u5251\u8d70\u504f\u950b\u7684\u6781\u81f4\u3002</p><p>\u3000\u3000\u5bf9\u9526\u6e56\u6765\u8bf4\uff0c\u51ed\u501f\u5168\u9762\u7a81\u7834\u7684\u52aa\u529b\uff0c\u5df2\u7ecf\u521d\u6b65\u5177\u4f53\u4e86\u5236\u9020\u9ad8\u7aef\u624b\u673a\u4ea7\u54c1\u7684\u80fd\u529b\uff0c\u65d7\u4e0b\u624b\u673a\u4ea7\u54c1\u4ece\u4e2d\u4f4e\u7aef\u5230\u9ad8\u7aef\u5c42\u6b21\u4e30\u5bcc\u800c\u5206\u660e\u3002\u5728\u666e\u901a\u624b\u673a\u4e0a\u5d4c\u7389\u77f3\uff0c\u4e4d\u770b\u4e0a\u53bb\u80fd\u5438\u5f15\u773c\u7403\uff0c\u5b9e\u9645\u4e0a\u6df7\u6dc6\u4e86\u6d88\u8d39\u8005\u5bf9\u9526\u6e56\u624b\u673a\u4ea7\u54c1\u7684\u51c6\u786e\u5b9a\u4f4d\uff0c\u7279\u522b\u662f\u6b64\u7c7b\u4ea7\u54c1\u8bbe\u8ba1\u6781\u6613\u7ed9\u4eba\u6a21\u4eff\u800c\u8fc5\u901f\u6d41\u4e8e\u5eb8\u4fd7\uff0c\u6700\u7ec8\u4f1a\u635f\u5bb3\u9526\u6e56\u5728\u56fd\u5185\u5e02\u573a\u5df2\u7ecf\u5f62\u6210\u4f18\u52bf\u5730\u4f4d\u7684\u54c1\u724c\u4ef7\u503c\u4e0e\u5f62\u8c61\u3002\u5728\u54c1\u724c\u6714\u9020\u7684\u4e0d\u540c\u9636\u6bb5\uff0c\u5bf9\u8425\u9500\u624b\u6bb5\u540c\u7684\u9009\u62e9\u662f\u6709\u6781\u5927\u533a\u522b\u7684\uff0c\u6b64\u65f6\u7684\u9526\u6e56\u80fd\u591f\u505a\u7684\u5927\u6982\u5c31\u662f\u7ed9\u5b9a\u5236\u7c7b\u624b\u673a\u9576\u5d4c\u4ef7\u503c\u8f83\u9ad8\u7684\u94bb\u77f3\u7c7b\u5b9d\u77f3\uff0c\u4e0d\u8fc7\u8fd9\u5df2\u7ecf\u662f\u5962\u534e\u624b\u673a\u7684\u6982\u5ff5\u4e86\u3002</p><p>\u3000\u3000\u770b\u5230\u675c\u98de\u624b\u91cc\u7684\u90a3\u53ea\u624b\u673a\u4e4b\u540e\uff0c\u8c22\u5b50\u5609\u5c31\u660e\u767d\u9526\u6e56\u65e9\u5c31\u6709\u624b\u673a\u52a0\u5b9d\u77f3\u7684\u8bbe\u8ba1\u6982\u5ff5\u4e86\uff0c\u5979\u8bef\u4ee5\u4e3a\u5f20\u606a\u665a\u4e0a\u89c1\u9762\u65f6\u6ca1\u6709\u63d0\u53ca\u8fd9\u70b9\u800c\u4e00\u53e3\u7b54\u5e94\u8981\u652f\u6301\u79d1\u738b\u9ad8\u79d1\u5f00\u53d1\u5b9d\u77f3\u624b\u673a\u662f\u7eaf\u7cb9\u4e3a\u4e86\u8ba8\u597d\u9648\u9759\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba8\u597d\u9648\u9759\u4e5f\u65e0\u6240\u8c13\uff0c\u4f46\u662f\u60f3\u5230\u4ee5\u540e\u5373\u4f7f\u5f00\u53d1\u51fa\u5b9d\u77f3\u624b\u673a\u83b7\u5f97\u6210\u529f\u4e5f\u6709\u4e00\u79cd\u7ed9\u9526\u6e56\u6545\u610f\u653e\u6c34\u7684\u611f\u89c9\uff0c\u5fc3\u9ad8\u6c14\u50b2\u7684\u8c22\u5b50\u5609\u5fc3\u91cc\u81ea\u7136\u5c31\u5f88\u6124\u6168\uff0c\u4e00\u65f6\u95f4\u6ca1\u6709\u8003\u8651\u5230\u5f20\u606a\u5bf9\u54c1\u724c\u4e0e\u8425\u9500\u6709\u7740\u66f4\u6df1\u5c42\u6b21\u7684\u601d\u8003\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u7a81\u7136\u89c9\u5f97\u521a\u624d\u7684\u6124\u6124\u4e0d\u5e73\u6709\u4e9b\u65e0\u8c13\u4e86\uff0c\u4efb\u5979\u795e\u7ecf\u575a\u97e7\u5f97\u8ddf\u94a2\u4e1d\u7ef3\u4e00\u6837\uff0c\u90fd\u4f1a\u89c9\u5f97\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u6487\u8fc7\u8138\u53bb\uff0c\u9887\u4e3a\u4e0d\u5c51\u7684\u8bf4\u9053\uff1a\u201c\u771f\u662f\u7684\uff0c\u6709\u4e9b\u597d\u60f3\u6cd5\uff0c\u4e8e\u9526\u6e56\u53c8\u65e0\u76ca\uff0c\u4e5f\u53ef\u4ee5\u65e9\u4e9b\u63d0\u9192\u4e00\u4e0b\u67d0\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4e0d\u4e0e\u8c22\u5b50\u5609\u4e89\u4ec0\u4e48\u53e3\u820c\u4e4b\u4fbf\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9648\u9759\u8fd8\u6c89\u6d78\u5728\u8033\u5782\u7ed9\u5f20\u606a\u624b\u6307\u89e6\u78b0\u7684\u611f\u89c9\u4e4b\u4e2d\uff0c\u8111\u5b50\u4e25\u91cd\u77ed\u8def\uff0c\u8c22\u5b50\u5609\u5b8c\u5168\u660e\u767d\u8fc7\u6765\uff0c\u5979\u8fd8\u5728\u72af\u8ff7\u7cca\uff0c\u89c1\u8c22\u5b50\u5609\u4e00\u526f\u660e\u4e86\u4e8e\u5fc3\u7684\u6837\u5b50\uff0c\u5979\u66f4\u4e0d\u597d\u610f\u601d\u5f00\u53e3\u95ee\u5f20\u606a\u8fd9\u4e48\u505a\u7684\u7528\u610f\uff0c\u53ea\u662f\u95ee\u5f20\u606a\uff1a\u201c\u8033\u5760\u5b50\u80fd\u8fd8\u7ed9\u6211\u4e86\u5427\uff1f\u201d\u5374\u662f\u4e0d\u6562\u8ba9\u5f20\u606a\u5e2e\u5979\u6234\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4eca\u5929\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\uff0c\u559d\u5b8c\u676f\u4e2d\u9152\u5c31\u5148\u56de\u53bb\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u4e0e\u8c22\u5b50\u5609\u53c8\u5750\u4e86\u4e00\u4f1a\u513f\u624d\u79bb\u5f00\uff0c\u5341\u4e8c\u680b\u9752\u5e74\u516c\u5bd3\u697c\u5206\u6210\u4e09\u4e2a\u72ec\u7acb\u7684\u793e\u533a\uff0c\u8c22\u5b50\u5609\u4f4f\u6700\u5317\u9762\u7684\u4e00\u4e2a\u5c0f\u533a\uff0c\u51fa\u4e86\u9152\u5df4\u5f80\u5317\u8d70\uff0c\u9648\u9759\u7ed9\u51b7\u98ce\u4e00\u5439\uff0c\u5934\u8111\u6e05\u695a\u8fc7\u6765\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u8f7b\u6342\u7740\u7ea2\u5507\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6211\u8bf4\u4ed6\u5728\u6253\u4ec0\u4e48\u54d1\u8ff7\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u624d\u60f3\u660e\u767d\u8fc7\u6765\uff1f\u201d\u8c22\u5b50\u5609\u7591\u60d1\u7684\u770b\u4e86\u9648\u9759\u4e00\u773c\uff0c\u53c8\u5a07\u7b11\u7740\u6402\u62b1\u8fc7\u9648\u9759\u7684\u8eab\u5b50\uff0c\u201c\u7ed9\u4eba\u5bb6\u6478\u4e00\u4e0b\u5b50\u8033\u6735\uff0c\u9b42\u90fd\u4e22\u4e86\u534a\u7247\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u624d\u9b42\u4e22\u4e86\u534a\u7247\u5462\uff0c\u4e00\u8d70\u8fc7\u53bb\u5c31\u6c14\u52bf\u6c79\u6c79\u7684\u545b\u4eba\u5bb6\uff0c\u540e\u6765\u600e\u4e48\u5c31\u6ca1\u58f0\u6ca1\u606f\u4e86\uff1f\u201d\u9648\u9759\u63a9\u9970\u7684\u8ba5\u8bbd\u4e86\u5b50\u5609\u4e00\u53e5\uff0c\u201c\u8981\u77e5\u9053\u5996\u5b7d\u4e00\u6837\u7684\u8111\u888b\u74dc\u5b50\u53ef\u4e0d\u662f\u53ea\u4f60\u6709\u2026\u2026\u4eca\u5929\u662f\u4e0d\u662f\u53d7\u6559\u80b2\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u54fc\uff0c\u201d\u8c22\u5b50\u5609\u4e0d\u5c51\u7684\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u4ed6\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\uff0c\u8fd8\u4e0d\u662f\u4e00\u4e2a\u52b2\u7684\u60f3\u505a\u5c0f\u9759\u4f60\u7684\u88d9\u4e0b\u4e4b\u81e3\uff1f\u201d\u53c8\u4e00\u672c\u6b63\u7ecf\u7684\u770b\u7740\u9648\u9759\uff0c\u201c\u4f60\u53ef\u4ee5\u575a\u6301\u4f4f\u554a\uff0c\u53ef\u4e0d\u8981\u53d1\u6d6a\u4e3b\u52a8\u53bb\u5012\u8d34\u7ed9\u4ed6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e2a\u5c0f\u6d6a\u8e44\u5b50\uff0c\u8fd8\u6709\u8138\u8bf4\u522b\u4eba\uff1f\u62dc\u6258\u4f60\u4e0d\u8981\u5c06\u6536\u5230\u7684\u60c5\u4e66\u968f\u4fbf\u4e22\u5783\u573e\u7b52\u91cc\u3002\u201d\u9648\u9759\u53cd\u624b\u53bb\u6390\u8c22\u5b50\u5609\u7684\u8138\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e9b\u662f\u60c5\u4e66\u5417\uff1f\u201d\u8c22\u5b50\u5609\u7591\u60d1\u4e0d\u89e3\u7684\u95ee\u9053\uff0c\u201c\u6211\u4e5f\u6ca1\u6709\u770b\uff0c\u5c31\u89c9\u5f97\u90a3\u4e9b\u82b1\u63d2\u5ba2\u5385\u91cc\u4e5f\u4e0d\u9519\uff0c\u5c31\u6536\u4e0b\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u540e\u9762\u7684\u516c\u5bd3\u697c\u4e0d\u50cf\u4e2d\u95f4\u7684\u56db\u680b\uff0c\u8c22\u5b50\u5609\u4f4f\u7684\u8fd9\u4e00\u680b\u516c\u5bd3\u697c\u4f4f\u6ee1\u4e86\u5728\u521b\u4e1a\u56ed\u91cc\u5de5\u4f5c\u7684\u7537\u5973\u9752\u5e74\uff0c\u8c22\u5b50\u5609\u8eab\u6750\u7ea4\u7ec6\u504f\u7626\uff0c\u8138\u86cb\u6e05\u4e3d\uff0c\u8fd8\u50cf\u4e2a\u5c0f\u5973\u5b69\uff0c\u9648\u9759\u5219\u660e\u8273\u7167\u4eba\u3001\u4e0d\u53ef\u65b9\u7269\uff0c\u4e24\u4eba\u7b11\u95f9\u7740\uff0c\u81ea\u7136\u662f\u683c\u5916\u7684\u5f15\u4eba\u77a9\u76ee\u3002</p><p>\u3000\u3000\u6070\u5de7\u4e01\u69d0\u6b21\u65e5\u4e5f\u6ca1\u6709\u79bb\u5f00\u5efa\u90ba\uff0c\u9648\u9759\u76f4\u63a5\u8054\u7cfb\u4e0a\u4e01\u69d0\u8c08\u624b\u673a\u5f00\u53d1\u6280\u672f\u5408\u4f5c\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u5728\u53bb\u91d1\u5c71\u4e4b\u524d\uff0c\u4e0e\u4e01\u69d0\u901a\u8fc7\u7535\u8bdd\u8bf4\u53ca\u6b64\u65f6\u3002\u9526\u6e56\u5728\u5efa\u90ba\u4e5f\u8bbe\u6709\u4ea7\u54c1\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u4e2d\u5fc3\uff0c\u9664\u4e86\u4ea7\u54c1\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u5c42\u6b21\u7684\u6280\u672f\u5408\u4f5c\uff0c\u66f4\u6df1\u5c42\u6b21\u7684\u6280\u672f\u5408\u4f5c\u4e5f\u53ef\u4ee5\u7531\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u6765\u627f\u62c5\u3002</p><p>\u3000\u3000\u4ece\u4e01\u69d0\u90a3\u91cc\u77e5\u9053\u5f20\u606a\u5df2\u7ecf\u79bb\u5f00\u5efa\u90ba\uff0c\u9648\u9759\u9887\u611f\u53ef\u60dc\uff0c\u53ea\u662f\u6ca1\u6709\u5c06\u8fd9\u5c42\u5fc3\u601d\u8868\u73b0\u51fa\u6765\uff0c\u5408\u4f5c\u7684\u4e8b\u60c5\u5f88\u5feb\u5c31\u7ea6\u5b9a\u4e86\u4e8b\u9879\uff0c\u4e0b\u5348\uff0c\u9648\u9759\u8fd8\u7279\u5730\u53bb\u9526\u6e56\u5728\u5efa\u90ba\u7684\u4ea7\u54c1\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u90e8\u95e8\u53c2\u89c2\uff0c\u5012\u662f\u6ca1\u6709\u673a\u4f1a\u53bb\u9526\u6e56\u7684\u771f\u6b63\u6280\u672f\u4f53\u7cfb\u6838\u5fc3\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u53bb\u53c2\u89c2\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u6280\u672f\u4e0a\u7684\u7814\u53d1\u6295\u5165\u662f\u5168\u9762\u7684\u3001\u6df1\u5c42\u6b21\u7684\uff0c\u8fd9\u4e5f\u4f7f\u5f97\u9526\u6e56\u4ea7\u54c1\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u90e8\u95e8\u7684\u4ea7\u54c1\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u80fd\u529b\u6709\u7740\u4e00\u822c\u624b\u673a\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u516c\u53f8\u65e0\u6cd5\u4f01\u53ca\u7684\u5e7f\u5ea6\u4e0e\u6df1\u5ea6\uff0c\u9526\u6e56\u4ea7\u54c1\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u90e8\u95e8\u5df2\u7ecf\u5c06\u65b0\u6b3e\u624b\u673a\u7684\u5f00\u53d1\u5468\u671f\u538b\u7f29\u5230\u4e24\u4e2a\u6708\u5de6\u53f3\u3002</p><p>\u3000\u3000\u8054\u5408\u5f00\u53d1\uff0c\u5176\u5b9e\u4e5f\u662f\u5728\u6280\u672f\u4e0a\u52a9\u5176\u4e00\u81c2\u4e4b\u529b\uff0c\u8fd8\u80fd\u5e2e\u52a9\u79d1\u738b\u9ad8\u79d1\u5c06\u4ea7\u54c1\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u961f\u4f0d\u953b\u70bc\u8d77\u6765\uff1b\u5f53\u7136\uff0c\u9526\u6e56\u4e5f\u662f\u6709\u597d\u5904\u7684\uff0c\u81f3\u5c11\u534f\u52a9\u79d1\u738b\u9ad8\u79d1\u5f00\u53d1\u51fa\u6765\u7684\u65b0\u624b\u673a\u5236\u9020\u5728\u8f6f\u786c\u4ef6\u4e0a\u662f\u4f1a\u6700\u5927\u9650\u5ea6\u7684\u4f9d\u8d56\u9526\u6e56\u7684\u4f9b\u5e94\u3002</p><p>\u3000\u3000\u4e0d\u4ec5\u5bf9\u79d1\u738b\u9ad8\u79d1\uff0c\u9526\u6e56\u5bf9\u5176\u4ed6\u76f8\u5173\u4f01\u4e1a\u7684\u652f\u6301\u90fd\u4e0d\u541d\u556c\uff0c\u9526\u6e56\u4e5f\u5f88\u96be\u6c38\u8fdc\u90fd\u4e00\u679d\u72ec\u79c0\uff0c\u505a\u5f3a\u8fd8\u9700\u8981\u6709\u4e00\u4e2a\u575a\u5b9a\u800c\u96c4\u539a\u7684\u4ea7\u4e1a\u57fa\u7840\uff0c\u518d\u8bf4\u9526\u6e56\u8fd8\u8981\u505a\u6280\u672f\u65b9\u6848\u7684\u4f9b\u5e94\u5546\uff0c\u9526\u6e56\u751a\u81f3\u6709\u610f\u8bc6\u7684\u8ba9\u4e00\u4e9b\u4f18\u79c0\u4eba\u624d\u6d41\u52a8\u5230\u5468\u8fb9\u4f01\u4e1a\u2014\u2014\u81f3\u5c11\u4e0d\u4f1a\u4e25\u5389\u7684\u63a7\u5236\u4eba\u624d\u7684\u6d41\u52a8\uff0c\u50cf\u79d1\u738b\u9ad8\u738b\u5728\u6d77\u5dde\u7684\u5de5\u5382\u91cc\u5c31\u6709\u8bb8\u591a\u7ba1\u7406\u4eba\u5458\u90fd\u6709\u8fc7\u66fe\u5728\u9526\u6e56\u5de5\u4f5c\u8fc7\u7684\u5c65\u5386\uff0c\u5bf9\u5f53\u521d\u79bb\u804c\u6027\u8d28\u76f8\u5bf9\u6076\u52a3\u7684\u5218\u660e\u8f89\uff0c\u9526\u6e56\u4e5f\u662f\u62b1\u4ee5\u6781\u4e3a\u5bbd\u5bb9\u7684\u6001\u5ea6\u3002</p><p>\u3000\u3000\u867d\u7136\u6709\u7740\u63fd\u5c3d\u5929\u4e0b\u82f1\u624d\u7684\u5b8f\u613f\uff0c\u4f46\u662f\u9526\u6e56\u4f53\u7cfb\u91cc\u805a\u96c6\u4e86\u592a\u591a\u7684\u4f18\u79c0\u4eba\u624d\u53cd\u800c\u4f1a\u6709\u4e00\u79cd\u7279\u522b\u7684\u538b\u529b\uff0c\u5373\u4f7f\u666e\u904d\u7684\u5b9e\u884c\u9ad8\u85aa\u9ad8\u798f\u5229\uff0c\u4e5f\u4e0d\u662f\u6bcf\u4e2a\u4eba\u90fd\u613f\u610f\u627f\u53d7\u8fd9\u79cd\u538b\u529b\u7684\uff0c\u4e5f\u4e0d\u662f\u6bcf\u4e2a\u4eba\u90fd\u8ba4\u4e3a\u7559\u5728\u9526\u6e56\u66f4\u80fd\u4f53\u73b0\u4ed6\u7684\u4e2a\u4eba\u4ef7\u503c\u3002\u867d\u7136\u6709\u4e9b\u4f01\u4e1a\u65e0\u6cd5\u5b9e\u73b0\u666e\u904d\u7684\u9ad8\u85aa\u3001\u9ad8\u798f\u5229\uff0c\u4f46\u662f\u7ed9\u67d0\u4e9b\u6280\u672f\u3001\u7ba1\u7406\u9aa8\u5e72\u9ad8\u85aa\u9ad8\u798f\u5229\u751a\u81f3\u9ad8\u5730\u4f4d\u8fd8\u662f\u80fd\u8f7b\u800c\u6613\u4e3e\u7684\u3002\u4e00\u4e9b\u4f18\u79c0\u4eba\u624d\u4ece\u9526\u6e56\u5f80\u8fd9\u4e9b\u4f01\u4e1a\u6d41\u52a8\uff0c\u90a3\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u4e86\uff0c\u751a\u81f3\u6709\u4eba\u5c06\u5728\u9526\u6e56\u5de5\u4f5c\u5f53\u6210\u9540\u91d1\u4e4b\u65c5\u2014\u2014\u5148\u8f9e\u804c\u8fdb\u5165\u9526\u6e56\u5de5\u4f5c\uff0c\u518d\u79bb\u5f00\u9526\u6e56\u8fd4\u56de\u539f\u5355\u4f4d\u5de5\u4f5c\uff0c\u6216\u8bb8\u53ea\u662f\u77ed\u5230\u4e00\u5e74\u534a\u8f7d\u7684\u65f6\u95f4\uff0c\u85aa\u6c34\u4e5f\u80fd\u6709\u5f88\u5927\u5e45\u5ea6\u7684\u63d0\u5347\uff0c\u4f3c\u4e4e\u80fd\u8fdb\u9526\u6e56\u5de5\u4f5c\uff0c\u80fd\u529b\u5c31\u5f97\u5230\u4e86\u6781\u5927\u7684\u80af\u5b9a\u3002\u5f53\u7136\u540c\u65f6\u6709\u66f4\u591a\u7684\u4eba\u5f80\u9526\u6e56\u6d8c\uff0c\u50cf\u4eca\u5e74\u9526\u6e56\u5404\u5927\u6210\u5458\u4f01\u4e1a\u5c31\u6ee1\u4e16\u754c\u7684\u62db\u8058\u6570\u4ee5\u5343\u8ba1\u7684\u9ad8\u6821\u6bd5\u4e1a\u751f\uff0c\u8fd9\u662f\u9488\u5bf9\u5e94\u5c4a\u6bd5\u4e1a\u751f\u7684\u62db\u8058\uff0c\u5e73\u65e5\u91cc\u7684\u62db\u8058\u5de5\u4f5c\u6bcf\u5468\u90fd\u6ca1\u6709\u505c\u987f\u8fc7\u53bb\u2014\u2014\u91cd\u8981\u662f\u57f9\u517b\u4f18\u79c0\u4eba\u624d\u7684\u673a\u5236\u4ee5\u53ca\u5c06\u4eba\u5458\u7684\u6d41\u52a8\u63a7\u5236\u5728\u53ef\u4ee5\u63a5\u53d7\u7684\u8303\u56f4\u4e4b\u5185\uff0c\u4e00\u70b9\u90fd\u4e0d\u6d41\u52a8\uff0c\u90a3\u4e5f\u662f\u4e00\u6f6d\u6b7b\u6c34\u3001\u66ae\u6c14\u6c89\u6c89\u3002</p></article>"}}}]);
