(window.webpackJsonp=window.webpackJsonp||[]).push([[964],{987:function(p){p.exports={chapterTitle:null,title:"\u7b2c961\u7ae0 \u91d1\u5c71\u4e4b\u884c",content:"<article><h2>\u7b2c961\u7ae0 \u91d1\u5c71\u4e4b\u884c</h2><p>\u3000\u3000\u5341\u4e8c\u6708\u4e2d\u65ec\u7684\u91d1\u5c71\u57ce\u90ca\uff0c\u867d\u7136\u6b63\u503c\u666f\u6c14\u8427\u745f\u7684\u5bd2\u51ac\u5b63\u8282\uff0c\u5374\u5df2\u7ecf\u6ca1\u6709\u6d2a\u6c34\u521d\u9000\u65f6\u7684\u6b8b\u7834\u4e0e\u72fc\u7c4d\uff0c\u88ab\u6e56\u6c34\u5012\u704c\u53d7\u521b\u6700\u4e25\u91cd\u7684\u91d1\u5c71\u65b0\u57ce\u4e5f\u6062\u590d\u4e86\u5efa\u8bbe\uff0c\u900f\u8fc7\u65b0\u690d\u7684\u9053\u8def\u7eff\u5316\u5e26\u5076\u5c14\u4e5f\u80fd\u770b\u5230\u91cc\u4fa7\u6709\u4e9b\u5012\u4f0f\u7740\u751f\u547d\u987d\u5f3a\u7684\u6811\u6728\uff0c\u707e\u540e\u7684\u4f24\u75d5\u6b63\u5728\u8fc5\u901f\u7684\u6108\u5408\u3002</p><p>\u3000\u3000\u9664\u53bb\u5404\u754c\u6350\u8d60\u53ca\u4e2d\u592e\u653f\u5e9c\u7684\u6551\u6d4e\u62d4\u6b3e\u5916\uff0c\u6c5f\u5357\u7701\u653f\u5e9c\u4e5d\u6708\u4e2d\u65ec\u4ece\u603b\u90e8\u8bbe\u5728\u65b0\u52a0\u5761\u7684\u534e\u5546\u94f6\u884c\u83b7\u5f972\u4ebf\u7f8e\u5143\u7d27\u6025\u6551\u6d4e\u8d37\u6b3e\u5728\u707e\u540e\u91cd\u5efa\u5de5\u4f5c\u4e2d\u53d1\u6325\u4e86\u5173\u952e\u6027\u7684\u4f5c\u7528\u2014\u2014\u4e3a\u4e86\u83b7\u5f97\u8fd9\u7b14\u6551\u707e\u8d37\u6b3e\uff0c\u5f20\u606a\u4e0e\u8c22\u665a\u6674\u8054\u5408\u5411\u534e\u5546\u94f6\u884c\u5b58\u51652\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\u6765\u51b2\u9500\u534e\u5546\u94f6\u884c\u7684\u8d44\u91d1\u5dee\u3002</p><p>\u3000\u3000\u5728\u53bb\u65b0\u7b51\u7684\u6e56\u5824\u53c2\u89c2\u4e4b\u524d\u8f66\u961f\u8981\u5148\u7ecf\u8fc7\u7535\u5b50\u5de5\u4e1a\u56ed\uff0c\u7ecf\u8fc7\u65f6\uff0c\u5f20\u606a\u7279\u522b\u8ba9\u8f66\u5b50\u5f00\u6162\u4e00\u4e9b\uff0c\u900f\u8fc7\u7a97\u6237\u4ece\u5916\u9762\u770b\u4e00\u770b\u7535\u5b50\u5de5\u4e1a\u56ed\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u4e4b\u524d\u79d1\u738b\u4e0e\u5609\u4fe1\u7535\u5b50\u8054\u5408\u6295\u8d44\u7684\u7535\u5b50\u5de5\u4e1a\u56ed\u6b64\u65f6\u5df2\u7ecf\u5b8c\u5168\u6210\u4e3a\u65b0\u79d1\u738b\u5728\u91d1\u5c71\u7684\u751f\u4ea7\u57fa\u5730\u3002\u63a5\u624bNEC\u7535\u5b50\u6dd8\u6c70\u4e0b\u6765\u7684\u751f\u4ea7\u7ebf\uff0c\u8fd9\u91cc\u7684\u534e\u590f\u3001\u79d1\u738b\u3001NEC\u5408\u8d44\u663e\u50cf\u7ba1\u5de5\u5382\u5df2\u7ecf\u6295\u5165\u8bd5\u8fd0\u8425\u4e86\uff0c\u901f\u5ea6\u5f88\u5feb\uff0c\u9664\u4e86\u6700\u521d\u7ea6\u5b9a\u7684\u5f69\u7535\u9879\u76ee\u5916\uff0c\u79d1\u738b\u8fd8\u5c06\u51ed\u501fNEC\u7684\u6280\u672f\u8fdb\u5165\u663e\u793a\u5668\u5e02\u573a\u2014\u2014\u56fd\u5185\u80fd\u751f\u4ea7CRT\u663e\u793a\u5668\u7684\u5382\u5546\u5012\u6ca1\u6709\u51e0\u5bb6\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u534e\u590f\u3001\u79d1\u738b\u3001NEC\u8fd8\u5408\u8d44\u6210\u7acb\u4e86\u79d1\u534e\u7269\u8d38\u6709\u9650\u516c\u53f8\uff0c\u636e\u5f20\u606a\u4e86\u89e3\u5230\u7684\u6d88\u606f\uff0c\u79d1\u534e\u7269\u8d38\u7684\u9ad8\u7ea7\u7ba1\u7406\u4eba\u5458\u4e3b\u8981\u7531\u65e5\u672c\u65b9\u6d3e\u51fa\u7684\u804c\u5458\u62c5\u4efb\uff0c\u4e5f\u5c31\u5c06\u7275\u7275\u7684\u63a7\u5236\u4f4f\u534e\u590f\u3001\u79d1\u738b\u3001NEC\u6240\u6709\u5408\u8d44\u516c\u53f8\u7684\u56fd\u9645\u8d38\u6613\u4e1a\u52a1\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5408\u8d44\u516c\u53f8\u7684\u6d77\u5916\u8d38\u6613\u6743\u5f7b\u5e95\u843d\u5165\u80cc\u540e\u7684\u4e09\u4e95\u8d22\u56e2\u624b\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u7535\u5b50\u5de5\u4e1a\u56ed\u5927\u95e8\u4e0a\u5d4c\u6302\u7740\u7684\u65b9\u5f0f\u91d1\u5c5e\u94ed\u724c\uff0c\u6b64\u65f6\u53ea\u6302\u7740\u201c\u79d1\u738b\uff08\u5efa\u90ba\uff09\u663e\u793a\u7535\u5b50\u6709\u9650\u516c\u53f8\u201d\u4e0e\u201c\u79d1\u534e\uff08\u5efa\u90ba\uff09\u7269\u8d38\u6709\u9650\u516c\u53f8\u201d\u4e24\u5757\u724c\u5b50\uff0c\u5fc3\u60f3\u5c31\u8fd9\u4e48\u8f7b\u677e\u7684\u8ba9\u4e09\u4e95\u8f7b\u677e\u5728\u4e2d\u56fd\u5b8c\u6210\u5bf9\u663e\u793a\u7535\u5b50\u5e02\u573a\u4ece\u7269\u6d41\u8d38\u6613\u5230\u5236\u9020\u9500\u552e\u7684\u6574\u4f53\u5e03\u5c40\u2014\u2014\u65b0\u6210\u7acb\u7684\u5408\u8d44\u516c\u53f8\u5728\u6280\u672f\u3001\u91c7\u8d2d\u7b49\u73af\u8282\u6446\u8131\u4e0d\u4e86\u88ab\u63a7\u5236\u7684\u5c40\u9762\uff0c\u5145\u5176\u91cf\u4e5f\u53ea\u662f\u4e09\u4e95\u5728\u4e2d\u56fd\u5e02\u573a\u5438\u98df\u5229\u6da6\u7684\u6e20\u9053\u7f62\u4e86\u3002</p><p>\u3000\u3000\u5728\u5916\u754c\u770b\u6765\uff0c\u4e0e\u5168\u7403\u91cd\u91cf\u7ea7\u7535\u5b50\u4f01\u4e1aNEC\u7684\u5408\u4f5c\u8ba9\u79d1\u738b\u5728\u56fd\u5185\u7684\u540d\u58f0\u66f4\u52a0\u7684\u8000\u773c\uff0c\u4e5f\u66f4\u52a0\u7684\u5f97\u5230\u91cd\u89c6\uff0c\u4e5f\u5438\u5f15\u4e86\u4e0d\u5c11\u516c\u4f17\u7684\u6ce8\u610f\u529b\uff0c\u4f7f\u5f97\u79d1\u738b\u7684\u789f\u673a\u4e1a\u52a1\u5728\u56fd\u5185\u5e02\u573a\u6709\u5c0f\u5e45\u7684\u63d0\u5347\u3002\u79d1\u738b\u4e4b\u524d\u6536\u8d2d\u6d77\u5dde\u4e09\u5143\u7535\u89c6\u673a\u5382\uff0c\u4f46\u662f\u5176\u7535\u89c6\u673a\u4e1a\u52a1\u5728\u56fd\u5185\u5e02\u573a\u8fd8\u6ca1\u6709\u591a\u5c11\u5730\u4f4d\uff0c\u5374\u662f\u5165\u79cb\u4e4b\u540e\uff0c\u4e1a\u7ee9\u5f97\u5230\u5feb\u901f\u7684\u63d0\u5347\uff0c\u5e02\u573a\u5360\u6709\u7387\u6324\u5230\u524d\u5341\u7684\u4f4d\u5b50\uff0c\u4e5f\u7b97\u662f\u4e0d\u5c11\u7684\u6210\u5c31\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7684\u79d1\u738b\u770b\u4e0a\u53bb\u7ed9\u4eba\u6709\u4e00\u79cd\u8131\u80ce\u6362\u9aa8\u7684\u9519\u89c9\uff0c\u5f20\u606a\u4e0d\u6653\u5f97\u8c22\u5251\u5357\u4f1a\u6709\u4ec0\u4e48\u611f\u89c9\uff1a\u662f\u610f\u6c14\u98de\u626c\u89c9\u5f97\u80fd\u518d\u91cd\u632f\u8f89\u714c\uff0c\u8fd8\u662f\u5728\u62c5\u5fe7\u79d1\u738b\u7684\u63a7\u5236\u6743\u4f1a\u6e10\u6e10\u6d41\u843d\u5230\u4e09\u4e95\u7684\u624b\u91cc\uff1f\u4e0d\u8fc7\u8c22\u5bb6\u4e4b\u524d\u7684\u6218\u7565\u5408\u4f5c\u4f19\u4f34\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u6b64\u65f6\u5c31\u8981\u51c4\u51c9\u5f97\u591a\u4e86\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u5341\u4e8c\u6708\u4e0a\u65ec\u5411\u9999\u6e2f\u8bc1\u76d1\u90e8\u95e8\u4ea4\u7f34\u516d\u4ebf\u6e2f\u5143\u7684\u7f5a\u6b3e\u6765\u7ec8\u6b62\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u8c03\u67e5\uff0c\u9664\u4e86\u4e4b\u6b64\uff0c\u845b\u6c0f\u7236\u5b50\u8fd8\u8981\u5411\u4e0a\u5e02\u516c\u53f8\u5609\u4fe1\u7535\u5b50\u8d54\u507f\u4e09\u4ebf\u6e2f\u5143\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u7b79\u96c6\u8fd9\u4e5d\u4ebf\u6e2f\u5143\u7684\u8d54\u7f5a\u5de8\u8d44\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u4ece\u5341\u4e00\u6708\u4e0b\u65ec\u5c31\u5f00\u59cb\u4f4e\u4ef7\u629b\u552e\u540d\u4e0b\u5305\u62ec\u80a1\u7968\u3001\u623f\u4ea7\u3001\u7269\u4e1a\u3001\u503a\u5238\u5728\u5185\u7684\u8d44\u4ea7\u2014\u2014\u845b\u5bb6\u7b97\u662f\u5f7b\u5e95\u4e27\u5931\u4e86\u5f53\u5e74\u7684\u98ce\u5149\u3002</p><p>\u3000\u3000\u5609\u4fe1\u7535\u5b50\u5373\u4f7f\u83b7\u5f97\u4e09\u4ebf\u6e2f\u5143\u8d54\u507f\uff0c\u4f46\u662f\u9893\u7136\u96be\u6539\uff0c\u4e3b\u8981\u662f\u5546\u4e1a\u4fe1\u7528\u7684\u5d29\u574f\u4e0e\u7ade\u4e89\u8005\u7684\u5d1b\u8d77\u3001\u538b\u5236\uff0c\u5609\u4fe1\u7535\u5b50\u5728\u5e7f\u4e1c\u7684\u5de5\u5382\u5df2\u7ecf\u9677\u5165\u7ecf\u8425\u56f0\u96be\u7684\u5883\u5730\uff0c\u5bf9\u4e8e\u62e5\u6709\u56db\u4e07\u540d\u5458\u5de5\u7684\u4ee3\u5de5\u57fa\u5730\uff0c\u83b7\u8d54\u4e09\u4ebf\u6e2f\u5143\u53ea\u591f\u652f\u4ed8\u4e0d\u5230\u534a\u5e74\u7684\u5458\u5de5\u85aa\u8d44\uff0c\u8981\u524a\u51cf\u751f\u4ea7\u89c4\u6a21\u3001\u88c1\u51cf\u5458\u5de5\uff0c\u8fd9\u4e9b\u94b1\u8fde\u5de5\u4eba\u8c34\u6563\u8d39\u90fd\u4e0d\u591f\u3002</p><p>\u3000\u3000\u4ece\u4e0a\u56de\u9999\u6e2f\u7535\u5b50\u5c55\u4e4b\u540e\uff0c\u5f20\u606a\u8fd8\u6ca1\u6709\u673a\u4f1a\u89c1\u5230\u845b\u6c0f\u7236\u5b50\u7684\u843d\u9b44\u6a21\u6837\uff0c\u4ed6\u5c06\u89c6\u7ebf\u4ece\u7a97\u5916\u6536\u56de\u6765\uff0c\u9760\u5230\u8212\u9002\u67d4\u8f6f\u7684\u5750\u6905\u9760\u6795\u4e0a\uff0c\u5750\u5728\u524d\u6392\u7684\u5085\u4fca\u63d0\u9192\u4ed6\u6e56\u5824\u5c31\u5728\u524d\u9762\u4e86\u3002</p><p>\u3000\u3000\u8f66\u5728\u65b0\u5efa\u7684\u91d1\u5c71\u6e56\u5824\u524d\u505c\u4e0b\u6765\uff0c\u6709\u8fc7\u6e83\u5824\u7684\u60e8\u75db\u6559\u8bad\uff0c\u65b0\u7b51\u7684\u6e56\u5824\u5728\u4e34\u57ce\u4e00\u6bb5\u91c7\u53d6\u590d\u5824\u7ed3\u6784\uff0c\u907f\u514d\u65b0\u57ce\u533a\u906d\u53d7\u6e56\u6c34\u7684\u76f4\u63a5\u5a01\u80c1\u3002</p><p>\u3000\u3000\u5728\u5f53\u521d\u6e83\u5824\u5904\u505c\u4e86\u8f66\uff0c\u5824\u4e0b\u7acb\u4e86\u4e00\u4e2a\u7eaa\u5ff5\u7891\uff0c\u8fc7\u4e24\u5929\u5c31\u662f\u7eaa\u5ff5\u7891\u624d\u7b97\u6b63\u5f0f\u843d\u6210\uff0c\u5f20\u606a\u4e0d\u559c\u6b22\u51d1\u70ed\u95f9\uff0c\u63d0\u524d\u8fc7\u6765\u796d\u62dc\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u5dcd\u5ce8\u77f3\u7891\u7891\u8eab\u4e0a\u96d5\u7740\u6e83\u5824\u65f6\u594b\u4e0d\u987e\u8eab\u8df3\u5165\u51b3\u53e3\u7684\u516d\u540d\u6218\u58eb\u4e0e\u5341\u4e07\u519b\u6c11\u5728\u4e5d\u6c5f\u6cb3\u897f\u5cb8\u4e3e\u9504\u7b51\u62a4\u57ce\u575d\u7684\u6d6e\u96d5\uff0c\u732e\u8fc7\u82b1\uff0c\u8d75\u6709\u4f26\u966a\u540c\u5f20\u606a\u4ece\u7eaa\u5ff5\u724c\u540e\u7684\u5761\u9053\u767b\u4e0a\u6e56\u5824\u3002</p><p>\u3000\u3000\u773a\u671b\u8fdc\u5904\u70df\u6ce2\u98d8\u6e3a\u7684\u6e56\u6c34\uff0c\u8981\u662f\u5f53\u521d\u4e0d\u80fd\u5728\u6e83\u5824\u540e\u516d\u5c0f\u65f6\u4e4b\u5185\u5728\u4e5d\u6c5f\u6cb3\u4e1c\u5cb8\u4e34\u65f6\u7b51\u8d77\u62a4\u57ce\u571f\u575d\u903c\u5f80\u6d2a\u6c34\u6539\u5411\uff0c\u8d75\u6709\u4f26\u4e5f\u5b9e\u5728\u60f3\u8c61\u4e0d\u51fa\u8ba9\u767e\u4ebf\u5428\u6d2a\u6c34\u51b2\u5165\u4e09\u767e\u4e07\u4eba\u53e3\u7684\u91d1\u5c71\u57ce\u4f1a\u9020\u6210\u4f55\u7b49\u60e8\u91cd\u7684\u707e\u96be\uff01</p><p>\u3000\u3000\u4e0d\u8fc7\u9020\u6210\u4e00\u5343\u4e8c\u767e\u591a\u519b\u6c11\u4f24\u4ea1\u3001\u76f4\u63a5\u7ecf\u6d4e\u635f\u5931\u63a5\u8fd1\u516d\u5341\u4ebf\u7684\u91d1\u5c71\u6e56\u6e83\u5824\u4ecd\u662f\u60e8\u91cd\u3001\u4e0d\u5bb9\u9057\u5fd8\u7684\u6559\u8bad\u3002</p><p>\u3000\u3000\u8d75\u6709\u4f26\u5728\u6e56\u5824\u4e0a\u8ddf\u5f20\u606a\u4ecb\u7ecd\u91d1\u5c71\u707e\u540e\u91cd\u5efa\u5de5\u4f5c\u4ee5\u53ca\u5bf9\u91d1\u5c71\u6e56\u3001\u996e\u9a6c\u6cb3\u7684\u758f\u7406\u5de5\u4f5c\uff0c\u6062\u590d\u91d1\u5c71\u6e56\u6cbf\u5cb8\u6ee9\u6d82\u3001\u6e7f\u5730\u5730\u8c8c\uff0c\u7ed9\u65b0\u5149\u7eb8\u4e1a\u6536\u8d2d\u7684\u8fd1\u4e24\u4e07\u4ea9\u6e56\u6ee8\u6797\u5730\u4e5f\u90fd\u6089\u6570\u8fd8\u6e56\u4e86\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u91d1\u5c71\u6e56\u7684\u84c4\u6d2a\u91cf\u589e\u52a0\u4e86\u8fd1\u4e00\u500d\uff0c\u4ec5\u6b64\u4e3e\u5c31\u5c06\u91d1\u5c71\u6e56\u6d41\u57df\u7684\u9632\u6d2a\u6807\u51c6\u63d0\u9ad8\u4e86\u4e00\u500d\u591a\u3002\u8981\u662f\u8fd9\u4e9b\u5de5\u4f5c\u80fd\u63d0\u524d\u4e00\u5e74\u534a\u8f7d\u5f00\u5c55\uff0c\u4eca\u5e74\u590f\u5b63\u7684\u6d2a\u6c34\u4e5f\u5c31\u4e0d\u4f1a\u5f62\u6210\u591a\u5927\u5a01\u80c1\u4e86\u3002\u4eba\u7c7b\u603b\u662f\u5728\u7ecf\u5386\u8840\u6dcb\u6dcb\u7684\u4e8b\u5b9e\u4e4b\u540e\u624d\u4f1a\u63a5\u53d7\u6559\u8bad\u3002</p><p>\u3000\u3000\u996e\u9a6c\u6cb3\u4e0a\u6e38\u5c71\u5cad\u5730\u5e26\u7684\u9000\u8015\u8fd8\u6797\u5de5\u4f5c\u4e5f\u90fd\u5168\u9762\u5c55\u5f00\uff0c\u65b0\u5149\u6797\u4e1a\u5728\u91d1\u5c71\u3001\u7ae0\u5dde\u7b49\u5730\u7684\u690d\u6797\u5de5\u4f5c\u4e5f\u5168\u9762\u5c55\u5f00\uff0c\u8981\u8d76\u5728\u660e\u5e74\u96e8\u5b63\u4e4b\u524d\u4e3a\u6c5f\u5357\u7701\u4e2d\u90e8\u65b0\u690d\u516b\u5341\u4e07\u4ea9\u5de5\u4e1a\u539f\u6599\u6797\u3002</p><p>\u3000\u3000\u5468\u590d\u6ca1\u6709\u4eb2\u5386\u6e56\u6c34\u5012\u704c\u7684\u60ca\u9669\u4e00\u523b\uff0c\u6e56\u5824\u4e0a\u7ed9\u98ce\u5439\u5f97\u6709\u4e9b\u51b7\uff0c\u4ed6\u638f\u51fa\u70df\u6765\u8ddf\u5085\u4fca\u4e92\u76f8\u6321\u7740\u98ce\u70b9\u70df\u62bd\uff0c\u5f20\u606a\u8f6c\u8fc7\u8eab\u6765\uff0c\u770b\u5230\u5468\u590d\u4e0e\u5085\u4fca\u5728\u540e\u9762\u62bd\u70df\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8d75\u4e66\u8bb0\u70df\u763e\u4e5f\u91cd\uff0c\u4f60\u4eec\u4e5f\u4e0d\u5206\u8d75\u4e66\u8bb0\u4e00\u6839\u70df\u3002\u201d</p><p>\u3000\u3000\u5468\u590d\u5c06\u624b\u91cc\u70df\u626c\u4e86\u626c\uff0c\u95ee\u9053\uff1a\u201c\u6211\u8fd9\u70df\u8d75\u4e66\u8bb0\u53ef\u62bd\u5f97\u60ef\uff1f\u201d</p><p>\u3000\u3000\u8d75\u6709\u4f26\u89c1\u5468\u590d\u624b\u91cc\u662f\u6700\u666e\u901a\u7684\u91d1\u5c71\u6e56\uff0c\u60f3\u4e0d\u5230\u8eab\u4e3a\u65b0\u5149\u7eb8\u4e1a\u7684\u526f\u603b\u88c1\u3001\u65b0\u5149\u7eb8\u4e1a\u5728\u91d1\u5c71\u8fd1\u4e09\u5341\u4ebf\u9020\u7eb8\u8d44\u4ea7\u7684\u603b\u8d1f\u8d23\u4eba\u7adf\u7136\u62bd\u8fd9\u70df\uff0c\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u4ece\u515c\u91cc\u4e5f\u638f\u51fa\u4e00\u6a21\u4e00\u6837\u7684\u91d1\u5c71\u6e56\u70df\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u8bf7\u5927\u5bb6\u62bd\u6211\u7684\u70df\u5f97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u79cd\u91d1\u5c71\u6e56\u70df\u52b2\u5f88\u5927\uff0c\u70df\u763e\u4e0d\u91cd\u7684\u4eba\u62bd\u591a\u4e86\u4f1a\u5934\u75bc\uff0c\u5f20\u606a\u4e5f\u638f\u51fa\u81ea\u5df1\u7684\u70df\u6765\u5206\u4e86\u4e00\u652f\u7ed9\u5f20\u5955\uff0c\u91d1\u5c71\u5e02\u7684\u966a\u540c\u5b98\u5458\u4e5f\u90fd\u5404\u81ea\u638f\u51fa\u70df\u6765\u62bd\u3002</p><p>\u3000\u3000\u7ad9\u5728\u8fd9\u5904\u6e56\u5824\u4e0a\uff0c\u53ef\u4ee5\u4e00\u76ee\u4e86\u7136\u7684\u773a\u671b\u91d1\u5c71\u57ce\uff0c\u53ef\u4ee5\u8fdc\u773a\u4e1c\u5357\u65b9\u9762\u7684\u4f4e\u77ee\u7fa4\u5c71\uff0c\u53ef\u4ee5\u773a\u671b\u996e\u9a6c\u6cb3\u897f\u5cb8\u7684\u9020\u7eb8\u57fa\u5730\u3002</p><p>\u3000\u3000\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u3001\u52a0\u4e0a\u6280\u6539\u6295\u5165\uff0c\u65b0\u5149\u7eb8\u4e1a\u5728\u91d1\u5c71\u7684\u6295\u8d44\u8d85\u8fc7\u4e09\u5341\u4ebf\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e5f\u56e0\u6b64\u5927\u89c4\u6a21\u7684\u63a5\u7eb3\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u3001\u9648\u6c0f\u4e07\u4e1a\u96c6\u56e2\u7b49\u534e\u5546\u96c6\u56e2\u7684\u6ce8\u8d44\uff0c\u4e91\u6e90\u3001\u4e07\u4e1a\u5bf9\u65b0\u5149\u7eb8\u4e1a\u7684\u6301\u80a1\u4e5f\u63d0\u9ad8\u523036\uff05\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u4e4b\u540e\u7684\u6062\u590d\u751f\u4ea7\u5de5\u4f5c\u4e5f\u4e0d\u80fd\u7b97\u4e00\u5e06\u98ce\u987a\uff0c\u5de5\u4eba\u4eec\u5f88\u4e0d\u4e60\u60ef\u65b0\u5149\u7eb8\u4e1a\u4e25\u683c\u7684\u7ba1\u7406\u6a21\u5f0f\uff0c\u7279\u522b\u662f\u5bf9\u91c7\u8d2d\u4f53\u7cfb\u7684\u91cd\u5851\u89e6\u53ca\u4e86\u8bb8\u591a\u4e4b\u524d\u4f9d\u8d56\u7eb8\u5382\u751f\u5b58\u7684\u5229\u76ca\u7fa4\u4f53\uff0c\u6020\u5de5\u3001\u7f62\u5de5\u3001\u805a\u4f17\u6297\u8bae\u7684\u4e8b\u60c5\u65f6\u6709\u53d1\u751f\u3002\u5728\u6c61\u6c34\u5382\u6280\u6539\u5b8c\u6210\u8981\u5168\u9762\u6062\u590d\u751f\u4ea7\u4e4b\u65f6\uff0c\u6574\u4e2a\u5382\u533a\u751a\u81f3\u60ac\u6302\u6ee1\u201c\u9a71\u8d76\u8d44\u672c\u5bb6\u8d70\u72d7\u5468\u590d\u3001\u5de5\u4eba\u9636\u7ea7\u8981\u5f53\u5bb6\u4f5c\u4e3b\u201d\u7684\u6761\u5e45\u6765\uff0c\u8fd9\u4e8b\u540e\u6765\u4e5f\u6210\u4e3a\u4e86\u5468\u590d\u65f6\u5e38\u7ed9\u4eba\u62ff\u6765\u5632\u7b11\u7684\u7b11\u67c4\u3002</p><p>\u3000\u3000\u5f53\u65f6\u8d76\u4e0a\u5f20\u606a\u5728\u631f\u6301\u4e8b\u4ef6\u5165\u9662\uff0c\u5468\u590d\u8fd8\u679c\u65ad\u51b3\u5b9a\u5bf9\u7eb8\u5382\u5b9e\u884c\u65e0\u9650\u671f\u505c\u5de5\uff0c\u9664\u4e86\u5fc5\u8981\u7684\u770b\u5b88\u4eba\u5458\uff0c\u7b2c\u4e8c\u5929\u5c31\u7ec4\u7ec7\u8f66\u961f\u51c6\u5907\u5c06\u7ba1\u7406\u56e2\u961f\u5168\u90e8\u64a4\u79bb\u7eb8\u5382\u3002</p><p>\u3000\u3000\u5728\u7ba1\u7406\u56e2\u961f\u5c06\u79c1\u4eba\u7269\u54c1\u90fd\u642c\u4e0a\u8f66\u3001\u8f66\u961f\u5c31\u8981\u5f00\u51fa\u5382\u95e8\u4e4b\u65f6\uff0c\u5de5\u4eba\u4eec\u5835\u5728\u5382\u95e8\u5916\u963b\u6b62\u7ba1\u7406\u5c42\u79bb\u5382\u3001\u8bf7\u6c42\u9762\u6062\u590d\u751f\u4ea7\u3002</p><p>\u3000\u3000\u6b64\u65f6\u8ddd\u91d1\u5c71\u7eb8\u5382\u5168\u9762\u6062\u590d\u751f\u4ea7\u624d\u8fc7\u53bb\u8fd8\u4e0d\u5230\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u5f53\u6708\u5c31\u80fd\u5b9e\u73b0\u76c8\u5229\uff0c\u6709\u4e86\u76c8\u5229\uff0c\u5c31\u80fd\u63d0\u9ad8\u5de5\u4eba\u7684\u798f\u5229\u3001\u6536\u5165\uff0c\u52a0\u4e0a\u9526\u6e56\u6350\u5efa\u7684\u5b89\u7f6e\u5c0f\u533a\u5c31\u8981\u5efa\u6210\u4ea4\u4ed8\u4f7f\uff0c\u6574\u4e2a\u7eb8\u5382\u7684\u5c40\u9762\u624d\u7b97\u6700\u7ec8\u7684\u7a33\u5b9a\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5468\u590d\u4f5c\u4e3a\u56fd\u5185\u6587\u9769\u7ed3\u675f\u540e\u7684\u7b2c\u4e00\u6279\u5927\u5b66\u751f\uff0c\u6709\u7740\u96c6\u4f53\u3001\u56fd\u6709\u9020\u7eb8\u5382\u7684\u4efb\u804c\u5c65\u5386\uff0c\u53c8\u5728\u65b0\u5149\u7eb8\u9020\u5382\u7ed9\u9526\u6e56\u627f\u5305\u3001\u6536\u8d2d\u540e\uff0c\u7ecf\u5386\u4f01\u4e1a\u6240\u6709\u4f53\u5236\u3001\u7ba1\u7406\u6a21\u5f0f\u8f6c\u53d8\u7684\u79cd\u79cd\u7ec6\u5fae\u75db\u5904\uff0c\u7279\u522b\u662f\u534f\u52a9\u5468\u6e38\u5c06\u6d77\u5dde\u5e02\u96f6\u6563\u7684\u9020\u7eb8\u8d44\u4ea7\u51dd\u805a\u6210\u5b9e\u529b\u5f3a\u5927\u7684\u65b0\u5149\u7eb8\u4e1a\u96c6\u56e2\uff0c\u89c1\u8bc6\u5230\u8d44\u4ea7\u6574\u5408\u3001\u8d44\u672c\u8fd0\u4f5c\u3001\u6280\u672f\u6539\u9020\u3001\u751f\u4ea7\u6539\u9020\u7684\u79cd\u79cd\u5999\u5904\uff0c\u638c\u63a7\u5982\u6b64\u4e4b\u89c4\u6a21\u7684\u91d1\u5c71\u7eb8\u5382\u4e5f\u6e38\u5203\u6709\u4f59\uff0c\u81f3\u5c11\u5728\u56fd\u5185\u9020\u7eb8\u884c\u4e1a\uff0c\u7ba1\u7406\u7ecf\u9a8c\u4e4b\u4e30\u5bcc\uff0c\u773c\u754c\u4e0e\u89c1\u89e3\uff0c\u90fd\u8981\u7b97\u4e09\u4e94\u4eba\u4e4b\u5217\u4e86\uff0c\u4e5f\u662f\u786e\u5b9e\u6709\u8d44\u683c\u8fdb\u5165\u9526\u6e56\u4e8c\u5341\u4e5d\u4eba\u4e4b\u5217\u3002</p><p>\u3000\u3000\u5927\u5bb6\u5728\u6e56\u5824\u4e0a\u62bd\u7740\u70df\uff0c\u4e0b\u4e00\u7ad9\u5c31\u8981\u53bb\u7eb8\u5382\u4e1c\u3001\u5728\u539f\u68da\u6237\u533a\u65b0\u5efa\u6210\u7684\u5b89\u7f6e\u5c0f\u533a\u53bb\u53c2\u89c2\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u60ca\u52a8\u5f90\u5b66\u5e73\u3001\u6881\u4f1f\u6cd5\uff0c\u8d75\u6709\u4f26\u4ed6\u662f\u539a\u7740\u8138\u76ae\u786c\u51d1\u8fc7\u6765\u7684\uff0c\u867d\u8bf4\u968f\u7740\u5370\u5c3c\u56fd\u5185\u653f\u5c40\u7684\u7f13\u548c\uff0c\u534e\u4eba\u8d44\u672c\u64a4\u79bb\u5370\u5c3c\u7684\u58f0\u52bf\u4e5f\u6e10\u6e10\u7684\u5c0f\u4e86\uff0c\u4f46\u662f\u5ffd\u89c6\u9526\u6e56\u6b64\u65f6\u5728\u4e1c\u5357\u4e9a\u534e\u4eba\u8d44\u672c\u4e2d\u7684\u5f71\u54cd\u529b\uff0c\u90fd\u662f\u6ca1\u6709\u8fdc\u89c1\u800c\u611a\u6627\u7684\u884c\u4e3a\uff0c\u8d75\u6709\u4f26\u603b\u4e0d\u80fd\u773c\u7741\u7741\u7684\u770b\u7740\u9526\u6e56\u5f15\u5bfc\u4e1c\u5357\u4e9a\u534e\u4eba\u8d44\u672c\u90fd\u5f80\u65b0\u4ead\u8f6c\u79fb\u5427\u2014\u2014\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\uff0c\u65e0\u8bba\u662f\u6c5f\u5357\u7701\uff0c\u8fd8\u662f\u6c5f\u5357\u7701\u4e0e\u4e1c\u6d77\u7701\u8054\u5408\u542f\u52a8\u5c0f\u6c5f\u6d41\u57df\u7ecf\u6d4e\u5408\u4f5c\u8ba1\u5212\uff0c\u90fd\u8d8a\u6765\u8d8a\u591a\u7684\u5c06\u91cd\u5fc3\u5f80\u65b0\u4ead\u8f6c\u79fb\uff0c\u91d1\u5c71\u6709\u88ab\u8fb9\u7f18\u5316\u7684\u8d8b\u52bf\u3002</p></article>"}}}]);
