(window.webpackJsonp=window.webpackJsonp||[]).push([[967],{990:function(p){p.exports={chapterTitle:null,title:"\u7b2c781\u7ae0 \u591c\u5077\u60c5",content:"<article><h2>\u7b2c781\u7ae0 \u591c\u5077\u60c5</h2><p>\u3000\u3000\u62b5\u8fbe\u5317\u4eac\u540e\uff0c\u5f20\u606a\u5c31\u8ba9\u5085\u4fca\u76f4\u63a5\u4ece\u5317\u4eac\u8f6c\u673a\u56de\u6d77\u5dde\u4e86\uff0c\u6574\u4e2a\u6625\u8282\u90fd\u8ba9\u5085\u4fca\u8ddf\u7740\u81ea\u5df1\u5728\u65e5\u672c\u6643\u8361\uff0c\u674e\u660e\u745c\u4e0e\u60dc\u5bb9\u3001\u60dc\u7fbd\u4e24\u4e2a\u5c0f\u4e2b\u5934\u80af\u5b9a\u662f\u6ee1\u8179\u610f\u89c1\uff0c\u4eca\u5929\u53c8\u662f\u5143\u5bb5\u4f73\u8282\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\u90fd\u8981\u8ba9\u5085\u4fca\u8d76\u56de\u6d77\u5dde\u4e0e\u5bb6\u4eba\u56e2\u805a\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u5317\u4eac\u7684\u51fa\u884c\uff0c\u81ea\u6709\u5317\u4eac\u5206\u516c\u53f8\u7684\u5de5\u4f5c\u4eba\u5458\u5b89\u6392\u3002</p><p>\u3000\u3000\u6697\u7d2b\u8272\u7684\u4e91\u5c42\u8986\u76d6\u7740\u5317\u4eac\u7684\u591c\u7a7a\uff0c\u906e\u853d\u4e86\u661f\u6708\uff0c\u591c\u7a7a\u5374\u6ca1\u6709\u7279\u522b\u7684\u6697\uff0c\u5927\u6982\u662f\u4e91\u5c42\u5f88\u8584\u7684\u7f18\u6545\u5427\u3002</p><p>\u3000\u3000\u4ece\u6613\u4e91\u98de\u5bb6\u4e2d\u51fa\u6765\uff0c\u5f20\u606a\u5c31\u8ba9\u53f8\u673a\u76f4\u63a5\u5c06\u4ed6\u4e0e\u7fdf\u4e39\u9752\u9001\u5230\u5bbe\u9986\u3002\u7fdf\u4e39\u9752\u4fa7\u5934\u770b\u7740\u5f20\u606a\uff0c\u63d0\u9192\u4ed6\u8bf4\uff1a\u201c\u4e0d\u7528\u53bb\u89c1\u8c22\u603b\uff1f\u201d\u62ac\u624b\u770b\u4e86\u770b\u7cbe\u81f4\u7684\u5973\u5f0f\u8155\u8868\uff0c\u8bf4\u9053\uff0c\u201c\u65f6\u95f4\u8fd8\u86ee\u65e9\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5dee\u70b9\u628a\u8fd9\u4e8b\u7ed9\u5fd8\u4e86\uff0c\u201d\u5f20\u606a\u65c1\u82e5\u65e0\u4e8b\u7684\u638f\u51fa\u624b\u673a\u7ed9\u665a\u6674\u6253\u7535\u8bdd\uff0c\u201c\u2026\u2026\u4f60\u4e5f\u4f4f\u738b\u5e9c\u4e95\u5927\u9152\u5e97\uff1f\u90a3\u6b63\u597d\uff0c\u6211\u4eec\u9a6c\u4e0a\u5c31\u56de\u53bb\uff0c\u4f60\u5728\u5927\u5802\u7684\u5496\u5561\u8336\u5ea7\u7b49\u6211\u4eec\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u65f6\u5019\u5c31\u62c5\u5fc3\u7fdf\u4e39\u9752\u4f1a\u592a\u806a\u660e\uff0c\u653e\u4e0b\u624b\u673a\uff0c\u4fa7\u5934\u770b\u7740\u8f66\u7a97\u5916\uff0c\u4ece\u8f66\u7a97\u7684\u5012\u5f71\u91cc\u770b\u5230\u7fdf\u4e39\u9752\u8138\u4e0a\u6709\u51e0\u5206\u72d0\u7591\uff0c\u5509\uff0c\u771f\u662f\u5934\u75bc\u3002\u5429\u5490\u53f8\u673a\u76f4\u63a5\u9001\u4ed6\u4e0e\u7fdf\u4e39\u9752\u56de\u9152\u5e97\uff0c\u665a\u6674\u5728\u9152\u5e97\u5927\u5802\u7684\u5496\u5561\u5385\u7b49\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5317\u4eac\u7684\u521d\u6625\uff0c\u5ba4\u5916\u6625\u5bd2\u6599\u5ced\uff0c\u5ba4\u5185\u6709\u6696\u6c14\u4f9b\u5e94\uff0c\u5ba4\u6e29\u901a\u5e38\u90fd\u7ef4\u6301\u5728\u4e8c\u5341\u5ea6\u4ee5\u4e0a\uff0c\u665a\u6674\u6ca1\u6709\u6253\u7b97\u51fa\u95e8\uff0c\u5750\u5728\u5927\u5802\u7684\u5496\u5561\u5385\u91cc\u7b49\u5f20\u606a\uff0c\u4e30\u8174\u7684\u8eab\u4e0a\u7a7f\u7740\u4e00\u4ef6\u8d34\u8eab\u7684\u9ec4\u8272\u788e\u82b1\u7ed2\u7ebf\u8863\uff0c\u9888\u95f4\u56f4\u7740\u4e00\u65b9\u6742\u8272\u788e\u82b1\u7684\u4e1d\u5dfe\uff0c\u5c06\u7855\u5927\u4e30\u633a\u7684\u80f8\u90e8\u906e\u63a9\u4e9b\uff0c\u4e0b\u8eab\u7a7f\u7740\u68d5\u9ec4\u8272\u7684\u88e4\u5b50\uff0c\u81c0\u90e8\u3001\u5927\u817f\u7ed9\u88f9\u5f97\u7d27\u7d27\u7684\uff0c\u6574\u4e2a\u8eab\u6750\u51f9\u51f8\u6709\u81f4\uff0c\u66f2\u7ebf\u73b2\u73d1\uff0c\u5e26\u7740\u81ea\u7136\u5377\u7684\u957f\u53d1\u62ab\u4e0b\u6765\uff0c\u53ea\u5728\u53d1\u68a2\u62ff\u5934\u7ef3\u62e2\u5728\u4e00\u8d77\uff0c\u886c\u6258\u5979\u7f8e\u4e3d\u7684\u8138\u86cb\u6210\u719f\u8ff7\u4eba\uff0c\u800c\u4e14\u6d0b\u6ea2\u7740\u6027\u611f\u7684\u6c14\u606f\u3002</p><p>\u3000\u3000\u665a\u6674\u7684\u52a9\u7406\u5f20\u5ead\u4e5f\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u201c\u8c22\u603b\uff0c\u4f60\u597d\u2026\u2026\u201d\u7fdf\u4e39\u9752\u770b\u665a\u6674\u8fd9\u8eab\u6253\u626e\uff0c\u6bd4\u8d77\u4e4b\u524d\u7684\u5e84\u7aef\uff0c\u591a\u4e86\u51e0\u5206\u6027\u611f\uff0c\u5927\u6982\u4f1a\u60f9\u5f97\u5496\u5561\u5385\u7684\u5176\u4ed6\u7537\u4eba\u6d6e\u60f3\u8054\u7fe9\u5427\uff0c\u53cc\u624b\u6446\u5728\u5c0f\u8179\u524d\u6b20\u8eab\u81f4\u610f\uff0c\u8bf4\u9053\uff0c\u201c\u4ee5\u540e\u8fd8\u8981\u8bf7\u8c22\u603b\u591a\u591a\u6307\u6559\u3002\u201d</p><p>\u3000\u3000\u201c\u6307\u6559\u8c08\u4e0d\u4e0a\uff0c\u4f60\u8fd8\u662f\u5524\u6211\u8c22\u59d0\u597d\u4e86\uff0c\u201d\u665a\u6674\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u4ee5\u540e\u5f20\u606a\u8fd8\u8981\u591a\u8bf7\u4f60\u7167\u987e\u5462\u2026\u2026\u201d\u8ba9\u5f00\u8eab\u5b50\u8bf7\u7fdf\u4e39\u9752\u4e0e\u5f20\u606a\u5750\u4e0b\u3002</p><p>\u3000\u3000\u4e0e\u4e24\u4e2a\u8273\u5149\u56db\u5c04\u7684\u7f8e\u5973\u5750\u5728\u4e00\u8d77\uff0c\u81ea\u7136\u8981\u627f\u53d7\u5468\u56f4\u5992\u5ac9\u4e0e\u6028\u6068\u7684\u76ee\u5149\uff0c\u53ef\u662f\u5f20\u606a\u60f3\u7740\u600e\u6837\u624d\u80fd\u5c06\u7fdf\u4e39\u9752\u652f\u5f00\uff0c\u8fd9\u5927\u6982\u5c31\u662f\u6240\u8c13\u7684\u201c\u4e09\u4e2a\u548c\u5c1a\u6ca1\u6c34\u5403\u5427\u3002\u201d</p><p>\u3000\u3000\u5750\u5728\u5496\u5561\u5385\u91cc\u95f2\u626f\u4e86\u4e00\u9635\uff0c\u53c8\u9047\u4e0a\u5468\u4e00\u5e73\u3001\u59da\u575a\u7b49\u4eba\u9001\u53f6\u5efa\u658c\u3001\u9648\u4fe1\u751f\u3001\u8bb8\u9e3f\u4f2f\u56de\u5bbe\u9986\uff0c\u53ea\u5f97\u5927\u5bb6\u90fd\u5750\u5728\u5496\u5561\u5385\u91cc\u95f2\u626f\uff0c\u5f20\u606a\u4e5f\u6839\u672c\u6ca1\u6709\u5fc3\u601d\u8c08\u4ec0\u4e48\u6b63\u4e8b\uff0c\u5e73\u65e5\u7cbe\u529b\u6700\u65fa\u76db\u7684\u4ed6\u5012\u662f\u6700\u5148\u6253\u8d77\u54c8\u6b20\u6765\uff0c\u4ed6\u5c31\u4e0d\u5ba2\u6c14\u7684\u7ad9\u8d77\u6765\u8d76\u4eba\uff1a\u201c\u6628\u5929\u591c\u91cc\u90fd\u6ca1\u6709\u600e\u4e48\u7761\u89c9\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u662f\u4e0d\u662f\u660e\u5929\u518d\u8bf4\uff1f\u201d\u8fd9\u65f6\u5019\u7684\u786e\u611f\u5230\u5f88\u75b2\u60eb\uff0c\u773c\u76ae\u5b50\u677e\u8f6f\uff0c\u624b\u6342\u7740\u5634\u5507\u6253\u7740\u54c8\u6b20\u63a5\u8fc7\u59da\u575a\u5e2e\u4ed6\u5b89\u6392\u597d\u7684\u623f\u5361\u2014\u2014\u884c\u674e\u65e9\u5c31\u7531\u5de5\u4f5c\u4eba\u5458\u9001\u8fdb\u5bbe\u9986\u623f\u95f4\uff0c\u5c31\u671d\u7535\u68af\u8d70\u53bb\uff0c\u53f6\u5efa\u658c\u5728\u5317\u4eac\u53e6\u6709\u4f4f\u5904\uff0c\u5927\u5bb6\u4e5f\u5c31\u8fd9\u6837\u6563\u5f00\u3002</p><p>\u3000\u3000\u8bf4\u6765\u5947\u602a\uff0c\u521a\u8d70\u8fdb\u623f\u95f4\u5c31\u53c8\u9661\u7136\u7cbe\u795e\u8d77\u6765\uff0c\u5f20\u606a\u638f\u51fa\u624b\u673a\u7ed9\u665a\u6674\u6253\u7535\u8bdd\u8ba9\u5979\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u771f\u9b3c\uff0c\u770b\u4f60\u7684\u6837\u5b50\u4ee5\u4e3a\u4f60\u770b\u5230\u5e8a\u5c31\u80fd\u7761\u89c9\u5462\uff0c\u201d\u665a\u6674\u5728\u7535\u8bdd\u91cc\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u9a6c\u4e0a\u8fc7\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6253\u5f00\u623f\u95f4\u95e8\u865a\u63a9\u7740\uff0c\u95e8\u7ed9\u63a8\u5f00\u65f6\uff0c\u53ea\u542c\u89c1\u665a\u6674\u5728\u95e8\u53e3\u8f7b\u58f0\u7684\u5c16\u53eb\u4e86\u4e00\u58f0\uff0c\u5f20\u606a\u63a2\u5934\u770b\u8fc7\u6765\uff0c\u665a\u6674\u6ee1\u8138\u901a\u7ea2\u7684\u95ea\u8fdb\u6765\uff0c\u5c06\u623f\u95e8\u5173\u4e0a\uff0c\u8eab\u5b50\u8d34\u7740\u623f\u95e8\u6342\u7740\u80f8\u53e3\uff0c\u4e00\u526f\u53d7\u5230\u60ca\u5413\u7684\u6837\u5b50\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff0c\u770b\u5230\u9b3c\u4e86\u5417\uff1f\u201d\u5f20\u606a\u5947\u602a\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4f1a\u5c06\u7fdf\u4e39\u9752\u7684\u623f\u95f4\u8ddf\u4f60\u5b89\u6392\u5728\u540c\u4e00\u680b\u697c\uff1f\u201d\u665a\u6674\u5fc3\u614c\u614c\u7684\u6342\u7740\u80f8\u53e3\uff0c\u201c\u5979\u770b\u5230\u6211\u8fdb\u6765\u4e86\uff01\u201d</p><p>\u3000\u3000\u5bbe\u9986\u623f\u95f4\u90fd\u662f\u5317\u4eac\u5206\u516c\u53f8\u8fd9\u8fb9\u7684\u5de5\u4f5c\u4eba\u5458\u5b89\u6392\u7684\uff0c\u4e0a\u697c\u65f6\u8fd8\u7279\u610f\u5173\u5fc3\u8fc7\u9648\u4fe1\u751f\u3001\u8bb8\u9e3f\u4f2f\u7684\u623f\u95f4\u90fd\u662f\u5176\u4ed6\u697c\u5c42\uff0c\u6ca1\u60f3\u8981\u5206\u516c\u53f8\u8fd9\u8fb9\u7684\u5de5\u4f5c\u4eba\u5458\u4f1a\u5c06\u7fdf\u4e39\u9752\u7684\u623f\u95f4\u8ddf\u4ed6\u5b89\u6392\u5728\u540c\u4e00\u697c\u5c42\uff0c\u5f20\u606a\u6478\u4e86\u6478\u8111\u888b\uff0c\u65e0\u5948\u7684\u8bf4\u9053\uff1a\u201c\u53ef\u80fd\u662f\u5b89\u6392\u623f\u95f4\u7684\u5de5\u4f5c\u4eba\u5458\u60f3\u592a\u591a\u4e86\u2026\u2026\u6211\u770b\u4e0a\u53bb\u50cf\u8ddf\u8eab\u8fb9\u5de5\u4f5c\u4eba\u5458\u4e71\u641e\u7684\u4eba\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e0d\u50cf\uff1f\u90a3\u59ae\u5b50\u8fd9\u4e48\u60f9\u4eba\u773c\uff0c\u6211\u90fd\u8981\u6000\u7591\u4f60\u7684\u5c45\u5fc3\uff0c\u4f60\u6709\u6ca1\u6709\u5077\u8165\u7684\u5fc3\u601d\uff1f\u201d\u665a\u6674\u6a2a\u4e86\u4ed6\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u6211\u54ea\u6709\u8fd9\u4e2a\u80c6\u5b50\uff1f\u201d\u5f20\u606a\u65e0\u5948\u7684\u8bf4\u8bdd\uff0c\u4e0d\u8981\u8bf4\u522b\u4eba\u4e0d\u4fe1\u4e86\uff0c\u8fde\u4ed6\u81ea\u5df1\u5bf9\u5c06\u7fdf\u4e39\u9752\u7559\u5728\u8eab\u8fb9\u7684\u4e8b\u60c5\u90fd\u5f88\u72b9\u8c6b\u3002</p><p>\u3000\u3000\u665a\u6674\u53c8\u5fc3\u865a\u7684\u95ee\uff1a\u201c\u7ed9\u5979\u770b\u5230\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u80fd\u600e\u4e48\u529e\uff0c\u96be\u9053\u6740\u4e86\u5979\u706d\u53e3\uff1f\u201d\u5f20\u606a\u521a\u624d\u5916\u5957\u8131\u4e0b\uff0c\u53ea\u7a7f\u7740\u886c\u886b\uff0c\u8d70\u8fc7\u53bb\uff0c\u5c06\u665a\u6674\u4e30\u8174\u7684\u8eab\u4f53\u7d27\u8d34\u5728\u623f\u95f4\u95e8\uff0c\u770b\u7740\u5979\u5fc3\u614c\u614c\u7684\u773c\u795e\uff0c\u50cf\u53ea\u521a\u5077\u5403\u9c7c\u7684\u5c0f\u732b\uff0c\u63c9\u6413\u7740\u5979\u4e30\u6ee1\u7684\u81c0\u90e8\uff0c\u5728\u5979\u8033\u8fb9\u8f7b\u58f0\u8bf4\uff0c\u201c\u5979\u8981\u662f\u95ee\u8d77\u6765\uff0c\u4f60\u54ac\u6b7b\u8bf4\u662f\u5230\u6211\u623f\u95f4\u6765\u8c08\u5de5\u4f5c\uff0c\u5979\u7231\u4fe1\u4e0d\u4fe1\u3002\u201d\u5c06\u5979\u7684\u5934\u538b\u8d34\u5728\u623f\u95e8\u4eb2\u543b\u8d77\u6765\u5979\u67d4\u5ae9\u7684\u5634\u5507\uff0c\u542e\u5438\u9999\u6ed1\u7684\u820c\u5c16\u3002</p><p>\u3000\u3000\u5728\u5f20\u606a\u7ecf\u9a8c\u8001\u9053\u7684\u629a\u6478\u4e0e\u4eb2\u543b\u4e0b\uff0c\u665a\u6674\u6d51\u8eab\u706b\u70ed\uff0c\u4e5f\u4e0d\u6015\u5978\u60c5\u4f1a\u4e0d\u4f1a\u7ed9\u4eba\u7aa5\u7834\uff0c\u6402\u4f4f\u5f20\u606a\uff0c\u67d4\u8f6f\u7684\u5634\u5507\u56de\u543b\u7740\uff0c\u4e30\u6ee1\u7684\u80f8\u90e8\u5bc6\u5b9e\u5b9e\u7684\u8d34\u7740\u5f20\u606a\u7684\u8eab\u5b50\uff0c\u611f\u89c9\u5230\u4e24\u817f\u4e4b\u95f4\u6e7f\u5f97\u5389\u5bb3\uff0c\u7ed9\u5f20\u606a\u4e00\u4e1d\u4e0d\u6302\u7684\u5265\u5012\u5728\u5e8a\u4e0a\uff0c\u665a\u6674\u518d\u4e5f\u8010\u4e0d\u4f4f\u60c5\u6b32\u7684\u7ffb\u6d8c\uff0c\u653e\u4e0b\u77dc\u6301\uff0c\u4e3b\u52a8\u5e2e\u5f20\u606a\u8131\u8863\u89e3\u5e26\uff0c\u63e1\u7740\u90a3\u575a\u633a\u7684\u52c3\u8d77\u5f15\u5bfc\u5230\u81ea\u5df1\u4e24\u817f\u4e4b\u95f4\u7684\u6f6e\u6e7f\u5904\uff0c\u8fd8\u5dee\u90a3\u4e48\u4e00\u70b9\u624d\u8fdb\u53bb\uff0c\u4e0b\u8eab\u523a\u6fc0\u5f97\u76f4\u633a\u8d77\u6765\u8fce\u8fc7\u53bb\uff0c\u8ba9\u4e24\u4eba\u7d27\u5bc6\u7684\u8fde\u63a5\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u867d\u7136\u77e5\u9053\u5957\u95f4\u7684\u9694\u95e8\u6548\u679c\u975e\u5e38\u597d\uff0c\u800c\u4e14\u9694\u58c1\u5e94\u8be5\u662f\u5957\u95f4\u7684\u8d77\u5c45\u5ba4\uff0c\u4f46\u662f\u60f3\u5230\u7fdf\u4e39\u9752\u4e5f\u6709\u53ef\u80fd\u5c31\u5728\u8d77\u5c45\u5ba4\u91cc\uff0c\u665a\u6674\u8fd8\u662f\u6342\u7740\u5634\u5507\u4e0d\u8ba9\u547b\u541f\u58f0\u6e32\u6cc4\u51fa\u6765\uff0c\u5927\u6982\u5978\u60c5\u7ed9\u7aa5\u7834\u7684\u7f18\u6545\uff0c\u4eab\u53d7\u8d77\u9163\u7545\u6dcb\u6f13\u6ca1\u6709\u534a\u70b9\u969c\u788d\uff0c\u9999\u6c57\u6dcb\u6f13\uff0c\u5012\u662f\u6700\u540e\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u54fc\u54fc\u5527\u5527\u7684\u53eb\u5524\u8d77\u6765\u3002</p><p>\u3000\u3000\u9163\u7545\u6dcb\u6f13\u7684\u4eab\u53d7\u8fc7\u4e4b\u540e\uff0c\u665a\u6674\u624d\u8eba\u5728\u5f20\u606a\u7684\u80f8\u53e3\u95ee\u9053\uff1a\u201c\u8bb8\u601d\u4e0e\u5510\u5a67\u89c1\u9762\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5012\u662f\u5fcd\u5f97\u4f4f\u5230\u73b0\u5728\u624d\u95ee\uff1f\u201d\u5f20\u606a\u6307\u7740\u665a\u6674\u7684\u9f3b\u5934\uff0c\u770b\u7740\u5979\u9161\u7ea2\u800c\u7f8e\u4e3d\u8ff7\u4eba\u7684\u8138\u7709\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u8fd8\u597d\uff0c\u6ca1\u6709\u4e25\u91cd\u5230\u62ff\u83dc\u5200\u4e92\u780d\u7684\u7a0b\u5ea6\uff0c\u6211\u5012\u662f\u7ed9\u5413\u4e86\u4e00\u8eab\u51b7\u6c57\u3002\u4eca\u5929\u65e9\u4e0a\u79bb\u5f00\u65f6\uff0c\u5510\u5a67\u8fd8\u7761\u5728\u8bb8\u601d\u7684\u5367\u5ba4\u91cc\u5185\uff0c\u8d76\u7740\u4eca\u5929\u8bb8\u7ef4\u3001\u6c5f\u9edb\u513f\u5979\u4eec\u56de\u9999\u6e2f\uff0c\u5510\u5a67\u9047\u5230\u5979\u4eec\uff0c\u8fd8\u4e00\u8d77\u53bb\u5b59\u5148\u751f\u5bb6\u770b\u671b\u5b59\u9759\u6aac\u3002\u6211\u56de\u5bbe\u9986\u524d\u521a\u8ddf\u5510\u5a67\u901a\u8fc7\u7535\u8bdd\uff0c\u5979\u9ec4\u660f\u4e4b\u524d\u624d\u56de\u5b66\u6821\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e0b\u5b50\u4f60\u5f97\u610f\u7684\u5427\uff1f\u4f60\u7adf\u7136\u8fd8\u771f\u5728\u5f97\u610f\u7684\u7b11\uff01\u201d\u665a\u6674\u4f38\u8fc7\u624b\u5c06\u5f20\u606a\u7fd8\u8d77\u7684\u5634\u89d2\u62c9\u5e73\u3002</p><p>\u3000\u3000\u201c\u6211\u54ea\u6709\u5f97\u610f\u4e86\uff1f\u201d\u5f20\u606a\u4e00\u8138\u65e0\u8f9c\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5609\u4fe1\u96c6\u56e2\u7684\u4e8b\u60c5\uff0c\u4f60\u6253\u7b97\u600e\u4e48\u5904\u7406\uff1f\u201d\u665a\u6674\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4e5f\u8be5\u8bf4\u8bf4\u6b63\u4e8b\u4e86\uff0c\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8eab\u5b50\u5f80\u4e0b\u6c89\u4e86\u4e9b\u8bb8\uff0c\u624b\u637b\u7740\u665a\u6674\u96ea\u767d\u80f8\u53e3\u7684\u4e73\u5c16\uff0c\u521a\u6709\u8fc7\u9ad8\u6f6e\uff0c\u9971\u6ee1\u7684\u4e73\u5c16\u662f\u8273\u7ea2\u8272\u7684\uff0c\u5c48\u6307\u8f7b\u5f39\u4e86\u4e24\u4e0b\uff0c\u53ea\u8981\u7ed9\u665a\u6674\u7684\u624b\u6441\u4f4f\uff0c\u624d\u8bf4\u6b63\u4e8b\uff0c\u201c\u5148\u4e0d\u8bf4\u5bf9\u4ed8\u5609\u4fe1\u96c6\u56e2\u7684\u4e8b\u60c5\uff0c\u201d\u5c06\u4eca\u5929\u665a\u4e0a\u5728\u6613\u4e91\u98de\u5bb6\u91cc\u542c\u5230\u7684\u6700\u65b0\u6d88\u606f\u544a\u8bc9\u665a\u6674\uff0c\u201c\u82e5\u662f\u7ec4\u5efa\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\uff0c\u9526\u6e56\u672a\u6765\u9762\u4e34\u7684\u7ade\u4e89\u5bf9\u624b\u5c06\u66f4\u591a\uff0c\u6211\u60f3\u5229\u7528\u6d77\u5916\u5206\u516c\u53f8\u5bf9\u56fd\u5185\u7684\u4f01\u4e1a\u8fdb\u884c\u4ea4\u53c9\u6301\u80a1\uff0c\u5229\u7528\u8fd9\u4e00\u8ba1\u5212\uff0c\u5c06\u9526\u6e56\u5bf9\u65d7\u4e0b\u4f01\u4e1a\u7684\u76f4\u63a5\u6301\u80a1\u964d\u4f4e\u5230\u4e00\u5b9a\u7684\u6c34\u5e73\uff0c\u8fd9\u6837\u770b\u4e0a\u53bb\u624d\u4e0d\u4f1a\u592a\u624e\u773c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u8fd8\u4e0d\u662f\u968f\u4f60\u9ad8\u5174\uff1f\u201d\u665a\u6674\u96ea\u767d\u7684\u8eab\u4f53\u5c31\u5e73\u8eba\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u5229\u7528\u6d77\u5916\u6295\u8d44\u516c\u53f8\u6765\u5b9e\u884c\u4ea4\u53c9\u6301\u80a1\u8ba1\u5212\uff0c\u65e0\u975e\u662f\u8ba9\u80a1\u6743\u5173\u7cfb\u53d8\u5f97\u66f4\u590d\u6742\u3001\u66f4\u9690\u853d\uff0c\u8fd8\u6709\u4e00\u79cd\u597d\u5904\u5c31\u662f\u7a0e\u6536\u4e0e\u4eab\u53d7\u5176\u4ed6\u4f18\u60e0\u653f\u7b56\u65f6\uff0c\u5c06\u83b7\u5f97\u4e0e\u5408\u8d44\u4f01\u4e1a\u540c\u7b49\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u5c31\u7b97\u5230\u5341\u5e74\u4e4b\u540e\uff0c\u56fd\u5185\u6c11\u8425\u4f01\u4e1a\u7684\u5730\u4f4d\u4e0e\u6240\u4eab\u53d7\u5230\u7684\u653f\u7b56\u8981\u8fdc\u8fdc\u843d\u540e\u4e8e\u5916\u8d44\u3001\u5408\u8d44\u4f01\u4e1a\uff0c\u8bb8\u591a\u6c11\u8425\u4f01\u4e1a\u4e0d\u5f97\u5df2\u62ab\u6302\u4e0a\u5408\u8d44\u4f01\u4e1a\u7684\u5916\u58f3\u3002</p><p>\u3000\u3000\u201c\u81f3\u4e8e\u5982\u4f55\u53bb\u5bf9\u4ed8\u5609\u4fe1\u96c6\u56e2\uff0c\u6211\u8003\u8651\u52a8\u7528\u6d77\u5916\u5e10\u6237\u91cc\u7684\u8d44\u91d1\uff0c\u5173\u952e\u8fd8\u662f\u8981\u501f\u52bf\u800c\u4e3a\uff0c\u83b7\u5f97\u5609\u4fe1\u5730\u4ea7\u3001\u5609\u4fe1\u7535\u5b50\u7684\u8bdd\u8bed\u6743\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u4e86\u7247\u523b\uff0c\u665a\u6674\u5012\u662f\u6253\u8d77\u54c8\u6b20\u6765\uff0c\u4f0f\u5728\u5f20\u606a\u7684\u80f8\u53e3\uff0c\u8f7b\u58f0\u8bf4\u9053\uff0c\u201c\u60f3\u5728\u4f60\u8eab\u8fb9\u597d\u597d\u7684\u7761\u4e00\u89c9\uff0c\u660e\u5929\u7684\u4e8b\u60c5\u660e\u5929\u518d\u8bf4\u5427\u3002\u201d</p><p>\u3000\u3000\u65e9\u6668\u5230\u897f\u9910\u5385\u7528\u65e9\u9910\uff0c\u665a\u6674\u4e0e\u5f20\u606a\u8fd8\u7279\u610f\u8d77\u4e86\u65e9\uff0c\u5206\u5f00\u6765\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u201c\u8c22\u59d0\u65e9\u554a\u3002\u201d\u7fdf\u4e39\u9752\u4e1a\u5df2\u5750\u5230\u897f\u9910\u5385\u91cc\u7528\u65e9\u9910\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u4e5f\u4e0d\u662f\u521a\u5b66\u4f1a\u505a\u574f\u4e8b\u7684\u5c0f\u5973\u5b69\u5b50\uff0c\u4e5f\u5c31\u6628\u5929\u591c\u91cc\u521a\u7ed9\u7fdf\u4e39\u9752\u649e\u5230\u65f6\u6709\u4e9b\u5fc3\u865a\uff0c\u8fd9\u65f6\u5019\u770b\u5230\u7fdf\u4e39\u9752\u65c1\u82e5\u65e0\u4e8b\u8ddf\u5979\u6253\u62db\u547c\uff0c\u4e5f\u751c\u871c\u7684\u56de\u7b11\uff0c\u5374\u88ab\u7740\u8eab\u5b50\u8ba9\u5df2\u7ecf\u8d70\u5230\u95e8\u53e3\u7684\u5f20\u606a\u9000\u56de\u53bb\u3002</p><p>\u3000\u3000\u8bf4\u5b9e\u8bdd\uff0c\u6bd4\u90a3\u4e9b\u5fc3\u60ca\u80c6\u602f\u7684\u5973\u5b69\u5b50\u4e0d\u540c\uff0c\u665a\u6674\u77e5\u9053\u4e0e\u5f20\u606a\u7684\u4e8b\u60c5\u7ed9\u7fdf\u4e39\u9752\u77e5\u9053\u4e4b\u540e\uff0c\u5c31\u6ca1\u6709\u5fc5\u8981\u5728\u5979\u9762\u524d\u4f2a\u88c5\u3001\u63a9\u9970\u4ec0\u4e48\u4e86\uff0c\u53cd\u800c\u5bf9\u5979\u6709\u4e00\u79cd\u5f88\u4eb2\u5207\u7684\u611f\u89c9\uff0c\u62ff\u8d77\u9910\u76d8\u9009\u4e86\u4e00\u4e9b\u65e9\u70b9\uff0c\u8d70\u8fc7\u53bb\u8ddf\u7fdf\u4e39\u9752\u5750\u5230\u4e00\u8fb9\uff0c\u95ee\u9053\uff1a\u201c\u6628\u5929\u7761\u5f97\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5012\u8fd8\u597d\uff0c\u8c22\u59d0\u6628\u5929\u7761\u5f97\u5982\u4f55\uff0c\u6211\u8fd8\u8981\u8ddf\u9152\u5e97\u6295\u8bc9\u5462\uff0c\u5e8a\u600e\u4e48\u53ef\u4ee5\u8d34\u7740\u5899\u58c1\u6446\u5462\uff01\u201d\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u665a\u6674\u7c89\u8138\u4e00\u7ea2\uff0c\u6628\u5929\u591c\u91cc\u5f00\u59cb\u8fd8\u6ce8\u610f\u7740\uff0c\u540e\u6765\u5c31\u653e\u5f00\u4e86\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u5e8a\u5934\u6709\u5728\u649e\u51fb\u5899\u58c1\uff0c\u770b\u5230\u7fdf\u4e39\u9752\u7ea2\u5507\u561f\u7740\u671d\u95e8\u53e3\u5476\u5634\uff0c\u665a\u6674\u56de\u5934\u770b\u8fc7\u53bb\uff0c\u5f20\u606a\u6b63\u4e00\u8138\u65e0\u8f9c\u7684\u8d70\u8fdb\u6765\uff0c\u665a\u6674\u7684\u8138\u53c8\u7ea2\u4e86\u4e00\u5206\uff0c\u80e1\u4e71\u5403\u8fc7\u4e24\u53e3\u65e9\u70b9\uff0c\u558a\u4e0a\u52a9\u7406\u5f20\u5ead\uff0c\u76f4\u63a5\u53bb\u63a5\u82b7\u5f64\u4e58\u98de\u673a\u56de\u6d77\u5dde\u4e86\uff0c\u4e5f\u6ca1\u6709\u8ddf\u53f6\u5efa\u658c\u3001\u9648\u4fe1\u751f\u3001\u8bb8\u9e3f\u4f2f\u4ed6\u4eec\u6253\u62db\u547c\u3002</p></article>"}}}]);
