(window.webpackJsonp=window.webpackJsonp||[]).push([[970],{993:function(p){p.exports={chapterTitle:null,title:"\u7b2c967\u7ae0 \u63d0\u524d\u8ba1\u5212",content:"<article><h2>\u7b2c967\u7ae0 \u63d0\u524d\u8ba1\u5212</h2><p>\u3000\u3000\u4e0d\u6653\u5f97\u5927\u4f2f\u54ea\u4e2a\u9f3b\u5b50\u55c5\u5230\u81ea\u5df1\u6216\u8005\u662f\u5988\u5988\u56de\u6d77\u5dde\u6765\uff0c\u5f20\u606a\u76ef\u7740\u95e8\u7981\u5c4f\u5e55\u770b\u4e86\u6709\u51e0\u79d2\u949f\uff1b\u4ed6\u8ba9\u5085\u4fca\u56de\u5bb6\u53bb\u8ddf\u59bb\u5b50\u3001\u5973\u513f\u56e2\u805a\uff0c\u4e0d\u8fc7\u968f\u884c\u7684\u5176\u4ed6\u5b89\u4fdd\u4eba\u5458\u4ee5\u53ca\u4e34\u65f6\u8bf7\u6765\u7167\u6599\u751f\u6d3b\u7684\u5de5\u4eba\u90fd\u4f4f\u5728\u9694\u58c1\u522b\u5885\u91cc\uff0c\u8fc7\u4e86\u51e0\u79d2\u949f\u5c31\u6709\u4eba\u4ece\u9694\u58c1\u697c\u91cc\u8d70\u51fa\u6765\u51fa\u73b0\u5728\u5c4f\u5e55\u91cc\u76d8\u95ee\u5927\u4f2f\u3002</p><p>\u3000\u3000\u201c\u662f\u8c01\u8fc7\u6765\u4e86\uff1f\u201d\u6881\u683c\u73cd\u89c1\u5f20\u606a\u7ad9\u5728\u95e8\u7981\u5c4f\u5e55\u524d\u770b\u4e86\u597d\u4e00\u4f1a\u513f\u4e5f\u4e0d\u5f00\u95e8\uff0c\u5947\u602a\u7684\u95ee\u4e86\u4e00\u58f0\u3002</p><p>\u3000\u3000\u201c\u5927\u4f2f\u8fc7\u6765\u4e86\u2026\u2026\u201d\u5f20\u606a\u8fd9\u624d\u63ff\u4e0b\u9065\u63a7\u9662\u5b50\u5927\u95e8\u7684\u6309\u94ae\uff0c\u4fbf\u5750\u56de\u6c99\u53d1\u4e0a\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5f20\u77e5\u5fae\u4f5d\u507b\u7740\u80cc\u7ad9\u5728\u95e8\u5916\uff0c\u72b9\u72b9\u8c6b\u8c6b\u7684\u5f80\u91cc\u770b\uff0c\u4e5f\u4e0d\u6562\u8e0f\u811a\u8fdb\u6765\u3002\u5f20\u606a\u77a5\u773c\u770b\u7740\u4ed6\u7684\u811a\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u7ed9\u5f20\u606a\u8fd9\u4e48\u770b\u7740\uff0c\u5f20\u77e5\u5fae\u5fc3\u91cc\u66f4\u662f\u53d1\u865a\uff0c\u5927\u534a\u751f\u5728\u5b98\u573a\u5b66\u7684\u5bdf\u989c\u89c2\u8272\u672c\u4e8b\uff0c\u8fd9\u4f1a\u513f\u90fd\u6d3e\u4e0d\u4e0a\u534a\u70b9\u7528\u573a\uff0c\u55eb\u5685\u7740\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u5f00\u53e3\u7684\u597d\uff1b\u518d\u8bf4\u540e\u9762\u8fd8\u6709\u4e2a\u7a7f\u897f\u670d\u7684\u4eba\u8b66\u60d5\u7684\u76ef\u7740\u4ed6\uff0c\u597d\u50cf\u4e00\u6709\u4e0d\u5bf9\u52b2\u5c31\u4f1a\u51b2\u4e0a\u6765\u62fd\u7740\u4ed6\u7684\u8116\u5b50\u5c06\u4ed6\u62d6\u51fa\u53bb\u4f3c\u7684\uff0c\u8ba9\u4ed6\u5fc3\u91cc\u5f88\u522b\u626d\u3002</p><p>\u3000\u3000\u8981\u602a\u53ea\u602a\u8001\u4e8c\u5bb6\u8fd9\u5c0f\u5b50\u592a\u5996\u5b7d\u4e86\uff0c\u7ffb\u624b\u4e3a\u4e91\u3001\u8986\u624b\u4e3a\u96e8\uff0c\u56db\u4e94\u5e74\u95f4\u6574\u51fa\u8bfa\u5927\u7684\u9526\u6e56\u5e1d\u56fd\u6765\u3002\u5373\u4f7f\u8fd9\u4e9b\u5e74\u8c01\u90fd\u4e0d\u628a\u9526\u6e56\u7684\u4e8b\u60c5\u8ddf\u4ed6\u8bf4\uff0c\u5f20\u77e5\u5fae\u53c8\u4e0d\u662f\u778e\u5b50\uff0c\u4ece\u65c1\u4eba\u7684\u53ea\u8a00\u7247\u8bed\u3001\u7ec6\u5fae\u4e3e\u884c\u91cc\u5c31\u80fd\u770b\u5230\u4eca\u65e5\u7684\u5f20\u5bb6\u5728\u6d77\u5dde\u62e5\u6709\u5f88\u96be\u7528\u8a00\u55bb\u5f62\u5bb9\u7684\u6743\u52bf\u4e0e\u5730\u4f4d\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u5230\u5e95\u8981\u987e\u7740\u5f20\u5bb6\uff0c\u6ca1\u6709\u7ed9\u5f20\u77e5\u5fae\u592a\u96be\u770b\u7684\u8138\u8272\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u7ad9\u8d77\u6765\u4e3b\u52a8\u62db\u547c\u7684\u610f\u601d\u3002\u5f20\u77e5\u975e\u7684\u59bb\u5b50\u95ee\u9053\uff1a\u201c\u5927\u54e5\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u5fae\u8fd9\u624d\u5f80\u5ba2\u5385\u91cc\u632a\u4e86\u4e00\u6b65\uff0c\u8bf4\u8d77\u8bdd\u6765\u6709\u4e9b\u8bed\u65e0\u4f26\u6b21\uff1a\u201c\u59d1\u672c\u6765\u4e5f\u8981\u8fc7\u6765\uff0c\u8fd9\u4e0b\u96ea\u5929\uff0c\u7ed9\u51b7\u98ce\u5439\u7740\u53ef\u4e0d\u597d\u2014\u2014\u542c\u522b\u4eba\u8bf4\uff0c\u8fd8\u4ee5\u4e3a\u8001\u4e8c\u4e5f\u4e00\u9053\u56de\u6765\u5462\uff0c\u539f\u6765\u683c\u73cd\u8ddf\u5c0f\u606a\u56de\u6765\u4e86\uff0c\u5f20\u5955\u7684\u5a5a\u4e8b\uff0c\u4f60\u4eec\u662f\u5f20\u5955\u7684\u5a76\u5a76\uff0c\u5a5a\u59fb\u5927\u4e8b\u8fd8\u662f\u8981\u542c\u542c\u4f60\u4eec\u7684\u610f\u89c1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u79cd\u4e8b\uff0c\u6211\u4eec\u63ba\u4ec0\u4e48\u624b\u554a\uff0c\u6709\u4ec0\u4e48\u8981\u5546\u91cf\uff0c\u4f60\u8fd8\u662f\u627e\u5b59\u5a77\u5979\u5bb6\u4eba\u5546\u91cf\u53bb\u5427\u2026\u2026\u201d\u5f20\u606a\u51b7\u4e0d\u4e01\u523a\u4e86\u4e00\u53e5\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u7406\u4e5f\u4e0d\u7406\u4ed6\u5927\u4f2f\uff0c\u8ddf\u4ed6\u5988\u8bf4\u4e86\u4e00\u58f0\uff0c\u201c\u6211\u6709\u4e8b\u51fa\u53bb\u4e00\u4e0b\uff0c\u591c\u91cc\u4e0d\u8981\u7ed9\u6211\u7559\u95e8\u3002\u201d\u6293\u8d77\u6c99\u53d1\u4e0a\u7684\u5916\u5957\u5c31\u5f80\u5916\u8d70\u3002\u521a\u8981\u51fa\u95e8\uff0c\u665a\u6674\u8d70\u5230\u95e8\u53e3\u5dee\u70b9\u7ed9\u649e\u4e0a\uff0c\u665a\u6674\u4f38\u5934\u5f80\u91cc\u770b\u4e86\u770b\uff0c\u8ddf\u7740\u5f20\u606a\u5f80\u53f0\u9636\u4e0b\u8d70\u4e86\u4e24\u6b65\uff0c\u538b\u7740\u58f0\u97f3\u5a07\u55d4\u9053\uff1a\u201c\u8fd9\u662f\u53d1\u54ea\u95e8\u5b50\u813e\u6c14\uff0c\u662f\u4e0d\u662f\u60f3\u501f\u673a\u91d1\u8749\u8131\u58f3\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u70b9\u5c0f\u5fc3\u601d\u7ed9\u665a\u6674\u8f7b\u6613\u6233\u7a7f\uff0c\u5f20\u606a\u56de\u5934\u770b\u4e86\u770b\u5ba2\u5385\u91cc\u5927\u4f2f\u8fd8\u80c6\u98a4\u5fc3\u8df3\u7684\u6837\u5b50\uff0c\u8146\u7740\u8138\u8ddf\u665a\u6674\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u597d\u8981\u8bf7\u8bb8\u601d\u8fd8\u6709\u4f60\u5403\u591c\u5bb5\u7684\u2026\u2026\u8981\u4e0d\u6211\u4eec\u4e09\u4e2a\u4eba\u665a\u4e0a\u518d\u7761\u4e39\u4e95\u5df7\u90a3\u680b\u5b85\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u4e0d\u8981\u626f\u4e0a\u6211\uff0c\u4e0d\u6253\u6270\u4f60\u98ce\u6d41\u5feb\u6d3b\u53bb\u2026\u2026\u201d\u665a\u6674\u4fcf\u8138\u6709\u4e9b\u5fae\u7ea2\uff0c\u60f3\u8d77\u90a3\u5929\u7684\u4e09\u4eba\u884c\u4e8b\u60c5\u90fd\u89c9\u5f97\u8352\u5510\uff0c\u8352\u5510\u4e00\u6b21\u8fd8\u4e0d\u591f\u554a\uff1f\u5550\u4e86\u5f20\u606a\u4e00\u58f0\uff0c\u4e5f\u4e0d\u62e6\u4ed6\u53bb\u89c1\u8bb8\u601d\u3002</p><p>\u3000\u3000\u5f20\u606a\u6325\u624b\u8ba9\u7ad9\u5728\u53f0\u9636\u4e0a\u7684\u4fdd\u5168\u4eba\u5458\u90fd\u56de\u53bb\u4f11\u606f\uff0c\u4ed6\u5750\u8fdb\u8f66\u91cc\uff0c\u5c06\u8f66\u5012\u51fa\u9662\u5b50\uff0c\u6ca1\u6709\u9a6c\u4e0a\u79bb\u5f00\uff0c\u6309\u4e0b\u8f66\u7a97\u73bb\u7483\uff0c\u671d\u665a\u6674\u62db\u4e86\u62db\u624b\u3002</p><p>\u3000\u3000\u5929\u4e0a\u8fd8\u5728\u98d8\u7740\u96ea\uff0c\u82b1\u94fa\u91cc\u90fd\u79ef\u4e86\u4e00\u5c42\uff0c\u665a\u6674\u4e0d\u6653\u5f97\u5f20\u606a\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8d70\u8fd1\u89c1\u4ed6\u6253\u5f00\u8f66\u95e8\u4fbf\u5750\u4e86\u8fdb\u53bb\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u624b\u6367\u8fc7\u6765\u5c31\u8981\u543b\u81ea\u5df1\uff0c\u665a\u6674\u5413\u5f97\u8fde\u8fde\u8eb2\u95ea\uff0c\u8eb2\u4e0d\u5f00\u7ed9\u4ed6\u543b\u4e86\u4e00\u6c14\uff0c\u624d\u5fc3\u7830\u7830\u4e71\u8df3\u7684\u5550\u9a82\u9053\uff1b\u201c\u8981\u6b7b\u554a\uff0c\u7ed9\u4eba\u770b\u89c1\u600e\u4e48\u529e\uff1f\u4f60\u53bb\u7978\u5bb3\u8bb8\u601d\u90a3\u5c0f\u9a9a\u8e44\u5b50\u5427\uff0c\u4e0d\u8981\u6765\u60f9\u6211\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u54c8\u54c8\u4e00\u7b11\uff0c\u8ba9\u665a\u6674\u4e0b\u8f66\u53bb\uff0c\u6325\u4e86\u6325\u624b\u9053\u522b\uff0c\u5f00\u8f66\u671d\u8bb8\u601d\u5bb6\u9a76\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5de5\u4e1a\u65b0\u533a\u7684\u9526\u6e56\u65b0\u57ce\u8fdb\u5165\u5efa\u8bbe\u7684\u9ad8\u5cf0\u671f\uff0c\u8bb8\u601d\u5f88\u591a\u65f6\u95f4\u90fd\u7559\u5728\u6d77\u5dde\uff0c\u53ea\u662f\u5f20\u606a\u56de\u6d77\u5dde\u7684\u673a\u4f1a\u5374\u662f\u5c11\u4e86\uff0c\u800c\u5f20\u606a\u56de\u6d77\u5dde\uff0c\u52a8\u9759\u90fd\u4f1a\u5f88\u5927\uff0c\u8981\u662f\u53ea\u4e3a\u5077\u60c5\u8ba9\u8bb8\u601d\u53bb\u5efa\u90ba\uff0c\u53c8\u592a\u59d4\u5c48\u5979\u4e86\u3002</p><p>\u3000\u3000\u96ea\u4e00\u76f4\u5728\u4e0b\uff0c\u8def\u9762\u8fd8\u6ca1\u6709\u79ef\u8d77\u6765\uff0c\u8f66\u524d\u706f\u626b\u8fc7\uff0c\u80fd\u7167\u51fa\u8def\u7259\u8fb9\u7684\u8349\u6728\u679d\u53f6\u90fd\u79ef\u4e86\u8584\u8584\u7684\u4e00\u5c42\uff0c\u8ddf\u767d\u971c\u4f3c\u7684\uff0c\u5927\u7247\u7684\u96ea\u82b1\u5c31\u50cf\u9ed1\u8272\u7684\u8774\u8776\u5728\u591c\u7a7a\u91cc\u98d8\u821e\uff0c\u5f20\u606a\u63a5\u5230\u8bb8\u601d\u4e70\u4e86\u4e00\u4e9b\u5403\u98df\u5c31\u76f4\u63a5\u5230\u4e39\u4e95\u5df7\u7684\u5b85\u5b50\uff0c\u76f8\u62e5\u5728\u4e8c\u697c\u7684\u5367\u5ba4\u91cc\u900f\u8fc7\u843d\u5730\u5927\u7a97\u770b\u98d8\u843d\u5230\u9732\u53f0\u4e0a\u7684\u96ea\u3002</p><p>\u3000\u3000\u201c\u4f60\u627e\u4ec0\u4e48\u501f\u53e3\u6e9c\u51fa\u6765\u7684\uff1f\u201d\u8bb8\u601d\u7279\u610f\u6ca1\u6709\u6253\u7a7a\u8c03\uff0c\u4e24\u4eba\u62e5\u7740\u88ab\u5b50\u5750\u7740\uff0c\u6709\u79cd\u56f4\u7089\u89c2\u96ea\u7684\u611f\u89c9\uff0c\u5979\u90fd\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u80fd\u8fd9\u4e48\u65e9\u8131\u8eab\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u544a\u8bc9\u5979\u5f20\u5955\u8981\u4e0e\u5b59\u5a77\u7ed3\u5a5a\u7684\u4e8b\u60c5\uff0c\u5c06\u5979\u67d4\u8f6f\u6e29\u70ed\u6563\u53d1\u7740\u8ff7\u4eba\u5e7d\u9999\u7684\u8eab\u4f53\u6402\u5728\u676f\u91cc\uff0c\u5728\u5979\u8033\u8fb9\u8f7b\u8bed\uff1a\u201c\u5728\u8fd9\u70b9\u4e0a\uff0c\u6211\u8981\u5dee\u5f20\u5955\u597d\u8fdc\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e0d\u662f\u4e2a\u8d2a\u5fc3\u7684\u5973\u4eba\u5427\uff1f\u201d\u8bb8\u601d\u62ac\u8d77\u5934\u6765\uff0c\u597d\u8ba9\u9999\u6ed1\u5982\u8102\u7684\u8138\u988a\u66f4\u8d34\u8fd1\u5f20\u606a\u5e26\u80e1\u5b50\u6e23\u7684\u4e0b\u9894\uff0c\u7738\u5b50\u671b\u7740\u5f20\u606a\uff0c\u4f38\u624b\u62cd\u4e86\u62cd\u5f20\u606a\u7684\u8138\u988a\uff0c\u201c\u4f60\u80e1\u5b50\u523a\u4eba\u4e86\uff0c\u53ef\u60dc\u6ca1\u6709\u5243\u987b\u5200\uff0c\u4e0d\u7136\u5c31\u5e2e\u4f60\u522e\u80e1\u5b50\u2014\u2014\u4eca\u5929\u6211\u5988\u7ed9\u6211\u7238\u522e\u80e1\u5b50\u6765\u7740\uff0c\u5c31\u60f3\u7740\u5728\u4f60\u4e0b\u5df4\u7ec3\u4e00\u7ec3\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u4e0b\u9894\u5728\u8bb8\u601d\u7684\u8138\u988a\u8e6d\u4e86\u4e24\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u966a\u6211\u53bb\u5317\u4eac\u5427\uff0c\u5317\u4eac\u4e5f\u5728\u4e0b\u96ea\uff0c\u4e0d\u4e0b\u96ea\u7684\u5317\u4eac\u7070\u8499\u8499\u7684\uff0c\u4e0d\u597d\u770b\uff0c\u4e0b\u4e86\u96ea\uff0c\u5e94\u8be5\u597d\u770b\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8bb8\u601d\u6709\u4e9b\u5fe4\u4e0e\u5f20\u606a\u5728\u5176\u4ed6\u573a\u5408\u516c\u5f00\u9732\u9762\uff0c\u89c9\u5f97\u592a\u5192\u9669\u53ef\u80fd\u4f1a\u6405\u4e71\u6b64\u65f6\u5e73\u9759\u7684\u751f\u6d3b\uff0c\u7ecf\u5386\u4e86\u592a\u591a\u7684\u4e8b\u60c5\uff0c\u8bb8\u601d\u601d\u6765\u60f3\u53bb\u4e5f\u53ea\u6709\u8fd9\u6837\u7684\u751f\u6d3b\u9002\u5408\u81ea\u5df1\u7684\u6027\u5b50\uff0c\u6709\u81ea\u5df1\u5728\u505a\u4e5f\u9ad8\u5174\u505a\u7684\u4e8b\u4e1a\uff0c\u6709\u7231\u7684\u4eba\uff0c\u4e5f\u80fd\u4e0e\u5bb6\u4eba\u65f6\u5e38\u751f\u6d3b\u5728\u4e00\u8d77\uff0c\u770b\u7740\u4ed6\u4eec\u7684\u751f\u6d3b\u4e5f\u5f88\u5b89\u5b9a\u77e5\u8db3\uff0c\u4e5f\u6839\u672c\u6ca1\u6709\u5fc3\u601d\u5e0c\u671b\u8d77\u4ec0\u4e48\u6ce2\u6f9c\uff0c\u5979\u89c1\u5f20\u606a\u7684\u773c\u795e\u6bd4\u8f83\u575a\u6301\uff0c\u53ea\u5f97\u8bf4\u9053\uff0c\u201c\u7b97\u4e86\uff0c\u53ef\u4e0d\u60f3\u5728\u5317\u4eac\u9001\u4f60\u53bb\u8ddf\u5510\u5a67\u90a3\u4e2b\u5934\u76f8\u4f1a\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u5f20\u606a\u7acb\u9a6c\u6cc4\u6c14\u7684\u4e0d\u518d\u575a\u6301\u4ec0\u4e48\uff0c\u53c8\u8ddf\u8bb8\u601d\u8bf4\u8d77\u91d1\u5c71\u6e56\u89c2\u9e1f\u7684\u4e8b\u60c5\uff0c\u518d\u8981\u5728\u4e1c\u5858\u5efa\u751f\u6001\u4fdd\u62a4\u533a\uff0c\u5728\u4fdd\u62a4\u533a\u7684\u5317\u9762\u4e5f\u53ef\u4ee5\u9002\u5ea6\u7684\u5212\u51fa\u4e00\u5757\u533a\u57df\u5efa\u6e7f\u5730\u516c\u56ed\uff0c\u4e3a\u4e86\u4fbf\u4e8e\u4eba\u4eec\u89c2\u9e1f\uff0c\u5728\u6e7f\u5730\u516c\u56ed\u7684\u8fb9\u7f18\u5efa\u4e00\u4e9b\u6709\u7279\u8272\u7684\u3001\u4e0e\u81ea\u7136\u73af\u5883\u76f8\u6eb6\u7684\u5efa\u7b51\u4e5f\u662f\u5fc5\u8981\u7684\uff0c\u5f20\u606a\u8981\u8bb8\u601d\u4e00\u8d77\u8fc7\u53bb\u770b\u770b\uff0c\u8981\u600e\u4e48\u89c4\u5212\u624d\u597d\u3002</p><p>\u3000\u3000\u201c\u90a3\u7b49\u660e\u5e74\u6625\u5929\u5427\uff0c\u4f60\u5e26\u6211\u8fc7\u53bb\u89c2\u9e1f\uff0c\u4e0d\u77e5\u9053\u9e1f\u6f6e\u662f\u4e0d\u662f\u771f\u50cf\u4f60\u8bf4\u7684\u8fd9\u4e48\u58ee\u89c2\u2026\u2026\u201d</p><p>\u3000\u3000\u5dee\u4e0d\u591a\u5341\u70b9\u949f\uff0c\u5f20\u606a\u63a5\u5230\u4ed6\u5988\u6253\u6765\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u4ed6\u5976\u5976\u521a\u521a\u6253\u7535\u8bdd\u8fc7\u53bb\uff0c\u8bf4\u8981\u662f\u5f20\u5955\u4e0e\u5b59\u5a77\u7684\u5a5a\u623f\u4e0d\u5e03\u7f6e\u5728\u5f20\u5bb6\u95e8\u91cc\uff0c\u800c\u5e03\u7f6e\u5728\u5973\u65b9\u5bb6\uff0c\u8fd9\u5c31\u8981\u7b97\u5165\u8d58\u8fc7\u53bb\uff0c\u8fd9\u600e\u4e48\u80fd\u884c\uff1f</p><p>\u3000\u3000\u5f20\u606a\u90fd\u4e0d\u6653\u5f97\u8981\u600e\u4e48\u8bc4\u4ef7\u8001\u4eba\u5bb6\u7684\u8111\u7b4b\uff0c\u53ef\u80fd\u5c31\u4e5f\u4e0a\u4e00\u4ee3\u7684\u4eba\u8ba1\u8f83\u8fd9\u4e2a\uff0c\u4e5f\u4e0d\u80fd\u8ddf\u8001\u4eba\u5bb6\u4ed6\u4eec\u62d7\u8fd9\u4e2a\u7406\uff0c\u8981\u662f\u4e0d\u987a\u7740\u8001\u592a\u592a\u7684\u610f\u601d\uff0c\u8001\u592a\u592a\u80fd\u51b0\u5929\u96ea\u5730\u91cc\u4ece\u4e1c\u793e\u8d76\u8fc7\u6765\u3002\u8fd9\u80cc\u540e\u591a\u534a\u4e5f\u6709\u5927\u4f2f\u5728\u4f7f\u529b\uff0c\u5f20\u606a\u5728\u7535\u8bdd\u91cc\u8ddf\u4ed6\u5988\u8bf4\uff1a\u201c\u5c31\u7b97\u5728\u8001\u5bb6\u7ed3\u5a5a\uff0c\u4e5f\u662f\u53ea\u4f4f\u4e00\u4e24\u591c\uff0c\u4e89\u8fd9\u4e2a\u540d\u5206\u6ca1\u6709\u610f\u601d\uff0c\u5976\u5976\u8ddf\u7740\u63ba\u4e4e\u4ec0\u4e48\u52b2\u554a\uff1f\u201d\u8f7b\u53f9\u4e86\u4e00\u58f0\uff0c\u201c\u8981\u4e0d\u4f60\u5148\u95ee\u4e00\u4e0b\u5b59\u5a77\u7684\u610f\u601d\uff1f\u603b\u5f97\u8981\u8ba9\u5927\u4f2f\u4eb2\u81ea\u5230\u5b59\u5bb6\u53bb\u767b\u95e8\u8c22\u7f6a\uff0c\u770b\u4ed6\u5f53\u5e74\u505a\u7684\u4ec0\u4e48\u597d\u4e8b\uff01\u201d</p><p>\u3000\u3000\u5b59\u5a77\u813e\u6c14\u8fd8\u662f\u8f6f\u7684\uff0c\u6ca1\u6709\u575a\u6301\u4ec0\u4e48\uff0c\u5012\u662f\u6881\u683c\u73cd\u66ff\u5979\u505a\u4e3b\u8981\u5f20\u77e5\u5fae\u592b\u5987\u53bb\u5b59\u5bb6\u5e2e\u5f20\u5955\u63d0\u4eb2\u9001\u65e5\u5b50\uff0c\u5f20\u606a\u5012\u662f\u4e0d\u518d\u8fc7\u95ee\u8fd9\u4e9b\u7410\u788e\u7684\u5bb6\u4e8b\uff0c\u9664\u4e86\u5f20\u5955\u4e0e\u5b59\u5a77\u7ed3\u5a5a\u4e4b\u5916\uff0c\u8868\u5144\u6881\u6587\u6c5f\u4e0e\u5973\u53cb\u738b\u5f69\u73b2\u8fc7\u4e24\u5929\u8981\u5728\u5317\u4eac\u8ba2\u5a5a\u3002\u8fd9\u8fb9\u5bb6\u957f\u9664\u4e86\u5c0f\u8205\u6881\u56fd\u76db\u4e0e\u5c0f\u8205\u5988\u4e4b\u5916\uff0c\u4e5f\u6ca1\u6709\u5927\u8bf7\u5bb4\u5e2d\u7684\u610f\u601d\uff0c\u5f20\u606a\u53ea\u662f\u987a\u4fbf\u5230\u5317\u4eac\u7ed5\u4e00\u5708\uff0c\u8e6d\u4e00\u987f\u8ba2\u5a5a\u5bb4\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u53bb\u5317\u4eac\u4e3b\u8981\u662f\u5173\u5fc3\u6606\u817e\u5728\u7ebf\u5230\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u7684\u65f6\u95f4\u5b89\u6392\u3002</p><p>\u3000\u3000\u4e92\u8054\u7f51\u70ed\u6f6e\u6e10\u6e10\u6c79\u6d8c\u8d77\u6765\uff0c\u867d\u8bf4\u6606\u817e\u5728\u7ebf\u7684\u5d1b\u8d77\u6709\u7740\u592a\u591a\u5076\u7136\u7684\u56e0\u7d20\uff0c\u4f46\u662f\u6606\u817e\u5728\u7ebf\u5bf9\u5168\u7403\u7684\u4e92\u8054\u7f51\u4e1a\u5f71\u54cd\u4e0d\u5927\uff0c\u8fd9\u91cc\u7684\u5386\u53f2\u4f9d\u65e7\u5faa\u7740\u65e2\u5b9a\u7684\u8f68\u8ff9\u5728\u524d\u8fdb\u3002</p><p>\u3000\u3000\u4ea7\u4e1a\u7684\u53d1\u5c55\u6709\u7740\u81ea\u8eab\u7684\u89c4\u5f8b\uff0c\u96f6\u96f6\u5e74\u6625\u5b63\u524d\u540e\u4e0e\u4e92\u8054\u7f51\u76f8\u5173\u7684\u79d1\u6280\u80a1\u6ce1\u6cab\u7834\u706d\u4e5f\u4e0d\u662f\u4ec0\u4e48\u96be\u4ee5\u60f3\u8c61\u7684\u4e8b\u60c5\u3002\u6ce1\u6cab\u7834\u706d\u4e0d\u7834\u706d\uff0c\u5f20\u606a\u5b9e\u5728\u4e0d\u592a\u5173\u5fc3\uff0c\u4e0d\u8fc7\u7834\u706d\u4e4b\u524d\u7684\u72c2\u70ed\u5012\u662f\u8ba9\u4eba\u8bb0\u5fc6\u6df1\u523b\u3002</p><p>\u3000\u3000\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u4e4b\u540e\uff0c\u673a\u6784\u6295\u8d44\u8005\u4ee5\u53ca\u53d1\u8d77\u4eba\u80a1\u4e1c\u3001\u98ce\u9669\u6295\u8d44\u57fa\u91d1\u6240\u6301\u7684\u80a1\u7968\u6709\u81f3\u5c11\u516d\u4e2a\u6708\u7684\u7981\u552e\u671f\u9650\uff0c\u8981\u60f3\u6293\u4f4f\u8fd9\u4e2a\u6295\u673a\u673a\u4f1a\uff0c\u6606\u817e\u5728\u7ebf\u4e0a\u5e02\u65f6\u95f4\u4e0d\u5e94\u8be5\u5b89\u6392\u5728\u79d1\u738b\u6982\u5ff5\u80a1\u6700\u72c2\u70ed\u7684\u90a3\u6bb5\u65f6\u95f4\uff0c\u800c\u662f\u8981\u5728\u6700\u72c2\u70ed\u90a3\u4e2a\u65f6\u95f4\u70b9\u81f3\u5c11\u518d\u63a8\u524d\u516d\u4e2a\u6708\uff0c\u4e5f\u5c31\u662f\u8981\u5728\u660e\u5e74\u79cb\u5b63\u4e4b\u524d\u5b89\u6392\u4e0a\u5e02\u624d\u80fd\u8d76\u5f97\u4e0a\u8fd9\u6b21\u6295\u673a\u76db\u5bb4\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6606\u817e\u8981\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\uff0c\u5c31\u8981\u9075\u5faa\u7f8e\u56fd\u7684\u6e38\u620f\u89c4\u5219\uff0c\u4e5f\u8981\u8ba9\u5728\u7f8e\u56fd\u8d44\u672c\u8bc1\u5238\u5e02\u573a\u6709\u8bdd\u8bed\u6743\u7684\u91d1\u878d\u673a\u6784\u5206\u4eab\u6606\u817e\u4e0a\u5e02\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u9664\u4e86\u9526\u6e56\u4ee5\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u7684\u540d\u4e49\u63d0\u4f9b\u7684\u6700\u521d\u4e0d\u5230\u4e09\u5343\u4e07\u53d1\u5c55\u8d44\u91d1\u5916\uff0c\u6606\u817e\u5728\u7ebf\u8fd8\u6709\u8fc7\u4e24\u6b21\u91cd\u8981\u7684\u98ce\u9669\u878d\u8d44\uff0c\u7136\u800c\u9526\u6e56\u901a\u8fc7\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u4e00\u5bb6\u5c31\u6301\u6709\u6606\u817e\u5728\u7ebf\u7684\u80a1\u6743\u6bd4\u4f8b\u5c31\u9ad8\u8fbe52\uff05\uff0c\u52a0\u4e0a\u7ba1\u7406\u5c4212\uff05\u7684\u6301\u80a1\uff0c\u63a7\u80a1\u8fbe64\uff05\u3002</p><p>\u3000\u3000\u4ee5\u8fd9\u6b21\u7684\u80a1\u6743\u7ed3\u6784\u60f3\u8981\u5728\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u6210\u529f\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u8fd9\u5c31\u8981\u5c06\u8fdb\u884c\u7b2c\u4e09\u6b21\u7684\u878d\u8d44\uff0c\u5f15\u8fdb\u66f4\u591a\u7684\u98ce\u9669\u6295\u8d44\u57fa\u91d1\uff0c\u4e5f\u8981\u5c06\u9526\u6e56\u7684\u6301\u80a1\u6bd4\u4f8b\u8fdb\u4e00\u6b65\u7a00\u91ca\u2014\u2014\u8fd9\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\u60c5\uff0c\u4efb\u4f55\u597d\u4e8b\u90fd\u6ca1\u6cd5\u8d2a\u592a\u591a\u7684\u3002</p></article>"}}}]);
