(window.webpackJsonp=window.webpackJsonp||[]).push([[974],{997:function(p){p.exports={chapterTitle:null,title:"\u7b2c787\u7ae0 \u52a8\u9759",content:"<article><h2>\u7b2c787\u7ae0 \u52a8\u9759</h2><p>\u3000\u3000\u845b\u836b\u5747\u8eab\u4e3a\u5609\u4fe1\u96c6\u56e2\u7684\u7ee7\u627f\u4eba\uff0c\u8eab\u9677\u5371\u9669\u9a7e\u8f66\u81f4\u4eba\u6b7b\u4ea1\u3001\u4e8b\u540e\u8d3f\u4e70\u6b7b\u8005\u5bb6\u5c5e\u7684\u4e11\u95fb\u5bf9\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u4e3b\u8981\u7684\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u6709\u7740\u4e00\u76f4\u63a5\u8d1f\u9762\u5f71\u54cd\u3002</p><p>\u3000\u3000\u867d\u7136\u6ca1\u6709\u4e25\u91cd\u5230\u8981\u88ab\u62d8\u6355\u7684\u7a0b\u5ea6\uff0c\u4f46\u662f\u845b\u836b\u5747\u5728\u6b64\u671f\u95f4\u88ab\u9650\u5236\u79bb\u6e2f\uff0c\u52a0\u4e0a\u4ed6\u8fd9\u51e0\u5929\u6765\u7ed9\u8f66\u7978\u4e8b\u4ef6\u6405\u5f97\u5934\u6655\u8111\u80c0\uff0c\u4e5f\u5c31\u6ca1\u6709\u5fc3\u601d\u7533\u8bf7\u7279\u522b\u624b\u7eed\u79bb\u6e2f\uff0c\u5c31\u7559\u5728\u5609\u4fe1\u96c6\u56e2\u5728\u4e2d\u73af\u7684\u603b\u697c\u5927\u53a6\u91cc\u5904\u7406\u5185\u5730\u7684\u4e8b\u52a1\u3002</p><p>\u3000\u3000\u79d1\u738b\u7535\u5b50\u789f\u673a\u4e1a\u52a1\u906d\u53d7\u91cd\u632b\uff0c\u4e5f\u770b\u4e0d\u5230\u6d77\u5916\u5e02\u573a\u5728\u77ed\u671f\u5185\u6709\u6062\u590d\u7684\u8ff9\u8c61\uff0c\u845b\u836b\u5747\u81ea\u7136\u4e0d\u7518\u5fc3\u5c06\u4ed6\u7684\u7cbe\u529b\u90fd\u6d88\u8017\u5728\u79d1\u738b\u6d77\u5916\u516c\u53f8\u91cc\uff0c\u4fbf\u5c06\u79d1\u738b\u6d77\u5916\u516c\u53f8\u7684\u4e1a\u52a1\u59d4\u6258\u7ed9\u526f\u624b\u5904\u7406\uff0c\u4ed6\u91cd\u56de\u5609\u4fe1\u7535\u5b50\u8d1f\u8d23\u4e3b\u5bfc\u5230\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\u7684\u5efa\u8bbe\u91cd\u4efb\u3002</p><p>\u3000\u3000\u8f66\u7978\u7684\u95ee\u9898\u5b8c\u5168\u53ef\u80fd\u4ea4\u7ed9\u5f8b\u5e08\u5176\u5904\u7406\uff0c\u4e0d\u9700\u8981\u5f88\u62c5\u5fc3\uff0c\u5728\u9999\u6e2f\uff0c\u5a92\u4f53\u8206\u8bba\u5bf9\u6700\u7ec8\u7684\u6cd5\u5ead\u5224\u51b3\u5f71\u54cd\u5e76\u4e0d\u5927\uff0c\u73b0\u5728\u9700\u8981\u505a\u7684\u5c31\u662f\u8ba9\u5f8b\u5e08\u7ed9\u90a3\u4e9b\u7092\u4f5c\u4e11\u95fb\u7684\u5a92\u4f53\u53d1\u5f8b\u5e08\u51fd\uff0c\u8fd8\u8981\u5c06\u6700\u521d\u62a5\u9053\u8f66\u7978\u7684\u9999\u6e2f\u65b0\u5468\u520a\u7ed9\u544a\u4e0a\u6cd5\u5ead\uff0c\u5927\u7ea6\u518d\u8fc7\u4e00\u6bb5\u65f6\u95f4\uff0c\u5a92\u4f53\u8206\u8bba\u7684\u8d1f\u9762\u5f71\u54cd\u5c31\u4f1a\u6d88\u5f31\u3002\u73b0\u5728\u8981\u62c5\u5fc3\u5c31\u662f\u8fd9\u7c7b\u4e11\u95fb\u5f88\u5bb9\u6613\u7ed9\u5bf9\u624b\u5229\u7528\uff0c\u845b\u836b\u5747\u4e5f\u77e5\u9053\u5b59\u5c1a\u4e49\u3001\u5f20\u606a\u7b49\u4eba\u5c31\u662f\u63a8\u6ce2\u52a9\u6f9c\u7684\u5e55\u540e\u9ed1\u624b\u3002</p><p>\u3000\u3000\u201c\u836b\u5747\uff0c\u5e02\u573a\u7814\u7a76\u90e8\u95e8\u7684\u62a5\u544a\u521a\u9012\u8fc7\u6765\uff0c\u4f60\u4e5f\u6765\u770b\u4e00\u4e0b\u3002\u201d\u845b\u836b\u5747\u5728\u81ea\u5df1\u7684\u529e\u516c\u5ba4\u91cc\u63a5\u5230\u4ed6\u7236\u4eb2\u845b\u660e\u4fe1\u7684\u7535\u8bdd\uff0c\u653e\u4e0b\u624b\u5934\u7684\u6587\u4ef6\uff0c\u5f80\u4ed6\u7236\u4eb2\u845b\u660e\u4fe1\u7684\u529e\u516c\u5ba4\u8d70\u53bb\uff0c\u7ecf\u8fc7\u79d8\u4e66\u5ba4\uff0c\u770b\u89c1\u7a7f\u7740\u7070\u5462\u5957\u88d9\u7684\u79d8\u4e66\u6b63\u5f2f\u8170\u5904\u7406\u590d\u5370\u673a\u4e0a\u7684\u6587\u4ef6\uff0c\u5c41\u80a1\u9ad8\u9ad8\u62f1\u8d77\uff0c\u845b\u836b\u5747\u5728\u79d8\u4e66\u6d51\u5706\u4e30\u6ee1\u7684\u5c41\u80a1\u62cd\u4e86\u4e00\u4e0b\uff0c\u770b\u7740\u79d8\u4e66\u5c16\u53eb\u4e86\u4e00\u58f0\u53c8\u60ca\u53c8\u7f9e\u7684\u62b1\u7740\u6587\u4ef6\u5f80\u5899\u89d2\u843d\u91cc\u9000\u53bb\uff0c\u845b\u836b\u5747\u624d\u5f97\u610f\u7684\u63a8\u5f00\u4ed6\u7236\u4eb2\u845b\u660e\u4fe1\u529e\u516c\u5ba4\u7684\u95e8\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u4ed6\u624b\u91cc\u521a\u62ff\u5230\u4e00\u4efd\u5e02\u573a\u7814\u7a76\u90e8\u95e8\u63d0\u4ea4\u7684\u62a5\u544a\uff0c\u4ed6\u5c06\u62a5\u544a\u9012\u7ed9\u845b\u836b\u5747\uff1a\u201c\u8fd9\u4efd\u62a5\u544a\u4f60\u770b\u4e00\u4e0b\uff1b\u4ee5\u540e\u5c11\u5bf9\u516c\u53f8\u91cc\u7684\u5973\u5b69\u5b50\u6253\u4ec0\u4e48\u4e3b\u610f\uff0c\u8bf7\u5979\u4eec\u8fc7\u6765\u662f\u8981\u5979\u4eec\u6765\u5de5\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u845b\u836b\u5747\u563f\u7136\u7b11\u7740\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u6094\u6539\u7684\u610f\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u5e74\u7237\u7237\u662f\u4e0d\u662f\u4e5f\u8fd9\u6837\u8b66\u544a\u8fc7\u4f60\uff1f\u201d</p><p>\u3000\u3000\u845b\u660e\u4fe1\u65e0\u5948\u7684\u6325\u4e86\u6325\u624b\uff0c\u8ba9\u4ed6\u8d76\u7d27\u770b\u62a5\u544a\u3002\u8fd9\u4efd\u62a5\u544a\u8bc1\u5b9e\u8fd9\u4e24\u5929\u786e\u5b9e\u6709\u8d44\u91d1\u5728\u8bc1\u5238\u5e02\u573a\u4e0a\u5438\u7eb3\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u7968\uff0c\u8ff9\u8c61\u76f8\u5f53\u660e\u663e\uff0c\u5609\u4fe1\u5730\u4ea7\u7684\u80a1\u7968\u5012\u6682\u65f6\u6ca1\u6709\u89c2\u5bdf\u5230\u8d44\u91d1\u8fdb\u5165\u7684\u660e\u663e\u8ff9\u8c61\uff0c\u6216\u8bb8\u9526\u6e56\u524d\u671f\u7684\u76ee\u6807\u5c31\u653e\u5728\u5609\u4fe1\u7535\u5b50\u8eab\u4e0a\u3002\u53e6\u5916\uff0c\u845b\u660e\u5fb7\u8fd9\u4e24\u5929\u5728\u9999\u6e2f\u76f8\u5f53\u6d3b\u8dc3\uff0c\u9891\u9891\u63a5\u89e6\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u8fd9\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u7684\u91cd\u8981\u80a1\u4e1c\uff0c\u6709\u573a\u5916\u6536\u8d2d\u8fd9\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u80a1\u7968\u7684\u610f\u613f\uff0c\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u610f\u56fe\u66f4\u5f3a\u70c8\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u201c\u5c31\u7b97\u6211\u4eec\u5b8c\u5168\u653e\u5f03\u53cd\u6297\uff0c\u522b\u4eba\u8981\u6765\u4e89\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u7684\u63a7\u5236\u6743\uff0c\u5c11\u8bf4\u4e5f\u8981\u52a8\u7528\u4e94\u516d\u5341\u4ebf\u6e2f\u5143\u7684\u8d44\u91d1\u624d\u6709\u53ef\u80fd\u3002\u4e0d\u8fc7\u770b\u60c5\u51b5\uff0c\u4ed6\u4eec\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u5174\u8da3\u66f4\u5927\u4e00\u4e9b\u554a\u2026\u2026\u201d\u845b\u836b\u5747\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u65d7\u4e0b\u53ea\u6709\u7eb8\u4e1a\u4e0e\u7535\u5b50\u5236\u9020\u4e24\u79cd\u4e3b\u8981\u4e1a\u52a1\uff0c\u82e5\u662f\u8ba9\u4ed6\u4eec\u6536\u8d2d\u5609\u4fe1\u7535\u5b50\u6210\u529f\uff0c\u5c06\u80fd\u5728\u6700\u5927\u7a0b\u5ea6\u4e0a\u5f25\u8865\u4ed6\u4eec\u5728\u7535\u5b50\u4ea7\u54c1\u751f\u4ea7\u57fa\u7840\u4e0a\u7684\u4e25\u91cd\u4e0d\u8db3\uff0c\u201d\u845b\u660e\u4fe1\u5750\u5728\u9ed1\u8272\u7684\u5bbd\u76ae\u6905\u4e0a\uff0c\u201c\u53e6\u5916\uff0c\u8c22\u5bb6\u4e0e\u6211\u4eec\u7684\u5408\u4f5c\uff0c\u7edd\u5927\u591a\u6570\u90fd\u662f\u901a\u8fc7\u5609\u4fe1\u7535\u5b50\u8fdb\u884c\u7684\uff0c\u82e5\u8ba9\u4ed6\u4eec\u6536\u8d2d\u5609\u4fe1\u7535\u5b50\u6210\u529f\uff0c\u5c06\u662f\u5bf9\u8c22\u5bb6\u6700\u5927\u7684\u6253\u51fb\u3002\u4ed6\u4eec\u53ef\u662f\u8001\u51a4\u5bb6\u4e86\uff0c\u82e5\u662f\u60f3\u5728\u5e02\u573a\u4e0a\u538b\u5236\u8c22\u5bb6\uff0c\u53ea\u53ef\u80fd\u662f\u2018\u6740\u654c\u4e00\u4e07\u3001\u81ea\u635f\u4e09\u5343\u2019\u7684\u7ed3\u5c40\uff0c\u82e5\u80fd\u901a\u8fc7\u8fd9\u79cd\u624b\u6bb5\u6765\u6253\u51fb\u8c22\u5bb6\uff0c\u4e00\u7bad\u53cc\u96d5\uff0c\u5927\u6982\u518d\u7406\u60f3\u4e0d\u8fc7\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u845b\u836b\u5747\u60f3\u8d77\u8fd9\u4e24\u5929\u8c22\u5251\u5357\u6bd4\u4ee5\u5f80\u66f4\u5bc6\u5207\u7684\u7535\u8bdd\u8054\u7edc\uff0c\u60f3\u5fc5\u4ed6\u4eec\u4e5f\u62c5\u5fc3\u8fd9\u70b9\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u53c8\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u4eec\u7684\u76ee\u6807\u5e94\u8be5\u5c31\u662f\u5609\u4fe1\u7535\u5b50\u4e86\uff0c\u540c\u65f6\u5bf9\u5609\u4fe1\u5730\u4ea7\u52a8\u624b\uff0c\u4e0d\u8fc7\u662f\u4ed6\u4eec\u7684\u8ff7\u9b42\u5f39\uff0c\u60f3\u5206\u6563\u6211\u4eec\u7684\u6ce8\u610f\u529b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u7b97\u4ed6\u4eec\u53ea\u4e89\u5609\u4fe1\u7535\u5b50\u4e00\u5bb6\u516c\u53f8\u7684\u63a7\u5236\u6743\uff0c\u6ca1\u6709\u4e09\u5341\u4ebf\u6e2f\u5143\u7684\u8d44\u91d1\u4e5f\u662f\u4e0d\u73b0\u5b9e\u7684\uff0c\u8fd9\u8fd8\u662f\u6211\u4eec\u5b8c\u5168\u653e\u5f03\u62b5\u6297\u4e0b\u7684\u6700\u7406\u60f3\u7ed3\u679c\uff0c\u201d\u845b\u836b\u5747\u624b\u6307\u538b\u7740\u5e02\u573a\u7814\u7a76\u90e8\u95e8\u63d0\u4ea4\u7684\u62a5\u544a\uff0c\u770b\u7740\u4e0a\u9762\u5217\u51fa\u7684\u6570\u636e\u53d8\u5316\uff0c\u201c\u4ed6\u4eec\u5927\u6982\u4e5f\u4e0d\u4f1a\u611a\u8822\u5230\u4ee5\u4e3a\u6211\u4eec\u662f\u4e56\u4e56\u653e\u5f03\u62b5\u6297\u7684\u5c0f\u7ef5\u7f8a\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u60c5\u51b5\u6bd4\u60f3\u8c61\u4e2d\u590d\u6742\uff0c\u4e5f\u6bd4\u60f3\u8c61\u4e2d\u4e25\u91cd\uff0c\u201d\u845b\u660e\u4fe1\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u9664\u4e86\u8fd9\u4efd\u62a5\u544a\u4e4b\u5916\uff0c\u6211\u624b\u91cc\u8fd8\u6536\u5230\u5609\u4fe1\u7535\u5b50\u4e5d\u4e03\u5e74\u7684\u5168\u5e74\u8d22\u52a1\u62a5\u544a\uff0c\u201d\u845b\u660e\u4fe1\u53c8\u4ece\u62bd\u5c49\u91cc\u62ff\u51fa\u4e00\u53e0\u539a\u539a\u7684\u62a5\u544a\u9012\u7ed9\u513f\u5b50\uff0c\u201c\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u7206\u53d1\u4e4b\u540e\uff0c\u6211\u4eec\u5728\u5e7f\u4e1c\u7684\u5de5\u5382\u7684\u7535\u5b50\u51fa\u53e3\u4e1a\u52a1\u53d7\u5230\u4e25\u91cd\u7684\u5f71\u54cd\uff0c\u4e5d\u4e03\u5e74\u76c8\u5229\u6bd4\u4e5d\u516d\u5e74\u4e0b\u964d80\uff05\uff0c\u4eca\u5e74\u4e00\u5b63\u5ea6\u7684\u60c5\u51b5\u66f4\u4e0d\u5bb9\u4e50\u89c2\u2026\u2026\u4ed6\u4eec\u9009\u62e9\u8fd9\u4e2a\u65f6\u673a\uff0c\u7edd\u4e0d\u662f\u56e0\u4e3a\u8f66\u7978\u7684\u7f18\u6545\uff0c\u800c\u662f\u4ed6\u4eec\u5df2\u7ecf\u9884\u6d4b\u5230\u5609\u4fe1\u7535\u5b50\u8fd9\u6bb5\u65f6\u95f4\u4f1a\u5904\u4e8e\u4e1a\u7ee9\u7684\u4f4e\u8c37\uff0c\u7ed9\u4e86\u4ed6\u4eec\u6700\u597d\u7684\u51fa\u624b\u65f6\u673a\u3002\u201d</p><p>\u3000\u3000\u7ecf\u5386\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\uff0c\u5609\u4fe1\u7535\u5b50\u7684\u603b\u5e02\u503c\u6bd4\u53bb\u5e74\u4e94\u516d\u6708\u7684\u9ad8\u4f4d\u5df2\u7ecf\u6574\u4f53\u4e0b\u964d\u4e86\u8d85\u8fc740\uff05\uff0c\u8fd9\u65f6\u5019\u518d\u7206\u51fa\u4e00\u4efd\u7cdf\u7cd5\u7684\u4e1a\u7ee9\u518d\u52a0\u4e0a\u5bf9\u4eca\u5e74\u5bf9\u7ecf\u6d4e\u5f62\u52bf\u7684\u60b2\u89c2\u9884\u6d4b\uff0c\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u4ef7\u8fd8\u6709\u53ef\u80fd\u4f1a\u8fdb\u4e00\u6b65\u7684\u4e0b\u632b\uff0c\u65e0\u8bba\u662f\u5728\u516c\u5f00\u5e02\u573a\u8fd8\u662f\u8fdb\u884c\u573a\u5916\u4ea4\u6613\uff0c\u8fd9\u7684\u786e\u662f\u4e89\u593a\u5609\u4fe1\u7535\u5b50\u63a7\u5236\u6743\u7684\u6700\u6709\u5229\u65f6\u673a\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u8003\u8651\u5230\u79cd\u79cd\u60c5\u51b5\uff0c\u4e5f\u4e0d\u80fd\u6392\u9664\u4ed6\u4eec\u5728\u865a\u5f20\u58f0\u52bf\u7684\u53ef\u80fd\uff0c\u6211\u4eec\u603b\u4e0d\u80fd\u56e0\u4e3a\u4ed6\u4eec\u7684\u4e00\u5219\u58f0\u660e\u3001\u51e0\u5343\u4e07\u6e2f\u5143\u7684\u8fd0\u4f5c\u5c31\u4e71\u4e86\u9635\u811a\u3002\u201d</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u80fd\u8bf4\u5c31\u4e71\u4e86\u9635\u811a\uff0c\u5c31\u7b97\u4e3a\u4e86\u5e94\u4ed8\u8bc1\u5238\u6295\u8d44\u8005\u5bf9\u8fd9\u4efd\u4e1a\u7ee9\u62a5\u544a\u7684\u8d23\u96be\uff0c\u201d\u845b\u660e\u4fe1\u6307\u7740\u5609\u4fe1\u7535\u5b50\u8fd8\u672a\u516c\u5e03\u7684\u4e5d\u4e03\u5e74\u5ea6\u8d22\u52a1\u62a5\u544a\uff0c\u201c\u6211\u4eec\u4e5f\u91c7\u53d6\u4e00\u4e9b\u66f4\u79ef\u7ea7\u7684\u63aa\u65bd\u6765\u7ef4\u7cfb\u6295\u8d44\u8005\u7684\u4fe1\u5fc3\uff0c\u6d77\u5916\u5e02\u573a\u6682\u65f6\u8fd8\u770b\u4e0d\u5230\u6709\u590d\u82cf\u7684\u8ff9\u8c61\uff0c\u552f\u6709\u5185\u5730\u5e02\u573a\u975e\u4f46\u6ca1\u53d7\u5230\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u5f71\u54cd\uff0c\u800c\u4e14\u8fd8\u6709\u66f4\u5927\u7684\u6f5c\u529b\u53ef\u6316\u2026\u2026\u5728\u8fc7\u53bb\u5341\u5e74\u91cc\uff0c\u867d\u7136\u5609\u4fe1\u7535\u5b50\u575a\u6301\u4e3a\u56fd\u9645\u7535\u5b50\u5382\u5546\u4ee3\u5de5\uff0c\u4f46\u662f\u5609\u4fe1\u7535\u5b50\u4e00\u76f4\u6ca1\u6709\u653e\u5f03\u5728\u6280\u672f\u7814\u53d1\u4e0a\u7684\u6295\u5165\uff0c\u6211\u60f3\u5609\u4fe1\u7535\u5b50\u6709\u7740\u66f4\u7075\u6d3b\u7684\u65b9\u5f0f\u6765\u62a2\u5360\u5185\u5730\u5e02\u573a\uff0c\u4f60\u51c6\u5907\u4e00\u4e0b\uff0c\u8fd9\u4e24\u5929\u966a\u6211\u53bb\u5185\u5730\u8d70\u4e00\u8d9f\u3002\u201d\u845b\u660e\u4fe1\u8bf4\u9053\uff0c\u201c\u53e6\u5916\uff0c\u6211\u5728\u79bb\u5f00\u9999\u6e2f\u4e4b\u524d\uff0c\u4f1a\u53ec\u5f00\u8463\u4e8b\u4f1a\u8bae\uff0c\u7814\u7a76\u4e00\u4e0b\u8981\u4e0d\u8981\u5bf9\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u8fdb\u884c\u589e\u6301\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u96c6\u56e2\u7684\u8d44\u91d1\u4e5f\u76f8\u5f53\u6709\u9650\u5427\uff1f\u201d\u845b\u836b\u5747\u62c5\u5fe7\u7684\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u5176\u4ed6\u63aa\u65bd\u4e0d\u53ef\u80fd\u7acb\u7aff\u89c1\u5f71\u5c31\u8d77\u6548\u679c\uff0c\u8981\u7ef4\u6301\u6295\u8d44\u8005\u7684\u4fe1\u5fc3\uff0c\u4e5f\u9632\u6b62\u4ed6\u4eec\u5728\u4f4e\u4ef7\u65f6\u5438\u7b79\uff0c\u542f\u52a8\u589e\u6301\u8ba1\u5212\u4e5f\u662f\u8feb\u4e0d\u5f97\u5df2\u7684\u9009\u62e9\u3002\u201d\u845b\u660e\u4fe1\u8bf4\u9053\u3002</p><p>\u3000\u3000\u867d\u8bf4\u7ee7\u7eed\u4e86\u5bb6\u65cf\u5927\u534a\u7684\u9057\u4ea7\uff0c\u7262\u7262\u63a7\u5236\u4f4f\u5609\u4fe1\u96c6\u56e2\uff0c\u845b\u660e\u4fe1\u7684\u8eab\u5bb6\u4e00\u5ea6\u8d85\u8fc7\u4e00\u767e\u516d\u4e03\u5341\u4ebf\u6e2f\u5143\uff0c\u5373\u4f7f\u7ecf\u5386\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u6253\u51fb\u4e4b\u540e\uff0c\u845b\u660e\u4fe1\u7684\u8eab\u5bb6\u4f9d\u65e7\u7ef4\u6301\u5728\u767e\u4ebf\u5de6\u53f3\uff0c\u4f46\u662f\u767e\u4ebf\u8eab\u5bb6\u7edd\u5927\u591a\u6570\u90fd\u662f\u4ee5\u516c\u53f8\u8d44\u4ea7\u7684\u5f62\u5f0f\u5b58\u5728\uff0c\u5609\u4fe1\u96c6\u56e2\u62e5\u6709\u7684\u8d44\u91d1\u603b\u91cf\u5e76\u4e0d\u591a\u3002\u8fc7\u53bb\u534a\u5e74\u65f6\u95f4\u91cc\u91d1\u878d\u5e02\u573a\u52a8\u8361\u4e0d\u5b89\uff0c\u4e3a\u4e86\u7ef4\u6301\u65d7\u4e0b\u5404\u5927\u516c\u53f8\u5e73\u7a33\u7684\u6e21\u8fc7\u5f53\u4e0b\u7684\u5371\u673a\uff0c\u4e5f\u4e3a\u4e86\u5c06\u4e00\u4e9b\u5f02\u5df1\u5206\u5b50\u8e22\u51fa\u5609\u4fe1\u96c6\u56e2\uff0c\u845b\u660e\u4fe1\u90fd\u6d88\u8017\u4e86\u5927\u91cf\u7684\u8d44\u91d1\u3002\u5c31\u73b0\u91d1\u4f59\u91cf\u6765\u8bf4\uff0c\u751a\u81f3\u8fd8\u4e0d\u5982\u9526\u6e56\u6765\u5f97\u5145\u6c9b\uff0c\u60f3\u60f3\u770b\uff0c\u9526\u6e56\u4ec5\u5c06\u7231\u8fbe\u7535\u5b50\u5728\u91d1\u878d\u98ce\u66b4\u524d\u5915\u501f\u58f3\u4e0a\u5e02\u5c31\u5377\u8d70\u56db\u5341\u4ebf\u6e2f\u5143\uff0c\u5c31\u8fd9\u4e00\u70b9\u5c31\u8ba9\u4eba\u773c\u998b\u65e0\u6bd4\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u6b63\u8981\u79bb\u5f00\u529e\u516c\u5ba4\uff0c\u53c8\u7ed9\u4ed6\u7236\u4eb2\u558a\u4f4f\uff1a\u201c\u73e0\u5ac2\u7684\u846c\u793c\uff0c\u4f60\u662f\u4e0d\u662f\u9732\u4e0b\u9762\uff1f\u201d</p><p>\u3000\u3000\u845b\u836b\u5747\u6478\u4e86\u6478\u8138\uff0c\u8138\u4e0a\u7ed9\u5b59\u9759\u6aac\u6293\u7834\u7684\u75a4\u75d5\u8fd8\u6ca1\u6709\u6d88\u5462\uff0c\u4ed6\u5fc3\u5934\u6709\u4e9b\u5fe4\uff0c\u4e3a\u96be\u7684\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8ba9\u5176\u4ed6\u4eba\u4ee3\u8868\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u9732\u9762\uff0c\u5a92\u4f53\u53c8\u8981\u4e00\u987f\u597d\u8bf4\u3002\u201d\u845b\u660e\u4fe1\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u9732\u9762\uff0c\u5a92\u4f53\u4e0d\u662f\u66f4\u5174\u594b\uff1f\u201d\u845b\u836b\u5747\u6447\u5934\u575a\u6301\u4e0d\u80af\u51fa\u5e2d\u846c\u793c\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u6709\u7279\u610f\u524d\u5f80\u9999\u6e2f\u53c2\u52a0\u73e0\u5ac2\u7684\u846c\u793c\uff0c\u4ed6\u4e00\u76f4\u5bc6\u5207\u5173\u6ce8\u7740\u9999\u6e2f\u90a3\u8fb9\u7684\u4fe1\u606f\uff0c\u5609\u4fe1\u96c6\u56e2\u5728\u73e0\u5ac2\u846c\u793c\u540e\u7684\u7b2c\u4e09\u5929\uff0c\u5ba3\u5e03\u5c06\u5bf9\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u80a1\u7968\u7684\u589e\u6301\u8ba1\u5212\uff0c\u523a\u6fc0\u80a1\u4ef7\u4e0a\u626c\u3002</p><p>\u3000\u3000\u5f20\u606a\u76f8\u4fe1\u5609\u4fe1\u96c6\u56e2\u4e0d\u53ef\u80fd\u53ea\u6709\u8fd9\u4e00\u4e24\u62db\u6323\u624e\u7684\u52a8\u4f5c\uff0c\u5bf9\u4e8e\u5e9e\u5927\u7684\u3001\u6ca1\u6709\u785d\u70df\u7684\u5546\u4e1a\u6218\u4e89\uff0c\u4e0d\u53ef\u80fd\u4e00\u8e74\u800c\u5c31\uff0c\u9700\u8981\u7684\u662f\u66f4\u591a\u7684\u65f6\u95f4\u4e0e\u66f4\u591a\u7684\u8010\u5fc3\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4e8c\u5341\u516d\u65e5\u624d\u4ece\u9999\u6e2f\u8d81\u98de\u673a\u8fd4\u56de\u5efa\u90ba\uff0c\u5979\u8fd8\u6ca1\u6709\u4ece\u60b2\u4f24\u4e2d\u6062\u590d\u8fc7\u6765\uff0c\u5b59\u9759\u9999\u5c06\u516c\u53f8\u7684\u4e8b\u60c5\u6682\u65f6\u4e22\u4e0b\u6765\u966a\u5979\u4e00\u8d77\u98de\u56de\u5efa\u90ba\uff0c\u5f20\u606a\u4eb2\u81ea\u5230\u673a\u573a\u63a5\u5979\u4eec\u3002</p><p>\u3000\u3000\u5728\u5efa\u90ba\u56fd\u9645\u673a\u573a\u7684\u63a5\u673a\u5927\u5385\u91cc\uff0c\u770b\u5230\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u8d70\u51fa\u6765\uff0c\u59d0\u59b9\u4fe9\u8138\u8272\u90fd\u4e0d\u5927\u597d\u770b\uff0c\u5f20\u606a\u8fd8\u4ee5\u4e3a\u5979\u4eec\u5750\u98de\u673a\u96be\u53d7\uff0c\u4e5f\u6216\u8005\u6c89\u6d78\u5728\u60b2\u4f24\u7684\u60c5\u7eea\u91cc\u65e0\u6cd5\u81ea\u62d4\uff0c\u7b49\u770b\u5230\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u5728\u968f\u884c\u4eba\u5458\u62e5\u62a4\u4e0b\u8d70\u51fa\u6765\uff0c\u5f20\u606a\u4e5f\u53ea\u80fd\u644a\u624b\u82e6\u7b11\uff0c\u96be\u602a\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u4e24\u4eba\u7684\u8138\u8272\u4f1a\u8fd9\u4e48\u5dee\uff0c\u53c8\u4e0d\u80fd\u5728\u673a\u573a\u91cc\u63ea\u4f4f\u845b\u836b\u5747\u75db\u6241\u4e00\u987f\u5e2e\u9759\u6aac\u51fa\u53e3\u6c14\u3002</p><p>\u3000\u3000\u201c\u8fd9\u624d\u662f\u5609\u4fe1\u96c6\u56e2\u771f\u6b63\u7684\u53cd\u5e94\u5427\uff1f\u201d\u7fdf\u4e39\u9752\u7ad9\u5728\u5f20\u606a\u8eab\u8fb9\u6084\u58f0\u7684\u8bf4\uff0c\u53c8\u73af\u987e\u5de6\u53f3\uff0c\u6ca1\u6709\u770b\u5230\u7ed9\u845b\u660e\u4fe1\u7236\u5b50\u63a5\u673a\u7684\u4eba\uff0c\u201c\u5efa\u90ba\u53ea\u662f\u4ed6\u4eec\u7684\u4e2d\u8f6c\u7ad9\u554a\uff0c\u4ed6\u4eec\u51fa\u4e86\u673a\u573a\u4f1a\u4e0d\u4f1a\u76f4\u63a5\u524d\u5f80\u91d1\u5c71\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u5609\u4fe1\u96c6\u56e2\u4e3a\u53cd\u51fb\u9526\u6e56\u7684\u52a8\u4f5c\u800c\u5ba3\u5e03\u5bf9\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u7684\u589e\u6301\u8ba1\u5212\uff0c\u53ea\u662f\u6cbb\u6807\u4e0d\u6cbb\u672c\u7684\u63aa\u65bd\uff0c\u8fd9\u4e00\u5207\u8fd8\u53ea\u662f\u521a\u521a\u5f00\u59cb\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u4e5f\u4e0d\u53ef\u80fd\u53ea\u6709\u8fd9\u4e00\u4e24\u5f0f\u7684\u4f0e\u4fe9\uff0c\u90a3\u5c31\u5148\u62d6\u7740\u4ed6\u4eec\u6df1\u9677\u8fdb\u6765\u518d\u8bf4\u3002</p></article>"}}}]);
