(window.webpackJsonp=window.webpackJsonp||[]).push([[975],{998:function(p){p.exports={chapterTitle:null,title:"\u7b2c972\u7ae0 \u9152\u9189\u4e4b\u540e",content:"<article><h2>\u7b2c972\u7ae0 \u9152\u9189\u4e4b\u540e</h2><p>\u3000\u3000\u8ff7\u8ff7\u7cca\u7cca\u95f4\u6709\u4e9b\u5934\u75bc\uff0c\u8fd9\u624d\u89c9\u5f97\u6628\u5929\u591c\u91cc\u9152\u559d\u5f97\u592a\u591a\u4e86\uff0c\u542c\u7740\u8033\u7554\u4f20\u6765\u6e05\u6670\u7684\u5fc3\u8df3\u58f0\uff0c\u674e\u99a8\u4e88\u786e\u4fe1\u81ea\u5df1\u662f\u8db4\u5728\u5f20\u606a\u7684\u6000\u91cc\u7761\u7740\u4e86\u3002</p><p>\u3000\u3000\u6628\u5929\u771f\u662f\u9152\u559d\u592a\u591a\u4e86\uff0c\u4e00\u74f6\u7ea2\u9152\u559d\u5b8c\uff0c\u53c8\u4ece\u9152\u67dc\u91cc\u62ff\u4e86\u4e00\u74f6\u666e\u901a\u7684\u6d0b\u9152\uff0c\u559d\u5f97\u5174\u8d77\uff0c\u8fd8\u8ddf\u5f20\u606a\u62a2\u7740\u559d\u6765\uff0c\u8bf4\u4e86\u4e00\u4e9b\u5f88\u65e0\u8c13\u7684\u8bdd\uff0c\u6709\u6ca1\u6709\u5c06\u81ea\u5df1\u7b2c\u4e00\u6b21\u6765\u6708\u7ecf\u7684\u4e8b\u60c5\u8ddf\u4ed6\u8bf4\uff1f</p><p>\u3000\u3000\u674e\u99a8\u4e88\u6709\u4e9b\u4e0d\u5927\u786e\u5b9a\uff0c\u6216\u8bb8\u8bf4\u4e86\uff0c\u6216\u8bb8\u6ca1\u6709\u8bf4\uff0c\u8bf4\u6216\u8005\u4e0d\u8bf4\u90fd\u65e0\u6240\u8c13\uff0c\u81ea\u5df1\u4e00\u5b9a\u5c06\u4e00\u4e9b\u66f4\u4e0d\u582a\u5165\u8033\u7684\u4e11\u4e8b\u8bf4\u51fa\u6765\u4e86\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u4e0d\u786e\u5b9a\u5f20\u606a\u6709\u6ca1\u6709\u9192\uff0c\u773c\u775b\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u7741\u5f00\u4e00\u9053\u7f1d\uff0c\u89c1\u5f20\u606a\u8fd8\u7a7f\u7740\u886c\u886b\uff0c\u76b1\u5df4\u5df4\u7684\uff0c\u81f3\u5c11\u8fd8\u7a7f\u7740\uff0c\u611f\u89c9\u5230\u6709\u4e9b\u51b7\uff0c\u90a3\u662f\u7a97\u6237\u5f00\u7740\u534a\u8fb9\uff0c\u8bb0\u8d77\u6765\u6628\u591c\u9152\u559d\u5f97\u8eab\u5b50\u53d1\u70ed\uff0c\u5c06\u5916\u5957\u8131\u6389\uff0c\u53ea\u7a7f\u7740\u8d34\u8eab\u7684\u8863\u7269\u8fd8\u662f\u89c9\u5f97\u70ed\uff0c\u624d\u5c06\u7a97\u6237\u6253\u5f00\u534a\u8fb9\u6765\u900f\u6c14\u3002</p><p>\u3000\u3000\u4e4d\u770b\u5230\u6302\u5728\u5e8a\u6cbf\u4e0a\u9ed1\u8272\u857e\u4e1d\u8fb9\u7684\u4e73\u7f69\uff0c\u674e\u99a8\u4e88\u5413\u4e86\u4e00\u8df3\uff0c\u773c\u5e18\u4f4e\u5782\u770b\u7740\u8eab\u4e0a\u8fd8\u7a7f\u7740\u8d34\u8eab\u7684\u8584\u7ed2\u886b\uff0c\u624d\u9690\u7ea6\u7684\u60f3\u8d77\u6765\u9189\u610f\u6d53\u65f6\u95f2\u90a3\u91cc\u52d2\u5f97\u614c\u8fd8\u8ba9\u5f20\u606a\u624b\u4f38\u8fdb\u80cc\u540e\u5e2e\u81ea\u5df1\u5c06\u642d\u6263\u89e3\u5f00\uff0c\u89e3\u5f00\u4e4b\u540e\u4f3c\u4e4e\u8fd8\u8ba8\u8bba\u8fc7\u80f8\u5927\u7684\u70e6\u607c\uff0c\u771f\u662f\u80c6\u5927\u5230\u5984\u4e3a\uff0c\u8fd8\u597d\u6ca1\u6709\u9189\u9152\u5c06\u8863\u670d\u90fd\u8131\u5e72\u51c0\u3002</p><p>\u3000\u3000\u8fd8\u505a\u8fc7\u4ec0\u4e48\uff1f\u8fd8\u63a5\u543b\u4e86\uff0c\u8bb0\u5fc6\u7684\u611f\u89c9\u5f88\u6e05\u6670\uff0c\u4e0d\u5bf9\u554a\uff0c\u9152\u9189\u5f97\u90a3\u4e48\u5389\u5bb3\uff0c\u54ea\u53ef\u80fd\u6709\u8fd9\u4e48\u6e05\u6670\u611f\u89c9\u7684\u8bb0\u5fc6\u554a\uff0c\u662f\u68a6\u91cc\u63a5\u543b\u5427\uff1f</p><p>\u3000\u3000\u674e\u99a8\u4e88\u56de\u5fc6\u7740\u63a5\u543b\u65f6\u5634\u5507\u76f8\u89e6\u7684\u6e29\u8f6f\u611f\u89c9\uff0c\u592a\u771f\u5b9e\u4e86\uff0c\u53c8\u4e0d\u50cf\u662f\u5728\u505a\u68a6\uff0c\u53c8\u60f3\u81ea\u5df1\u90fd\u6ca1\u6709\u63a5\u543b\u7684\u7ecf\u9a8c\uff0c\u54ea\u91cc\u77e5\u9053\u771f\u5b9e\u7684\u63a5\u543b\u662f\u4ec0\u4e48\u611f\u89c9\uff1f\u53ea\u662f\u505a\u68a6\u4e5f\u592a\u5c0f\u5fc3\u7ffc\u7ffc\u4e86\uff0c\u8fd9\u4e48\u60f3\u7740\uff0c\u674e\u99a8\u4e88\u90fd\u611f\u89c9\u5f97\u8138\u6709\u4e9b\u5fae\u70eb\u4e86\uff0c\u8fd9\u65f6\u5019\u6700\u7d27\u8981\u7684\u8fd8\u662f\u600e\u4e48\u4ece\u8fd9\u4e2a\u623f\u95f4\u9003\u79bb\u51fa\u53bb\uff0c\u54ea\u53ef\u4ee5\u771f\u53bb\u60f3\u90a3\u4e9b\u6709\u7684\u6ca1\u7684\uff01</p><p>\u3000\u3000\u674e\u99a8\u4e88\u8eab\u5b50\u5fae\u5fae\u52a8\u4e86\u52a8\uff0c\u9971\u6ee1\u575a\u5b9e\u7684\u4e73\u623f\u8d34\u7740\u5f20\u606a\u7684\u808b\u9aa8\u592a\u4e45\u4e86\uff0c\u7a0d\u7a0d\u4e00\u52a8\u5c31\u6709\u4e9b\u9165\u9ebb\u7684\u611f\u89c9\uff0c\u8eab\u4f53\u7684\u611f\u89c9\u77ac\u65f6\u90fd\u6062\u590d\u8fc7\u6765\uff0c\u611f\u89c9\u5f20\u606a\u7684\u4e00\u624b\u642d\u5728\u81ea\u5df1\u7684\u8170\u4e0a\uff0c\u4e00\u624b\u642d\u5728\u81ea\u5df1\u7684\u81c0\u4e0a\uff0c\u611f\u89c9\u5f97\u7ea4\u957f\u6307\u8282\u901a\u8fc7\u8584\u7ed2\u886b\u6216\u8584\u88e4\u5e03\u4f20\u6765\u4e9b\u5fae\u7684\u70ed\u5ea6\uff0c\u5fc3\u91cc\u60f3\uff1a\u4ed6\u6709\u6ca1\u6709\u8d81\u673a\u8f7b\u8584\u7684\u6478\u81ea\u5df1\u7684\u5c41\u80a1\uff1f\u7ed9\u5f20\u606a\u7684\u624b\u638c\u642d\u5728\u81c0\u4e0a\uff0c\u674e\u99a8\u4e88\u90fd\u80fd\u611f\u89c9\u5230\u81ea\u5df1\u81c0\u90e8\u7684\u4e30\u76c8\u9971\u6ee1\u5f39\u6027\u5341\u8db3\uff0c\u81c0\u5f62\u4e5f\u76f8\u5f53\u4e0d\u8db3\uff0c\u8fd9\u65f6\u5019\u53c8\u4e3a\u81ea\u5df1\u6709\u8fd9\u4e48\u8ff7\u4eba\u7684\u81c0\u90e8\u800c\u5f97\u610f\uff0c\u5012\u4e5f\u4e0d\u4ecb\u610f\u5f20\u606a\u7684\u624b\u638c\u644a\u653e\u5728\u4e0a\u9762\uff0c\u6709\u4e9b\u5fae\u75d2\u7684\u611f\u89c9\uff0c\u53ea\u662f\u6e05\u9192\u8fc7\u6765\uff0c\u4e0d\u80fd\u653e\u7eb5\u8fd9\u79cd\u611f\u89c9\uff0c\u8fd8\u662f\u8d76\u7d27\u60f3\u6cd5\u5b50\u9003\u79bb\u8fd9\u4e2a\u623f\u95f4\u624d\u662f\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u6452\u4f4f\u547c\u5438\u7b49\u4e86\u7247\u523b\uff0c\u89c1\u5f20\u606a\u6ca1\u6709\u52a8\u9759\uff0c\u624b\u80cc\u540e\u8eab\u540e\u5c06\u5f20\u606a\u642d\u5728\u81ea\u5df1\u8170\u4e0a\u7684\u90a3\u53ea\u624b\u632a\u5f00\uff0c\u53c8\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u53c8\u8bd5\u7740\u53bb\u632a\u642d\u5728\u81ea\u5df1\u5c41\u80a1\u4e0a\u7684\u90a3\u53ea\u624b\uff0c\u6574\u4e2a\u8fc7\u7a0b\u8138\u90fd\u8d34\u5728\u5f20\u606a\u7684\u80f8\u53e3\u4e0a\uff0c\u4e0d\u6562\u62ac\u8d77\u5934\u6765\u770b\u4ed6\u7684\u8138\u3002</p><p>\u3000\u3000\u5c06\u5f20\u606a\u7684\u53cc\u624b\u90fd\u632a\u5230\u4e00\u8fb9\uff0c\u674e\u99a8\u4e88\u624d\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u53cc\u624b\u6491\u5230\u5f20\u606a\u8170\u95f4\u7684\u5e8a\u57ab\u4e0a\uff0c\u6162\u6162\u7684\u7528\u529b\u8ba9\u81ea\u5df1\u7684\u8eab\u4f53\u7a0d\u7a0d\u62ac\u9ad8\u8d77\u6765\uff0c\u89c1\u5f20\u606a\u8fd8\u6ca1\u6709\u52a8\u9759\uff0c\u8fd9\u624d\u62ac\u8d77\u5934\u6765\u770b\u5f20\u606a\u7684\u8138\uff0c\u5374\u770b\u89c1\u4e00\u53cc\u6676\u4eae\u7684\u661f\u7738\u6b63\u7741\u7740\u770b\u7740\u81ea\u5df1\u2026\u2026</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u674e\u99a8\u4e88\u5fc3\u865a\u7684\u624b\u4e00\u8f6f\uff0c\u8eab\u5b50\u53c8\u8dcc\u56de\u5230\u5f20\u606a\u7684\u6000\u91cc\uff0c\u5fd9\u4e0d\u8fed\u7684\u722c\u5750\u8d77\u6765\uff0c\u4e0d\u6562\u770b\u5f20\u606a\u7684\u773c\u775b\uff0c\u7ec6\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u9192\u8fc7\u6765\u591a\u4e45\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c06\u6211\u624b\u79fb\u5f00\u7684\u65f6\u5019\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff01\u201d\u674e\u99a8\u4e88\u53c8\u4e0d\u80fd\u8bf4\u4e0d\u4fe1\uff0c\u8981\u8bf4\u5f20\u606a\u65e9\u5c31\u9192\u4e86\uff0c\u90a3\u4ed6\u7684\u624b\u653e\u5728\u81ea\u5df1\u7684\u5c41\u80a1\u53c8\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f</p><p>\u3000\u3000\u201c\u53ea\u8bb0\u5f97\u9152\u559d\u5230\u6700\u540e\u90fd\u6709\u4e9b\u9189\u7684\uff0c\u8fd8\u6ca1\u6709\u9189\u5f97\u8fd9\u4e48\u8212\u670d\u8fc7\uff0c\u4f60\u611f\u89c9\u8fd8\u597d\uff1f\u201d\u5f20\u606a\u6b20\u7740\u8eab\u5b50\u5750\u8d77\u6765\uff0c\u624d\u53d1\u73b0\u9152\u559d\u591a\u4e86\u5165\u7761\u978b\u5b50\u90fd\u6ca1\u6709\u8131\uff0c\u4e00\u8eab\u7684\u8863\u670d\u90fd\u76b1\u5df4\u5df4\u7684\uff0c\u4ed6\u8fd9\u624d\u5f2f\u7740\u8170\u5c06\u978b\u5b50\u8131\u6389\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u7f9e\u6da9\u7684\u7b11\u4e86\u7b11\uff0c\u70b9\u4e86\u70b9\uff0c\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff0c\u8fd9\u6837\u7684\u9189\u9152\u8fd8\u771f\u662f\u8212\u7545\u3002</p><p>\u3000\u3000\u7a97\u5e18\u6ca1\u6709\u62c9\u4e0a\uff0c\u6e05\u4eae\u7684\u6668\u5149\u4ece\u73bb\u7483\u7a97\u5916\u900f\u8fdb\u6765\uff0c\u5929\u8272\u8fd8\u65e9\uff0c\u770b\u4e0a\u53bb\u5149\u4eae\uff0c\u4e0d\u8fc7\u662f\u96ea\u7684\u53cd\u5149\uff0c\u5f20\u606a\u638f\u51fa\u624b\u673a\u6765\u770b\u4e86\u770b\uff0c\u624d\u4e94\u70b9\u949f\u591a\u4e9b\uff0c\u7ad9\u8d77\u6765\u95ee\u674e\u99a8\u4e88\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u518d\u7761\u4e00\u4f1a\u513f\uff1f\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u6293\u8d77\u8131\u653e\u5728\u4e00\u65c1\u7684\u5916\u5957\u4e0e\u624b\u888b\u4e5f\u6089\u7709\u82e6\u8138\uff0c\u5979\u5fd8\u8bb0\u5c06\u623f\u5361\u62ff\u5728\u8eab\u8fb9\uff0c\u8fd9\u65f6\u5019\u56de\u623f\u95f4\u80af\u5b9a\u8981\u60ca\u9192\u6734\u8d1e\u513f\uff0c\u600e\u4e48\u8ddf\u5979\u89e3\u91ca\uff1f\u8981\u662f\u7ed9\u5728\u6d19\u77e5\u9053\u81ea\u5df1\u5728\u5f20\u606a\u7684\u623f\u95f4\u91cc\u8fc7\u591c\uff0c\u540e\u679c\u53ef\u6bd4\u4e0a\u56de\u8fd8\u8981\u4e25\u91cd\uff0c\u6307\u4e0d\u5b9a\u5bb6\u65cf\u91cc\u4e5f\u4f1a\u95f9\u5f97\u4eba\u4ef0\u9a6c\u7ffb\u2014\u2014\u9152\u5e97\u884c\u653f\u5c42\u7684\u534a\u5c42\u697c\u90fd\u7ed9\u4e09\u661f\u6765\u5317\u4eac\u53c2\u52a0\u65b0\u54c1\u53d1\u5e03\u4f1a\u7684\u9ad8\u5c42\u4f4f\u6ee1\u4e86\uff0c\u8fd8\u771f\u662f\u5077\u60c5\u7684\u597d\u5730\u70b9\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u9887\u4e3a\u65e0\u5948\u7684\u770b\u7740\u5f20\u606a\uff0c\u5f20\u606a\u89c1\u5979\u6101\u7709\u51dd\u7ed3\u7684\u6837\u5b50\uff0c\u731c\u5230\u5979\u5728\u62c5\u5fe7\u4ec0\u4e48\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5c06\u8863\u670d\u6362\u4e0b\u6765\u6d17\u4e00\u4e0b\uff0c\u6211\u6253\u7535\u8bdd\u8ba9\u9152\u5e97\u5c06\u4f60\u7684\u8863\u670d\u62ff\u53bb\u71a8\u70eb\u4e00\u4e0b\uff0c\u9152\u5e97\u8fd9\u65f6\u5019\u5e94\u8be5\u8fd8\u80fd\u63d0\u4f9b\u670d\u52a1\u2014\u2014\u8981\u662f\u5728\u9152\u5e97\u5916\u7a7f\u7740\u6574\u6d01\u7684\u8863\u670d\u5403\u65e9\u9910\uff0c\u63a5\u5230\u6734\u8d1e\u513f\u7684\u7535\u8bdd\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u7cca\u5f04\u8fc7\u53bb\u3002\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u5fc3\u60f3\u4e5f\u53ea\u6709\u5982\u6b64\uff0c\u5148\u5230\u6d74\u5ba4\u91cc\u8131\u4e0b\u8863\u670d\u8eab\u5b50\u85cf\u5728\u95e8\u540e\u9012\u7ed9\u5f20\u606a\uff0c\u8fd9\u65f6\u5019\u624d\u53d1\u73b0\u591c\u91cc\u6254\u5728\u5e8a\u5934\u7684\u4e73\u7f69\u6ca1\u6709\u62ff\u8fdb\u6765\uff0c\u6d74\u888d\u6bd4\u8f83\u66b4\u9732\uff0c\u674e\u99a8\u4e88\u53ef\u6ca1\u6709\u52c7\u6c14\u5149\u6e9c\u6e9c\u7684\u53ea\u7a7f\u4ef6\u6d74\u888d\u8d70\u51fa\u53bb\uff0c\u7ec6\u58f0\u8bf4\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u5e2e\u6211\u5c06\u5e8a\u4e0a\u7684\u90a3\u4e2a\u62ff\u8fc7\u6765\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6361\u8d77\u5e8a\u4e0a\u9ed1\u8272\u857e\u4e1d\u8fb9\u7684\u4e73\u7f69\uff0c\u5373\u4f7f\u518d\u6b63\u5e38\u7684\u7537\u4eba\uff0c\u4e5f\u4f1a\u60c5\u4e0d\u81ea\u7981\u7684\u51d1\u5230\u9f3b\u7aef\u95fb\u4e00\u95fb\uff0c\u8fd8\u6b8b\u7559\u4e86\u6de1\u6de1\u7684\u4f53\u9999\u3002</p><p>\u3000\u3000\u201c\u4f60\u2026\u2026\u201d\u674e\u99a8\u4e88\u4ece\u95e8\u540e\u63a2\u51fa\u5934\u6765\uff0c\u770b\u5230\u5f20\u606a\u5728\u95fb\u81ea\u5df1\u7684\u4e73\u7f69\uff0c\u7f9e\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u5fc3\u60f3\u7537\u4eba\u679c\u771f\u90fd\u8fd9\u822c\u6a21\u6837\uff0c\u5374\u6ca1\u6709\u751f\u6c14\u7684\u611f\u89c9\uff0c\u597d\u50cf\u662f\u5979\u505a\u4e86\u4e8f\u5fc3\u4e8b\uff0c\u4e0d\u6562\u770b\u5f20\u606a\u7684\u773c\u775b\uff0c\u4f38\u51fa\u624b\u6765\uff0c\u5a07\u55d4\u9053\uff0c\u201c\u6709\u4ec0\u4e48\u597d\u95fb\u7684\uff0c\u8fd8\u4e0d\u5feb\u7ed9\u6211\u62ff\u8fc7\u6765\uff1f\u90fd\u51b7\u6b7b\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u8146\u7740\u8138\u9012\u8fc7\u53bb\uff0c\u867d\u8bf4\u590f\u5929\u65f6\u770b\u5230\u8fc7\u674e\u99a8\u4e88\u8863\u7740\u5355\u8584\u7684\u65f6\u5019\uff0c\u8fd9\u65f6\u5019\u770b\u5230\u5979\u4f38\u51fa\u96ea\u5ae9\u5706\u6da6\u7684\u624b\u81c2\u6765\uff0c\u53c8\u77e5\u9053\u95e8\u540e\u7684\u5979\u6700\u591a\u7a7f\u7740\u4e00\u6761\u5185\u88e4\uff0c\u5c24\u5176\u7684\u89c9\u5f97\u6027\u611f\uff0c\u53ef\u60dc\u674e\u99a8\u4e88\u5f88\u5feb\u5c31\u5c06\u6d74\u5ba4\u7684\u95e8\u5173\u4e25\u5b9e\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u6d17\u4e86\u6fa1\u7a7f\u4e0a\u6d74\u888d\u6362\u5f20\u606a\u8fdb\u53bb\u6d17\u6fa1\uff0c\u4e5f\u62c5\u5fc3\u6734\u8d1e\u513f\u7684\u7535\u8bdd\u968f\u65f6\u4f1a\u6253\u8fc7\u6765\uff0c\u7b49\u6362\u4e0a\u6170\u597d\u7684\u8863\u670d\uff0c\u4e0e\u5f20\u606a\u8d70\u51fa\u9152\u5e97\uff0c\u624d\u677e\u4e86\u4e00\u53e3\u6c14\u3002</p><p>\u3000\u3000\u9152\u5e97\u524d\u7684\u8857\u4e0a\u79ef\u96ea\u90fd\u5df2\u7ecf\u7ed9\u73af\u536b\u5de5\u4eba\u6e05\u65e9\u6e05\u6389\u4e86\uff0c\u4e0d\u8fc7\u9152\u5e97\u4e1c\u4fa7\u7684\u5df7\u5b50\u91cc\u8fd8\u79ef\u7740\u7691\u7691\u767d\u96ea\uff0c\u8fd8\u6ca1\u6709\u884c\u4eba\u8df5\u8e0f\u8fc7\u7684\u75d5\u8ff9\uff0c\u547c\u5438\u7740\u6e05\u51b7\u7684\u7a7a\u6c14\uff0c\u674e\u99a8\u4e88\u60c5\u4e0d\u81ea\u7981\u7684\u633d\u7740\u5f20\u606a\u7684\u624b\u81c2\uff0c\u8ba4\u8ba4\u771f\u771f\u7684\u8bf4\uff1a\u201c\u5076\u5c14\u559d\u9189\u9152\u7684\u611f\u89c9\u8fd8\u771f\u662f\u4e0d\u9519\u3002\u201d</p><p>\u3000\u3000\u9152\u5e97\u53f3\u62d0\u51fa\u6765\u5c31\u662f\u5546\u4e1a\u8857\uff0c\u8fd9\u4e48\u65e9\u8981\u627e\u5230\u5403\u65e9\u9910\u7684\u5730\u65b9\u53ef\u4e0d\u5bb9\u6613\uff0c\u5f20\u606a\u4e0e\u674e\u99a8\u4e88\u5c31\u5728\u8857\u4e0a\u80e1\u4e71\u7684\u901b\u7740\uff0c\u80fd\u9047\u5230\u8bb8\u591a\u6668\u8d77\u953b\u70bc\u7684\u4eba\uff0c\u8857\u4e0a\u884c\u9a76\u7684\u8f66\u8f86\u66f4\u591a\u7684\u662f\u51fa\u79df\u8f66\uff0c\u627e\u5230\u4e00\u5bb6\u8fde\u9501\u7684\u65e9\u9910\u5e97\uff0c\u65e9\u9910\u5e97\u4eba\u5012\u662f\u4e0d\u5c11\uff0c\u70b9\u4e86\u6cb9\u6761\u3001\u7626\u8089\u76ae\u86cb\u7ca5\u3001\u8336\u53f6\u86cb\u3001\u8c46\u6d46\uff0c\u8fd9\u65f6\u5019\u674e\u99a8\u4e88\u624d\u63a5\u5230\u6734\u8d1e\u513f\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u6734\u8d1e\u513f\u8fd9\u624d\u9189\u9152\u9192\u6765\uff0c\u89c1\u674e\u99a8\u4e88\u4e0d\u7761\u5728\u623f\u95f4\u91cc\u5c31\u62c5\u5fe7\u7684\u6253\u7535\u8bdd\u7ed9\u5979\uff0c\u77e5\u9053\u5979\u4e0e\u5f20\u606a\u5230\u9152\u5e97\u5916\u5403\u65e9\u9910\uff0c\u5fc3\u91cc\u867d\u7136\u6709\u4e9b\u7591\u60d1\uff0c\u5374\u4e5f\u6ca1\u6709\u591a\u60f3\uff0c\u8d76\u7d27\u51b2\u4e86\u6fa1\u5faa\u7740\u674e\u99a8\u4e88\u5728\u7535\u8bdd\u91cc\u7684\u6307\u793a\u8d70\u5230\u65e9\u9910\u5e97\uff0c\u89c1\u674e\u99a8\u4e88\u867d\u7136\u8fd8\u7a7f\u7740\u6628\u5929\u7684\u8863\u670d\uff0c\u4f46\u662f\u8863\u670d\u6574\u6d01\u65e0\u76b1\uff0c\u4e0e\u5f20\u606a\u5750\u5728\u65e9\u9910\u5e97\u91cc\u4e5f\u6709\u8bf4\u6709\u7b11\u3002\u6734\u8d1e\u513f\u77e5\u9053\u674e\u99a8\u4e88\u4e0d\u8ba4\u540c\u4e00\u591c\u60c5\u7684\u9053\u5fb7\u89c2\uff0c\u6240\u4ee5\u5b8c\u5168\u4e0d\u4f1a\u6000\u7591\u5230\u5979\u5728\u5f20\u606a\u7684\u623f\u95f4\u91cc\u8fc7\u591c\uff0c\u5bbf\u9189\u5934\u75bc\u5f97\u5389\u5bb3\uff0c\u4e5f\u6ca1\u6709\u5fc3\u601d\u53bb\u5173\u6ce8\u66f4\u591a\u7684\u7ec6\u8282\u3002</p><p>\u3000\u3000\u6734\u8d1e\u513f\u4e3a\u6628\u5929\u7684\u9189\u9152\u5931\u793c\u9053\u6b49\uff0c\u8fd8\u7ea6\u5f20\u606a\u665a\u4e0a\u518d\u805a\u5230\u4e00\u8d77\u559d\u9152\uff0c\u5f20\u606a\u770b\u7740\u674e\u99a8\u4e88\u4fa7\u8fc7\u8138\u77a5\u6765\u8574\u7740\u7b11\u610f\u7684\u773c\u795e\uff0c\u4e5f\u4f1a\u610f\u7684\u4e00\u7b11\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u4e0e\u6734\u8d1e\u513f\u7528\u8fc7\u65e9\u9910\u5148\u79bb\u5f00\uff0c\u5f20\u606a\u4e70\u4e86\u4efd\u62a5\u7eb8\uff0c\u53c8\u7ed9\u5085\u4fca\u6253\u7535\u8bdd\uff0c\u8981\u4ed6\u966a\u5c0f\u8205\u4ee5\u53ca\u6881\u6587\u6c5f\u4ed6\u4eec\u4e00\u8d77\u5230\u8fd9\u8fb9\u6765\u7528\u65e9\u9910\uff0c\u9152\u5e97\u91cc\u53ea\u63d0\u4f9b\u897f\u5f0f\u81ea\u52a9\u65e9\u9910\uff0c\u8fd8\u662f\u5230\u8fd9\u8fb9\u559d\u4e9b\u7a00\u7ca5\u6cb9\u6761\u8212\u670d\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u3001\u6734\u8d1e\u513f\u4eca\u5929\u5374\u672a\u80fd\u5355\u72ec\u884c\u52a8\uff0c\u8981\u53c2\u52a0\u4e00\u4e9b\u5546\u4e1a\u6d3b\u52a8\uff0c\u5f20\u606a\u4e0b\u5348\u4e0e\u4fe1\u606f\u4ea7\u4e1a\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\u4f1a\u8c08\uff0c\u665a\u4e0a\u53c8\u8981\u53bb\u987e\u5b85\u62dc\u4f1a\u987e\u957f\u5e9a\u8001\u4eba\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e09\u661f\u7535\u5b50\u7684\u5bb6\u7535\u3001\u7535\u5b50\u4ea7\u54c1\u8fdb\u5165\u4e2d\u56fd\u5e02\u573a\u5df2\u6709\u591a\u5e74\uff0c\u6bcf\u5e74\u90fd\u8981\u4e3e\u529e\u591a\u6b21\u65b0\u54c1\u53d1\u5e03\u4f1a\uff0c\u5730\u70b9\u4e5f\u4e0d\u4f1a\u5c40\u9650\u5728\u5317\u4eac\uff0c\u9080\u8bf7\u5a92\u4f53\u8bb0\u8005\u53c2\u52a0\u65b0\u54c1\u53d1\u5e03\u4f1a\uff0c\u751a\u81f3\u4f1a\u6709\u610f\u7684\u9009\u62e9\u4e00\u4e9b\u6bd4\u8f83\u8457\u540d\u7684\u65c5\u6e38\u57ce\u5e02\u3002</p><p>\u3000\u3000\u5f20\u606a\u57fa\u672c\u4e0a\u80fd\u80af\u5b9a\u4e09\u661f\u5185\u90e8\u63d0\u524d\u5236\u8ba2\u4e86\u6570\u5b57\u6280\u672f\u878d\u5408\u6218\u7565\uff0c\u5bf9\u4e2d\u56fd\u5e02\u573a\u7684\u5f00\u53d1\u7b56\u7565\u4e5f\u8fdb\u884c\u7684\u8c03\u6574\uff0c\u8fd9\u6b21\u65b0\u54c1\u53d1\u5e03\u4f1a\u5bf9\u4e09\u661f\u6765\u8bf4\u610f\u4e49\u91cd\u5927\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u662f\u63d0\u524d\u4e00\u5929\u624d\u62ff\u5230\u4e09\u661f\u8fd9\u6b21\u8981\u53d1\u5e03\u7684\u65b0\u4ea7\u54c1\u5217\u8868\uff0c\u4e09\u661f\u5c06\u5728\u8fd9\u6b21\u65b0\u54c1\u53d1\u5e03\u4f1a\u4e0a\u63a8\u51fa\u4ed6\u4eec\u6700\u65b0\u7684\u6db2\u6676\u663e\u793a\u5668\u3001\u6570\u5b57\u624b\u673a\u7b49\u9ad8\u7aef\u4ea7\u54c1\uff0c\u4e5f\u9884\u793a\u7740\u4e09\u661f\u6b63\u5f0f\u5728\u4e2d\u56fd\u542f\u52a8\u9ad8\u7aef\u7b56\u7565\uff0c\u8fd9\u4e00\u7b56\u7565\u5c06\u5ef6\u7eed\u5230\u591a\u5e74\u4e4b\u540e\u624d\u518d\u6b21\u505a\u8c03\u6574\uff0c\u800c\u4e14\u6db2\u6676\u663e\u793a\u5668\u4e0e\u6570\u5b57\u624b\u673a\u6070\u6070\u662f\u4e09\u661f\u4ee5\u540e\u7684\u4e09\u5927\u652f\u67f1\u4ea7\u54c1\u4e4b\u4e8c\uff0c\u8fd9\u4e5f\u8bf4\u660e\u4e09\u661f\u9009\u62e9\u7684\u53d1\u5c55\u65b9\u5411\u5e76\u6ca1\u6709\u56e0\u4e3a\u5076\u5c14\u7684\u56f0\u7d20\u800c\u53d1\u751f\u504f\u5dee\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u59da\u575a\u8fd8\u6709\u5317\u4eac\u5206\u516c\u53f8\u7684\u4e24\u540d\u5de5\u4f5c\u4eba\u5458\u8d76\u5230\u4e09\u661f\u53d1\u5e03\u4f1a\u7684\u73b0\u8c61\uff0c\u4f1a\u573a\u5e03\u7f6e\u6781\u5177\u73b0\u4ee3\u611f\uff0c\u6709\u5982\u73b0\u4ee3\u827a\u672f\u7684\u7535\u5b50\u5c55\u9986\u3002\u770b\u7740\u4f1a\u573a\u6512\u52a8\u7684\u4eba\u5934\uff0c\u59da\u575a\u7684\u5567\u5567\u7684\u8f7b\u5482\u7740\u820c\uff1a\u201c\u5dee\u4e0d\u591a\u6709\u4e0a\u5343\u53f7\u4eba\u5427\uff0c\u4e09\u661f\u7684\u53f7\u53ec\u8fd8\u771f\u662f\u4e0d\u5dee\uff01\u201d\u5f20\u606a\u5374\u9694\u7740\u6512\u52a8\u7684\u4eba\u5934\uff0c\u4e0e\u8fdc\u5904\u7a7f\u7740\u793c\u670d\u88d9\u7684\u674e\u99a8\u4e88\u4ea4\u6362\u773c\u795e\u793a\u610f\uff0c\u6709\u7740\u5fc3\u7075\u4ea4\u878d\u4e4b\u611f\u3002</p></article>"}}}]);
