(window.webpackJsonp=window.webpackJsonp||[]).push([[977],{1000:function(p){p.exports={chapterTitle:null,title:"\u7b2c789\u7ae0 \u9489\u5b50",content:"<article><h2>\u7b2c789\u7ae0 \u9489\u5b50</h2><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u521b\u57df\u7f51\u5427\u4e09\u697c\u7684\u529e\u516c\u5ba4\u623f\u95f4\u91cc\uff0c\u51ed\u7a97\u671b\u7740\u5df7\u5b50\u5bf9\u9762\u7684\u7a97\u6237\u91cc\u4e5f\u4eae\u7740\u706f\uff0c\u8c22\u5b50\u5609\u770b\u4f3c\u7ea4\u5f31\u7684\u8eab\u5f71\u5012\u6620\u5728\u7a97\u6237\u4e0a\uff0c\u4e13\u5fc3\u81f4\u5fd7\u7684\u505a\u7740\u81ea\u5df1\u7684\u4e8b\u60c5\uff0c\u6ca1\u6709\u6ce8\u610f\u5230\u5f20\u606a\u5750\u5728\u5bf9\u9762\u7684\u529e\u516c\u5ba4\u623f\u95f4\u5728\u89c2\u5bdf\u5979\u3002</p><p>\u3000\u3000\u201c\u5f88\u591a\u65f6\u5019\uff0c\u6211\u4eec\u79bb\u5f00\u65f6\uff0c\u5bf9\u9762\u8fd8\u4eae\u7740\u706f\uff0c\u201d\u675c\u98de\u770b\u5230\u5f20\u606a\u671b\u7740\u5bf9\u9762\u7684\u7a97\u6237\uff0c\u8d70\u8fc7\u6765\u8bf4\u9053\uff0c\u201c\u6307\u4e0d\u5b9a\u4ec0\u4e48\u65f6\u5019\u4f1a\u62ff\u51fa\u53eb\u4eba\u5927\u5403\u4e00\u60ca\u7684\u4e1c\u897f\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u662f\u4e00\u5b9a\u7684\uff0c\u4e5f\u503c\u5f97\u671f\u5f85\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u79bb\u5f00\u7a97\u5b50\uff0c\u62d6\u4e86\u4e00\u5f20\u6905\u5b50\u5750\u4e0b\u6765\uff0c\u770b\u7740\u51e0\u4e2a\u5973\u5b69\u5b50\u5728\u65c1\u8fb9\u56f4\u7740\u5e2d\u82e5\u7433\u5b89\u6170\u5979\u3002\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u3001\u9648\u5983\u84c9\u5728\u5df7\u5b50\u53e3\u9047\u5230\u4f24\u5fc3\u6d41\u6cea\u7684\u5e2d\u82e5\u7433\uff0c\u5b89\u6170\u522b\u4eba\u7684\u5973\u4eba\u603b\u662f\u4e00\u4ef6\u5f88\u75db\u82e6\u7684\u4e8b\u60c5\uff0c\u5c31\u4e00\u8d77\u5230\u521b\u57df\u7684\u529e\u516c\u5ba4\u91cc\uff0c\u5b89\u6170\u7684\u5de5\u4f5c\u4ea4\u7ed9\u522b\u4eba\u53bb\u505a\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u95f4\u8fd8\u7559\u5728\u529e\u516c\u5ba4\u7684\u5dee\u4e0d\u591a\u90fd\u662f\u4e0e\u5e2d\u82e5\u7433\u5173\u7cfb\u6781\u719f\u7684\u4eba\uff0c\u867d\u8bf4\u5b59\u9759\u6aac\u3001\u4ee4\u5c0f\u71d5\u8fd9\u4e9b\u5973\u5b69\u5b50\u90fd\u8eab\u540c\u611f\u53d7\u7684\u8c34\u8d23\u738b\u6d77\u7c9f\u3001\u5b89\u6170\u5e2d\u82e5\u7433\uff0c\u65f6\u5b66\u658c\u8fd9\u4e9b\u7537\u7684\u5374\u53c8\u90fd\u6ca1\u5fc3\u6ca1\u80ba\u7684\u3001\u7b11\u5bb9\u8be1\u5f02\u7684\u62fd\u7740\u8499\u4e50\u5230\u4e00\u8fb9\u8bf4\u8bdd\uff1a\u5e2d\u82e5\u7433\u4e0e\u738b\u6d77\u7c9f\u5206\u624b\u90fd\u662f\u4ed6\u4eec\u559c\u800c\u4e50\u89c1\u7684\u4e8b\u60c5\u3002\u4e4b\u524d\u5927\u5bb6\u5c31\u5bf9\u738b\u6d77\u7c9f\u6ee1\u8179\u610f\u89c1\uff0c\u4f46\u53c8\u4e0d\u80fd\u76f4\u63a5\u660e\u8a00\u529d\u544a\u8981\u5e2d\u82e5\u7433\u540c\u738b\u6d77\u7c9f\u5206\u624b\uff0c\u8fd9\u65f6\u5019\u4ed6\u4eec\u90fd\u8981\u5f39\u51a0\u76f8\u5e86\u4e86\uff0c\u867d\u7136\u8499\u4e50\u8868\u73b0\u51fa\u75db\u5fc3\u75be\u9996\u7684\u6837\u5b50\uff0c\u9b3c\u624d\u76f8\u4fe1\u4ed6\u4f1a\u4e3a\u5e2d\u82e5\u7433\u4e0e\u738b\u6d77\u7c9f\u5206\u624b\u7684\u4e8b\u60c5\u4f24\u5fc3\u3002</p><p>\u3000\u3000\u201c\u8fd9\u7b97\u600e\u4e48\u56de\u4e8b\u561b\uff0c\u5206\u624b\u8fde\u4e00\u4e2a\u7406\u7531\u90fd\u4e0d\u7ed9\u2026\u2026\u201d\u4ee4\u5c0f\u71d5\u5fff\u6068\u4e0d\u5e73\u7684\u8bf4\u9053\uff0c\u9648\u5983\u84c9\u5750\u5728\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u4e5f\u9644\u548c\u7740\u8bf4\uff1a\u201c\u771f\u662f\u592a\u8fc7\u5206\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c0f\u58f0\u8ddf\u9648\u5983\u84c9\u7b11\u7740\u8bf4\uff1a\u201c\u7edd\u5927\u591a\u6570\u5206\u624b\u540e\u7684\u75db\u82e6\uff0c\u4e3b\u8981\u662f\u7ea0\u7ed3\u5176\u4e2d\u7684\u604b\u4eba\u4eec\u4e00\u76f4\u627e\u4e0d\u5230\u4e3a\u4ec0\u4e48\u5206\u624b\u7684\u7b54\u6848\uff0c\u7ea0\u7ed3\u5728\u5fc3\u91cc\u5927\u6982\u4f1a\u4e00\u76f4\u75db\u82e6\u4e0b\u53bb\u5427\uff0c\u201d\u770b\u7740\u65f6\u5b66\u658c\u4ed6\u4eec\u4e00\u8138\u7684\u8f7b\u677e\uff0c\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u8fd9\u91cc\u771f\u4e0d\u662f\u4e00\u4e2a\u5b89\u6170\u521a\u7ecf\u5386\u5206\u624b\u75db\u82e6\u7684\u5973\u5b69\u7684\u597d\u5730\u65b9\uff0c\u6709\u8fd9\u4e9b\u5bb6\u4f19\u5728\uff0c\u53ea\u4f1a\u8ba9\u4eba\u5fc3\u91cc\u66f4\u96be\u8fc7\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u5728\u65c1\u8fb9\u542c\u4e86\uff0c\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u628a\u7b54\u6848\u8bf4\u51fa\u6765\uff0c\u5c82\u4e0d\u662f\u53ef\u80fd\u4f1a\u66f4\u6b8b\u5fcd\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8ba9\u65f6\u5b66\u658c\u8fd9\u4e9b\u5bb6\u4f19\u5148\u8d70\u5427\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u5bb6\u4f19\u597d\u4e8b\u4e0d\u505a\uff0c\u5c3d\u4f1a\u6dfb\u6cb9\u52a0\u918b\u4e86\uff0c\u201d\u5f20\u606a\u7ad9\u8d77\u6765\uff0c\u8d70\u5230\u5e2d\u82e5\u7433\u7684\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6709\u4e86\u4e00\u4e2a\u7b54\u6848\u5c31\u4f1a\u8f7b\u677e\u4e0b\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5e2d\u82e5\u7433\u5934\u57cb\u5728\u529e\u516c\u684c\u4e0a\u5728\u62bd\u6ce3\u7740\uff0c\u5979\u542c\u5230\u5f20\u606a\u8ddf\u5979\u8bf4\u8bdd\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u773c\u775b\u7ea2\u80bf\u7684\u770b\u7740\u4ed6\uff0c\u54fd\u54bd\u7684\u8bf4\u9053\uff0c\u201c\u54ea\u6015\u4ed6\u9047\u5230\u66f4\u597d\u7684\u5973\u5b69\u5b50\u4e5f\u597d\uff0c\u54ea\u6015\u4ed6\u7a81\u7136\u5bf9\u6211\u611f\u5230\u4e5f\u597d\uff0c\u603b\u4e0d\u80fd\u50cf\u5f80\u5e38\u90a3\u6837\u7684\u5403\u8fc7\u996d\u7a81\u7136\u63d0\u51fa\u5206\u624b\u5374\u4e00\u4e2a\u7406\u7531\u90fd\u4e0d\u7ed9\u7684\u8d70\u6389\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6362\u4f5c\u662f\u6211\u7684\u8bdd\uff0c\u6211\u901a\u5e38\u4e3a\u4e86\u8ba9\u5bf9\u65b9\u597d\u53d7\u4e00\u4e9b\uff0c\u4f1a\u544a\u8bc9\u5bf9\u65b9\u6211\u7a81\u7136\u5bf9\u5f02\u6027\u4e0d\u611f\u5174\u8da3\u4e86\u2026\u2026\u5206\u624b\u53c8\u4e0d\u662f\u5929\u5d29\u5730\u88c2\u7684\u4e8b\u60c5\uff0c\u4f60\u6216\u8bb8\u4ee5\u4e3a\u738b\u6d77\u7c9f\u98de\u9ec4\u817e\u8fbe\u4e86\uff0c\u4ed6\u518d\u4e5f\u770b\u4e0d\u4e0a\u4f60\u4e86\uff0c\u4f60\u611f\u5230\u81ea\u5df1\u7ed9\u629b\u5f03\u4e86\uff0c\u4f60\u624d\u611f\u5230\u5982\u6b64\u7684\u4f24\u5fc3\uff1f\u6291\u6216\uff0c\u4f60\u5bf9\u4ed6\u7684\u611f\u60c5\u6df1\u5230\u5206\u624b\u5c31\u6709\u4e00\u79cd\u7ed9\u6495\u88c2\u7684\u75db\u82e6\uff1f\u201d\u5f20\u606a\u4ece\u529e\u516c\u684c\u4e0a\u6495\u4e86\u4e00\u5f20\u4fbf\u7b7e\u7eb8\u9012\u5230\u5e2d\u82e5\u7433\u9762\u524d\uff0c\u201c\u738b\u6d77\u7c9f\u7684\u7535\u8bdd\u662f\u591a\u5c11\uff0c\u4f60\u5199\u4e0b\u6765\u2026\u2026\u4f60\u4e0d\u662f\u5c31\u8981\u4e00\u4e2a\u7b54\u6848\u5417\uff1f\u6211\u8ba9\u4ed6\u8fc7\u6765\u544a\u4f60\u7b54\u6848\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5e2d\u82e5\u7433\u5403\u60ca\u7684\u770b\u7740\u5f20\u606a\uff0c\u4e0d\u6e05\u695a\u5f20\u606a\u4e3a\u4ec0\u4e48\u80fd\u80af\u5b9a\u4ed6\u5bf9\u738b\u6d77\u7c9f\u547c\u4e4b\u5219\u6765\uff0c\u8fdf\u7591\u7684\u62ff\u8d77\u7b14\uff0c\u5c06\u738b\u6d77\u7c9f\u7684\u624b\u673a\u53f7\u7801\u5199\u4e0b\u6765\u9012\u7ed9\u5f20\u606a\u3002</p><p>\u3000\u3000\u675c\u98de\u5c06\u5176\u4ed6\u95f2\u6742\u4eba\u7b49\u90fd\u8d76\u8d70\u4e86\uff0c\u5c31\u5269\u4e0b\u4ed6\u4eec\u51e0\u4e2a\u4eba\u7559\u5728\u529e\u516c\u5ba4\u3002\u4ee4\u5c0f\u71d5\u5750\u5728\u65c1\u8fb9\uff0c\u542c\u5f20\u606a\u8ddf\u5e2d\u82e5\u7433\u8bf4\u4e86\u8fd9\u756a\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u771f\u7ed9\u5973\u5b69\u5b50\u65e0\u60c5\u7684\u629b\u5f03\u8fc7\u4f3c\u7684\uff0c\u771f\u60f3\u77e5\u9053\u8fd9\u4e2a\u5973\u5b69\u5b50\u662f\u8c01\u554a\u2026\u2026\u6211\u60f3\u60f3\u8ba4\u8bc6\u8fd9\u4e2a\u5973\u5b69\u5b50\u7684\u4eba\u4e0d\u5728\u5c11\u6570\u5427\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u62ff\u8d77\u529e\u516c\u684c\u4e0a\u7684\u5ea7\u673a\uff0c\u7ed9\u738b\u6d77\u7c9f\u62e8\u7535\u8bdd\u3002</p><p>\u3000\u3000\u7535\u8bdd\u4e00\u62e8\u5c31\u901a\uff0c\u5927\u6982\u5e2d\u82e5\u7433\u4e4b\u524d\u7528\u8fc7\u516c\u53f8\u7684\u7535\u8bdd\u7ed9\u738b\u6d77\u7c9f\u6253\u8fc7\u7535\u8bdd\uff0c\u738b\u6d77\u7c9f\u4ee5\u4e3a\u662f\u5e2d\u82e5\u7433\u6253\u7ed9\u4ed6\u7684\u7535\u8bdd\uff0c\u63a5\u901a\u7535\u8bdd\u8bed\u6c14\u6709\u4e9b\u4e0d\u8010\u70e6\uff1a\u201c\u51b7\u9759\u7684\u60f3\u4e00\u60f3\u5427\uff0c\u5bf9\u4f60\u4e5f\u597d\u3001\u5bf9\u6211\u4e5f\u597d\uff0c\u5206\u624b\u662f\u6700\u5408\u9002\u7684\u9009\u62e9\u2026\u2026\u6ca1\u6709\u5e0c\u671b\u7684\u8017\u5728\u4e00\u8d77\uff0c\u5bf9\u8c01\u90fd\u6ca1\u6709\u597d\u5904\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\uff0c\u201d\u5f20\u606a\u8f7b\u7b11\u4e86\u4e00\u58f0\uff0c\u6253\u65ad\u738b\u6d77\u7c9f\u7684\u8bdd\uff0c\u201c\u6211\u662f\u5f20\u606a\uff0c\u6211\u5728\u521b\u57df\u7f51\u5427\u697c\u4e0a\uff0c\u4f60\u65b9\u4e0d\u65b9\u4fbf\u8fc7\u6765\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u80fd\u542c\u89c1\u7535\u8bdd\u90a3\u5934\u5239\u8f66\u7684\u58f0\u97f3\uff0c\u738b\u6d77\u7c9f\u5927\u6982\u6ca1\u6709\u6599\u5230\u4f1a\u662f\u81ea\u5df1\u7ed9\u4ed6\u6253\u7535\u8bdd\u5427\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u738b\u6d77\u7c9f\u7684\u58f0\u97f3\u53c8\u4f20\u8fc7\u6765\uff1a\u201c\u597d\u7684\uff0c\u6211\u9a6c\u4e0a\u8fc7\u6765\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u7535\u8bdd\u6302\u4e86\uff0c\u5bf9\u5e2d\u82e5\u7433\u8bf4\u9053\uff1a\u201c\u4ed6\u9a6c\u4e0a\u8fc7\u6765\uff0c\u4f60\u82e5\u662f\u9700\u8981\u4ec0\u4e48\u7b54\u6848\uff0c\u7b49\u4f1a\u513f\u53ef\u4ee5\u95ee\u4ed6\u2026\u2026\u4e0d\u8fc7\u5462\uff0c\u604b\u4eba\u8d70\u5230\u5c3d\u5934\uff0c\u8ffd\u7a76\u4ec0\u4e48\u7b54\u6848\u90fd\u53ea\u662f\u8ddf\u81ea\u5df1\u8fc7\u4e0d\u53bb\u800c\u5df2\u3002\u201d</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u8be7\u5f02\u738b\u6d77\u7c9f\u600e\u4e48\u4f1a\u8ba9\u5f20\u606a\u62db\u4e4b\u5373\u6765\uff0c\u8be7\u5f02\u4e4b\u4f59\uff0c\u5012\u4e0d\u7528\u4f24\u5fc3\u7684\u62bd\u6ce3\u4e86\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u6ca1\u6709\u79bb\u5f00\u591a\u8fdc\uff0c\u8fc7\u4e86\u4e09\u56db\u5206\u949f\u5c31\u542c\u89c1\u697c\u4e0b\u7684\u6c7d\u8f66\u5f15\u64ce\u7184\u706d\u7684\u58f0\u97f3\uff0c\u4e00\u4f1a\u513f\u5c31\u542c\u89c1\u738b\u6d77\u7c9f\u8e29\u7740\u697c\u68af\u4e0a\u6765\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u63a8\u95e8\u8fdb\u6765\uff0c\u76ee\u5149\u626b\u8fc7\u4f17\u4eba\uff0c\u5728\u5e2d\u82e5\u7433\u8138\u4e0a\u4e5f\u53ea\u505c\u7559\u4e86\u4e24\u79d2\uff0c\u5374\u662f\u4ee5\u4e00\u79cd\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8bed\u6c14\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u606a\u5c11\u627e\u6211\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u770b\u5230\u738b\u6d77\u7c9f\u8fd9\u822c\u6a21\u6837\uff0c\u5e2d\u82e5\u7433\u90a3\u7ed9\u5206\u624b\u540e\u7684\u75db\u82e6\u5360\u636e\u6ee1\u7684\u5fc3\u7a81\u7136\u677e\u4e86\u4e0b\u6765\uff0c\u5f20\u606a\u4fa7\u8fc7\u5934\u770b\u4e86\u5979\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u9700\u8981\u4ec0\u4e48\u7b54\u6848\u7684\u8bdd\uff0c\u4f60\u8fd9\u65f6\u5019\u5c3d\u53ef\u4ee5\u5f00\u53e3\u95ee\u3002\u201d\u5e2d\u82e5\u7433\u6447\u4e86\u6447\u5934\uff0c\u8fd9\u624d\u53d1\u89c9\u7b54\u6848\u5df2\u7ecf\u4e0d\u91cd\u8981\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u5bf9\u738b\u6d77\u7c9f\u8bf4\u9053\uff1a\u201c\u6211\u627e\u4f60\u6709\u4e8b\u60c5\uff0c\u4f60\u8ddf\u6211\u5230\u529e\u516c\u5ba4\u91cc\u6765\u8c08\u2026\u2026\u201d\u62cd\u4e86\u62cd\u675c\u98de\u7684\u80a9\u8180\uff0c\u8ba9\u4ed6\u4e00\u8d77\u8fdb\u6765\u3002</p><p>\u3000\u3000\u501f\u4e86\u675c\u98de\u7684\u529e\u516c\u5ba4\uff0c\u675c\u98de\u8fd9\u65f6\u5019\u6b63\u5904\u4e8e\u521b\u4e1a\u9636\u6bb5\uff0c\u80fd\u7701\u5219\u7701\uff0c\u4f1a\u5ba2\u533a\u57df\u5c31\u4e24\u5ea7\u5355\u4eba\u6c99\u53d1\uff0c\u5f20\u606a\u8bf7\u738b\u6d77\u7c9f\u5750\u4e0b\u6765\uff0c\u8ba9\u675c\u98de\u5728\u4e00\u65c1\u7ad9\u7740\uff0c\u4ed6\u5bf9\u738b\u6d77\u7c9f\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u65e0\u804a\u5230\u56e0\u4e3a\u4f60\u8ddf\u5e2d\u82e5\u7433\u5206\u624b\u624d\u627e\u4f60\u8fc7\u6765\uff0c\u6b63\u597d\u6709\u8fd9\u6837\u7684\u673a\u4f1a\uff0c\u5c31\u627e\u4f60\u8fc7\u6765\u804a\u4e00\u804a\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u77e5\u9053\u5f20\u606a\u7684\u8eab\u4efd\u4e4b\u540e\uff0c\u4e00\u76f4\u5bb3\u6015\u770b\u5230\u4ed6\uff0c\u867d\u7136\u4ed6\u73b0\u5728\u80fd\u9047\u5230\u6bd4\u5e2d\u82e5\u7433\u59ff\u8272\u66f4\u51fa\u4f17\u7684\u5973\u5b69\u5b50\uff0c\u4f46\u662f\uff0c\u8981\u4e0d\u662f\u5f20\u606a\u7684\u7f18\u6545\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u8f7b\u6613\u8ddf\u5e2d\u82e5\u7433\u8bf4\u5206\u624b\uff0c\u53c8\u50bb\u53c8\u5929\u771f\u7684\u5973\u5b69\u5b50\uff0c\u8eab\u8fb9\u591a\u4e00\u4e2a\u53c8\u600e\u4e48\u4f1a\u5acc\u591a\u5462\uff1f\u4f46\u662f\u5f20\u606a\u6253\u7535\u8bdd\u7ed9\u4ed6\uff0c\u4ed6\u53c8\u6ca1\u6709\u80c6\u91cf\u62d2\u7edd\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u6709\u4ec0\u4e48\u4e8b\u60c5\u5429\u5490\u5c31\u5c3d\u7ba1\u5429\u5490\u597d\u4e86\u2026\u2026\u201d\u738b\u6d77\u7c9f\u641e\u4e0d\u6e05\u5f20\u606a\u558a\u4ed6\u8fc7\u6765\u7684\u539f\u56e0\uff0c\u53ea\u662f\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8bf4\u7740\u8bdd\u3002</p><p>\u3000\u3000\u201c\u4e0d\u7528\u8fd9\u4e48\u62d8\u675f\uff0c\u6d77\u7c9f\u79d1\u6280\u5982\u6b64\u4e5f\u662f\u5927\u516c\u53f8\u4e86\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u867d\u7136\u6211\u8ddf\u7cbe\u5178\u5730\u4ea7\u7684\u6797\u96ea\u7b49\u4eba\u6709\u4e9b\u5c0f\u77db\u76fe\uff0c\u4f46\u6211\u8ddf\u4f60\u603b\u662f\u6709\u670b\u53cb\u4e4b\u8c0a\u7684\uff0c\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u7684\u76d8\u5b50\u662f\u8d8a\u6491\u8d8a\u5927\u4e86\uff0c\u6211\u627e\u4f60\u8fc7\u6765\u662f\u60f3\u95ee\u95ee\u4f60\u5bf9\u8fd9\u4e2a\u9879\u76ee\u6b64\u65f6\u7684\u89c4\u6a21\u6709\u4ec0\u4e48\u60f3\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u542c\u5230\u5f20\u606a\u679c\u7136\u63d0\u8d77\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\uff0c\u738b\u6d77\u7c9f\u989d\u5934\u7684\u6c57\u6c34\u90fd\u5feb\u6e17\u51fa\u6765\u4e86\uff0c\u4ed6\u5982\u4eca\u7b97\u662f\u7ed9\u5f20\u606a\u62ff\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u7ed1\u67b6\u4f4f\u4e86\uff0c\u770b\u5230\u5f20\u606a\u63d0\u8d77\u8fd9\u70b9\uff0c\u5c31\u80c6\u98a4\u5fc3\u60ca\uff0c\u9664\u975e\u4ed6\u80fd\u6bc5\u7136\u653e\u5f03\u773c\u4e0b\u56e0\u4e3a\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u6240\u83b7\u5f97\u7684\u6743\u52bf\u4e0e\u5730\u4f4d\u3002\u53ef\u662f\u4eba\u751f\u767e\u5e74\uff0c\u4e00\u4e2a\u4eba\u80fd\u6709\u591a\u5c11\u673a\u4f1a\u722c\u5230\u5982\u6b64\u7684\u9ad8\u5ea6\uff0c\u4e00\u65e6\u62e5\u6709\u8c01\u53c8\u7518\u5fc3\u5931\u53bb\uff1f\u738b\u6d77\u7c9f\u60ca\u9b42\u4e0d\u5b9a\u7684\u770b\u7740\u5f20\u606a\uff0c\u8bf4\u9053\uff1a\u201c\u606a\u5c11\u6709\u4ec0\u4e48\u770b\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u4ec0\u4e48\u770b\u6cd5\uff0c\u8981\u4e0d\u662f\u770b\u5230\u4f60\uff0c\u6211\u751a\u81f3\u90fd\u5fd8\u6389\u6709\u8fd9\u56de\u4e8b\u4e86\u3002\u770b\u5230\u4f60\u4e4b\u540e\uff0c\u5c31\u6709\u4e9b\u62c5\u5fc3\uff1a\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u8fdb\u884c\u5230\u73b0\u5728\uff0c\u53ef\u4ee5\u8bf4\u662f\u8981\u5730\u6709\u5730\u3001\u8981\u94b1\u6709\u94b1\uff0c\u80fd\u8e0f\u8e0f\u5b9e\u5b9e\u7684\u53bb\u505a\u9879\u76ee\uff0c\u4f1a\u6709\u5f88\u5927\u7684\u53d1\u5c55\uff0c\u6211\u5c31\u62c5\u5fc3\u6709\u4e9b\u4eba\u4f1a\u6ca1\u6709\u592a\u591a\u7684\u8010\u5fc3\u2026\u2026\u201d\u5f20\u606a\u76ee\u5149\u9510\u5229\u7684\u76ef\u7740\u738b\u6d77\u7c9f\uff0c\u201c\u8981\u662f\u4efb\u67d0\u4e9b\u4eba\u501f\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u540d\u4e49\u80e1\u4f5c\u975e\u4e3a\uff0c\u5c06\u4ee5\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u540d\u4e49\u5708\u6765\u7684\u5730\u5212\u8d70\u3001\u5c06\u4ee5\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u540d\u4e49\u878d\u6765\u7684\u8d44\u91d1\u632a\u7528\u5230\u522b\u5904\uff0c\u6211\u60f3\u653e\u4efb\u4ed6\u4eec\u8fd9\u4e48\u505a\u5bf9\u4f60\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u597d\u5904\uff0c\u5230\u5934\u6765\u8bf4\u4e0d\u5b9a\u4f60\u53ea\u80fd\u5f97\u5230\u4e00\u573a\u7a7a\u6b22\u559c\u2026\u2026\u8c01\u90fd\u4e0d\u60f3\u81ea\u5df1\u5230\u6700\u540e\u4f1a\u7ed9\u6253\u56de\u539f\u5f62\uff0c\u6211\u4e0d\u60f3\uff0c\u6211\u77e5\u9053\u4f60\u4e5f\u4e0d\u60f3\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u738b\u6d77\u7c9f\u542c\u5f20\u606a\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e00\u5934\u51b7\u6c57\uff0c\u4ed6\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u72e0\u4e0b\u5fc3\u6765\u8bf4\u9053\uff0c\u201c\u6d77\u7c9f\u79d1\u6280\u5b9e\u9645\u4e0a\u5df2\u7ecf\u7ed9\u7cbe\u5178\u5730\u4ea7\u63a7\u80a1\u4e86\uff0c\u606a\u5c11\u4ee5\u4e3a\u6211\u80fd\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ef\u4ee5\u505a\u7684\u4e8b\u5f53\u7136\u8fd8\u6709\u5f88\u591a\uff0c\u8096\u660e\u5efa\u5e02\u957f\u672a\u5fc5\u5c31\u4f1a\u7eb5\u5bb9\u4ed6\u4eec\u80e1\u4f5c\u975e\u4e3a\uff0c\u6211\u5076\u5c14\u4e5f\u4f1a\u7741\u5f00\u4e00\u53ea\u773c\u775b\u770b\u7740\u4f60\u4eec\u2026\u2026\u5173\u952e\u8fd8\u5728\u4f60\u81ea\u5df1\uff0c\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u4f60\u8ddf\u4ed6\u4eec\u540c\u6d41\u5408\u6c61\u53c8\u80fd\u6709\u591a\u5927\u597d\u5904\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4e5f\u6ca1\u6709\u6253\u7b97\u8ddf\u738b\u6d77\u7c9f\u63a8\u5fc3\u7f6e\u8179\u7684\u6df1\u804a\u4e0b\u53bb\uff0c\u7ad9\u4e86\u8d77\u6765\u90d1\u91cd\u5176\u4e8b\u7684\u8bf4\u9053\uff0c\u201c\u4f60\u7684\u5e0c\u671b\u4e0e\u51fa\u8def\u90fd\u5728\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u4e0a\uff0c\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5230\u5934\u6765\u662f\u7a7a\uff0c\u4f60\u5f97\u5230\u7684\u4e5f\u662f\u7a7a\uff0c\u4f60\u597d\u81ea\u4e3a\u4e4b\u5427\u2026\u2026\u54e6\uff0c\u5bf9\u4e86\uff0c\u5e2d\u82e5\u7433\u8fd8\u5728\u5916\u9762\uff0c\u8981\u662f\u6ca1\u8bdd\u53ef\u8bf4\u5c31\u5f84\u76f4\u79bb\u5f00\u597d\u4e86\uff0c\u6211\u603b\u4e0d\u4f1a\u4e3a\u8fd9\u79cd\u4e8b\u66ff\u5979\u6253\u62b1\u4e0d\u5e73\u7684\u3002\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u6253\u5f00\u529e\u516c\u5ba4\u7684\u95e8\u9001\u738b\u6d77\u7c9f\u79bb\u5f00\u3002</p><p>\u3000\u3000\u770b\u5230\u738b\u6d77\u7c9f\u7ecf\u8fc7\u5916\u9762\u7684\u529e\u516c\u5ba4\u65f6\u53ea\u56de\u5934\u770b\u4e86\u5e2d\u82e5\u7433\u4e00\u773c\u6ca1\u6709\u8bf4\u4ec0\u4e48\u5c31\u79bb\u5f00\u4e86\uff0c\u675c\u98de\u63a9\u4e0a\u95e8\uff0c\u8ddf\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u80e1\u5b97\u5e86\u3001\u6797\u96ea\u4ed6\u4eec\u5927\u6982\u6c38\u8fdc\u90fd\u60f3\u4e0d\u5230\uff0c\u738b\u6d77\u7c9f\u662f\u4f60\u63d2\u8fdb\u53bb\u7684\u4e00\u6839\u9489\u5b50\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u738b\u6d77\u7c9f\u8fd9\u679a\u9489\u5b50\u73b0\u5728\u8fd8\u6ca1\u6709\u53cd\u6297\u7684\u4f59\u5730\uff0c\u6240\u4ee5\u80fd\u4efb\u6211\u4eec\u634f\u6765\u634f\u53bb\uff0c\u7b49\u4ed6\u6210\u4e86\u706b\u5019\uff0c\u6307\u4e0d\u5b9a\u5c31\u6210\u4e86\u54ac\u4eba\u7684\u6bd2\u86c7\u4e86\u3002\u201d</p></article>"}}}]);
