(window.webpackJsonp=window.webpackJsonp||[]).push([[979],{1002:function(p){p.exports={chapterTitle:null,title:"\u7b2c791\u7ae0 \u679d\u8282\u6a2a\u751f",content:"<article><h2>\u7b2c791\u7ae0 \u679d\u8282\u6a2a\u751f</h2><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u662f\u5728\u4e5d\u516b\u91d1\u5c71\u7535\u5b50\u4ea7\u4e1a\u53d1\u5c55\u8bba\u575b\u53ec\u5f00\u7684\u5f53\u5929\u8d76\u5230\u91d1\u5c71\u4e0a\u7684\uff0c\u4ece\u5efa\u90ba\u51fa\u53d1\u8d70\u56fd\u9053\u5230\u91d1\u5c71\u9700\u8981\u8fd1\u4e94\u4e2a\u5c0f\u65f6\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u8d76\u5230\u91d1\u5c71\u4e4b\u524d\uff0c\u8bb8\u9e3f\u4f2f\u3001\u5468\u6e38\u7b49\u4eba\u5df2\u7ecf\u5148\u8d76\u5230\u91d1\u5c71\u6709\u4e24\u5929\u4e86\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u5728\u4e1c\u6d77\u7701\u5185\u5927\u89c4\u6a21\u7684\u8425\u6797\u9879\u76ee\u5728\u7ecf\u8fc7\u8fd1\u4e24\u5e74\u8270\u82e6\u5353\u7edd\u7684\u594b\u6218\u4e4b\u540e\u7ec8\u4e8e\u63a5\u8fd1\u5c3e\u58f0\u4e86\uff0c\u4f46\u662f\u6b63\u8ba1\u5212\u5c06\u8425\u6797\u5de5\u4f5c\u63a8\u8fdb\u5230\u4e00\u6c5f\u4e4b\u9694\u7684\u6c5f\u5357\u7701\u5374\u53d7\u963b\u4e86\uff0c\u5f97\u4e0d\u5230\u7701\u7ea7\u6709\u5173\u90e8\u95e8\u7684\u652f\u6301\uff0c\u5468\u6e38\u3001\u8bb8\u9e3f\u4f2f\u5230\u91d1\u5c71\u6765\uff0c\u662f\u8ddf\u6c5f\u5357\u7701\u6709\u5173\u90e8\u95e8\u8fdb\u884c\u8fdb\u4e00\u6b65\u7684\u6c9f\u901a\uff0c\u770b\u6709\u6ca1\u6709\u53ef\u80fd\u52aa\u529b\u7684\u5730\u65b9\u3002\u7701\u91cc\u4e0d\u5728\u6797\u4e1a\u4f53\u5236\u6539\u9769\u7684\u95ee\u9898\u4e0a\u5927\u5200\u9614\u65a7\u7684\u5f00\u53e3\u5b50\uff0c\u4e0b\u9762\u53bf\u5e02\u5730\u65b9\u653f\u5e9c\u60f3\u8981\u505a\u51fa\u7a81\u7834\u7684\u96be\u5ea6\u66f4\u5927\u3001\u5de5\u4f5c\u5c06\u66f4\u7e41\u7410\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e5f\u662f\u5728\u5f20\u606a\u4ed6\u4eec\u4e4b\u524d\u4e00\u5929\u8d76\u5230\u91d1\u5c71\u7684\uff0c\u4ed6\u662f\u53d7\u9080\u53c2\u52a0\u91d1\u5c71\u5e02\u59d4\u3001\u5e02\u653f\u5e9c\u7ec4\u7ec7\u4e3e\u529e\u7684\u4e5d\u516b\u91d1\u5c71\u7535\u5b50\u4ea7\u4e1a\u53d1\u5c55\u8bba\u575b\uff0c\u867d\u8bf4\u5468\u747e\u745c\u672a\u5fc5\u9ad8\u5174\u770b\u5230\u6709\u9526\u6e56\u7684\u4eba\u51fa\u5e2d\u91d1\u5c71\u5e02\u59d4\u3001\u5e02\u653f\u5e9c\u7ec4\u7ec7\u4e3e\u529e\u7684\u8fd9\u6b21\u6d3b\u52a8\uff0c\u4f46\u662f\u9526\u6e56\u5728\u56fd\u5185\u7535\u5b50\u4ea7\u4e1a\u7684\u5730\u4f4d\u5df2\u7ecf\u662f\u4e3e\u8db3\u8f7b\u91cd\uff0c\u4e0d\u662f\u5979\u4eec\u60f3\u5ffd\u7565\u5c31\u80fd\u591f\u5ffd\u7565\u7684\uff0c\u751a\u81f3\u8981\u5b89\u6392\u9648\u4fe1\u751f\u5728\u5927\u4f1a\u4e0a\u8f83\u957f\u65f6\u95f4\u7684\u53d1\u8a00\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u5230\u91d1\u5c71\u540e\uff0c\u6ca1\u6709\u76f4\u63a5\u8d76\u5230\u5e02\u533a\uff0c\u5927\u4e2d\u5348\u7684\uff0c\u9648\u4fe1\u751f\u3001\u5468\u6e38\u3001\u8bb8\u9e3f\u4f2f\u4ed6\u4eec\u90fd\u6709\u5404\u81ea\u7684\u4e8b\u60c5\u8981\u5fd9\u788c\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u5c31\u9009\u62e9\u5728\u91d1\u5c71\u7684\u897f\u5317\u90ca\u6c5f\u5729\u9547\u505c\u8f66\u5403\u996d\uff1b\u53f6\u5efa\u658c\u4e5f\u6ca1\u6709\u901a\u77e5\u76db\u946b\u73af\u7403\u7535\u5668\u9a7b\u91d1\u5c71\u7684\u7b79\u5907\u5904\u5de5\u4f5c\u4eba\u5458\u3002</p><p>\u3000\u3000\u6c5f\u5729\u9547\u662f\u996e\u9a6c\u6cb3\u6d41\u5165\u5c0f\u6c5f\u7684\u4ea4\u4f1a\u53e3\u5b50\uff0c\u91d1\u5c71\u6e56\u5c31\u5728\u79bb\u6c5f\u5729\u9547\u4e09\u5341\u516c\u91cc\u4e0d\u5230\u7684\u4e0a\u6e38\u3002\u5f20\u606a\u5230\u91d1\u5c71\u7684\u6b21\u6570\u5c48\u6307\u53ef\u6570\uff0c\u4f46\u4e5f\u6e05\u695a\u6c5f\u5729\u9547\u7684\u6e56\u9c9c\u3001\u6c5f\u9c9c\u5f88\u51fa\u540d\uff0c\u8fdb\u5165\u57ce\u91cc\uff0c\u5728\u8c6a\u534e\u9152\u5e97\u91cc\u53cd\u800c\u5403\u4e0d\u4e0a\u5730\u65b9\u7279\u8272\u9c9c\u660e\u7684\u83dc\u80b4\u4e86\u3002</p><p>\u3000\u3000\u770b\u5230\u4e00\u5bb6\u4e0d\u5927\u663e\u773c\u3001\u4f46\u662f\u95e8\u524d\u505c\u7740\u51e0\u90e8\u5f53\u5730\u8f66\u724c\u8c6a\u534e\u8f7f\u8f66\u7684\u9152\u5e97\u524d\u505c\u4e0b\u6765\uff0c\u5f20\u606a\u8ddf\u53f6\u5efa\u658c\u8bf4\u9053\uff1a\u201c\u8ddf\u7740\u5f53\u5730\u6709\u94b1\u4eba\u627e\u5403\u98df\u603b\u4e0d\u4f1a\u9519\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u62b1\u6028\u5728\u8def\u4e0a\u8017\u7684\u65f6\u95f4\u592a\u957f\uff0c\u4e0b\u8f66\u540e\u6447\u5934\u8e22\u817f\uff0c\u8212\u5c55\u7b4b\u9aa8\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u6caa\u6d59\u7684\u6c11\u8425\u8001\u677f\u6709\u8d2d\u4e70\u79c1\u4eba\u98de\u673a\u7684\u4e86\uff0c\u5565\u65f6\u5019\u6211\u4eec\u4e5f\u6574\u4e24\u67b6\u73a9\u73a9\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u53ea\u80fd\u73a9\u73a9\u800c\u5df2\uff0c\u91d1\u5c71\u7684\u673a\u573a\u4f1a\u5141\u8bb8\u79c1\u4eba\u98de\u673a\u964d\u843d\u5417\uff1f\u201d\u5f20\u606a\u624d\u4e0d\u9ad8\u5174\u9ad8\u4ef7\u4e70\u4e24\u67b6\u98de\u673a\u6446\u5728\u540e\u9662\u91cc\u5f53\u73a9\u5177\u73a9\uff0c\u201c\u803f\u91cd\u9633\u662f\u5982\u4f55\u7406\u76f4\u6c14\u58ee\u7684\u5b9e\u884c\u5bf9\u7535\u4fe1\u8fd0\u8425\u7684\u5784\u65ad\u7684\uff1f\u4e0d\u5c31\u662f\u62ff\u65e0\u7ebf\u7535\u7ba1\u5236\u4e0e\u56fd\u5bb6\u5b89\u5168\u5212\u7b49\u53f7\u5417\uff1f\u6211\u5c31\u4e0d\u660e\u767d\u4e86\uff0c\u90fd\u8fdb\u5165\u4e92\u8054\u7f51\u65f6\u4ee3\uff0c\u803f\u91cd\u9633\u7adf\u7136\u8fd8\u80fd\u7406\u76f4\u6c14\u58ee\u7684\u62ff\u6765\u65e0\u7ebf\u7535\u7ba1\u5236\u4e0e\u56fd\u5bb6\u5b89\u5168\u51fa\u6765\u8bf4\u4e8b\uff1f\u4ed6\u4eec\u600e\u4e48\u4e0d\u628a\u4e92\u8054\u7f51\u7ed9\u5c01\u6389\uff1f\u4f60\u8bf4\u79c1\u4eba\u98de\u673a\u554a\uff0c\u5475\u5475\uff0c\u7b49\u4e0a\u4e8c\u5341\u5e74\uff0c\u90fd\u4e0d\u77e5\u9053\u6709\u6ca1\u6709\u673a\u4f1a\u98de\u4e0a\u5929\uff01\u6bd4\u8d77\u592a\u5962\u4f88\u7684\u68a6\u60f3\uff0c\u8fd8\u662f\u6307\u671b\u5efa\u90ba\u5230\u91d1\u5c71\u7684\u9ad8\u901f\u516c\u8def\u8d76\u7d27\u5efa\u6210\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u662f\u53ef\u6015\u7684\u5b9e\u7528\u4e3b\u4e49\u8005\uff0c\u5c0f\u4f19\u5b50\u4f60\u4eca\u5e74\u624d\u6ee1\u4e8c\u5341\u5c81\u554a\uff0c\u8003\u8651\u95ee\u9898\u6ca1\u5fc5\u8981\u6bd4\u6211\u8fd8\u8001\u6c14\u6a2a\u79cb\u5427\uff0c\u8fd8\u662f\u501f\u673a\u62b1\u6028\u90ae\u7535\u90e8\u7684\u5784\u65ad\u884c\u5f84\uff1f\u201d\u53f6\u5efa\u658c\u63fd\u8fc7\u5f20\u606a\u7684\u80a9\u8180\uff0c\u201c\u8bf4\u8d77\u90ae\u7535\u90e8\u7684\u5784\u65ad\u884c\u4e3a\uff0c\u8fd8\u771f\u662f\u8ba9\u4eba\u6ca1\u6709\u813e\u6c14\u2026\u2026\u201d\u8f66\u5b50\u505c\u5728\u86ee\u8fdc\u7684\u5730\u65b9\uff0c\u4e24\u4eba\u52fe\u80a9\u642d\u80a9\u671d\u95e8\u8138\u4e0d\u5927\u7684\u9152\u5e97\u91cc\u8d70\u53bb\u3002</p><p>\u3000\u3000\u8d75\u9e4f\u3001\u9a6c\u6d77\u9f99\u3001\u7fdf\u4e39\u9752\u4ee5\u53ca\u968f\u884c\u7684\u4e24\u540d\u5de5\u4f5c\u4eba\u5458\u53ca\u4e24\u8f86\u8f66\u7684\u53f8\u673a\u4e00\u5171\u4e03\u4e2a\u4eba\u8ddf\u7740\u5f20\u606a\u3001\u53f6\u5efa\u658c\u7684\u540e\u9762\u8fdb\u4e86\u9152\u5e97\u3002</p><p>\u3000\u3000\u9152\u5e97\u5185\u683c\u5c40\u4e0d\u5927\uff0c\u8fce\u5bbe\u7684\u5973\u670d\u52a1\u5458\u5012\u662f\u6e05\u79c0\uff0c\u957f\u76f8\u751c\u7f8e\uff0c\u7a7f\u7740\u7ea2\u7f0e\u523a\u7ee3\u7684\u65d7\u888d\uff0c\u65d7\u888d\u5185\u7a7f\u7740\u8584\u7ed2\u7684\u8089\u8272\u88e4\u889c\u5fa1\u5bd2\uff0c\u591a\u5c11\u663e\u5f97\u6709\u4e9b\u4e0d\u4f26\u4e0d\u7c7b\u3002\u8fd9\u65f6\u5019\u5df2\u7ecf\u6ca1\u6709\u5305\u53a2\u4e86\uff0c\u5927\u5802\u91cc\u4e5f\u4e03\u4e03\u516b\u516b\u5750\u6ee1\u4e86\u4eba\uff0c\u6ca1\u6709\u9009\u62e9\uff0c\u53ea\u80fd\u5728\u5927\u5802\u89d2\u843d\u91cc\u7684\u4e00\u5f20\u5706\u684c\u4e0a\u5750\u4e0b\u6765\u3002\u70b9\u8fc7\u83dc\uff0c\u670d\u52a1\u5458\u63a8\u8350\u5f53\u5730\u917f\u4ea7\u7684\u7c73\u9152\uff0c\u5f20\u606a\u5148\u8ba9\u670d\u52a1\u5458\u5c06\u7c73\u9152\u6e29\u4e86\u62ff\u4e0a\u6765\u5148\u559d\u7740\u3002</p><p>\u3000\u3000\u9152\u5e97\u91cc\u7684\u53a8\u5e08\u52a8\u4f5c\u5f88\u5feb\uff0c\u6ca1\u591a\u5927\u5de5\u592b\uff0c\u70b9\u7684\u83dc\u5c31\u51e0\u4e4e\u6ca1\u6709\u505c\u6b47\u7684\u7aef\u4e0a\u6765\u4e86\u3002\u65e9\u996d\u51e0\u4e4e\u6ca1\u6709\u5403\u4ec0\u4e48\uff0c\u8fd9\u65f6\u5019\u997f\u72e0\u4e86\uff0c\u770b\u5230\u83dc\u4e0a\u6765\uff0c\u5f20\u606a\u5c31\u62ff\u4e0b\u7b77\u5b50\u5939\u83dc\uff0c\u4e5f\u4e0d\u987e\u4ec0\u4e48\u5f62\u8c61\u3002\u6c5f\u9c9c\u6742\u9c7c\u7684\u76d8\u5b50\u6709\u4e00\u6839\u957f\u5934\u53d1\u8d34\u7740\u76d8\u8fb9\u2014\u2014\u9152\u5e97\u4e0d\u5927\uff0c\u6709\u4e9b\u7ec6\u8282\u603b\u4e0d\u4f1a\u592a\u5b8c\u7f8e\uff0c\u8fd9\u70b9\u5c0f\u4e8b\u60c5\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u8ba1\u8f83\u4ec0\u4e48\uff0c\u6b63\u6253\u7b97\u5c06\u957f\u5934\u53d1\u62e8\u51fa\u6765\u4e22\u6389\uff0c\u6401\u684c\u4e0a\u7684\u624b\u673a\u9707\u52a8\u8d77\u6765\uff0c\u7fdf\u4e39\u9752\u62ff\u8d77\u6765\u5148\u770b\uff0c\u544a\u8bc9\u5f20\u606a\uff1a\u201c\u9648\u5148\u751f\u6253\u6765\u7684\u7535\u8bdd\u3002\u201d\u4ee5\u4e3a\u9648\u4fe1\u751f\u6709\u4ec0\u4e48\u6025\u4e8b\uff0c\u5f20\u606a\u62e8\u4e86\u4e24\u4e0b\u6ca1\u6709\u5c06\u5934\u53d1\u4e1d\u62e8\u51fa\u6765\uff0c\u5148\u63a5\u7535\u8bdd\uff0c\u6307\u4e86\u6307\u83dc\u76d8\u91cc\u7684\u957f\u5934\u53d1\uff0c\u8ba9\u53f6\u5efa\u658c\u5f04\u6389\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\uff0c\u4f60\u4eec\u7684\u8f66\u5927\u6982\u5230\u4ec0\u4e48\u5730\u65b9\u4e86\uff0c\u79bb\u91d1\u5c71\u8fd8\u6709\u591a\u8fdc\uff1f\u201d\u9648\u4fe1\u751f\u5728\u7535\u8bdd\u91cc\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6ca1\u8bf4\u4e2d\u5348\u8981\u8bf7\u4f60\u5403\u996d\u554a\uff0c\u4f60\u600e\u4e48\u6253\u7535\u8bdd\u6765\u95ee\u6211\u5230\u54ea\u91cc\u4e86\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u770b\u7740\u53f6\u5efa\u658c\u6ca1\u6709\u5c06\u83dc\u91cc\u7684\u957f\u5934\u53d1\u6361\u8d77\u6765\u4e22\u6389\u800c\u662f\u62db\u624b\u5524\u521a\u624d\u90a3\u540d\u670d\u52a1\u5458\u8fc7\u6765\uff0c\u5fc3\u60f3\u8fd9\u5bb6\u4f19\u5927\u6982\u662f\u770b\u5230\u4eba\u5bb6\u670d\u52a1\u5458\u957f\u76f8\u751c\u7f8e\uff0c\u65e0\u804a\u60f3\u8c03\u620f\u51e0\u53e5\u6253\u53d1\u65f6\u95f4\uff0c\u6ca1\u7406\u4f1a\u4ed6\uff0c\u7ee7\u7eed\u8ddf\u9648\u4fe1\u751f\u901a\u7535\u8bdd\uff0c\u201c\u6211\u4eec\u5230\u91d1\u5c71\u4e86\uff0c\u5c31\u5728\u6c5f\u5729\u9547\u4e0a\u7684\u9152\u5e97\u91cc\u5403\u996d\uff0c\u79bb\u6536\u8d39\u7ad9\u5927\u6982\u4e5f\u5c31\u4e24\u4e09\u91cc\u8def\u5427\uff0c\u4f60\u4eec\u8981\u662f\u5927\u4e2d\u5348\u6ca1\u5730\u65b9\u5403\u996d\uff0c\u90a3\u5c31\u8fc7\u6765\u5427\uff0c\u8d76\u5f97\u65e9\uff0c\u8fd8\u6709\u4e9b\u6b8b\u7fb9\u51b7\u7099\u7b49\u7740\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u91d1\u5c71\u7684\u8d75\u6709\u4f26\u8d75\u5e02\u957f\u77e5\u9053\u4f60\u8981\u5230\u91d1\u5c71\u6765\uff0c\u4e00\u5b9a\u8981\u4eb2\u81ea\u5230\u6536\u8d39\u7ad9\u90a3\u91cc\u53bb\u63a5\u4f60\u4eec\uff0c\u4f60\u770b\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u5f20\u606a\u8ba9\u8bb8\u9e3f\u4f2f\u3001\u5468\u6e38\u4ee5\u53ca\u9648\u4fe1\u751f\u6709\u673a\u4f1a\u5411\u91d1\u5c71\u5e02\u5b98\u65b9\u900f\u9732\u4ed6\u4e0e\u53f6\u5efa\u658c\u6765\u91d1\u5c71\u7684\u6d88\u606f\uff0c\u770b\u5468\u747e\u745c\u4e0e\u91d1\u5c71\u5e02\u7684\u5176\u4ed6\u5b98\u5458\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\uff0c\u6ca1\u60f3\u5230\u91d1\u5c71\u5e02\u7684\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e02\u957f\u8d75\u6709\u4f26\u4f1a\u8fd9\u4e48\u79ef\u6781\u3002\u5f20\u606a\u5012\u662f\u6709\u4e9b\u72b9\u8c6b\u4e86\uff0c\u4ed6\u603b\u4e0d\u4f1a\u7acb\u9a6c\u8d76\u5230\u6536\u8d39\u7ad9\u53bb\u8fce\u63a5\u8d75\u6709\u4f26\uff0c\u4f46\u662f\u8ba9\u8d75\u6709\u4f26\u8d76\u8fc7\u6765\u770b\u7740\u4ed6\u4eec\u8fd9\u4e00\u684c\u7684\u6b8b\u7fb9\u51b7\u7099\u53c8\u6709\u4e9b\u5931\u793c\u3002</p><p>\u3000\u3000\u5f20\u606a\u544a\u8bc9\u9648\u4fe1\u751f\u9152\u5e97\u7684\u540d\u5b57\uff0c\u8ba9\u9648\u4fe1\u751f\u966a\u540c\u8d75\u6709\u4f26\u8d76\u8fc7\u6765\uff0c\u4ed6\u8fd9\u8fb9\u53e6\u5907\u4e00\u684c\u9152\u5e2d\u7b49\u7740\u8d75\u6709\u4f26\u8fc7\u6765\u4e00\u8d77\u7528\u9910\u5c31\u662f\u3002\u5f20\u606a\u6302\u4e86\u7535\u8bdd\uff0c\u53f6\u5efa\u658c\u6b63\u5c06\u90a3\u670d\u52a1\u5458\u558a\u8fc7\u6765\u6b63\u6307\u7740\u9910\u76d8\u91cc\u7684\u5934\u53d1\u4e1d\u5f97\u610f\u7684\u95ee\u5979\uff1a\u201c\u8fd9\u4e2a\u600e\u4e48\u8bf4\uff1f\u201d</p><p>\u3000\u3000\u670d\u52a1\u5458\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u8ba9\u53a8\u5b50\u7ed9\u4f60\u4eec\u91cd\u505a\u4e00\u76d8\u9c7c\u884c\u4e0d\u884c\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u5c31\u7b97\u4e86\uff0c\u201d\u9648\u4fe1\u751f\u5e26\u7740\u8d75\u6709\u4f26\u5f88\u5feb\u5c31\u4f1a\u8d76\u8fc7\u6765\uff0c\u5f20\u606a\u53ef\u6ca1\u6709\u65f6\u95f4\u966a\u53f6\u5efa\u658c\u5728\u8fd9\u91cc\u8c03\u620f\u5973\u670d\u52a1\u5458\uff0c\u5c06\u5934\u53d1\u4e1d\u6361\u8d77\u6765\u4e22\u6389\uff0c\u5bf9\u90a3\u4e2a\u5973\u670d\u52a1\u5458\u8bf4\u9053\uff0c\u201c\u4f60\u5e2e\u6211\u4eec\u7167\u7740\u83dc\u5355\u518d\u51c6\u5907\u4e00\u684c\u83dc\uff0c\u8981\u8d76\u7d27\u3002\u201d</p><p>\u3000\u3000\u5973\u670d\u52a1\u5458\u7741\u7740\u773c\u775b\u76ef\u4e86\u5f20\u606a\u770b\u4e86\u4e24\u79d2\u949f\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\u5c31\u8d70\u6389\u4e86\uff0c\u5979\u6ca1\u6709\u5f80\u540e\u9762\u7684\u53a8\u623f\u8d70\u53bb\uff0c\u800c\u662f\u8d70\u5230\u5927\u5385\u7684\u53e6\u4e00\u4fa7\uff0c\u63a8\u95e8\u8fdb\u4e86\u4e00\u95f4\u5305\u53a2\u3002\u5f20\u606a\u6b63\u8ddf\u53f6\u5efa\u658c\u7b49\u4eba\u89e3\u91ca\u9648\u4fe1\u751f\u4ed6\u4eec\u6b63\u5e26\u7740\u91d1\u5c71\u5e02\u5e02\u957f\u8d75\u6709\u4f26\u6b63\u5f80\u8fd9\u8fb9\u8d76\uff0c\u770b\u5230\u5973\u670d\u52a1\u5458\u8d70\u8fdb\u5305\u53a2\u4e5f\u6ca1\u6709\u591a\u60f3\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u5305\u53a2\u7684\u95e8\u6253\u5f00\uff0c\u4e00\u540d\u9752\u5e74\u7ad9\u5728\u5305\u53a2\u95e8\u53e3\u5f80\u8fd9\u8fb9\u770b\uff0c\u90a3\u540d\u5973\u670d\u52a1\u7ad9\u5728\u4ed6\u8eab\u8fb9\uff0c\u624b\u6307\u7740\u8fd9\u8fb9\u8ddf\u4ed6\u8bf4\u7740\u4e9b\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u90a3\u9752\u5e74\u5927\u6b65\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\uff0c\u5305\u53a2\u91cc\u53c8\u8d70\u51fa\u4e03\u516b\u540d\u6c49\u5b50\u4e0e\u90a3\u5973\u670d\u52a1\u5458\u4e00\u8d77\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\uff0c\u5f20\u606a\u6478\u4e86\u6478\u4e86\u540e\u8111\u52fa\uff0c\u8f6c\u8fc7\u5934\u95ee\u7fdf\u4e39\u9752\uff1a\u201c\u6211\u662f\u4e0d\u662f\u8bf4\u4e86\u4ec0\u4e48\u8bdd\u8ba9\u4ed6\u4eec\u8bef\u4f1a\u4e86\uff1f\u201d</p><p>\u3000\u3000\u90a3\u9752\u5e74\u8d70\u8fc7\u6765\uff0c\u4ed6\u4f3c\u4e4e\u4e5f\u8ba4\u51c6\u5f20\u606a\u5c31\u662f\u9886\u5934\u7684\u4eba\uff0c\u5bf9\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6211\u662f\u9152\u5e97\u7684\u8001\u677f\uff0c\u4e2d\u5348\u4e8b\u60c5\u591a\uff0c\u96be\u514d\u6709\u4e9b\u758f\u6f0f\uff0c\u5c31\u56e0\u4e3a\u83dc\u91cc\u51fa\u73b0\u4e00\u6839\u5934\u53d1\u4e1d\uff0c\u5c31\u8981\u6362\u6574\u684c\u83dc\uff0c\u662f\u4e0d\u662f\u6709\u4e9b\u5f3a\u4eba\u6240\u96be\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u6b63\u51c6\u5907\u5c06\u8bef\u4f1a\u89e3\u91ca\u6e05\u695a\uff0c\u6709\u4e2a\u5243\u7740\u5e73\u5934\u7684\u9752\u5e74\u8eab\u5b50\u63d2\u8fdb\u6765\uff0c\u77aa\u7740\u5f20\u606a\u4ed6\u4eec\uff1a\u201c\u5988\u7684\uff0c\u54ea\u6765\u7684\u6742\u79cd\u8d27\u8272\uff0c\u6562\u5230\u6c5f\u5729\u7684\u5730\u76d8\u6765\u8bb9\u8bc8\uff1f\u4f60\u4eec\u4ee5\u4e3a\u4eba\u591a\u5c31\u4e86\u4e0d\u8d77\u2026\u2026\u201d\u56de\u5934\u671d\u53e6\u4e00\u4e2a\u9752\u5e74\u558a\u9053\uff0c\u201c\u5f3a\u5b50\uff0c\u6253\u7535\u8bdd\uff0c\u558a\u4eba\u8fc7\u6765\uff01\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u9a6c\u6d77\u9f99\u4ee5\u53ca\u53f6\u5efa\u658c\u7684\u4fdd\u9556\u517c\u53f8\u673a\u7ad9\u8d77\u6765\u62e6\u5728\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u7684\u9762\u524d\uff0c\u514d\u5f97\u4ed6\u4eec\u7ed9\u51b2\u649e\u4e86\uff0c\u9a6c\u6d77\u9f99\u8ddf\u90a3\u4e2a\u81ea\u79f0\u662f\u9152\u5e97\u8001\u677f\u7684\u9752\u5e74\u89e3\u91ca\u9053\uff1a\u201c\u6211\u60f3\u4f60\u4eec\u662f\u8bef\u4f1a\u4e86\uff0c\u6211\u4eec\u6709\u5ba2\u4eba\u6b63\u8d76\u8fc7\u6765\uff0c\u6240\u4ee5\u8bf7\u4f60\u5e2e\u7740\u518d\u51c6\u5907\u4e00\u684c\u83dc\u2026\u2026\u5934\u53d1\u4e1d\u4e0d\u5934\u53d1\u4e1d\u7684\uff0c\u6211\u4eec\u4e5f\u6ca1\u6709\u6253\u7b97\u8ba1\u8f83\u4ec0\u4e48\u3002\u201d\u53e6\u4e24\u540d\u968f\u884c\u7684\u4fdd\u9556\u4e5f\u7ad9\u5230\u5f20\u606a\u4ed6\u4eec\u8eab\u540e\u3002</p><p>\u3000\u3000\u201c\u5988\u7684\u5df4\u5b50\uff0c\u8fd9\u65f6\u5019\u77e5\u9053\u6015\u4e86\uff1f\u201d\u9152\u5e97\u8001\u677f\u6ca1\u6709\u72d0\u7591\u7684\u770b\u7740\u9a6c\u6d77\u9f99\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5e73\u5934\u9752\u5e74\u5374\u5550\u4e86\u4e00\u53e3\uff0c\u4e00\u8138\u7684\u9119\u89c6\uff0c\u6307\u7740\u9a6c\u6d77\u9f99\u7684\u9f3b\u5b50\uff0c\u201c\u521a\u624d\u600e\u4e48\u4e0d\u6382\u91cf\u6382\u91cf\u81ea\u5df1\u7684\u5206\u91cf\uff1f\u201d\u53c8\u5429\u5490\u65c1\u8fb9\u7684\u5973\u670d\u52a1\u5458\uff0c\u201c\u7ed9\u4ed6\u4eec\u7ed3\u8d26\u628a\u4ed6\u4eec\u8d76\u51fa\u53bb\uff0c\u7b97\u4e09\u500d\u7684\u4ef7\u94b1\uff0c\u4ed6\u4eec\u8981\u8fd8\u6562\u6709\u4ec0\u4e48\u5e9f\u8bdd\uff0c\u518d\u8fc7\u6765\u627e\u6211\u4eec\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u54d1\u7136\u5931\u7b11\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u8fd9\u7b97\u662f\u6211\u4eec\u7ed9\u8bb9\u8bc8\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\uff0c\u4e0d\u670d\u6c14\uff01\u201d\u90a3\u5e73\u5934\u9752\u5e74\u542c\u5230\u53f6\u5efa\u658c\u8fd9\u4e48\u8bf4\uff0c\u53c8\u4e0e\u5176\u4ed6\u4e03\u516b\u540d\u6c49\u5b50\u56f4\u903c\u4e86\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u670d\u6c14\uff0c\u6211\u4eec\u600e\u4e48\u4f1a\u4e0d\u670d\u52a1\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8d26\u6211\u4eec\u53ef\u4ee5\u5148\u7ed3\uff0c\u4e09\u500d\u3001\u56db\u500d\u4efb\u7b97\uff0c\u4f46\u662f\u6211\u4eec\u7684\u786e\u9700\u8981\u627e\u5730\u65b9\u5403\u996d\uff0c\u4f60\u4eec\u4e5f\u6ca1\u6709\u5fc5\u8981\u5c06\u6211\u4eec\u5c31\u8fd9\u6837\u8d76\u8d70\u5427\u2026\u2026\u201d\u62ac\u5934\u5bf9\u9a6c\u6d77\u9f99\u8bf4\uff0c\u201c\u4f60\u62ff\u94b1\u7ed9\u4ed6\u4eec\u628a\u8d26\u5148\u7ed3\u4e86\u3002\u201d</p><p>\u3000\u3000\u8fd9\u5e74\u5934\u4fe1\u7528\u5361\u8fdc\u6ca1\u6709\u73b0\u91d1\u597d\u4f7f\uff0c\u5f20\u606a\u8981\u51fa\u884c\uff0c\u9a6c\u6d77\u9f99\u968f\u8eab\u4f1a\u5907\u4e00\u4e9b\u73b0\u91d1\uff0c\u4ed6\u4ece\u516c\u6587\u5305\u91cc\u53d6\u51fa\u4e00\u6c93\u94b1\u538b\u5230\u684c\u4e0a\uff0c\u63a8\u5230\u90a3\u5e73\u5934\u9752\u5e74\u7684\u9762\u524d\uff0c\u7b11\u7740\u95ee\uff1a\u201c\u591f\u4e0d\u591f\uff1f\u201d\u89c1\u90a3\u5e73\u5934\u9752\u5e74\u4e00\u8138\u8fdf\u7591\u7684\u5c06\u94b1\u63a5\u8fc7\u53bb\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u53e6\u5916\u9ebb\u70e6\u4f60\u4eec\u518d\u5e2e\u6211\u4eec\u7167\u521a\u624d\u7684\u83dc\u5355\u51c6\u5907\u4e00\u684c\u5b50\uff0c\u94b1\u82e5\u662f\u4e0d\u591f\uff0c\u518d\u8ddf\u6211\u4eec\u8bf4\u3002\u201d\u90a3\u67b6\u5f0f\u6068\u4e0d\u5f97\u662f\u53f3\u8138\u7ed9\u62bd\u4e86\u53c8\u5c06\u5de6\u8138\u8d34\u8fc7\u53bb\u3002</p></article>"}}}]);
