(window.webpackJsonp=window.webpackJsonp||[]).push([[98],{121:function(p){p.exports={chapterTitle:null,title:"\u7b2c98\u7ae0 \u9ed1\u793e\u4f1a\u5835\u6821\u95e8",content:"<article><h2>\u7b2c98\u7ae0 \u9ed1\u793e\u4f1a\u5835\u6821\u95e8</h2><p>\u3000\u3000\u5f88\u591a\u4e8b\u60c5\u90fd\u8981\u4e00\u6b65\u6b65\u7684\u63a8\u8fdb\uff0c\u4ec5\u9020\u7eb8\u5382\u65b0\u5382\u6295\u4ea7\u7684\u4e8b\u60c5\u5c31\u5343\u5934\u4e07\u7eea\uff0c\u8bb8\u601d\u6b64\u65f6\u7684\u80fd\u529b\u8fd8\u4e0d\u8db3\u4ee5\u7edf\u7b79\u5168\u5c40\uff0c\u867d\u7136\u5c06\u6574\u4e2a\u73af\u8282\u5206\u6210\u4e09\u5757\uff0c\u4f46\u662f\u6bcf\u73af\u8282\u4e4b\u95f4\u7684\u8854\u63a5\u5c31\u4ee4\u4eba\u5934\u75bc\u4e07\u5206\u3002</p><p>\u3000\u3000\u5468\u6e38\u66fe\u63d0\u8fc7\u8981\u8131\u79bb\u6d77\u6cf0\u8d38\u6613\uff0c\u4f46\u662f\u5f20\u606a\u4e0d\u60f3\u8fd9\u65f6\u5019\u6709\u4efb\u4f55\u53ef\u80fd\u5f15\u8d77\u6b63\u6cf0\u96c6\u56e2\u53cd\u611f\u7684\u52a8\u4f5c\uff0c\u5e0c\u671b\u4ed6\u6682\u65f6\u8fd8\u7559\u5728\u6d77\u6cf0\u8d38\u6613\uff0c\u591a\u5173\u5fc3\u4e00\u4e9b\u7eb8\u54c1\u9500\u552e\u7684\u6e20\u9053\uff0c\u65f6\u673a\u6210\u719f\u4e86\uff0c\u81ea\u7136\u4f1a\u8bf7\u4ed6\u5230\u9526\u6e56\u6765\u3002</p><p>\u3000\u3000\u8001\u5382\u6539\u9020\u6210\u9910\u996e\u4f11\u95f2\u5e7f\u573a\u662f\u5f20\u606a\u65e9\u5c31\u6709\u7684\u8ba1\u5212\uff0c\u53ea\u662f\u6628\u5929\u624d\u7a81\u5140\u7684\u63d0\u51fa\u6765\uff0c\u8981\u5468\u590d\u4ed6\u4eec\u4e00\u6bb5\u65f6\u95f4\u6d88\u5316\u4e00\u4e0b\uff0c\u4e0d\u6653\u5f97\u5468\u590d\u6709\u6ca1\u6709\u5c06\u8fd9\u4e8b\u5411\u5b8b\u57f9\u660e\u6c47\u62a5\u8fd9\u4e8b\u3002\u80fd\u591f\u4ee3\u8868\u57ce\u5357\u533a\u653f\u5e9c\u7684\u5b8b\u57f9\u660e\u53ef\u662f\u76f8\u5f53\u91cd\u8981\u7684\u4e00\u73af\uff0c\u600e\u4e48\u4e5f\u8981\u5c06\u4ed6\u62c9\u4e0a\u8239\u3002</p><p>\u3000\u3000\u5728\u6ce8\u518c\u9526\u6e56\u8d23\u4efb\u6709\u9650\u516c\u53f8\u65f6\uff0c\u5f20\u606a\u540c\u65f6\u6ce8\u518c\u4e86\u53e6\u4e00\u5bb6\u540d\u4e3a\u8d8a\u79c0\u7684\u58f3\u516c\u53f8\uff0c\u51fa\u552e\u6d77\u6cf0\u8d38\u6613\u80a1\u4efd\u540e\u6240\u5f97\u7684\u8d44\u91d1\u9664\u4e86\u5c11\u90e8\u5206\u62e8\u7ed9\u9526\u6e56\uff0c\u7edd\u5927\u90e8\u5206\u8d44\u91d1\u8f6c\u5165\u7531\u5f20\u606a\u5b8c\u5168\u63a7\u5236\u7684\u8d8a\u79c0\u516c\u53f8\u8d26\u6237\uff0c\u540c\u65f6\u5212\u5165\u8d8a\u79c0\u516c\u53f8\u540d\u4e0b\u7684\u8fd8\u6709\u65b0\u5149\u5546\u6807\u7684\u6240\u6709\u6743\u5c5e\u3002\u8fd9\u5176\u4e2d\u6709\u7e41\u7410\u7684\u624b\u7eed\u8981\u53bb\u529e\u7406\uff0c\u5f20\u606a\u5fc3\u60f3\u8bb8\u601d\u4eca\u5929\u5927\u7ea6\u6ca1\u6cd5\u5b50\u505c\u4e0b\u4f11\u606f\u4e00\u4f1a\u513f\u3002</p><p>\u3000\u3000\u5f53\u524d\u6700\u7d27\u8981\u7684\u4e8b\u60c5\u662f\u8ba9\u8bb8\u9e3f\u4f2f\u5c06\u90a3\u7bc7\u6587\u7ae0\u5199\u51fa\u6765\uff0c\u5f88\u591a\u4eba\u90fd\u76fc\u671b\u7740\u8fd9\u7bc7\u6587\u7ae0\u7684\u95ee\u4e16\u3002</p><p>\u3000\u3000\u5f20\u606a\u661f\u671f\u4e00\u8001\u8001\u5b9e\u5b9e\u7684\u5728\u6559\u5ba4\u5750\u4e86\u4e00\u5929\uff0c\u6700\u540e\u4e24\u5802\u81ea\u4e60\u8bfe\uff0c\u674e\u829d\u82b3\u5750\u73ed\uff0c\u5f20\u606a\u6ca1\u6765\u5f97\u53ca\u5728\u5979\u5230\u6559\u5ba4\u4e4b\u524d\u6e9c\u6389\uff0c\u4e5f\u4e0d\u65b9\u4fbf\u4e2d\u9014\u9003\u8bfe\u3002\u5b9e\u5728\u6ca1\u6709\u628a\u63e1\u8fd9\u5a18\u4eec\u4f1a\u4e0d\u4f1a\u5f53\u573a\u53d1\u98da\uff0c\u867d\u7136\u7528\u4e0d\u7740\u6015\u5979\uff0c\u9762\u5b50\u5374\u662f\u5f88\u5927\u7684\u95ee\u9898\u3002\u6216\u8bb8\u5979\u5bf9\u81ea\u5df1\u5b8c\u5168\u6b7b\u5fc3\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u4e0d\u63d0\u5979\u8ddf\u738b\u708e\u658c\u5077\u60c5\u8fd9\u4e00\u8282\uff0c\u674e\u829d\u82b3\u5012\u7b26\u5408\u4e00\u540d\u4f18\u79c0\u6559\u5e08\u7684\u6807\u51c6\uff0c\u5f20\u606a\u4e0d\u4f1a\u5e7c\u7a1a\u5230\u4f1a\u8bb0\u6068\u674e\u829d\u82b3\u6628\u5929\u7684\u63ed\u77ed\u884c\u4e3a\uff0c\u80fd\u591f\u4e0d\u8003\u8651\u5b66\u751f\u7684\u5bb6\u5ead\u80cc\u666f\u3001\u5b66\u4e60\u597d\u574f\u800c\u4e88\u4ee5\u516c\u6b63\u5bf9\u5f85\u7684\u6559\u5e08\u90fd\u5e94\u8be5\u503c\u5f97\u5c0a\u656c\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u6258\u7740\u4e0b\u5df4\uff0c\u65e0\u804a\u7684\u6253\u91cf\u7740\u6559\u5ba4\u91cc\u7684\u5c11\u5973\u4eec\uff0c\u5979\u4eec\u770b\u4e0a\u53bb\u5f88\u7a1a\u5ae9\uff0c\u4e0d\u53ef\u5426\u8ba4\uff0c\u5341\u4e94\u516d\u5c81\u7684\u79c0\u4e3d\u5c11\u5973\u5bf9\u4efb\u4f55\u5e74\u9f84\u6bb5\u7684\u7537\u4eba\u90fd\u6709\u8bf1\u60d1\u529b\uff0c\u5f20\u606a\u8ba4\u4e3a\u81ea\u5df1\u7684\u5fc3\u8fd8\u4e0d\u592a\u8001\uff1b\u5076\u5c14\u4f1a\u7ed9\u56de\u5934\u770b\u8fc7\u6765\u7684\u5510\u5a67\u77aa\u4e0a\u51e0\u773c\u3002</p><p>\u3000\u3000\u671f\u4e2d\u8003\u8bd5\u4e4b\u4e2d\uff0c\u674e\u829d\u82b3\u8981\u6c42\u73ed\u4e0a\u7684\u8d70\u8bfb\u751f\u4e5f\u540c\u6837\u5728\u6821\u4e0a\u591c\u81ea\u4fee\uff0c\u5f20\u606a\u5f53\u7136\u65e0\u89c6\u674e\u829d\u82b3\u7684\u8981\u6c42\uff0c\u5510\u5a67\u5374\u8981\u7559\u5728\u5b66\u6821\u4e0a\u81ea\u4fee\u3002</p><p>\u3000\u3000\u653e\u665a\u5b66\u540e\uff0c\u5f20\u606a\u5728\u6559\u5ba4\u8ddf\u5510\u5a67\u80e1\u626f\u4e86\u4e00\u4f1a\u513f\uff0c\u5510\u5a67\u7ed9\u5173\u7cfb\u597d\u7684\u5973\u540c\u5b66\u62c9\u53bb\u98df\u5802\u5403\u996d\uff0c\u5510\u5a67\u8ba9\u5f20\u606a\u8ddf\u7740\uff0c\u5f20\u606a\u53ea\u5f97\u4e56\u4e56\u7684\u8ddf\u7740\u540e\u9762\u3002</p><p>\u3000\u3000\u6559\u5b66\u533a\u4e0e\u98df\u5802\u3001\u6559\u804c\u5de5\u5bbf\u820d\u533a\u9694\u7740\u4e00\u6761\u6e05\u6c34\u6cb3\uff0c\u6709\u5c0f\u6865\u76f8\u8fde\u3002\u8fc7\u6865\u65f6\uff0c\u770b\u89c1\u6821\u95e8\u56f4\u4e86\u4e00\u5708\u4eba\uff0c\u5510\u5a67\u552f\u6050\u5929\u4e0b\u4e0d\u4e71\uff0c\u62c9\u7740\u5f20\u606a\u5c0f\u6b65\u6e9c\u8fc7\u53bb\u770b\u70ed\u95f9\u3002</p><p>\u3000\u3000\u8d70\u5230\u6821\u95e8\u53e3\uff0c\u53ea\u6709\u4f20\u8fbe\u5ba4\u5de6\u8fb9\u7684\u5c0f\u95e8\u5f00\u7740\uff0c\u53f3\u8fb9\u7684\u94c1\u6805\u95e8\u5916\uff0c\u5f88\u591a\u653e\u665a\u5b66\u7684\u8d70\u8bfb\u751f\u90fd\u56f4\u5728\u90a3\u91cc\u3002\u7ed9\u56f4\u4eba\u7fa4\u91cc\u7684\u662f\u6628\u5929\u90a3\u4e09\u4e2a\u5c0f\u9752\u76ae\u6df7\u6df7\uff0c\u4e00\u4eba\u8138\u4e0a\u88f9\u7740\u7eb1\u5e03\uff0c\u4e00\u4eba\u5de6\u624b\u7528\u7ef7\u5e26\u6302\u5728\u8116\u5b50\u4e0a\uff0c\u6700\u540e\u90a3\u4eba\u597d\u4e00\u4e9b\uff0c\u5634\u89d2\u8d34\u7740\u521b\u53e3\u8d34\uff0c\u8138\u6709\u4e9b\u6d6e\u80bf\u3002\u770b\u6837\u5b50\u4e5f\u4e0d\u50cf\u6765\u5bfb\u4ec7\u7684\uff0c\u56f4\u7740\u4e00\u7fa4\u770b\u70ed\u95f9\u7684\u5b66\u751f\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u89c1\u5468\u6587\u658c\u5934\u63a2\u51fa\u4f20\u8fbe\u5ba4\u7684\u7a97\u6237\u671d\u4ed6\u62db\u624b\uff0c\u8fd9\u624d\u770b\u89c1\u5965\u8fea\u8f66\u505c\u5728\u6821\u95e8\u5916\u7684\u573a\u5730\u4e0a\uff0c\u4e0e\u5510\u5a67\u8d70\u8fc7\u53bb\uff0c\u95ee\u4ed6\uff1a\u201c\u4f60\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u848b\u7ecf\u7406\u5429\u5490\u7684\uff0c\u4ee5\u540e\u5965\u8fea\u8f66\u4e13\u95e8\u63a5\u9001\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u624d\u51e0\u6b65\u8def\uff0c\u62ac\u811a\u5c31\u5230\u7684\uff0c\u8fd9\u8fb9\u5750\u51fa\u79df\u8f66\u4e5f\u65b9\u4fbf\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u63a2\u5934\u770b\u89c1\u653f\u6559\u5904\u4e3b\u4efb\u66f9\u5149\u660e\u7ad9\u5728\u4f20\u8fbe\u5ba4\u91cc\uff0c\u8b66\u60d5\u7684\u76ef\u7740\u6821\u53e3\u7684\u4e09\u4e2a\u5c0f\u9752\u5e74\uff0c\u60f3\u5fc5\u8ba9\u6821\u95e8\u53e3\u7684\u52a8\u9759\u5438\u5f15\u8fc7\u6765\uff0c\u89c1\u4ed6\u6ca1\u6ce8\u610f\u5230\u81ea\u5df1\uff0c\u95ee\u5468\u6587\u658c\uff0c\u201c\u95e8\u53e3\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u597d\u50cf\u662f\u627e\u4f60\u7684\u2026\u2026\u201d\u5468\u6587\u658c\u8bdd\u521a\u843d\uff0c\u5916\u9762\u5c31\u6709\u4eba\u8ba4\u51fa\u5f20\u606a\u6765\uff0c\u5927\u58f0\u558a\uff1a\u201c\u5f20\u606a\u51fa\u6765\u4e86\uff01\u201d</p><p>\u3000\u3000\u56f4\u5728\u6821\u95e8\u53e3\u7684\u5b66\u751f\u90fd\u62e5\u5230\u94c1\u6805\u95e8\u524d\uff0c\u8fd8\u6709\u8bd5\u56fe\u7ed5\u5230\u5c0f\u95e8\u5f80\u91cc\u95ef\uff0c\u8ba9\u5b88\u5728\u95e8\u53e3\u7684\u8b66\u536b\u62e6\u4f4f\uff0c\u66f9\u5149\u660e\u8fd9\u65f6\u5019\u624d\u770b\u89c1\u5f20\u606a\u7ad9\u5728\u4f20\u8fbe\u5ba4\u7684\u7a97\u5b50\u5916\u3002\u8d70\u8fc7\u6765\uff0c\u9694\u7740\u7a97\u6237\u95ee\u5f20\u606a\uff1a\u201c\u5f20\u606a\uff0c\u4f60\u662f\u4e0d\u662f\u5728\u5916\u9762\u6253\u67b6\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u554a\uff1f\u201d\u5f20\u606a\u89c1\u5510\u5a67\u8138\u8272\u4e0d\u5584\uff0c\u53ea\u5f97\u88c5\u7cca\u6d82\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u4ed6\u4eec\u662f\u6765\u627e\u6211\u7684\uff1f\u90a3\u6211\u51fa\u6765\u95ee\u4ed6\u4eec\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5148\u522b\u51fa\u6765\uff0c\u5148\u95ee\u6e05\u695a\u600e\u4e48\u56de\u4e8b\u518d\u8bf4\uff0c\u5b66\u6821\u8981\u4e3a\u4f60\u8d1f\u8d23\uff0c\u4f60\u4eec\u73ed\u7684\u674e\u8001\u5e08\u4e00\u4f1a\u513f\u5c31\u8fc7\u6765\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u6839\u672c\u6ca1\u4ec0\u4e48\u597d\u62c5\u5fc3\u7684\uff0c\u4f46\u662f\u7ed9\u5510\u5a67\u62fd\u7740\u8863\u670d\uff0c\u4fa7\u5934\u89c1\u5979\u773c\u775b\u91cc\u6709\u4e9b\u754f\u60e7\uff0c\u597d\u50cf\u66f4\u591a\u7684\u662f\u5174\u594b\uff0c\u771f\u641e\u4e0d\u5f97\u5979\u8111\u5b50\u91cc\u5728\u60f3\u4ec0\u4e48\u4e1c\u897f\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\uff0c\u201d\u90a3\u4e2a\u9f3b\u6881\u9aa8\u7ed9\u5f20\u606a\u8e22\u65ad\u7684\u5c0f\u9752\u5e74\u9694\u7740\u94c1\u6805\u95e8\u558a\uff0c\u201c\u5c0a\u54e5\u8ba9\u6211\u4eec\u8fc7\u6765\u627e\u4f60\u9053\u6b49\uff0c\u5e0c\u671b\u683c\u54e5\u5927\u4eba\u4e0d\u8ba1\u5c0f\u4eba\u8fc7\u2026\u2026\u201d</p><p>\u3000\u3000\u4ed6\u8fd9\u4e00\u558a\uff0c\u56f4\u5728\u6821\u95e8\u5185\u5916\u7684\u540c\u5b66\u773c\u795e\u90fd\u53d8\u4e86\u3002</p><p>\u3000\u3000\u5c0a\u54e5\u662f\u8c01\uff1f\u5f20\u606a\u5934\u76ae\u90fd\u8981\u70b8\u6389\u4e86\uff0c\u77a5\u773c\u770b\u89c1\u674e\u829d\u82b3\u4e0e\u675c\u98de\u6b63\u5f80\u8fd9\u91cc\u8d76\uff0c\u542c\u5230\u8fd9\u8bdd\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u4e00\u8138\u7684\u56f0\u60d1\u3002\u8fd9\u65f6\u770b\u89c1\u9648\u5983\u84c9\u63a8\u7740\u8f66\u8fdc\u8fdc\u7684\u8eb2\u5728\u529e\u516c\u697c\u524d\u9762\u7684\u82b1\u575b\u540e\u9762\uff0c\u5927\u6982\u662f\u6015\u90a3\u4e09\u540d\u9752\u5e74\u662f\u6765\u5b66\u6821\u5835\u5979\u7684\uff0c\u8eb2\u7740\u4e00\u76f4\u6ca1\u6562\u51fa\u6821\u95e8\uff0c\u7cbe\u81f4\u7c89\u5ae9\u7684\u8138\u4e0a\u6709\u4e9b\u60ca\u60f6\uff0c\u8ba9\u4eba\u770b\u4e86\u4e0d\u582a\u601c\u60dc\u3002</p><p>\u3000\u3000\u90a3\u9752\u5e74\u770b\u4e0d\u6e05\u5f62\u52bf\uff0c\u4ece\u53e3\u888b\u91cc\u638f\u51fa\u4e00\u53ea\u4fe1\u5c01\u4ece\u6805\u680f\u7f1d\u91cc\u9012\u8fdb\u6765\uff0c\u8bf4\uff1a\u201c\u6211\u4eec\u7ed9\u606a\u5c11\u6559\u8bad\uff0c\u662f\u6211\u4eec\u7f6a\u6709\u5e94\u5f97\uff0c\u4e0d\u6562\u518d\u8ba9\u606a\u54e5\u7834\u8d39\u533b\u836f\u8d39\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u829d\u82b3\u542c\u5230\u8fd9\u91cc\uff0c\u8f6c\u8eab\u5c31\u8d70\u3002\u675c\u98de\u7ad9\u5728\u90a3\u91cc\u6123\u4e86\u4e00\u4e0b\uff0c\u5012\u662f\u6ca1\u6709\u53bb\u8ffd\u4ed6\u5fc3\u76ee\u4e2d\u7684\u719f\u5973\uff0c\u6324\u7709\u5f04\u773c\u7684\u8d34\u8fc7\u6765\uff1a\u201c\u8001\u5927\uff0c\u6211\u53eb\u4f60\u8001\u5927\u884c\u4e0d\u884c\uff0c\u4ee5\u540e\u9047\u5230\u8fd9\u4e8b\uff0c\u80fd\u4e0d\u80fd\u4e0d\u8981\u843d\u4e0b\u6211\uff1f\u201d</p><p>\u3000\u3000\u201c\u675c\u98de\uff01\u201d\u674e\u829d\u82b3\u5728\u524d\u9762\u505c\u4e0b\u811a\u6b65\uff0c\u674f\u773c\u77aa\u7740\u8fd9\u8fb9\uff0c\u597d\u50cf\u675c\u98de\u9a6c\u4e0a\u5c31\u8981\u7ed9\u5f20\u606a\u5e26\u574f\u3002\u675c\u98de\u5e78\u707e\u4e50\u7978\u7684\u7b11\u4e86\u7b11\uff0c\u5c0f\u8dd1\u8fc7\u53bb\u3002\u5f20\u829d\u82b3\u610f\u72b9\u672a\u5c3d\uff0c\u53c8\u558a\u9053\uff1a\u201c\u5510\u5a67\u2026\u2026\u201d\u53c8\u5524\u90a3\u4e24\u4e2a\u51c6\u5907\u4e0e\u5510\u5a67\u4e00\u8d77\u53bb\u98df\u5802\u5403\u996d\u7684\u5973\u5b69\u5b50\u7684\u540d\u5b57\uff0c\u201c\u8fd8\u8981\u4e0a\u591c\u81ea\u4fee\uff0c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u53bb\u5403\u665a\u996d\uff1f\u201d\u90a3\u6837\u5b50\u662f\u5b8c\u5168\u5bf9\u5f20\u606a\u653e\u5f03\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u6b63\u5934\u75bc\u600e\u4e48\u6837\u624d\u80fd\u5c06\u5510\u5a67\u652f\u8d70\u5462\uff0c\u671d\u5979\u7b11\u4e86\u7b11\uff0c\u624b\u85cf\u5728\u8eab\u4fa7\u7ed9\u5979\u505a\u51fa\u665a\u4e0a\u6253\u7535\u8bdd\u7684\u624b\u52bf\uff0c\u5510\u5a67\u4e00\u8138\u4e0d\u60c5\u613f\u7684\u8ddf\u7740\u674e\u829d\u82b3\u8d70\u5f00\u4e86\uff0c\u8d70\u5230\u6865\u4e0a\u8fd8\u5f80\u8fd9\u8fb9\u770b\u3002</p><p>\u3000\u3000\u66f9\u5149\u660e\u6ca1\u6599\u5230\u793e\u4f1a\u9752\u76ae\u6d41\u6c13\u5835\u6821\u95e8\u53e3\u7adf\u7136\u4f1a\u662f\u8fd9\u6837\uff0c\u60f3\u5230\u6628\u5929\u5f20\u77e5\u884c\u4e0e\u5510\u5b66\u8c26\u7684\u6001\u5ea6\uff0c\u81ea\u7136\u4e0d\u6562\u5f00\u53e3\u8bad\u5f20\u606a\uff0c\u4f46\u662f\u4ed6\u53c8\u8981\u7ef4\u6301\u4e3a\u4eba\u5e08\u8868\u7684\u9762\u76ee\uff0c\u9634\u6c89\u7740\u8138\u5c06\u5835\u5728\u6821\u95e8\u53e3\u7684\u5b66\u751f\u9a71\u6563\uff0c\u4e5f\u6ca1\u6709\u8ddf\u5f20\u606a\u518d\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u72ec\u81ea\u8fdb\u4e86\u5b66\u6821\u3002</p><p>\u3000\u3000\u5f20\u606a\u5934\u75bc\u65e0\u6bd4\uff0c\u786c\u7740\u5934\u76ae\u8d70\u51fa\u6821\u95e8\uff0c\u8fd9\u624d\u770b\u89c1\u6821\u95e8\u5916\u7684\u573a\u5730\u4e0a\u505c\u7740\u4e00\u8f86\u4e30\u7530\uff0c\u521a\u624d\u7ed9\u4f20\u8fbe\u5ba4\u6321\u4f4f\u6ca1\u6709\u770b\u89c1\uff0c\u4e00\u540d\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5e74\u8f7b\u4eba\u4f9d\u7740\u8f66\u5934\uff0c\u6307\u5934\u5939\u7740\u9999\u70df\uff0c\u5929\u8272\u6709\u4e9b\u6697\uff0c\u70df\u5934\u5728\u66ae\u8272\u91cc\u660e\u706d\u3002\u4e07\u5929\u624d\u4e0e\u8d75\u53f8\u660e\u4e24\u4eba\u4e5f\u5728\u6821\u95e8\u5916\uff0c\u4ed6\u4eec\u770b\u89c1\u81ea\u5df1\u8d70\u51fa\u6765\uff0c\u8138\u8272\u6709\u4e9b\u4e0d\u81ea\u7136\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5230\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u4e24\u4eba\u7684\u8138\u8272\uff0c\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u5fc3\u91cc\u7684\u6028\u6c14\u5012\u662f\u6d88\u4e86\u4e0d\u5c11\u3002\u90a3\u4e2a\u4f9d\u7740\u4e30\u7530\u8f66\u5934\u62bd\u70df\u7684\u5e74\u8f7b\u4eba\u770b\u89c1\u5f20\u606a\u8d70\u51fa\u6765\uff0c\u5c06\u624b\u91cc\u7684\u70df\u5934\u4e22\u6389\uff0c\u8d70\u8fc7\u6765\u8bf4\uff1a\u201c\u6211\u53eb\u5434\u5c0a\uff0c\u6211\u7238\u662f\u5efa\u90ba\u9152\u5e97\u7684\u5434\u5929\u5b9d\u2026\u2026\u201d\u8bf4\u7740\u8bdd\uff0c\u4f38\u8fc7\u624b\u6765\u3002</p><p>\u3000\u3000\u5434\u5929\u5b9d\u5f88\u80d6\uff0c\u5dee\u4e0d\u591a\u62b5\u4e0a\u773c\u524d\u4e24\u4e2a\u5434\u5c0a\u3002\u5f20\u606a\u7ec6\u8fa8\u4e86\u4e00\u4e0b\uff0c\u5434\u5c0a\u8ddf\u4ed6\u8001\u5b50\u4f53\u578b\u5dee\u8ddd\u5f88\u5927\uff0c\u4f46\u662f\u7709\u773c\u95f4\u76f8\u8096\uff0c\u80a5\u9f3b\u5934\u3001\u7ec6\u957f\u773c\u775b\uff0c\u4e0b\u5df4\u6709\u9053\u75a4\u75d5\u3002\u5f20\u606a\u7ad9\u5728\u90a3\u91cc\u770b\u4e86\u4ed6\u4e24\u773c\uff0c\u624d\u6162\u60a0\u60a0\u7684\u8bf4\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4eca\u5929\u9886\u7740\u8fd9\u51e0\u4e2a\u4e0d\u6210\u5668\u7684\u5bb6\u4f19\u8fc7\u6765\u5411\u4f60\u9053\u6b49\uff0c\u4ed6\u4eec\u60f9\u607c\u4f60\uff0c\u88ab\u6559\u8bad\u4e5f\u662f\u6d3b\u8be5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u4f60\u4e0d\u63d0\uff0c\u6211\u8fc7\u51e0\u5929\u5c31\u80fd\u5fd8\u6389\u4e86\u3002\u9664\u4e86\u8fd9\u4e8b\uff0c\u4f60\u8fd8\u6709\u522b\u7684\u4ec0\u4e48\u4e8b\uff1f\u201d\u5f20\u606a\u7709\u5934\u76b1\u4e86\u76b1\uff0c\u6ca1\u6709\u63a9\u9970\u8138\u4e0a\u7684\u4e0d\u60a6\uff0c\u201c\u4f60\u9886\u7740\u4ed6\u4eec\u5835\u6211\u6821\u95e8\u53e3\uff0c\u771f\u89c9\u5f97\u5f88\u7ed9\u6211\u9762\u5b50\uff1f\u201d</p><p>\u3000\u3000\u5468\u6587\u658c\u5c06\u5965\u8fea\u8f66\u5f00\u8fc7\u6765\uff0c\u4e0d\u5fd8\u4e0b\u8f66\u5e2e\u5f20\u606a\u6253\u5f00\u8f66\u95e8\u3002\u5f20\u606a\u4e34\u4e0a\u8f66\u524d\uff0c\u8ddf\u5434\u5c0a\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u90a3\u70b9\u533b\u836f\u8d39\uff0c\u5728\u6211\u773c\u91cc\u6839\u672c\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d\u626d\u5934\u76ef\u4e86\u7ad9\u5728\u4e0d\u8fdc\u5904\u7684\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u4e00\u773c\uff0c\u624d\u94bb\u8fdb\u8f66\u91cc\uff0c\u5429\u5490\u5468\u6587\u658c\uff1a\u201c\u9648\u5983\u84c9\u8fd8\u5728\u5b66\u6821\u91cc\u9762\uff0c\u5148\u8fdb\u53bb\u95ee\u5979\u8981\u4e0d\u8981\u4e00\u8d77\u8d70\uff0c\u522b\u7684\u4e8b\u4e0d\u8981\u591a\u8bf4\u3002\u201d</p><p>\u3000\u3000\u5965\u8fea\u8f66\u5728\u6821\u95e8\u53e3\u6309\u4e86\u4e24\u58f0\u5587\u53ed\uff0c\u6821\u8b66\u536b\u5c0f\u6e9c\u7740\u51fa\u6765\u5c06\u7d27\u95ed\u7684\u5927\u95e8\u6253\u5f00\uff0c\u4e09\u540d\u5c0f\u9752\u5e74\u6123\u6123\u7684\u770b\u7740\u8f66\u5b50\u8fdb\u53bb\uff0c\u6123\u662f\u6ca1\u6562\u8bf4\u591a\u8bf4\u4e00\u53e5\u8bdd\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u8fd8\u63a8\u7740\u8f66\u7ad9\u5728\u5b66\u6821\u529e\u516c\u697c\u524d\u7684\u82b1\u575b\u540e\u9762\uff0c\u65c1\u8fb9\u662f\u5979\u90a3\u4e9b\u95e8\u7259\u6709\u4e9b\u8c41\u7684\u5973\u540c\u5b66\u3002\u9648\u5983\u84c9\u770b\u89c1\u5468\u6587\u658c\u7684\u5934\u4ece\u8f66\u7a97\u6765\u63a2\u51fa\u6765\uff0c\u4e00\u8138\u6b23\u559c\u7684\u8bf4\uff1a\u201c\u5c0f\u5468\u53d4\u53d4\uff0c\u4f60\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u9694\u7740\u8d34\u819c\u7684\u8f66\u7a97\uff0c\u9648\u5983\u84c9\u91ca\u7136\u8f7b\u677e\u4e0b\u6765\u7684\u7b11\u5bb9\u5f02\u5e38\u7684\u8ff7\u4eba\uff0c\u7cbe\u81f4\u7684\u8138\u5e9e\uff0c\u6ca1\u6709\u4e00\u4e1d\u6742\u8d28\u7684\u96ea\u767d\uff0c\u5728\u66ae\u8272\u91cc\u5c24\u5176\u663e\u5f97\u5a07\u5a9a\u4fcf\u4e3d\u3002</p><p>\u3000\u3000\u201c\u8fc7\u6765\u9001\u4f60\u56de\u53bb\u3002\u201d\u5468\u6587\u658c\u80f3\u818a\u8098\u6491\u7740\u8f66\u7a97\u8bf4\uff0c\u901a\u8fc7\u540e\u89c6\u955c\u770b\u89c1\u5f20\u606a\u9759\u9759\u7684\u5750\u5728\u5ea7\u6905\u4e0a\u4e00\u58f0\u4e0d\u542d\u3002</p><p>\u3000\u3000\u201c\u90a3\u7b49\u6211\u4eec\u5148\u628a\u8f66\u5b50\u653e\u5230\u8f66\u68da\u53bb\u3002\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u4e0e\u5979\u540c\u5b66\u63a8\u7740\u8f66\u5f80\u91cc\u8d70\uff0c\u81ea\u884c\u8f66\u68da\u6700\u8fd1\u5728\u6559\u5b66\u6821\u524d\u6709\u4e00\u5904\uff0c\u5468\u6587\u658c\u5f00\u7740\u8f66\u8ddf\u5728\u5979\u4eec\u540e\u9762\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u9648\u5983\u84c9\u4e0e\u540c\u5b66\u653e\u597d\u8f66\uff0c\u62c9\u5f00\u8f66\u95e8\uff0c\u534a\u4e2a\u8eab\u5b50\u94bb\u8fdb\u8f66\u91cc\uff0c\u624d\u770b\u89c1\u5f20\u606a\u5750\u5728\u91cc\u9762\uff0c\u6ee1\u8138\u7684\u5174\u594b\u77ac\u95f4\u51dd\u56fa\uff0c\u8bb6\u7136\u7591\u60d1\u7684\u773c\u795e\u8fd8\u6709\u4e9b\u8bb8\u7684\u4e0d\u5b89\uff0c\u8eab\u5b50\u50f5\u5728\u90a3\u91cc\uff0c\u8111\u5b50\u91cc\u4e00\u7247\u7a7a\u767d\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u4e0d\u8fdb\u53bb\u554a\uff1f\u201d\u5979\u540c\u5b66\u6ca1\u6709\u770b\u89c1\u5f20\u606a\u5728\u8f66\u91cc\uff0c\u7ad9\u5728\u5916\u9762\u63a8\u4e86\u9648\u5983\u84c9\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u624b\u4e00\u8f6f\uff0c\u8eab\u5b50\u683d\u8fdb\u8f66\u91cc\uff0c\u53cc\u624b\u614c\u4e71\u4e2d\u6491\u7740\u5f20\u606a\u7684\u5927\u817f\uff0c\u5934\u62b5\u5230\u4ed6\u7684\u8170\u4e0a\uff0c\u6323\u624e\u7740\u7a33\u4f4f\u8eab\u4f53\uff0c\u6309\u7740\u5f20\u606a\u5927\u817f\u7684\u624b\u60ca\u60f6\u7684\u6536\u56de\u53bb\uff0c\u7c89\u8138\u7f9e\u7ea2\uff0c\u8eab\u5b50\u614c\u5fd9\u7684\u9000\u51fa\u53bb\u3002</p><p>\u3000\u3000\u201c\u6211\u5750\u5230\u524d\u95e8\u5c31\u4e0b\u8f66\uff0c\u201d\u5f20\u606a\u7a0d\u7a0d\u7684\u4f4e\u4e0b\u4e9b\u5934\uff0c\u770b\u7740\u7ad9\u5728\u8f66\u95e8\u5916\u4e0d\u77e5\u6240\u63aa\u7684\u9648\u5983\u84c9\uff0c\u201c\u4f60\u8981\u4ecb\u610f\u7684\u8bdd\uff0c\u6211\u4e0b\u53bb\u5750\u51fa\u79df\u8f66\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u662f\u4f60\uff1f\u201d\u9648\u5983\u84c9\u540c\u5b66\u63a2\u5934\u770b\u89c1\u5f20\u606a\u53cc\u624b\u62b1\u80f8\u5750\u5728\u8f66\u91cc\uff0c\u201c\u4f60\u600e\u4e48\u4f1a\u5728\u8f66\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u5983\u84c9\uff0c\u600e\u4e48\u4e0d\u4e0a\u8f66\uff1f\u201d\u5468\u6587\u658c\u53ef\u4e0d\u77e5\u9053\u9648\u5983\u84c9\u5bf9\u5f20\u606a\u6709\u8bef\u4f1a\uff0c\u201c\u4f60\u4e0d\u5f97\u665a\u4e0a\u53bb\u89c1\u4f60\u5988\u5417\uff1f\u5c0f\u5468\u53d4\u53d4\u9001\u4f60\u8fc7\u53bb\u3002\u201d</p><p>\u3000\u3000\u201c\u54b3\u2026\u2026\u201d\u9648\u5983\u84c9\u5431\u5514\u4e86\u4e00\u58f0\uff0c\u6253\u5f00\u524d\u95e8\uff0c\u53cc\u624b\u53ef\u601c\u516e\u516e\u7684\u671d\u5979\u540c\u5b66\u62dc\u4e86\u62dc\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u770b\u7740\u9648\u5983\u84c9\u7684\u540c\u5b66\u4e00\u8138\u4e0d\u60c5\u613f\u7684\u5750\u5230\u81ea\u5df1\u8eab\u8fb9\uff0c\u95ee\u5979\uff1a\u201c\u7ecf\u5e38\u770b\u5230\u4f60\uff0c\u8fd8\u4e0d\u77e5\u9053\u4f60\u7684\u540d\u5b57\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u5fc5\u8981\u8ba9\u4f60\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u201d\u90a3\u5973\u5b69\u7709\u6bdb\u4e00\u626c\uff0c\u8bf4\u8bdd\u65f6\u9732\u51fa\u7684\u95e8\u7259\u6709\u4e9b\u8c41\uff0c\u6a21\u6837\u8fd8\u7b97\u6e05\u79c0\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u5979\u62a2\u767d\uff0c\u53ea\u5f97\u95ed\u5634\u4e0d\u8bf4\u8bdd\u3002\u8f66\u5b50\u51fa\u6821\u95e8\uff0c\u5434\u5c0a\u3001\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u4e09\u4e2a\u4eba\u51d1\u5728\u4e00\u8d77\uff0c\u4e0d\u6653\u5f97\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\uff0c\u4e09\u4e2a\u5c0f\u9752\u5e74\u7ad9\u5728\u4e00\u65c1\u3002\u5f20\u606a\u6309\u4e0b\u8f66\u7a97\uff0c\u6307\u7740\u8d75\u53f8\u660e\u5bf9\u5434\u5c0a\u8bf4\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u6211\u975e\u5e38\u4e0d\u559c\u6b22\u3002\u201d\u7559\u4e0b\u4e00\u8138\u60ca\u8c14\u7684\u4f17\u4eba\uff0c\u5965\u8fea\u8f66\u7edd\u5c18\u800c\u53bb\u3002</p><p>\u3000\u3000\u5230\u65b0\u6d77\u901a\u5927\u53a6\u524d\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u5468\u6587\u658c\u7684\u80a9\u8180\uff0c\u5bf9\u4ed6\u8bf4\uff1a\u201c\u5468\u54e5\uff0c\u5983\u84c9\u7684\u81ea\u884c\u8f66\u5728\u5b66\u6821\u91cc\uff0c\u4f60\u660e\u65e9\u8fd8\u53bb\u63a5\u5979\u4e0a\u5b66\uff0c\u7b49\u4f1a\u513f\u5c31\u4e0d\u7528\u6765\u63a5\u6211\u4eec\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u55ef\u3002\u201d\u5468\u6587\u658c\u8981\u4e0b\u8f66\u5e2e\u5f20\u606a\u53bb\u5f00\u8f66\u95e8\uff0c\u80a9\u8180\u5374\u8ba9\u4ed6\u7ed9\u6309\u4f4f\uff0c\u770b\u7740\u4ed6\u81ea\u5df1\u63a8\u95e8\u4e0b\u8f66\uff0c\u8fdb\u4e86\u65b0\u6d77\u901a\u5927\u53a6\u3002</p><p>\u3000\u3000\u201c\u5c0f\u5468\u53d4\u53d4\uff0c\u4f60\u600e\u4e48\u4f1a\u8fc7\u6765\u63a5\u5f20\u606a\uff1f\u201d\u770b\u7740\u5f20\u606a\u8d70\u8fdb\u5927\u53a6\uff0c\u9648\u5983\u84c9\u624d\u6062\u590d\u6b63\u5e38\uff0c\u8be7\u5f02\u7684\u95ee\u5468\u6587\u658c\u3002</p><p>\u3000\u3000\u201c\u6b63\u597d\u5f00\u8f66\u7ecf\u8fc7\u4f60\u4eec\u5b66\u6821\uff0c\u5148\u770b\u5230\u4ed6\u4e86\uff0c\u201d\u5468\u6587\u658c\u8bb0\u5f97\u5f20\u606a\u7684\u5429\u5490\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u4eca\u5929\u8c03\u9526\u6e56\u516c\u53f8\u4e86\uff0c\u600e\u4e48\u770b\u4f60\u633a\u6015\u5f20\u606a\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e00\u4e2d\u6ca1\u4eba\u4e0d\u6015\u4ed6\uff0c\u4ed6\u662f\u4e00\u4e2d\u56db\u5927\u6076\u5c11\u4e4b\u9996\u5462\u3002\u4f60\u6ca1\u770b\u89c1\u4ed6\u51f6\u4eba\u65f6\u5019\u7684\u6837\u5b50\uff0c\u5835\u5b66\u6821\u95e8\u53e3\u7684\u4e09\u4e2a\u5c0f\u6d41\u6c13\u5c31\u662f\u7ed9\u4ed6\u6253\u6210\u90a3\u6837\u5b50\u7684\u3002\u201d\u9648\u5983\u84c9\u60f3\u5230\u5f20\u606a\u6628\u5929\u51fa\u624b\u7684\u72e0\u8fa3\u52b2\uff0c\u5fc3\u8fd8\u662f\u76f4\u6cdb\u5bd2\u6c14\u3002</p><p>\u3000\u3000\u201c\u771f\u7684\uff1f\u201d\u5468\u6587\u658c\u4e4b\u524d\u8fd8\u4e0d\u4fe1\uff0c\u4e09\u4e2a\u9752\u5e74\u4e24\u4eba\u4f24\u52bf\u6bd4\u8f83\u91cd\uff0c\u4e00\u4eba\u4e0a\u4e86\u9f3b\u5939\u5957\uff0c\u4e00\u4eba\u5de6\u624b\u6253\u4e86\u77f3\u818f\uff0c\u4f46\u662f\u5f20\u606a\u5f85\u4ed6\u4eec\u5012\u662f\u6587\u8d28\u5f6c\u5f6c\u7684\uff0c\u8bf4\u8bdd\u5f88\u5ba2\u6c14\uff0c\u4f46\u662f\u60f3\u60f3\u4e5f\u4e0d\u662f\u6ca1\u6709\u53ef\u80fd\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u8bf4\u80fd\u8ba9\u5468\u590d\u4fef\u9996\u542c\u547d\uff0c\u9664\u4e86\u5bb6\u91cc\u7684\u80cc\u666f\uff0c\u4e5f\u4e00\u5b9a\u6709\u4e9b\u624b\u6bb5\uff0c\u53ea\u662f\u8fd9\u4e9b\u624b\u6bb5\u6ca1\u6709\u5fc5\u8981\u5bf9\u4e0b\u9762\u4eba\u6446\u51fa\u6765\u7f62\u4e86\u3002</p></article>"}}}]);
