(window.webpackJsonp=window.webpackJsonp||[]).push([[984],{1007:function(p){p.exports={chapterTitle:null,title:"\u7b2c981\u7ae0 \u6d74\u573a",content:"<article><h2>\u7b2c981\u7ae0 \u6d74\u573a</h2><p>\u3000\u3000\u4f59\u5e86\u6797\u7684\u6001\u5ea6\u575a\u51b3\uff0c\u5f20\u5065\u5c31\u6ca1\u6709\u4ec0\u4e48\u597d\u575a\u6301\u7684\u3002</p><p>\u3000\u3000\u5f20\u5065\u4ed6\u4e5f\u4e0d\u662f\u8111\u888b\u4e00\u70ed\u5c06\u4ec0\u4e48\u4e8b\u60c5\u90fd\u63fd\u4e0b\u6765\uff0c\u4ed6\u53ef\u4ee5\u627e\u51fa\u4e8b\u5934\u6765\u8ba9\u94b1\u6842\u534e\u505c\u804c\uff0c\u751a\u81f3\u66f4\u4e25\u91cd\u4e00\u4e9b\u5f00\u9664\u516c\u804c\u90fd\u6ca1\u6709\u95ee\u9898\uff0c\u4f46\u5bf9\u94b1\u6842\u534e\u7684\u5904\u7f6e\u53ea\u9650\u4e8e\u505c\u804c\u6216\u5f00\u9664\u516c\u804c\u4e0a\uff0c\u4e0d\u80fd\u8fdb\u4e00\u6b65\u6df1\u5165\u8c03\u67e5\u3002</p><p>\u3000\u3000\u94b1\u6842\u534e\u6562\u7ed9\u6d89\u9ed1\u7684\u8fd0\u8f93\u516c\u53f8\u4e0e\u5a31\u4e50\u573a\u6240\u5145\u5f53\u4fdd\u62a4\u4f1e\uff0c\u5176\u80cc\u540e\u80af\u5b9a\u4e5f\u6709\u4fdd\u62a4\u4f1e\uff0c\u81f3\u4e8e\u94b1\u6842\u534e\u7684\u540e\u53f0\u662f\u4e0d\u662f\u53ea\u6709\u533a\u59d4\u526f\u4e66\u3001\u533a\u957f\u8c37\u5cb3\u4e00\u4eba\uff0c\u5f20\u5065\u8fd8\u4e0d\u5f97\u800c\u77e5\uff0c\u5c31\u7b97\u8c03\u67e5\u94b1\u6842\u534e\u53ea\u7275\u6d89\u5230\u8c37\u5cb3\uff0c\u8c37\u5cb3\u4e3a\u81ea\u4fdd\u6240\u505a\u7684\u5782\u6b7b\u6323\u624e\uff0c\u4e5f\u591f\u5f20\u5065\u559d\u4e00\u58f6\u7684\u3002</p><p>\u3000\u3000\u4f59\u5e86\u6797\u4e5f\u6015\u7bd3\u5b50\u4f1a\u6345\u592a\u5927\uff0c\u7ea6\u5b9a\u597d\u7531\u5f20\u5065\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u5c06\u94b1\u6842\u534e\u8e22\u5230\u4e00\u8fb9\uff0c\u9648\u79c9\u5fb7\u5219\u5728\u5e02\u5c40\u91cc\u7ec4\u7ec7\u8b66\u529b\u8c03\u67e5\u957f\u6797\u8fd0\u8f93\u516c\u53f8\u3001\u4e91\u68a6\u9601\u5a31\u4e50\u5bab\u7684\u975e\u6cd5\u7ecf\u8425\u95ee\u9898\u3002</p><p>\u3000\u3000\u9648\u79c9\u5fb7\u5fc3\u60f3\u8fd9\u4e48\u5b89\u6392\u4e5f\u6070\u5f53\uff0c\u80fd\u5c06\u5f71\u54cd\u529b\u4e0e\u7834\u574f\u529b\u90fd\u63a7\u5236\u5728\u80fd\u591f\u638c\u63a7\u7684\u8303\u56f4\u4e4b\u5185\uff0c\u53ea\u8981\u914d\u5408\u9ed8\u5951\u3001\u52a8\u4f5c\u8fc5\u901f\uff0c\u5c31\u4e0d\u4f1a\u89e6\u52a8\u94b1\u6842\u534e\u80cc\u540e\u7684\u9760\u5c71\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u738b\u5efa\u6797\u8fd8\u4e0d\u77e5\u9053\u4ed6\u7684\u547d\u8fd0\u5df2\u7ecf\u73a9\u5f04\u5728\u522b\u4eba\u7684\u6307\u638c\u4e4b\u95f4\uff0c\u4ed6\u79bb\u5f00\u533a\u5206\u5c40\u4e4b\u540e\uff0c\u4e5f\u77e5\u9053\u8981\u85cf\u5934\u7f29\u5c3e\u4e24\u5929\u6536\u655b\u4e00\u4e0b\uff0c\u56de\u5230\u4f4d\u4e8e\u56db\u724c\u697c\u7684\u4e91\u68a6\u9601\uff0c\u901a\u77e5\u51e0\u4e2a\u5408\u4f19\u4eba\u53ca\u4e0b\u624b\u8fd9\u4e24\u5929\u4e0d\u8981\u627e\u4ed6\uff0c\u5c31\u771f\u8ba9\u4ed6\u7ed9\u5173\u8fdb\u53f7\u5b50\u5750\u82e6\u7262\uff0c\u6311\u4e86\u4e24\u4e2a\u5976\u5927\u80a4\u767d\u8138\u86cb\u7cbe\u81f4\u7684\u5973\u5b69\u5b50\u5c31\u8fdb\u4e86\u4e91\u68a6\u9601\u7684\u5e1d\u738b\u5305\u53a2\u91cc\u660f\u5929\u9ed1\u5730\u53bb\u4e71\u641e\u53bb\u4e86\uff0c\u4e2d\u996d\u4e5f\u8ba9\u4eba\u9001\u5230\u5305\u53a2\u91cc\u6765\u3002</p><p>\u3000\u3000\u4e0b\u5348\u5728\u5305\u53a2\u91cc\u5c0f\u7761\u4e86\u4e00\u89c9\uff0c\u7cbe\u6c14\u5b8c\u8db3\uff0c\u81ea\u5df1\u4e5f\u89c9\u5f97\u5305\u53a2\u91cc\u90a3\u79cd\u8ddf\u516b\u56db\u6d88\u6bd2\u6db2\u7c7b\u4f3c\u7684\u5473\u9053\u592a\u6d53\u4e86\uff0c\u4fbf\u8dbf\u7740\u62d6\u978b\u5230\u9644\u697c\u7684\u6d74\u573a\u91cc\u53bb\u6ce1\u6fa1\u84b8\u6851\u62ff\u3002</p><p>\u3000\u3000\u4e91\u68a6\u9601\u5a31\u4e50\u5bab\u4e3b\u8981\u8fd8\u662f\u4ee5KTV\u4ff1\u4e50\u90e8\u7ecf\u8425\u4e3a\u4e3b\uff0c\u4e0d\u8fc7\u8fd9\u4e24\u5e74\u6d17\u6d74\u4e1a\u6e10\u6e10\u7ea2\u706b\uff0c\u738b\u5efa\u6797\u5c31\u5c06\u539f\u5a31\u4e50\u5bab\u4e1c\u4fa7\u7684\u798f\u5229\u5382\u51e0\u4ea9\u5730\u7ed9\u6316\u4e86\u8fc7\u6765\uff0c\u6295\u8d44\u5efa\u4e86\u6d74\u573a\uff0c\u8fd8\u82b1\u4e86\u5fc3\u601d\u8bf7\u4eba\u5c06\u6d74\u573a\u8bbe\u8ba1\u5728\u65e5\u5f0f\u6c64\u6c60\u7684\u683c\u5c40\uff0c\u5728\u5357\u90ba\u533a\u8fd8\u662f\u72ec\u6811\u4e00\u5e1c\uff0c\u9887\u4e3a\u8457\u540d\u3002</p><p>\u3000\u3000\u5916\u9762\u51b0\u5929\u96ea\u5730\u3001\u5929\u5bd2\u5730\u51b0\uff0c\u4e0b\u5348\u4e24\u4e09\u70b9\u949f\uff0c\u5230\u6d74\u573a\u91cc\u6765\u6ce1\u6fa1\u517c\u5bfb\u6b22\u4f5c\u4e50\u7684\u5ba2\u4eba\u6e10\u591a\u8d77\u6765\u3002\u738b\u5efa\u6797\u627e\u4e86\u4e00\u4e2a\u5b89\u9759\u7684\u89d2\u843d\u5750\u5230\u70ed\u6c34\u91cc\u95ed\u76ee\u517b\u795e\uff0c\u60f3\u7740\u5317\u4eac\u6b64\u884c\u6536\u83b7\u9887\u591a\uff0c\u89c1\u8bc6\u8fc7\u5317\u4eac\u540c\u884c\u4eec\u7684\u98ce\u5149\uff0c\u7279\u522b\u662f\u90a3\u4e2a\u5730\u4e0a\u4eba\u5bb6\u771f\u662f\u8ba9\u4ed6\u5927\u5f00\u773c\u754c\uff0c\u60f3\u5230\u4ed6\u4e4b\u524d\u5b8c\u5168\u5728\u59d0\u592b\u94b1\u6842\u534e\u7684\u906e\u5e87\u4e0b\u7ecf\u8425\u573a\u5b50\u7684\u60f3\u6cd5\u662f\u5b8c\u5168\u9519\u4e86\uff0c\u82e5\u80fd\u5c06\u4e91\u68a6\u9601\u7ecf\u8425\u6210\u5efa\u90ba\u9996\u5c48\u4e00\u6307\u7684\u4ff1\u4e50\u90e8\uff0c\u6781\u5c3d\u5962\u534e\u53ca\u58f0\u8272\u72ac\u9a6c\u4e4b\u80fd\u4e8b\uff0c\u4e0d\u8981\u8bf4\u5357\u90ba\u533a\u516c\u68c0\u6cd5\u53f8\u7eaa\u53ca\u653f\u5e9c\u515a\u59d4\u7684\u5934\u5934\u8111\u8111\u4e86\uff0c\u8fd8\u8981\u8ba9\u90a3\u4e9b\u5e02\u7ea7\u751a\u81f3\u7701\u7ea7\u515a\u59d4\u653f\u5e9c\u673a\u5173\u7684\u5927\u7237\u4eec\u8fc7\u6765\u201c\u73a9\u5f97\u5f00\u5fc3\u3001\u8d70\u5f97\u653e\u5fc3\u201d\uff0c\u7b49\u90a3\u9648\u79c9\u5fb7\u4e5f\u6210\u4e86\u4e91\u68a6\u9601\u7684\u5ea7\u4e0a\u5e38\u5ba2\uff0c\u8fd9\u8001\u68cd\u5b50\u8fd8\u662f\u5e2e\u90a3\u5c0f\u762a\u4e09\u6491\u8170\u6559\u8bad\u8001\u5b50\uff1f</p><p>\u3000\u3000\u738b\u5efa\u6797\u6ce1\u5728\u6fa1\u6c60\u91cc\u5e7b\u60f3\u7740\u4e91\u68a6\u9601\u5c06\u6765\u7684\u98ce\u5149\uff0c\u5012\u662f\u8d8a\u60f3\u8d8a\u5174\u594b\uff0c\u60f3\u7740\u8981\u5c06\u8fd9\u4e9b\u60f3\u6cd5\u8d76\u7d27\u5199\u4e0b\u53bb\u514d\u5f97\u4e8b\u540e\u5fd8\u8bb0\uff0c\u7ad9\u8d77\u6765\u8eab\u6765\u5c31\u8981\u5f80\u5916\u8d70\uff0c\u4e0d\u66fe\u7559\u610f\u8eab\u8fb9\u4e24\u9752\u5e74\u811a\u5f80\u6c60\u5fc3\u4f38\u5f97\u76f4\uff0c\u7ad9\u5230\u4e00\u4eba\u7684\u5c0f\u817f\u4e0a\u3002</p><p>\u3000\u3000\u201c\u4f60\u4ed6\u5988\u773c\u775b\u778e\u4e86\uff1f\u201d</p><p>\u3000\u3000\u738b\u5efa\u6797\u56de\u8f6c\u5934\u770b\u4e86\u4e00\u773c\uff0c\u7ed9\u4ed6\u8e22\u4e2d\u5c0f\u817f\u7684\u9752\u5e74\u8fd8\u8981\u7ad9\u8d77\u6765\uff0c\u53cd\u624b\u4e00\u5df4\u638c\u7529\u8fc7\u53bb\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u6bd4\u517b\u7684\uff0c\u4f60\u518d\u9a82\u4e00\u58f0\u7ed9\u6211\u542c\u542c\u2026\u2026\u201d\u5fc3\u91cc\u60f3\u7740\u771f\u4ed6\u5988\u7684\u5012\u9709\uff0c\u968f\u4fbf\u6765\u4e2a\u762a\u4e09\u90fd\u80fd\u4ed6\u5fc3\u91cc\u6dfb\u5835\uff0c\u4e5f\u4e0d\u770b\u770b\u5728\u8c01\u7684\u573a\u5b50\u91cc\uff1f\u51f6\u72e0\u7684\u77aa\u7740\u773c\u524d\u8fd9\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u9752\u5e74\uff0c\u4ee5\u4e3a\u80fd\u5c06\u4eba\u552c\u9000\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u8fd9\u9752\u5e74\u6342\u7740\u8138\u53ea\u662f\u7a0d\u6123\u4e86\u7247\u523b\uff0c\u5c31\u731b\u4f38\u624b\u8fc7\u6765\u63ea\u8d77\u4ed6\u5934\u53d1\uff0c\u8fd8\u6ca1\u6709\u7b49\u4ed6\u53cd\u6297\uff0c\u4e0e\u8fd9\u9752\u5e74\u4e00\u8d77\u7684\u53e6\u5916\u4e00\u4e2a\u9752\u5e74\u4ece\u4fa7\u9762\u4e00\u62f3\u6253\u5230\u4ed6\u7684\u8138\u988a\u4e0a\uff0c\u518d\u7ed9\u773c\u524d\u9752\u5e74\u63ea\u4f4f\u5934\u53d1\u731b\u626f\uff0c\u8eab\u4f53\u5931\u53bb\u5e73\u8861\uff0c\u683d\u5012\u5728\u6d74\u6c60\u91cc\u3002</p><p>\u3000\u3000\u738b\u5efa\u6797\u7ed9\u6441\u5728\u6c34\u91cc\u7ed9\u8fde\u8e22\u5e26\u6253\u7684\u72e0\u6328\u4e86\u597d\u51e0\u4e0b\uff0c\u90a3\u4e24\u4eba\u624d\u677e\u5f00\u624b\u79bb\u5f00\u6d74\u6c60\uff0c\u4ed6\u53e3\u9f3b\u91cc\u545b\u4e86\u6c34\uff0c\u5dee\u70b9\u8981\u618b\u8fc7\u6c14\u53bb\uff0c\u773c\u775b\u4e5f\u7ed9\u6c34\u8499\u7740\u4e86\uff0c\u53ea\u9690\u9690\u7ea6\u7ea6\u7684\u770b\u5230\u90a3\u4e24\u4eba\u5f80\u66f4\u8863\u5ba4\u91cc\u8d70\u53bb\u60f3\u8981\u79bb\u5f00\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u738b\u5efa\u6797\u6ca1\u60f3\u5230\u5728\u81ea\u5df1\u7684\u573a\u5b50\u91cc\u4e5f\u7ed9\u5916\u4eba\u66b4\u6253\u4e00\u987f\uff0c\u5fc3\u60c5\u6124\u6012\u5230\u6781\u70b9\uff0c\u5927\u58f0\u5486\u54ee\uff1a\u201c\u4ed6\u5988\u7684\u4eba\u5462\uff01\u201d</p><p>\u3000\u3000\u4ed6\u4e00\u58f0\u62db\u547c\u4e0d\u6253\u7684\u5230\u89d2\u843d\u91cc\u6ce1\u4e86\u534a\u5929\uff0c\u6d74\u6c60\u91cc\u7684\u5de5\u4f5c\u4eba\u5458\u4e5f\u6ca1\u6709\u7559\u610f\u5230\u4ed6\uff0c\u770b\u5230\u6d74\u573a\u91cc\u6709\u4eba\u5728\u6253\u67b6\uff0c\u4e5f\u53ea\u662f\u901a\u77e5\u770b\u573a\u5b50\u7684\u4fdd\u5b89\u8fdb\u6765\u5904\u7406\uff0c\u7b49\u4ed6\u88ab\u6253\u5b8c\u5927\u58f0\u5486\u54ee\u8d77\u6765\uff0c\u624d\u77e5\u9053\u662f\u4ed6\u88ab\u6253\u4e86\uff0c\u4e24\u4eba\u8d76\u7d27\u8df3\u8fdb\u6d74\u6c60\u6765\u6276\u4ed6\uff0c\u7ed9\u4ed6\u4e00\u62f3\u4e00\u811a\u90fd\u6253\u5012\u5728\u6d74\u6c60\u91cc\uff0c\u6307\u7740\u66f4\u8863\u95f4\uff0c\u5bf9\u8dd1\u8fc7\u6765\u7684\u51e0\u540d\u4fdd\u5b89\u5927\u53eb\uff1a\u201c\u66f4\u8863\u95f4\uff0c\u4ed6\u4eec\u4fe9\u90fd\u53bb\u66f4\u8863\u95f4\uff0c\u8dd1\u6389\u4e00\u4eba\uff0c\u5c06\u4f60\u4eec\u7684\u76ae\u90fd\u6252\u4e0b\u6765\u3002\u201d\u611f\u89c9\u6709\u6e29\u70ed\u7684\u6db2\u4f53\u4ece\u9f3b\u5b54\u91cc\u6d41\u51fa\u6765\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u6d74\u6c60\u91cc\u7684\u6c34\u90fd\u67d3\u7ea2\u4e86\u4e00\u7247\uff0c\u6709\u540d\u521a\u7ed9\u4ed6\u8e22\u5012\u7684\u5de5\u4f5c\u4eba\u5458\u4ece\u6d74\u6c60\u91cc\u6323\u624e\u7740\u722c\u8d77\u6765\uff0c\u4ed6\u53c8\u4e00\u811a\u8e39\u8fc7\u53bb\uff0c\u558a\u9053\uff1a\u201c\u5988\u7684\uff0c\u8001\u5b50\u8d1f\u4f24\u4e86\uff0c\u8d76\u7ed9\u8001\u5b50\u62ff\u6b62\u8840\u68c9\u6765\u3002\u201d\u4ed6\u8eba\u5230\u6d74\u6c60\u91cc\u5934\u4ef0\u9760\u5230\u6c60\u58c1\u4e0a\uff0c\u514d\u5f97\u9f3b\u8840\u6d41\u592a\u591a\uff0c\u7cbe\u8840\u7cbe\u8840\uff0c\u4eca\u5929\u6d41\u7cbe\u592a\u591a\uff0c\u8fd9\u4f1a\u513f\u53c8\u6d41\u8840\u4e86\uff0c\u8981\u5403\u591a\u5c11\u987f\u5927\u9910\u624d\u8865\u5f97\u56de\u6765\uff1f\u8fd9\u4e24\u4e2a\u755c\u7272\u52a8\u624b\u8fd8\u771f\u72e0\uff0c\u8fd9\u65f6\u5019\u808b\u4e0b\u8fd8\u9690\u9690\u7684\u75bc\uff0c\u5148\u6b47\u4e00\u4e0b\uff0c\u7b49\u4f1a\u513f\u597d\u597d\u7684\u6536\u62fe\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u738b\u5efa\u6797\u7b49\u611f\u89c9\u5230\u9f3b\u5b50\u4e0d\u518d\u6d41\u8840\u4e86\uff0c\u5c06\u68c9\u7403\u62d4\u51fa\u6765\uff0c\u624d\u4e0a\u4e86\u5cb8\uff0c\u6162\u6761\u7406\u4e1d\u7684\u64e6\u5e72\u8eab\u5b50\uff0c\u7a7f\u4e0a\u6d74\u888d\uff0c\u624d\u95ee\u624b\u4e0b\uff1a\u201c\u90a3\u4e24\u4e2a\u9f9f\u513f\u5b50\u5462\uff0c\u4f60\u4eec\u6709\u6ca1\u6709\u597d\u597d\u62db\u547c\u4ed6\u4eec\uff1f\u5c06\u4ed6\u4eec\u62c9\u5230\u4e09\u697c\u5305\u53a2\u6765\u2026\u2026\u201d\u8fd9\u65f6\u5019\u4e00\u4e2a\u4eba\u6025\u51b2\u51b2\u7684\u8d70\u8fc7\u6765\uff0c\u624b\u91cc\u62ff\u7740\u624b\u673a\u9012\u7ed9\u4ed6\uff1a\u201c\u94b1\u5c40\u957f\u7684\u7535\u8bdd\uff0c\u8981\u4f60\u9a6c\u4e0a\u63a5\u542c\uff01\u201d</p><p>\u3000\u3000\u738b\u5efa\u6797\u624b\u6342\u7740\u901a\u8bdd\u5b54\uff0c\u4e00\u817f\u8e39\u8fc7\u53bb\uff0c\u9a82\u9053\uff1a\u201c\u8c01\u8ba9\u4f60\u4eec\u7ed9\u90a3\u4e24\u4e2a\u9f9f\u513f\u5b50\u6709\u673a\u4f1a\u7ed9\u5916\u9762\u6253\u7535\u8bdd\u7684\uff1f\u201d</p><p>\u3000\u3000\u90a3\u4eba\u6367\u7740\u5c0f\u809a\u5b50\u5fcd\u75db\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u63a5\u94b1\u5c40\u957f\u7684\u8bdd\uff0c\u8981\u662f\u803d\u6401\u4e8b\uff0c\u94b1\u5c40\u957f\u8bf4\u8981\u62bd\u6211\u7684\u7b4b\uff0c\u94b1\u5c40\u957f\u7684\u8bed\u6c14\u4e0d\u50cf\u662f\u5f00\u73a9\u7b11\u3002\u201d</p><p>\u3000\u3000\u738b\u5efa\u6797\u77e5\u9053\u8981\u63a5\u4e86\u8fd9\u4e2a\u7535\u8bdd\uff0c\u521a\u624d\u90a3\u51e0\u4e0b\u5c31\u767d\u767d\u7684\u751f\u6328\u4e86\uff0c\u4e0d\u8fc7\u4eca\u5929\u4e0a\u5348\u624d\u53d1\u751f\u90a3\u6837\u7684\u4e8b\u60c5\uff0c\u4ed6\u8fd9\u65f6\u5019\u53ef\u4e0d\u6562\u8fdd\u62e7\u59d0\u592b\uff0c\u9876\u591a\u5c06\u6c14\u5f80\u624b\u4e0b\u4eba\u8eab\u4e0a\u6492\uff0c\u5bf9\u624b\u4e0b\u9a82\u9a82\u54a7\u54a7\u7684\u8bad\u4e86\u4e24\u53e5\uff0c\u8fd8\u662f\u63a5\u4e86\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\uff0c\u662f\u80e1\u526f\u4e66\u8bb0\u7684\u4f84\u5b50\u2026\u2026\u201d\u738b\u5efa\u6797\u6068\u4e0d\u5f97\u627c\u4f4f\u81ea\u5df1\u7684\u55d3\u5b50\u5c31\u8fd9\u6837\u6b7b\u6389\u7b97\u4e86\uff0c\u8fd9\u624d\u786e\u4fe1\u521a\u624d\u90a3\u51e0\u4e0b\u662f\u7ed9\u767d\u6253\u4e86\uff0c\u7ed9\u767d\u6253\u4e86\u8fd8\u4e0d\u7b97\uff0c\u8fd8\u8981\u53bb\u8d54\u7b11\u8138\uff0c\u9634\u6c89\u7740\u8138\uff0c\u6302\u6389\u7535\u8bdd\u3002\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u4e0d\u6b63\u60f3\u7740\u901a\u8fc7\u4e91\u68a6\u9601\u7ed3\u4ea4\u6743\u8d35\u5417\uff0c\u8981\u80fd\u6500\u4e0a\u80e1\u526f\u4e66\u8bb0\u7684\u5173\u7cfb\uff0c\u8fd8\u4f55\u60e7\u533a\u533a\u4e00\u4e2a\u9648\u79c9\u5fb7\uff1f</p><p>\u3000\u3000\u53ea\u662f\u8fd9\u5173\u7cfb\u600e\u4e48\u624d\u80fd\u7f13\u548c\u4e0b\u6765\uff0c\u53c8\u600e\u4e48\u624d\u80fd\u70ed\u7edc\u8d77\u6765\uff0c\u738b\u5efa\u6797\u770b\u7740\u6d74\u6c60\uff0c\u4e00\u6ee9\u9f3b\u8840\u65e9\u5c31\u7ed9\u6c60\u6c34\u51b2\u7a00\u6de1\u65e0\u5f71\u4e86\uff0c\u521a\u624d\u8fd8\u62f3\u811a\u76f8\u52a0\u6765\u7740\uff0c\u8fd9\u65f6\u5019\u51fa\u53bb\u8d54\u793c\u9053\u6b49\u3001\u4eba\u5bb6\u8896\u624b\u800c\u8d70\u518d\u4e0d\u9732\u9762\uff0c\u4ed6\u53c8\u80fd\u5948\u5176\u4f55\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u96ea\u8fc7\u4e86\u4e2d\u5348\u5c31\u5c0f\u4e86\u4e9b\uff0c\u4e0d\u8fc7\u98d8\u98d8\u626c\u626c\u7684\u4e00\u76f4\u5728\u4e0b\uff0c\u697c\u9876\u7684\u7a7a\u4e2d\u5ead\u56ed\u90fd\u8986\u4e0a\u7691\u7691\u767d\u96ea\uff0c\u51ed\u6728\u7bf1\u800c\u7ad9\uff0c\u4e1c\u8fb9\u7684\u6821\u820d\u3001\u7f51\u573a\u7403\u3001\u8349\u576a\u3001\u5408\u6b22\u5c71\u90fd\u8986\u4e86\u767d\u96ea\uff0c\u5df2\u7ecf\u662f\u5efa\u90ba\u4e00\u5e74\u4e2d\u6700\u51b7\u7684\u5b63\u8282\u4e86\uff0c\u8fd8\u662f\u80fd\u770b\u5230\u5408\u6b22\u5c71\u4e0a\u6709\u8bb8\u591a\u9752\u5e74\u7537\u5973\u5728\u96ea\u4e2d\u5e7d\u4f1a\u3002</p><p>\u3000\u3000\u89c1\u5510\u5a67\u7684\u96ea\u4eba\u8fd8\u6709\u5934\u6ca1\u6709\u5806\u8d77\u6765\uff0c\u5f20\u606a\u4e3e\u8d77\u624b\u8155\u6765\u793a\u610f\u5979\u5230\u65f6\u95f4\u4e86\uff0c\u8981\u5979\u6536\u62fe\u4e00\u4e0b\uff0c\u51c6\u5907\u4e00\u8d77\u53bb\u7f57\u541b\u5728\u5efa\u90ba\u7684\u4f4f\u6240\u5403\u665a\u996d\u53bb\u3002\u867d\u8bf4\u662f\u62c9\u8fd1\u79c1\u4eba\u5173\u7cfb\u7684\u79c1\u5bb4\uff0c\u5f20\u606a\u603b\u4e0d\u80fd\u7b49\u5230\u5929\u8272\u6697\u4e0b\u6765\u518d\u52a8\u8eab\u53bb\u7f57\u541b\u5bb6\u4e2d\u3002</p><p>\u3000\u3000\u5510\u5a67\u62cd\u4e86\u62cd\u624b\uff0c\u5bf9\u9648\u5983\u84c9\u5979\u4eec\u8bf4\u9053\uff1b\u201c\u771f\u662f\u626b\u5174\u554a\uff0c\u90fd\u4e0d\u80fd\u966a\u4f60\u4eec\u4e00\u8d77\u5403\u665a\u996d\u3002\u201d</p><p>\u3000\u3000\u201c\u4e00\u8d77\u5403\u591c\u5bb5\u8fd8\u662f\u53ef\u4ee5\u7684\u2026\u2026\u201d\u5f20\u606a\u5c06\u624b\u8d34\u5230\u5510\u5a67\u7684\u8138\u988a\u4e0a\uff0c\u5929\u5bd2\u5730\u51b0\u7684\uff0c\u5979\u7684\u8138\u6108\u53d1\u7684\u96ea\u767d\uff0c\u8ddf\u679d\u53f6\u4e0a\u7684\u8986\u96ea\u4f3c\u7684\uff0c\u50ac\u4fc3\u5979\u8d76\u7d27\u4e0b\u53bb\u51c6\u5907\u4e00\u4e0b\u3002\u4ed6\u8d70\u8fdb\u6696\u9601\u91cc\u5c06\u624b\u673a\u62ff\u8d77\u6765\uff0c\u51c6\u5907\u901a\u77e5\u53f8\u673a\u5c06\u8f66\u505c\u5230\u697c\u4e0b\uff0c\u4ed6\u4eec\u81ea\u5df1\u5f00\u8f66\u53bb\u7f57\u541b\u5bb6\u3002</p><p>\u3000\u3000\u5085\u4fca\u7684\u7535\u8bdd\u5148\u6253\u8fdb\u6765\uff0c\u4e0e\u5f20\u606a\u6240\u6599\u4e0d\u5dee\uff0c\u94b1\u6842\u534e\u679c\u771f\u6ca1\u6709\u5c06\u4ed6\u8205\u5b50\u738b\u5efa\u6797\u9001\u770b\u5b88\u6240\u5173\u8d77\u6765\u3002\u8bf4\u8d77\u4f4d\u4e8e\u56db\u724c\u697c\u7684\u4e91\u68a6\u9601\u6765\uff0c\u5f20\u606a\u8fd8\u8bb0\u5f97\u65f6\u5b66\u658c\u8fd8\u5728\u90a3\u91cc\u7559\u4e0b\u4e00\u6bb5\u60c5\uff0c\u90a3\u8fd8\u662f\u738b\u6d77\u7c9f\u4e3a\u8ba8\u597d\u8bf7\u4ed6\u4eec\u53bb\u56db\u724c\u697c\u7684\u591c\u5e97\u53bb\u73a9\uff0c\u65f6\u5b66\u658c\u5728\u4e91\u68a6\u9601\u8ba4\u8bc6\u4e86\u4e2a\u8c8c\u4f3c\u6e05\u7eaf\u7684\u201c\u5b66\u751f\u59b9\u201d\uff0c\u53ef\u60dc\u7b49\u65f6\u5b66\u658c\u4e00\u8154\u70ed\u60c5\u6295\u8fdb\u53bb\uff0c\u90a3\u201c\u5b66\u751f\u59b9\u201d\u53c8\u7ed9\u738b\u6d77\u7c9f\u4ecb\u7ecd\u7ed9\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u80e1\u91d1\u661f\u4e86\uff0c\u4e3a\u6b64\u65f6\u5b66\u658c\u8fd8\u4e0e\u80e1\u91d1\u661f\u5927\u6253\u51fa\u624b\u60f9\u51fa\u4e00\u573a\u7ea0\u7eb7\u6765\u3002</p><p>\u3000\u3000\u201c\u786e\u5b9a\u770b\u5230\u7684\u662f\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\uff1f\u201d\u5f20\u606a\u53c8\u95ee\u4e86\u4e00\u53e5\uff0c\u5fae\u54ac\u7740\u5634\u5507\uff0c\u5fc3\u60f3\u7740\u8bf4\u4e0d\u5b9a\u53d7\u738b\u6d77\u7c9f\u7684\u8bf1\u5bfc\uff0c\u80e1\u91d1\u661f\u5f00\u59cb\u8ff7\u604b\u4e0a\u56db\u724c\u697c\u7684\u591c\u5e97\u6b22\u573a\u4e86\uff0c\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u5143\u65e6\u653e\u5047\uff0c\u80e1\u91d1\u661f\u5012\u662f\u559c\u6b22\u53bb\u8fd9\u79cd\u5730\u65b9\uff0c\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u5bf9\u6709\u4e9b\u4eba\u6765\u8bf4\uff0c\u8fd9\u79cd\u573a\u5408\u7684\u5973\u5b69\u5b50\u6709\u79cd\u96be\u8a00\u7684\u8bf1\u60d1\u529b\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u7537\u4eba\u7684\u5174\u8da3\u603b\u662f\u591a\u6837\u6027\u7684\u3002\u5c31\u80e1\u5b97\u5e86\u7684\u672c\u610f\u6765\u770b\uff0c\u4ed6\u819d\u4e0b\u65e0\u5b50\uff0c\u5bf9\u80e1\u91d1\u661f\u7684\u5bc4\u671b\u9887\u9ad8\uff0c\u591a\u534a\u4e0d\u4f1a\u5e0c\u671b\u770b\u5230\u4ed6\u5e74\u9f84\u8f7b\u8f7b\u7684\u5c31\u6d41\u8fde\u8fd9\u4e9b\u82b1\u67f3\u4e4b\u5730\u7684\uff0c\u786c\u8981\u4ece\u8fd9\u4ef6\u4e8b\u5224\u65ad\u94b1\u6842\u534e\u4e0e\u80e1\u5b97\u5e86\u6709\u7275\u6d89\u5f88\u52c9\u5f3a\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u53c8\u901a\u4e86\u4e00\u4f1a\u513f\u7535\u8bdd\uff0c\u5631\u5490\u4ed6\u4eec\u53ea\u80fd\u4e86\u89e3\u60c5\u51b5\u3001\u638c\u63e1\u60c5\u51b5\uff0c\u4e0d\u8981\u8d8a\u4e86\u754c\u7ebf\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u6ca1\u6709\u60f3\u5230\u4eca\u5929\u7684\u738b\u5efa\u6797\u5fc3\u6d6e\u6c14\u8e81\uff0c\u6781\u6613\u8ddf\u4eba\u8d77\u51b2\u7a81\uff0c\u800c\u4e14\u4e5f\u5df2\u7ecf\u5728\u6d74\u573a\u91cc\u8ddf\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u80e1\u91d1\u661f\u8d77\u4e86\u51b2\u7a81\uff0c\u5de7\u5408\u7684\u4e8b\u90fd\u8d76\u4e0a\u4eca\u5929\u4e86\u3002</p></article>"}}}]);
