(window.webpackJsonp=window.webpackJsonp||[]).push([[985],{1008:function(p){p.exports={chapterTitle:null,title:"\u7b2c797\u7ae0 \u534e\u590f\u7535\u5b50",content:"<article><h2>\u7b2c797\u7ae0 \u534e\u590f\u7535\u5b50</h2><p>\u3000\u3000\u867d\u7136\u4e00\u76f4\u5230\u56db\u6708\u4e0a\u65ec\uff0c\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u7684\u7ec4\u5efa\u5de5\u4f5c\u90fd\u6ca1\u6709\u901a\u8fc7\u65b0\u95fb\u5a92\u4f53\u516c\u5e03\u6b63\u5f0f\u7684\u4fe1\u606f\uff0c\u4f46\u662f\u5b9e\u9645\u7684\u7ec4\u5efa\u5de5\u4f5c\u4ece\u4e09\u6708\u4e2d\u65ec\u5c31\u5df2\u7ecf\u5f00\u59cb\u8fdb\u884c\u4e86\u3002\u8054\u8baf\u3001\u8054\u4fe1\u3001\u4e1c\u5174\u3001\u534e\u90ae\u7b49\u539f\u90ae\u7535\u90e8\u3001\u7535\u5b50\u5de5\u4e1a\u90e8\u6240\u5c5e\u7535\u5b50\u4f01\u4e1a\u7684\u8001\u603b\u4eec\u8fd9\u6bb5\u65f6\u95f4\u90fd\u805a\u96c6\u5728\u5317\u4eac\u5f00\u4f1a\uff0c\u7531\u539f\u56fd\u5bb6\u8ba1\u59d4\u8f6c\u53d8\u8fc7\u6765\u7684\u53d1\u6539\u59d4\u56db\u6708\u521d\u516c\u5e03\u7684\u526f\u90e8\u7ea7\u5b98\u5458\u540d\u5355\u4e2d\uff0c\u845b\u5efa\u5fb7\u4e5f\u4e0d\u5728\u5176\u5217\u3002</p><p>\u3000\u3000\u65b0\u4e00\u5c4a\u653f\u5e9c\u8868\u8fbe\u8981\u52a0\u5f3a\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u53d1\u5c55\u7684\u51b3\u5fc3\uff0c\u4fe1\u606f\u4ea7\u4e1a\u4f5c\u4e3a\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u6700\u4e3b\u8981\u7684\u5206\u652f\uff0c\u5176\u53d1\u5c55\u4e5f\u5f97\u5230\u5f02\u4e8e\u5f80\u65e5\u7684\u91cd\u89c6\uff0c\u9664\u4e86\u7ec4\u5efa\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4e4b\u5916\uff0c\u7ecf\u515a\u4e2d\u592e\u3001\u56fd\u52a1\u9662\u6279\u51c6\uff0c\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u7684\u7ea7\u522b\u5728\u4e5d\u5c4a\u5168\u56fd\u4eba\u5927\u4f1a\u8bae\u4e4b\u540e\u9661\u7136\u63d0\u9ad8\uff0c\u7531\u526f\u603b\u7406\u66fe\u5982\u5723\u62c5\u4efb\u7ec4\u957f\uff0c\u803f\u91cd\u9633\u62c5\u4efb\u526f\u7ec4\u957f\uff0c\u539f\u5c0f\u7ec4\u7ec4\u957f\u845b\u5efa\u5fb7\u4ec5\u4e3a\u5c0f\u7ec4\u6210\u5458\u3002</p><p>\u3000\u3000\u4eca\u5e74\u7684\u6625\u5929\u4f3c\u4e4e\u6765\u5f97\u683c\u5916\u7684\u8fdf\u7f13\uff0c\u79bb\u5f00\u6e29\u6696\u7684\u63a5\u673a\u5927\u5385\u8d70\u5230\u5ba4\u5916\uff0c\u5916\u5957\u655e\u5f00\u7740\u8fce\u98ce\u800c\u7acb\u8fd8\u89c9\u5f97\u4e9b\u5bd2\u51b7\uff0c\u5df2\u7ecf\u8fdb\u5165\u56db\u6708\u4e86\uff0c\u5f20\u606a\u7ad9\u5728\u673a\u573a\u63a5\u673a\u5927\u5385\u524d\u7684\u5e7f\u573a\u524d\u7b49\u5019\u8f66\u5b50\u4ece\u505c\u8f66\u573a\u5f00\u8fc7\u6765\uff0c\u770b\u7740\u5317\u65b9\u7684\u5929\u7a7a\u4f3c\u6709\u4e00\u7247\u7070\u9ed1\u4e91\u70df\u5f25\u6f2b\u800c\u6765\uff0c\u968f\u884c\u7684\u5085\u4fca\u3001\u7fdf\u4e39\u9752\u7b49\u4eba\u4ece\u6765\u90fd\u6ca1\u6709\u9047\u5230\u7c7b\u4f3c\u7684\u60c5\u5f62\uff0c\u4e0d\u77e5\u9053\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\u3002</p><p>\u3000\u3000\u6709\u7ecf\u9a8c\u7684\u65c5\u5ba2\u8def\u8fc7\u770b\u7740\u5317\u65b9\u7684\u5929\u7a7a\uff0c\u53ea\u662f\u611f\u6168\u4e86\u4e00\u53e5\uff1a\u201c\u72d7\u65e5\u7684\uff0c\u53c8\u9047\u4e0a\u98ce\u5c18\u66b4\u4e86\uff01\u4e09\u6708\u5728\u5317\u4eac\u4f4f\u4e86\u534a\u4e2a\u6708\uff0c\u5728\u8857\u4e0a\u9047\u5230\u4e09\u56de\u98ce\u5c18\u66b4\uff0c\u6bcf\u56de\u90fd\u7ed9\u5439\u5f97\u7070\u5934\u571f\u8138\uff0c\u8eb2\u90fd\u6ca1\u529e\u6cd5\u8eb2\uff0c\u5317\u4eac\u7684\u5973\u5b69\u5b50\u76ae\u80a4\u6bd4\u8f83\u7c97\u7cd9\uff0c\u4f60\u8bf4\u8fd9\u9b3c\u5929\u6c14\u5230\u5927\u8857\u4e0a\u8d70\u4e24\u56de\uff0c\u8c01\u80fd\u591f\u6c34\u7075\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8981\u5e86\u5e78\u662f\u98de\u673a\u964d\u843d\u4e4b\u540e\uff0c\u98ce\u5c18\u66b4\u624d\u522e\u8fc7\u6765\u2026\u2026\u201d\u540c\u884c\u7684\u65c5\u5ba2\u5728\u65c1\u8fb9\u8bf4\u9053\uff0c\u8bf4\u7740\u8bdd\uff0c\u626d\u8138\u770b\u7740\u5f20\u606a\u8eab\u8fb9\u660e\u8273\u52a8\u4eba\u7684\u7fdf\u4e39\u9752\uff0c\u6123\u6123\u7684\u770b\u7740\uff0c\u5dee\u70b9\u4e00\u5934\u649e\u5eca\u67f1\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u6709\u89c1\u8bc6\u8fc7\u98ce\u5c18\u66b4\uff0c\u770b\u7740\u5317\u65b9\u6f2b\u5929\u7684\u9ec4\u4e91\u79bb\u673a\u573a\u8fd9\u8fb9\u8fd8\u6709\u4e9b\u8ddd\u79bb\uff0c\u94bb\u8fdb\u8f66\u5b50\u91cc\u3002\u4f46\u662f\u5f80\u5e02\u533a\u53ea\u884c\u9a76\u4e86\u4e03\u516b\u5206\u949f\uff0c\u5c31\u7ed9\u6f2b\u5929\u6c99\u5c18\u515c\u5934\u515c\u8138\u7684\u902e\u4e86\u4e2a\u6b63\u7740\uff0c\u53ea\u770b\u7740\u8f66\u7a97\u5916\u9ec4\u6f2b\u6f2b\u7684\u4e00\u7247\uff0c\u80fd\u89c1\u5ea6\u4e0d\u8d85\u8fc7\u5341\u7c73\uff0c\u7ec6\u6c99\u783e\u5439\u6253\u7740\u8f66\u8eab\u3001\u8f66\u7a97\u73bb\u7483\u91cc\u3002\u867d\u7136\u8fd9\u79cd\u7a0b\u5ea6\u6c99\u783e\u5439\u6253\u4e0d\u4f1a\u7ed9\u51ef\u8fea\u62c9\u514b\u7684\u8f66\u8eab\u6f06\u9020\u6210\u4ec0\u4e48\u75d5\u8ff9\uff0c\u53ea\u662f\u8fd9\u4e9b\u8eab\u5904\u6c99\u5c18\u66b4\u4e4b\u4e2d\u7684\u58f0\u54cd\u542c\u4e0a\u53bb\u5fc3\u91cc\u8fd8\u662f\u6bdb\u6bdb\u7684\u3002</p><p>\u3000\u3000\u8f66\u5b50\u505c\u5728\u8def\u8fb9\u7b49\u5019\u6c99\u5c18\u66b4\u8fc7\u53bb\uff0c\u5f20\u606a\u60f3\u7740\u4e5d\u516b\u5e74\uff0c\u5317\u4eac\u7684\u6c99\u5c18\u66b4\u662f\u5f88\u4e25\u91cd\u4e86\uff0c\u5468\u8fb9\u73af\u5883\u6076\u5316\uff0c\u751a\u81f3\u5a92\u4f53\u4e0a\u90fd\u520a\u767b\u5b66\u8005\u5efa\u8bae\u8fc1\u90fd\u7684\u6587\u7ae0\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u8fd9\u6837\u7684\u8bba\u8c03\uff0c\u5f20\u606a\u53ea\u662f\u7b11\u7b11\uff0c\u603b\u4e0d\u80fd\u5c06\u8fd9\u4e9b\u4e13\u5bb6\u5b66\u8005\u7684\u5634\u5df4\u90fd\u62ff\u9488\u7f1d\u8d77\u6765\u5427\u3002</p><p>\u3000\u3000\u515c\u91cc\u7684\u624b\u673a\u9707\u52a8\u8d77\u6765\uff0c\u662f\u5f90\u5b66\u5e73\u5728\u5317\u4eac\u5bb6\u91cc\u7684\u7535\u8bdd\uff0c\u5f90\u5b66\u5e73\u7684\u59bb\u5b50\u5468\u6dd1\u60e0\u95ee\u4ed6\u4e0b\u98de\u673a\u591a\u4e45\u4e86\u3002</p><p>\u3000\u3000\u201c\u79bb\u5e02\u533a\u4e0d\u8fdc\u4e86\uff0c\u4e0d\u8fc7\u7ed9\u6c99\u5c18\u66b4\u56f0\u5728\u534a\u8def\u4e0a\u4e86\uff0c\u5f90\u4f2f\u4f2f\u56de\u6765\u4e86\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u6b21\u90e8\u59d4\u5b98\u5458\u8c03\u6574\u4e2d\uff0c\u5f90\u5b66\u5e73\u804c\u52a1\u6ca1\u6709\u53d1\u751f\u53d8\u52a8\uff0c\u8fd8\u5728\u8d75\u6d4e\u4e1c\u4eb2\u81ea\u62c5\u4efb\u7ec4\u957f\u7684\u79d1\u6559\u5174\u56fd\u9886\u5bfc\u5c0f\u7ec4\u91cc\u62c5\u4efb\u5c0f\u7ec4\u6210\u5458\uff0c\u5728\u4e2d\u592e\u653f\u5e9c\u91cc\u7684\u5730\u4f4d\u53ef\u4ee5\u8bf4\u662f\u52a0\u5f3a\u4e86\u3002</p><p>\u3000\u3000\u201c\u672c\u6765\u5df2\u7ecf\u5728\u56de\u6765\u7684\u8def\u4e0a\uff0c\u4e34\u65f6\u7ed9\u66fe\u526f\u603b\u7406\u6709\u4e8b\u7ed9\u627e\u4e86\u8fc7\u53bb\uff0c\u4e0d\u89c1\u5f97\u6bd4\u4f60\u65e9\u56de\u6765\u3002\u201d\u5468\u6dd1\u60e0\u5728\u7535\u8bdd\u91cc\u8bf4\u9053\u3002</p><p>\u3000\u3000\u66fe\u5982\u5723\u526f\u603b\u7406\u662f\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u7684\u7ec4\u957f\uff0c\u5728\u56fd\u52a1\u9662\u5185\u5206\u7ba1\u4fe1\u606f\u4ea7\u4e1a\u90e8\uff0c\u4e4b\u524d\u7684\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u5bf9\u56fd\u5185\u7684\u4fe1\u606f\u4ea7\u4e1a\u5f71\u54cd\u529b\u8fdc\u4e0d\u5982\u73b0\u5728\u4e4b\u5927\uff0c\u53ef\u4ee5\u8bf4\u6b64\u65f6\u7684\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u624d\u662f\u6700\u7ec8\u51b3\u5b9a\u56fd\u5185\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u65b9\u5411\u7684\u673a\u6784\u3002</p><p>\u3000\u3000\u66fe\u5982\u5723\u526f\u603b\u7406\u540c\u65f6\u53c8\u517c\u4efb\u79d1\u6559\u5174\u56fd\u9886\u5bfc\u5c0f\u7ec4\u526f\u7ec4\u957f\uff0c\u4e0e\u5f90\u5b66\u5e73\u63a5\u89e6\u4e5f\u591a\uff0c\u5f20\u606a\u4e0d\u6e05\u695a\u66fe\u5982\u5723\u4f1a\u4e0d\u4f1a\u4ece\u5f90\u5b66\u5e73\u90a3\u91cc\u4e86\u89e3\u9526\u6e56\u7684\u52a8\u6001\u3002</p><p>\u3000\u3000\u5f85\u66b4\u8650\u7684\u98ce\u5c18\u66b4\u6d88\u6563\uff0c\u8def\u9762\u4e0a\u79ef\u4e86\u4e00\u5c42\u6d6e\u6c99\uff0c\u8f66\u73bb\u79bb\u6cbe\u4e86\u6d6e\u5c18\uff0c\u7070\u8499\u8499\u7684\uff0c\u4e24\u4fa7\u7684\u5efa\u7b51\u7684\u7a97\u6237\u4e5f\u90fd\u662f\u5982\u6b64\uff0c\u8981\u662f\u54ea\u6237\u4eba\u5bb6\u5fd8\u4e86\u5173\u7a97\u6237\uff0c\u5bb6\u91cc\u7684\u7070\u5c18\u53ef\u6709\u7684\u6e05\u626b\u4e86\u3002</p><p>\u3000\u3000\u8d76\u5230\u5f90\u5b66\u5e73\u5728\u4e1c\u5355\u7684\u5bd3\u6240\uff0c\u8fdb\u5927\u95e8\u65f6\uff0c\u8d76\u5de7\u5f90\u5b66\u5e73\u7684\u8f66\u4e5f\u521a\u8fdb\u53bb\uff0c\u5f90\u5b66\u5e73\u4e0b\u4e86\u8f66\uff0c\u5c31\u7ad9\u5728\u524d\u9762\u7684\u8349\u576a\u7b49\u5f20\u606a\u4ed6\u4eec\u4e00\u8d77\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0b\u4e86\u8f66\uff0c\u966a\u5f90\u5b66\u5e73\u6b65\u884c\u5f80\u91cc\u3002</p><p>\u3000\u3000\u201c\u534e\u590f\u7535\u5b50\u7684\u7ec4\u5efa\u6d88\u606f\uff0c\u8fd9\u4e24\u5929\u5c31\u8981\u516c\u5e03\u4e86\uff0c\u201d\u5f90\u5b66\u5e73\u53cc\u624b\u526a\u5728\u8eab\u540e\uff0c\u201c\u66fe\u526f\u603b\u7406\u521a\u521a\u627e\u6211\u8fc7\u53bb\uff0c\u95ee\u6211\u9526\u6e56\u7684\u4e8b\u60c5\u3002\u4ed6\u8fd9\u65f6\u5019\u5fd9\u4e8e\u5176\u4ed6\u4e8b\u52a1\uff0c\u65e0\u6cd5\u7279\u610f\u7684\u53bb\u5173\u5fc3\u9526\u6e56\u7684\u4e8b\u52a1\uff0c\u4e0d\u8fc7\u4ed6\u5bf9\u534e\u590f\u7535\u5b50\u7ec4\u5efa\u4e4b\u540e\uff0c\u5c06\u4e0e\u9526\u6e56\u7ade\u4e89\u7684\u95ee\u9898\uff0c\u8fd8\u662f\u6301\u8d5e\u6210\u6001\u5ea6\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u867d\u7136\u6709\u4e9b\u56fd\u6709\u8d44\u4ea7\u6b63\u8bd5\u9a8c\u6027\u7684\u4ece\u4e00\u4e9b\u7ade\u4e89\u6027\u884c\u4e1a\u4e2d\u9000\u51fa\uff0c\u4f46\u662f\u52a0\u5f3a\u56fd\u6709\u8d44\u4ea7\u5728\u6838\u5fc3\u4ea7\u4e1a\u4e2d\u7684\u5730\u4f4d\u662f\u8fd9\u5c4a\u653f\u5e9c\u4f1a\u91cd\u70b9\u52a0\u5f3a\u7684\u5de5\u4f5c\uff0c\u66fe\u526f\u603b\u7406\u7684\u8bdd\u5927\u6982\u662f\u6709\u76f8\u5f53\u5927\u7684\u4fdd\u7559\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u526f\u603b\u7406\u7684\u8bdd\uff0c\u4f60\u90fd\u6562\u8d28\u7591\uff1f\u201d\u5f90\u5b66\u5e73\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u6240\u8c13\u516c\u5e73\u7ade\u4e89\uff0c\u4ece\u6765\u90fd\u662f\u5634\u4e0a\u8bf4\u5f97\u6f02\u4eae\u554a\uff0c\u201d\u5f20\u606a\u644a\u4e86\u644a\u624b\uff0c\u201c\u539f\u7535\u5b50\u5de5\u4e1a\u90e8\u4e3a\u5927\u80a1\u4e1c\u7684\u8054\u901a\u7ec4\u5efa\u591a\u5c11\u5e74\u4e86\uff1f\u4e2d\u592e\u4e00\u76f4\u90fd\u652f\u6301\u8054\u901a\u4e0e\u90ae\u7535\u90e8\u7684\u7535\u4fe1\u96c6\u56e2\u516c\u5e73\u7ade\u4e89\uff0c\u4f46\u662f\u8054\u901a\u7ed9\u538b\u5236\u5230\u73b0\u5728\uff0c\u8981\u4e0d\u662f\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0e\u90ae\u7535\u90e8\u5408\u5e76\u7ec4\u6210\u4fe1\u606f\u4ea7\u4e1a\u90e8\uff0c\u54ea\u91cc\u6709\u7ffb\u8eab\u7684\u673a\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u201c\u66fe\u526f\u603b\u7406\u5fc3\u91cc\u7a76\u7adf\u662f\u600e\u4e48\u8003\u8651\u7684\uff0c\u4ed6\u6ca1\u6709\u8ddf\u6211\u8be6\u8c08\uff0c\u4f46\u662f\u6709\u4e00\u70b9\u662f\u53ef\u4ee5\u80af\u5b9a\uff0c\u8981\u662f\u9526\u6e56\u6bd4\u672a\u6765\u7684\u534e\u590f\u7535\u5b50\u8981\u51fa\u8272\u5f97\u591a\uff0c\u56fd\u5bb6\u5e94\u8be5\u4f1a\u652f\u6301\u9526\u6e56\u7684\u2026\u2026\u201d\u5f90\u5b66\u5e73\u8bf4\u9053\uff0c\u201c\u5373\u4f7f\u4e0d\u652f\u6301\uff0c\u4e5f\u6ca1\u6709\u6253\u538b\u7684\u9053\u7406\uff0c\u4e2d\u6676\u5fae\u82af\u9879\u76ee\uff0c\u56fd\u5bb6\u4e0d\u4e5f\u662f\u5927\u529b\u575a\u6301\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u4e0e\u534e\u590f\u5728\u5e02\u573a\u4e0a\u516c\u5f00\u7ade\u4e89\uff0c\u4fe1\u606f\u4ea7\u4e1a\u90e8\u66f4\u652f\u6301\u534e\u590f\uff0c\u5c31\u662f\u4e8b\u5b9e\u4e0a\u5bf9\u9526\u6e56\u7684\u538b\u5236\uff0c\u5f88\u96be\u8bf4\u80fd\u4e00\u7897\u6c34\u7aef\u5e73\uff0c\u201d\u5f20\u606a\u6487\u4e86\u6487\u5634\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u66fe\u526f\u603b\u7406\u65e2\u7136\u8fd9\u4e48\u8bf4\u4e86\uff0c\u5728\u5e02\u573a\u7ade\u4e89\u4e2d\uff0c\u53ef\u4e0d\u8981\u6028\u9526\u6e56\u6ca1\u6709\u7ed9\u534e\u590f\u7559\u4f59\u5730\uff01\u6211\u76f8\u4fe1\u9526\u6e56\u4f1a\u6bd4\u672a\u6765\u7684\u534e\u590f\u66f4\u51fa\u8272\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u66fe\u526f\u603b\u7406\u53ef\u6ca1\u6709\u6307\u671b\u9526\u6e56\u4f1a\u7559\u4ec0\u4e48\u4f59\u5730\uff0c\u4ed6\u751a\u81f3\u5e0c\u671b\u9526\u6e56\u7684\u51fa\u8272\u8868\u73b0\u80fd\u72e0\u72e0\u523a\u6fc0\u5230\u672a\u6765\u7684\u534e\u590f\u7535\u5b50\uff0c\u6ca1\u6709\u4eba\u8d76\u4e00\u97ad\u5b50\uff0c\u5f88\u96be\u8bf4\u80fd\u8ba9\u8499\u773c\u7684\u76f2\u9a74\u6b65\u4f10\u8d70\u591a\u5feb\uff0c\u201d\u5f90\u5b66\u5e73\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u4e5f\u7406\u89e3\u5f53\u524d\u7684\u7ecf\u6d4e\u4f53\u5236\u6539\u9769\u8fdb\u7a0b\uff0c\u6b65\u5b50\u8981\u4e00\u6b65\u4e00\u6b65\u7684\u53bb\u8fc8\u3002\u9526\u6e56\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u8ba1\u5212\uff0c\u6b65\u4f10\u5c31\u6bd4\u8f83\u5927\uff0c\u73b0\u5728\u8fd8\u6ca1\u6709\u6345\u5230\u4e2d\u592e\u6765\uff0c\u4e0d\u8fc7\u5c31\u7b97\u6345\u5230\u4e2d\u592e\u6765\uff0c\u5206\u6b67\u8fd8\u4f1a\u5f88\u5feb\u3002\u6536\u8d2d\u8ba1\u5212\u5f97\u4e0d\u5230\u73b0\u6709\u653f\u7b56\u7684\u652f\u6301\uff0c\u4f46\u662f\u7ecf\u6d4e\u4f53\u5236\u6539\u9769\uff0c\u4ece\u6765\u90fd\u662f\u6478\u7740\u77f3\u5934\u8fc7\u6cb3\uff0c\u5e76\u4e0d\u662f\u8bf4\u6ca1\u6709\u5148\u4f8b\u5c31\u4e0d\u53ef\u4ee5\u505a\u7684\uff0c\u4e0d\u8fc7\u6668\u66e6\u7eb8\u4e1a\u8fd8\u662f\u5c5e\u4e8e\u6c5f\u5357\u7701\u7684\u5730\u65b9\u4f01\u4e1a\uff0c\u6700\u7ec8\u7684\u51b3\u5b9a\u6743\u8fd8\u5728\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u90a3\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u518d\u8fc7\u4e24\u5e74\uff0c\u9526\u6e56\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u5c31\u4e0d\u4f1a\u9047\u5230\u4f53\u5236\u4e0a\u7684\u969c\u788d\uff0c\u8fd9\u65f6\u5019\u5374\u8981\u4f9d\u8d56\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u9886\u5bfc\u7684\u4e2a\u4eba\u51b3\u65ad\u4e86\u3002\u76f8\u5173\u5de5\u4f5c\u8fd8\u5728\u79d8\u5bc6\u8fdb\u884c\u4e2d\u3002</p><p>\u3000\u3000\u591c\u91cc\uff0c\u5f20\u606a\u5c31\u4f4f\u5728\u5f90\u5b66\u5e73\u5bb6\uff0c\u6b21\u65e5\u4e0b\u5348\uff0c\u4e5f\u5c31\u56db\u6708\u516b\u65e5\u7684\u4e0b\u5348\uff0c\u4fe1\u606f\u4ea7\u4e1a\u90e8\u5411\u5916\u516c\u5e03\u6d88\u606f\uff0c\u5ba3\u5e03\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u6b63\u5f0f\u6302\u724c\u6210\u7acb\u3002</p><p>\u3000\u3000\u4e3a\u54cd\u5e94\u653f\u5e9c\u63a8\u52a8\u653f\u4f01\u4e1a\u5206\u79bb\u3001\u8fdb\u4e00\u6b65\u52a0\u5f3a\u56fd\u6709\u4f01\u4e1a\u7ade\u4e89\u529b\u7684\u6307\u5bfc\u601d\u60f3\uff0c\u65b0\u6210\u7acb\u7684\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u5c06\u628a\u539f\u7535\u5b50\u5de5\u4e1a\u90e8\u3001\u90ae\u7535\u90e8\u65d7\u4e0b\u7cfb\u7edf\u88c5\u5907\u4e0e\u7cfb\u7edf\u5de5\u7a0b\u4f01\u4e1a\u3001\u8f6f\u4ef6\u4f01\u4e1a\u4ee5\u53ca\u76f8\u5173\u79d1\u7814\u9662\u6240\u5171\u5341\u4e00\u5bb6\u673a\u6784\u5355\u4f4d\u5e76\u5165\u65d7\u4e0b\uff0c\u603b\u8d44\u4ea7\u8d85\u8fc7\u4e8c\u767e\u56db\u5341\u4ebf\uff0c\u845b\u5efa\u5fb7\u51fa\u4efb\u96c6\u56e2\u8463\u4e8b\u957f\u3001\u603b\u7ecf\u7406\u517c\u515a\u7ec4\u4e66\u8bb0\u3002</p><p>\u3000\u3000\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4e3a\u6b64\u4e3e\u529e\u7684\u6210\u7acb\u5927\u4f1a\u3001\u8bb0\u8005\u62db\u5f85\u4f1a\u5e76\u975e\u5b9e\u65f6\u76f4\u64ad\uff0c\u5177\u4f53\u7684\u65b0\u95fb\u6d88\u606f\u8fd8\u8981\u7b49\u5230\u5404\u5bb6\u65b0\u95fb\u5a92\u4f53\u5b89\u6392\u9002\u5f53\u7684\u65f6\u95f4\u64ad\u51fa\u6216\u520a\u767b\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eba\u65e2\u7136\u5728\u5317\u4eac\uff0c\u5c31\u7279\u610f\u8d76\u5230\u534e\u590f\u7535\u5b50\u53ec\u5f00\u7684\u8bb0\u8005\u4f1a\u73b0\u573a\u53bb\u770b\u845b\u5efa\u5fb7\u7684\u5f97\u610f\u6837\u3002</p><p>\u3000\u3000\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u65b0\u8fd1\u7b79\u5efa\uff0c\u5e76\u65e0\u5355\u72ec\u7684\u529e\u516c\u697c\uff0c\u800c\u662f\u501f\u7528\u6210\u5458\u4f01\u4e1a\u534e\u590f\u7535\u5b50\u7cfb\u7edf\u5de5\u7a0b\u603b\u516c\u53f8\u5728\u5317\u4eac\u4e1c\u57ce\u533a\u4e1c\u76f4\u95e8\u5185\u5927\u8857\u7684\u529e\u516c\u5730\u3002</p><p>\u3000\u3000\u8d76\u5230\u6210\u7acb\u5927\u4f1a\u7684\u73b0\u573a\uff0c\u697c\u524d\u53f0\u9636\u4e0a\u4e0b\u904d\u5730\u90fd\u662f\u5c0f\u97ad\u70ae\u7684\u7eb8\u5c51\uff0c\u60f3\u5fc5\u662f\u521a\u624d\u4e3a\u534e\u590f\u7535\u5b50\u7684\u6210\u7acb\u72e0\u72e0\u7684\u5e86\u795d\u8fc7\uff0c\u8fd9\u65f6\u5019\u5230\u8bb0\u8005\u62db\u5f85\u4f1a\u73af\u8282\u3002\u845b\u5efa\u5fb7\u4e3a\u534e\u590f\u7535\u5b50\u7684\u6210\u7acb\u5927\u4f1a\u7cbe\u5fc3\u51c6\u5907\u4e86\u4e00\u6bb5\u65f6\u95f4\uff0c\u9080\u8bf7\u4e86\u8bb8\u591a\u5609\u5bbe\uff0c\u9664\u4e86\u4fe1\u606f\u4ea7\u4e1a\u90e8\u7684\u8bf8\u591a\u9ad8\u5b98\u4e4b\u5916\uff0c\u8fd8\u9080\u8bf7\u4e86\u8bb8\u591a\u6d77\u5916\u7535\u5b50\u5382\u5546\u9a7b\u4eac\u673a\u6784\u7684\u4ee3\u8868\uff0c\u5f20\u606a\u8ba4\u8bc6\u7684\u5c31\u6709\u4e09\u661f\u9a7b\u534e\u516c\u53f8\u4e13\u52a1\u674e\u5728\u6d19\u4ee5\u53ca\u7d22\u5c3c\u9a7b\u534e\u516c\u53f8\u603b\u88c1\u6e21\u8fb9\u656c\u4e00\uff0c\u8c22\u5bb6\u4ee5\u53ca\u6d77\u7c9f\u79d1\u6280\u5173\u8054\u7684\u673a\u6784\u5012\u6ca1\u6709\u6d3e\u4eba\u8d76\u5230\u5317\u4eac\u6765\u53c2\u52a0\u534e\u590f\u7535\u5b50\u7684\u8fd9\u4e2a\u6210\u7acb\u5927\u4f1a\u3002\u5f20\u606a\u867d\u7136\u6ca1\u6709\u53d7\u5230\u9080\u8bf7\uff0c\u4f46\u662f\u4ed6\u501f\u7740\u4e0e\u6613\u4e91\u98de\u7684\u5173\u7cfb\uff0c\u6df7\u8fdb\u4f1a\u573a\u5e76\u4e0d\u96be\uff0c\u60f3\u6765\u845b\u5efa\u5fb7\u770b\u5230\u4ed6\u4e5f\u4e0d\u4f1a\u6beb\u65e0\u98ce\u5ea6\u8d76\u4ed6\u51fa\u4f1a\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u8d76\u5230\u53ec\u5f00\u8bb0\u8005\u4f1a\u7684\u5927\u5385\uff0c\u845b\u5efa\u5fb7\u6b63\u5c31\u534e\u590f\u7535\u5b50\u6210\u7acb\u4e8b\u7b54\u73b0\u573a\u8bb0\u8005\u95ee\uff0c\u5f20\u606a\u6ca1\u6709\u542c\u5230\u8bb0\u8005\u95ee\u7684\u662f\u4ec0\u4e48\u95ee\u9898\uff0c\u4ed6\u4eec\u8fdb\u53bb\uff0c\u845b\u5efa\u5fb7\u6b63\u4f83\u4f83\u800c\u8c08\uff1a\u201c\u2026\u2026\u5bf9\u4e8e\u6709\u7740\u91cd\u5408\u4e1a\u52a1\u7684\u6210\u5458\u4f01\u4e1a\uff0c\u6211\u4eec\u4e5f\u662f\u9f13\u52b1\u6210\u5458\u4f01\u4e1a\u4e4b\u95f4\u5728\u5e02\u573a\u4e0a\u7684\u76f8\u4e92\u7ade\u4e89\uff0c\u96c6\u56e2\u603b\u90e8\u7edd\u5bf9\u4f1a\u4e0d\u504f\u4e0d\u501a\u3002\u5f53\u7136\uff0c\u96c6\u56e2\u603b\u90e8\u66f4\u5e0c\u671b\u770b\u5230\u6210\u5458\u4f01\u4e1a\u80fd\u76f8\u4e92\u4fc3\u8fdb\uff0c\u5171\u540c\u6210\u957f\uff0c\u8d44\u6e90\u4e5f\u5e94\u66f4\u6709\u6548\u7684\u5f97\u5230\u6574\u5408\u2026\u2026\u81f3\u4e8e\u5982\u4f55\u6574\u5408\uff1f\u534e\u590f\u7535\u5b50\u5c06\u4e0e\u5730\u65b9\u653f\u5e9c\u5408\u4f5c\uff0c\u6210\u7acb\u4fa7\u91cd\u70b9\u4e0d\u540c\u7684\u51e0\u5bb6\u7535\u5b50\u5de5\u4e1a\u56ed\u6765\uff0c\u6bd4\u5982\u8bf4\u4e0e\u91d1\u5c71\u5e02\u653f\u5e9c\u6210\u7acb\u7535\u5b50\u5de5\u4e1a\u56ed\uff0c\u4ee5\u6b64\u6765\u5b8c\u6210\u534e\u590f\u7535\u5b50\u65d7\u4e0b\u4e00\u90e8\u5206\u6210\u5458\u4f01\u4e1a\u751f\u4ea7\u8d44\u6e90\u7684\u6574\u5408\u4e0e\u76f8\u4e92\u652f\u6301\u2026\u2026\u201d</p></article>"}}}]);
