(window.webpackJsonp=window.webpackJsonp||[]).push([[987],{1010:function(p){p.exports={chapterTitle:null,title:"\u7b2c80\u7ae0 \u8d44\u91d1\u7684\u89e3\u51b3\u65b9\u6848",content:"<article><h2>\u7b2c80\u7ae0 \u8d44\u91d1\u7684\u89e3\u51b3\u65b9\u6848</h2><p>\u3000\u3000\u770b\u7740\u53f6\u5efa\u658c\u4ed6\u4eec\u7684\u8f66\u79bb\u5f00\uff0c\u8bb8\u601d\u4e0e\u8c22\u665a\u6674\u7a81\u7136\u5bf9\u7b11\u8d77\u6765\uff0c\u8c22\u665a\u6674\u6342\u7740\u8170\u773c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u8fd8\u62c5\u5fc3\u600e\u4e48\u8ddf\u53f6\u5efa\u658c\u89e3\u91ca\u4f60\u7684\u5e74\u9f84\u95ee\u9898\u5462\uff0c\u6ca1\u60f3\u5230\uff0c\u4ed6\u4eec\u5b8c\u5168\u6ca1\u6709\u610f\u8bc6\u5230\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u770b\u4ed6\u6bcf\u56de\u53ea\u7a7f\u8fd9\u79cd\u8001\u6c14\u6a2a\u79cb\u7684\u8863\u670d\uff0c\u8fd8\u603b\u559c\u6b22\u677f\u7740\u8138\uff0c\u5634\u5507\u62bf\u7740\uff0c\u773c\u775b\u559c\u6b22\u76ef\u7740\u4eba\u770b\uff0c\u8c01\u8981\u80fd\u770b\u51fa\u4ed6\u7684\u5e74\u9f84\uff0c\u624d\u53eb\u6709\u9b3c\u5462\uff1f\u201d\u8bb8\u601d\u7c89\u8138\u7709\u773c\u7b11\u5a9a\u5982\u82b1\uff0c\u201c\u6211\u90fd\u8bb0\u4e0d\u5f97\u521a\u8ba4\u8bc6\u4ed6\u7684\u6837\u5b50\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd8\u8bb0\u5f97\uff0c\u5fd8\u4e0d\u4e86\uff0c\u5f53\u65f6\u5f20\u606a\u51b2\u51fa\u53bb\u4ece\u8f66\u8f6e\u4e0b\u62b1\u51fa\u82b7\u5f64\u7684\u6837\u5b50\uff0c\u6211\u600e\u4e48\u4e5f\u5fd8\u4e0d\u4e86\u3002\u201d\u8c22\u665a\u6674\u655b\u7740\u7b11\uff0c\u4f38\u624b\u633d\u7740\u8bb8\u601d\u7684\u80f3\u818a\u8098\u513f\uff0c\u201c\u8bd5\u8bd5\u4f60\u4eec\u7684\u65b0\u8f66\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6123\u4e86\u6123\uff0c\u5f20\u606a\u4e4b\u524d\u6ca1\u8ddf\u5979\u63d0\u8d77\u4ed6\u4ece\u8f66\u8f6e\u4e0b\u6551\u8d77\u82b7\u5f64\u7684\u4e8b\uff0c\u89c1\u8c22\u665a\u6674\u5f88\u4ece\u5bb9\uff0c\u5dee\u4e0d\u591a\u4ece\u8f66\u7978\u7684\u60b2\u4f24\u4e2d\u6446\u8131\u51fa\u6765\uff0c\u53ea\u662f\u82b7\u5f64\u8fd8\u4e0d\u80af\u5f00\u53e3\u8bf4\u8bdd\uff0c\u6ca1\u6709\u60f3\u5230\u4f1a\u662f\u5f20\u606a\u4ece\u8f66\u8f6e\u4e0b\u6551\u51fa\u82b7\u5f64\uff0c\u96be\u9053\u8c22\u665a\u6674\u5bf9\u5f20\u606a\u662f\u8fd9\u6837\u7684\u4fe1\u4efb\u4e0e\u7eb5\u5bb9\uff1b\u5f20\u606a\u5012\u6ca1\u6709\u8f9c\u8d1f\u8c22\u665a\u6674\u7684\u4fe1\u4efb\u4e0e\u7eb5\u5bb9\u3002</p><p>\u3000\u3000\u65b0\u5149\u9020\u7eb8\u5382\u8fd9\u4e8b\uff0c\u773c\u4e0b\u8fd8\u770b\u4e0d\u51fa\u4ec0\u4e48\u7709\u76ee\uff0c\u5173\u952e\u662f\u4e0d\u6653\u5f97\u5f20\u606a\u5fc3\u91cc\u5230\u5e95\u6709\u600e\u6837\u7684\u6253\u7b97\u3002</p><p>\u3000\u3000\u65b0\u8f66\u91cc\u90fd\u662f\u76ae\u9769\u5473\uff0c\u8bb8\u601d\u5f00\u7740\u65b0\u8f66\u5728\u4e1c\u6d77\u5609\u5bcc\u7684\u505c\u8f66\u573a\u5916\u8bd5\u4e86\u8bd5\u624b\uff0c\u5f20\u606a\u53d7\u4e0d\u4e86\u76ae\u9769\u5473\uff0c\u7ad9\u5728\u505c\u8f66\u573a\u7684\u8fb9\u4e0a\uff0c\u8ddf\u8c22\u665a\u6674\u8bf4\u627f\u5305\u9020\u7eb8\u5382\u7684\u4e8b\u60c5\uff1a\u201c\u5341\u5e74\u7684\u627f\u5305\u671f\u9650\uff0c\u76c8\u5229\u4e0e\u57ce\u5357\u533a\u653f\u5e9c\u56db\u516d\u5206\u6210\uff0c\u8425\u4e1a\u7a0e\u662f\u5730\u65b9\u8d22\u653f\u7684\u4e3b\u8981\u6536\u5165\uff0c\u5f88\u96be\u51cf\u514d\uff0c\u4f01\u4e1a\u6240\u5f97\u7a0e\u7b49\u5176\u4ed6\u5730\u65b9\u7a0e\u8d39\u4e09\u5e74\u5168\u989d\u51cf\u514d\uff0c\u4e94\u5e74\u51cf\u534a\u5f81\u6536\uff0c\u5728\u627f\u5305\u671f\u5185\u516c\u53f8\u8fd8\u6709\u4f18\u5148\u6536\u8d2d\u9020\u7eb8\u5382\u8d44\u4ea7\u7684\u6743\u9650\uff0c\u6761\u4ef6\u90fd\u8fd8\u4e0d\u9519\uff0c\u5c31\u7b49\u65b0\u516c\u53f8\u6ce8\u518c\u5b8c\u6210\uff0c\u5c31\u8ba2\u7acb\u627f\u5305\u7ecf\u8425\u5408\u540c\u3002\u4e4b\u524d\u8ba9\u8521\u59d0\u5c06\u65b0\u5149\u9020\u7eb8\u5382\u7684\u8be6\u7ec6\u8d44\u6599\uff0c\u5e26\u4e86\u4e00\u4efd\u7ed9\u4f60\uff0c\u8001\u5382\u80af\u5b9a\u8981\u5173\u505c\uff0c\u65b0\u5382\u7684\u8bbe\u5907\u4e0e\u5de5\u827a\uff0c\u80fd\u751f\u4ea7\u56fd\u5185\u6025\u9700\u7684\u4e2d\u9ad8\u6863\u7eb8\u54c1\uff0c\u5bf9\u539f\u6750\u6599\u7eb8\u6d46\u7684\u8981\u6c42\u4e5f\u5f88\u9ad8\uff0c\u76ee\u524d\u53ea\u6709\u8fdb\u53e3\u63ba\u5408\u7eb8\u6d46\u80fd\u6ee1\u8db3\u8981\u6c42\uff0c\u80fd\u4e0d\u80fd\u8ba9\u6d77\u88d5\u516c\u53f8\u4ee3\u4e3a\u8054\u7edc\u6d77\u5916\u8d38\u6613\u5546\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u6ca1\u6709\u95ee\u9898\uff0c\u4f46\u662f\u7ec4\u7ec7\u751f\u4ea7\u6240\u9700\u7684\u8d44\u91d1\u7f3a\u53e3\u6bd4\u8f83\u5927\uff0c\u8981\u5b9e\u5168\u4ea7\u80fd\u7684\u6295\u4ea7\uff0c\u8fd8\u9700\u8981\u4e00\u5343\u591a\u4e07\u7684\u8d44\u91d1\uff0c\u4f60\u6253\u7b97\u600e\u4e48\u89e3\u51b3\uff1f\u201d</p><p>\u3000\u3000\u201c\u8f66\u5230\u5c71\u524d\u5fc5\u6709\u8def\uff0c\u773c\u524d\u4e5f\u7740\u6025\u4e0d\u6765\uff0c\u6211\u53ef\u4e0d\u60f3\u518d\u8ddf\u6b63\u6cf0\u96c6\u56e2\u5408\u4f5c\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u7b11\u4e86\u7b11\uff1a\u201c\u4eca\u5e74\u7684\u901a\u8d27\u81a8\u80c0\u7387\u5f88\u5feb\uff0c\u592e\u884c\u6536\u7f29\u94f6\u6839\uff0c\u73b0\u5728\u5df2\u7ecf\u5feb\u5230\u5e74\u5c3e\u4e86\uff0c\u94f6\u884c\u4e00\u5e74\u7684\u8d37\u6b3e\u989d\u5ea6\u4e5f\u65e9\u5c31\u7528\u5149\u4e86\uff0c\u4e5f\u53ea\u6709\u5927\u516c\u53f8\u624d\u6709\u5145\u88d5\u7684\u8d44\u91d1\uff0c\u4f60\u80fd\u81ea\u5df1\u60f3\u5230\u529e\u6cd5\uff0c\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u6211\u8003\u8651\u4e86\u4e00\u4e9b\uff0c\u539f\u6599\u7eb8\u6d46\u7531\u6d77\u88d5\u516c\u53f8\u4ee5\u4fe1\u7528\u8bc1\u7684\u5f62\u5f0f\u4e0e\u6d77\u5916\u8d38\u6613\u5546\u8fdb\u884c\u4ea4\u6613\uff0c\u6709\u4e09\u4e2a\u6708\u5230\u534a\u5e74\u7684\u7f13\u51b2\u671f\uff0c\u800c\u9020\u7eb8\u5382\u65b0\u5382\u7684\u5168\u8d1f\u8377\u751f\u4ea7\u5468\u671f\u53ea\u9700\u8981\u4e00\u4e2a\u534a\u6708\uff0c\u53ea\u8981\u65f6\u673a\u6390\u597d\uff0c\u5b8c\u5168\u53ef\u4ee5\u5229\u7528\u4fe1\u7528\u8bc1\u7684\u65f6\u95f4\u5dee\uff0c\u89e3\u51b3\u539f\u6599\u91c7\u8d2d\u8d44\u91d1\u7684\u95ee\u9898\uff0c\u5c31\u7b97\u5404\u5bb6\u94f6\u884c\u4eca\u5e74\u7684\u4fe1\u8d37\u989d\u5ea6\u90fd\u7528\u5149\uff0c\u6d77\u88d5\u516c\u53f8\u5411\u94f6\u884c\u5f00\u4e00\u5f20\u4e00\u5343\u4e07\u989d\u5ea6\u7684\u4fe1\u7528\u8bc1\uff0c\u5e94\u8be5\u4e0d\u6210\u4ec0\u4e48\u95ee\u9898\u3002\u53bb\u6389\u539f\u6599\u91c7\u8d2d\u8fd9\u4e00\u5927\u9879\uff0c\u65b0\u5382\u53ea\u9700\u8981\u4e09\u56db\u767e\u4e07\u7684\u8d44\u91d1\u5c31\u80fd\u6295\u4ea7\uff0c\u5e94\u8be5\u6ca1\u6709\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u4fe1\u7528\u8bc1\u652f\u4ed8\u65b9\u5f0f\uff0c\u662f\u94f6\u884c\u53c2\u4e0e\u56fd\u9645\u8d38\u6613\u7ed3\u7b97\u4e00\u79cd\u4e3b\u8981\u4ed8\u6b3e\u65b9\u5f0f\u3002\u5bf9\u6d77\u5916\u8d38\u6613\u5546\u800c\u8a00\uff0c\u8fd9\u79cd\u65b9\u5f0f\u63d0\u4f9b\u53ef\u9760\u7684\u4ed8\u6b3e\u4eba\u2014\u2014\u94f6\u884c\uff0c\u4ed6\u53ea\u8981\u4e25\u683c\u6309\u5408\u7ea6\u51c6\u65f6\u8fd0\u51fa\u539f\u6599\u7eb8\u6d46\uff0c\u5c31\u80fd\u5728\u4fe1\u7528\u8bc1\u5230\u671f\u4e4b\u65f6\u662f\u4ece\u94f6\u884c\u53d6\u5f97\u8d37\u6b3e\u3002</p><p>\u3000\u3000\u6d77\u88d5\u516c\u53f8\u4f5c\u4e3a\u6d77\u5916\u8d38\u6613\u5546\u4e0e\u65b0\u5149\u9020\u7eb8\u5382\u7684\u4e2d\u95f4\u8d38\u6613\u5546\uff0c\u8981\u57ab\u4ed8\u8d44\u91d1\u505a\u6210\u4e70\u5356\uff0c\u4f1a\u9020\u6210\u81ea\u8eab\u7684\u8d44\u91d1\u7d27\u5f20\uff0c\u53ea\u9700\u8981\u76f8\u719f\u7684\u94f6\u884c\u5f00\u5177\u4e00\u5b9a\u6570\u989d\u7684\u4fe1\u7528\u8bc1\uff0c\u5c31\u80fd\u5b8c\u6210\u4ea4\u6613\uff0c\u4f7f\u539f\u6599\u7eb8\u6d46\u8fd0\u62b5\u56fd\u5185\u6e2f\u53e3\u3002\u751a\u81f3\u4e0d\u9700\u8981\u6d77\u88d5\u516c\u53f8\u51fa\u9762\uff0c\u53ea\u8981\u63d0\u4f9b\u5177\u4f53\u7684\u7968\u636e\u4e0e\u5355\u8bc1\uff0c\u5c31\u80fd\u4e0e\u65b0\u5149\u9020\u7eb8\u5382\u5b8c\u6210\u4e8c\u6b21\u8f6c\u624b\u4ea4\u6613\uff0c\u65b0\u5149\u9020\u7eb8\u5382\u83b7\u5f97\u539f\u6599\u7eb8\u6d46\uff0c\u7ec4\u7ec7\u751f\u4ea7\u3001\u9500\u552e\uff0c\u56de\u7b3c\u8d44\u91d1\uff0c\u53ea\u9700\u8981\u5728\u4fe1\u7528\u8bc1\u5230\u671f\u4e4b\u524d\uff0c\u5c06\u8d37\u6b3e\u652f\u4ed8\u7ed9\u94f6\u884c\uff0c\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u4fe1\u7528\u8bc1\u6709\u6548\u671f\u9650\u901a\u5e38\u4e3a\u4e09\u4e2a\u6708\u5230\u534a\u5e74\uff0c\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\uff0c\u5305\u62ec\u539f\u6599\u6d46\u8fd0\u8f93\u5230\u56fd\u5185\u3001\u7ec4\u7ec7\u751f\u4ea7\u3001\u9500\u552e\u56de\u6b3e\uff0c\u4ee5\u9500\u552e\u56de\u6b3e\u652f\u4ed8\u6700\u521d\u7684\u8d27\u6b3e\uff0c\u6240\u6709\u7684\u65f6\u673a\u90fd\u8981\u7b79\u5212\u7684\u76f8\u5f53\u7cbe\u51c6\u624d\u80fd\u6700\u7ec8\u7684\u6210\u529f\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u4e0d\u6653\u5f97\u5f20\u606a\u4ece\u54ea\u91cc\u5b66\u6765\u8fd9\u4e48\u4e30\u5bcc\u7684\u91d1\u878d\u8d38\u6613\u65b9\u9762\u7684\u77e5\u8bc6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u65b9\u5f0f\u7406\u8bba\u4e0a\u80fd\u89e3\u51b3\u539f\u6599\u8d27\u6b3e\u7684\u95ee\u9898\uff0c\u53ea\u662f\u64cd\u4f5c\u8d77\u6765\u96be\u5ea6\u6709\u4e9b\u5927\uff0c\u5404\u65b9\u9762\u65f6\u673a\u8981\u914d\u5408\u597d\uff0c\u51fa\u4e86\u7eb0\u6f0f\u53ef\u4e0d\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e3b\u8981\u770b\u6d77\u88d5\u516c\u53f8\u7684\u8868\u73b0\u4e86\uff0c\u4ece\u73b0\u5728\u8d77\u5c31\u8981\u79ef\u6781\u5bfb\u627e\u6d77\u5916\u539f\u6599\u7eb8\u6d46\u4f9b\u5e94\u5546\uff0c\u7b49\u65b0\u5149\u9020\u7eb8\u5382\u7684\u51c6\u5907\u5de5\u4f5c\u4e00\u5c31\u7eea\uff0c\u5c31\u8fdb\u884c\u79bb\u5cb8\u4ea4\u6613\uff1b\u8fd8\u6709\u4e00\u70b9\uff0c\u7b7e\u8ba2\u5408\u7ea6\u65f6\uff0c\u5c3d\u53ef\u80fd\u5c06\u4fe1\u7528\u8bc1\u7684\u671f\u9650\u5ef6\u957f\uff0c\u8ba9\u6211\u4eec\u6709\u5145\u88d5\u7684\u65f6\u95f4\u5dee\u3002\u5f53\u7136\uff0c\u5c31\u7b97\u6709\u4ec0\u4e48\u7eb0\u6f0f\uff0c\u65f6\u95f4\u5df2\u7ecf\u62d6\u5230\u660e\u5e74\uff0c\u8d44\u91d1\u8fd8\u6709\u7f3a\u53e3\u7684\u8bdd\uff0c\u4e5f\u4e0d\u4f1a\u592a\u5927\uff0c\u603b\u6709\u529e\u6cd5\u5f25\u8865\u4e0a\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6211\u5c31\u5e2e\u4f60\u8d1f\u8d23\u539f\u6599\u7eb8\u6d46\u8fd9\u4e00\u5757\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u7b14\u505a\u6210\u4e86\uff0c\u6d77\u88d5\u516c\u53f8\u53ef\u4ee5\u5411\u8fdb\u51fa\u53e3\u8f6c\u624b\u8d38\u6613\u65b9\u5411\u53d1\u5c55\uff0c\u5730\u533a\u6e20\u9053\u90a3\u4e00\u5757\uff0c\u5c31\u53ef\u4ee5\u5b8c\u5168\u79fb\u5165\u6d77\u6cf0\u516c\u53f8\uff1b\u4f5c\u4e3a\u6743\u5b9c\u4e4b\u8ba1\uff0c\u65b0\u5149\u9020\u7eb8\u5382\u7684\u4ea7\u54c1\u9500\u552e\u53ef\u4ee5\u6682\u65f6\u7684\u5168\u6743\u59d4\u6258\u6d77\u6cf0\u8d38\u6613\uff0c\u80fd\u63d0\u524d\u4e00\u5230\u4e24\u4e2a\u6708\u8fdb\u884c\u9500\u552e\u51c6\u5907\u7684\u8bdd\uff0c\u4ee5\u76ee\u524d\u56fd\u5185\u5bf9\u4e2d\u9ad8\u6863\u7eb8\u54c1\u7684\u9700\u6c42\uff0c\u9500\u552e\u56de\u6b3e\u4e0d\u5b58\u5728\u592a\u5927\u7684\u95ee\u9898\uff0c\u4f55\u51b5\u6d77\u6cf0\u8d38\u6613\u8d26\u4e0a\u8fd8\u6709\u4e00\u4e9b\u8d44\u91d1\uff0c\u5b8c\u5168\u53ef\u4ee5\u5148\u884c\u57ab\u4ed8\uff01\u201d</p><p>\u3000\u3000\u56fd\u5185\u4e2d\u9ad8\u6863\u7eb8\u54c1\u7684\u9700\u6c42\u7f3a\u53e3\u5f88\u5927\uff0c\u6574\u4e2a\u65b9\u6848\u4e2d\u9500\u552e\u56de\u6b3e\u65f6\u95f4\u8fd9\u4e00\u70b9\uff0c\u4e5f\u662f\u6700\u4e0d\u53ef\u63a7\u5236\u7684\u4e00\u70b9\uff0c\u4e00\u65e6\u7531\u6d77\u6cf0\u8d38\u6613\u53c2\u4e0e\u8fd9\u4e00\u73af\u8282\uff0c\u76f8\u5bf9\u65b0\u5149\u9020\u7eb8\u5382\u6765\u8bf4\uff0c\u6240\u627f\u62c5\u7684\u9500\u552e\u98ce\u9669\u5c31\u80fd\u964d\u5230\u6700\u4f4e\uff0c\u4f7f\u5f97\u5f20\u606a\u63d0\u51fa\u7684\u8fd9\u4e2a\u89e3\u51b3\u65b9\u6848\uff0c\u6709\u5f88\u9ad8\u7684\u53ef\u6267\u884c\u6027\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u8c22\u665a\u6674\u5fc3\u60c5\u4e5f\u5f88\u632f\u594b\u3002\u8981\u8bf4\u8d44\u6e90\u7684\u5229\u7528\uff0c\u5f20\u606a\u53ef\u662f\u5c06\u4ed6\u624b\u91cc\u7684\u8d44\u6e90\u90fd\u7528\u5230\u6781\u81f4\u4e86\u3002\u627f\u5305\u7ecf\u8425\u65b0\u5149\u9020\u7eb8\u5382\uff0c\u5c06\u6d77\u88d5\u516c\u53f8\u3001\u6d77\u6cf0\u8d38\u6613\u7684\u8d44\u6e90\u90fd\u5145\u5206\u5229\u7528\u8d77\u6765\uff0c\u5c06\u539f\u6599\u91c7\u8d2d\u3001\u4ea7\u54c1\u9500\u552e\u8fd9\u4e24\u4e2a\u76f8\u5bf9\u6765\u8bf4\u77ed\u65f6\u95f4\u5185\u96be\u4ee5\u628a\u63e1\u7684\u73af\u8282\uff0c\u5254\u9664\u5728\u65b0\u5149\u9020\u7eb8\u5382\u4e4b\u5916\uff0c\u7531\u6d77\u88d5\u516c\u53f8\u3001\u6d77\u6cf0\u8d38\u6613\u5206\u62c5\uff0c\u5316\u89e3\u6574\u4f53\u8fd0\u4f5c\u7684\u96be\u5ea6\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u6d77\u88d5\u516c\u53f8\u3001\u6d77\u6cf0\u8d38\u6613\u5374\u4e5f\u6709\u6781\u5927\u597d\u5904\uff0c\u6d77\u88d5\u516c\u53f8\u4e0e\u6d77\u6cf0\u8d38\u6613\u5728\u6e20\u9053\u8d38\u6613\u4e1a\u52a1\u4e0a\u6709\u6240\u62b5\u89e6\uff0c\u9700\u8981\u8fdb\u884c\u7ecf\u8425\u65b9\u5411\u7684\u8f6c\u578b\uff0c\u6d77\u6cf0\u8d38\u6613\u5728\u6570\u5b57\u624b\u673a\u4e4b\u5916\uff0c\u9700\u8981\u65b0\u7684\u4e1a\u52a1\u589e\u52a0\u70b9\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u73b0\u5728\u7a81\u7136\u5f88\u671f\u5f85\u770b\u5f20\u606a\u5982\u4f55\u89e3\u51b3\u6295\u4ea7\u6240\u5fc5\u9700\u7684\u542f\u52a8\u8d44\u91d1\u7f3a\u53e3\u95ee\u9898\u3002\u8981\u8bf4\u4e09\u56db\u767e\u4e07\u7684\u542f\u52a8\u8d44\u91d1\uff0c\u4e1c\u62fc\u897f\u51d1\uff0c\u4e5f\u4e0d\u4f1a\u592a\u56f0\u96be\uff0c\u4f46\u662f\u5f20\u606a\u7684\u8868\u73b0\u66f4\u8ba9\u4eba\u671f\u5f85\uff0c\u8c22\u665a\u6674\u62bf\u7740\u5634\uff0c\u770b\u7740\u5f20\u606a\u7a0d\u663e\u7a1a\u6c14\u7684\u8138\u5374\u6ee1\u662f\u575a\u6bc5\uff0c\u5b8c\u5168\u6ca1\u6709\u9047\u5230\u56f0\u96be\u7684\u60d1\u7136\u3002</p><p>\u3000\u3000\u7531\u4e8e\u4e2d\u996d\u803d\u6401\u592a\u957f\u7684\u65f6\u95f4\uff0c\u8bb8\u601d\u8fd8\u6ca1\u6709\u65f6\u95f4\u53bb\u4e1c\u6d77\u5927\u5b66\u770b\u671b\u59b9\u59b9\u8bb8\u7ef4\u3002</p><p>\u3000\u3000\u665a\u996d\u662f\u5728\u65b0\u6885\u82d1\u5403\u7684\uff0c\u8bb8\u601d\u7b2c\u4e00\u6b21\u8d70\u8fdb\u4e1c\u6d77\u7701\u8fd9\u5ea7\u5728\u5916\u4eba\u770b\u6765\u795e\u79d8\u83ab\u6d4b\u7684\u5c0f\u533a\uff0c\u4e1c\u6d77\u7701\u515a\u653f\u9886\u5bfc\u5927\u90fd\u4f4f\u5728\u65b0\u6885\u82d1\uff0c\u4e5f\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u4e3b\u7ba1\u4e01\u5411\u5c71\u6848\u4ef6\u5ba1\u7406\u7684\u7701\u653f\u6cd5\u59d4\u4e66\u8bb0\u5f90\u5b66\u5e73\u3002\u53ef\u4ee5\u770b\u5f97\u51fa\uff0c\u5f90\u5b66\u5e73\u4e00\u5bb6\u5bf9\u5f20\u606a\u7684\u559c\u7231\uff0c\u4e0d\u4ec5\u4ec5\u56e0\u4e3a\u5f20\u606a\u4ece\u8f66\u8f6e\u4e0b\u6551\u51fa\u82b7\u5f64\u3002</p><p>\u3000\u3000\u9910\u684c\u4e0a\uff0c\u5f90\u5b66\u5e73\u95ee\u8d77\u6d77\u6cf0\u8d38\u6613\u7684\u4e8b\u60c5\uff0c\u4ed6\u4ece\u8c22\u665a\u6674\u5634\u91cc\u77e5\u9053\u5f20\u606a\u53c2\u4e0e\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u76f8\u5bf9\u7231\u4eba\u5468\u53d4\u60e0\u62c5\u5fe7\u8fd9\u4e9b\u53ef\u80fd\u4f1a\u803d\u6401\u5f20\u606a\u7684\u5b66\u4e60\uff0c\u5f90\u5b66\u5e73\u53ef\u662f\u6301\u8d5e\u540c\u7684\u6001\u5ea6\uff1a\u201c\u6211\u5341\u4e00\u5c81\u5c31\u8fdb\u57ce\u5f53\u5b66\u5f99\uff0c\u89e3\u653e\u540e\u624d\u8fdb\u5b66\u6821\u8bfb\u4e66\uff0c\u56de\u60f3\u8d77\u6765\uff0c\u8fd8\u662f\u4e09\u5e74\u7684\u5b66\u5f92\u751f\u6daf\uff0c\u5bf9\u6211\u7684\u4eba\u751f\u6700\u6709\u76ca\u2026\u2026\u201d</p><p>\u3000\u3000\u9910\u684c\u4e0a\u8c08\u4e86\u5f88\u591a\u5b8f\u89c2\u7ecf\u6d4e\u7684\u8bdd\u9898\uff0c\u4e5d\u56db\u5e74\u56fd\u5185\u7ecf\u6d4e\u6700\u4e25\u91cd\u7684\u95ee\u9898\u8fd8\u662f\u5c45\u9ad8\u4e0d\u4e0b\u7684\u901a\u8d27\u81a8\u80c0\u7387\uff0c\u5982\u4f55\u5229\u7528\u5e02\u573a\u624b\u6bb5\u8c03\u63a7\u7ecf\u6d4e\uff0c\u964d\u4f4e\u901a\u8d27\u81a8\u80c0\u7387\u7684\u540c\u65f6\uff0c\u53c8\u4fdd\u6301\u7ecf\u6d4e\u7684\u5fc5\u8981\u589e\u957f\uff0c\u662f\u4ee4\u6240\u6709\u56fd\u5bb6\u9886\u5bfc\u4eba\u5934\u75bc\u7684\u95ee\u9898\u3002\u901a\u8d27\u81a8\u80c0\u95ee\u9898\u56f0\u6270\u56fd\u5185\u7ecf\u6d4e\u4e00\u76f4\u5230\u4e5d\u516d\u5e74\uff0c\u624d\u5b9e\u73b0\u6210\u529f\u7684\u8f6f\u7740\u9646\uff0c\u907f\u514d\u56fd\u5185\u7ecf\u6d4e\u5728\u4e5d\u4e03\u5e74\u7206\u53d1\u7684\u4e1c\u5357\u4e9a\u91d1\u878d\u5371\u673a\u4e2d\u906d\u53d7\u91cd\u521b\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba4\u4e3a\u6b64\u65f6\u8d1f\u8d23\u56fd\u5185\u91d1\u878d\u7ecf\u6d4e\u7684\u526f\u603b\u7406\u83b7\u5f97\u8bfa\u8d1d\u5c14\u7ecf\u6d4e\u5b66\u5956\u4e00\u70b9\u90fd\u4e0d\u8fc7\u5206\u3002</p><p>\u3000\u3000\u8eab\u4e3a\u7701\u653f\u6cd5\u59d4\u4e66\u8bb0\uff0c\u7a81\u7136\u5173\u5fc3\u8d77\u56fd\u5185\u7ecf\u6d4e\u5f62\u52bf\uff0c\u80cc\u540e\u7684\u610f\u4e49\u4e0d\u8a00\u81ea\u660e\uff0c\u4ece\u4e5d\u4e8c\u5e74\u8d77\uff0c\u7ecf\u6d4e\u5efa\u8bbe\u5f53\u4ec1\u4e0d\u8ba9\u7684\u6210\u4e3a\u56fd\u5185\u5efa\u8bbe\u7684\u91cd\u4e2d\u4e4b\u91cd\uff1b\u6b64\u524d\u5f90\u5b66\u5e73\u8fd8\u53c2\u52a0\u4e86\u4e00\u5c4a\u77ed\u671f\u7684\u7ecf\u6d4e\u7814\u4fee\u73ed\u3002</p><p>\u3000\u3000\u7531\u4e8e\u804c\u4e1a\u7684\u5173\u7cfb\uff0c\u5f20\u606a\u66f4\u5173\u6ce8\u6240\u5904\u884c\u4e1a\u7684\u53d1\u5c55\u52bf\u6001\uff0c\u5c5e\u4e8e\u5fae\u89c2\u7ecf\u6d4e\u8303\u7574\uff0c\u5bf9\u5b8f\u89c2\u7ecf\u6d4e\u6ca1\u6709\u592a\u6df1\u5165\u7684\u4e86\u89e3\uff0c\u4f46\u5bf9\u56fd\u5185\u4eca\u540e\u5341\u51e0\u5e74\u76f8\u5bf9\u6765\u8bf4\u8fd8\u7b97\u6210\u529f\u7684\u5b8f\u89c2\u7ecf\u6d4e\u8c03\u63a7\uff0c\u8fd8\u7b97\u6709\u521d\u6b65\u7684\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u867d\u7136\u6ca1\u6709\u66f4\u6df1\u5165\u7684\u8ba4\u8bc6\uff0c\u4f46\u662f\u8ba4\u8bc6\u7684\u65b9\u5411\u4e0a\uff0c\u5374\u7b26\u5408\u5f53\u524d\u7ecf\u6d4e\u5b66\u754c\u7684\u4e3b\u6d41\u89c2\u70b9\uff0c\u4e5f\u7b26\u5408\u5f90\u5b66\u5e73\u7684\u53e3\u80c3\u3002</p><p>\u3000\u3000\u591c\u91cc\u4f4f\u5728\u65b0\u6885\u82d1\uff0c\u8bb8\u601d\u4e0e\u8c22\u665a\u6674\u7167\u987e\u82b7\u5f64\uff0c\u5728\u540c\u4e00\u95f4\u623f\u91cc\u4f11\u606f\uff0c\u5f20\u606a\u966a\u5f90\u6c34\u5e73\u4e0b\u56f4\u68cb\uff0c\u7ee7\u7eed\u9910\u684c\u4e0a\u672a\u5b8c\u7684\u8bdd\u9898\uff0c\u4e00\u76f4\u5230\u51cc\u6668\u4e24\u70b9\uff0c\u6e34\u7761\u5f97\u76f4\u6253\u54c8\u6b20\uff0c\u624d\u56de\u623f\u6d17\u6f31\u7761\u89c9\u3002\u7b2c\u4e8c\u5929\u9192\u6765\u8fd8\u7b97\u65e9\uff0c\u4e0d\u8fc7\u5f90\u5b66\u5e73\u65e9\u65e9\u5c31\u79bb\u5f00\u5bb6\u95e8\u53bb\u4e86\u7701\u59d4\uff0c\u5fc3\u60f3\u5c06\u8fd1\u516d\u5341\u5c81\u7684\u8001\u4eba\uff0c\u4e5f\u771f\u4e0d\u7b80\u5355\uff0c\u53ef\u80fd\u4e00\u5929\u4f11\u606f\u7684\u65f6\u95f4\u90fd\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u516d\u4e2a\u5c0f\u65f6\uff0c\u51e0\u4e4e\u770b\u4e0d\u5230\u4ed6\u773c\u775b\u6709\u56f0\u987f\u7684\u75b2\u6001\u3002</p></article>"}}}]);
