(window.webpackJsonp=window.webpackJsonp||[]).push([[988],{1011:function(p){p.exports={chapterTitle:null,title:"\u7b2c985\u7ae0 \u9b4f\u4e1c\u5f3a\u7684\u79bb\u53bb",content:"<article><h2>\u7b2c985\u7ae0 \u9b4f\u4e1c\u5f3a\u7684\u79bb\u53bb</h2><p>\u3000\u3000\u201c\u9b4f\u4e1c\u5f3a\u79bb\u5f00\u4e1c\u5927\u4e86\uff1f\u201d\u542c\u5230\u79e6\u521a\u8bf4\u5230\u7684\u8fd9\u4e2a\u6d88\u606f\uff0c\u5f20\u606a\u8fd8\u662f\u9887\u611f\u610f\u5916\uff0c\u96be\u602a\u521a\u624d\u770b\u5230\u6797\u51b0\u8584\u65bd\u7c89\u9edb\u4e4b\u4e0b\u96be\u63a9\u6194\u60b4\uff1f\u6797\u51b0\u521a\u624d\u5c31\u5148\u56de\u53bb\u4f11\u606f\u4e86\uff0c\u6ca1\u6709\u7559\u4e0b\u6765\u51d1\u70ed\u95f9\u3002</p><p>\u3000\u3000\u8bf4\u8d77\u6765\uff0c\u5f20\u606a\u5bf9\u9b4f\u4e1c\u5f3a\u7684\u53bb\u7559\u8fd8\u771f\u4e0d\u5173\u5fc3\uff0c\u53ea\u662f\u8fd9\u4e9b\u5e74\u6765\u7ea0\u7f20\u9887\u6df1\u7684\u4e00\u4e2a\u4eba\u7269\u7a81\u7136\u5c31\u8df3\u4e86\u51fa\u53bb\uff0c\u5fc3\u91cc\u6709\u4e9b\u611f\u6168\u4e86\u3002</p><p>\u3000\u3000\u201c\u9b4f\u4e1c\u5f3a\u771f\u4e0d\u662f\u4e2a\u4e1c\u897f\uff0c\u201d\u4ee4\u5c0f\u71d5\u8d70\u8fc7\u6765\u8981\u8ddf\u79e6\u521a\u6253\u62db\u547c\uff0c\u542c\u5230\u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u9887\u4e3a\u6797\u51b0\u6253\u62b1\u4e0d\u5e73\uff0c\u201c\u4ed6\u8fdb\u5b66\u6821\u540e\u52e4\u516c\u53f8\u4e00\u76f4\u90fd\u4e0d\u5f97\u610f\uff0c\u4ed6\u7684\u5de5\u8d44\u672c\u6765\u5c31\u4f4e\uff0c\u4e4b\u524d\u5728\u5e74\u7ea7\u8fd8\u6709\u5c97\u4f4d\u6d25\u8d34\u6bd4\u8f83\u5bbd\u88d5\u2014\u2014\u8fd9\u65f6\u5019\u9664\u4e86\u4e2a\u4eba\u5f00\u9500\u5916\uff0c\u5c31\u6ca1\u6709\u591a\u5c11\u8282\u4f59\uff0c\u4ed6\u7684\u5bb6\u5883\u4e0d\u597d\uff0c\u4e0b\u9762\u6709\u4e2a\u59b9\u59b9\u8fd8\u8981\u6307\u671b\u4ed6\u5bc4\u94b1\u7f34\u5b66\u8d39\uff0c\u8fd8\u6709\u4ed6\u5988\u5988\u7684\u5173\u8282\u708e\uff0c\u5230\u51ac\u5929\u8981\u4e70\u836f\u6765\u7f13\u89e3\u75bc\u75db\u7684\uff0c\u6797\u51b0\u4e0d\u60f3\u4ed6\u8fd9\u4e48\u8f9b\u82e6\uff0c\u6bcf\u56de\u5e2e\u4ed6\u5bc4\u94b1\u56de\u5bb6\u65f6\u90fd\u8981\u5077\u5077\u7684\u518d\u6dfb\u4e9b\u94b1\u8fdb\u53bb\u3002\u4ed6\u4e00\u5e74\u591a\u6ca1\u6709\u56de\u8001\u5bb6\uff0c\u4e0a\u4e2a\u6708\u5e26\u7740\u6797\u51b0\u56de\u4e86\u4e00\u8d9f\u8001\u5bb6\uff0c\u77e5\u9053\u4e86\u8fd9\u4e8b\uff0c\u53ef\u601c\u7684\u81ea\u5c0a\u5fc3\u5c31\u4f5c\u602a\u4e86\uff0c\u5f53\u65f6\u5c31\u8ddf\u6797\u51b0\u5435\u4e86\u4e00\u67b6\uff0c\u6797\u51b0\u6c14\u5f97\u591c\u91cc\u8d70\u4e8c\u5341\u91cc\u5c71\u8def\u53bb\u53bf\u57ce\u5750\u8f66\u56de\u4e86\u5efa\u90ba\u2014\u2014\u8981\u662f\u6797\u51b0\u6709\u4e2a\u4e09\u957f\u4e24\u77ed\uff0c\u6211\u751f\u5241\u4e86\u4ed6\u7684\u5fc3\u90fd\u6709\u3002\u4ed6\u56de\u6765\u4e86\uff0c\u4e5f\u6ca1\u6709\u4e1d\u6beb\u9053\u6b49\u7684\u610f\u601d\uff0c\u8f9e\u804c\u3001\u5206\u624b\uff0c\u8bf4\u662f\u4e0d\u803d\u6401\u6797\u51b0\u7684\u524d\u7a0b\uff0c\u62cd\u62cd\u5c41\u80a1\u5c31\u8d70\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u79e6\u521a\u4e0d\u77e5\u9053\u9b4f\u4e1c\u5f3a\u8f9e\u804c\u80cc\u540e\u7684\u6545\u4e8b\uff0c\u542c\u4ee4\u5c0f\u71d5\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u8be7\u5f02\u7684\u5fae\u5f20\u7740\u5634\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6070\u597d\u8d70\u8fc7\u6765\u8981\u5e2e\u79e6\u521a\u592b\u5987\u5012\u9152\u6c34\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u54ac\u7259\u5207\u9f7f\uff0c\u6068\u4e0d\u5f97\u5c06\u9b4f\u4e1c\u5f3a\u7ed9\u751f\u5241\u4e86\u3002</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u5165\u4e86\u6b67\u9014\uff0c\u5f88\u96be\u633d\u6551\u4e86\u2014\u2014\u201d\u5f20\u606a\u6307\u4e86\u6307\u5fc3\u810f\u7684\u5730\u65b9\uff0c\u8f7b\u8f7b\u4e00\u53f9\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u5bf9\u6797\u51b0\u6765\u8bf4\uff0c\u672a\u5c1d\u4e0d\u662f\u597d\u4e8b\uff0c\u6539\u5929\u6211\u8ba9\u9526\u6e56\u7684\u672a\u5a5a\u672a\u604b\u9752\u5e74\u624d\u4fca\u6392\u7740\u961f\u6765\u7ed9\u6797\u51b0\u6311\uff0c\u6311\u4e0a\u8c01\u5c31\u8c01\uff0c\u6211\u5c31\u4e0d\u4fe1\u4e86\uff0c\u6211\u8fd8\u5305\u529e\u4e0d\u4e86\u4e00\u6869\u5a5a\u59fb\uff01\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5b59\u9759\u6aac\u90fd\u6251\u54e7\u7b11\u51fa\u58f0\uff0c\u5fcd\u4e0d\u4f4f\u4f38\u624b\u62fd\u4f4f\u4ed6\u7684\u8033\u6735\u634f\u7740\u8f7b\u626f\u4e86\u4e00\u4e0b\uff0c\u7b11\u9a82\u9053\uff1a\u201c\u86ee\u597d\u7684\u540c\u4ec7\u654c\u5ffe\u7684\u58eb\u6c14\u90fd\u8ba9\u4f60\u7ed9\u7834\u574f\u6389\u4e86\u2026\u2026\u201d\u7a81\u7136\u610f\u8bc6\u5230\u8fd9\u4e3e\u52a8\u8fc7\u4e8e\u4eb2\u6635\u4e86\u4e9b\uff0c\u4e0b\u610f\u8bc6\u7684\u56de\u5934\u770b\u5411\u5510\u5a67\uff0c\u6b63\u597d\u4e0e\u5510\u5a67\u7684\u89c6\u7ebf\u649e\u4e0a\uff0c\u5c31\u8ddf\u7ed9\u6349\u5978\u5728\u5e8a\u4f3c\u7684\u614c\u4e71\u907f\u5f00\u5510\u5a67\u7684\u89c6\u7ebf\uff0c\u5fd0\u5fd1\u4e0d\u5b89\u7684\u95ea\u5230\u5427\u53f0\u540e\u7ed9\u79e6\u521a\u592b\u5987\u9152\u6c34\u3002</p><p>\u3000\u3000\u770b\u5230\u5b59\u9759\u6aac\u7684\u614c\u4e71\u795e\u6001\uff0c\u5f20\u606a\u4e0d\u7528\u56de\u5934\u5c31\u77e5\u9053\u5510\u5a67\u6b63\u770b\u5411\u8fd9\u8fb9\uff0c\u5fc3\u91cc\u5728\u60b2\u9e23\uff1a\u505a\u8d3c\u4e0d\u8981\u5fc3\u865a\u554a\uff01\u4ed6\u5373\u4f7f\u611f\u89c9\u5230\u5510\u5a67\u8d70\u8fc7\u6765\uff0c\u8fd8\u662f\u4e0d\u52a8\u58f0\u8272\u7684\u95ee\u4ee4\u5c0f\u71d5\uff1a\u201c\u6211\u8bf4\u4f60\u4eca\u5929\u600e\u4e48\u4e0d\u966a\u6731\u5c0f\u519b\u53bb\u8ba4\u4e00\u8ba4\u672a\u6765\u7684\u4eb2\u621a\uff0c\u6562\u60c5\u4f60\u662f\u8981\u7559\u4e0b\u6765\u966a\u6797\u51b0\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u5728\u804a\u4ec0\u4e48\u5462\uff1f\u201d\u5510\u5a67\u4ece\u540e\u9762\u633d\u8fc7\u5f20\u606a\u7684\u624b\u81c2\uff0c\u8111\u888b\u8d34\u7740\u5f20\u606a\u7684\u80a9\u8180\u63a2\u8fc7\u6765\uff0c\u770b\u4e86\u770b\u4ed6\uff0c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u53ef\u6c14\u4e0d\u53ef\u6c14\uff0c\u201d\u5f20\u606a\u53c8\u58f0\u60c5\u5e76\u8302\u7684\u5c06\u6797\u51b0\u7684\u906d\u9047\u8ddf\u5510\u5a67\u8bf4\u4e86\u4e00\u904d\uff0c\u53c8\u52a0\u91cd\u8bed\u6c14\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u4eba\u8981\u504f\u6267\u8d77\u6765\u771f\u662f\u96be\u4ee5\u60f3\u8c61\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8fc7\u4ed6\u518d\u600e\u4e48\u6837\uff0c\u6797\u51b0\u59d0\u7ec8\u662f\u771f\u5fc3\u559c\u6b22\u4ed6\u7684\u5427\uff0c\u8fd9\u6837\u7edd\u60c5\u7684\u79bb\u5f00\uff0c\u6797\u51b0\u59d0\u4f1a\u66f4\u4f24\u5fc3\u5427\u2026\u2026\u201d\u5510\u5a67\u5374\u6ca1\u6709\u8bf4\u201c\u5206\u624b\u5012\u597d\u201d\u7684\u8bdd\u3002</p><p>\u3000\u3000\u542c\u7740\u5510\u5a67\u4e3a\u6797\u51b0\u611f\u4f24\u7684\u8bdd\uff0c\u5f20\u606a\u6709\u4e9b\u4e0d\u5b89\u6765\uff0c\u5fae\u4fa7\u8fc7\u8138\uff0c\u8138\u988a\u8d34\u5230\u5979\u7684\u989d\u5934\u4e0a\uff0c\u53c8\u4f38\u624b\u63c9\u4e86\u63c9\u7684\u8111\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u6797\u51b0\u4e0d\u662f\u8fd8\u6709\u8fd9\u4e48\u591a\u670b\u53cb\u4e0d\u662f\uff1f\u201d</p><p>\u3000\u3000\u4ed6\u8fd9\u8fb9\u8bf4\u7740\u8bdd\uff0c\u5b59\u9759\u6aac\u5c06\u8981\u7ed9\u79e6\u521a\u592b\u5987\u7684\u9152\u4e0e\u996e\u6599\u653e\u5427\u53f0\u4e0a\uff0c\u4eba\u505a\u8d3c\u5fc3\u865a\u7684\u65e9\u5c31\u6e9c\u5230\u4e00\u8fb9\u53bb\u4e86\uff0c\u5510\u5a67\u5012\u50cf\u662f\u5fd8\u4e86\u8fd9\u56de\u4e8b\uff0c\u633d\u7740\u5f20\u606a\u7684\u624b\u81c2\uff0c\u53c8\u5230\u522b\u5904\u8bf4\u8bdd\u53bb\u4e86\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u4e5f\u53ea\u5f53\u773c\u775b\u770b\u4e0d\u89c1\uff0c\u5c06\u6731\u5c0f\u519b\u558a\u8fc7\u6765\uff0c\u4e0e\u79e6\u521a\u592b\u5987\u4e00\u8d77\u5750\u5728\u5427\u53f0\u8fb9\u804a\u5929\u3002\u4ee4\u5c0f\u71d5\u5fc3\u91cc\u611f\u6168\u8fd8\u771f\u662f\u6027\u683c\u51b3\u5b9a\u4eba\u751f\uff0c\u60f3\u79e6\u521a\u8d77\u521d\u4e5f\u53ea\u662f\u5e74\u7ea7\u4e3b\u4efb\u800c\u5df2\uff0c\u5f97\u5f20\u606a\u3001\u675c\u98de\u4ed6\u4eec\u7684\u652f\u6301\uff0c\u6210\u4e3a\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u7684\u6307\u5bfc\u8001\u5e08\u3002\u8d77\u521d\u770b\u4e0a\u53bb\u662f\u5f88\u5fae\u4e0d\u8db3\u9053\u7684\u517c\u804c\uff0c\u5927\u6982\u79e6\u521a\u4ed6\u81ea\u5df1\u4e5f\u4f1a\u5c06\u6b64\u5f53\u6210\u4ed6\u4eba\u751f\u7684\u8f6c\u6298\u70b9\u5427\uff0c\u5728\u5f97\u5230\u5b66\u6821\u4ee5\u53ca\u5f20\u606a\u3001\u675c\u98de\u4ed6\u4eec\u91cd\u89c6\u4e4b\u540e\uff0c\u5f85\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u6b63\u5f0f\u501f\u4e1c\u5927\u7684\u540d\u4e49\u6210\u7acb\u8ba1\u7b97\u673a\u7f51\u7edc\u57f9\u8bad\u5b66\u6821\uff0c\u5c31\u8fdb\u4e00\u6b65\u517c\u4efb\u4e1c\u5927\u521b\u57df\u7684\u603b\u7ecf\u7406\u3002\u867d\u7136\u79e6\u521a\u5728\u5b66\u6821\u91cc\u4e0d\u518d\u62c5\u4efb\u5e74\u7ea7\u4e3b\u4efb\uff0c\u5374\u63d0\u62d4\u5f53\u4e0a\u6821\u56e2\u59d4\u526f\u4e66\u8bb0\uff0c\u4e13\u804c\u62c5\u4efb\u521b\u534f\u7684\u6307\u5bfc\u8001\u5e08\u3002\u4e5d\u516b\u5e74\uff0c\u521b\u57df\u5728\u53d1\u5c55\u7f51\u5427\u8fde\u9501\u3001\u76f4\u5c5e\u7f51\u5427\u8fd0\u8425\u3001\u7f51\u6821\u57f9\u8bad\u7b49\u51e0\u5927\u4e3b\u8425\u4e1a\u52a1\u4e0a\u5b8c\u6210\u8fd1\u4e24\u5343\u4e07\u7684\u7eaf\u6536\u76ca\uff0c\u79e6\u521a\u5373\u4f7f\u6ca1\u6709\u80a1\u4efd\uff0c\u5e74\u7ec8\u5956\u52b1\u4e5f\u4e0d\u4f1a\u4f4e\u4e8e\u4e8c\u5341\u4e07\u3002\u79e6\u521a\u82e5\u662f\u4e0d\u62ff\u5956\u91d1\uff0c\u53ef\u4ee5\u6362\u53d61\uff05\u7684\u80a1\u6743\uff0c\u8fd9\u79cd\u9009\u62e9\u6743\u53ef\u4e0d\u662f\u6bcf\u4e2a\u4eba\u90fd\u6709\u7684\u2014\u2014\u521b\u57df1\uff05\u7684\u80a1\u6743\u7edd\u5bf9\u8fdc\u8fdc\u4e0d\u6b62\u4e8c\u5341\u4e07\u3002</p><p>\u3000\u3000\u4e8c\u5341\u4e07\u5bf9\u90a3\u4e9b\u8eab\u5bb6\u5de8\u4ebf\u7684\u5bcc\u8c6a\u6765\u8bf4\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u5728\u4e1c\u5927\uff0c\u9664\u4e86\u9526\u6e56\u5de8\u8d44\u8d5e\u52a9\u7684\u4fe1\u606f\u5de5\u7a0b\u53ca\u5fae\u7535\u5b50\u5de5\u7a0b\u3001\u8ba1\u7b97\u673a\u7b49\u9662\u7cfb\u5916\u2014\u2014\u9526\u6e56\u5de8\u8d44\u8d5e\u52a9\u91cc\u81f3\u5c11\u6709\u4e24\u5343\u4e07\u8d44\u91d1\u662f\u76f4\u63a5\u7528\u6765\u63d0\u9ad8\u6559\u804c\u5458\u85aa\u8d44\u7684\uff0c\u4e00\u822c\u7684\u9662\u7cfb\u9662\u957f\uff0c\u4e0d\u8ba1\u7b97\u7070\u8272\u6536\u5165\uff0c\u50cf\u56fd\u5546\u9662\u9662\u957f\u5d14\u56fd\u6052\u5728\u5b66\u6821\u9886\u5230\u7684\u5de5\u8d44\u52a0\u6d25\u8d34\u4e00\u5e74\u4e5f\u4e0d\u8fc7\u624d\u4e09\u4e07\u591a\u4e9b\uff0c\u5f53\u7136\uff0c\u5d14\u56fd\u6052\u8fd8\u517c\u4efb\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u4e3b\u4efb\u4e00\u804c\uff0c\u4ece\u9526\u6e56\u9886\u5230\u7684\u804c\u52a1\u6d25\u8d34\u7edd\u5bf9\u8981\u8fdc\u8fdc\u591a\u8fc7\u4ed6\u5728\u5b66\u6821\u91cc\u7684\u6536\u5165\u3002</p><p>\u3000\u3000\u518d\u8bf4\u8499\u4e50\u5427\uff0c\u51e0\u4e4e\u5728\u5f20\u606a\u3001\u675c\u98de\u521a\u8fdb\u5165\u4e1c\u5927\u4e4b\u65f6\uff0c\u4ed6\u5c31\u7ed9\u63a5\u7eb3\u5230\u6838\u5fc3\u5708\u5185\uff0c\u4e0e\u675c\u98de\u5171\u540c\u6210\u7acb\u4e86\u521b\u57df\u4e0e\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\uff0c\u60f3\u5230\u8fd9\u91cc\uff0c\u4ee4\u5c0f\u71d5\u90fd\u4e0d\u7981\u6709\u4e9b\u7fa1\u6155\u5462\uff0c\u4e0d\u8bf4\u79c1\u4eba\u5173\u7cfb\u7684\u758f\u8fd1\uff0c\u81f3\u5c11\u5728\u540d\u4e49\u4e0a\uff0c\u675c\u98de\u662f\u5979\u7684\u5927\u8001\u677f\uff0c\u8499\u4e50\u662f\u5979\u7684\u4e8c\u8001\u677f\uff0c\u8499\u4e50\u79c1\u4eba\u6301\u6709\u521b\u57df18\uff05\u7684\u80a1\u6743\uff0c\u5979\u5728\u521b\u57df\u4e3b\u6301\u516c\u53f8\u7684\u65e5\u5e38\u4e8b\u52a1\uff0c\u800c\u8ba9\u675c\u98de\u4e0e\u8499\u4e50\u7b49\u4eba\u5c06\u7cbe\u529b\u653e\u5728\u521b\u4e1a\u6295\u8d44\u4e00\u5757\uff0c\u81ea\u5df1\u4eca\u5e74\u653e\u5f03\u73b0\u91d1\u5956\u52b1\uff0c\u6700\u591a\u624d\u80fd\u6301\u6709\u521b\u57df2\uff05\u7684\u80a1\u6743\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5609\u5e74\u534e\u5f0f\u7684\u72c2\u6b22\u4e00\u76f4\u6301\u7eed\u5230\u51cc\u6668\u4e09\u70b9\uff0c\u60f3\u5230\u4e0b\u5348\u8fd8\u8981\u5728\u516c\u5bd3\u91cc\u7f57\u742a\u3001\u8d75\u7559\u5b87\u7b49\u4eba\uff0c\u5f20\u606a\u4e5f\u4e0d\u60f3\u5230\u65f6\u5019\u6ca1\u7cbe\u6253\u91c7\u7684\uff0c\u5c31\u6ca1\u6709\u518d\u7ee7\u7eed\u4e0b\u53bb\uff0c\u5927\u5bb6\u4e5f\u90fd\u5404\u81ea\u6563\u4e86\u3002</p><p>\u3000\u3000\u5165\u591c\u4e4b\u524d\uff0c\u96ea\u5c31\u505c\u4e86\uff0c\u653e\u6674\u7684\u878d\u96ea\u4e4b\u591c\u5c24\u5176\u7684\u5bd2\u51b7\u3002</p><p>\u3000\u3000\u597d\u4e9b\u4eba\u90fd\u4f4f\u9752\u5e74\u516c\u5bd3\u91cc\uff0c\u82cf\u4e00\u5a77\u3001\u4f55\u5f26\u3001\u9648\u5983\u84c9\u5979\u4eec\u90fd\u4e0d\u80fd\u8fd9\u4e2a\u65f6\u95f4\u56de\u5bbf\u820d\u53bb\uff0c\u4e5f\u4fbf\u4e00\u8d77\u53bb\u9752\u5e74\u516c\u5bd3\u91cc\u501f\u5bbf\u3002\u5927\u5bb6\u79bb\u5f001978\u8d70\u56de\u516c\u5bd3\uff0c\u4e5f\u662f\u70ed\u70ed\u95f9\u95f9\u7684\uff0c\u90fd\u5e26\u7740\u51e0\u5206\u9189\u610f\uff0c\u6405\u788e\u5b81\u9759\u5bd2\u51b7\u7684\u591c\u3002</p><p>\u3000\u3000\u8d70\u51fa1978\uff0c\u5b59\u9759\u6aac\u5c31\u4eb2\u70ed\u7684\u633d\u7740\u536b\u5170\u7684\u80f3\u818a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u591c\u91cc\u8ddf\u4f60\u7761\u53bb\uff0c\u514d\u5f97\u4f60\u72ec\u81ea\u7761\u516b\u697c\u4f1a\u5bb3\u6015\u2026\u2026\u201d\u62c9\u7740\u536b\u5170\u6025\u6025\u7684\u8d70\u5728\u524d\u9762\uff0c\u4e0e\u5f20\u606a\u3001\u5510\u5a67\u7684\u8ddd\u79bb\u62c9\u5f97\u8fdc\u8fdc\u7684\u3002\u4e5f\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u6545\u610f\u907f\u5f00\u4e0e\u5510\u5a67\u78b0\u9762\uff0c\u5728\u5f20\u606a\u4ed6\u4eec\u4ece\u5317\u4eac\u8d76\u56de\u6765\u4e4b\u524d\uff0c\u7fdf\u4e39\u9752\u8d81\u653e\u5047\u56de\u8001\u5bb6\u53bb\u4e86\uff0c\u6628\u5929\u591c\u91cc\uff0c\u536b\u5170\u5c31\u5230\u9876\u697c\u4e0e\u5b59\u9759\u6aac\u4f5c\u4f34\uff0c\u8fd9\u65f6\u5019\u4e5f\u4e0d\u7591\u5b59\u9759\u6aac\u662f\u505a\u8d3c\u5fc3\u865a\u60f3\u907f\u5f00\u5510\u5a67\u3002</p><p>\u3000\u3000\u6e56\u5f52\u71d5\u4e1c\u5317\u89d2\u4e0a\u7684\u522b\u5885\u71d5\u6e56\u4e1c\u56ed\u5341\u4e8c\u6708\u4e0a\u65ec\u5efa\u6210\u4e4b\u540e\uff0c\u675c\u98de\u5728\u90a3\u91cc\u6574\u4e86\u4e00\u5957\u522b\u5885\uff0c\u5f53\u6210\u4ed6\u4e0e\u76db\u590f\u53cc\u5bbf\u53cc\u98de\u7684\u7231\u5de2\uff0c\u79bb\u5f001978\uff0c\u4ed6\u4e0e\u76db\u590f\u5c31\u76f4\u63a5\u5f00\u8f66\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u71d5\u6e56\u4e1c\u56ed\u7684\u522b\u5885\u4e3b\u8981\u63d0\u4f9b\u7ed9\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u7684\u4e2d\u9ad8\u7ea7\u79d1\u7814\u4eba\u5458\u5c45\u4f4f\uff0c\u5b9e\u5728\u65e0\u6cd5\u60f3\u8c61\u4e0e\u8bb8\u601d\u6216\u665a\u6674\u6216\u4e39\u9752\u5728\u90a3\u91cc\u8fc7\u591c\u65e9\u6668\u8d77\u6765\u4e0e\u4eba\u7167\u9762\u6253\u62db\u547c\u7684\u60c5\u5f62\uff0c\u6240\u4ee5\u71d5\u6e56\u4e1c\u56ed\u7684\u4e8c\u767e\u591a\u680b\u9ad8\u6863\u522b\u5885\uff0c\u4ed6\u81ea\u5df1\u4e00\u5957\u90fd\u6ca1\u6709\u7559\uff0c\u8fd8\u975e\u5e38\u8c26\u865a\u7684\u8bf4\uff1a\u201c\u90a3\u91cc\u7684\u522b\u5885\u662f\u7559\u7ed9\u5bf9\u9526\u6e56\u505a\u51fa\u8d21\u732e\u7684\u5458\u5de5\uff0c\u6211\u600e\u4e48\u597d\u610f\u601d\u53bb\u4f4f\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5bb3\u5f97\u90b5\u81f3\u521a\u3001\u76db\u9752\u4ed6\u4eec\u90fd\u4e0d\u597d\u610f\u601d\u5728\u90a3\u91cc\u7ed9\u81ea\u5df1\u641e\u680b\u522b\u91ce\u3002</p><p>\u3000\u3000\u867d\u8bf4\u5efa\u90ba\u7684\u522b\u5885\u5efa\u8bbe\u8fd8\u6ca1\u6709\u5361\u7d27\uff0c\u4f46\u662f\u71d5\u6e56\u4e1c\u56ed\u897f\u5357\u6fd2\u6e56\u3001\u4e1c\u5357\u9762\u5c71\u3001\u4e1c\u4fa7\u662f\u672a\u6765\u7684\u57ce\u5e02\u526f\u5546\u8d38\u4e2d\u5fc3\uff0c\u897f\u4fa7\u662f\u98ce\u666f\u4f18\u7f8e\u7684\u79d1\u7814\u56ed\u533a\u4e0e\u9ad8\u6821\u7fa4\uff0c\u8fd9\u79cd\u9576\u5d4c\u5728\u5546\u4e1a\u4e2d\u5fc3\u3001\u6587\u5316\u4e2d\u5fc3\u4e0e\u81ea\u7136\u98ce\u5149\u4e4b\u95f4\u7684\u5c45\u4f4f\u4e4b\u5730\uff0c\u53ef\u4ee5\u8bf4\u662f\u5efa\u90ba\u9996\u5c48\u4e00\u6307\u7684\uff0c\u6709\u7740\u201c\u8fc7\u4e86\u8fd9\u6751\u5c31\u6ca1\u6709\u8fd9\u5e97\u201d\u7684\u610f\u5473\uff0c\u53ea\u53ef\u60dc\u5f20\u606a\u88c5\u6a21\u4f5c\u6837\u7684\u63a8\u8f9e\uff0c\u9526\u6e56\u7684\u5176\u4ed6\u9ad8\u5c42\u81ea\u7136\u4e5f\u6709\u505a\u51fa\u8868\u7387\uff0c\u5c06\u522b\u5885\u90fd\u7559\u7ed9\u9526\u6e56\u7684\u79d1\u7814\u4eba\u5458\uff0c\u4ee5\u793a\u5bf9\u7814\u53d1\u7684\u91cd\u89c6\uff0c\u675c\u98de\u5012\u662f\u7aa5\u7834\u5f20\u606a\u7684\u5fc3\u601d\uff0c\u539a\u7740\u8138\u76ae\u5728\u91cc\u9762\u5360\u4e86\u4e00\u680b\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4e0e\u536b\u5170\u5148\u4e0a\u4e86\u697c\uff0c\u5e2d\u82e5\u7433\u8fd8\u88c5\u6a21\u4f5c\u6837\u7684\u9080\u82cf\u4e00\u5a77\u53bb\u5979\u623f\u91cc\u7761\uff0c\u65f6\u5b66\u658c\u4ed6\u4eec\u90fd\u5728\u7535\u68af\u91cc\u5632\u7b11\u8499\u4e50\u60f3\u9152\u540e\u4e71\u6027\u7684\u8ba1\u5212\u6ce1\u6c64\u4e86\uff0c\u62c9\u7740\u82cf\u4e00\u5a77\u7684\u7537\u670b\u53cb\u53bb\u4ed6\u4eec\u90a3\u91cc\u7761\u89c9\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5510\u5a67\u8fd8\u6709\u9648\u5983\u84c9\u62d6\u5728\u540e\u9762\uff0c\u8d70\u5230\u5927\u5385\u91cc\uff0c\u770b\u5230\u4f55\u5f26\u8fd8\u7b49\u5728\u90a3\u91cc\u3002\u4f55\u5f26\u4e0d\u597d\u610f\u601d\u7684\u8bf4\u9053\uff1a\u201c\u7535\u68af\u591a\u6211\u4e00\u4e2a\u5c31\u5600\u5600\u5600\u7684\u62a5\u8d85\u91cd\u2026\u2026\u6211\u4e5f\u6ca1\u6709\u90a3\u4e48\u91cd\u5427\u3002\u201d</p><p>\u3000\u3000\u4f55\u5f26\u8eab\u6750\u4ead\u5300\u4fee\u957f\uff0c\u5a77\u5a77\u7389\u7acb\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u8ba9\u4eba\u611f\u89c9\u5230\u51cf\u4e00\u5206\u3001\u589e\u4e00\u5206\u90fd\u4f1a\u89c9\u5f97\u6709\u6240\u4e0d\u8db3\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u3001\u9648\u5983\u84c9\u5148\u8d70\u8fdb\u6765\u7535\u68af\uff0c\u53c8\u8ddf\u4f55\u5f26\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u8e0f\u4e00\u53ea\u811a\u6765\u8bd5\u8bd5\u2026\u2026\u201d\u7b49\u7740\u4f55\u5f26\u88c5\u6a21\u4f5c\u6837\u8981\u8e29\u51fa\u4e00\u53ea\u811a\uff0c\u53c8\u5938\u5f20\u7684\u53eb\u9053\uff0c\u201c\u7b49\u7b49\uff0c\u7b49\u7b49\uff0c\u8fd8\u662f\u6211\u5148\u51fa\u6765\uff0c\u4f60\u518d\u8e0f\u4e00\u53ea\u811a\u8fdb\u53bb\u8bd5\u8bd5\u2026\u2026\u201d\u60f9\u5f97\u4e09\u4e2a\u5973\u5b69\u5b50\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\u3002</p><p>\u3000\u3000\u5728\u7535\u68af\u91cc\uff0c\u5510\u5a67\u8ddf\u9648\u5983\u84c9\u3001\u4f55\u5f26\u63a8\u9500\u8d77\u4e0e\u5f20\u606a\u623f\u95f4\u76f8\u8fde\u7684\u6d74\u5ba4\u91cc\u7684\u90a3\u53ea\u5927\u6d74\u7f38\u6765\uff1a\u201c\u559d\u9152\u90fd\u6709\u4e9b\u5934\u6655\u4e86\uff0c\u771f\u8981\u597d\u597d\u7684\u5728\u90a3\u6d74\u7f38\u91cc\u6ce1\u4e00\u6ce1\uff0c\u7edd\u5bf9\u89e3\u4e4f\uff0c\u4f60\u4eec\u7b49\u4f1a\u513f\u4e5f\u8f6e\u6d41\u6ce1\u7740\u8bd5\u8bd5\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u8ba9\u6211\u5148\u6ce1\u534a\u4e2a\u5c0f\u65f6\u5148\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u53bb\uff0c\u8ba9\u4f60\u5148\u6ce1\u4e86\uff0c\u6211\u4eec\u5c82\u4e0d\u662f\u6d17\u6d74\u7f38\u90fd\u8981\u6d17\u534a\u4e2a\u5c0f\u65f6\uff1f\u201d\u5510\u5a67\u5a07\u7b11\u7740\u63c9\u5f20\u606a\u7684\u8138\uff0c\u201c\u4f60\u6392\u6700\u540e\u597d\u4e0d\u597d\uff1f\u201d</p></article>"}}}]);
