(window.webpackJsonp=window.webpackJsonp||[]).push([[989],{1012:function(p){p.exports={chapterTitle:null,title:"\u7b2c986\u7ae0 \u6668\u5b09",content:"<article><h2>\u7b2c986\u7ae0 \u6668\u5b09</h2><p>\u3000\u3000\u8bfa\u5927\u7684\u516c\u5bd3\uff0c\u9664\u4e86\u5ba2\u5385\u5317\u9762\u7684\u536b\u751f\u95f4\u4e4b\u5916\uff0c\u6bcf\u4e2a\u5367\u5ba4\u90fd\u5e26\u72ec\u7acb\u7684\u76e5\u6d17\u95f4\uff0c\u5374\u662f\u5f20\u606a\u5367\u5ba4\u91cc\u7684\u76e5\u6d17\u95f4\u6700\u5962\u534e\uff0c\u5927\u51b7\u5929\u7684\uff0c\u8981\u662f\u4e0d\u60f3\u8212\u8212\u670d\u670d\u7684\u6ce1\u4e00\u4e0b\u6fa1\uff0c\u7b80\u5355\u6d17\u6f31\u4e00\u4e0b\u4e5f\u5c31\u7761\u89c9\u4e86\uff0c\u8981\u662f\u60f3\u8212\u8212\u670d\u670d\u7684\u6ce1\u4e00\u4e0b\u6fa1\uff0c\u8fd8\u5c31\u5f20\u606a\u7684\u6d74\u5ba4\u6700\u5408\u9002\u3002</p><p>\u3000\u3000\u8d85\u8c6a\u534e\u7684\u6309\u6469\u6d74\u7f38\u652f\u5728\u7a7a\u8361\u8361\u7684\u6d74\u5ba4\u91cc\uff0c\u534a\u5f27\u5f62\u7684\u5355\u89c6\u5411\u843d\u5730\u73bb\u7483\u7a97\uff0c\u89c6\u91ce\u5f00\u62d3\uff0c\u53ef\u4ee5\u8fb9\u6ce1\u6fa1\u8fb9\u770b\u5230\u5916\u9762\u7684\u96ea\u591c\u98ce\u666f\uff0c\u610f\u5927\u5229\u5927\u7406\u77f3\u8d34\u94fa\u7684\u5899\u58c1\u4e0a\u5d4c\u7740\u8d85\u5927\u5c4f\u5e55\u7684\u7b49\u79bb\u5b50\u5c4f\u2026\u2026\u8fd9\u95f4\u6d74\u5ba4\u7684\u88c5\u6f62\u8d39\u7528\u80fd\u5728\u65b0\u5e02\u8857\u4e70\u5957\u516c\u5bd3\u4e86\u3002</p><p>\u3000\u3000\u60f3\u5230\u8981\u662f\u8ba9\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u4f55\u5f26\u4e00\u4e2a\u4e00\u4e2a\u7684\u53bb\u6ce1\u6fa1\uff0c\u8f6e\u5230\u81ea\u5df1\u5c82\u4e0d\u662f\u8981\u770b\u5230\u592a\u9633\u4ece\u4e1c\u65b9\u5347\u8d77\u6765\u4e86\uff1f\u59a5\u534f\u540e\u7684\u7ed3\u679c\uff0c\u5c31\u662f\u4e09\u4e2a\u5973\u5b69\u5b50\u4e00\u8d77\u5148\u8fdb\u53bb\u6ce1\u6fa1\uff0c\u5f20\u606a\u8fd8\u662f\u6392\u5728\u6700\u540e\u3002</p><p>\u3000\u3000\u5f20\u606a\u6700\u540e\u8fd8\u662f\u795e\u8272\u7325\u7410\u7684\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u4f60\u4eec\u5acc\u6211\u810f\uff0c\u6211\u4e0d\u5acc\u4f60\u4eec\u810f\uff0c\u4f60\u4eec\u6ce1\u5b8c\u6fa1\uff0c\u6c34\u80fd\u4e0d\u80fd\u4e0d\u8981\u653e\u6389\uff1f\u201d\u8bdd\u97f3\u672a\u843d\uff0c\u597d\u51e0\u53ea\u7ed2\u62d6\u978b\u5c31\u98de\u88ad\u8fc7\u6765\uff0c\u53ea\u80fd\u9887\u4e3a\u843d\u5bde\u7684\u770b\u7740\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u3001\u4f55\u5f26\u4e0a\u697c\u53bb\uff0c\u542c\u7740\u6e05\u67d4\u8106\u7f8e\u7684\u5a07\u7b11\u58f0\u7ed9\u63a9\u5728\u95e8\u540e\uff0c\u5fc3\u60f3\u8fd8\u4e0d\u5982\u8ba9\u5979\u4eec\u4e00\u4e2a\u4e00\u4e2a\u53bb\u6ce1\u6fa1\u597d\u4e86\uff0c\u81f3\u5c11\u8fd8\u4f1a\u7559\u4e24\u4e2a\u4eba\u5728\u5916\u9762\u966a\u81ea\u5df1\uff0c\u90fd\u60f3\u5230\u697c\u4e0b\u53bb\u627e\u674e\u99a8\u4e88\u804a\u4f1a\u513f\u5929\u4e86\u3002</p><p>\u3000\u3000\u542c\u7740\u9690\u9690\u7ea6\u7ea6\u7684\u5b09\u6c34\u58f0\uff0c\u5f20\u606a\u773c\u76ae\u5b50\u6e10\u91cd\uff0c\u5934\u6b6a\u5728\u6c99\u53d1\u4e0a\u5148\u7761\u7740\u4e86\u3002\u518d\u9192\u8fc7\u6765\uff0c\u5929\u5149\u5df2\u7ecf\u5927\u4eae\uff0c\u8fd9\u680b\u697c\u91cc\u8fd8\u662f\u5b89\u5b89\u9759\u9759\u7684\uff0c\u624d\u5143\u65e6\u4e09\u5929\u5047\u671f\uff0c\u8fd9\u624d\u7b2c\u4e8c\u5929\uff0c\u6628\u5929\u5927\u5bb6\u53c8\u75af\u73a9\u4e86\u592a\u665a\uff0c\u8fd9\u65f6\u5019\u5dee\u4e0d\u591a\u90fd\u8fd8\u5728\u7761\u89c9\u3002\u4e0d\u6653\u5f97\u4ec0\u4e48\u65f6\u5019\u8eab\u4e0a\u7ed9\u76d6\u4e86\u4e00\u4ef6\u6bef\u5b50\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\uff0c\u51ed\u7f8a\u7ed2\u6bef\u843d\u5230\u5730\u677f\u4e0a\uff0c\u8d64\u811a\u8d70\u5230\u843d\u5730\u7a97\u91cc\u4f38\u7740\u61d2\u8170\uff0c\u8eab\u4f53\u91cc\u7684\u56f0\u987f\u8fd8\u6ca1\u6709\u6d88\u5c3d\uff0c\u63c9\u7740\u773c\u76ae\u5b50\u6276\u7740\u697c\u68af\u5c31\u4e0a\u4e86\u697c\uff0c\u63a8\u5f00\u5367\u5ba4\u7684\u95e8\uff0c\u4e09\u4e2a\u5973\u5b69\u5b50\u6b63\u6a2a\u9648\u4ed6\u4ed6\u90a3\u5f20\u88d9\u8fb9\u5305\u9ed1\u9769\u7684\u5927\u5e8a\u4e0a\u3002</p><p>\u3000\u3000\u5510\u5a67\u7761\u5728\u6700\u5de6\u8fb9\uff0c\u96ea\u85d5\u822c\u7684\u67d4\u8f6f\u7389\u81c2\u538b\u5728\u88ab\u8925\u4e0a\uff0c\u51cc\u4e71\u800c\u4e30\u76db\u7684\u957f\u53d1\u91cc\uff0c\u9999\u816e\u56e0\u719f\u7761\u800c\u5fae\u5e26\u6f6e\u7ea2\uff0c\u808c\u80a4\u5439\u5f39\u5f97\u7834\uff0c\u6d3b\u8131\u8131\u7684\u56fd\u8272\u5929\u9999\u7684\u5927\u7f8e\u4eba\u513f\uff1b\u9648\u5983\u84c9\u7761\u5728\u4e2d\u95f4\uff0c\u5373\u4f7f\u5728\u719f\u7761\u4e2d\uff0c\u4e4c\u9ed1\u6cb9\u4eae\u7684\u957f\u53d1\u8fd8\u662f\u987a\u76f4\u7684\uff0c\u53d1\u68a2\u538b\u7740\u767d\u7389\u822c\u7eaf\u6d01\u7684\u8116\u5b50\u4e0a\uff0c\u5a07\u9765\u660e\u4e3d\u800c\u9759\u96c5\uff0c\u4f55\u5f26\u7684\u7761\u76f8\u6700\u5dee\uff0c\u5934\u8138\u88f9\u5728\u88ab\u5b50\u91cc\uff0c\u4ece\u8584\u7ed2\u88ab\u52fe\u52d2\u51fa\u6765\u73b2\u73d1\u6d6e\u51f8\u7684\u66f2\u7ebf\u53ef\u4ee5\u5224\u65ad\u5979\u662f\u8db4\u7740\u7761\u7684\uff0c\u5c0f\u81c0\u5728\u7761\u68a6\u91cc\u8fd8\u5fae\u5fae\u6485\u7740\uff0c\u4e00\u53ea\u6676\u83b9\u6d01\u767d\u7684\u5ae9\u8db3\u8fd8\u4f38\u5728\u88ab\u5b50\u5916\u3002</p><p>\u3000\u3000\u5f20\u606a\u771f\u662f\u60cb\u60dc\u8fd9\u65f6\u5019\u4e0d\u662f\u9ed1\u591c\u53ef\u4ee5\u8d81\u9ed1\u94bb\u8fdb\u88ab\u5b50\u91cc\u5de6\u62e5\u53f3\u62b1\u4e00\u756a\uff0c\u7136\u540e\u5047\u88c5\u8bef\u4f1a\u65c1\u82e5\u65e0\u4e8b\u7684\u8d70\u5f00\uff0c\u8fd9\u65f6\u5019\u53ea\u80fd\u9000\u800c\u6c42\u5176\u6b21\uff0c\u63a8\u95e8\u8fdb\u53bb\u6d74\u5ba4\u91cc\u8212\u8212\u670d\u670d\u7684\u6ce1\u4e00\u4e0b\u6fa1\u3002</p><p>\u3000\u3000\u843d\u5730\u7a97\u6b63\u5bf9\u7740\u5408\u6b22\u5c71\u9876\uff0c\u5927\u51b7\u5929\u7684\uff0c\u4e00\u5927\u65e9\u5c31\u6709\u5728\u5c71\u9876\u4e0a\u6668\u8bfb\uff0c\u4e5f\u6709\u7537\u5973\u604b\u4eba\u8d77\u65e9\u51fa\u6765\u5e7d\u4f1a\uff0c\u4e5f\u5230\u5c71\u9876\u4e0a\u6668\u7ec3\u7684\uff0c\u5f20\u606a\u8d64\u8eab\u88f8\u4f53\u7684\u6d78\u5230\u70ed\u6c34\u91cc\uff0c\u8212\u670d\u5f97\u5fcd\u4e0d\u4f4f\u60f3\u8981\u547b\u541f\u8d77\u6765\uff0c\u5fc3\u91cc\u5728\u60f3\u7740\u4ec0\u4e48\u65f6\u5019\u9526\u6e56\u7684\u6280\u672f\u5b9e\u529b\u80fd\u8ba9\u6574\u4e2a\u843d\u5730\u73bb\u7483\u7a97\u4e5f\u5b9e\u73b0\u5c4f\u5e55\u6295\u5f71\u7684\u529f\u80fd\uff0c\u8eba\u5728\u8fd9\u6d74\u7f38\u91cc\u6ce1\u6fa1\u5c31\u66f4\u8212\u670d\u4e86\u3002</p><p>\u3000\u3000\u7ed9\u70ed\u6c34\u6d78\u7740\u660f\u660f\u6b32\u7761\uff0c\u542c\u89c1\u6d74\u5ba4\u7684\u95e8\u7ed9\u5431\u5440\u7684\u6253\u5f00\uff0c\u7a7f\u7740\u7ef8\u8d28\u7761\u88d9\u7684\u5510\u5a67\u62ab\u5934\u6563\u53d1\u7684\u8d70\u8fdb\u6765\uff0c\u89c1\u5979\u773c\u76ae\u5b50\u677e\u76ae\uff0c\u7741\u90fd\u7741\u4e0d\u5f00\u6765\uff0c\u987a\u624b\u5c06\u95e8\u5173\u4e0a\uff0c\u8d64\u7740\u9c9c\u5ae9\u767d\u7699\u7684\u79c0\u8db3\uff0c\u8dcc\u8dcc\u649e\u649e\u7684\u671d\u62bd\u6c34\u9a6c\u6876\u6478\u8fc7\u53bb\uff0c\u624b\u4f38\u5230\u7761\u88d9\u91cc\u5c06\u68c9\u8d28\u5c0f\u5185\u88e4\u892a\u5230\u819d\u76d6\u5f2f\u5904\uff0c\u6b63\u8981\u5f2f\u8170\u5750\u5230\u62bd\u6c34\u9a6c\u6876\u4e0a\u624d\u9661\u7136\u770b\u5230\u5f20\u606a\u5750\u5728\u6d74\u7f38\u91cc\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5510\u5a67\u8f7b\u547c\u4e86\u4e00\u58f0\uff0c\u5fd9\u5c06\u5c0f\u5185\u88e4\u62c9\u8d77\u6765\uff0c\u5a07\u67d4\u7684\u8f7b\u9a82\u9053\uff0c\u201c\u4f60\u4e2a\u53d8\u6001\uff0c\u7adf\u7136\u5077\u770b\u4eba\u5bb6\u5973\u5b69\u5b50\u4e0a\u5395\u6240\u3002\u201d\u8d70\u8fc7\u6765\u8981\u6390\u5f20\u606a\u7684\u8138\u3002</p><p>\u3000\u3000\u5f20\u606a\u987a\u4ece\u7684\u7ed9\u5979\u6390\u5230\u8138\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u660e\u660e\u662f\u4f60\u6765\u5077\u770b\u6211\u6d17\u6fa1\uff0c\u201d\u770b\u7740\u5979\u8f7b\u8584\u7761\u88d9\u4e0b\u5a40\u5a1c\u591a\u59ff\u7684\u66f2\u7ebf\uff0c\u88d9\u4e0b\u7389\u4e73\u9ad8\u8038\uff0c\u96ea\u817f\u7ea4\u6ed1\u4fee\u957f\uff0c\u5c0f\u8170\u4ec5\u582a\u76c8\u76c8\u4e00\u63e1\uff0c\u770b\u5f97\u5f20\u606a\u5fc3\u8361\u795e\u8ff7\uff0c\u63fd\u8fc7\u5979\u7684\u5c0f\u8170\uff0c\u5934\u9760\u8d34\u7740\u5979\u5e73\u6ed1\u7684\u5c0f\u8179\uff0c\u95fb\u7740\u5e7d\u6de1\u82ac\u82b3\u7684\u4f53\u9999\uff0c\u95ee\u9053\uff0c\u201c\u8981\u4e0d\u8981\u4e00\u8d77\u6ce1\u4e00\u4f1a\u513f\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u6b7b\u554a\uff01\u201d\u5510\u5a67\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u770b\u6d74\u5ba4\u7684\u95e8\u53c8\u6ca1\u6709\u63a9\u597d\uff0c\u9648\u5983\u84c9\u4e0e\u4f55\u5f26\u5c31\u7761\u5728\u5916\u95f4\u7684\u5e8a\u4e0a\uff0c\u7ed9\u5979\u5341\u4e2a\u80c6\u5b50\u90fd\u4e0d\u6562\u8ddf\u5f20\u606a\u73a9\u9e33\u9e2f\u5171\u6d74\uff0c\u5979\u662f\u8fc7\u6765\u4e0a\u5395\u6240\u7684\uff0c\u56f0\u610f\u8fd8\u6ca1\u6709\u6d88\uff0c\u8f6c\u8eab\u8981\u8d70\u51fa\u53bb\u3002</p><p>\u3000\u3000\u201c\u5e2e\u6211\u64e6\u4e0b\u80cc\u3002\u201d\u5f20\u606a\u62c9\u4f4f\u5510\u5a67\u5ae9\u6ed1\u7684\u5c0f\u624b\u3002</p><p>\u3000\u3000\u201c\u6211\u8981\u51fa\u53bb\u4e0a\u5395\u6240\u5462\uff0c\u201d\u89c1\u5f20\u606a\u793a\u610f\u5c31\u5728\u8fd9\u91cc\u4e0a\uff0c\u5510\u5a67\u53c8\u7a0d\u5e26\u7f9e\u6da9\u7684\u8bf4\u9053\uff0c\u201c\u4f60\u5728\u8fd9\u91cc\uff0c\u6211\u53ef\u4e0d\u5f53\u4f60\u7684\u9762\u5728\u8fd9\u91cc\u4e0a\u5395\u6240\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u8f6c\u8fc7\u8eab\u53bb\u4e0d\u770b\u5c31\u662f\u4e86\u3002\u201d\u5f20\u606a\u6362\u4e86\u4e2a\u65b9\u5411\u800c\u5750\u3002</p><p>\u3000\u3000\u201c\u4f60\u5c06\u8033\u6735\u4e5f\u6342\u8d77\u6765\u3002\u201d\u5510\u5a67\u8bf4\u9053\uff0c\u770b\u5230\u5f20\u606a\u8001\u5b9e\u7684\u5c06\u8033\u6735\u4e5f\u6342\u8d77\u6765\uff0c\u53c8\u5429\u5490\u9053\uff0c\u201c\u6342\u7d27\u8033\u6735\u554a\uff0c\u4f60\u8981\u662f\u5077\u542c\uff0c\u6211\u53ef\u9976\u4e0d\u4e86\u4f60\u3002\u201d</p><p>\u3000\u3000\u5f85\u542c\u5230\u6709\u63a7\u5236\u7740\u6dc5\u6ca5\u7684\u6c34\u58f0\uff0c\u5f20\u606a\u5c31\u60a0\u7136\u81ea\u5f97\u7684\u8f6c\u8fc7\u8eab\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u8bf4\u8bdd\u4e0d\u7b97\u6570\u554a\u2026\u2026\u201d\u504f\u504f\u8fd9\u65f6\u5019\u53c8\u4e0d\u80fd\u6251\u8fc7\u6765\u6559\u8bad\u8fd9\u98df\u8a00\u800c\u80a5\u7684\u5bb6\u4f19\uff0c\u770b\u7740\u5f20\u606a\u5634\u89d2\u8fb9\u6302\u7740\u620f\u8c11\u7684\u7b11\uff0c\u5510\u5a67\u53c8\u7f9e\u53c8\u6025\uff0c\u53c8\u4e0d\u80fd\u8bf4\u505c\u5728\u8fd9\u91cc\u63d0\u88e4\u7ad9\u8d77\u6765\uff0c\u80a1\u95f4\u7a0d\u7528\u4e86\u4e9b\u529b\uff0c\u5e0c\u671b\u80fd\u6d88\u6389\u4e9b\u6c34\u58f0\uff0c\u5374\u5c06\u6c34\u67f1\u675f\u538b\u5f97\u66f4\u6025\uff0c\u843d\u5728\u6c34\u4e0a\uff0c\u6dc5\u6dc5\u6ca5\u6ca5\u7684\u54cd\uff0c\u5373\u4f7f\u65e9\u5c31\u53d1\u751f\u8fc7\u7d27\u5bc6\u7684\u5173\u7cfb\uff0c\u5510\u5a67\u4e5f\u6ca1\u6709\u60f3\u5230\u8981\u5728\u5f20\u606a\u9762\u524d\u89e3\u624b\uff0c\u4fcf\u8138\u7f9e\u5f97\u901a\u7ea2\uff0c\u53cc\u624b\u6367\u4f4f\u8138\u57cb\u5728\u819d\u76d6\u95f4\u3002\u5f85\u89e3\u5b8c\u624b\u63d0\u8d77\u5185\u88e4\uff0c\u5510\u5a67\u624d\u7a0d\u51cf\u5185\u5fc3\u7684\u5a07\u7f9e\uff0c\u6076\u72e0\u72e0\u7684\u8d70\u5230\u6d74\u7f38\u8fb9\uff0c\u626f\u4f4f\u5f20\u606a\u7684\u8033\u6735\uff0c\u95ee\u9053\uff1a\u201c\u6628\u5929\u6211\u770b\u5230\u67d0\u4eba\u4e5f\u8fd9\u4e48\u626f\u4f60\u7684\u8033\u6735\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u5077\u770b\u4eba\u5bb6\u4e0a\u5395\u6240\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u54ea\u6562\u554a\uff1f\u201d\u5f20\u606a\u4f38\u624b\u9694\u7740\u7761\u88d9\u6293\u4f4f\u5510\u5a67\u5a07\u633a\u7684\u4e00\u5bf9\u7389\u4e73\uff0c\u9694\u7740\u8584\u8584\u7684\u7ef8\u8d28\u88d9\u5e03\u611f\u53d7\u5230\u60ca\u4eba\u7684\u8f6f\u5f39\u4e4b\u611f\uff0c\u5927\u6982\u5c11\u5973\u7684\u7389\u4e73\u662f\u6700\u8bf1\u60d1\u4eba\u4f38\u624b\u53bb\u63e1\u4f4f\u7684\u4e1c\u897f\u4e86\uff0c\u5f20\u606a\u53ea\u671f\u5f85\u8fd9\u80fd\u8f6c\u79fb\u5979\u7684\u6ce8\u610f\u529b\uff0c\u201c\u4f60\u4e0a\u5395\u6240\u7684\u6837\u5b50\u624d\u503c\u5f97\u770b\uff0c\u4f60\u521a\u624d\u7684\u6837\u5b50\u597d\u7f8e\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e2a\u5927\u8272\u72fc\u554a\uff0c\u201d\u5510\u5a67\u7ec6\u58f0\u7684\u8bf4\u9053\uff0c\u8eab\u4f53\u5374\u6ca1\u6709\u95ea\u5f00\uff0c\u8ba9\u5f20\u606a\u8f7b\u63c9\u7ec6\u634f\u7740\u5979\u7684\u4e73\u5cf0\uff0c\u5979\u662f\u8f7b\u8f7b\u7684\u6402\u8fc7\u5f20\u606a\u7684\u8116\u5b50\uff0c\u4e73\u5c16\u7ed9\u63c9\u5f04\u5f97\u50cf\u7389\u7c73\u7c92\u4f3c\u7684\u633a\u7acb\u8d77\u6765\uff0c\u624d\u653e\u5f00\u624b\uff0c\u8eab\u4f53\u5f80\u540e\u8eb2\u4e86\u8eb2\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u8981\u95f9\u4e86\uff0c\u5983\u84c9\u5979\u4eec\u8fd8\u7761\u5728\u5916\u9762\u5462\uff0c\u6211\u7ed9\u4f60\u6413\u80cc\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u80fd\u8ddf\u5510\u5a67\u8fa9\u89e3\u8bf4\u4ed6\u4e0e\u9759\u6aac\u4e4b\u95f4\u771f\u6ca1\u6709\u4ec0\u4e48\uff0c\u8981\u4e0d\u662f\u9759\u6aac\u575a\u6301\uff0c\u65e9\u5c31\u8d8a\u8fc7\u90a3\u9053\u7ebf\u4e86\uff0c\u80cc\u8fc7\u8eab\u53bb\uff0c\u8ba9\u5510\u5a67\u5e2e\u4ed6\u6413\u80cc\uff0c\u6413\u80cc\u6413\u5f97\u8212\u670d\uff0c\u53c8\u8eba\u4e0b\u6765\uff0c\u8ba9\u5510\u5a67\u5e2e\u4ed6\u6413\u80f8\u53e3\u3001\u8179\u90e8\uff0c\u8fd8\u53e3\u82b1\u82b1\u7684\u8bf4\uff1a\u201c\u4e0b\u56de\u4e5f\u5e2e\u4f60\u6413\u5168\u8eab\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u624d\u4e0d\u7ed9\u4f60\u5360\u4fbf\u5b9c\uff0c\u4f60\u4e2a\u5927\u8272\u72fc\uff0c\u201d\u5510\u5a67\u5634\u91cc\u8f7b\u9a82\u7740\uff0c\u8fd8\u662f\u5e2e\u5f20\u606a\u6413\u6d17\u524d\u9762\u8eab\u4f53\u6765\uff0c\u770b\u7740\u90a3\u6839\u6b7b\u86c7\u4e00\u6837\u7684\u4e1c\u897f\u7ed9\u624b\u638c\u8fb9\u7f18\u78b0\u4e86\u4e24\u4e0b\u5c31\u9762\u76ee\u72f0\u72de\u7684\u6602\u5934\u652f\u51fa\u6c34\u9762\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u8fd9\u5927\u5bb6\u4f19\u771f\u4e11\u964b\uff0c\u771f\u4e0d\u6562\u76f8\u4fe1\u6211\u600e\u4e48\u4f1a\u8ba9\u8fd9\u4e48\u4e11\u964b\u7684\u5927\u5bb6\u4f19\u8fdb\u6765\u2026\u2026\u201d\u4f38\u624b\u8f7b\u8f7b\u7684\u4e00\u63e1\uff0c\u597d\u73a9\u7684\u634b\u4e86\u4e24\u4e0b\uff0c\u90a3\u9633\u7269\u4e0a\u5c31\u6709\u9752\u7b4b\u6d6e\u8d77\u6765\uff0c\u770b\u5230\u5f20\u606a\u772f\u773c\u4eab\u53d7\u7684\u6a21\u6837\uff0c\u5510\u5a67\u5c48\u6307\u505a\u51fa\u8981\u7528\u529b\u5f39\u7684\u6a21\u6837\uff0c\u5f20\u606a\u6015\u5c0f\u59ae\u5b50\u4e0d\u77e5\u8f7b\u91cd\uff0c\u90a3\u5145\u8840\u3001\u654f\u611f\u5230\u6781\u70b9\u7684\u5934\u4e0a\u54ea\u6015\u7ed9\u6307\u7532\u8f7b\u8f7b\u7684\u6390\u4e00\u4e0b\uff0c\u5c31\u8981\u96be\u53d7\u597d\u534a\u5929\uff0c\u632a\u7740\u5c41\u80a1\u5c31\u8981\u8eb2\u5f00\u3002\u5510\u5a67\u4e0d\u4f9d\u4e0d\u6320\u7684\u7784\u51c6\u7740\u90a3\u4e1c\u897f\u8981\u5f39\uff0c\u4e24\u4eba\u95f9\u817e\u4e86\u4e00\u5c0f\u4f1a\u513f\uff0c\u5012\u662f\u6e85\u51fa\u8bb8\u591a\u6c34\u5c06\u5979\u8eab\u4e0a\u90fd\u6cfc\u6e7f\u4e86\u3002</p><p>\u3000\u3000\u201c\u5431\u5440\u201d\uff0c\u6d74\u5ba4\u7684\u95e8\u53c8\u7ed9\u4eba\u4ece\u5916\u9762\u63a8\u5f00\uff0c\u5510\u5a67\u53d7\u60ca\u7684\u56de\u5934\u770b\u5230\u4e00\u773c\uff0c\u5374\u89c1\u4f55\u5f26\u624b\u8499\u7740\u773c\u775b\u5927\u53eb\uff1a\u201c\u9648\u5983\u84c9\uff0c\u5b8c\u86cb\u4e86\uff0c\u6211\u770b\u5230\u5c11\u513f\u4e0d\u5b9c\u7684\u4e1c\u897f\uff0c\u8fd9\u4e0b\u5b50\u8981\u957f\u9488\u773c\u4e86\u3002\u201d\u8f6c\u8fc7\u8eab\u5c31\u5f80\u95e8\u5916\u8d70\u53bb\u3002</p><p>\u3000\u3000\u4f55\u5f26\u7761\u76f8\u5dee\uff0c\u8d77\u8eab\u4e0a\u5395\u6240\u91cc\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u7761\u88d9\u7684\u4e00\u89d2\u6298\u5728\u5185\u88e4\u91cc\uff0c\u5c0f\u5185\u88e4\u4e5f\u7531\u4e8e\u7761\u59ff\u592a\u5dee\u800c\u5d4c\u5230\u80a1\u95f4\uff0c\u8f6c\u8eab\u8fc7\u53bb\u9732\u51fa\u5c0f\u534a\u7247\u6d51\u5706\u96ea\u81c0\uff0c\u4ee5\u53ca\u7389\u7acb\u633a\u62d4\u7684\u4fee\u957f\u7f8e\u817f\uff0c\u5f20\u606a\u5fcd\u4e0d\u4f4f\u77a5\u773c\u6b23\u8d4f\u4e86\u4e24\u773c\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e2a\u5927\u8272\u72fc\uff0c\u53c8\u5bb3\u6211\u4e22\u4eba\u4e22\u5230\u5bb6\u4e86\uff0c\u8fd8\u4e0d\u6653\u5f97\u5979\u4eec\u4f1a\u600e\u4e48\u5632\u7b11\u6211\u5462\u2026\u2026\u201d\u5510\u5a67\u5a07\u7f9e\u4e0d\u582a\u7684\u5a07\u6028\u9053\uff0c\u4f38\u624b\u906e\u4f4f\u5f20\u606a\u7684\u773c\u775b\u4e0d\u8ba9\u4ed6\u778e\u770b\uff0c\u53c8\u62bd\u51fa\u6bdb\u5dfe\u5c06\u95f9\u6e7f\u7684\u7761\u88d9\u7a0d\u7a0d\u64e6\u5e72\u4e9b\uff0c\u8fd9\u65f6\u5019\u542c\u89c1\u4f55\u5f26\u4e0e\u9648\u5983\u84c9\u5728\u5916\u95f4\u5927\u58f0\u7684\u5a07\u7b11\uff0c\u77aa\u773c\u770b\u7740\u5f20\u606a\u53c8\u7f9e\u53c8\u607c\uff0c\u201c\u660e\u660e\u4e0d\u662f\u5979\u4eec\u60f3\u8c61\u7684\u90a3\u6837\uff0c\u90fd\u662f\u4f60\u8fd9\u5927\u8272\u72fc\u5bb3\u4eba\u5bb6\u7684\uff01\u201d\u72b9\u4e0d\u89e3\u6068\u7684\u53cc\u624b\u4ece\u6d74\u7f38\u91cc\u63ac\u8d77\u4e00\u6367\u6c34\u6cfc\u5230\u5f20\u606a\u7684\u8138\uff0c\u624d\u8f6c\u8eab\u8d70\u51fa\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u773c\u775b\u91cc\u7684\u6c34\u64e6\u5e72\uff0c\u5c31\u542c\u89c1\u4e09\u4e2a\u5973\u5b69\u5b50\u5728\u5916\u95f4\u7684\u5a07\u8106\u7b11\u58f0\u4f20\u6765\uff0c\u663e\u7136\u662f\u5510\u5a67\u5728\u5a01\u903c\u5229\u8bf1\u9648\u5983\u84c9\u3001\u4f55\u5f26\u4e0d\u8981\u5c06\u4e8b\u60c5\u4f20\u51fa\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u94b1\u6842\u534e\u5728\u4e91\u68a6\u9601\u7684\u5ba2\u623f\u91cc\u8fc7\u4e86\u4e00\u591c\uff0c\u9192\u6765\u770b\u4e86\u770b\u8eba\u5728\u8eab\u8fb9\u7684\u96ea\u767d\u8089\u4f53\uff0c\u4f11\u606f\u8fc7\uff0c\u8fd8\u80fd\u518d\u73a9\u4e00\u56de\uff0c\u5c31\u73a9\u5f04\u8d77\u96ea\u767d\u8089\u4f53\u7684\u96ea\u81c0\u6765\uff0c\u624b\u6307\u4f38\u5230\u4e24\u817f\u4e4b\u95f4\u7684\u5fae\u9876\u5f00\u6765\uff0c\u8fd9\u4e48\u5e74\u8f7b\u7684\u8089\u4f53\uff0c\u90a3\u5185\u5916\u4e24\u5c42\u5507\u76ae\u8fb9\u7f18\u5374\u662f\u7d2b\u9ed1\u8272\u7684\uff0c\u5012\u662f\u4e0e\u8089\u4f53\u7684\u7f8e\u4e3d\u5f62\u6210\u9c9c\u660e\u7684\u53cd\u5dee\uff0c\u94b1\u6842\u534e\u5012\u4e5f\u4e0d\u7ba1\uff0c\u624b\u6307\u5728\u5507\u76ae\u4e0a\u8e6d\u7740\uff0c\u770b\u7740\u91cc\u9762\u9c9c\u5ae9\u7684\u8089\u82bd\u6e10\u6e10\u7ed9\u76c8\u6cfd\u7684\u6c34\u5149\u8986\u4e0a\uff0c\u73a9\u5f04\u7684\u5174\u81f4\u8fd8\u662f\u6781\u4e3a\u65fa\u76db\u3002</p><p>\u3000\u3000\u684c\u4e0a\u7684\u624b\u673a\u54cd\u4e86\u8d77\u6765\uff0c\u62ff\u8d77\u6765\u770b\u662f\u533a\u957f\u8c37\u5cb3\u6253\u6765\u7535\u8bdd\uff0c\u89c1\u5e8a\u4e0a\u7684\u96ea\u767d\u8089\u4f53\u521a\u624d\u7ed9\u624b\u6307\u6478\u5f04\u4e0b\u9762\u597d\u4e00\u4f1a\u513f\u90fd\u6ca1\u6709\u53cd\u5e94\u3001\u8fd9\u65f6\u5019\u542c\u5230\u624b\u673a\u54cd\u5374\u4fa7\u8fc7\u8eab\u6765\u770b\u5411\u8fd9\u91cc\uff0c\u94b1\u6842\u534e\u505a\u4e86\u5b89\u9759\u7684\u624b\u52bf\uff0c\u63a5\u901a\u7535\u8bdd\uff0c\u5374\u542c\u5230\u4e00\u4e2a\u8ba9\u4ed6\u76f8\u5f53\u610f\u5916\u7684\u6d88\u606f\uff1a\u533a\u59d4\u4e66\u8bb0\u5f20\u5065\u4e0e\u533a\u957f\u8c37\u5cb3\u4e34\u65f6\u5546\u8bae\u8981\u5728\u8fd9\u6b21\u53bb\u6b27\u6d32\u5341\u56fd\u8003\u5bdf\u56e2\u7684\u540d\u5355\u91cc\u6dfb\u4e0a\u4ed6\u4e0e\u533a\u653f\u534f\u526f\u4e3b\u5e2d\u5434\u7231\u6c11\u4e24\u4eba\uff0c\u8c37\u5cb3\u8981\u4ed6\u8fd9\u4e24\u5929\u5c06\u51fa\u56fd\u7684\u624b\u7eed\u7ed9\u529e\u9f50\u5168\u4e86\u3002</p></article>"}}}]);
