(window.webpackJsonp=window.webpackJsonp||[]).push([[99],{122:function(p){p.exports={chapterTitle:null,title:"\u7b2c1084\u7ae0 \u88ab\u56f4\u6bb4\u4e0e\u7a81\u56f4\u8ba1\u5212",content:"<article><h2>\u7b2c1084\u7ae0 \u88ab\u56f4\u6bb4\u4e0e\u7a81\u56f4\u8ba1\u5212</h2><p>\u3000\u3000\u963b\u6320\u9526\u6e56\u4e0e\u5fae\u8f6f\u5408\u4f5c\u7684\u65b9\u6848\u7ed9\u5426\u51b3\u6389\u4e4b\u540e\uff0c\u674e\u5728\u6d19\u4ee3\u8868\u4e09\u661f\u7535\u5b50\u5728\u5b8f\u4fe1\u7684\u6413\u548c\u4e0b\uff0c\u4e0e\u6587\u821f\u5e02\u653f\u5e9c\u8fdb\u884c\u7d27\u6025\u4f1a\u5546\uff0c\u8ba8\u8bba\u4e09\u661f\u7535\u5b50\u5408\u8d44\u5728\u6587\u821f\u6295\u5efa\u6676\u5706\u5382\u7684\u8ba1\u5212\uff0c\u5dee\u4e0d\u591a\u534a\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u5c31\u57fa\u672c\u8c08\u59a5\u4e09\u661f\u5b8f\u82af\u6676\u5706\u5382\u7684\u5408\u8d44\u534f\u8bae\uff0c\u5306\u5fd9\u8d76\u5728\u7f8e\u56fd\u5f53\u5c40\u901a\u8fc7\u9526\u6e56\u4e0e\u5fb7\u4eea\u6280\u672f\u8f6c\u8ba9\u534f\u4f1a\u5ba1\u67e5\u6b21\u7684\u65e5\u5ba3\u5e03\u8fd9\u9879\u5408\u4f5c\u8ba1\u5212\u3002</p><p>\u3000\u3000\u76f8\u6bd4\u8f83\u8d77\u4e2d\u6676\u5fae\u82af\u4e0e\u534e\u590fNEC\u6f2b\u957f\u7684\u5efa\u5382\u7b79\u5907\u671f\uff0c\u4e09\u661f\u7535\u5b50\u4e0e\u6587\u821f\u5e02\u653f\u5e9c\u4ee5\u53ca\u5b8f\u4fe1\u6295\u8d44\u5173\u4e8e\u4e09\u661f\u5b8f\u82af\u5408\u8d44\u5efa\u5382\u7684\u8c08\u5224\u901f\u5ea6\u771f\u662f\u591f\u60ca\u4eba\u7684\u3002</p><p>\u3000\u3000\u6587\u821f\u89c2\u6d77\u5927\u9152\u5e97\uff0c\u4e09\u5341\u516d\u5c42\u9ad8\u7ea7\u5957\u623f\uff0c\u674e\u5728\u6d19\u7ad9\u5728\u843d\u5730\u73bb\u7483\u7a97\u524d\uff0c\u770b\u7740\u89c6\u91ce\u8fdc\u9645\u7684\u6d77\u6c34\u4ee5\u53ca\u8fd1\u5904\u5fd9\u788c\u7684\u6d77\u6e2f\u4e0e\u6574\u6d01\u8fbd\u9614\u7684\u4e34\u6e2f\u5de5\u4e1a\u533a\u3002</p><p>\u3000\u3000\u5728\u5317\u4eac\u3001\u5728\u5efa\u90ba\u3001\u5728\u6587\u821f\uff0c\u674e\u5728\u6d19\u90fd\u6df1\u523b\u611f\u53d7\u5230\u811a\u4e0b\u8fd9\u4e2a\u56fd\u5bb6\u867d\u7136\u8fd8\u5b58\u5728\u7740\u8fd9\u6837\u90a3\u6837\u7684\u95ee\u9898\uff0c\u5374\u65e0\u6cd5\u5426\u8ba4\u5176\u6b63\u5728\u8fc5\u901f\u5d1b\u8d77\u7684\u4e8b\u5b9e\u3002</p><p>\u3000\u3000\u8eab\u4e3a\u97e9\u56fd\u4eba\uff0c\u662f\u8981\u4e3a\u97e9\u56fd\u8fd1\u4e09\u5341\u5e74\u6765\u7684\u7ecf\u6d4e\u6210\u5c31\u81ea\u8c6a\uff0c\u4f46\u662f\u4e5f\u8981\u770b\u5230\u5f53\u4e2d\u56fd\u771f\u6b63\u5d1b\u8d77\u4e4b\u540e\uff0c\u7ed9\u4e2d\u56fd\u3001\u65e5\u672c\u6324\u5728\u4e1c\u5317\u4e9a\u4e00\u9685\u7684\u97e9\u56fd\uff0c\u5730\u4f4d\u5c31\u4f1a\u76f8\u5f53\u7684\u5c34\u5c2c\u8ddf\u7a98\u8feb\u3002\u4ece\u5730\u7f18\u653f\u6cbb\u4e0a\uff0c\u97e9\u56fd\u662f\u6ca1\u6709\u6210\u4e3a\u5927\u56fd\u7684\u673a\u4f1a\u7684\uff0c\u4f46\u662f\u666e\u901a\u7684\u97e9\u56fd\u9752\u5e74\u6709\u7740\u7ed9\u4e09\u5341\u5e74\u7ecf\u6d4e\u6210\u5c31\u5439\u81a8\u80c0\u7684\u81ea\u4fe1\u5fc3\uff0c\u662f\u7edd\u4e0d\u80af\u627f\u8ba4\u8fd9\u70b9\u7684\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u5fc3\u60c5\u590d\u6742\u7684\u95ed\u4e0a\u773c\u775b\uff0c\u4ece\u4ed6\u4e2a\u4eba\u7684\u611f\u60c5\u51fa\u53d1\uff0c\u4ed6\u662f\u51b3\u4e0d\u5e0c\u671b\u770b\u5230\u4e2d\u56fd\u6709\u5d1b\u8d77\u7684\u4e00\u5929\uff0c\u4f46\u63a9\u8033\u76d7\u94c3\u5f0f\u7684\u5426\u8ba4\u5bf9\u89e3\u51b3\u5b9e\u9645\u95ee\u9898\u6ca1\u6709\u4efb\u4f55\u7684\u5e2e\u52a9\uff0c\u5b9e\u9645\u7684\u60c5\u51b5\uff0c\u4e09\u661f\u7535\u5b50\u5fc5\u987b\u8981\u6b63\u9762\u770b\u5f85\u6765\u81ea\u9526\u6e56\u7684\u7ade\u4e89\u5a01\u80c1\u3002</p><p>\u3000\u3000\u9009\u62e9\u5728\u8fd9\u4e2a\u65f6\u673a\u4e0e\u6587\u821f\u5e02\u653f\u5e9c\u3001\u5b8f\u4fe1\u5408\u8d44\u5efa\u6676\u5706\u5382\uff0c\u770b\u4e0a\u53bb\u6709\u4e9b\u4ed3\u4fc3\uff0c\u4e5f\u662f\u7ecf\u8fc7\u6df1\u601d\u719f\u8651\u7684\u3002\u674e\u5728\u6d19\u77e5\u9053\u53d4\u53d4\u590f\u5929\u5230\u4e2d\u56fd\u6765\u8bbf\u95ee\uff0c\u5bf9\u4e2d\u56fd\u5e02\u573a\u4e00\u76f4\u5f88\u5173\u6ce8\uff0c\u4e5f\u5e94\u8be5\u6709\u8fc7\u6df1\u5165\u7684\u601d\u8003\u3002\u65e0\u8bba\u662f\u5426\u5b9a\u6389\u7531\u4e09\u661f\u7535\u5b50\u76f4\u63a5\u51fa\u9762\u7834\u574f\u9526\u6e56\u4e0e\u5fae\u8f6f\u5408\u4f5c\u7684\u8ba1\u5212\uff0c\u8fd8\u662f\u51b3\u5b9a\u5230\u4e2d\u56fd\u5883\u5185\u6765\u5efa\u6676\u5706\u5382\uff0c\u51b3\u7b56\u7684\u65f6\u95f4\u90fd\u5f88\u77ed\u800c\u4e14\u575a\u51b3\uff0c\u4e5f\u65e0\u7591\u662f\u6b63\u786e\u7684\u3002</p><p>\u3000\u3000\u5408\u8d44\u542f\u52a8\u4e09\u661f\u5b8f\u82af\u7684\u5efa\u8bbe\u8ba1\u5212\uff0c\u4e00\u65b9\u9762\u662f\u53ef\u4ee5\u914d\u5408\u4e09\u4e95\u6324\u5151\u4e2d\u6676\u5fae\u82af\uff0c\u4f7f\u5f97\u4e2d\u6676\u5fae\u82af\u7684\u65b0\u5382\u5efa\u8bbe\u6210\u672c\u4ece\u8bbe\u5907\u91c7\u8d2d\u3001\u878d\u8d44\u3001\u653f\u7b56\u652f\u6301\u3001\u5e02\u573a\u7ade\u4e89\u7b49\u591a\u65b9\u9762\u63d0\u9ad8\u6210\u672c\uff0c\u6700\u597d\u80fd\u8feb\u4f7f\u4e2d\u6676\u5fae\u82af\u653e\u5f03\u65b0\u5382\u5efa\u8bbe\u8ba1\u5212\uff0c\u5373\u4f7f\u4e2d\u6676\u5fae\u82af\u4ecd\u7136\u786c\u7740\u5934\u76ae\u5efa\u65b0\u5382\uff0c\u90a3\u5c31\u4f1a\u6324\u5360\u9526\u6e56\u6295\u5165\u5176\u4ed6\u4ea7\u4e1a\u9886\u57df\u7684\u8d44\u6e90\uff0c\u8fd9\u4e5f\u662f\u6709\u5229\u7684\u3002</p><p>\u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u4e09\u661f\u7535\u5b50\u65e9\u5c31\u51b3\u5b9a\u8981\u5c06\u5236\u9020\u91cd\u5fc3\u9010\u6b65\u7684\u5411\u4e2d\u56fd\u8f6c\u79fb\u964d\u4f4e\u5236\u9020\u6210\u672c\u3002\u53ea\u4e0d\u8fc7\u6676\u5706\u5236\u9020\u5c5e\u4e8e\u7535\u5b50\u4ea7\u4e1a\u7684\u6839\u57fa\uff0c\u8981\u4e0d\u8981\u8f6c\u79fb\uff0c\u4f55\u65f6\u8f6c\u79fb\uff0c\u4e0d\u8981\u8bf4\u4e09\u661f\u5185\u90e8\u6709\u4e89\u8bae\uff0c\u97e9\u56fd\u6c11\u4f17\u4e0e\u653f\u5e9c\u5f53\u5c40\u5bf9\u6b64\u4e5f\u662f\u6709\u4e89\u8bae\u7684\u2014\u2014\u773c\u4e0b\u770b\u6765\uff0c\u8981\u662f\u7ee7\u7eed\u7ea0\u7f20\u4e8e\u8fd9\u4e9b\u4e89\u8bae\u800c\u6ca1\u6709\u5b9e\u9645\u7684\u52a8\u4f5c\uff0c\u4f1a\u4f7f\u4e09\u661f\u5728\u5236\u9020\u6210\u672c\u65b9\u9762\u7684\u52a3\u52bf\u4f1a\u8d8a\u6765\u8d8a\u660e\u663e\u2014\u2014\u8fd9\u624d\u662f\u4e09\u661f\u51b3\u5b9a\u5728\u4e2d\u56fd\u5efa\u6676\u5706\u5382\u7684\u6839\u672c\u539f\u56e0\u3002</p><p>\u3000\u3000\u91d1\u5357\u52c7\u6572\u95e8\u8fdb\u6765\uff0c\u89c1\u674e\u5728\u6d19\u7ad9\u5728\u7a97\u524d\u770b\u6d77\uff0c\u8d70\u8fc7\u6765\u8ddf\u4ed6\u8bf4\u9053\uff1a\u201c\u91d1\u4e66\u8bb0\u4e0e\u97e9\u5e02\u957f\u5c31\u8981\u5230\u9152\u5e97\u4e86\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u4e0b\u53bb\u8fce\u63a5\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u201c\u91d1\u3001\u97e9\u2026\u2026\u201d\u674e\u5728\u6d19\u6de1\u6de1\u4e00\u7b11\uff0c\u5f00\u73a9\u7b11\u7684\u8bf4\u9053\uff0c\u201c\u542c\u4e0a\u53bb\u5012\u662f\u50cf\u6211\u4eec\u97e9\u56fd\u4eba\u3002\u201d</p><p>\u3000\u3000\u91d1\u5357\u52c7\u4e5f\u7b11\u4e86\u7b11\uff0c\u5fc3\u91cc\u60f3\u4e2d\u56fd\u5185\u90e8\u6076\u6027\u7ade\u4e89\u8d77\u6765\u8fd8\u771f\u662f\u8ba9\u4eba\u53f9\u4e3a\u89c2\u6b62\u3002</p><p>\u3000\u3000\u4e4b\u6240\u4ee5\u51b3\u5b9a\u5728\u6587\u821f\u5efa\u5382\u800c\u4e0d\u662f\u5c06\u5382\u5740\u9009\u5728\u5efa\u90ba\uff0c\u662f\u56e0\u4e3a\u6587\u821f\u5e02\u653f\u5e9c\u4e0e\u5b8f\u4fe1\u6295\u8d44\u627f\u8bfa\u7531\u4fe1\u901a\u94f6\u884c\u3001\u6587\u821f\u5e02\u5546\u4e1a\u94f6\u884c\u7b49\u91d1\u878d\u673a\u6784\u4e3a\u5efa\u5382\u6240\u9700\u5de8\u8d44\u63d0\u4f9b\u5168\u90e8\u7684\u4f4e\u606f\u8d37\u6b3e\uff0c\u800c\u4e14\u6587\u821f\u5e02\u653f\u5e9c\u5c06\u5168\u90e8\u627f\u62c5\u914d\u5957\u5de5\u7a0b\u7684\u5efa\u8bbe\u91cd\u4efb\uff0c\u4e09\u661f\u7535\u5b50\u9664\u4e86\u63d0\u4f9b\u6280\u672f\u4e0e\u5de5\u7a0b\u7ba1\u7406\u6280\u672f\u4eba\u5458\u4e4b\u5916\uff0c\u51e0\u4e4e\u4e0d\u7528\u4e3a\u6b64\u627f\u62c5\u4efb\u4f55\u989d\u5916\u7684\u98ce\u9669\u3002\u5b9e\u9645\u4e0a\uff0c\u6240\u8c13\u7684\u4e09\u661f\u5b8f\u82af\u5b9e\u9645\u4e0a\u53ea\u662f\u5c06\u4e09\u661f\u7535\u5b50\u5728\u97e9\u56fd\u6c34\u6e90\u5e02\u4e00\u5bb60.35\u5fae\u7c73\u516b\u82f1\u5bf8\u6676\u5706\u5382\u6574\u4f53\u642c\u8fc1\u5230\u6587\u821f\u6765\uff0c\u8bbe\u5907\u4ef7\u683c\u4e5f\u5c06\u6309\u7167\u65b0\u8bbe\u5907\u65f6\u4ef7\u8ba1\u7b97\uff0c\u97e9\u56fd\u6c34\u6e90\u5de5\u5382\u8bbe\u5907\u6e05\u7a7a\u4e4b\u4e2d\uff0c\u5c31\u53ef\u4ee5\u7ed90.13\u5fae\u7c73\u56db\u6761\u7ebf\u817e\u51fa\u5730\u65b9\u6765\uff0c\u91d1\u5357\u52c7\u4e5f\u5b9e\u5728\u60f3\u4e0d\u51fa\u6709\u4ec0\u4e48\u7406\u7531\u62d2\u7edd\u8ddf\u6587\u821f\u5408\u4f5c\u3002</p><p>\u3000\u3000\u4e5f\u96be\u602a\u674e\u5728\u6d19\u5f00\u73a9\u7b11\u8bf4\u6587\u821f\u5e02\u7684\u5e02\u59d4\u4e66\u8bb0\u91d1\u7acb\u5e73\u3001\u5e02\u957f\u97e9\u8d77\u5e9a\u50cf\u97e9\u56fd\u4eba\u3002</p><p>\u3000\u3000\u201c\u4e25\u6587\u4ecb\u8fd8\u8981\u8fc7\u591a\u4e45\u624d\u4f1a\u8fc7\u6765\uff1f\u201d\u674e\u5728\u6d19\u5c06\u6c99\u53d1\u4e0a\u7684\u897f\u670d\u62ff\u8d77\u6765\u7a7f\u4e0a\uff0c\u51c6\u5907\u4e0e\u91d1\u5357\u52c7\u4e0b\u53bb\u8fce\u63a5\u4e00\u4e0b\u6587\u821f\u7684\u4e3b\u4eba\u3002</p><p>\u3000\u3000\u5408\u8d44\u5efa\u6676\u5706\u5382\u4e00\u4e8b\uff0c\u4e25\u6587\u4ecb\u4e0e\u5b8f\u4fe1\u7cfb\u662f\u6413\u5408\u4eba\uff0c\u674e\u5728\u6d19\u5fc3\u91cc\u6e05\u695a\u5b9e\u9645\u4e0a\u662f\u6c60\u4f50\u79c0\u85cf\u5728\u80cc\u540e\u51fa\u4e3b\u610f\uff0c\u524d\u6bb5\u65f6\u95f4\uff0c\u6c60\u4f50\u79c0\u85cf\u7559\u5728\u5efa\u90ba\u4e0e\u4e25\u6587\u4ecb\u63a5\u89e6\u751a\u5bc6\uff0c\u4e0d\u7136\u4e25\u6587\u4ecb\u600e\u4e48\u53ef\u80fd\u4e00\u4e0b\u5b50\u6293\u5230\u4e09\u661f\u7684\u75d2\u5904\uff1f</p><p>\u3000\u3000\u5408\u8d44\u6676\u5706\u5382\uff0c\u5b8f\u4fe1\u65b9\u9762\u6301\u80a1\u6bd4\u4f8b\u5c06\u8fbe\u523025\uff05\uff0c\u5b8f\u4fe1\u7cfb\u63a7\u5236\u7684\u4fe1\u901a\u94f6\u884c\u8fd8\u5c06\u6676\u5706\u5382\u5efa\u8bbe\u63d0\u4f9b\u540c\u7b49\u6bd4\u4f8b\u7684\u4f4e\u606f\u8d37\u6b3e\uff0c\u8fd9\u5bf9\u5b8f\u4fe1\u7cfb\u6765\u8bf4\uff0c\u4e5f\u662f\u4e00\u9879\u6781\u5408\u7b97\u7684\u4e70\u5356\uff0c\u6295\u8d44\u98ce\u9669\u5b9e\u9645\u4e0a\u8f6c\u5ac1\u7ed9\u4fe1\u901a\u94f6\u884c\u6765\u627f\u62c5\u4e86\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u6587\u821f\u5e02\u653f\u5e9c\u76f4\u63a5\u63a7\u5236\u7684\u6587\u821f\u57ce\u5efa\u6295\u8d44\u96c6\u56e2\u6301\u80a125\uff05\uff0c\u4e09\u661f\u7535\u5b50\u6301\u80a150\uff05\u3002\u540d\u4e49\u4e0a\uff0c\u4e09\u661f\u7535\u5b50\u5bf9\u5408\u8d44\u6676\u5706\u5382\u4e0d\u63a7\u80a1\uff0c\u5b9e\u9645\u4e0a\uff0c\u672a\u6765\u6676\u5706\u5382\u7684\u6574\u4e2a\u6280\u672f\u56e2\u961f\u8ddf\u7ba1\u7406\u5c42\u90fd\u5c06\u53d7\u4e09\u661f\u7535\u5b50\u76f4\u63a5\u63a7\u5236\uff0c\u96be\u9053\u8fd8\u6709\u66f4\u76f4\u63a5\u3001\u66f4\u4e25\u5bc6\u7684\u63a7\u5236\u5417\uff1f</p><p>\u3000\u3000\u201c\u4e25\u6587\u4ecb\u4f1a\u4e0e\u4e25\u4e3b\u4efb\u4e00\u8d77\u8fc7\u6765\uff0c\u53ef\u80fd\u8fd8\u8981\u803d\u6401\u4e00\u4e9b\u65f6\u95f4\u3002\u201d\u91d1\u5357\u52c7\u8bf4\u9053\u3002</p><p>\u3000\u3000\u4e25\u5bb6\u5728\u6d59\u4e1c\u7684\u52bf\u529b\u8fd8\u771f\u662f\u6050\u6016\uff0c\u4e25\u6bc5\u4f5c\u4e3a\u4e25\u6587\u4ecb\u7684\u5802\u5144\u3001\u4e25\u5bbd\u548c\u7684\u4f84\u5b50\uff0c\u66fe\u957f\u671f\u5728\u6587\u821f\u62c5\u4efb\u9886\u5bfc\u804c\u52a1\uff0c\u5b8f\u4fe1\u7cfb\u6d6e\u51fa\u6c34\u9762\u4e4b\u540e\uff0c\u4e25\u6bc5\u8c03\u79bb\u6587\u821f\uff0c\u62c5\u4efb\u56fd\u5bb6\u5de5\u4e1a\u673a\u68b0\u59d4\u5458\u4f1a\u4e3b\u4efb\u4e00\u804c\uff0c\u53c8\u6709\u6d88\u606f\u79f0\u4e25\u6bc5\u5c06\u8fdb\u4e2d\u56fd\u8ba1\u5212\u53d1\u5c55\u59d4\u62c5\u4efb\u5206\u7ba1\u5de5\u4e1a\u65b9\u9762\u7684\u526f\u4e3b\u4efb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u63a5\u5230\u7535\u8bdd\u542c\u8bf4\u91d1\u7acb\u5e73\u4e0e\u97e9\u8d77\u5e9a\u5df2\u7ecf\u5230\u4e86\u89c2\u6d77\u5927\u9152\u5e97\uff0c\u4ed6\u4e5f\u50ac\u4fc3\u53f8\u673a\u5c06\u8f66\u5f00\u5feb\u4e00\u4e9b\uff0c\u6bd5\u7adf\u8ba9\u91d1\u7acb\u5e73\u3001\u674e\u5728\u6d19\u4ed6\u4eec\u7b49\u5019\u592a\u4e45\u6709\u4e9b\u5931\u793c\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u4e09\u661f\u5b8f\u82af\u7684\u6295\u8d44\u8ba1\u5212\uff0c\u4e25\u6587\u4ecb\u8fd8\u662f\u6781\u4e3a\u6ee1\u610f\u7684\u3002\u5b8f\u4fe1\u5b9e\u9645\u4e0a\u65e0\u9700\u76f4\u63a5\u627f\u62c5\u4efb\u4f55\u6295\u8d44\u98ce\u9669\uff0c\u6240\u6709\u8981\u505a\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u6309\u6bd4\u4f8b\u63d0\u4f9b\u4e24\u767e\u4e94\u5341\u4e07\u7f8e\u5143\u7684\u6ce8\u518c\u8d44\u91d1\uff0c\u4e09\u661f\u5b8f\u82af\u4f5c\u4e3a\u4e09\u661f\u7535\u5b50\u5728\u4e2d\u56fd\u76840.35\u5fae\u7c73\u7ebf\u7a0b\u82af\u7247\u751f\u4ea7\u57fa\u5730\uff0c\u5c06\u83b7\u5f97\u4e09\u661f\u7535\u5b50\u7684\u5185\u90e8\u8ba2\u5355\uff0c\u65e0\u6cd5\u62c5\u5fe7\u5916\u90e8\u5e02\u573a\u7684\u6076\u52a3\u72b6\u51b5\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u4e4b\u6240\u4ee5\u80fd\u6d3b\u5f97\u6ecb\u6da6\uff0c\u800c\u534e\u590fNEC\u6295\u4ea7\u540e\u7684\u5e02\u573a\u524d\u666f\u582a\u5fe7\uff0c\u65e0\u975e\u5c31\u662f\u4e2d\u6676\u5fae\u82af\u4ece\u9526\u6e56\u83b7\u5f97\u5927\u91cf\u7684\u5185\u90e8\u8ba2\u5355\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u8fd9\u79cd\u7a33\u8d5a\u4e0d\u8d54\u7684\u4e70\u5356\uff0c\u4e25\u6587\u4ecb\u600e\u4e48\u4f1a\u8ba9\u5b8f\u4fe1\u9519\u8fc7\u5462\uff1f\u5728\u5efa\u90ba\u4e0e\u6c60\u4f50\u79c0\u85cf\u5bc6\u4f1a\u65f6\uff0c\u542c\u5230\u6c60\u4f50\u79c0\u85cf\u5982\u6b64\u7684\u5efa\u8bae\uff0c\u4e25\u6587\u4ecb\u5f53\u65f6\u5c31\u770b\u5230\u91cc\u9762\u9690\u85cf\u7684\u8bf8\u591a\u597d\u5904\u3002</p><p>\u3000\u3000\u867d\u7136\u8bf4\u4e00\u8d77\u56f4\u6bb4\u9526\u6e56\u662f\u8ba9\u4eba\u5fc3\u60c5\u6109\u60a6\uff0c\u4f46\u662f\u4f5c\u4e3a\u5546\u4eba\uff0c\u9996\u5148\u8003\u8651\u7684\u8fd8\u662f\u5229\u76ca\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u5fc3\u91cc\u5f88\u6e05\u695a\uff0c\u5408\u8d44\u540e\u7684\u4e09\u661f\u5b8f\u82af\u5c06\u7ed9\u4e09\u661f\u4e25\u5bc6\u63a7\u5236\uff0c\u4e09\u661f\u6709\u8db3\u591f\u591a\u7684\u8d22\u52a1\u624b\u6bb5\u5c06\u4e09\u661f\u7684\u5229\u6da6\u6700\u5927\u5316\uff0c\u867d\u7136\u5bf9\u5b8f\u4fe1\u6765\u8bf4\u662f\u7a33\u8d5a\u4e0d\u8d54\u7684\u4e70\u5356\uff0c\u5b9e\u9645\u6240\u8d5a\u4e5f\u662f\u6781\u4e3a\u6709\u9650\u7684\u3002\u5c31\u8fd9\u70b9\u5229\u6da6\u5b9e\u5728\u503c\u4e0d\u5f97\u4e3a\u6b64\u5927\u52a8\u5e72\u6208\uff0c\u4f46\u662f\u4e09\u661f\u5b8f\u82af\u91cc\u5f52\u5b8f\u4fe125\uff05\u7684\u80a1\u6743\u7531\u5b8f\u4fe1\u63a7\u5236\u7684\u6d77\u7c9f\u79d1\u6280\u6765\u6301\u6709\uff0c\u53c8\u4f1a\u4ea7\u751f\u600e\u6837\u7684\u8fde\u9501\u53cd\u5e94\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u91d1\u5c71\u6c81\u56ed\uff0c\u996e\u9a6c\u6cb3\u7554\u7684\u8c6a\u534e\u5c45\u5ba4\u91cc\uff0c\u5f20\u606a\u5728\u4e66\u623f\uff0c\u773c\u775b\u76ef\u7740\u7535\u8111\u5c4f\u5e55\u4e0a\u7684\u80a1\u7968\u8d70\u52bf\u56fe\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u968f\u5373\u8d70\u5230\u7a97\u8fb9\uff0c\u770b\u7740\u5915\u9633\u4e0b\u7684\u996e\u9a6c\u6cb3\u9762\u4e0a\u95ea\u8000\u7740\u7cbc\u7cbc\u6ce2\u5149\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u4e8c\u817f\u9ad8\u8df7\u7684\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u8ddf\u7ec5\u58eb\u4f3c\u7684\u62bd\u7740\u70df\uff0c\u9887\u4e3a\u5f97\u610f\u7684\u5728\u90a3\u91cc\u5410\u70df\u5708\uff0c\u89c1\u5f20\u606a\u773c\u775b\u79bb\u5f00\u7535\u8111\u5c4f\u5e55\uff0c\u624d\u8ddf\u4ed6\u8bf4\u9053\uff1a\u201c\u4e25\u6587\u4ecb\u8fd9\u4e2a\u4eba\u7684\u8111\u5b50\u662f\u6781\u806a\u660e\u7684\uff0c\u8111\u5b50\u6bd4\u4ed6\u66f4\u597d\u4f7f\u7684\uff0c\u6211\u770b\u4e5f\u4e0d\u591a\u89c1\u4e86\u2026\u2026\u201d\u4ed6\u8fd8\u4f38\u51fa\u624b\u6307\u6bd4\u5212\u7740\u6307\u4e86\u6307\u592a\u9633\u7a74\u3002</p><p>\u3000\u3000\u5f20\u606a\u8f6c\u8fc7\u8eab\uff0c\u9760\u7740\u7a97\u53f0\u800c\u7ad9\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4ed6\u7684\u91ce\u5fc3\u4e5f\u5f88\u5927\uff0c\u552f\u4e00\u7f3a\u70b9\u5c31\u662f\u592a\u8d2a\u5fc3\u4e86\uff0c\u4e5f\u4e0d\u77e5\u9053\u7559\u4e9b\u5e95\u9650\u3002\u201d</p><p>\u3000\u3000\u201c\u4ed6\u5927\u6982\u4ee5\u4e3a\u4ee5\u56db\u4e24\u62e8\u5343\u65a4\u4e4b\u672f\u771f\u7684\u80fd\u5c06\u6d77\u7c9f\u79d1\u6280\u6253\u9020\u6210\u91d1\u5149\u95ea\u95ea\u7684\u9f99\u5934\u79d1\u6280\u4f01\u4e1a\u3002\u201d\u53f6\u5efa\u658c\u4e0d\u5c51\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u6caa\u6df1\u4e24\u5e02\u4eca\u5929\u4f9d\u65e7\u5728\u632f\u8361\u8c03\u6574\uff0c\u7531\u4e8e\u6709\u6d88\u606f\u79f0\u5b8f\u4fe1\u6295\u8d44\u5c06\u8ba9\u6d77\u7c9f\u79d1\u6280\u51fa\u9762\u53c2\u4e0e\u4e09\u661f\u5b8f\u82af\u7684\u5408\u8d44\u5efa\u5382\u8ba1\u5212\uff0c\u4eca\u5929\u4e00\u5f00\u76d8\u80a1\u4ef7\u5c31\u7ed9\u5929\u91cf\u4e70\u5355\u9876\u5230\u6da8\u505c\u677f\u3002\u7531\u4e8e\u6d77\u7c9f\u79d1\u6280\u4e09\u5b63\u5ea6\u65f6\u5b9e\u884c\u4e86\u8d60\u80a1\u914d\u9001\u65b9\u6848\uff0c\u8868\u9762\u4e0a\u770b\u8fd1\u4e24\u4e2a\u6708\u6765\u80a1\u4ef7\u662f\u4e0b\u964d\u4e86\uff0c\u5b9e\u9645\u7684\u5e02\u503c\u53c8\u589e\u6da8\u4e86\u8fd1\u4e00\u500d\u3002</p><p>\u3000\u3000\u201c\u5509\uff0c\u201d\u5f20\u606a\u6447\u5934\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u5929\u8981\u4e0b\u96e8\uff0c\u5a18\u8981\u5ac1\u4eba\uff0c\u968f\u4ed6\u4eec\u53bb\u6298\u817e\u5427\uff0c\u53ea\u8981\u7834\u574f\u89c4\u5219\u4e0d\u7834\u574f\u5230\u9526\u6e56\u5934\u4e0a\uff0c\u6211\u4eec\u8fd8\u80fd\u4f38\u51fa\u624b\u53bb\u963b\u6b62\u4ed6\u4eec\uff1f\u201d</p><p>\u3000\u3000\u5f88\u663e\u7136\uff0c\u6309\u7167\u6b63\u5e38\u7684\u6e38\u620f\u89c4\u5219\uff0c\u6d77\u7c9f\u79d1\u6280\u662f\u6ca1\u6709\u8d44\u683c\u53c2\u4e0e\u6676\u5706\u5382\u5982\u6b64\u91cd\u5927\u9879\u76ee\u7684\u6295\u8d44\uff0c\u81f3\u5c11\u8d44\u672c\u5b9e\u529b\u8fdc\u8fdc\u4e0d\u591f\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u65b0\u6d66\u5de5\u5382\u5efa\u8bbe\u8d44\u91d1\uff0c\u9664\u4e86\u6765\u81ea\u91d1\u878d\u673a\u6784\u3001\u94f6\u884c\u8d22\u56e2\u7684\u5de8\u989d\u878d\u8d44\u4e4b\u5916\uff0c\u9526\u6e56\u4e0e\u5176\u4ed6\u80a1\u4e1c\u5171\u540c\u627f\u62c5\u4e8650\uff05\u7ea6\u516d\u4ebf\u7f8e\u5143\u7684\u51fa\u8d44\u4e49\u52a1\u3002\u51fa\u73b0\u4e8f\u635f\u6216\u5176\u4ed6\u7ecf\u8425\u6027\u98ce\u9669\uff0c\u4e5f\u5c06\u9996\u5148\u7531\u80a1\u4e1c\u51fa\u8d44\u90e8\u5206\u6765\u627f\u62c5\uff1b\u4e00\u822c\u8bf4\u6765\uff0c\u51fa\u73b0\u7ecf\u8425\u98ce\u9669\uff0c\u80a1\u6743\u635f\u5931\u8981\u8fdc\u8fdc\u8d85\u8fc7\u503a\u6743\u635f\u5931\u3002</p><p>\u3000\u3000\u4e09\u661f\u5b8f\u82af\u5374\u5c06\u6295\u8d44\u98ce\u9669\u5b8c\u5168\u8f6c\u5ac1\u7ed9\u91d1\u878d\u673a\u6784\u4e86\uff0c\u5408\u8d44\u80a1\u4e1c\u6839\u672c\u5c31\u6ca1\u6709\u5f80\u91cc\u6295\u591a\u5c11\u8d44\u91d1\uff0c\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u53ef\u4ee5\u635f\u5931\u7684\uff0c\u6d77\u7c9f\u79d1\u6280\u4e5f\u624d\u80fd\u4ee5\u56db\u4e24\u62e8\u5343\u65a4\u4e4b\u672f\u53c2\u4e0e\u4e09\u661f\u5b8f\u82af\u7684\u5408\u8d44\uff0c\u53ea\u63d0\u4f9b\u4e24\u767e\u4e94\u5341\u4e07\u7f8e\u5143\u7684\u6ce8\u518c\u8d44\u91d1\u5c31\u6301\u6709\u4e09\u661f\u5b8f\u82af25\uff05\u7684\u80a1\u6743\u3002</p><p>\u3000\u3000\u4e09\u661f\u5b8f\u82af\u7684\u5408\u8d44\u65b9\u6848\u867d\u8bf4\u662f\u7834\u574f\u4e86\u6e38\u620f\u89c4\u5219\u7684\u5e95\u9650\uff0c\u4f46\u662f\u6709\u6d59\u4e1c\u7701\u4e0e\u6587\u821f\u5e02\u5730\u65b9\u653f\u5e9c\u5728\u80cc\u540e\u529b\u633a\uff0c\u5f20\u606a\u4ed6\u4eec\u5c31\u662f\u60f3\u641e\u7834\u574f\uff0c\u4e5f\u6709\u97ad\u957f\u83ab\u53ca\u7684\u9057\u61be\u3002</p><p>\u3000\u3000\u957f\u8fdc\u4ee5\u6765\uff0c\u6587\u821f\u7684\u7ecf\u6d4e\u53d1\u5c55\u5728\u534e\u4e1c\u5730\u533a\u90fd\u662f\u9996\u5c48\u4e00\u6307\u7684\uff0c\u6d59\u4e1c\u7701\u7ecf\u6d4e\u5728\u4e5d\u4e03\u3001\u4e5d\u516b\u5e74\u4e4b\u524d\u4e5f\u662f\u6574\u4f53\u4f18\u4e8e\u4e1c\u6d77\u7684\u3002\u60e0\u5c71\u3001\u5efa\u90ba\u3001\u6d77\u5dde\u8fd1\u51e0\u5e74\u7684\u5d1b\u8d77\uff0c\u7279\u522b\u662f\u5728\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u4e0a\u7684\u5f3a\u52bf\u5d1b\u8d77\uff0c\u5c06\u4e1c\u6d77\u7701\u7ecf\u6d4e\u53d1\u5c55\u6c34\u5e73\u63d0\u9ad8\u5230\u4e00\u4e2a\u5168\u65b0\u7684\u9636\u6bb5\uff0c\u4ee4\u6d59\u4e1c\u7701\u6709\u4e9b\u9eef\u7136\u5931\u8272\u3002\u53e6\u5916\uff0c\u65b0\u4ead\u7684\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u6b63\u5982\u706b\u5982\u837c\u7684\u5c55\u5f00\uff0c\u4e5f\u5c06\u5206\u6563\u6389\u5305\u62ec\u6587\u821f\u5728\u5185\u6d59\u4e1c\u7701\u8bf8\u591a\u6cbf\u6d77\u57ce\u5e02\u5728\u6d77\u6e2f\u4ea7\u4e1a\u9886\u57df\u7684\u7ade\u4e89\u4f18\u52bf\u3002</p><p>\u3000\u3000\u5728\u8fd9\u79cd\u80cc\u666f\u4e0b\uff0c\u6d59\u4e1c\u5730\u65b9\u653f\u5e9c\u9965\u4e0d\u62e9\u98df\u7684\u51fa\u9762\u4e0e\u4e09\u661f\u7535\u5b50\u6572\u5b9a\u4e09\u661f\u5b8f\u82af\u7684\u5408\u8d44\u65b9\u6848\uff0c\u4e5f\u5c31\u4e0d\u96be\u7406\u89e3\u4e86\uff1b\u4f5c\u4e3a\u6d59\u4e1c\u7701\u4f20\u7edf\u7684\u5730\u65b9\u52bf\u529b\u4ee3\u8868\uff0c\u5b8f\u4fe1\u4ece\u4e2d\u635e\u4fbf\u5b9c\uff0c\u66f4\u4e0d\u662f\u4ec0\u4e48\u96be\u4ee5\u7406\u89e3\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5085\u4fca\u6572\u95e8\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4e66\u8bb0\u5230\u9152\u5e97\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u8d75\u6709\u4f26\u6765\u4e86\uff0c\u201d\u53f6\u5efa\u658c\u8fd9\u624d\u4ece\u6c99\u53d1\u4e0a\u7ad9\u8d77\u6765\uff0c\u5c06\u8eab\u4e0a\u7684\u70df\u7070\u62cd\u6389\uff0c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4ed6\u5927\u6982\u662f\u8981\u6e38\u8bf4\u6211\u4eec\u5c06\u4e00\u5ea7\u6676\u5706\u5382\u5efa\u5728\u91d1\u5c71\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u60f3\u6211\u4eec\u7ed9\u4e09\u4e95\u3001\u4e09\u661f\u3001\u53f0\u79ef\u7535\u3001\u534e\u590f\u7535\u5b50\u3001\u5b8f\u4fe1\u8054\u5408\u8d77\u6765\u56f4\u6bb4\u7684\u51c4\u60e8\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8bed\u6c14\u8f7b\u677e\uff0c\u4f46\u5e76\u4e0d\u610f\u5473\u7740\u9526\u6e56\u6b64\u65f6\u7684\u5904\u5883\u8f7b\u677e\u3002</p><p>\u3000\u3000\u4e9a\u6d32\u6676\u5706\u4f01\u4e1a\u4ea7\u80fd\u5927\u6269\u5f20\uff0c\u6d88\u606f\u4e00\u7ecf\u5ba3\u5e03\uff0c\u6b27\u7f8e\u6676\u5706\u5236\u9020\u8bbe\u5907\u4ef7\u683c\u5c31\u7acb\u7aff\u89c1\u5f71\u7684\u5927\u5e45\u4e0a\u626c\uff0c\u800c\u6676\u5706\u5382\u7684\u4e3b\u8981\u6295\u8d44\u5c31\u662f\u8fd9\u4e9b\u9020\u4ef7\u6602\u8d35\u7684\u5236\u9020\u8bbe\u5907\uff0c\u4e2d\u6676\u5fae\u82af\u88ab\u8feb\u653e\u5f03\u539f\u6709\u7684\u5efa\u5382\u8ba1\u5212\u3002\u5177\u4f53\u4fee\u6b63\u5efa\u5382\u8ba1\u5212\uff0c\u8fd8\u8981\u4e0e\u5fb7\u4eea\u3001\u4e91\u6e90\u4ee5\u53ca\u65b0\u52a0\u5761\u521b\u65b0\u7b49\u5176\u4ed6\u80a1\u4e1c\u5750\u4e0b\u6765\u6162\u6162\u5546\u8bae\u3002</p><p>\u3000\u3000\u5c31\u50cf\u5fae\u8f6f\u4e3a\u4e86\u4ea7\u4e1a\u5e03\u5c40\u7684\u6574\u4f53\u5229\u76ca\u4e0d\u4f1a\u4ecb\u610f\u591a\u638f\u51e0\u4ebf\u7f8e\u5143\u8d2d\u4e70\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7684\u80a1\u6743\uff0c\u5fb7\u4eea\u4e5f\u4e0d\u4f1a\u4ecb\u610f\u4e3a\u65b0\u5382\u5efa\u8bbe\u591a\u627f\u62c5\u4e00\u4e24\u4ebf\u7f8e\u5143\u7684\u51fa\u8d44\u4efb\u52a1\uff1b\u90ed\u6c0f\u4e0e\u9526\u6e56\u5173\u7cfb\u5bc6\u5207\uff0c\u4f46\u662f\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u6bd5\u7adf\u8fd8\u662f\u5bb6\u65cf\u4f01\u4e1a\u6027\u8d28\uff0c\u771f\u6b63\u7684\u8d44\u672c\u5b9e\u529b\u6709\u9650\uff0c\u591a\u627f\u62c5\u4e00\u4e24\u4ebf\u7f8e\u5143\u7684\u51fa\u8d44\uff0c\u5c31\u4f1a\u4e25\u91cd\u5f71\u54cd\u5230\u5176\u5728\u5176\u4ed6\u9886\u57df\u7684\u6295\u8d44\u8ba1\u5212\u2026\u2026</p><p>\u3000\u3000\u201c\u522b\u4eba\u6765\u56f4\u6bb4\uff0c\u6211\u4eec\u8981\u7a81\u56f4\uff0c\u771f\u662f\u6709\u8da3\u5f97\u7d27\u554a\uff0c\u201d\u5f20\u606a\u6447\u5934\u82e6\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u53bb\u8bf7\u90ed\u5148\u751f\uff0c\u4f60\u5148\u53bb\u5e94\u4ed8\u8d75\u6709\u4f26\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u8d70\u5230\u8d70\u5eca\u4e0a\uff0c\u90ed\u677e\u5ef6\u4e5f\u6b63\u4e0e\u59bb\u5b50\u4ece\u623f\u95f4\u91cc\u8d70\u51fa\u6765\uff0c\u4ed6\u521a\u4e0b\u98de\u673a\u65f6\uff0c\u8840\u538b\u6709\u4e9b\u9ad8\uff0c\u4e5f\u8fd9\u4e24\u5929\u5230\u5904\u5954\u6ce2\u6240\u7d2f\uff0c\u5230\u6c81\u56ed\u5c31\u5148\u56de\u623f\u4f11\u606f\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd9\u65f6\u5019\u6062\u590d\u7cbe\u795e\u6765\uff0c\u770b\u5230\u5f20\u606a\uff0c\u8bf4\u9053\uff1a\u201c\u65b0\u52a0\u5761\u65b9\u9762\u6709\u9000\u7f29\u4e4b\u610f\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8868\u793a\u7406\u89e3\uff1a\u201c\u82af\u7247\u5e02\u573a\u524d\u666f\u4e0d\u660e\uff0c\u4e9a\u6d32\u5e02\u573a\u7684\u82af\u7247\u4ea7\u80fd\u53c8\u5c06\u5de8\u589e\uff0c\u5728\u6295\u8d44\u9884\u7b97\u6709\u53ef\u80fd\u5927\u5e45\u4e0a\u6da8\u4e4b\u65f6\uff0c\u4fdd\u5b88\u7684\u6295\u8d44\u59ff\u6001\u662f\u6070\u5f53\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u867d\u7136\u80fd\u591f\u7406\u89e3\uff0c\u53ea\u662f\u4e5f\u9887\u4e3a\u68d8\u624b\u554a\u3002\u201d\u90ed\u677e\u5ef6\u5fae\u5fae\u4e00\u53f9\uff0c\u4e3a\u4e86\u8fd9\u4e8b\uff0c\u5b59\u5c1a\u4e49\u8fd8\u7559\u5728\u65b0\u52a0\u5761\u4e0e\u6709\u5173\u65b9\u9762\u78cb\u5546\uff0c\u65b0\u52a0\u5761\u65b9\u9762\u9000\u7f29\uff0c\u6309\u7167\u4e4b\u524d\u7684\u5efa\u5382\u9884\u7b97\u5c31\u5c06\u76f4\u63a5\u4ea7\u751f\u8fd1\u56db\u4ebf\u7f8e\u5143\u7684\u6295\u8d44\u7f3a\u53e3\uff0c\u8bbe\u5907\u4ef7\u683c\u5927\u5e45\u4e0a\u6da8\u540e\uff0c\u6295\u8d44\u7f3a\u53e3\u5f88\u53ef\u80fd\u6269\u5927\u5230\u516d\u4ebf\u7f8e\u5143\uff0c\u5012\u4e0d\u662f\u4e00\u822c\u7684\u68d8\u624b\u3002</p><p>\u3000\u3000\u201c\u6211\u5728\u60f3\u6709\u6ca1\u6709\u8fd9\u79cd\u53ef\u80fd\u2026\u2026\u4e2d\u6676\u5fae\u82af\u8fd0\u884c\u4e00\u5e74\u4ee5\u6765\uff0c\u5b9e\u9645\u4e5f\u8bc1\u660e\u4e86\u9526\u6e56\u7684\u5185\u90e8\u8ba2\u5355\u80fd\u591f\u4fdd\u8bc1\u76c8\u5229\u8fd0\u8425\uff0c\u65b0\u52a0\u5761\u65b9\u9762\u6240\u6301\u7684\u80a1\u6743\u53ef\u4e0d\u53ef\u4ee5\u8f6c\u7531\u7279\u8bb8\u534a\u5bfc\u4f53\u76f4\u63a5\u6301\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u7279\u8bb8\u5df2\u7ecf\u6301\u7eed\u516d\u4e2a\u5b63\u5ea6\u4e8f\u635f\uff0c\u6ca1\u6709\u65b0\u52a0\u5761\u91d1\u7ba1\u5c40\u5728\u80cc\u540e\u652f\u6301\uff0c\u7279\u8bb8\u65e2\u6ca1\u6709\u80fd\u529b\u63a5\u624b\u65b0\u52a0\u5761\u91d1\u7ba1\u5c40\u6301\u6709\u7684\u4e2d\u6676\u5fae\u82af\u7684\u80a1\u6743\uff0c\u66f4\u662f\u6ca1\u6709\u80fd\u529b\u627f\u62c5\u5bf9\u65b0\u5382\u7684\u51fa\u8d44\u4efb\u52a1\u2026\u2026\u201d</p><p>\u3000\u3000\u65b0\u52a0\u5761\u7279\u8bb8\u534a\u5bfc\u4f53\u662f\u5168\u7403\u7b2c\u56db\u5927\u82af\u7247\u4ee3\u5de5\u5382\u5546\uff0c\u53ea\u662f\u53d7\u65b0\u52a0\u5761\u5730\u65b9\u72ed\u5c0f\u3001\u4eba\u529b\u8d44\u6e90\u6709\u9650\u3001\u7535\u5b50\u4ea7\u4e1a\u683c\u5c40\u3001\u89c4\u6a21\u8fdc\u4e0d\u53ca\u65e5\u97e9\u53ca\u53f0\u6e7e\u7684\u9650\u5236\uff0c\u5728\u4ee3\u5de5\u5e02\u573a\u7684\u7ade\u4e89\u529b\u8fdc\u8fdc\u4f4e\u4e8e\u53f0\u79ef\u7535\u3002\u7279\u8bb8\u7684\u591a\u5bb6\u6676\u5706\u5382\u5efa\u6210\u4e4b\u540e\uff0c\u5373\u4f7f\u6709\u76c8\u5229\u4e5f\u662f\u5fae\u5229\uff0c\u5927\u591a\u6570\u65f6\u95f4\u90fd\u662f\u4e8f\u635f\uff0c\u4ece\u4e5d\u4e03\u5e74\u4e0b\u534a\u5e74\u4e4b\u540e\uff0c\u66f4\u662f\u53d7\u5230\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u76f4\u63a5\u51b2\u51fb\uff0c\u65e5\u97e9\u8d27\u5e01\u6c47\u7387\u5927\u5e45\u4e0b\u632b\uff0c\u4e00\u65b9\u9762\u4f7f\u65e5\u97e9\u7ecf\u6d4e\u8499\u53d7\u635f\u5931\uff0c\u53e6\u4e00\u65b9\u9762\u523a\u6fc0\u4e86\u65e5\u97e9\u51fa\u53e3\u52a0\u5de5\u4ea7\u4e1a\u7684\u53d1\u5c55\uff0c\u5728\u82af\u7247\u4ee3\u5de5\u5e02\u573a\uff0c\u7279\u8bb8\u534a\u5bfc\u4f53\u66f4\u5904\u4e8e\u52a3\u52bf\u3002\u5373\u4f7f\u5f53\u524d\u65b0\u79d1\u6280\u6d6a\u6f6e\u6c79\u6d8c\uff0c\u4e5f\u8fdc\u8fdc\u672a\u80fd\u8ba9\u7279\u8bb8\u6062\u590d\u5143\u6c14\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e0e\u65b0\u52a0\u5761\u5728\u4e2d\u6676\u5fae\u82af\u4e0a\u7684\u5408\u4f5c\uff0c\u5b9e\u9645\u4e0a\u662f\u8ddf\u7279\u8bb8\u534a\u5bfc\u4f53\u5408\u4f5c\uff0c\u6700\u521d0.35\u5fae\u7c73\u7684\u6280\u672f\u4e3b\u8981\u6765\u81ea\u7279\u8bb8\u534a\u5bfc\u4f53\uff0c\u53ea\u662f\u7279\u8bb8\u7684\u8d22\u52a1\u72b6\u51b5\u582a\u5fe7\uff0c\u5f53\u521d\u624d\u7531\u65b0\u52a0\u5761\u91d1\u7ba1\u5c40\u51fa\u9762\u76f4\u63a5\u5bf9\u4e2d\u6676\u5fae\u82af\u6295\u8d44\u6301\u80a1\u3002</p><p>\u3000\u3000\u7279\u8bb8\u7684\u8d22\u52a1\u72b6\u51b5\u6bd4\u4e24\u5e74\u524d\u66f4\u52a0\u7684\u4ee4\u4eba\u582a\u5fe7\uff0c\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\uff0c\u66f4\u52a0\u6ca1\u6709\u76f4\u63a5\u5bf9\u4e2d\u6676\u5fae\u82af\u6301\u80a1\u7684\u53ef\u80fd\uff0c\u90ed\u677e\u5ef6\u7591\u60d1\u7684\u770b\u7740\u5f20\u606a\uff0c\u4e0d\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u5efa\u8bae\u7531\u7279\u8bb8\u534a\u5bfc\u4f53\u6765\u76f4\u63a5\u6301\u80a1\u3002</p><p>\u3000\u3000\u201c\u73b0\u5728\u5927\u5bb6\u90fd\u5728\u56f4\u6bb4\u4e2d\u6676\u5fae\u82af\uff0c\u201d\u5f20\u606a\u8bf7\u90ed\u677e\u5ef6\u8d70\u5728\u524d\u9762\uff0c\u8fb9\u8d70\u8fb9\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6211\u60f3\u7279\u8bb8\u534a\u5bfc\u4f53\u8fd9\u4e24\u5e74\u6765\u65e5\u5b50\u4e5f\u4e0d\u597d\u8fc7\u3002\u4e9a\u6d32\u82af\u7247\u4ea7\u80fd\u660e\u540e\u5e74\u5c31\u8981\u6fc0\u589e\uff0c\u5e02\u573a\u524d\u666f\u5374\u4e0d\u660e\u6717\uff0c\u7279\u8bb8\u534a\u5bfc\u4f53\u660e\u540e\u5e74\u7684\u65e5\u5b50\u4f1a\u66f4\u52a0\u4e0d\u597d\u8fc7\uff0c\u7279\u522b\u662f0.25\u5fae\u7c73\u5236\u7a0b\u7684\u82af\u7247\u4ee3\u5de5\u5e02\u573a\uff0c\u7279\u8bb8\u7684\u7ade\u4e89\u529b\u5c06\u66f4\u5dee\uff0c\u5c31\u7b97\u5224\u65ad\u660e\u540e\u5e74\u7279\u8bb8\u5728\u8fd9\u4e00\u5757\u5e02\u573a\u5f7b\u5e95\u4e27\u5931\u7ade\u4e89\u529b\u4e5f\u4e0d\u4e3a\u8fc7\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u90ed\u677e\u5ef6\u6293\u4f4f\u5f20\u606a\u601d\u8def\u4e2d\u7684\u8981\u70b9\uff0c\u624b\u6307\u4e3e\u5230\u773c\u524d\u6325\u52a8\u7740\uff0c\u201c\u7279\u8bb8\u8981\u907f\u514d\u6301\u7eed\u5de8\u989d\u4e8f\u635f\uff0c\u5173\u95ed\u7ade\u4e89\u529b\u5dee\u7684\u751f\u4ea7\u7ebf\u662f\u660e\u667a\u7684\u9009\u62e9\u2026\u2026\u7279\u8bb8\u867d\u7136\u6ca1\u6709\u80fd\u529b\u76f4\u63a5\u62ff\u51fa\u6570\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u4f60\u662f\u4e0d\u662f\u60f3\u8ba9\u7279\u8bb8\u5c06\u5173\u95ed\u7684\u751f\u4ea7\u7ebf\u8bbe\u5907\u8fd0\u5230\u56fd\u5185\u6765\u4f5c\u4e3a\u5bf9\u4e2d\u6676\u5fae\u82af\u7684\u51fa\u8d44\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u80af\u5b9a\u4e86\u90ed\u677e\u5ef6\u7684\u731c\u6d4b\uff0c\u201c\u4e00\u65b9\u9762\u65b0\u8bbe\u5907\u4ef7\u683c\u66b4\u6da8\uff0c\u6da8\u5f97\u8ba9\u4eba\u5fc3\u75db\uff0c\u6211\u4e5f\u4e0d\u60f3\u5728\u8fd9\u4e0a\u9762\u5403\u5927\u4e8f\uff1b\u4e00\u65b9\u9762\uff0c\u7279\u8bb8\u5c06\u7ade\u4e89\u529b\u5dee\u3001\u957f\u671f\u4e8f\u635f\u7684\u751f\u4ea7\u7ebf\u8f6c\u79fb\u7ed9\u4e2d\u6676\u5fae\u82af\u5c31\u80fd\u8f6c\u5316\u6210\u80fd\u76c8\u5229\u7684\u6295\u8d44\u2014\u2014\u6211\u5b9e\u5728\u60f3\u8c61\u4e0d\u51fa\u8fd8\u6709\u6bd4\u8fd9\u66f4\u53cc\u8d62\u7684\u65b9\u6848\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u90ed\u677e\u5ef6\u91cd\u91cd\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u8fd9\u4e2a\u65b9\u6848\u5927\u597d\uff0c\u5f88\u6709\u5e0c\u671b\u8bf4\u670d\u65b0\u52a0\u5761\u5f53\u5c40\u2026\u2026\u4f60\u600e\u4e48\u4e0d\u65e9\u4e9b\u544a\u8bc9\u6211\uff0c\u6211\u4e5f\u6ca1\u6709\u5fc5\u8981\u5fe7\u5fc3\u5f97\u5750\u98de\u673a\u8840\u538b\u5347\u9ad8\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u770b\u5230\u4e09\u661f\u5b8f\u82af\u7684\u65b9\u6848\u4e4b\u540e\u624d\u6709\u8fd9\u6837\u7684\u60f3\u6cd5\uff0c\u6709\u8fd9\u4e2a\u60f3\u6cd5\u65f6\uff0c\u90ed\u5148\u751f\u4f60\u53ef\u662f\u5728\u98de\u673a\u4e0a\u554a\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4e09\u661f\u5b8f\u82af\u7684\u5408\u8d44\u65b9\u6848\uff0c\u90ed\u5148\u751f\u6709\u6ca1\u6709\u770b\u5230\uff1f\u8fd9\u4e2a\u65b9\u6848\u5b9e\u9645\u4e0a\u5c31\u662f\u4e09\u661f\u7535\u5b50\u5c06\u56fd\u5185\u7ade\u4e89\u529b\u5dee\u7684\u6676\u5706\u751f\u4ea7\u7ebf\u8f6c\u79fb\u5230\u4e2d\u56fd\u6765\u2026\u2026\u6211\u60f3\u6709\u4e09\u661f\u7535\u5b50\u7684\u524d\u4f8b\uff0c\u65b0\u52a0\u5761\u65b9\u9762\u4f1a\u66f4\u5bb9\u6613\u63a5\u53d7\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u4e09\u661f\u5b8f\u82af\u7684\u5408\u8d44\u65b9\u6848\uff0c\u6211\u8fd8\u6ca1\u6709\u770b\u5230\u2026\u2026\u201d\u90ed\u677e\u5ef6\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u5085\u4fca\u56de\u623f\u95f4\u5c06\u4e09\u661f\u5b8f\u82af\u7684\u5408\u8d44\u65b9\u6848\u62ff\u8fc7\u6765\u7ed9\u90ed\u677e\u5ef6\u770b\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u53e6\u5916\uff0c\u5c31\u6211\u6240\u77e5\uff0c\u65b0\u52a0\u5761\u5f53\u5c40\u73b0\u5728\u8fd8\u6ca1\u6709\u653e\u5f03\u5bf9\u7279\u8bb8\u652f\u6301\u7684\u610f\u601d\uff0c\u4f46\u662f\u7279\u8bb8\u8981\u652f\u6491\u4e0b\u53bb\uff0c\u5c31\u5fc5\u987b\u786c\u7740\u5934\u76ae\u4e0a0.13\u5fae\u7c73\u7ea7\u7684\u751f\u4ea7\u7ebf\uff0c\u624d\u4e0d\u81f3\u4e8e\u7ed9\u53f0\u79ef\u7535\u3001\u7ed9\u65e5\u97e9\u6676\u5706\u5382\u5f7b\u5e95\u538b\u5236\u2026\u2026\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u7279\u8bb8\u66f4\u9700\u8981\u5c06\u7ade\u4e89\u529b\u5dee\u7684\u751f\u4ea7\u7ebf\u8f6c\u79fb\u6389\u5316\u89e3\u7ecf\u8425\u4e0a\u7684\u538b\u529b\uff0c\u65b0\u52a0\u5761\u5f53\u5c40\u8981\u662f\u613f\u610f\uff0c0.18\u5fae\u7c73\u7684\u751f\u4ea7\u7ebf\uff0c\u6211\u4eec\u4e5f\u53ef\u4ee5\u5750\u4e0b\u6765\u8c08\u2026\u2026\u201d</p><p>\u3000\u3000\u201c0.18\u5fae\u7c73\u6709\u4e9b\u96be\uff0c\u7279\u8bb8\u5728\u8fd9\u4e0a\u9762\u8fd8\u662f\u6709\u4e9b\u76c8\u5229\u7684\u2026\u2026\u201d\u90ed\u677e\u5ef6\u8bf4\u9053\uff0c\u4ed6\u60f3\u4e86\u4e00\u4f1a\u513f\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u5f39\u4e38\u4e4b\u5730\uff0c\u4efb\u4f55\u4e2d\u4f4e\u7aef\u5e02\u573a\u4e0a\u7684\u7ade\u4e89\uff0c\u90fd\u8981\u53d7\u5230\u672c\u571f\u5e02\u573a\u72ed\u5c0f\u3001\u4eba\u529b\u8d44\u6e90\u6210\u672c\u8fc7\u9ad8\u7684\u4e25\u91cd\u9650\u5236\uff0c\u53ea\u6709\u5728\u4e2d\u9ad8\u7aef\u5e02\u573a\uff0c\u65b0\u52a0\u5761\u7684\u7535\u5b50\u4f01\u4e1a\u80fd\u591f\u51ed\u501f\u8d44\u672c\u3001\u5148\u8fdb\u6280\u672f\u4e0a\u7684\u4f18\u52bf\uff0c\u4fdd\u6301\u4e00\u5b9a\u7684\u7ade\u4e89\u529b\u3002</p><p>\u3000\u3000\u5b9e\u9645\u4e0a\uff0c\u7279\u8bb8\u4eca\u540e\u4e5f\u5b9e\u9645\u9009\u62e9\u8d70\u4e0a\u4e00\u6761\u653e\u5f03\u4e2d\u4f4e\u7aef\u5e02\u573a\u3001\u4ee5\u5148\u8fdb\u5236\u7a0b\u6280\u672f\u4fdd\u6301\u7ade\u4e89\u529b\u7684\u9053\u8def\u3002\u5373\u4f7f\u5982\u6b64\uff0c\u7279\u8bb8\u7684\u53d1\u5c55\u9053\u8def\u4e5f\u8d70\u5f97\u6781\u4e3a\u8270\u96be\u4e0e\u574e\u5777\u3002</p><p>\u3000\u3000\u201c\u96be\u8c08\u5c31\u8c08\uff0c\u6211\u4eec\u4e3b\u8981\u8fd8\u662f\u770b\u4e2d\u7279\u8bb80.25\u5fae\u7c73\u7ebf\u7a0b\u8bbe\u5907\uff0c\u201d\u5f20\u606a\u4e5f\u77e5\u9053\u73b0\u5728\u5c31\u8981\u7279\u8bb8\u5c060.18\u5fae\u7c73\u7684\u751f\u4ea7\u7ebf\u6dd8\u6c70\u7ed9\u4e2d\u6676\u5fae\u82af\u6709\u4e9b\u96be\u5ea6\uff0c\u8bf4\u9053\uff1a\u201c\u53e6\u5916\uff0c\u8ddf\u90ed\u5148\u751f\u4f60\u62a5\u4e2a\u559c\uff0c\u9526\u6e56512M\u95ea\u5b58\u82af\u7247\u539f\u578b\u5df2\u7ecf\u5f00\u53d1\u51fa\u6765\uff0c\u662f\u91c7\u75280.13\u5fae\u7c73\u7ebf\u7a0b\u8bbe\u8ba1\uff0c\u6216\u8bb8\u80fd\u589e\u52a0\u4e00\u4e9b\u8c08\u5224\u7b79\u7801\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u6a61\u6811\u56ed\u5df2\u7ecf\u505a\u5230\u8fd9\u4e00\u6b65\u4e86\uff1f\u201d\u90ed\u677e\u5ef6\u6b23\u559c\u7684\u95ee\u9053\uff0c\u201c\u771f\u662f\u4e0d\u7b80\u5355\u554a\uff0c\u6211\u770b\u5f88\u6709\u8c08\u6210\u5e0c\u671b\uff0c\u6211\u5728\u91d1\u5c71\u6b47\u4e24\u5929\uff0c\u4e24\u5929\u540e\u624d\u6709\u53bb\u65b0\u52a0\u5761\u7684\u98de\u673a\uff0c\u5b59\u5148\u751f\u4eba\u5728\u65b0\u52a0\u5761\uff0c\u53ef\u4ee5\u76f4\u63a5\u4e0e\u65b0\u52a0\u5761\u91d1\u7ba1\u5c40\u4e0e\u6de1\u9a6c\u9521\u7684\u9ad8\u5c42\u6c9f\u901a\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5bb9\u91cf\u95ea\u5b58\u82af\u7247\uff0c\u9526\u6e56\u80fd\u8bbe\u8ba1\u4e0d\u80fd\u751f\u4ea7\uff0c\u5c31\u9700\u8981\u5c06\u8ba2\u5355\u4ea4\u7ed9\u4ee3\u5de5\u5382\uff0c\u95ea\u5b58\u82af\u7247\u7684\u5e02\u573a\u89c4\u6a21\u8d8a\u6765\u8d8a\u5927\uff0c\u4e89\u53d6\u95ea\u5b58\u82af\u7247\u4ee3\u5de5\u4e1a\u52a1\uff0c\u5c06\u662f\u6676\u5706\u4ee3\u5de5\u5382\u5546\u4e89\u593a\u7684\u4e00\u4e2a\u6218\u573a\u3002\u800c\u5728\u95ea\u5b58\u82af\u7247\u5e02\u573a\uff0c\u9526\u6e56\u76f8\u6bd4\u8f83\u4e1c\u829d\u3001\u4e09\u661f\uff0c\u5e76\u4e0d\u5904\u4e8e\u5f31\u52bf\uff0c\u9526\u6e56\u7684\u8ba2\u5355\u7edd\u5bf9\u6709\u8bf1\u60d1\u529b\u3002</p><p>\u3000\u3000\u8d70\u8fc7\u6c81\u56ed\u5ba2\u623f\u533a\u57df\u66f2\u66f2\u6298\u6298\u7684\u8d70\u5eca\uff0c\u8d70\u5230\u524d\u5385\u5bb4\u4f1a\u533a\u57df\uff0c\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u8d75\u6709\u4f26\u4e0e\u968f\u884c\u5b98\u5458\u5df2\u7ecf\u5728\u90a3\u91cc\u7b49\u5019\u3002\u4e2d\u6676\u5fae\u82af\u6b64\u65f6\u9762\u4e34\u4e9a\u6d32\u4f17\u591a\u6676\u5706\u5382\u5546\u56f4\u653b\uff0c\u8fd9\u4e2a\u5c40\u52bf\uff0c\u8d75\u6709\u4f26\u5fc3\u91cc\u4e5f\u5f88\u6e05\u695a\uff0c\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u5bf9\u4e2d\u6676\u5fae\u82af\u80cc\u540e\u7684\u5fb7\u4eea\u4e0e\u9526\u6e56\u5f88\u6709\u4fe1\u5fc3\uff0c\u4e2d\u6676\u5fae\u82af\u8981\u540c\u65f6\u5efa\u4e24\u5ea7\u65b0\u5382\uff0c\u91d1\u5c71\u8fd9\u6b21\u600e\u4e48\u7740\u8981\u4e89\u4e00\u5ea7\u8fc7\u6765\u3002</p></article>"}}}]);
