(window.webpackJsonp=window.webpackJsonp||[]).push([[990],{1013:function(p){p.exports={chapterTitle:null,title:"\u7b2c987\u7ae0 \u5bb6\u5bb4",content:"<article><h2>\u7b2c987\u7ae0 \u5bb6\u5bb4</h2><p>\u3000\u3000\u94b1\u6842\u534e\u63a5\u5230\u7535\u8bdd\u901a\u77e5\u8bf4\u4ed6\u4e0e\u533a\u653f\u534f\u526f\u4e3b\u5e2d\u5434\u7231\u6c11\u4e34\u65f6\u7ed9\u6dfb\u5230\u8fd9\u6b21\u6b27\u6d32\u8003\u5bdf\u56e2\u7684\u540d\u5355\u4e4b\u4e2d\uff0c\u5fc3\u91cc\u6709\u4e9b\u7591\u60d1\uff0c\u5374\u4e5f\u6ca1\u6709\u60f3\u592a\u591a\uff0c\u6bd5\u7adf\u80fd\u501f\u51fa\u56fd\u8003\u5bdf\u7684\u540d\u4e49\u51fa\u53bb\u73a9\u4e00\u5708\uff0c\u4e5f\u662f\u5341\u5206\u96be\u5f97\u7684\u673a\u4f1a\uff0c\u4e0d\u7528\u6574\u5929\u5bf9\u7740\u5bb6\u91cc\u51f6\u5df4\u5df4\u7684\u9ec4\u8138\u5a46\u3002</p><p>\u3000\u3000\u94b1\u6842\u534e\u6293\u7d27\u65f6\u95f4\u5c06\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u53c8\u73a9\u5f04\u4e86\u4e00\u756a\uff0c\u9760\u7740\u5e8a\u5934\u7a0d\u4f5c\u4f11\u606f\u65f6\uff0c\u7ed9\u8205\u5b50\u738b\u5efa\u6797\u6253\u4e86\u4e2a\u7535\u8bdd\uff1a\u201c\u6211\u521a\u521a\u63a5\u5230\u8c37\u533a\u957f\u7684\u7535\u8bdd\uff0c\u6211\u8ddf\u5434\u7231\u6c11\u8fd9\u51e0\u5929\u4f1a\u4e00\u8d77\u5230\u6b27\u6d32\u8003\u5bdf\u53bb\uff0c\u4f1a\u79bb\u5f00\u534a\u4e2a\u591a\u6708\uff0c\u4f60\u7ed9\u6211\u8001\u8001\u5b9e\u5b9e\u7684\u5728\u8fd9\u91cc\u4f4f\u4e0a\u4e00\u4e2a\u661f\u671f\u4e0d\u8981\u51fa\u53bb\uff0c\u5c31\u7b97\u5728\u8fd9\u91cc\uff0c\u4f60\u4e5f\u4e0d\u8981\u60f9\u4e8b\u751f\u975e\uff0c\u6628\u5929\u8981\u4e0d\u662f\u6211\u53ca\u65f6\u63a5\u5230\u7535\u8bdd\uff0c\u771f\u8981\u5c06\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u5f97\u7f6a\u4e86\uff0c\u4f60\u4e0d\u6b7b\u4e5f\u5f97\u8131\u5c42\u76ae\uff0c\u6ca1\u6709\u4eba\u80fd\u4fdd\u5f97\u4e86\u4f60\u2026\u2026\u201d\u51b2\u4e86\u6fa1\u4fbf\u79bb\u5f00\u4e91\u68a6\u9601\u5a31\u4e50\u5bab\u9644\u697c\u7684\u5ba2\u623f\uff0c\u7a81\u7136\u63a5\u5230\u901a\u77e5\u8bf4\u8ba9\u51fa\u56fd\u8003\u5bdf\uff0c\u51fa\u56fd\u7684\u624b\u7eed\u8981\u52a0\u6025\u529e\uff0c\u5c40\u91cc\u8fd8\u6709\u597d\u4e9b\u4e8b\u60c5\u8981\u5b89\u6392\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7684\u94b1\u6842\u534e\u7edd\u8ba1\u60f3\u4e0d\u5230\u8ba9\u4ed6\u968f\u56e2\u51fa\u56fd\u8003\u5bdf\u662f\u522b\u4eba\u5bf9\u4ed6\u4f7f\u7684\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\uff0c\u5357\u90ba\u533a\u59d4\u4e66\u8bb0\u5f20\u5065\u5982\u6b64\u5b89\u6392\uff0c\u4e5f\u662f\u8003\u8651\u5230\u8ba9\u94b1\u6842\u534e\u968f\u56e2\u51fa\u56fd\u8003\u5bdf\u534a\u4e2a\u6708\uff0c\u8ba9\u9648\u79c9\u5fb7\u6709\u8db3\u591f\u65f6\u95f4\u5bf9\u957f\u6797\u8fdc\u8f93\u516c\u53f8\u53ca\u4e91\u68a6\u9601\u8fdb\u884c\u53d6\u8bc1\u8c03\u67e5\u4e86\uff0c\u533a\u91cc\u4e5f\u4e0d\u7528\u60ca\u6270\u592a\u5927\uff1b\u9664\u4e86\u94b1\u6842\u534e\u4e4b\u5916\uff0c\u5f20\u5065\u4e5f\u5c06\u957f\u6797\u8fd0\u8f93\u516c\u53f8\u53ca\u4e91\u68a6\u9601\u5a31\u4e50\u5bab\u80cc\u540e\u522b\u4e00\u4e2a\u4fdd\u62a4\u4f1e\u5434\u7231\u6c11\u4e5f\u4e00\u5e76\u501f\u8fd9\u673a\u4f1a\u8c03\u5f00\u3002</p><p>\u3000\u3000\u5143\u65e6\u4e09\u5929\u5047\u671f\u7684\u7b2c\u4e8c\u5929\uff0c\u5e02\u516c\u5b89\u5c40\u5c40\u957f\u9648\u79c9\u5fb7\u5c31\u51fa\u73b0\u5728\u4ed6\u7684\u529e\u516c\u5ba4\u91cc\uff0c\u8fd9\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5947\u602a\u7684\uff0c\u8282\u5047\u65e5\uff0c\u9648\u79c9\u5fb7\u5373\u4f7f\u6709\u5047\u671f\uff0c\u4e5f\u4e0d\u4f1a\u653e\u5fc3\u5728\u5bb6\u4f11\u606f\u7684\u3002</p><p>\u3000\u3000\u8981\u8c03\u67e5\u957f\u6797\u8fd0\u8f93\u516c\u53f8\u4ee5\u53ca\u5357\u90ba\u533a\u8fd0\u8f93\u5e02\u573a\u80cc\u540e\u7684\u6d89\u9ed1\u95ee\u9898\uff0c\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u5357\u90ba\u533a\u516c\u5b89\u5206\u5c40\u7684\u53c2\u4e0e\uff0c\u5373\u4f7f\u5728\u5e02\u5c40\u5185\uff0c\u9648\u79c9\u5fb7\u4e5f\u4e0d\u6562\u4fdd\u8bc1\u6709\u4ece\u5357\u90ba\u533a\u5206\u5c40\u63d0\u62d4\u5230\u5e02\u5c40\u7684\u8b66\u5458\u4e0e\u94b1\u6842\u534e\u5c31\u6ca1\u6709\u7275\u8fde\uff0c\u4ed6\u5750\u5728\u529e\u516c\u5ba4\u91cc\u8003\u8651\uff1a\u7528\u8c01\u53bb\u8d1f\u8d23\u4e13\u6848\u7ec4\u5408\u9002\uff1f</p><p>\u3000\u3000\u684c\u4e0a\u7684\u7535\u8bdd\u673a\u54cd\u4e86\uff0c\u9648\u79c9\u5fb7\u63a5\u901a\u7535\u8bdd\uff0c\u7535\u8bdd\u662f\u5e02\u59d4\u529e\u6253\u6765\u7684\uff0c\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u8981\u6765\u6170\u95ee\u516c\u5b89\u7cfb\u7edf\u8282\u5047\u65e5\u8fd8\u5de5\u4f5c\u5728\u4e00\u7ebf\u7684\u57fa\u5c42\u5e72\u8b66\uff0c\u8981\u5e02\u5c40\u8fd9\u91cc\u6d3e\u4eba\u966a\u540c\u3002</p><p>\u3000\u3000\u80fd\u6709\u4e0e\u7f57\u541b\u4eb2\u5bc6\u63a5\u89e6\u7684\u673a\u4f1a\uff0c\u9648\u79c9\u5fb7\u662f\u5f53\u4ec1\u4e0d\u8ba9\u7684\u3002\u5e02\u91cc\u7684\u9886\u5bfc\u4e0d\u7b97\uff0c\u5168\u5e02\u90a3\u4e48\u591a\u6b63\u5c40\uff08\u526f\u5385\u7ea7\uff09\u3001\u526f\u5c40\u7ea7\u5e72\u90e8\uff0c\u7f57\u541b\u5b9e\u9645\u4e0a\u4e5f\u719f\u6089\u7684\u4e5f\u6ca1\u6709\u591a\u5c11\uff0c\u6709\u65f6\u5019\u4e00\u4e2a\u8f83\u6df1\u523b\u7684\u5370\u8c61\u5c31\u80fd\u5728\u5e72\u90e8\u8c03\u6574\u4e2d\u5e26\u6765\u6781\u5927\u7684\u597d\u5904\u3002\u9648\u79c9\u5fb7\u95ee\u6e05\u695a\u5e02\u59d4\u529e\u7ed9\u7f57\u541b\u5b89\u6392\u7684\u8def\u7ebf\uff0c\u5c06\u4eca\u5929\u503c\u73ed\u7684\u73ed\u5b50\u6210\u5458\u627e\u8fc7\u6765\uff0c\u8ba9\u4ed6\u4eec\u4e0b\u53bb\u76d1\u7763\u53ef\u80fd\u8d70\u8bbf\u5230\u7684\u57fa\u5c42\u5355\u4f4d\u505a\u597d\u8fce\u63a5\u8d70\u8bbf\u7684\u51c6\u5907\uff0c\u4ed6\u5148\u8d76\u5230\u5e02\u59d4\u529e\u53bb\u6c47\u5408\u3002</p><p>\u3000\u3000\u9648\u79c9\u5fb7\u8d76\u5230\u5e02\u59d4\u529e\uff0c\u770b\u5230\u653f\u6cd5\u59d4\u4e66\u8bb0\u4f59\u5e86\u6797\u5df2\u7ecf\u5230\u4e86\uff0c\u4ed6\u6b63\u4e0e\u7f57\u541b\u5728\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u7f57\u742a\u96be\u5f97\u6765\u4e00\u56de\u5efa\u90ba\uff0c\u7f57\u4e66\u8bb0\u60a8\u8fd8\u4e00\u5fc3\u6251\u5728\u5de5\u4f5c\u4e0a\uff0c\u4e0d\u6015\u7ed9\u7f57\u742a\u62b1\u6028\uff1f\u201d\u4f59\u5e86\u6797\u7b11\u7740\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5e74\u8f7b\u4eba\u6709\u5e74\u8f7b\u4eba\u7684\u6d3b\u52a8\u7a7a\u95f4\u2014\u2014\u8fd9\u65f6\u5019\u53bb\u901b\u8857\u4e86\uff0c\u901b\u5b8c\u8857\u8fd8\u8981\u7ed9\u9080\u53bb\u5b66\u5e9c\u5df7\uff0c\u53ef\u6ca1\u6709\u5fc3\u601d\u6765\u62b1\u6028\u6211\u2026\u2026\u201d\u7f57\u541b\u5fc3\u60c5\u4e0d\u9519\uff0c\u9887\u6709\u8010\u5fc3\u7684\u8ddf\u4f59\u5e86\u6797\u7ad9\u5728\u90a3\u91cc\u62c9\u5bb6\u5e38\u3002</p><p>\u3000\u3000\u9648\u79c9\u5fb7\u8d70\u8fc7\u53bb\u62db\u547c\u4e86\u4e00\u58f0\uff0c\u5c31\u7ad9\u5728\u4e00\u65c1\uff0c\u89c1\u7f57\u541b\u4e0d\u4ecb\u610f\u8ba9\u522b\u4eba\u77e5\u9053\u4ed6\u5bb6\u4e0e\u5f20\u606a\u7684\u79c1\u4ea4\u4e0d\u9519\uff0c\u5fc3\u60f3\u8fd9\u4e0e\u4ee5\u5f80\u4ed6\u5bf9\u9526\u6e56\u5728\u5927\u529b\u652f\u6301\u4e4b\u4f59\u4ecd\u4fdd\u7559\u4e00\u4e9b\u6212\u5fc3\u7684\u6001\u5ea6\u4f3c\u4e4e\u53c8\u6709\u6240\u4e0d\u540c\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u59da\u6587\u76db\u3001\u9646\u5065\u4ed6\u4eec\u4e0a\u5348\u9192\u8fc7\u6765\u65e0\u6240\u4e8b\u4e8b\u5c31\u5148\u8d76\u5230\u5b66\u5e9c\u5df7\u6765\uff0c\u4e00\u8d77\u5403\u8fc7\u4e2d\u996d\uff0c\u5728\u9752\u5e74\u516c\u5bd3\u91cc\u6253\u724c\u6d88\u78e8\u65f6\u95f4\u3002</p><p>\u3000\u3000\u4e0d\u8981\u8bf4\u5bb4\u8bf7\u4e86\uff0c\u5f20\u606a\u5e73\u65e5\u5728\u516c\u5bd3\u91cc\u52a8\u706b\u4e5f\u53ea\u662f\u716e\u7897\u6c64\u5706\u3001\u4e0b\u7897\u9984\u9968\u4ec0\u4e48\u7684\uff0c\u6731\u5c0f\u519b\u4ee5\u4e3a\u665a\u4e0a\u7684\u79c1\u5bb4\u5f20\u606a\u4f1a\u8ba9\u9152\u5e97\u91cc\u9001\u4e24\u684c\u9152\u5e2d\u5230\u516c\u5bd3\u91cc\u6765\u6216\u8005\u76f4\u63a5\u8bf7\u54ea\u5bb6\u9152\u5e97\u91cc\u7684\u5927\u53a8\u5e26\u7740\u98df\u6750\u5230\u516c\u5bd3\u91cc\u6765\u505a\u4e00\u987f\u5927\u9910\uff0c\u4ed6\u4e0e\u4ee4\u5c0f\u71d5\u5373\u4f7f\u60f3\u5f88\u70ed\u5207\u8fc7\u6765\u5e2e\u5fd9\uff0c\u4e5f\u6ca1\u6709\u50cf\u59da\u6587\u76db\u4ed6\u4eec\u90a3\u4e48\u968f\u610f\u7684\u5c31\u8d76\u8fc7\u6765\u8e6d\u4e2d\u5348\u996d\uff0c\u5fc3\u91cc\u60f3\u7740\u665a\u4e0a\u5bb4\u5e2d\u5927\u6982\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u9700\u8981\u5e2e\u5fd9\u7684\u5730\u65b9\uff0c\u4ed6\u4eec\u4e0a\u5348\u8d77\u5e8a\u540e\u5148\u53bb\u901b\u4e86\u8857\uff0c\u5728\u65b0\u5e02\u8857\u5403\u8fc7\u4e2d\u996d\u624d\u8d76\u5230\u9752\u5e74\u516c\u5bd3\u3002</p><p>\u3000\u3000\u6731\u5c0f\u519b\u4e0e\u4ee4\u5c0f\u71d5\u8fdb\u4e86\u5c4b\uff0c\u5c31\u770b\u89c1\u6613\u99a8\u3001\u5f20\u6b23\u3001\u76db\u590f\u3001\u5e2d\u82e5\u7433\u8fd8\u6709\u5b59\u9759\u6aac\u51e0\u4e2a\u5973\u5b69\u5b50\u5728\u5ba2\u53d1\u7684\u6c99\u53d1\u4e0a\u8fb9\u770b\u7535\u89c6\u8fb9\u804a\u5929\uff0c\u59da\u6587\u76db\u3001\u9646\u5065\u3001\u675c\u98de\u3001\u8499\u4e50\u5728\u9694\u907f\u7684\u5a31\u4e50\u5ba4\u91cc\u6253\u724c\u3002\u5ba2\u5385\u8fc7\u53bb\u9694\u7740\u534a\u5f27\u5f62\u7684\u95e8\u6d1e\u662f\u88c5\u4fee\u8c6a\u534e\u7684\u9910\u5385\uff0c\u53a8\u623f\u95f4\u5728\u62d0\u89d2\u91cc\u9762\uff0c\u7ad9\u5728\u5ba2\u5385\u91cc\u770b\u5230\u4e0d\uff0c\u95fb\u7740\u6709\u6251\u9f3b\u7684\u9999\u6c14\u4ece\u53a8\u623f\u95f4\u900f\u51fa\u6765\uff0c\u4ee4\u5c0f\u71d5\u731b\u55c5\u4e86\u4e00\u53e3\uff0c\u611f\u6168\u7684\u95ee\u9053\uff1a\u201c\u597d\u9999\u554a\uff0c\u4ece\u54ea\u91cc\u8bf7\u6765\u7684\u5927\u53a8\u554a\uff1f\u201d\u8d70\u5230\u5ba2\u5385\u4e0e\u9910\u5385\u4e4b\u95f4\u7684\u95e8\u6d1e\u4e0b\uff0c\u5f80\u53a8\u623f\u90a3\u91cc\u770b\u4e86\u770b\uff0c\u6015\u6cb9\u70df\u98d8\u51fa\u6765\uff0c\u78e8\u7802\u73bb\u7483\u95e8\u63a9\u7740\uff0c\u9690\u7ea6\u770b\u89c1\u91cc\u9762\u6709\u4e09\u4e2a\u4eba\u8e72\u7740\u5730\u4e0a\u6361\u83dc\uff0c\u53c8\u8f6c\u56de\u6765\u95ee\u5e2d\u82e5\u7433\uff0c\u201c\u5f20\u606a\u4ed6\u4eec\u4eba\u5462\uff0c\u6709\u4ec0\u4e48\u9700\u8981\u6211\u4eec\u5e2e\u505a\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u8bf7\u4f60\u4eec\u662f\u6765\u505a\u5ba2\u7684\uff0c\u8981\u662f\u8fd9\u91cc\u65e0\u804a\uff0c\u53bb\u5b66\u5e9c\u5df7\u5148\u901b\u72c2\u4e5f\u884c\u3002\u201d</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u542c\u7740\u5f20\u606a\u7684\u58f0\u97f3\uff0c\u56de\u5934\u53c8\u770b\u53a8\u623f\u65b9\u5411\u770b\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u5f20\u606a\u7a7f\u7740\u7ed2\u7ebf\u8863\uff0c\u8170\u95f4\u7cfb\u7740\u56f4\u88d9\uff0c\u624b\u91cc\u6293\u7740\u4e00\u628a\u6b63\u53bb\u53f6\u7684\u897f\u82b9\uff0c\u5c06\u73bb\u7483\u95e8\u6253\u5f00\u4e9b\u8ddf\u81ea\u5df1\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u4ece\u6ca1\u6709\u89c1\u8fc7\u5f20\u606a\u8fd9\u526f\u5f62\u8c61\uff0c\u5fae\u5fae\u5f20\u4e86\u5f20\u5634\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8be5\u4e0d\u4f1a\u8ba9\u6211\u4eec\u5230\u4e03\u516b\u5341\u5c81\u65f6\u8fd8\u8ddf\u522b\u4eba\u6d25\u6d25\u4e50\u9053\u7684\u63d0\u8d77\u8fd9\u4e8b\u5427\uff1f\u6211\u4eec\u54ea\u91cc\u53d7\u5f97\u4f4f\u8ba9\u4f60\u4eb2\u81ea\u4e0b\u53a8\u505a\u83dc\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u8bf7\u5927\u5bb6\u5230\u5bb6\u91cc\u6765\u5403\u996d\uff0c\u8981\u6709\u4e9b\u8bda\u610f\u624d\u884c\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u8981\u8bf4\u66f2\u610f\u5949\u627f\u7684\u8bdd\uff0c\u56fd\u5185\u6709\u51e0\u4e2a\u4eba\u62c5\u5f53\u5f97\u8d77\u8ba9\u5f20\u606a\u4eb2\u81ea\u4e0b\u53a8\uff0c\u4e0d\u8981\u8bf4\u7f57\u7f57\u7684\u5973\u513f\u3001\u51c6\u5973\u5a7f\u4e86\uff0c\u5c31\u662f\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u90fd\u6ca1\u6709\u8fd9\u8d44\u683c\uff0c\u4e0d\u8fc7\u4e00\u4eba\u8981\u6574\u597d\u4e00\u987f\u665a\u5bb4\uff0c\u5de5\u4f5c\u91cf\u8fd8\u662f\u76f8\u5f53\u7684\u5927\uff0c\u4ee4\u5c0f\u71d5\u633d\u7740\u8896\u5b50\u5c31\u51c6\u5907\u8fc7\u53bb\u5e2e\u5fd9\u3002</p><p>\u3000\u3000\u201c\u522b\uff0c\u5c31\u8ba9\u6211\u5356\u5f04\u4e00\u4e0b\u53a8\u827a\uff0c\u201d\u5f20\u606a\u4e0d\u8ba9\u4ee4\u5c0f\u71d5\u8fc7\u6765\u5e2e\u5fd9\uff0c\u201c\u6211\u8fd9\u91cc\u6709\u4e24\u4e2a\u52a9\u624b\uff0c\u867d\u7136\u4e00\u4e2a\u52a9\u624b\u6363\u86cb\u7684\u4f5c\u7528\u66f4\u5927\u4e00\u4e9b\uff0c\u505a\u4e00\u987f\u665a\u996d\u8fd8\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\u7740\uff0c\u8e72\u5728\u5730\u4e0a\u7684\u5510\u5a67\u62ff\u8d77\u624b\u91cc\u7684\u897f\u82b9\u5c31\u8981\u5f80\u4ed6\u8138\u4e0a\u626b\uff0c\u7b11\u7740\u95ee\uff1a\u201c\u8bec\u9677\u8c01\u6363\u86cb\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u9664\u4e86\u536b\u5170\u59d0\uff0c\u96be\u9053\u8fd8\u662f\u4f60\u554a\uff1f\u201d\u5f20\u606a\u8bf4\u7740\u53cd\u8bdd\uff0c\u7b11\u7740\u8e72\u4e0b\u6765\u7ee7\u7eed\u6361\u897f\u82b9\u83dc\u3002</p><p>\u3000\u3000\u7f57\u742a\u4e0e\u7537\u670b\u53cb\u8d75\u7559\u5b87\u4e09\u70b9\u949f\u5de6\u53f3\u5750\u8f66\u8fc7\u6765\uff0c\u5f20\u606a\u5c31\u51fa\u6765\u62db\u547c\u4e86\u4e00\u58f0\uff0c\u53c8\u56de\u53a8\u623f\u5fd9\u788c\u53bb\u4e86\uff0c\u8c01\u4e5f\u6ca1\u6709\u8d44\u683c\u62b1\u6028\u7ed9\u5f20\u606a\u51b7\u843d\u4e86\uff0c\u8d75\u7559\u5b87\u4e5f\u662f\u5317\u4eac\u516c\u5b50\u5708\u5185\u7684\u4e00\u5458\uff0c\u5e74\u7eaa\u7a0d\u5c0f\u4e00\u4e9b\uff0c\u4e0d\u8fc7\u4e0e\u9646\u5065\u4e5f\u662f\u8ba4\u8bc6\u7684\uff0c\u675c\u98de\u7ad9\u8d77\u6765\u8ba9\u4ed6\u63a5\u7740\u6253\u724c\u3002</p><p>\u3000\u3000\u6216\u8bb8\u5728\u5f20\u606a\u6216\u5728\u7f57\u541b\u9762\u524d\u65e0\u6cd5\u76f4\u63a5\u8bf4\u51fa\u5fc3\u610f\uff0c\u5fc3\u91cc\u611f\u53d7\u5230\u7684\u538b\u529b\u5f88\u5927\uff0c\u8fd9\u65f6\u5019\u5750\u4e0b\u6765\u6253\u724c\uff0c\u59da\u6587\u76db\u3001\u9646\u5065\u90fd\u7b97\u5728\u5317\u4eac\u5c31\u8ba4\u8bc6\u7684\u719f\u4eba\uff0c\u7ad9\u5728\u4e00\u65c1\u770b\u724c\u7684\u6731\u5c0f\u519b\u3001\u675c\u98de\u8fd8\u6709\u5750\u7740\u6253\u724c\u7684\u8499\u4e50\u90fd\u662f\u5f20\u606a\u8981\u4ecb\u7ecd\u7ed9\u4ed6\u8ba4\u8bc6\u7684\u670b\u53cb\uff0c\u4e5f\u7ed9\u4e0d\u4e86\u4ed6\u4ec0\u4e48\u538b\u529b\uff0c\u8d75\u7559\u5b87\u6253\u724c\u65f6\u5c31\u63d0\u5230\u4ed6\u6709\u673a\u4f1a\u8c03\u53bb\u534e\u7a00\u77ff\u4e1a\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u534e\u7a00\u77ff\u4e1a\u7ea688\uff05\u7684\u80a1\u6743\u7ed9\u63a7\u5236\u7a00\u571f\u5de5\u4e1a\u534f\u4f1a\u624b\u91cc\uff0c\u5c5e\u4e8e\u56fd\u6709\u80a1\u6743\u6027\u8d28\uff0c\u4f46\u662f\u56fd\u6709\u80a1\u6743\u6210\u5206\u590d\u6742\uff0c\u878d\u5408\u4e86\u591a\u5bb6\u5730\u65b9\u3001\u4e2d\u592e\u4e0e\u7a00\u571f\u4ea7\u4e1a\u76f8\u5173\u7684\u4f01\u4e8b\u4e1a\u5355\u4f4d\u7684\u5229\u76ca\uff0c\u6b63\u56e0\u4e3a\u8fd9\u6210\u5206\u590d\u6742\u7684\u6743\u76ca\u5173\u7cfb\uff0c\u624d\u80fd\u5c06\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u521d\u6b65\u7684\u6574\u5408\u5230\u4e00\u8d77\uff0c\u5c06\u7a00\u571f\u8fdb\u51fa\u53e3\u8d38\u6613\u7b49\u6743\u9650\u90fd\u96c6\u4e2d\u5230\u534e\u7a00\u77ff\u4e1a\u624b\u91cc\u3002\u8981\u662f\u5c06\u7a00\u571f\u5de5\u4e1a\u534f\u4f1a\u64a4\u6d88\uff0c\u5c06\u7a00\u571f\u5de5\u4e1a\u534f\u4f1a\u80cc\u540e\u590d\u6742\u6210\u5206\u5173\u7cfb\u76f4\u63a5\u53cd\u5e94\u5230\u534e\u7a00\u77ff\u4e1a\u7684\u80a1\u6743\u5206\u914d\u4e0a\uff0c\u9526\u6e56\u867d\u7136\u624d\u6301\u670912\uff05\u7684\u80a1\u6743\uff0c\u624d\u662f\u6700\u5927\u7684\u80a1\u4e1c\uff0c\u800c\u534e\u7a00\u77ff\u4e1a\u7684\u7b2c\u4e8c\u5927\u80a1\u6743\u5efa\u90ba\u5e02\u5546\u4e1a\u94f6\u884c\uff0c\u9526\u6e56\u53c8\u62e5\u6709\u76f8\u5f53\u7684\u5f71\u54cd\u529b\u3002</p><p>\u3000\u3000\u8d75\u7559\u5b87\u6709\u673a\u4f1a\u8c03\u8fdb\u534e\u7a00\u77ff\u4e1a\u5de5\u4f5c\uff0c\u4f46\u662f\u5728\u534e\u7a00\u77ff\u4e1a\u80fd\u4e0d\u80fd\u5f97\u5230\u5e94\u6709\u7684\u53d1\u5c55\uff0c\u8fd8\u662f\u8981\u770b\u9526\u6e56\u7684\u8138\u8272\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u89c1\u8d75\u7559\u5b87\u80fd\u5c06\u610f\u601d\u8bf4\u660e\u4e5f\u597d\uff0c\u4ed6\u9876\u591a\u518d\u5e2e\u7740\u5728\u4e2d\u95f4\u4f20\u4e2a\u8bdd\uff0c\u9762\u5bf9\u5f20\u606a\u8fd9\u4e2a\u65b0\u8d35\uff0c\u5317\u4eac\u90a3\u91cc\u5f88\u591a\u5c11\u90fd\u62b9\u4e0d\u4e0b\u9762\u5b50\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u53a8\u827a\u4e0d\u7f3a\u4e4f\u8ba9\u522b\u4eba\u79f0\u8d5e\u7684\u673a\u4f1a\uff0c\u5165\u665a\u540e\uff0c\u5927\u5bb6\u95fb\u7740\u6d53\u90c1\u7684\u9999\u5473\u8d70\u5230\u9910\u5385\u91cc\uff0c\u9910\u684c\u4e0a\u5df2\u7ecf\u6446\u4e0a\u4e30\u76db\u800c\u7cbe\u81f4\u7684\u83dc\u80b4\uff0c\u6613\u99a8\u611f\u6168\u7684\u8ddf\u5510\u5a67\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u770b\u4e0a\u53bb\u4f3c\u4e4e\u4e0d\u6bd4\u9152\u5e97\u91cc\u5dee\u554a\uff0c\u6562\u60c5\u8fd9\u5bb6\u4f19\u771f\u662f\u60f3\u5356\u5f04\u4e00\u4e0b\u554a\uff0c\u8fd9\u4e48\u98ce\u9a9a\u800c\u8ff7\u4eba\u7684\u7537\u4eba\uff0c\u4f60\u53ef\u8981\u770b\u7d27\u4e86\uff0c\u5c0f\u5fc3\u4e00\u4e0d\u7559\u795e\u5c31\u7ed9\u4eba\u5bb6\u5077\u8d70\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8c01\u8981\u6211\u8bba\u65a4\u5356\u7ed9\u5979\u2026\u2026\u201d\u5510\u5a67\u9a84\u50b2\u7684\u9f3b\u7ffc\u5fae\u76b1\u7740\uff0c\u4e0d\u5c51\u7684\u8bf4\u9053\u3002\u5b59\u9759\u6aac\u53c8\u5fc3\u865a\u7684\u8eb2\u5f00\u5510\u5a67\u7684\u773c\u6674\uff0c\u5979\u672c\u6765\u4e0b\u5348\u60f3\u8eb2\u56de\u5b66\u6821\u7684\uff0c\u6bd5\u7adf\u8fd9\u79cd\u5e26\u4ea4\u9645\u6027\u8d28\u7684\u79c1\u4eba\u5bb4\u8bf7\uff0c\u9648\u5983\u84c9\u3001\u674e\u99a8\u4e88\u5979\u4eec\u90fd\u6ca1\u6709\u53c2\u52a0\uff0c\u536b\u5170\u540d\u4e49\u4e0a\u662f\u5f20\u606a\u7684\u59d0\u59d0\uff0c\u8981\u5e2e\u7740\u51c6\u5907\u665a\u9910\uff0c\u5979\u662f\u53ef\u4ee5\u4e0d\u53c2\u52a0\uff0c\u5012\u662f\u5510\u5a67\u9887\u4e3a\u70ed\u60c5\u7684\u4e00\u5b9a\u8981\u5979\u53c2\u52a0\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u9910\uff0c\u8fd8\u8981\u8bf7\u7f57\u742a\u4e0e\u8d75\u7559\u5b87\u518d\u53bb1978\u5750\u4e00\u4f1a\u513f\uff0c\u9910\u684c\u4e0a\u7684\u6b8b\u7fb9\u51b7\u7099\u5012\u662f\u6709\u5de5\u4eba\u8fc7\u6765\u5e2e\u7740\u6536\u62fe\u6389\uff0c\u5728\u8def\u4e0a\uff0c\u5927\u5bb6\u8fd8\u8d5e\u4e0d\u7edd\u53e3\u7684\u79f0\u8d5e\u4eca\u5929\u7684\u665a\u9910\u5341\u5206\u7684\u5730\u9053\uff0c\u5510\u5a67\u633d\u7740\u5f20\u606a\u7684\u80f3\u818a\uff0c\u5728\u540e\u9762\u8d70\u7740\uff0c\u95ee\u9053\uff1a\u201c\u8981\u662f\u6211\u4e0d\u7559\u795e\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u7ed9\u522b\u4eba\u5077\u8d70\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u5510\u5a67\u5728\u8def\u706f\u4e0b\u6676\u83b9\u5254\u900f\u7684\u773c\u775b\uff0c\u8499\u7740\u4e00\u5c42\u96fe\u6c14\uff0c\u4e00\u65f6\u4e0d\u6653\u5f97\u8981\u8bf4\u4ec0\u4e48\uff0c\u53ea\u662f\u8f7b\u6402\u7740\u5979\u7684\u7ec6\u8170\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\uff0c\u8c01\u7231\u5077\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u7ba1\u554a\uff0c\u201d\u5510\u5a67\u8f7b\u58f0\u7684\u8bf4\uff0c\u201c\u4f60\u603b\u8981\u8ba9\u6211\u80fd\u627e\u5f97\u5230\u4f60\u2026\u2026\u201d</p></article>"}}}]);
