(window.webpackJsonp=window.webpackJsonp||[]).push([[991],{1014:function(p){p.exports={chapterTitle:null,title:"\u7b2c801\u7ae0 \u5c11\u5987\u76f8\u77e5",content:"<article><h2>\u7b2c801\u7ae0 \u5c11\u5987\u76f8\u77e5</h2><p>\u3000\u3000\u5efa\u90ba\u7684\u6625\u79cb\u4e24\u5b63\u90fd\u683c\u5916\u7684\u77ed\u6682\uff0c\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u611f\u89c9\uff0c\u5c31\u767d\u9a79\u8fc7\u9699\u7684\u8fc7\u53bb\u4e86\u3002\u4e0a\u98de\u673a\u524d\u8fd8\u611f\u53d7\u5230\u5317\u4eac\u671d\u9633\u521d\u5347\u65f6\u7684\u6e05\u5bd2\uff0c\u98de\u673a\u5728\u5efa\u90ba\u964d\u843d\uff0c\u5df2\u6709\u66ae\u6625\u82b1\u6b8b\u3001\u521d\u590f\u5c06\u81f3\u7684\u708e\u70ed\uff0c\u8bb8\u591a\u4eba\u90fd\u7a7f\u4e0a\u5355\u8863\u3002</p><p>\u3000\u3000\u665a\u6674\u7a7f\u7740\u5b9d\u84dd\u8272\u7684\u7f0e\u8d28\u4eae\u8272\u886c\u8863\uff0c\u5916\u9762\u8fd8\u7a7f\u7740\u6df1\u68d5\u8272\u7684\u77ed\u5916\u5957\uff0c\u53ea\u6709\u9886\u895f\u5904\u9732\u51fa\u886c\u8863\u7684\u660e\u4e3d\u8272\u5f69\uff0c\u6b63\u9762\u5f00\u53c9\u7684\u9ed1\u8272\u53ca\u819d\u7a84\u88d9\uff0c\u8db3\u8e6c\u9ed1\u8272\u9ad8\u8ddf\u978b\uff0c\u5a34\u9759\u7684\u7ad9\u5728\u63a5\u673a\u5927\u5385\u91cc\uff0c\u6df1\u9083\u800c\u660e\u4eae\u7684\u7738\u5b50\u9759\u8c27\u7684\u51dd\u89c6\u7740\u5f20\u606a\u4ece\u884c\u674e\u8f93\u9001\u5e26\u90a3\u91cc\u8d70\u51fa\u6765\uff0c\u6e29\u5b9b\u800c\u52a8\u4eba\u7684\u7b11\u7740\u3002</p><p>\u3000\u3000\u201c\u6ca1\u542c\u4f60\u8bf4\u8981\u8fc7\u6765\u63a5\u673a\u554a\uff1f\u201d\u4e0d\u80fd\u6709\u592a\u4eb2\u6635\u7684\u4e3e\u52a8\uff0c\u5f20\u606a\u7ad9\u5728\u665a\u6674\u9762\u524d\uff0c\u53ea\u662f\u542b\u7b11\u770b\u7740\u5979\uff0c\u95ee\u5979\u600e\u4e48\u4f1a\u8fc7\u6765\u63a5\u673a\u3002</p><p>\u3000\u3000\u201c\u6211\u5230\u5efa\u90ba\u65f6\uff0c\u6b63\u8d76\u4e0a\u7ed9\u4f60\u63a5\u673a\u7684\u8f66\u5b50\u4ece\u6a61\u6811\u56ed\u51fa\u53d1\uff0c\u5c31\u4e00\u8d77\u8fc7\u6765\u4e86\u2026\u2026\u201d\u665a\u6674\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u4e0e\u4e2d\u6676\u5fae\u82af\u4e24\u4e2a\u9879\u76ee\u4ece\u6d77\u5916\u62db\u63fd\u5927\u91cf\u7684\u79d1\u5b66\u5bb6\u3001\u5de5\u7a0b\u5e08\u7b49\u4e2d\u9ad8\u7ea7\u6280\u672f\u4eba\u624d\uff0c\u9526\u6e56\u4e0e\u5efa\u90ba\u5e02\u5171\u540c\u627f\u8bfa\u8981\u5c3d\u6700\u5927\u7684\u53ef\u80fd\u4e3a\u8fd9\u4e9b\u4ece\u6d77\u5916\u5f52\u6765\u7684\u4e2d\u9ad8\u7ea7\u6280\u672f\u4eba\u624d\u89e3\u51b3\u5176\u672c\u4eba\u53ca\u5bb6\u5c5e\u5728\u5efa\u90ba\u7684\u751f\u6d3b\u95ee\u9898\uff0c\u5f53\u7136\u4e5f\u5305\u62ec\u5b50\u5973\u7684\u6559\u80b2\u3002</p><p>\u3000\u3000\u5efa\u90ba\u867d\u7136\u540d\u6821\u5f88\u591a\uff0c\u4f46\u662f\u56fd\u5185\u586b\u9e2d\u5f0f\u7684\u6559\u80b2\u6a21\u5f0f\u5374\u5f97\u4e0d\u5230\u7edd\u5927\u591a\u6570\u6709\u7740\u6d77\u5916\u7559\u5b66\u7ecf\u5386\u4eba\u7684\u8ba4\u540c\uff0c\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u5230\u5efa\u90ba\u6765\u5efa\u5206\u6821\u4eca\u5e74\u4e5f\u5c31\u63d0\u5230\u65e5\u7a0b\u4e0a\u6765\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u8c22\u665a\u6674\u80fd\u5728\u5efa\u90ba\u673a\u573a\u7ed9\u5f20\u606a\u63a5\u673a\u7684\u539f\u6545\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u7b97\u81ea\u5df1\u9a7e\u8f66\u8f7d\u7740\u665a\u6674\u4e0e\u7fdf\u4e39\u9752\u8fd4\u56de\u5e02\u533a\uff0c\u5085\u4fca\u53ca\u5176\u4ed6\u968f\u884c\u4eba\u5458\u5219\u6324\u53e6\u4e00\u90e8\u8f66\u8fd4\u56de\u6a61\u6811\u56ed\u3002\u5728\u5efa\u90ba\uff0c\u5f20\u606a\u5c31\u4e0d\u559c\u6b22\u6709\u4eba\u8d34\u8eab\u4fdd\u536b\u3002</p><p>\u3000\u3000\u8bf4\u6765\u5947\u602a\uff0c\u8c22\u665a\u6674\u5bf9\u7fdf\u4e39\u9752\u6709\u4e00\u79cd\u5f88\u4eb2\u8fd1\u7684\u611f\u89c9\uff0c\u6216\u8bb8\u81ea\u5df1\u6700\u9690\u79d8\u7684\u4e8b\u60c5\u7ed9\u5979\u77e5\u9053\uff0c\u6ca1\u6709\u5fc5\u8981\u518d\u9690\u85cf\u6216\u4f2a\u88c5\uff0c\u518d\u770b\u5230\u7fdf\u4e39\u9752\u4e5f\u4e0d\u4f1a\u50cf\u9752\u6da9\u5c11\u5973\u90a3\u6837\u60ca\u602f\u7684\u8eb2\u5f00\uff0c\u53cd\u800c\u60f3\u5230\u80fd\u6709\u4e00\u4e2a\u4eba\u53ef\u4ee5\u8ddf\u5979\u8bf4\u81ea\u5df1\u5fc3\u91cc\u7684\u611f\u53d7\uff0c\u4f1a\u662f\u4e00\u79cd\u76f8\u5f53\u4e0d\u9519\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u540e\u89c6\u955c\u91cc\u770b\u5230\u665a\u6674\u4e0e\u7fdf\u4e39\u9752\u4eb2\u5bc6\u7684\u8bf4\u8bdd\uff0c\u624d\u53d1\u89c9\u4ed6\u5bf9\u5973\u4eba\u7684\u4e86\u89e3\u4e5f\u8c08\u4e0d\u4e0a\u591a\u6df1\u5165\uff0c\u4ed6\u4e4b\u524d\u4f1a\u4ee5\u4e3a\u665a\u6674\u4f1a\u907f\u514d\u5728\u6709\u7fdf\u4e39\u9752\u7684\u573a\u5408\u4e0b\u4e0e\u81ea\u5df1\u89c1\u9762\uff0c\u6ca1\u60f3\u5230\u665a\u6674\u5012\u662f\u5982\u6b64\u5766\u7136\u5927\u65b9\u7684\u5c06\u7fdf\u4e39\u9752\u5f53\u6210\u201c\u81ea\u5df1\u4eba\u201d\u4e86\u3002</p><p>\u3000\u3000\u201c\u5efa\u90ba\u4eca\u5929\u771f\u591f\u70ed\u7684\uff0c\u201d\u665a\u6674\u4e0a\u8f66\u5c31\u5c06\u5916\u5957\u8131\u4e0b\u6765\uff0c\u53ea\u7a7f\u7740\u5b9d\u84dd\u8272\u7684\u886c\u886b\uff0c\u95ee\u7fdf\u4e39\u9752\uff0c\u201c\u4f60\u4e0d\u70ed\u5417\uff1f\u201d</p><p>\u3000\u3000\u662f\u6709\u4e9b\u70ed\uff0c\u4e0d\u8fc7\u7fdf\u4e39\u9752\u5728\u7f69\u886b\u91cc\u9762\u7a7f\u7740\u6628\u5929\u591c\u91cc\u5f20\u606a\u66ff\u5979\u9009\u7684\u90a3\u4ef6\u4f4eV\u9886T\u6064\uff0c\u5728\u673a\u573a\u4e0a\u6ca1\u6709\u6025\u7740\u5c06\u5916\u5957\u8131\u4e0b\u6765\uff0c\u8fd9\u65f6\u5019\u542c\u8c22\u665a\u6674\u63d0\u8d77\uff0c\u7684\u786e\u89c9\u5f97\u6709\u4e9b\u70ed\uff0c\u5c31\u5c06\u5916\u5957\u8131\u4e0b\u6765\u3002</p><p>\u3000\u3000\u6697\u7ea2\u8272\u7684T\u6064\u886b\u886c\u6258\u51fa\u4e73\u6c9f\u7684\u96ea\u767d\u6da6\u6ed1\uff0c\u8bf1\u4eba\u9050\u601d\u3002</p><p>\u3000\u3000\u201c\u54e6\u54e6\uff0c\u8fd9\u4e48\u6f02\u4eae\u7684\u8863\u670d\u600e\u4e48\u85cf\u5728\u91cc\u9762\u4e0d\u9732\u51fa\u6765\uff1f\u5efa\u90ba\u7684\u5b9d\u59ff\u4e13\u5356\u5e97\uff0c\u6211\u600e\u4e48\u6ca1\u770b\u5230\u8fd9\u6b3eT\u6064\u554a\uff0c\u4f60\u662f\u5728\u5317\u4eac\u4e70\u7684\u5417\uff1f\u6211\u7279\u522b\u559c\u6b22\u8fd9\u79cd\u989c\u8272\uff0c\u53ea\u662f\u6211\u4e0d\u6562\u7a7f\u8fd9\u4e48\u4f4e\u7684\u9886\u5b50\uff0c\u4f60\u7a7f\u8d77\u6765\u4e0d\u6653\u5f97\u8981\u998b\u6b7b\u591a\u5c11\u7537\u4eba\u2026\u2026\u201d\u8c22\u665a\u6674\u4f38\u624b\u8981\u53bb\u8bd5\u8863\u670d\u7684\u6750\u8d28\uff0c\u7a81\u7136\u770b\u5230\u7fdf\u4e39\u9752\u9732\u51fa\u6765\u7684\u96ea\u767d\u4e73\u808c\u6709\u4e00\u62b9\u7ea2\u75d5\uff0c\u773c\u775b\u95ea\u8fc7\u4e00\u4e1d\u7591\u4e91\uff0c\u624b\u4f38\u5230\u524d\u6392\u6390\u4e86\u5f20\u606a\u7684\u8170\u4e00\u628a\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u6211\u5728\u5f00\u8f66\uff0c\u7a81\u7136\u6390\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u4e0d\u6e05\u695a\u665a\u6674\u4e3a\u4ec0\u4e48\u6390\u4ed6\uff0c\u53d7\u51a4\u7684\u558a\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u597d\u610f\u601d\u558a\u51a4\uff01\u201d\u665a\u6674\u6a2a\u4e86\u4ed6\u4e00\u773c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7a81\u7136\u610f\u8bc6\u5230\u8c22\u665a\u6674\u4e3a\u4ec0\u4e48\u8981\u6390\u5f20\u606a\uff0c\u5979\u80f8\u4e0a\u7684\u7ea2\u75d5\u662f\u6628\u5929\u7ed9\u70eb\u8fc7\u4e4b\u540e\u6ca1\u6709\u6d88\u6389\u7684\u75d5\u8ff9\uff0c\u591a\u534a\u7ed9\u8c22\u665a\u6674\u8bef\u4ee5\u4e3a\u662f\u4e0e\u5f20\u606a\u4eb2\u70ed\u65f6\u7559\u4e0b\u7684\u6de4\u75d5\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8033\u6735\u6709\u4e9b\u53d1\u70ed\uff0c\u62c9\u7740\u8c22\u665a\u6674\u7684\u624b\u6545\u4f5c\u4e0d\u77e5\u7684\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u597d\u610f\u601d\u7a7f\u7740\u8fd9\u4e48\u4f4e\u7684\u9886\u5b50\uff0c\u6628\u5929\u559d\u5496\u5561\u65f6\uff0c\u4e0d\u5c0f\u5fc3\u7ed9\u6cfc\u4e86\u6eda\u6c64\u7684\u5496\u5561\uff0c\u4e0d\u6562\u5c06\u8fd9\u91cc\u7ed9\u6342\u8d77\u6765\uff0c\u8fd9\u4e34\u65f6\u4e70\u4e86\u8fd9\u4e48\u4f4e\u9886\u5b50\u7684T\u6064\u2026\u2026\u201d</p><p>\u3000\u3000\u7ec6\u770b\u770b\u8fd8\u771f\u50cf\u70eb\u4f24\u7684\u75d5\u8ff9\u591a\u4e8e\u543b\u75d5\uff1b\u4e0d\u7136\u4e0d\u8981\u8bf4\u8fd9\u543b\u75d5\u4e86\uff0c\u7fdf\u4e39\u9752\u5728\u5f20\u606a\u8eab\u8fb9\u505a\u4e8b\u4e5f\u4e0d\u53ef\u80fd\u505a\u8fd9\u4e48\u6027\u611f\u7684\u8863\u670d\uff0c\u9664\u4e86\u52fe\u5f15\u5f20\u606a\u8fd8\u80fd\u52fe\u5f15\u5230\u8c01\uff1f</p><p>\u3000\u3000\u8c22\u665a\u6674\u6342\u7740\u5634\u5507\u7b11\u4e86\u7b11\uff0c\u4f38\u624b\u6478\u4e86\u6478\u5f20\u606a\u7ed9\u6390\u7684\u8170\u8089\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u597d\u610f\u601d\uff0c\u8bef\u4f1a\u4f60\u4e86\u3002\u201d\u4e5f\u4e0d\u4ecb\u610f\u8ba9\u7fdf\u4e39\u9752\u770b\u5230\u5979\u4e0e\u5f20\u606a\u4eb2\u5bc6\u7684\u52a8\u4f5c\uff0c\u8fd9\u79cd\u611f\u89c9\u7684\u786e\u5f88\u597d\uff0c\u4e4b\u524d\u5c31\u7b97\u5728\u8bb8\u601d\u9762\u524d\uff0c\u4e5f\u8981\u987e\u8651\u5230\u8bb8\u601d\u7684\u611f\u53d7\u3002</p><p>\u3000\u3000\u201c\u8bef\u4f1a\u6211\u4ec0\u4e48\u4e86\uff1f\u201d\u5f20\u606a\u83ab\u80c6\u5176\u5999\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u4f60\u5c31\u522b\u7ba1\u4e86\uff0c\u201d\u665a\u6674\u7b11\u4e86\u8d77\u6765\uff0c\u53c8\u89c9\u5f97\u7fdf\u4e39\u9752\u7684\u5fc3\u601d\u771f\u662f\u7075\u5de7\uff0c\u53c8\u96be\u5f97\u51fa\u6de4\u6ce5\u800c\u4e0d\u67d3\uff0c\u4fbf\u60f3\u5c06\u5728\u5efa\u90ba\u5f00\u5206\u6821\u7684\u4e8b\u60c5\u8bf4\u7ed9\u5979\u542c\uff0c\u8ba9\u5979\u5e2e\u81ea\u5df1\u51fa\u4e9b\u4e3b\u610f\uff0c\u201c\u5c0f\u606a\u73b0\u5728\u662f\u4e0d\u5f97\u4e86\u4e86\uff0c\u8fd9\u79cd\u5c0f\u4e8b\u60c5\uff0c\u6211\u90fd\u4e0d\u6562\u9ebb\u70e6\u4ed6\u5173\u5fc3\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u968f\u4fbf\u4f60\u600e\u4e48\u8bf4\uff0c\u6211\u90fd\u4e0d\u53cd\u6297\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u56de\u5e94\uff0c\u53c8\u95ee\u665a\u6674\uff0c\u201c\u4e2d\u5348\u53bb\u54ea\u91cc\u5403\u996d\uff0c\u4eca\u5929\u4f60\u8fd8\u6709\u4ec0\u4e48\u5b89\u6392\uff0c\u4e0d\u4f1a\u76f4\u63a5\u56de\u5b66\u5e9c\u5df7\u4e0b\u9986\u5b50\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u56de\u4f60\u81ea\u5df1\u56de\u53bb\uff0c\u6211\u60f3\u4e39\u9752\u7559\u4e0b\u6765\u966a\u6211\uff0c\u5206\u6821\u7684\u4e8b\u60c5\u4e0d\u6025\u7740\u8ba8\u8bba\uff0c\u4e24\u4e2a\u5973\u4eba\u53ef\u4ee5\u901b\u901b\u8857\u3001\u505a\u505a\u7f8e\u5bb9\uff0c\u4e24\u4e2a\u5973\u4eba\u5728\u4e00\u8d77\uff0c\u53ef\u505a\u7684\u4e8b\u60c5\u591a\u7740\u5462\u2026\u2026\u201d\u665a\u6674\u60f3\u4e86\u60f3\uff0c\u8bf4\u9053\uff0c\u201c\u90a3\u53bb\u9752\u5c71\u516c\u5bd3\u5427\uff0c\u6211\u4eec\u53ef\u4ee5\u81ea\u5df1\u52a8\u624b\u505a\u996d\u5403\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8f66\u62d0\u4e0a\u524d\u5f80\u9752\u5c71\u516c\u5bd3\u7684\u8def\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u5317\u4eac\u5403\u8fc7\u5468\u59e8\u505a\u7684\u83dc\uff0c\u5fc3\u60f3\u5403\u4ec0\u4e48\u8fd8\u662f\u81ea\u5df1\u52a8\u624b\u51c6\u5907\u7684\u597d\u3002\u201d</p><p>\u3000\u3000\u9752\u5c71\u516c\u5bd3\u662f\u5f20\u606a\u4e0e\u665a\u6674\u5e7d\u4f1a\u7684\u79d8\u5bc6\u5730\u70b9\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u665a\u6674\u5728\u5efa\u90ba\u5c31\u6709\u6ca1\u4efb\u4f55\u843d\u811a\u5730\uff0c\u8981\u662f\u8d76\u4e0a\u5979\u5230\u5efa\u90ba\u6765\u800c\u5f20\u606a\u4e0d\u5728\u5efa\u90ba\uff0c\u5979\u591a\u534a\u4f1a\u9009\u62e9\u4f4f\u8fdb\u9152\u5e97\u800c\u6781\u5c11\u4f1a\u4f4f\u8fdb\u9752\u5c71\u516c\u5bd3\u3002</p><p>\u3000\u3000\u665a\u6674\u5efa\u8bae\u53bb\u9752\u5c71\u516c\u5bd3\uff0c\u770b\u6765\u5979\u5bf9\u7fdf\u4e39\u9752\u6beb\u65e0\u82a5\u8482\uff0c\u5973\u4eba\u95f4\u7684\u5c0f\u5fc3\u773c\u4e0e\u53cb\u8c0a\uff0c\u5f20\u606a\u4ece\u6765\u90fd\u641e\u4e0d\u6e05\u695a\u7684\u3002</p><p>\u3000\u3000\u9014\u4e2d\u7ecf\u8fc7\u51c0\u83dc\u8d85\u5e02\uff0c\u4e70\u4e86\u83dc\u5230\u9752\u5c71\u516c\u5bd3\u3002\u7fdf\u4e39\u9752\u867d\u7136\u8d1f\u8d23\u9152\u5e97\u7ecf\u8425\u6709\u597d\u4e9b\u5e74\uff0c\u53ef\u662f\u8ddf\u5979\u7684\u53a8\u827a\u597d\u4e0d\u597d\u6ca1\u6709\u4e00\u4e01\u70b9\u7684\u5173\u7cfb\uff0c\u665a\u6674\u4e5f\u662f\u90a3\u79cd\u79bb\u5f00\u9910\u9986\u4e0e\u4fdd\u59c6\u3001\u65b9\u4fbf\u98df\u54c1\u3001\u901f\u98df\u9762\u5f88\u53ef\u80fd\u4f1a\u5c06\u81ea\u5df1\u997f\u6b7b\u7684\u4eba\uff0c\u505a\u996d\u7684\u91cd\u4efb\u5c31\u5168\u538b\u5728\u5f20\u606a\u4e00\u4eba\u7684\u5934\u4e0a\u4e86\uff0c\u4ed6\u4e0d\u6e05\u695a\u4e24\u4e2a\u6027\u611f\u719f\u5973\u8eb2\u5728\u623f\u95f4\u91cc\u5527\u5527\u55b3\u55b3\u7684\u8c08\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4e0b\u5348\u4e0d\u80fd\u8017\u5728\u8fd9\u91cc\uff0c\u845b\u5efa\u5fb7\u5728\u534e\u590f\u7535\u5b50\u7684\u6210\u7acb\u5927\u4f1a\u53d1\u51fa\u5730\u57df\u503e\u5411\u6027\u7684\u8a00\u8bba\uff0c\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u3001\u738b\u7ef4\u5747\u7b49\u4eba\u5fc3\u91cc\u542c\u4e86\u4e00\u5b9a\u4f1a\u6709\u4e9b\u4e0d\u8212\u670d\uff0c\u867d\u7136\u534e\u590f\u7535\u5b50\u76ee\u524d\u8fd8\u65e0\u6cd5\u5bf9\u9526\u6e56\u65bd\u52a0\u5f71\u54cd\uff0c\u6bd5\u7adf\u534e\u590f\u7535\u5b50\u5185\u90e8\u7684\u6574\u5408\u5c31\u8981\u7275\u626f\u4ed6\u4eec\u6781\u5927\u7684\u7cbe\u529b\uff0c\u4f46\u662f\u845b\u5efa\u5fb7\u90fd\u653e\u51fa\u8bdd\u6765\u4e86\uff0c\u4e5f\u8981\u8003\u8651\u5c0f\u5c0f\u7684\u56de\u656c\u4e00\u4e0b\u3002\u9526\u6e56\u8981\u662f\u6084\u65e0\u58f0\u606f\uff0c\u522b\u4eba\u8fd8\u53ea\u5f53\u9526\u6e56\u4e0d\u5b58\u5728\u5462\u3002</p><p>\u3000\u3000\u5f20\u606a\u5403\u8fc7\u996d\uff0c\u5c06\u6536\u62fe\u7897\u7b77\u7684\u6d3b\u4ea4\u7ed9\u4e24\u4e2a\u5973\u4eba\uff0c\u5c31\u5148\u9a7e\u8f66\u79bb\u5f00\u4e86\u3002</p><p>\u3000\u3000\u655e\u5f00\u5fc3\u6249\u6765\u76f8\u5904\uff0c\u76f8\u5904\u8d8a\u4e45\uff0c\u8d8a\u4f1a\u53d1\u73b0\u5bf9\u65b9\u6709\u7740\u81ea\u5df1\u66f4\u591a\u7684\u5171\u540c\u70b9\uff0c\u867d\u7136\u8c22\u665a\u6674\u4e0e\u7fdf\u4e39\u9752\u6709\u7740\u622a\u7136\u4e0d\u540c\u7684\u51fa\u8eab\u3002\u8c22\u665a\u6674\u51fa\u8eab\u5bcc\u8d35\uff0c\u5ac1\u4eba\u53c8\u662f\u5ac1\u5165\u5f90\u5bb6\uff0c\u4e00\u751f\u96cd\u5bb9\u534e\u8d35\uff0c\u7ed9\u7cbe\u81f4\u5305\u88c5\u8d77\u6765\u7684\u8d35\u5987\u4eba\u751f\u6d3b\u770b\u4e0a\u53bb\u534e\u4e3d\u7eda\u70c2\uff0c\u7fdf\u4e39\u9752\u51fa\u8eab\u5c31\u8981\u7b97\u66f2\u6298\u51c4\u60e8\uff0c\u7528\u7740\u7edd\u72e0\u7684\u5fc3\u673a\u82e6\u82e6\u6323\u624e\u5230\u4eca\u65e5\u624d\u6562\u7a0d\u5598\u4e00\u53e3\u6c14\u3002\u4f46\u662f\u4e0d\u540c\u7684\u7ecf\u5386\u5e76\u4e0d\u80fd\u51b3\u5b9a\u4e24\u4e2a\u4eba\u5c31\u4e0d\u80fd\u6709\u5171\u540c\u4e4b\u5904\u3002\u5728\u8bd5\u8863\u95f4\u91cc\u6362\u8863\u670d\uff0c\u8c22\u665a\u6674\u770b\u5230\u7fdf\u4e39\u9752\u53ea\u7a7f\u7740\u5185\u8863\u7684\u8eab\u4f53\uff0c\u751a\u81f3\u53d1\u73b0\u4e24\u4eba\u7684\u8eab\u6750\u4e5f\u6709\u8bb8\u591a\u76f8\u4f3c\u4e4b\u5916\uff0c\u4e00\u822c\u9ad8\u77ee\uff0c\u4e30\u81c0\u7ea4\u817f\u5c31\u8ddf\u6a21\u5b50\u91cc\u590d\u5236\u51fa\u6765\u4f3c\u7684\uff0c\u8170\u56f4\u3001\u81c0\u56f4\u4e5f\u4e00\u6a21\u4e00\u6837\uff0c\u817f\u957f\u4e5f\u51e0\u4e4e\u4e00\u81f4\uff0c\u665a\u6674\u624b\u6258\u4e86\u6258\u81ea\u5df1\u7684\u80f8\u90e8\uff0c\u8bf4\u9053\uff1a\u201c\u9664\u4e86\u8fd9\u91cc\u6211\u8981\u5c0f\u5f97\u591a\u4e4b\u5916\uff0c\u5176\u4ed6\u5730\u65b9\u597d\u50cf\u6ca1\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u6709\u5acc\u4f60\u90a3\u91cc\u5c0f\uff1f\u90a3\u4ed6\u591f\u8d2a\u5fc3\u7684\u3002\u201d\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u90fd\u89c9\u5f97\u81ea\u5df1\u7684\u592a\u7d2f\u8d58\uff0c\u4e0d\u6653\u5f97\u6709\u6ca1\u6709\u7f29\u80f8\u7684\u624b\u672f\u3002\u201d\u7fdf\u4e39\u9752\u5fc3\u91cc\u60f3\u96be\u602a\u5f20\u606a\u6628\u5929\u80fd\u4e00\u4e0b\u5b50\u627e\u5bf9\u5c3a\u7801\uff0c\u539f\u6765\u5fc3\u91cc\u65e9\u62ff\u6765\u81ea\u5df1\u7684\u8eab\u4f53\u8ddf\u8c22\u665a\u6674\u6bd4\u8f83\u8fc7\u4e86\uff0c\u60f3\u5230\u90a3\u5929\u5728\u6e29\u6cc9\u6e56\u7684\u5c9b\u5c4b\u91cc\u7ed9\u5f20\u606a\u770b\u5f97\u7cbe\u5149\uff0c\u5fc3\u91cc\u6709\u7740\u5f02\u6837\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5f20\u606a\u591c\u91cc\u8981\u4e0e\u7f57\u541b\u3001\u738b\u7ef4\u5747\u4e00\u8d77\u5403\u996d\uff0c\u8131\u4e0d\u5f00\u8eab\u6765\uff0c\u53ea\u662f\u7ed9\u665a\u6674\u6253\u4e86\u4e2a\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u8c22\u665a\u6674\u7559\u7fdf\u4e39\u9752\u665a\u4e0a\u966a\u81ea\u5df1\uff0c\u7fdf\u4e39\u9752\u72b9\u8c6b\u7684\u8bf4\u9053\uff1a\u201c\u6211\u7559\u4e0b\u6765\u4e0d\u662f\u788d\u4e8b\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u80fd\u788d\u4ec0\u4e48\u4e8b\uff1f\u90a3\u8ba9\u4ed6\u665a\u4e0a\u4e0d\u8981\u8fc7\u6765\u5c31\u662f\u3002\u201d\u8c22\u665a\u6674\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4e5f\u4e0d\u7ecf\u5e38\u89c1\u9762\u5427\uff1f\u201d\u7fdf\u4e39\u9752\u8fd9\u6bb5\u65f6\u95f4\u4e00\u76f4\u5728\u5f20\u606a\u8eab\u8fb9\uff0c\u77e5\u9053\u8c22\u665a\u6674\u4e5f\u5c31\u4e0a\u56de\u5728\u5317\u4eac\u4e0e\u5f20\u606a\u89c1\u8fc7\u9762\uff0c\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u604b\u5978\u60c5\u70ed\u8c01\u8981\u6709\u80c6\u91cf\u5f53\u7535\u706f\u6ce1\u53ef\u80fd\u7ed9\u4ed6\u4eec\u4fe9\u6740\u4eba\u706d\u53e3\u3002</p><p>\u3000\u3000\u201c\u90a3\u4f60\u8fd9\u51e0\u5e74\u6709\u522b\u7684\u7537\u4eba\u6ca1\u6709\uff1f\u201d\u8c22\u665a\u6674\u95ee\u7fdf\u4e39\u9752\u3002</p><p>\u3000\u3000\u201c\u8981\u7537\u4eba\u505a\u4ec0\u4e48\uff1f\u201d\u7fdf\u4e39\u9752\u8138\u6709\u4e9b\u70ed\uff0c\u53ef\u6ca1\u6709\u89c9\u5f97\u8ddf\u8c22\u665a\u6674\u8c08\u8fd9\u8bdd\u9898\u6709\u4ec0\u4e48\u4e0d\u59a5\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e0d\u5c31\u5f97\u4e86\u3002\u201d</p><p>\u3000\u3000\u591c\u91cc\uff0c\u8c22\u665a\u6674\u4e0e\u7fdf\u4e39\u9752\u5750\u5728\u6c99\u53d1\u4e0a\u770b\u65e7\u7535\u5f71\u65f6\uff0c\u7a81\u7136\u63a5\u5230\u674e\u660e\u745c\u4ece\u6d77\u5dde\u6253\u6765\u7684\u7535\u8bdd\uff0c\u4e09\u4e2a\u4e2b\u5934\u4e0b\u5348\u5728\u5916\u9762\u5403\u4e86\u4e0d\u5e72\u51c0\u7684\u98df\u7269\uff0c\u4e00\u8d77\u95f9\u809a\u5b50\u8179\u6cfb\u3002</p><p>\u3000\u3000\u867d\u7136\u674e\u660e\u745c\u5728\u6d77\u5dde\u6709\u8bf7\u533b\u751f\u770b\u8fc7\uff0c\u4f46\u662f\u8179\u6cfb\u75c7\u72b6\u8fd8\u6ca1\u6709\u5b8c\u5168\u6d88\u9664\uff0c\u8c22\u665a\u6674\u653e\u4e0d\u4e0b\u5fc3\u6765\uff0c\u5979\u8fd9\u51e0\u5e74\u867d\u7136\u80fd\u4ece\u5f20\u606a\u90a3\u91cc\u5f97\u5230\u6781\u5927\u7684\u6170\u85c9\uff0c\u4f46\u662f\u5927\u534a\u7684\u5fc3\u601d\u8fd8\u843d\u5728\u82b7\u5f64\u7684\u8eab\u4e0a\uff0c\u7acb\u5373\u901a\u77e5\u52a9\u624b\u5f20\u5ead\u8fd8\u6709\u53f8\u673a\u5230\u9752\u5e74\u516c\u5bd3\u63a5\u5979\u56de\u6d77\u5dde\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u79bb\u5f00\u9752\u5c71\u516c\u5bd3\uff0c\u7fdf\u4e39\u9752\u4e5f\u8981\u56de\u53bb\uff0c\u8c22\u665a\u6674\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u4f4f\u4e0b\u6765\u597d\u4e86\uff0c\u8fd9\u91cc\u51fa\u79df\u8f66\u6bd4\u8f83\u5c11\uff0c\u4f60\u8fd8\u6709\u884c\u674e\u52a0\u4e0a\u4eca\u5929\u4e70\u7684\u4e00\u5806\u4e1c\u897f\uff0c\u4e00\u4e2a\u4eba\u600e\u4e48\u62ff\uff1f\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u5728\u8f66\u4e0a\u7ed9\u5f20\u606a\u6253\u4e86\u4e24\u901a\u7535\u8bdd\uff0c\u4e24\u901a\u7535\u8bdd\u90fd\u662f\u5fd9\u97f3\uff0c\u5fc3\u60f3\u5c31\u7b97\u5f20\u606a\u591c\u91cc\u6478\u56de\u53bb\u770b\u5230\u7fdf\u4e39\u9752\u4e5f\u4e0d\u53ef\u80fd\u53d1\u751f\u4ec0\u4e48\uff0c\u9876\u591a\u7ed9\u7fdf\u4e39\u9752\u770b\u5230\u7b11\u8bdd\u7f62\u4e86\uff0c\u5fc3\u601d\u5c31\u6574\u4e2a\u7684\u843d\u5728\u82b7\u5f64\u8eab\u4e0a\uff0c\u4e0d\u518d\u53bb\u7ba1\u5f20\u606a\u4e86\u3002</p></article>"}}}]);
