(window.webpackJsonp=window.webpackJsonp||[]).push([[992],{1015:function(p){p.exports={chapterTitle:null,title:"\u7b2c989\u7ae0 \u5546\u4e1a\u5229\u76ca",content:"<article><h2>\u7b2c989\u7ae0 \u5546\u4e1a\u5229\u76ca</h2><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u4e3b\u8981\u5c06\u7cbe\u529b\u96c6\u4e2d\u5728\u6e56\u4e1c\u5546\u5708\u9879\u76ee\u4e0a\uff0c\u4f46\u4e0d\u610f\u5473\u7740\u5728\u5176\u4ed6\u5730\u533a\u5c31\u4e0d\u518d\u62ff\u5730\u5f00\u53d1\u5730\u4ea7\u4e86\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\u4e0b\u534a\u5e74\uff0c\u9ad8\u65b0\u533a\u5bf9\u97f3\u4e50\u5b66\u9662\u3001\u5efa\u90ba\u8d22\u7ecf\u5927\u5b66\u3001\u653f\u6cd5\u5b66\u9662\u4e0e\u4e1c\u6d77\u5927\u5b66\u53ca\u5efa\u90ba\u5e08\u8303\u5927\u5b66\u4e4b\u95f4\u7684\u5b66\u56ed\u8def\u8fdb\u884c\u7ffb\u65b0\u6539\u9020\uff0c\u539f\u5148\u8def\u4e24\u4fa7\u7684\u6c11\u623f\u4f4e\u77ee\u7834\u65e7\u90fd\u8981\u62c6\u9664\u6389\uff0c\u96f6\u96f6\u788e\u788e\u7684\uff0c\u5dee\u4e0d\u591a\u4e00\u4e24\u767e\u4ea9\u7684\u6837\u5b50\uff0c\u9ad8\u65b0\u533a\u5bf9\u8fd9\u4e9b\u96f6\u788e\u5730\u5757\u6ca1\u6709\u7edf\u4e00\u7684\u89c4\u5212\uff0c\u8981\u4e48\u7ed9\u90bb\u8fd1\u7684\u9ad8\u6821\u5e76\u8d2d\u8fc7\u53bb\u6269\u5927\u6821\u56ed\uff0c\u8981\u4e48\u7ed9\u5f00\u53d1\u5546\u62ff\u53bb\u5f00\u53d1\u5546\u4e1a\u5730\u4ea7\u3002</p><p>\u3000\u3000\u4e1c\u5927\u5317\u5927\u95e8\u4e0e\u9752\u5e74\u516c\u5bd3\u5317\u95e8\u4e4b\u95f4\u7684\u90a3\u5757\u5730\u4e5f\u5c31\u4e09\u5341\u4ea9\u4e0d\u5230\u7684\u6837\u5b50\uff0c\u662f\u76f8\u5bf9\u8f83\u5927\u7684\u4e00\u6574\u5757\u5730\uff0c\u4e1c\u5927\u5bf9\u8fd9\u5757\u5730\u4e00\u76f4\u90fd\u5f88\u6709\u5174\u8da3\uff0c\u4e0d\u8fc7\u4e16\u7eaa\u9526\u6e56\u63d0\u51fa\u8981\u62ff\u8fd9\u5757\u5730\u4e4b\u540e\uff0c\u4e1c\u5927\u5c31\u6ca1\u6709\u8ddf\u4e16\u7eaa\u9526\u6e56\u4e89\u3002</p><p>\u3000\u3000\u8fd9\u5757\u5730\u5728\u5730\u7f18\u4e0a\u4e0e\u5b66\u5e9c\u5df7\u662f\u76f8\u8fde\u7684\uff0c\u9274\u4e8e\u5b66\u5e9c\u5df7\u9879\u76ee\u7684\u5de8\u5927\u6210\u529f\uff0c\u8fd9\u5757\u5730\u4f5c\u4e3a\u5b66\u5e9c\u5df7\u7684\u5ef6\u4f38\u6bb5\u6765\u5f00\u53d1\uff0c\u662f\u6700\u7b26\u5408\u5546\u4e1a\u5229\u76ca\u7684\uff1b\u518d\u8bf4\u9526\u6e56\u4e5d\u516b\u5e74\u5411\u4e1c\u5927\u76f8\u5173\u9662\u7cfb\u6350\u8d60\u8d85\u8fc7\u4e00\u4e2a\u4ebf\u7684\u5b66\u79d1\u5efa\u8bbe\u7ecf\u8d39\uff0c\u4e1c\u5927\u4e3b\u52a8\u5c06\u5730\u8ba9\u7ed9\u4e16\u7eaa\u9526\u6e56\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5947\u602a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u53ef\u4ee5\u8bf4\u662f\u5efa\u90ba\u5730\u4ea7\u4e1a\u8fd1\u5e74\u6765\u7684\u7ecf\u5178\u4e4b\u4f5c\uff0c\u5b66\u5e9c\u5df7\u6539\u6269\u5efa\u4e4b\u540e\uff0c\u5e95\u5c42\u5546\u4e1a\u5efa\u7b51\u9762\u79ef\u589e\u52a0\u4e86\u8fd1\u4e00\u500d\uff0c\u4e5f\u901a\u8fc7\u5b8c\u5584\u7684\u5546\u4e1a\u89c4\u5212\u4e0e\u8fd0\u4f5c\uff0c\u4e00\u5e74\u591a\u7684\u65f6\u95f4\u4f7f\u5f97\u5b66\u5e9c\u5df7\u7684\u7e41\u8363\u80dc\u8fc7\u5f80\u6614\uff0c\u7531\u4e8e\u5546\u94fa\u7ecf\u8425\u5c42\u8d44\u7684\u63d0\u9ad8\uff0c\u5546\u94fa\u79df\u91d1\u76f8\u5e94\u7684\u6bd4\u8f83\u4e4b\u524d\u4e5f\u63d0\u9ad8\u4e86\u4e00\u500d\u6709\u4f59\u3002</p><p>\u3000\u3000\u5f53\u521d\u4ee5\u6bcf\u5e73\u65b9\u7c73\u516b\u5343\u5143\u7684\u4ef7\u683c\u5c06\u5916\u8857\u62c6\u8fc1\u5e97\u9762\u8f6c\u8ba9\u7ed9\u4e16\u7eaa\u9526\u6e56\u3001\u4e00\u4e0b\u5b50\u62ff\u8d70\u51e0\u5341\u4e07\u751a\u81f3\u4e0a\u767e\u4e07\u8865\u507f\u6b3e\u7684\u62c6\u8fc1\u6237\u8fd9\u65f6\u5019\u5403\u540e\u6094\u836f\u90fd\u665a\u4e86\uff0c\u5e97\u79df\u589e\u6da8\u4e00\u500d\uff0c\u610f\u5473\u7740\u5b9e\u9645\u7684\u623f\u4ef7\u4e5f\u589e\u6da8\u4e86\u6709\u4e00\u500d\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u6b64\u65f6\u624b\u91cc\u63e1\u7740\u5b66\u5e9c\u5df7\u8fd1\u56db\u5206\u4e4b\u4e09\u7684\u4ea7\u6743\uff0c\u4e5d\u516b\u5e74\u4ec5\u5e97\u79df\u6536\u5165\u5c31\u8d85\u8fc7\u56db\u5343\u4e07\uff0c\u6709\u4e86\u5b66\u5e9c\u5df7\u7684\u6210\u529f\uff0c\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u5185\u90e8\u5bf9\u5f00\u53d1\u5b66\u5e9c\u5df7\u897f\u5ef6\u6bb5\u9879\u76ee\u81ea\u7136\u4e5f\u5145\u6ee1\u4e86\u6781\u5927\u7684\u671f\u5f85\uff0c\u4ec5\u5f00\u53d1\u65b9\u6848\u5c31\u63a8\u7ffb\u6389\u4e24\u6b21\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u9879\u76ee\u7ec4\u53ec\u5f00\u7b2c\u4e09\u6b21\u65b9\u6848\u8ba8\u8bba\u4f1a\uff0c\u90b5\u81f3\u521a\u62bd\u51fa\u65f6\u95f4\u8fc7\u6765\u53c2\u52a0\uff0c\u6ca1\u60f3\u5230\u5728\u4f1a\u8bae\u9014\u4e2d\u63a5\u5230\u5f20\u606a\u7684\u7535\u8bdd\uff0c\u653e\u4e0b\u7535\u8bdd\uff0c\u5408\u4e0a\u8bb0\u5f55\u672c\uff0c\u7ad9\u8d77\u6765\u5c31\u5ba3\u5e03\u5b66\u5e9c\u5df7\u897f\u5ef6\u6bb5\u9879\u76ee\u6682\u65f6\u51bb\u7ed3\u8d77\u6765\u3002</p><p>\u3000\u3000\u867d\u8bf4\u90b5\u81f3\u521a\u5728\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u5185\u90e8\u6709\u7740\u7edd\u9ad8\u7684\u5a01\u4fe1\uff0c\u4f46\u662f\u7a81\u7136\u5ba3\u5e03\u8fd9\u4e48\u591a\u4eba\u4e3a\u4e4b\u594b\u6597\u4e86\u4e24\u4e2a\u591a\u6708\u7684\u9879\u76ee\u7a81\u7136\u51bb\u7ed3\uff0c\u9879\u76ee\u7ec4\u7684\u6210\u5458\u81ea\u7136\u7edd\u96be\u7406\u89e3\uff0c\u5728\u4e0b\u9762\u5c31\u8bae\u8bba\u8d77\u6765\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u4e5f\u4e0d\u8ddf\u4e0b\u9762\u4eba\u591a\u89e3\u91ca\u4ec0\u4e48\uff0c\u4ed6\u8fd8\u4e00\u809a\u5b50\u7684\u7591\u60d1\u5462\uff0c\u8981\u5f00\u53d1\u7ecf\u7406\u9f9a\u8c6a\u8ddf\u4ed6\u53bb\u89c1\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u7a81\u7136\u5c31\u51bb\u7ed3\u9879\u76ee\u4e86\uff1f\u6211\u4eec\u64cd\u4f5c\u7684\u7a0b\u5e8f\u53ef\u90fd\u6ca1\u6709\u4ec0\u4e48\u95ee\u9898\u554a\uff0c\u4e1c\u5927\u90a3\u91cc\u5bf9\u6211\u4eec\u62ff\u90a3\u5757\u5730\u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f\u89c1\uff0c\u4e0d\u4f1a\u6709\u4eba\u5728\u80cc\u540e\u4f7f\u4ec0\u4e48\u7eca\u5b50\u5427\uff1f\u201d\u9f9a\u8c6a\u662f\u5f00\u53d1\u56db\u51e4\u6865\u996e\u98df\u5e7f\u573a\u91cc\u5c31\u8ddf\u90b5\u81f3\u521a\u7684\u8001\u4eba\uff0c\u4e0a\u4e86\u90b5\u81f3\u521a\u7684\u8f66\uff0c\u618b\u4e0d\u4f4f\u809a\u5b50\u91cc\u7684\u7591\u95ee\uff0c\u5c31\u76f4\u63a5\u95ee\u90b5\u81f3\u521a\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u770b\u4e2d\u90a3\u5757\u5730\u4e86\u2026\u2026\u201d\u90b5\u81f3\u521a\u8ddf\u4ed6\u8bf4\u9053\uff0c\u201c\u4ed6\u8981\u62ff\u90a3\u5757\u5730\u5efa\u5ea7\u56fe\u4e66\u9986\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u9f9a\u8c6a\u5fae\u5fae\u4e00\u6123\uff0c\u5927\u8001\u677f\u8981\u5efa\u56fe\u4e66\u9986\u8fd9\u4e8b\uff0c\u4ed6\u4e0d\u80fd\u505a\u4ec0\u4e48\u8bc4\u5224\uff0c\u5c31\u7b97\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u80fd\u5f53\u7740\u90b5\u81f3\u521a\u7684\u9762\uff0c\u53ea\u662f\u72b9\u89c9\u5f97\u53ef\u60dc\uff0c\u8bf4\u9053\uff0c\u201c\u90a3\u4e09\u5341\u4ea9\u5730\uff0c\u5149\u5730\u4ef7\u5c31\u4e24\u5343\u516d\u767e\u591a\u4e07\uff0c\u5efa\u6210\u540e\uff0c\u6211\u4eec\u80fd\u83b7\u5f97\u7684\u51c0\u5229\u6da6\u7edd\u5bf9\u8981\u8d85\u8fc7\u56db\u5343\u4e07\uff0c\u62ff\u90a3\u5757\u5730\u53bb\u5efa\u56fe\u4e66\u9986\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u53ef\u60dc\u4e86\uff1f\u65b0\u6d66\u5927\u9053\u5f80\u4e1c\uff0c\u6709\u7684\u662f\u7a7a\u5730\uff0c\u968f\u4fbf\u5212\u4e00\u5757\u5730\u51fa\u6765\u5efa\u56fe\u4e66\u9986\u5c82\u4e0d\u662f\u66f4\u597d\uff1f\u201d</p><p>\u3000\u3000\u201c\u5148\u53bb\u89c1\u606a\u5c11\u518d\u8bf4\u2026\u2026\u201d\u90b5\u81f3\u521a\u4e5f\u89c9\u5f97\u6709\u4e9b\u53ef\u60dc\u4e86\uff0c\u60f3\u7740\u8fc7\u53bb\u89c1\u9762\u770b\u80fd\u4e0d\u80fd\u6539\u53d8\u5f20\u606a\u7684\u521d\u8877\uff0c\u5fc3\u91cc\u76d8\u7b97\u7740\uff0c\u5c06\u4e24\u5343\u516d\u767e\u4e07\u7684\u5730\u4ef7\u4ee5\u53ca\u5c06\u6765\u53ef\u80fd\u4f1a\u6709\u7684\u56db\u5343\u4e07\u51c0\u5229\u4e00\u5171\u516d\u5343\u516d\u767e\u4e07\u90fd\u6350\u51fa\u6765\uff0c\u5c31\u80fd\u5efa\u4e00\u5ea7\u8d85\u8c6a\u534e\u7684\u56fe\u4e66\u9986\uff0c\u4e1c\u5927\u65b0\u6d66\u6821\u56ed\u7684\u65b0\u56fe\u4e66\u9986\u53f7\u79f0\u85cf\u4e66\u8d85\u767e\u4e07\u518c\uff0c\u6295\u8d44\u4e5f\u4e0d\u8fc7\u4e09\u5343\u4e07\u800c\u5df2\u2014\u2014\u90b5\u81f3\u521a\u4e00\u65f6\u8fd8\u60f3\u8c61\u4e0d\u51fa\u62e5\u6709\u4e07\u4eba\u9605\u89c8\u5ba4\u7684\u56fe\u4e66\u9986\u8be5\u662f\u591a\u5927\u7684\u89c4\u6a21\u3002</p><p>\u3000\u3000\u8f66\u5b50\u505c\u5728\u9752\u5e74\u516c\u5bd3\u5317\u9762\u7684\u505c\u8f66\u573a\u4e0a\uff0c\u6070\u770b\u5230\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u5c31\u5728\u505c\u8f66\u70b9\u6b63\u5bf9\u9762\u7684\u6c49\u5821\u5c4b\u91cc\uff0c\u91cc\u9762\u4eba\u4e0d\u5c11\uff0c\u4e0d\u8fc7\u9648\u5983\u84c9\u7a7f\u7740\u73ab\u7ea2\u8272\u7684\u7fbd\u7ed2\u886b\uff0c\u6bd4\u8f83\u663e\u773c\uff0c\u7fdf\u4e39\u9752\u8fd8\u6bd4\u4ed6\u4eec\u5148\u8d76\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u80cc\u5bf9\u4ed6\u4eec\u4f0f\u6848\u5199\u4e9b\u4ec0\u4e48\uff0c\u90b5\u81f3\u521a\u4e0e\u5f00\u53d1\u7ecf\u7406\u9f9a\u8c6a\u4e0b\u8f66\u8d70\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u8fc7\u6765\u4e86\uff0c\u201d\u5f20\u606a\u770b\u4e86\u770b\u9f9a\u8c6a\uff0c\u95ee\u90b5\u81f3\u521a\uff0c\u201c\u4ed6\u662f\u4f60\u7ed9\u6211\u627e\u6765\u7684\u8d1f\u8d23\u4eba\uff1f\u201d\u4e5f\u4e0d\u7b49\u90b5\u81f3\u521a\u56de\u7b54\uff0c\u5c31\u6ee1\u53e3\u201c\u597d\u597d\u597d\u201d\u7684\u7ad9\u8d77\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u91cc\u8bf4\u8bdd\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u6211\u4eec\u53bb\u4f60\u8f66\u91cc\u8c08\u53bb\u3002\u201d</p><p>\u3000\u3000\u6c49\u5821\u5c4b\u91cc\u4eba\u591a\u8033\u6742\uff0c\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u3001\u7fdf\u4e39\u9752\u8fd8\u6709\u90b5\u81f3\u521a\u3001\u9f9a\u8c6a\u5750\u4e0a\u8f66\uff0c\u5012\u662f\u8ba9\u53f8\u673a\u4e0b\u8f66\u53bb\u6c49\u5821\u5c4b\u91cc\u5750\u7740\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u4ed6\u4e00\u76f4\u90fd\u5728\u5199\u5199\u753b\u753b\u7684\u90a3\u5f20\u7eb8\u9012\u7ed9\u90b5\u81f3\u521a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7b80\u5355\u7684\u8ba1\u7b97\u4e86\u4e00\u4e0b\uff0c\u8981\u5efa\u4e00\u5ea7\u53ef\u4ee5\u5bb9\u7eb3\u4e07\u4eba\u9605\u8bfb\u7684\u56fe\u4e66\u9986\uff0c\u5dee\u4e0d\u591a\u9700\u8981\u6295\u8d44\u4e24\u4ebf\u56db\u5343\u4e07\u5230\u4e24\u4ebf\u516d\u5343\u4e07\u7684\u6837\u5b50\uff0c\u53e6\u5916\u8865\u507f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u7684\u571f\u5730\u53ca\u76f8\u5173\u635f\u5931\uff0c\u6211\u7ed9\u4f60\u4eec\u4e09\u4e2a\u4ebf\uff0c\u4f60\u4eec\u5e2e\u6211\u5c06\u8fd9\u5ea7\u56fe\u4e66\u9986\u7ed9\u5efa\u8d77\u6765\u2014\u2014\u6211\u60f3\u8fd9\u5757\u5730\u66f4\u6539\u4f7f\u7528\u76ee\u7684\uff0c\u5e02\u91cc\u4e0d\u4f1a\u6709\u4ec0\u4e48\u610f\u89c1\u7684\uff0c\u6211\u521a\u7ed9\u9648\u4fe1\u751f\u6253\u8fc7\u7535\u8bdd\uff0c\u4e5f\u7ed9\u8bb8\u601d\u6253\u8fc7\u7535\u8bdd\uff0c\u8bb8\u601d\u4f1a\u5c3d\u5feb\u5230\u5efa\u90ba\u6765\uff0c\u4f60\u770b\u80fd\u4e0d\u80fd\u8d76\u5728\u5e74\u524d\u5c06\u5728\u521d\u6b65\u7684\u8bbe\u8ba1\u7a3f\u3001\u5f00\u53d1\u65b9\u6848\u62ff\u51fa\u6765\uff0c\u5728\u660e\u5e74\u79cb\u5b63\u5f00\u5b66\u4e4b\u524d\uff0c\u5c06\u56fe\u4e66\u9986\u7ed9\u5efa\u8d77\u6765\uff1f\u201d</p><p>\u3000\u3000\u89c1\u90b5\u81f3\u521a\u8138\u4e0a\u7a0d\u6709\u4e9b\u72b9\u8c6b\uff0c\u5f20\u606a\u7b11\u7740\u95ee\uff1a\u201c\u600e\u4e48\u4e86\uff0c\u6709\u4e9b\u820d\u4e0d\u5f97\u8fd9\u5757\u5730\uff1f\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u672c\u6765\u8fd8\u60f3\u7740\u770b\u6709\u6ca1\u6709\u673a\u4f1a\u529d\u5f20\u606a\u6253\u6d88\u5ff5\u5934\u6216\u8005\u5c06\u56fe\u4e66\u9986\u5efa\u5230\u5176\u4ed6\u5730\u65b9\u53bb\uff0c\u89c1\u5f20\u606a\u5728\u8349\u7a3f\u7eb8\u4e0a\u753b\u51fa\u4e00\u5f20\u5730\u5f62\u793a\u610f\u56fe\uff0c\u8fd9\u5757\u5730\u6070\u597d\u5728\u71d5\u5f52\u6e56\u9ad8\u6821\u7fa4\u7684\u4e2d\u5fc3\uff0c\u4fbf\u80fd\u77e5\u9053\u5f20\u606a\u7684\u610f\u601d\uff0c\u518d\u8bf4\u5f20\u606a\u4ed6\u90fd\u8ba1\u5212\u597d\u8981\u62ff\u51fa\u4e09\u4e2a\u4ebf\u51fa\u6765\uff0c\u4e5f\u4f1a\u8865\u507f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u7684\u635f\u5931\uff0c\u4ed6\u8fd8\u80fd\u8bf4\u820d\u4e0d\u5f97\u8fd9\u5757\u5730\uff1f\u7b11\u7740\u8bf4\uff1a\u201c\u606a\u5c11\u90fd\u6709\u5927\u4f53\u7684\u65b9\u6848\u4e86\uff0c\u6211\u4eec\u8fd8\u662f\u6309\u7167\u4f60\u7684\u610f\u601d\u6765\u2026\u2026\u201d\u4e0d\u518d\u591a\u8bf4\u4ec0\u4e48\uff0c\u5750\u4e0b\u6765\u62ff\u8d77\u5f20\u606a\u5199\u5f97\u6f66\u8349\u7684\u8349\u7a3f\u7eb8\u8ba4\u771f\u770b\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u4e0b\u8f66\u5e2e\u90b5\u81f3\u521a\u3001\u9f9a\u8c6a\u4e70\u4e86\u4e24\u676f\u70ed\u5496\u5561\u518d\u56de\u6765\uff0c\u5927\u51b7\u5929\u7684\uff0c\u5916\u9762\u5929\u5bd2\u5730\u51b0\uff0c\u5750\u5728\u8f66\u91cc\uff0c\u559d\u4e00\u676f\u70ed\u5496\u5561\u7684\u611f\u89c9\u76f8\u5f53\u4e0d\u9519\u3002</p><p>\u3000\u3000\u4e24\u4e09\u9875\u8349\u7a3f\u7eb8\uff0c\u5199\u6ee1\u5b57\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u8bfb\u8fc7\u53bb\uff0c\u4e5f\u53ea\u9700\u8981\u4e09\u4e94\u5206\u949f\u800c\u5df2\uff0c\u90b5\u81f3\u521a\u8fd9\u4e00\u770b\u5374\u662f\u534a\u4e2a\u5c0f\u65f6\u624d\u5c06\u8349\u7a3f\u7eb8\u8fd8\u7ed9\u5f20\u606a\uff0c\u95ee\u9053\uff1a\u201c\u8981\u62bd\u8c03\u4e09\u4ebf\u8d44\u91d1\u51fa\u6765\uff0c\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u538b\u529b\uff1f\u201d</p><p>\u3000\u3000\u9526\u6e56\u4ec5\u7231\u8fbe\u96c6\u56e2\u4eca\u5e74\u7684\u5229\u6da6\u5c31\u63a5\u8fd1\u56db\u5341\u4ebf\uff0c\u4f46\u4e0d\u662f\u610f\u5473\u7740\u5f20\u606a\u53ef\u4ee5\u80e1\u4e71\u7684\u6325\u970d\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u9526\u6e56\u5df2\u7ecf\u76f8\u5f53\u7cbe\u7ec6\u7684\u5728\u82b1\u6bcf\u4e00\u5206\u94b1\u3002\u5bf9\u6b63\u5904\u4e8e\u6781\u901f\u53d1\u5c55\u4e2d\u7684\u9526\u6e56\u6765\u8bf4\uff0c\u53ea\u6101\u8d44\u91d1\u4e0d\u591f\u7528\uff0c\u4ece\u6765\u90fd\u6ca1\u6709\u89c9\u5f97\u94b1\u591f\u7528\u4e86\u2014\u2014\u4e5f\u5c31\u53bb\u5e74\u4e94\u6708\u4efd\u4ece\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u5377\u5230\u56db\u5341\u4ebf\uff0c\u653e\u5728\u624b\u91cc\u51ff\u5b9e\u82b1\u4e86\u4e00\u5e74\u591a\u624d\u82b1\u5e72\u51c0\uff0c\u4e0d\u8981\u8bf4\u4e5d\u516b\u5e74\u4e86\uff0c\u4e5d\u4e5d\u5e74\u7684\u5229\u6da6\u90fd\u7ed9\u9884\u7b97\u5206\u51c0\u4e86\uff0c\u8981\u989d\u5916\u62bd\u51fa\u4e09\u4e2a\u4ebf\u6765\uff0c\u5c31\u8981\u5bf9\u65e2\u5b9a\u7684\u9879\u76ee\u6240\u6709\u5f71\u54cd\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u8003\u8651\u518d\u5411\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u3001\u9648\u6c0f\u4e07\u4e1a\u96c6\u56e2\u7b49\u534e\u5546\u518d\u8f6c\u8ba9\u4e9b\u540d\u4e0b\u7684\u80a1\u7968\uff0c\u4e09\u4e94\u4ebf\u8d44\u91d1\u5f88\u8f7b\u677e\u5c31\u80fd\u7b79\u5230\u2026\u2026\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u70b9\u70b9\u5934\uff0c\u4e4b\u524d\u5f20\u606a\u4e0e\u8c22\u665a\u6674\u8054\u5408\u8f6c\u8ba9\u7231\u8fbe\u96c6\u56e26\uff05\u7684\u80a1\u6743\uff0c\u5c31\u83b7\u5f97\u4e00\u4ebf\u4e24\u5343\u4e07\u7f8e\u5143\u7684\u5de8\u8d44\uff0c\u800c\u4e14\u662f\u4e1c\u5357\u4e9a\u534e\u5546\u5bb6\u65cf\u5bf9\u9526\u6e56\u65d7\u4e0b\u7684\u8d44\u4ea7\u4e5f\u6781\u611f\u5174\u8da3\uff0c\u8d44\u91d1\u5bf9\u5f20\u606a\u6765\u8bf4\uff0c\u8fd8\u771f\u4e0d\u6210\u4ec0\u4e48\u95ee\u9898\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u795e\u8272\u8fd8\u7b97\u6b63\u5e38\uff0c\u5012\u662f\u9f9a\u8c6a\u82e5\u6709\u6240\u601d\uff0c\u5f20\u606a\u770b\u5230\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u8981\u662f\u4e0d\u52a0\u89e3\u91ca\uff0c\u5012\u663e\u5f97\u6709\u4e9b\u4e13\u65ad\u72ec\u884c\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u76ca\u4e8b\u4e1a\u4e5f\u4e0d\u662f\u4e0e\u5546\u4e1a\u5229\u76ca\u7edd\u7136\u5206\u5272\u7684\uff0c\u6295\u8d44\u4e09\u4e2a\u4ebf\u5efa\u56fe\u4e66\u9986\uff0c\u5728\u56fd\u5185\u5e94\u8be5\u8981\u7b97\u6781\u5927\u7684\u516c\u76ca\u6587\u5316\u9879\u76ee\uff0c\u5728\u516c\u4f17\u4e0e\u5a92\u4f53\u4e2d\u6240\u5f15\u8d77\u7684\u8f70\u52a8\u6548\u5e94\uff0c\u4f60\u4ee5\u4e3a\u6bd4\u4e09\u661f\u7684\u78d5\u5934\u62dc\u5e74\u79c0\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u7b11\u7740\u611f\u6168\u9053\uff1a\u201c\u4e09\u661f\u7684\u51e0\u4e2a\u9ad8\u5c42\u5728\u5317\u4eac\u78d5\u4e86\u51e0\u4e2a\u5934\uff0c\u5c31\u503c\u4e24\u4e09\u4ebf\uff0c\u8fd8\u771f\u662f\u4fbf\u5b9c\u4ed6\u4eec\u4e86\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u4e2d\u56fd\u4eba\u5bf9\u78d5\u5934\u793c\u6781\u770b\u91cd\uff0c\u97e9\u56fd\u4eba\u5374\u662f\u4e0d\u770b\u91cd\uff0c\u8fd9\u7b14\u8d26\uff0c\u4ed6\u4eec\u7b97\u5f97\u975e\u5e38\u7684\u7cbe\uff0c\u4e0d\u7136\u4e5f\u5f15\u4e0d\u8d77\u8fd9\u4e48\u5927\u7684\u8f70\u52a8\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u5012\u4e5f\u662f\u7684\u3002\u201d\u90b5\u81f3\u521a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u53e6\u5916\uff0c\u5bf9\u5efa\u90ba\u9ad8\u6821\u7fa4\u4f53\u6350\u8d60\u5de8\u8d44\u52a9\u5b66\u52a9\u6559\uff0c\u63d0\u9ad8\u6559\u5b66\u3001\u5b66\u672f\u3001\u79d1\u7814\u6c34\u5e73\uff0c\u4f7f\u6a61\u6811\u56ed\u4e0e\u5404\u9ad8\u6821\u5efa\u7acb\u7c7b\u8054\u5408\u4f53\u6027\u8d28\u7684\u5173\u7cfb\u662f\u7b26\u5408\u9526\u6e56\u957f\u671f\u5229\u76ca\u7684\uff0c\u201d\u5f20\u606a\u53c8\u8bf4\u9053\uff0c\u201c\u5728\u71d5\u5f52\u6e56\u9644\u8fd1\u7684\u9ad8\u6821\u91cc\uff0c\u5efa\u6821\u6700\u65e9\u7684\u4e1c\u5927\u3001\u5e08\u5927\u90fd\u6709\u8fd1\u767e\u5e74\u7684\u5386\u53f2\uff0c\u5176\u4ed6\u9ad8\u6821\u4e5f\u591a\u6709\u56db\u4e94\u516d\u5341\u5e74\u7684\u5386\u53f2\uff0c\u9ad8\u6821\u56fe\u4e66\u9986\u90fd\u9648\u65e7\u800c\u683c\u5c40\u72ed\u5c0f\uff0c\u53c8\u7531\u4e8e\u6821\u56ed\u5efa\u8bbe\u7528\u5730\u504f\u7d27\uff0c\u5373\u4f7f\u7ecf\u8d39\u5bbd\u88d5\u7684\u9ad8\u6821\u60f3\u8981\u5728\u5b66\u6821\u91cc\u65b0\u5efa\u56fe\u4e66\u9986\u90fd\u4e5f\u632a\u4e0d\u51fa\u6574\u5757\u7684\u5730\u65b9\u6765\uff0c\u6211\u4eec\u53ef\u4ee5\u770b\u5230\u65b0\u6821\u533a\u7684\u56fe\u4e66\u9986\u5bcc\u4e3d\u5802\u7687\u5927\u6c14\u5962\u534e\uff0c\u8001\u6821\u533a\u60f3\u5728\u56fe\u4e66\u9986\u91cc\u627e\u4e2a\u770b\u4e66\u7684\u5730\u65b9\u90fd\u96be\uff0c\u4f46\u662f\u8001\u6821\u533a\u59cb\u7ec8\u662f\u6839\u672c\u2014\u2014\u6211\u4eec\u8fd9\u6b21\u5c31\u662f\u8981\u96c6\u4e2d\u8d44\u6e90\u5728\u9ad8\u6821\u7fa4\u7684\u4e2d\u5fc3\u4f4d\u7f6e\uff0c\u5728\u8fd9\u91cc\u5efa\u4e00\u5ea7\u5927\u578b\u7684\u7efc\u5408\u56fe\u4e66\u9986\uff0c\u8fd9\u6837\u5c31\u53ef\u4ee5\u7f13\u89e3\u5404\u9ad8\u6821\u7684\u56fe\u4e66\u9986\u7d27\u7f3a\u538b\u529b\u2014\u2014\u4e1c\u5927\u4e4b\u524d\u60f3\u62ff\u4e0b\u8fd9\u5757\u5730\uff0c\u5c31\u662f\u6253\u7b97\u7528\u6765\u6295\u8d44\u5efa\u65b0\u56fe\u4e66\u9986\u7684\uff0c\u4e00\u65b9\u9762\u662f\u4e1c\u5927\u81ea\u8eab\u7ecf\u8d39\u4e0a\u5b58\u5728\u4e00\u4e9b\u95ee\u9898\uff0c\u4e00\u65b9\u9762\u662f\u4f60\u4eec\u5bf9\u8fd9\u5757\u5730\u8868\u73b0\u51fa\u6781\u5927\u7684\u5174\u8da3\uff0c\u4e1c\u5927\u5c31\u6ca1\u6709\u4e89\u8fd9\u5757\u5730\u2014\u2014\u6211\u4eec\u4e5f\u8981\u4f53\u8c05\u522b\u4eba\u7684\u5fc3\u601d\u3002\u201d</p></article>"}}}]);
