(window.webpackJsonp=window.webpackJsonp||[]).push([[995],{1018:function(p){p.exports={chapterTitle:null,title:"\u7b2c992\u7ae0 \u9e21\u72ac\u5347\u5929",content:"<article><h2>\u7b2c992\u7ae0 \u9e21\u72ac\u5347\u5929</h2><p>\u3000\u3000\u8d76\u5230\u7701\u59d4\u76f4\u5c5e\u673a\u5173\u5927\u9662\u9644\u8fd1\u7684\u4f55\u8bb0\u5927\u9152\u5e97\uff0c\u534e\u706f\u521d\u4e0a\uff0c\u5929\u8fb9\u8fd8\u6d6e\u7740\u4e00\u4e1d\u91d1\u8272\u7684\u6d6e\u4e91\uff0c\u6e5b\u84dd\u8272\u7684\u591c\u7a7a\uff0c\u5728\u5165\u591c\u540e\u5c24\u5176\u7684\u6f84\u6f88\u3002</p><p>\u3000\u3000\u9152\u5e97\u6ca1\u6709\u505c\u8f66\u573a\uff0c\u675c\u98de\u8fd8\u8981\u5148\u627e\u5730\u65b9\u505c\u8f66\uff0c\u5f20\u606a\u4e0e\u9646\u5929\u53c8\u8fd8\u6709\u4e24\u4e2a\u5973\u5b69\u5b50\u5148\u4e0b\u4e86\u8f66\uff0c\u7ad9\u5728\u8def\u7259\u4e0a\u7b49\u675c\u98de\u8fc7\u6765\u3002</p><p>\u3000\u3000\u6240\u8c13\u4f55\u8bb0\u5927\u9152\u5e97\uff0c\u89c4\u6a21\u5176\u5b9e\u4e00\u70b9\u90fd\u4e0d\u5927\uff0c\u7ad9\u5728\u9152\u5e97\u95e8\u5916\uff0c\u900f\u8fc7\u73bb\u7483\u95e8\u770b\u8fdb\u53bb\uff0c\u9152\u5e97\u91cc\u88c5\u4fee\u5e72\u51c0\u7cbe\u81f4\uff0c\u4e0d\u5962\u534e\uff0c\u9009\u5728\u8fd9\u91cc\u7528\u9910\uff0c\u5012\u662f\u7b26\u5408\u9646\u6587\u592b\u7684\u6027\u5b50\u3002</p><p>\u3000\u3000\u201c\u5475\uff0c\u9152\u5e97\u7684\u8001\u677f\u77e5\u9053\u4eca\u5929\u662f\u9646\u526f\u7701\u957f\u5c48\u5c0a\u5728\u8fd9\u91cc\u7528\u9910\uff1f\u201d\u675c\u98de\u8d70\u8fc7\u6765\uff0c\u89c1\u9152\u5e97\u6beb\u4e0d\u8d77\u773c\uff0c\u4e00\u8fb9\u5f80\u91cc\u8d70\u4e00\u8fb9\u5f00\u73a9\u7b11\u7684\u95ee\u9646\u5929\u53c8\u3002</p><p>\u3000\u3000\u201c\u5929\u53c8\uff0c\u5929\u53c8\u2026\u2026\u201d\u6536\u94f6\u53f0\u540e\u9762\u7ad9\u7740\u4e30\u97f5\u72b9\u5b58\u3001\u8001\u677f\u5a18\u6a21\u6837\u7684\u534a\u8001\u5f90\u5a18\uff0c\u770b\u5230\u9646\u5929\u53c8\u4e0e\u5f20\u606a\u4ed6\u4eec\u8d70\u8fc7\u6765\uff0c\u51fa\u58f0\u62db\u547c\u4ed6\uff0c\u773c\u795e\u53c8\u7591\u60d1\u7684\u626b\u8fc7\u5f20\u606a\u4ed6\u4eec\uff0c\u201c\u4ed6\u4eec\u662f\uff1f\u201d</p><p>\u3000\u3000\u201c\u848b\u59e8\uff0c\u4ed6\u4eec\u90fd\u662f\u6211\u5b66\u6821\u91cc\u7684\u540c\u5b66\uff0c\u4e00\u8d77\u8fc7\u6765\u5403\u996d\uff0c\u6211\u7238\u4ed6\u4eba\u6709\u6ca1\u6709\u8fc7\u6765\uff1f\u201d\u9646\u5929\u53c8\u8d70\u8fc7\u53bb\uff0c\u624b\u6491\u5230\u6536\u94f6\u53f0\uff0c\u4e0e\u8001\u677f\u5a18\u8bf4\u8bdd\uff0c\u201c\u6bcf\u56de\u5403\u996d\u90fd\u662f\u4ed6\u6700\u665a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u4ed6\u4eec\u7ad9\u5728\u5927\u5802\u7684\u5165\u53e3\uff0c\u89c1\u9646\u5929\u53c8\u4e0e\u8001\u677f\u5a18\u770b\u4e0a\u53bb\u9887\u4e3a\u719f\u6089\uff0c\u60f3\u5fc5\u9646\u5929\u53c8\u4e00\u5bb6\u7ecf\u5e38\u5728\u8fd9\u5bb6\u4e0d\u5927\u8d77\u773c\u7684\u9910\u5385\u91cc\u5403\u996d\uff0c\u770b\u5230\u8001\u677f\u5a18\u773c\u775b\u5728\u6c88\u7b71\u4e0e\u9648\u5983\u84c9\u8138\u4e0a\u626b\uff0c\u53c8\u4e0e\u9646\u5929\u53c8\u54ac\u8033\u6735\u8bf4\u8bdd\uff0c\u518d\u770b\u9646\u5929\u53c8\u7684\u795e\u8272\uff0c\u5c31\u6653\u5f97\u9646\u5929\u53c8\u5e26\u6c88\u7b71\u8fc7\u6765\u5403\u996d\u662f\u4eba\u6240\u7686\u77e5\u7684\u4e8b\u60c5\uff0c\u4e0e\u9646\u5929\u53c8\u719f\u6089\u7684\u4eba\u90fd\u5728\u597d\u5947\u4ed6\u4f1a\u5e26\u4ec0\u4e48\u6837\u7684\u5973\u5b69\u5b50\u8fc7\u5462\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u6c88\u7b71\u5c40\u4fc3\u4e0d\u5b89\u7684\u7ad9\u5728\u90a3\u91cc\u62ff\u811a\u5c16\u637b\u7740\u5730\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8682\u8681\u8ddf\u4f60\u6709\u4ec7\u554a\uff0c\u4eca\u5929\u5c31\u770b\u89c1\u4f60\u5728\u90a3\u91cc\u637b\u8682\u8681\u4e86\uff1f\u201d</p><p>\u3000\u3000\u6c88\u7b71\u8f7b\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u7a0d\u632f\u4f5c\u4e0b\u795e\u8272\uff0c\u5c06\u7d27\u5f20\u7684\u60c5\u7eea\u63a9\u9970\u6389\u4e9b\u3002</p><p>\u3000\u3000\u9646\u5929\u53c8\u4e0e\u8001\u677f\u5a18\u8bf4\u5b8c\u8bdd\uff0c\u4e0e\u5f20\u606a\u4ed6\u4eec\u76f4\u63a5\u5f80\u91cc\u9762\u7684\u5305\u53a2\u8d70\u53bb\uff0c\u8d70\u5230\u91cc\u9762\u7684\u8fc7\u9053\u91cc\uff0c\u624d\u544a\u8bc9\u6c88\u7b71\u4eca\u5929\u662f\u4ed6\u5988\u5988\u7684\u751f\u65e5\u624d\u5728\u9152\u5e97\u91cc\u5403\u996d\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u6c88\u7b71\u4e4b\u524d\u4ee5\u4e3a\u662f\u5bfb\u5e38\u7684\u5bb6\u5bb4\u5c31\u5df2\u7ecf\u62c5\u5fc3\u5f97\u8981\u547d\uff0c\u8fd9\u4f1a\u513f\u5fc3\u7830\u7830\u7684\u8df3\uff0c\u90fd\u5feb\u5230\u4ece\u5589\u5b50\u773c\u91cc\u8df3\u51fa\u6765\u4e86\uff0c\u5c0f\u58f0\u7684\u62b1\u6028\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u65e9\u8bf4\uff1f\u6211\u4ec0\u4e48\u793c\u7269\u90fd\u6ca1\u6709\u51c6\u5907\uff0c\u4f60\u5988\u5988\u4e00\u5b9a\u4f1a\u8bf4\u6211\u4e0d\u61c2\u4e8b\u7684\u3002\u201d</p><p>\u3000\u3000\u524d\u9762\u9576\u7740\u96d5\u6885\u5de5\u827a\u73bb\u7483\u7684\u5305\u53a2\u95e8\uff0c\u5c31\u91cc\u9762\u85cf\u7740\u4e00\u5934\u6076\u864e\uff0c\u8981\u4e0d\u662f\u5c0f\u624b\u7ed9\u9646\u5929\u53c8\u7275\u7740\uff0c\u770b\u6c88\u7b71\u90a3\u6837\u5b50\uff0c\u5f20\u606a\u90fd\u4e0d\u6000\u7591\u5979\u4f1a\u6492\u817f\u6e9c\u8d70\uff0c\u9152\u5e97\u7684\u9694\u97f3\u5f88\u5dee\u52b2\uff0c\u504f\u504f\u8fd9\u65f6\u5019\u91cc\u9762\u6709\u4eba\u8bae\u8bba\u8d77\u6c88\u7b71\u6765\u3002</p><p>\u3000\u3000\u201c\u5929\u53c8\u8981\u5e26\u4ec0\u4e48\u6837\u7684\u5973\u5b69\u5b50\u8fc7\u6765\uff0c\u201d\u662f\u4e2a\u7565\u5e26\u6c99\u54d1\u7684\u5e74\u8f7b\u5973\u4eba\u7684\u58f0\u97f3\uff0c\u5750\u7684\u79bb\u95e8\u8fd1\uff0c\u55d3\u95e8\u4e5f\u5927\uff0c\u201c\u73b0\u5728\u7684\u5973\u5b69\u5b50\u5fc3\u773c\u591a\u5f97\u5f88\uff0c\u7279\u522b\u662f\u73b0\u5728\u7684\u5927\u5b66\u91cc\uff0c\u597d\u4e9b\u5973\u5b69\u5b50\u4e3a\u4e86\u94b1\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u505a\u7684\u51fa\u6765\uff0c\u5f90\u660e\u4ed6\u4eec\u5c40\u91cc\uff0c\u4e0a\u56de\u5728\u94f6\u6c99\u6253\u9ec4\u626b\u975e\uff0c\u6293\u4e86\u8bb8\u591a\u5c0f\u59d0\uff0c\u91cc\u9762\u5c31\u6709\u597d\u51e0\u4e2a\u662f\u5927\u5b66\u751f\u2014\u2014\u5929\u53c8\u624d\u662f\u4ea4\u7b2c\u4e00\u4e2a\u5973\u670b\u53cb\u5427\uff0c\u600e\u4e48\u53ef\u4ee5\u968f\u4fbf\u5c31\u5e26\u5973\u5b69\u5b50\u56de\u6765\u5462\uff1f\u4ed6\u6709\u6ca1\u6709\u5173\u5fc3\u8fc7\u4eba\u5bb6\u7684\u5bb6\u5ead\u600e\u4e48\u6837\uff0c\u5bb6\u6559\u5f88\u91cd\u8981\u7684\uff0c\u603b\u8981\u627e\u95e8\u5f53\u6237\u5bf9\u7684\u5973\u5b69\u5b50\uff0c\u7279\u522b\u662f\u90a3\u4e9b\u5bb6\u5ead\u6761\u4ef6\u4e0d\u597d\u7684\u5973\u5b69\u5b50\uff0c\u5fc3\u673a\u6df1\u90a3\u5c31\u4e0d\u7528\u8bf4\u4e86\uff0c\u8fd9\u79cd\u5973\u5b69\u5b50\u4e5f\u6700\u7ecf\u4e0d\u4f4f\u8bf1\u60d1\uff0c\u6700\u7231\u6155\u865a\u8363\u2026\u2026\u5988\uff0c\u4f60\u5012\u662f\u8981\u529d\u529d\u5929\u53c8\uff0c\u7b2c\u4e00\u4e2a\u5973\u670b\u53cb\u5f53\u4e0d\u4e86\u771f\u7684\uff0c\u968f\u4fbf\u73a9\u73a9\u5c31\u597d\u4e86\uff0c\u771f\u7b49\u5230\u4ed6\u7ed3\u5a5a\u751f\u5b50\u7684\u65f6\u5019\uff0c\u8fd8\u4e0d\u662f\u6709\u5927\u628a\u8eab\u4e16\u6e05\u767d\u7684\u957f\u76f8\u53c8\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u7b49\u7740\u4ed6\u6311\uff1f\u201d</p><p>\u3000\u3000\u9646\u5929\u53c8\u4e00\u8138\u7684\u5c34\u5c2c\uff0c\u90fd\u4e0d\u6653\u5f97\u8981\u600e\u4e48\u89e3\u91ca\u624d\u597d\uff0c\u6c88\u7b71\u542c\u5f97\u8138\u8272\u90fd\u6e10\u6e10\u53d1\u767d\u4e86\uff0c\u675c\u98de\u8d1f\u7740\u770b\u7740\u8fc7\u9053\u91cc\u7684\u5929\u82b1\uff0c\u9648\u5983\u84c9\u90fd\u62bf\u7740\u5634\u4e0d\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u5019\u8981\u63a8\u95e8\u8fdb\u53bb\uff0c\u8fd8\u771f\u662f\u96be\u582a\uff0c\u90fd\u5728\u60f3\u662f\u4e0d\u662f\u9000\u51fa\u53bb\u66f4\u5408\u9002\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u542c\u8bf4\u8bdd\u91cc\u7684\u7528\u8bcd\uff0c\u8fd9\u4e2a\u5e74\u8f7b\u7684\u5973\u4eba\u4f3c\u4e4e\u662f\u9646\u5929\u53c8\u7684\u59d0\u59d0\uff0c\u8fd9\u8bdd\u542c\u8d77\u6765\u96be\u542c\uff0c\u4e5f\u5f88\u52bf\u5229\uff0c\u5374\u662f\u5c06\u771f\u5b9e\u7684\u5b58\u5728\u4eba\u6027\u9762\u524d\u7684\u90a3\u6761\u5de8\u5927\u6c9f\u58d1\u8840\u6dcb\u6dcb\u7684\u6446\u5728\u6c88\u7b71\u7684\u9762\u524d\u3002</p><p>\u3000\u3000\u5f20\u606a\u6de1\u7136\u800c\u7b11\uff0c\u8fd9\u65f6\u5019\u8981\u9000\u56de\u53bb\uff0c\u6c88\u7b71\u4e0b\u56de\u66f4\u6ca1\u6709\u52c7\u6c14\u8ddf\u9646\u5929\u53c8\u7684\u5bb6\u4eba\u89c1\u9762\uff0c\u4e0d\u8981\u5c06\u8fd9\u4e2a\u793e\u4f1a\u60f3\u8c61\u7684\u592a\u7eaf\u6d01\uff0c\u4e16\u4fd7\u7684\u504f\u89c1\u3001\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u9694\u9602\u3001\u5e02\u4fa9\u52bf\u5229\uff0c\u8fd9\u4e9b\u90fd\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u4e1c\u897f\uff0c\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u592a\u754f\u60e7\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u201c\u54d7\u201d\u7684\u4e00\u58f0\u62c9\u5f00\u5305\u53a2\u7684\u63a8\u95e8\uff0c\u58f0\u52bf\u9887\u5927\u3002</p><p>\u3000\u3000\u5305\u53a2\u88c5\u6f62\u7cbe\u81f4\u800c\u5178\u96c5\uff0c\u6446\u4e0b\u56db\u684c\u9152\u5e2d\uff0c\u51b7\u76d8\u90fd\u5df2\u7ecf\u4e0a\u4e86\uff0c\u5ba2\u4eba\u4e5f\u90fd\u5dee\u4e0d\u591a\u5230\u4e86\uff0c\u4e34\u95e8\u7684\u90a3\u5f20\u684c\u524d\u6216\u5750\u6216\u7ad9\u7684\u56f4\u4e86\u4e00\u7fa4\u5973\u7737\uff0c\u7ed9\u5f20\u606a\u58f0\u52bf\u9887\u5927\u7684\u62c9\u95e8\u52a8\u4f5c\u7ed9\u60ca\u52a8\u4e86\uff0c\u9f50\u5237\u5237\u7684\u671b\u8fc7\u6765\uff0c\u4e00\u4e2a\u5999\u9f84\u5973\u90ce\u4e0e\u4e00\u4e2a\u4e2d\u5e74\u5987\u5973\u7ed9\u8fd9\u4e9b\u5973\u4eba\u56f4\u5728\u4e2d\u95f4\uff0c\u9646\u5929\u53c8\u59d0\u5f1f\u4fe9\u7ee7\u627f\u5979\u5988\u5988\u592a\u591a\u7684\u9057\u4f20\u7279\u5f81\uff0c\u5f20\u606a\u5fc3\u60f3\u5999\u9f84\u5973\u90ce\u5e94\u8be5\u662f\u9646\u5929\u53c8\u7684\u59d0\u59d0\uff0c\u4e2d\u5e74\u5987\u5973\u662f\u9646\u5929\u53c8\u7684\u6bcd\u4eb2\u3001\u9646\u6587\u592b\u7684\u59bb\u5b50\u3002</p><p>\u3000\u3000\u5979\u4eec\u89c1\u5f20\u606a\u8138\u751f\uff0c\u90fd\u7591\u60d1\u7684\u76f8\u4e92\u671b\u4e86\u671b\uff0c\u9646\u6587\u592b\u7684\u59bb\u5b50\u4ee5\u4e3a\u662f\u9646\u6587\u592b\u7684\u5ba2\u4eba\uff0c\u5c31\u7ad9\u8d77\u6765\u558a\u5750\u5728\u89d2\u843d\u91cc\u4e0e\u4eba\u804a\u5929\u7684\u9646\u6587\u592b\uff1a\u201c\u8001\u9646\uff0c\u8001\u9646\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u9646\u5929\u4e0e\u795e\u8272\u52c9\u5f3a\u7684\u6c88\u7b71\u4ee5\u53ca\u675c\u98de\u3001\u9648\u5983\u84c9\u8d70\u8fc7\u6765\uff0c\u8ddf\u4ed6\u5988\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u8fd9\u662f\u6c88\u7b71\uff0c\u4ed6\u4eec\u4e09\u4e2a\u662f\u6211\u5927\u5b66\u7684\u540c\u5b66\uff0c\u4e5f\u4e00\u8d77\u8fc7\u6765\u5403\u996d\u3002\u201d</p><p>\u3000\u3000\u4ed6\u59d0\u521d\u65f6\u773c\u775b\u8fd8\u76ef\u5728\u9648\u5983\u84c9\u7684\u8eab\u4e0a\uff0c\u9648\u5983\u84c9\u90a3\u660e\u8273\u7167\u4eba\u7684\u5bb9\u989c\u8ba9\u4eba\u5982\u6c90\u6625\u98ce\uff0c\u5979\u8fd8\u4ee5\u4e3a\u8fd9\u8863\u7740\u54c1\u5473\u5f88\u4e0d\u51e1\u7684\u5973\u5b69\u5b50\u5c31\u662f\u5f1f\u5f1f\u7684\u5973\u670b\u53cb\u5462\uff0c\u5fc3\u60f3\u4e0d\u9519\uff0c\u5374\u4e0d\u6599\u5f1f\u5f1f\u5c06\u85cf\u5728\u8eab\u540e\u90a3\u4e2a\u7a7f\u7740\u5bd2\u9178\u3001\u795e\u8272\u5c40\u4fc3\u7684\u5973\u5b69\u5b50\u7275\u624b\u62c9\u5230\u8eab\u524d\u6765\uff0c\u8138\u8272\u5c31\u6709\u4e9b\u5fae\u53d8\uff0c\u5bf9\u5f20\u606a\u4ed6\u4eec\u7684\u6001\u5ea6\u81ea\u7136\u4e5f\u5c31\u6076\u52a3\u8d77\u6765\uff0c\u4e5f\u4e0d\u63a9\u9970\u7684\u5c31\u8d23\u602a\u8d77\u9646\u5929\u53c8\u6765\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u63d0\u524d\u6253\u4e2a\u7535\u8bdd\u8fc7\u6765\uff0c\u5c31\u51c6\u5907\u4e86\u56db\u684c\u9152\u5e2d\uff0c\u7b97\u597d\u7684\u4eba\u5934\uff0c\u8fd9\u4e0b\u5b50\u591a\u51fa\u4e09\u4e2a\u4eba\u6765\uff0c\u4f60\u8bf4\u600e\u4e48\u529e\u624d\u597d\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u4ece\u65c1\u8fb9\u684c\u4e0a\u8d70\u8fc7\u4e00\u4e2a\u9752\u5e74\uff0c\u4ed6\u4eb2\u6635\u7684\u6276\u7740\u9646\u5929\u53c8\u4ed6\u59d0\u59d0\u7684\u80a9\u8180\uff0c\u5429\u5490\u9646\u5929\u53c8\uff1a\u201c\u603b\u4e0d\u80fd\u4e3a\u4e09\u4e2a\u4eba\u989d\u5916\u518d\u5f00\u4e00\u684c\u9152\u5e2d\uff0c\u4f60\u5feb\u53bb\u5927\u5385\u627e\u848b\u59e8\uff0c\u770b\u5927\u5385\u91cc\u8fd8\u6709\u6ca1\u6709\u56db\u4eba\u7684\u5ea7\u4f4d\u7559\u4e0b\u6765\uff0c\u8ba9\u4f60\u540c\u5b66\u90fd\u5750\u5927\u5385\u91cc\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u9646\u5929\u53c8\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u611f\u89c9\u8138\u9762\u7ed9\u5bb6\u4eba\u5728\u5f20\u606a\u4ed6\u4eec\u4e22\u5c3d\u6389\uff0c\u800c\u4e14\u6c88\u7b71\u5df2\u7ecf\u5728\u6323\u4ed6\u7684\u624b\u8981\u5f80\u5916\u8d70\uff0c\u770b\u7740\u4ed6\u7238\u7238\u8d70\u8fc7\u6765\uff0c\u5fc3\u60f3\u4ed6\u7238\u7238\u5728\u5b66\u6821\u91cc\u89c1\u8fc7\u6c88\u7b71\u7684\uff0c\u6216\u8bb8\u4f1a\u5e2e\u7740\u8bf4\u53e5\u8bdd\u7684\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u8d70\u8fc7\u6765\uff0c\u770b\u7740\u5f20\u606a\uff0c\u6447\u5934\u82e6\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u4eba\u5f97\u9053\u3001\u9e21\u72ac\u5347\u5929\u7684\u6076\u4fd7\u4e4b\u6001\u5728\u606a\u5c11\u9762\u524d\u5448\u73b0\u65e0\u906e\uff0c\u771f\u662f\u60ed\u6127\u554a\uff01\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u58f0\u97f3\u4e5f\u4e0d\u5927\uff0c\u4f46\u8db3\u4ee5\u8ba9\u5305\u53a2\u91cc\u7684\u4eba\u90fd\u542c\u6e05\u695a\uff0c\u8c14\u7136\u7684\u671b\u7740\u95e8\u53e3\uff0c\u90fd\u4e0d\u6e05\u695a\u6b63\u6625\u98ce\u5f97\u610f\u7684\u9646\u6587\u592b\u4e3a\u4f55\u8981\u5728\u4e00\u4e2a\u964c\u751f\u7684\u9752\u5e74\u9762\u524d\u8bf4\u51fa\u5bf9\u4ed6\u4eec\u5982\u6b64\u5c16\u9178\u523b\u8584\u7684\u8bdd\u6765\u3002</p><p>\u3000\u3000\u9646\u5929\u53c8\u4e5f\u4ece\u6765\u90fd\u6ca1\u6709\u60f3\u5230\u8fc7\u4ed6\u7236\u4eb2\u4f1a\u8ba4\u8bc6\u5f20\u606a\uff0c\u800c\u4e14\u8fd8\u4e00\u526f\u5f88\u719f\u6089\u7684\u6837\u5b50\uff0c\u4ed6\u5e73\u65e5\u542c\u5230\u522b\u4eba\u79f0\u5f20\u606a\u201c\u606a\u5c11\u201d\uff0c\u5c31\u89c9\u5f97\u8fd9\u79f0\u8c13\u7ea8\u7ed4\u6c14\u592a\u91cd\u4e86\u4e9b\uff0c\u8fd9\u65f6\u5019\u4ed6\u7238\u7238\u4e5f\u79f0\u5f20\u606a\u201c\u606a\u5c11\u201d\uff0c\u53ea\u6295\u624b\u4e3e\u8db3\u4e4b\u95f4\u4ece\u5bb9\u4e0d\u8feb\uff0c\u5012\u50cf\u662f\u670b\u53cb\u4e4b\u95f4\u968f\u610f\u7684\u79f0\u8c13\uff1b\u6c88\u7b71\u5fc3\u91cc\u7684\u9707\u60ca\u81ea\u7136\u66f4\u4e0d\u7528\u8bf4\uff0c\u6682\u65f6\u90fd\u5fd8\u8981\u4ece\u8fd9\u91cc\u9003\u51fa\u53bb\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u7684\u59bb\u5b50\u521a\u5f53\u4e0a\u7701\u957f\u592b\u4eba\uff0c\u4ece\u5f20\u606a\u4ed6\u4eec\u51fa\u73b0\u5728\u95e8\u53e3\uff0c\u5979\u90fd\u6ca1\u610f\u8bc6\u5230\u8981\u7ad9\u8d77\u6765\uff0c\u8fd9\u65f6\u5019\u7ad9\u8d77\u6765\u8f7b\u58f0\u95ee\u4e86\u4e00\u58f0\u9646\u6587\u592b\uff1a\u201c\u8001\u9646\uff0c\u8fd9\u4f4d\u662f\u4f60\u670b\u53cb\uff1f\u5feb\u8bf7\u4f60\u670b\u53cb\u5750\u5230\u4f60\u4eec\u684c\u4e0a\u53bb\uff0c\u5ba2\u4eba\u90fd\u5230\u9f50\u4e86\uff0c\u53ef\u4ee5\u5f00\u59cb\u4e0a\u83dc\u3002\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u56de\u5934\u626b\u4e86\u5305\u53a2\u91cc\u4e00\u773c\uff0c\u8ddf\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u91cc\u592a\u6324\u4e86\uff0c\u6211\u4e0e\u4f60\u4eec\u5750\u5927\u5385\u91cc\u53bb\u2026\u2026\u201d\u8bf4\u7740\u5c31\u5f80\u5916\u8d70\uff0c\u5305\u53a2\u91cc\u7684\u4eba\u90fd\u50bb\u7ad9\u5728\u90a3\u91cc\uff0c\u4e0d\u77e5\u9053\u8be5\u6709\u4ec0\u4e48\u53cd\u5e94\u624d\u5408\u9002\uff0c\u9646\u6587\u592b\u59bb\u5b50\u8138\u4e0a\u7684\u7b11\u6bd4\u54ed\u8fd8\u96be\u770b\uff0c\u9646\u6587\u592b\u5973\u513f\u7684\u773c\u775b\u90fd\u5df2\u7ecf\u7ea2\u4e86\uff0c\u5979\u4eec\u4e07\u4e07\u90fd\u6ca1\u6709\u60f3\u5230\uff0c\u9646\u6587\u592b\u4f1a\u5728\u8fd9\u9752\u5e74\u9762\u524d\u5bf9\u5979\u4eec\u8fd9\u4e48\u4e0d\u7559\u60c5\u9762\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u60f3\u81ea\u5df1\u8fc7\u6765\u53c8\u4e0d\u662f\u4e3a\u4e89\u53e3\u6c14\u7684\uff0c\u4e0e\u9646\u6587\u592b\u8d70\u51fa\u53bb\u5230\u5927\u5385\u91cc\u627e\u5f20\u5c0f\u684c\u5b50\u7528\u9910\uff0c\u59ff\u6001\u662f\u6446\u8db3\u4e86\uff0c\u4e5f\u4e0d\u7528\u7406\u4f1a\u5305\u53a2\u91cc\u7684\u8fd9\u4e9b\u4eba\uff0c\u8fd9\u987f\u996d\u5403\u8d77\u6765\u81ea\u7136\u662f\u6109\u5feb\uff0c\u4f46\u5bf9\u6c88\u7b71\u6ca1\u6709\u597d\u5904\uff0c\u770b\u7740\u9646\u6587\u592b\u59bb\u5973\u8138\u8272\u5c34\u5c2c\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u4fbf\u558a\u4f4f\u9646\u6587\u592b\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u6324\u4e00\u6324\u5c31\u597d\u4e86\uff0c\u6bcf\u684c\u63d2\u4e00\u628a\u6905\u5b50\uff0c\u4e5f\u4e0d\u591a\u6211\u4eec\u4e09\u4e2a\u4eba\u3002\u201d\u53c8\u4e0e\u9646\u6587\u592b\u4ecb\u7ecd\u675c\u98de\u3001\u9648\u5983\u84c9\u3002</p><p>\u3000\u3000\u201c\u9646\u53d4\u53d4\u597d\uff0c\u6211\u4e0e\u5929\u53c8\u662f\u540c\u73ed\u540c\u5b66\uff0c\u65e9\u5c31\u60f3\u5230\u9646\u53d4\u53d4\u5bb6\u6765\u767b\u95e8\u62dc\u8bbf\u4e86\u3002\u201d\u675c\u98de\u4e56\u5de7\u7684\u5f88\uff0c\u4ed6\u5f53\u7136\u77e5\u9053\u5728\u9646\u6587\u592b\u9762\u524d\uff0c\u4ed6\u8fdc\u6ca1\u6709\u8d44\u683c\u6446\u51fa\u5f20\u606a\u7684\u90a3\u6837\u59ff\u6001\u6765\uff0c\u9648\u5983\u84c9\u795e\u8272\u6e05\u6de1\u7684\u5524\u4e86\u4e00\u58f0\uff0c\u633d\u7740\u6c88\u7b71\u7684\u80f3\u818a\uff0c\u4e0e\u5979\u7ad9\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u4ecb\u7ecd\u6c88\u7b71\uff1a\u201c\u8bb8\u8001\u5e08\u6bcf\u56de\u5e26\u7ed9\u9646\u526f\u7701\u957f\u4f60\u7684\u5c71\u8336\uff0c\u90fd\u662f\u4ece\u6c88\u7b71\u7236\u4eb2\u90a3\u91cc\u62ff\u8fc7\u6765\u7684\uff0c\u9646\u526f\u7701\u957f\u4ee5\u540e\u8fd8\u60f3\u559d\u90a3\u5c71\u8336\u53f6\uff0c\u5012\u53ef\u4ee5\u76f4\u63a5\u53eb\u6c88\u7b71\u5e26\u4e9b\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u6c88\u7ea6\u7684\u5973\u513f\uff1f\u201d\u9646\u6587\u592b\u9887\u4e3a\u8be7\u5f02\u7684\u770b\u7740\u6c88\u7b71\uff0c\u53c8\u89c9\u5f97\u8fd9\u4e48\u95ee\u592a\u7a81\u7136\u4e86\uff0c\u7b11\u7740\u89e3\u91ca\u9053\uff0c\u201c\u4e0a\u56de\u6211\u966a\u674e\u7701\u957f\u53bb\u6d2a\u81f4\u6797\u573a\u770b\u690d\u6797\u60c5\u51b5\uff0c\u8fd8\u5728\u4f60\u7238\u7238\u7684\u68cb\u9986\u91cc\u6b47\u8fc7\u811a\uff0c\u53ef\u60dc\u65f6\u95f4\u4ed3\u4fc3\uff0c\u6211\u8ddf\u4f60\u7238\u7238\u53ea\u4e0b\u4e86\u534a\u5c40\u68cb\u2026\u2026\u4e0a\u6b21\u5e26\u56de\u6765\u7684\u5c71\u8336\u53f6\u5df2\u7ecf\u559d\u4e86\u5dee\u4e0d\u591a\uff0c\u8fd8\u771f\u8981\u8bf7\u4f60\u5e2e\u6211\u518d\u634e\u51e0\u4e24\u8fc7\u6765\uff0c\u4e0b\u56de\u8ddf\u5929\u53c8\u5230\u5bb6\u91cc\u6765\u73a9\uff0c\u5c31\u8bf7\u5e26\u8fc7\u6765\u5427\uff0c\u6211\u5c31\u6307\u671b\u4f60\u4e86\uff0c\u4ece\u8bb8\u9e3f\u4f2f\u90a3\u91cc\u8981\u8fd9\u8336\u53f6\uff0c\u6bd4\u5272\u4ed6\u7684\u8089\u8fd8\u75db\u82e6\u3002\u201d</p><p>\u3000\u3000\u201c\u55ef\uff01\u201d\u6c88\u7b71\u773c\u5708\u6709\u4e9b\u5fae\u7ea2\uff0c\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u8bdd\u624d\u597d\uff0c\u53ea\u662f\u91cd\u91cd\u7684\u70b9\u4e86\u70b9\u5934\u3002</p></article>"}}}]);
