(window.webpackJsonp=window.webpackJsonp||[]).push([[996],{1019:function(p){p.exports={chapterTitle:null,title:"\u7b2c993\u7ae0 \u6293\u6355\u884c\u52a8",content:"<article><h2>\u7b2c993\u7ae0 \u6293\u6355\u884c\u52a8</h2><p>\u3000\u3000\u9646\u6587\u592b\u9886\u7740\u5f20\u606a\u5f80\u5305\u53a2\u91cc\u4fa7\u8d70\uff0c\u4e24\u8fb9\u7684\u4eba\u641e\u4e0d\u6e05\u5f20\u606a\u4ed6\u4eec\u7684\u8eab\u4efd\uff0c\u8bf4\u662f\u5929\u53c8\u7684\u540c\u5b66\uff0c\u4f46\u662f\u9646\u6587\u592b\u53c8\u662f\u8fd9\u6837\u7684\u6001\u5ea6\uff0c\u4ed6\u4eec\u4e5f\u90fd\u7eb7\u7eb7\u7684\u7ad9\u8d77\u6765\uff0c\u966a\u7740\u7b11\u8138\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u8981\u5e2e\u6c88\u7b71\u89e3\u91ca\u4e3a\u4ec0\u4e48\u6ca1\u6709\u5e26\u793c\u7269\uff0c\u8fb9\u8d70\u8fb9\u7b11\u7740\u8ddf\u9646\u6587\u592b\u8bf4\uff1a\u201c\u5929\u53c8\u8ddf\u6211\u4eec\u6253\u4e86\u9a6c\u864e\u773c\uff0c\u6211\u8fd8\u4ee5\u4e3a\u8fd9\u987f\u9152\u662f\u5e86\u795d\u4f60\u9ad8\u5347\uff0c\u60f3\u7740\u5e26\u793c\u7269\u592a\u4e0d\u5408\u9002\uff0c\u5c31\u4e24\u624b\u7a7a\u7a7a\u800c\u6765\uff1b\u5230\u8fd9\u91cc\u540e\uff0c\u5929\u53c8\u624d\u8bf4\u662f\u9646\u963f\u59e8\u751f\u65e5\uff0c\u60f3\u7740\u518d\u53bb\u4e70\u793c\u7269\uff0c\u53c8\u8981\u803d\u6401\u5927\u5bb6\u5403\u996d\u4e86\u2014\u2014\u771f\u53eb\u4eba\u5de6\u53f3\u4e3a\u96be\uff0c\u201d\u5f20\u606a\u505c\u4e0b\u811a\u6b65\uff0c\u8ddf\u9646\u6587\u592b\u7684\u59bb\u5b50\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u7a7a\u624b\u800c\u6765\uff0c\u9646\u963f\u59e8\u4e0d\u8981\u8d23\u602a\u554a\u3002\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u7684\u59bb\u5b50\u5728\u4e00\u65c1\u966a\u7b11\u7740\u8bf4\uff1a\u201c\u90fd\u662f\u5929\u53c8\u7684\u540c\u5b66\uff0c\u80fd\u8fc7\u6765\u5403\u996d\u5c31\u597d\uff0c\u963f\u59e8\u600e\u4e48\u4f1a\u6536\u4f60\u4eec\u7684\u793c\u7269\uff1f\u4eca\u5929\u8001\u9646\u65e9\u5c31\u5b9a\u597d\u7684\u89c4\u77e9\uff0c\u5c31\u7b97\u6709\u4eba\u63d0\u7740\u793c\u7269\u3001\u793c\u91d1\u8fc7\u6765\uff0c\u5403\u8fc7\u996d\u90fd\u8981\u63d0\u56de\u53bb\u3002\u201d</p><p>\u3000\u3000\u9646\u5929\u53c8\u4ed6\u59d0\u59d0\u9646\u4e00\u5a77\u3001\u59d0\u592b\u5f90\u660e\u8fd8\u6709\u4e9b\u5c34\u5c2c\uff0c\u6ca1\u8138\u51d1\u8fc7\u6765\uff0c\u7ad9\u5728\u540e\u9762\u8fdc\u8fdc\u7684\u770b\u7740\uff0c\u5c0f\u58f0\u7684\u5600\u5495\uff1a\u201c\u8fd9\u4eba\u8c01\u554a\uff0c\u8be5\u4e0d\u4f1a\u662f\u603b\u4e66\u8bb0\u7684\u513f\u5b50\uff1f\u201d\u8c01\u6653\u5f97\u5462\uff0c\u4ed6\u4eec\u8fd9\u65f6\u5019\u6ca1\u6709\u80c6\u6c14\u53bb\u95ee\u90a3\u9752\u5e74\u7684\u8eab\u4efd\uff0c\u8bc6\u673a\u53bb\u558a\u670d\u52a1\u5458\u518d\u6dfb\u4e09\u628a\u6905\u5b50\uff0c\u5ea7\u4f4d\u65e9\u5c31\u5b89\u6392\u597d\u4e86\uff0c\u4f46\u662f\u770b\u7740\u7236\u4eb2\u5c06\u5929\u53c8\u8ddf\u4ed6\u7684\u540c\u5b66\u90fd\u9886\u5230\u4e3b\u684c\u4e0a\u53bb\uff0c\u90a3\u5c31\u8981\u5b89\u6392\u539f\u5148\u4e3b\u684c\u4e0a\u7684\u4e94\u4e2a\u4eba\u63d2\u5230\u5176\u4ed6\u684c\u4e0a\u53bb\uff0c\u597d\u5728\u4eca\u5929\u5230\u573a\u7684\u5ba2\u4eba\u6ca1\u6709\u8c01\u8ddf\u9646\u5bb6\u8ba1\u8f83\u5750\u5e2d\u7684\u95ee\u9898\uff0c\u597d\u4e9b\u4eba\u90fd\u4e3b\u52a8\u8ba9\u51fa\u5ea7\u4f4d\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u5ba2\u6c14\u4e0e\u675c\u98de\u5750\u4e0b\u6765\uff0c\u53c8\u4fa7\u8fc7\u5934\u770b\u7740\u9648\u5983\u84c9\u7b11\u7740\u6324\u4e86\u4e00\u4e0b\u773c\u775b\uff0c\u9648\u5983\u84c9\u5fc3\u9886\u795e\u4f1a\uff0c\u6400\u7740\u6c88\u7b71\u7684\u80f3\u818a\uff0c\u8bf4\u9053\uff1a\u201c\u5750\u8fd9\u684c\u90fd\u8981\u559d\u9152\u7684\uff0c\u6211\u4eec\u8ddf\u9646\u963f\u59e8\u5750\u4e00\u684c\u5427\u3002\u201d\u5979\u8fd9\u4e00\u8bf4\uff0c\u8fd1\u95e8\u7684\u90a3\u684c\u7acb\u9a6c\u6709\u4e24\u4eba\u7ad9\u8d77\u5c06\u6905\u5b50\u8ba9\u7ed9\u5979\u4eec\u3002</p><p>\u3000\u3000\u6ca1\u6709\u60f3\u5230\u9646\u5929\u53c8\u7684\u7238\u7238\u7adf\u7136\u8ddf\u7238\u7238\u8ba4\u8bc6\uff0c\u8a00\u8bed\u95f4\u8fd8\u5728\u7ed9\u5979\u6491\u8170\uff0c\u6c88\u7b71\u5fc3\u91cc\u5012\u6ca1\u6709\u591a\u754f\u60e7\u4e86\uff0c\u518d\u8bf4\u9646\u6587\u592b\u59bb\u5b50\u521a\u624d\u8138\u8272\u6de1\u6f20\uff0c\u8ddf\u5916\u9762\u7684\u51b7\u98ce\u4f3c\u7684\uff0c\u8fd9\u65f6\u5019\u5374\u548c\u989c\u60a6\u8272\uff0c\u8138\u4e0a\u5806\u7740\u6148\u5584\u7684\u7b11\uff0c\u4fbf\u968f\u9648\u5983\u84c9\u4e0e\u9646\u5929\u53c8\u4ed6\u5988\u3001\u4ed6\u59d0\u5750\u4e00\u684c\u53bb\u3002</p><p>\u3000\u3000\u9646\u5929\u53c8\u65e2\u62c5\u5fc3\u6c88\u7b71\u4f1a\u4e0d\u4f1a\u7ed9\u4ed6\u59d0\u6b3a\u8d1f\uff0c\u65f6\u4e0d\u65f6\u56de\u5934\u770b\u4e00\u773c\uff0c\u5fc3\u91cc\u4e5f\u7591\u60d1\u7238\u7238\u600e\u4e48\u8ba4\u8bc6\u5f20\u606a\u7684\uff0c\u4e3a\u4ec0\u4e48\u4e4b\u524d\u538b\u6839\u513f\u5c31\u6ca1\u6709\u8ddf\u81ea\u5df1\u8bf4\u8d77\uff1f\u6709\u65f6\u5019\u4e8b\u60c5\u6ca1\u6709\u4eba\u5e2e\u4ed6\u6345\u7834\u5929\u7a97\uff0c\u8eab\u9677\u5c40\u4e2d\u662f\u5f88\u96be\u770b\u51fa\u7aef\u502a\u7684\uff0c\u53bb\u5e74\u7684\u4f5c\u5f0a\u4e8b\u4ef6\u5f97\u4ee5\u987a\u5229\u7684\u89e3\u51b3\uff0c\u9646\u5929\u53c8\u90fd\u4e00\u76f4\u4ee5\u4e3a\u662f\u4ed6\u4e3b\u52a8\u7ad9\u51fa\u6765\u201c\u81ea\u9996\u201d\u53d1\u6325\u4e86\u4f5c\u7528\u3002</p><p>\u3000\u3000\u9152\u5e97\u8001\u677f\u5a18\u848b\u59e8\u8d70\u8fc7\u6765\uff0c\u6307\u6325\u7740\u670d\u52a1\u5458\u6dfb\u52a0\u6905\u5b50\uff0c\u770b\u5230\u9646\u6587\u592b\u59bb\u5973\u5bf9\u5929\u53c8\u5e26\u8fc7\u6765\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\u548c\u989c\u60a6\u8272\u3001\u70ed\u60c5\u6709\u52a0\uff0c\u8fd8\u9887\u4e3a\u8be7\u5f02\u3002\u5f20\u606a\u5c06\u5979\u8be7\u5f02\u7684\u8868\u60c5\u770b\u5728\u773c\u91cc\uff0c\u5fc3\u91cc\u5374\u662f\u5fae\u5fae\u4e00\u53f9\uff0c\u4e5f\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\uff0c\u9646\u6587\u592b\u662f\u503c\u5f97\u4fe1\u4efb\u7684\uff0c\u957f\u65f6\u671f\u5c0f\u5fc3\u8c28\u614e\u7684\u505a\u653f\u5e9c\u7ba1\u5bb6\u7684\u5de5\u4f5c\uff0c\u751f\u6027\u5374\u8c41\u8fbe\u800c\u7387\u771f\uff0c\u4f46\u662f\u4e5f\u4e0d\u80fd\u6307\u671b\u9646\u6587\u592b\u7684\u5bb6\u4eba\u90fd\u6709\u5f88\u9ad8\u7684\u89c9\u609f\uff0c\u4e16\u4fd7\u5f80\u5f80\u8981\u5728\u4eba\u8eab\u4e0a\u6253\u4e0a\u9636\u5c42\u7684\u70d9\u5370\uff0c\u4e5f\u6216\u8bb8\u662f\u9646\u6587\u592b\u521d\u767b\u9ad8\u4f4d\uff0c\u4ed6\u7684\u5bb6\u4eba\u8fd8\u4e0d\u80fd\u5f88\u597d\u7684\u9002\u5e94\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u52c7\u5750\u51fa\u79df\u8f66\u8d76\u5230\u5317\u9601\u5927\u9152\u5e97\uff0c\u62ff\u51fa\u624b\u673a\u7ed9\u80e1\u91d1\u661f\u62e8\u4e86\u7535\u8bdd\uff0c\u627e\u5230\u5305\u53a2\uff0c\u770b\u5230\u738b\u5efa\u6797\u4e5f\u5df2\u7ecf\u8d76\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u4ed6\u5976\u5976\u7684\uff0c\u9648\u5983\u84c9\u90a3\u5c0f\u9a9a\u8e44\u5b50\u7709\u8df3\u773c\u98de\u90fd\u6709\u4e9b\u610f\u52a8\u4e86\uff0c\u7a81\u7136\u6709\u8f86\u8b66\u8f66\u5f00\u8fc7\u6765\uff0c\u8bf4\u5200\u5b50\u4e0e\u9752\u59d0\u5728\u5b66\u5e9c\u5df7\u975e\u6cd5\u62db\u5de5\uff0c\u5c06\u4ed6\u4eec\u5e26\u8d70\u4e86\uff0c\u771f\u4ed6\u5988\u7684\u53ef\u60dc\u4e86\uff0c\u201d\u9648\u52c7\u5927\u51bd\u51bd\u7684\u5750\u4e0b\u6765\uff0c\u62ff\u8d77\u684c\u4e0a\u4e00\u74f6\u5f00\u4e86\u76d6\u7684\u5564\u9152\uff0c\u5012\u8fdb\u9152\u676f\u91cc\uff0c\u4e00\u6c14\u559d\u6389\u534a\u74f6\uff0c\u8bf4\u9053\uff0c\u201c\u770b\u6765\u4e0b\u56de\u8981\u6362\u4e2a\u65b9\u5f0f\u4e86\u2026\u2026\u201d\u770b\u7740\u5305\u53a2\u91cc\u4e24\u5f20\u684c\u4e0a\u90fd\u6446\u4e0a\u51b7\u76d8\uff0c\u5899\u89d2\u91cc\u5806\u4e86\u597d\u51e0\u7bb1\u9152\uff0c\u95ee\u9053\uff0c\u201c\u665a\u4e0a\u662f\u4e0d\u662f\u8fd8\u6709\u8c01\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5728\u5357\u90ba\u6709\u4e9b\u670b\u53cb\u60f3\u8ddf\u4f60\u4eec\u8ba4\u8bc6\u4e00\u4e0b\uff0c\u4ee5\u540e\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u627e\u4ed6\u4eec\u4e00\u6837\u80fd\u5e2e\u4f60\u4eec\u89e3\u51b3\u6389\u3002\u201d\u738b\u5efa\u6797\u8bf4\u9053\uff0c\u4ed6\u8fd9\u65f6\u5019\u4e5f\u4e0d\u53bb\u7ba1\u7ed9\u8b66\u5bdf\u6263\u7559\u7684\u4e24\u4e2a\u624b\u4e0b\uff0c\u5728\u5b66\u5e9c\u5df7\u62c9\u4eba\u5230\u591c\u5e97\u91cc\u505a\u670d\u52a1\u5458\uff0c\u867d\u8bf4\u6709\u4e9b\u4e0d\u6b63\u89c4\uff0c\u4f46\u662f\u79bb\u72af\u6cd5\u8fd8\u8fdc\uff0c\u8fc7\u51e0\u4e2a\u5c0f\u65f6\u4eba\u5c31\u4f1a\u7ed9\u653e\u51fa\u6765\uff0c\u738b\u5efa\u6797\u90fd\u8d56\u5f97\u5728\u59d0\u592b\u6ca1\u6709\u56de\u56fd\u4e4b\u524d\u53e6\u6258\u5173\u7cfb\u3002</p><p>\u3000\u3000\u9648\u52c7\u6487\u6487\u5634\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u8fd9\u4e9b\u5e74\uff0c\u56f4\u7740\u4ed6\u51fa\u73b0\u7684\u5404\u8272\u4eba\u7269\u4e5f\u4e0d\u5c11\uff0c\u8fd9\u79cd\u4e8b\u60c5\u4e5f\u53f8\u7a7a\u89c1\u60ef\u4e86\uff0c\u9887\u6709\u4e0d\u5c51\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4ecb\u7ecd\u7684\u90a3\u4e9b\u670b\u53cb\u80fd\u5e2e\u80e1\u91d1\u661f\u5c06\u90a3\u5c0f\u9a9a\u8e44\u89e3\u51b3\u6389\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c0f\u80e1\u5230\u5e95\u662f\u5ff5\u8fc7\u4e66\u7684\uff0c\u51e1\u4e8b\u8981\u8bb2\u4ec0\u4e48\u60c5\u8c03\uff0c\u6362\u4f5c\u662f\u6211\uff0c\u627e\u4e2a\u558a\u7834\u5589\u5499\u90fd\u6ca1\u6709\u4eba\u7b54\u5e94\u7684\u5730\u65b9\u5c06\u4e8b\u60c5\u7ed9\u529e\u4e86\uff0c\u201d\u738b\u5efa\u6797\u8bf4\u9053\uff0c\u201c\u5feb\u5200\u65a9\u4e71\u9ebb\uff0c\u4e4b\u540e\u7684\u4e8b\u60c5\u53cd\u800c\u597d\u89e3\u51b3\uff0c\u8981\u4e0d\u8981\u6211\u5c06\u9053\u4e0a\u7684\u7ecf\u9a8c\u7ed9\u4f60\u4eec\u4fe9\u4ecb\u7ecd\u4ecb\u7ecd\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u8fd9\u4e8b\u8fd8\u662f\u4e0b\u4e9b\u6c34\u78e8\u5de5\u592b\u597d\u3002\u201d\u80e1\u91d1\u661f\u6447\u4e86\u6447\u5934\uff0c\u4ed6\u4e5f\u542c\u8bf4\u5973\u5b69\u5b50\u7ed9\u5f3a\u66b4\u540e\uff0c\u5973\u5b69\u5b50\u4ee5\u53ca\u5bb6\u957f\u8003\u8651\u5230\u540d\u58f0\u3001\u5f71\u54cd\uff0c\u6781\u5c11\u62a5\u8b66\u7684\uff0c\u624b\u811a\u6536\u62fe\u5f97\u5e72\u51c0\u4e00\u4e9b\uff0c\u5c31\u7b97\u62a5\u8b66\uff0c\u4e5f\u5bb9\u6613\u64cd\u4f5c\uff0c\u4f46\u662f\u4e0d\u62a5\u8b66\u4e0d\u610f\u5473\u7740\u4e8b\u540e\u4e0d\u4f1a\u906d\u6765\u62a5\u590d\uff0c\u6ca1\u6743\u6ca1\u52bf\u7684\u4eba\u5bb6\u4e5f\u5c31\u7b97\u4e86\uff0c\u80e1\u91d1\u661f\u53ef\u4e0d\u4ee5\u4e3a\u81ea\u5df1\u771f\u80fd\u60f9\u5f97\u8d77\u5f20\u606a\uff0c\u5bf9\u9648\u5983\u84c9\u53ea\u80fd\u4e0b\u6c34\u78e8\u5de5\u592b\u3002\u8981\u9648\u5983\u84c9\u5fc3\u7518\u60c5\u613f\u7684\u8ddf\u7740\u81ea\u5df1\uff0c\u5230\u65f6\u5019\u5f20\u606a\u90a3\u5c0f\u5b50\u81ea\u7136\u4e5f\u65e0\u8bdd\u53ef\u8bf4\uff0c\u4e0d\u8fc7\u60f3\u6765\u4e5f\u6068\uff0c\u80e1\u91d1\u661f\u77e5\u9053\u56f4\u7ed5\u5728\u5f20\u606a\u8eab\u8fb9\u7684\u4e0d\u6b62\u4e00\u4e2a\u9648\u5983\u84c9\uff0c\u97f3\u4e50\u5b66\u9662\u7684\u90a3\u4e2a\u599e\u3001\u5e08\u5927\u6570\u5b57\u7cfb\u7684\u90a3\u4e2a\u7978\u6c34\u3001\u5e08\u5927\u7559\u5b66\u751f\u9662\u7684\u90a3\u4e2a\u97e9\u56fd\u599e\u7b49\uff0c\u65e0\u4e00\u4e0d\u662f\u9876\u5c16\u5c16\u7684\u6f02\u4eae\uff0c\u7279\u522b\u662f\u90a3\u4e2a\u97e9\u56fd\u599e\u66f4\u8ba9\u4eba\u773c\u998b\uff0c\u90a3\u5bf9\u5927\u80f8\u8981\u662f\u7528\u6765\u6253\u5976\u70ae\u5c31\u723d\u6b6a\u6389\u4e86\u2014\u2014\u597d\u50cf\u5929\u4e0b\u7684\u6f02\u4eae\u5973\u5b69\u5b50\u90fd\u662f\u4e3a\u90a3\u5c0f\u5b50\u624d\u805a\u5230\u5efa\u90ba\u4f3c\u7684\uff0c\u771f\u53eb\u4eba\u5fc3\u91cc\u6068\u5f97\u614c\u3002</p><p>\u3000\u3000\u4e5f\u4e0d\u662f\u8bf4\u5728\u4e1c\u5927\u3001\u5728\u5e08\u5927\u5c31\u73a9\u4e0d\u5230\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u53ef\u4ee5\u7f20\u5728\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u6bd4\u8d77\u9648\u5983\u84c9\u6765\uff0c\u80e1\u91d1\u661f\u603b\u89c9\u5f97\u7f3a\u4e9b\u4ec0\u4e48\uff0c\u6216\u8bb8\u662f\u5f97\u4e0d\u5230\u624d\u4f1a\u89c9\u5f97\u6700\u597d\u5427\u3002</p><p>\u3000\u3000\u738b\u5efa\u6797\u867d\u7136\u80c6\u5927\uff0c\u4f46\u4e5f\u4e0d\u662f\u6ca1\u6709\u5934\u8111\uff0c\u81f3\u5c11\u8fd8\u4e0d\u6562\u5c06\u7978\u7aef\u4f38\u5230\u4e1c\u6d77\u5927\u5b66\u53bb\uff0c\u53ea\u662f\u80e1\u91d1\u661f\u770b\u4e2d\u7684\u90a3\u4e2a\u599e\u6839\u672c\u4e0d\u7ed9\u4ed6\u4eec\u63a5\u89e6\u7684\u673a\u4f1a\uff0c\u5c31\u7b97\u4ed6\u60f3\u505a\u6c34\u78e8\u5de5\u592b\uff0c\u4e5f\u8981\u6709\u89c1\u7f1d\u63d2\u9488\u7684\u673a\u4f1a\u624d\u884c\uff0c\u6324\u7740\u7709\u5934\u66ff\u80e1\u91d1\u661f\u60f3\u4e3b\u610f\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u684c\u4e0a\u7684\u624b\u673a\u54cd\u4e86\u8d77\u6765\uff0c\u738b\u5efa\u6797\u62ff\u8d77\u624b\u673a\u6765\u770b\u4e86\u770b\uff0c\u8ddf\u80e1\u91d1\u661f\u3001\u9648\u52c7\u8bf4\u9053\uff1a\u201c\u6211\u7684\u670b\u53cb\u8fc7\u6765\u4e86\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u7a0d\u5750\u4e00\u4f1a\u513f\uff0c\u6211\u53bb\u63a5\u4e00\u4e0b\u4ed6\u4eec\uff0c\u4ed6\u4eec\u53ef\u90fd\u662f\u4e9b\u6709\u529e\u6cd5\u7684\uff0c\u8bf4\u4e0d\u5b9a\u4e09\u4e2a\u81ed\u76ae\u5320\u62b5\u4e00\u4e2a\u8bf8\u845b\u4eae\u5e2e\u80e1\u5c11\u4f60\u5c06\u4e8b\u60c5\u529e\u6210\u4e86\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u770b\u7740\u738b\u5efa\u6797\u5c06\u4e00\u7fa4\u9762\u76f8\u51f6\u6076\u7684\u4eba\u63a5\u5230\u9152\u5e97\u91cc\uff0c\u9648\u79c9\u5fb7\u4e0d\u52a8\u58f0\u8272\u7684\u5750\u5728\u8f66\u91cc\u3002\u5929\u9ed1\u524d\u63a5\u5230\u5f20\u606a\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u738b\u5efa\u6797\u56e2\u4f19\u90fd\u5df2\u7ecf\u5c06\u9975\u98df\u90fd\u6492\u5230\u4e1c\u5927\u53bb\u4e86\u3002\u867d\u7136\u6ca1\u6709\u63a5\u5230\u62a5\u6848\uff0c\u4f46\u662f\u4ed6\u5fc3\u91cc\u6e05\u695a\uff0c\u8fd9\u5e76\u4e0d\u610f\u5473\u7740\u5c31\u6ca1\u6709\u53d1\u751f\u8fc7\u5973\u5b66\u751f\u7ed9\u808b\u8feb\u5356\u6deb\u7684\u6076\u6027\u4e8b\u4ef6\uff0c\u624b\u91cc\u638c\u63e1\u7684\u7f6a\u8bc1\uff0c\u5dee\u4e0d\u591a\u4e5f\u591f\u7528\u4e86\uff0c\u5c31\u51b3\u5b9a\u4e0d\u80fd\u62d6\u5ef6\u4e0b\u53bb\uff0c\u518d\u8bf4\u6293\u6355\u65f6\u80af\u5b9a\u8fd8\u80fd\u83b7\u5f97\u66f4\u591a\u7684\u66f4\u786e\u51ff\u7684\u7f6a\u8bc1\u3002</p><p>\u3000\u3000\u4e13\u6848\u7ec4\u5bf9\u738b\u5efa\u6797\u56e2\u4f19\u7684\u9aa8\u5e72\u6210\u5458\u884c\u8e2a\u90fd\u968f\u65f6\u638c\u63e1\u7684\uff0c\u738b\u5efa\u6797\u4e00\u4e0b\u51b3\u5fc3\uff0c\u6293\u6355\u884c\u52a8\u5c31\u79d8\u5bc6\u5c55\u5f00\u6765\uff0c\u4ed6\u4e5f\u6709\u4e9b\u4e0d\u653e\u5fc3\uff0c\u5c31\u4eb2\u81ea\u5230\u6293\u6355\u73b0\u573a\u6765\u770b\u4e00\u773c\u3002\u4ed6\u6ca1\u6709\u5750\u8b66\u8f66\u8fc7\u6765\uff0c\u4e13\u6848\u7ec4\u4ee5\u53ca\u4eca\u5929\u53c2\u4e0e\u884c\u52a8\u7684\u8b66\u5458\u90fd\u85cf\u5728\u5317\u9601\u5927\u9152\u5e97\u4e1c\u9762\u7684\u5df7\u5b50\u91cc\uff0c\u8fd9\u65f6\u5019\u4e13\u6848\u7ec4\u7684\u6210\u5458\u6b63\u5728\u6bd4\u5bf9\u6293\u6355\u540d\u5355\u3002</p><p>\u3000\u3000\u9664\u4e86\u8fd9\u91cc\uff0c\u4e91\u68a6\u9601\u5a31\u4e50\u5bab\u3001\u957f\u6797\u8fd0\u8f93\u516c\u53f8\u4ee5\u53ca\u738b\u5efa\u6797\u4ee5\u540c\u4f19\u591a\u5904\u85cf\u8eab\u5730\u70b9\u90fd\u5e03\u7f6e\u4e86\u8b66\u529b\uff0c\u5728\u8fd9\u91cc\u5b9e\u65bd\u6293\u6355\u7684\u540c\u65f6\uff0c\u51e0\u5904\u540c\u65f6\u5c55\u5f00\u7f6a\u8bc1\u641c\u67e5\u5de5\u4f5c\uff0c\u4e5f\u786e\u4fdd\u56e2\u4f19\u6210\u5458\u4e0d\u4f1a\u6709\u6f0f\u7f51\u4e4b\u9c7c\u3002</p><p>\u3000\u3000\u5211\u4fa6\u603b\u961f\u526f\u961f\u957f\uff0c\u4e5f\u662f\u8fd9\u6b21\u4e13\u9879\u6253\u9ed1\u5c0f\u7ec4\u7684\u7ec4\u957f\u90ed\u5fd7\u56fd\u7a7f\u7740\u4fbf\u8863\u4ece\u5df7\u5b50\u53e3\u8d70\u8fc7\u6765\uff0c\u9694\u7740\u8f66\u7a97\uff0c\u6c47\u62a5\u9053\uff1a\u201c\u80e1\u4e66\u8bb0\u7684\u4f84\u5b50\u4ee5\u53ca\u9648\u5385\u957f\u7684\u513f\u5b50\u90fd\u5728\u5305\u53a2\u91cc\uff0c\u662f\u4e0d\u662f\u7b49\u4ed6\u4eec\u79bb\u5f00\u540e\u624d\u5b9e\u65bd\u6293\u6355\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9648\u79c9\u5fb7\u6478\u7740\u4e0b\u5df4\u6c89\u541f\u7740\uff0c\u8981\u8ba9\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u5148\u79bb\u5f00\uff0c\u90a3\u4e9b\u5bb6\u4f19\u4e5f\u9152\u8db3\u996d\u9971\u5750\u4e0a\u8f66\u4e86\uff0c\u54ea\u91cc\u80fd\u8f7b\u6613\u7684\u4e00\u7f51\u6253\u5c3d\uff0c\u518d\u8bf4\u8981\u80fd\u8ba9\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u7275\u6d89\u8fdb\u6765\uff0c\u4f59\u5e86\u6797\u80af\u5b9a\u662f\u4e50\u610f\u7684\uff0c\u9648\u79c9\u5fb7\u770b\u4e86\u770b\u624b\u8868\uff0c\u8bf4\u9053\uff0c\u201c\u4e8c\u5341\u5206\u949f\u540e\u51c6\u65f6\u5b9e\u65bd\u6293\u6355\u884c\u52a8\uff0c\u6211\u5728\u5c40\u7b49\u5f85\u4f60\u4eec\u884c\u52a8\u6210\u529f\u7684\u6d88\u606f\u2026\u2026\u201d\u6309\u4e0a\u8f66\u7a97\uff0c\u4fbf\u5429\u5490\u53f8\u673a\u5f00\u8f66\u56de\u5e02\u5c40\u3002</p><p>\u3000\u3000\u90ed\u5fd7\u56fd\u89c1\u5c40\u957f\u9648\u79c9\u5fb7\u771f\u5c31\u5750\u8f66\u5148\u8d70\u4e86\uff0c\u5fc3\u60f3\u6697\u9a82\u4e86\u4e00\u58f0\uff1a\u8001\u72d0\u72f8\uff0c\u771f\u8981\u660e\u54f2\u4fdd\u8eab\uff0c\u4e0d\u60f3\u5f97\u7f6a\u4eba\uff0c\u4e3a\u4ec0\u4e48\u53c8\u6709\u52c7\u6c14\u4e0b\u51b3\u5fc3\u5c06\u5357\u90ba\u957f\u6797\u5e2e\u7684\u8fd9\u7fa4\u6e23\u5b50\u90fd\u7aef\u6389\uff1f\u9648\u79c9\u5fb7\u5927\u6982\u4e0d\u4f1a\u627f\u8ba4\u81ea\u5df1\u8ddf\u4ed6\u6c47\u62a5\u8fc7\u8fd9\u4e8b\uff0c\u80e1\u5b97\u5e86\u8981\u607c\u6068\u4ed6\u4f84\u5b50\u7ed9\u7275\u6d89\u8fdb\u6765\uff0c\u9648\u79c9\u5fb7\u4f30\u8ba1\u4f1a\u4e00\u8138\u65e0\u8f9c\u7684\u626e\u53ef\u601c\u76f8\u8ddf\u80e1\u5b97\u5e86\u89e3\u91ca\u8fd9\u4e00\u5207\u90fd\u662f\u610f\u5916\u5427\uff1f</p><p>\u3000\u3000\u5c31\u5f53\u662f\u610f\u5916\u5427\uff01\u90ed\u5efa\u56fd\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u770b\u4e86\u770b\u5de6\u53f3\u7684\u8f66\u8f86\uff0c\u98de\u5feb\u56de\u5230\u5df7\u5b50\u53e3\u91cc\uff0c\u51c6\u5907\u6293\u6355\u884c\u52a8\u524d\u7684\u6700\u540e\u90e8\u7f72\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6b63\u559d\u5f97\u9152\u9163\u8033\u70ed\uff0c\u80e1\u91d1\u661f\u542c\u89c1\u5916\u9762\u7684\u811a\u6b65\u58f0\u5f88\u4e71\uff0c\u4e5f\u6ca1\u6709\u5728\u610f\uff0c\u9152\u52b2\u51b2\u5230\u5934\u91cc\uff0c\u6b63\u5174\u594b\u5f97\u7d27\uff0c\u6b63\u9f13\u52a8\u9648\u52c7\u4e0e\u738b\u5efa\u6797\u5439\u9152\u74f6\uff0c\u53ea\u542c\u89c1\u7830\u7136\u5de8\u54cd\uff0c\u5305\u53a2\u95e8\u7ed9\u4eba\u4ece\u5916\u9762\u731b\u8e39\u5f00\u6765\uff0c\u6123\u4e86\u4e00\u6123\uff0c\u6ca1\u6709\u7b49\u4ed6\u6709\u4ec0\u4e48\u53cd\u5e94\uff0c\u9ed1\u538b\u538b\u7684\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u6b66\u8b66\u5634\u91cc\u5927\u558a\u7740\u201c\u4e0d\u8981\u52a8\u3001\u4e0d\u8981\u52a8\u201d\u51b2\u4e86\u8fdb\u6765\uff0c\u4ed6\u53d1\u6123\u7684\u5750\u5728\u90a3\u91cc\uff0c\u4e0d\u77e5\u9053\u53d1\u751f\u7684\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4e0b\u610f\u8bc6\u7684\u6293\u8d77\u684c\u4e0a\u624b\u673a\u8981\u6253\u7535\u8bdd\u51fa\u53bb\uff0c\u5374\u7ed9\u4e00\u8b66\u68cd\u62bd\u624b\u8155\u4e0a\uff0c\u7d27\u63a5\u7740\u7ed9\u4e00\u540d\u6b66\u8b66\u63ea\u4f4f\u5934\u53d1\u6b7b\u6b7b\u7684\u6309\u4f4f\u5f80\u5916\u62d6\u3002\u5305\u53a2\u91cc\u4fa7\u7684\u4eba\u89c1\u673a\u4e0d\u5bf9\uff0c\u6253\u5f00\u7a97\u5b50\u6b63\u8981\u5f80\u5916\u8df3\uff0c\u624d\u770b\u5230\u7a97\u5916\u6697\u5e7d\u5e7d\u7684\u9634\u5f71\u91cc\u4e5f\u7ad9\u6ee1\u4e86\u6b66\u8b66\u3002</p></article>"}}}]);
