(window.webpackJsonp=window.webpackJsonp||[]).push([[997],{1020:function(p){p.exports={chapterTitle:null,title:"\u7b2c806\u7ae0 \u7a00\u571f\u8d44\u6e90",content:"<article><h2>\u7b2c806\u7ae0 \u7a00\u571f\u8d44\u6e90</h2><p>\u3000\u3000\u56fd\u5185\u7ecf\u6d4e\u53d1\u5c55\u6b63\u5f0f\u8fdb\u5165\u8d75\u6d4e\u4e1c\u65f6\u4ee3\uff0c\u8d75\u6d4e\u4e1c\u6cbb\u4e0b\u7684\u4e2d\u592e\u653f\u5e9c\u4e0e\u524d\u5c4a\u76f8\u6bd4\u6709\u4e00\u4e2a\u9c9c\u660e\u7684\u7279\u70b9\uff0c\u5c31\u662f\u66f4\u503e\u5411\u4e8e\u63a5\u53d7\u4e13\u5bb6\u7684\u610f\u89c1\uff0c\u8fd9\u4e2a\u9c9c\u660e\u7684\u7279\u70b9\u4ece\u8d75\u6d4e\u4e1c\u4e3b\u6301\u56fd\u5185\u7ecf\u6d4e\u5927\u5c40\u3001\u4e5f\u5c31\u6240\u8c13\u7684\u524d\u8d75\u6d4e\u4e1c\u65f6\u671f\u5f00\u59cb\uff0c\u5c31\u5df2\u7ecf\u5d2d\u9732\u5934\u89d2\u4e86\u3002</p><p>\u3000\u3000\u5728\u5f71\u54cd\u4e2d\u592e\u51b3\u7b56\u7684\u5404\u79cd\u52bf\u529b\u4e2d\uff0c\u5b66\u672f\u754c\u7684\u5f71\u54cd\u529b\u65e5\u76ca\u589e\u5f3a\uff0c\u5b66\u672f\u754c\u53d1\u51fa\u7684\u58f0\u97f3\u4e5f\u65e5\u76ca\u54cd\u4eae\u3002\u5730\u65b9\u82e5\u6709\u4ec0\u4e48\u4e0e\u4e2d\u592e\u5229\u76ca\u51b2\u7a81\u4e4b\u5904\uff0c\u4e5f\u5e38\u5e38\u901a\u8fc7\u5b66\u672f\u754c\u53d1\u51fa\u58f0\u97f3\u8bd5\u63a2\u4e2d\u592e\u7684\u6001\u5ea6\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u4e0d\u662f\u611a\u8822\u7684\u4eba\uff0c\u4ed6\u65e9\u5c31\u53d1\u73b0\u5230\u8fd9\u79cd\u9690\u5f62\u7684\u6743\u529b\uff0c\u624d\u7518\u4e4b\u82e5\u9974\u7684\u7559\u5728\u4e1c\u5927\u7684\u6821\u56ed\u91cc\u3002\u5468\u747e\u73ba\u4ed6\u4ece\u5b66\u8005\u5230\u5b98\u5458\uff0c\u518d\u4ece\u5b98\u5458\u5230\u5b66\u8005\uff0c\u65e9\u5c31\u638c\u63e1\u4e86\u5176\u4e2d\u7684\u51b3\u7a8d\uff0c\u8fd9\u4e9b\u5e74\u5728\u56fd\u5185\u7ecf\u6d4e\u5b66\u754c\u4e5f\u6df7\u51fa\u9887\u5927\u7684\u540d\u6c14\uff0c\u53c8\u5229\u7528\u8fd9\u79cd\u540d\u6c14\u5728\u5b98\u573a\u5546\u754c\u5404\u8272\u4eba\u7269\u4e4b\u95f4\u6e38\u5fcd\u6709\u4f59\u2014\u2014\u4ed6\u7684\u8bba\u8c03\u91cc\u6709\u7740\u592a\u591a\u5229\u76ca\u4ee3\u8a00\u7684\u75d5\u8ff9\u3002</p><p>\u3000\u3000\u5305\u62ec\u5468\u747e\u73ba\u5728\u5185\u7684\u56fd\u5546\u9662\u5728\u56fd\u5185\u6700\u6709\u540d\u6c14\u7684\u51e0\u540d\u6559\u6388\uff0c\u7ecf\u5e38\u5229\u7528\u5468\u672b\u7684\u65f6\u95f4\u5728\u5b66\u6821\u4e3a\u56fd\u5546\u9662\u7684\u5b66\u751f\u505a\u5404\u79cd\u5404\u6837\u7684\u7ecf\u6d4e\u3001\u4ea7\u4e1a\u4e13\u9898\u62a5\u544a\uff0c\u6b64\u7c7b\u7684\u4e13\u9898\u62a5\u544a\u9887\u80fd\u5438\u5f15\u5468\u8fb9\u9662\u6821\u8bb8\u591a\u611f\u5174\u8da3\u7684\u5b66\u751f\u4e5f\u8702\u62e5\u8fc7\u6765\u7684\u542c\u8bb2\uff0c\u5e38\u5e38\u7684\u4e09\u767e\u591a\u4eba\u5ea7\u7684\u9636\u68af\u6559\u5ba4\u91cc\u8fde\u8fc7\u9053\u90fd\u4f1a\u7ed9\u6324\u6ee1\u3002</p><p>\u3000\u3000\u5f20\u606a\u53bb\u542c\u8fc7\u51e0\u56de\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u518d\u542c\u8fd9\u4e9b\u4e13\u9898\u62a5\u544a\uff0c\u591a\u5c11\u6709\u4e9b\u9648\u8bcd\u6ee5\u8c03\u7684\u611f\u89c9\uff0c\u6216\u8bb8\u66f4\u5927\u7684\u610f\u4e49\u4e0e\u662f\u524d\u5c18\u5f80\u4e8b\u91cc\u7684\u8bb0\u5fc6\u76f8\u5370\u8bc1\uff0c\u542c\u8fc7\u51e0\u56de\uff0c\u89c9\u5f97\u7d22\u7136\u65e0\u5473\uff0c\u4e5f\u5c31\u6ca1\u6709\u518d\u53bb\u542c\u8fc7\u3002</p><p>\u3000\u3000\u675c\u98de\u8bf4\u8d77\u6765\uff0c\u4e5f\u662f\u56e0\u4e3a\u4eca\u5929\u4e0b\u5348\u7684\u4e13\u9898\u62a5\u544a\u662f\u5468\u747e\u73ba\u4e3b\u8bb2\u3002\u5468\u747e\u73ba\u5728\u53f0\u4e0a\u505a\u62a5\u544a\uff0c\u4ed6\u60f3\u7740\u81ea\u5df1\u8ddf\u5f20\u606a\u5728\u53f0\u4e0b\u6311\u523a\u7684\u611f\u89c9\u4e00\u5b9a\u5f88\u723d\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u8fd8\u662f\u4e00\u4e2a\u9887\u6709\u80fd\u8010\u7684\u4eba\uff0c\u4ed6\u4e8c\u3001\u4e09\u6708\u4efd\u4e3a\u7ed9\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9020\u52bf\uff0c\u5506\u4f7f\u56fd\u5546\u9662\u7684\u5176\u4ed6\u6559\u6388\u4e0e\u4ed6\u4e24\u4e2a\u6708\u56f4\u7ed5\u8f6f\u4ef6\u4ea7\u4e1a\u4e00\u5171\u505a\u4e86\u516b\u573a\u4e13\u9898\u62a5\u544a\uff0c\u6700\u540e\u4e24\u573a\u751a\u81f3\u5438\u5f15\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u4eb2\u81ea\u5230\u4e1c\u5927\u9636\u6bb5\u6559\u5ba4\u6765\u542c\u8bb2\u2014\u2014\u7f57\u541b\u672a\u5fc5\u60f3\u6765\u542c\uff0c\u4e0d\u8fc7\u59ff\u6001\u8fd8\u662f\u8981\u505a\u51fa\u6765\u7684\u3002</p><p>\u3000\u3000\u4e0d\u77e5\u9053\u5468\u747e\u73ba\u4eca\u5929\u846b\u82a6\u91cc\u6253\u7b97\u4e70\u4ec0\u4e48\u836f\uff0c\u5f20\u606a\u672c\u6ca1\u6709\u5fc3\u601d\u53bb\u542c\u5468\u747e\u73ba\u505a\u4ec0\u4e48\u5b66\u672f\u62a5\u544a\uff0c\u542c\u675c\u98de\u8bf4\u4e86\uff0c\u968f\u53e3\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u5173\u4e8e\u4ec0\u4e48\u65b9\u9762\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u6211\u542c\u65f6\u5b66\u658c\u8bf4\u7684\uff0c\u65f6\u5b66\u658c\u4e5f\u662f\u542c\u522b\u4eba\u8bf4\u7684\uff0c\u597d\u50cf\u662f\u4e2a\u5c0f\u89c4\u683c\u7684\u5b66\u672f\u8bba\u575b\u8fd8\u662f\u4ec0\u4e48\u6765\u7740\uff0c\u542c\u8bf4\u6709\u5916\u6821\u7684\u6559\u6388\u53c2\u52a0\uff0c\u4e09\u98df\u5802\u90a3\u91cc\u7684\u6d77\u62a5\u5899\u4e0a\u8d34\u7740\u6d77\u62a5\u5462\uff0c\u6211\u8fd8\u6ca1\u6709\u65f6\u95f4\u53bb\u770b\uff0c\u201d\u675c\u98de\u53ea\u5f53\u5f20\u606a\u5bf9\u4ec0\u4e48\u77ff\u4e1a\u4e0d\u611f\u5174\u8da3\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u8fd8\u542c\u8bf4\u8ddf\u7535\u5b50\u5546\u52a1\u6709\u4e9b\u5173\u7cfb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u8001\u5bb6\u4f19\u5012\u662f\u86ee\u8d76\u65f6\u9ae6\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u7535\u5b50\u5546\u52a1\u5728\u6b27\u7f8e\u624d\u6709\u5174\u8d77\u4e0d\u4e45\uff0c\u56fd\u5185\u5728\u4e92\u8054\u7f51\u90fd\u666e\u901a\u7684\u5f53\u513f\uff0c\u7535\u5b50\u5546\u52a1\u90fd\u624d\u5c06\u79cd\u5b50\u79cd\u5230\u571f\u58e4\u91cc\uff0c\u5468\u747e\u73ba\u8fd9\u65f6\u5019\u5c31\u5f00\u4e13\u9898\u63d0\u5230\u7535\u5b50\u5546\u52a1\uff0c\u8fd8\u771f\u662f\u8d76\u65f6\u9ae6\u554a\u3002\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4e5f\u6ca1\u6709\u8bf4\u53bb\uff0c\u4e5f\u6ca1\u6709\u8bf4\u4e0d\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6e05\u6668\u7a0d\u7761\u5c11\u8bb8\uff0c\u542c\u7740\u5ba2\u5385\u91cc\u6709\u8bf4\u8bdd\u58f0\uff0c\u5f20\u606a\u5c31\u8d77\u5e8a\u6765\uff0c\u8d70\u51fa\u623f\u95f4\uff0c\u770b\u5230\u7fdf\u4e39\u9752\u4e0e\u5b59\u9759\u6aac\u5750\u5728\u9910\u684c\u524d\u7528\u65e9\u9910\uff0c\u60f3\u5230\u6628\u5929\u591c\u91cc\u8bef\u649e\u526f\u8f66\u7325\u4eb5\u7fdf\u4e39\u9752\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u4e0d\u81ea\u7136\uff0c\u624b\u629a\u7740\u680f\u6746\uff0c\u5bf9\u7fdf\u4e39\u9752\u8bf4\u9053\uff1a\u201c\u4e0a\u5348\u5e2e\u6211\u67e5\u4e00\u4e0b\u56fd\u5185\u7a00\u571f\u3001\u6709\u8272\u91d1\u5c5e\u77ff\u4ea7\u5f00\u91c7\u53ca\u5e94\u7528\u7b49\u65b9\u9762\u7684\u76f8\u5173\u8d44\u6599\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u7fdf\u4e39\u9752\u5e94\u4e86\u4e00\u58f0\uff0c\u660e\u6f88\u7684\u7738\u5b50\u62ac\u8d77\u64a9\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u4e5f\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u5582\uff0c\u201d\u5b59\u9759\u6aac\u8f6c\u8fc7\u5934\u6765\u770b\u7740\u5f20\u606a\uff0c\u201c\u51cc\u6668\u8ff7\u8ff7\u7cca\u7cca\u7684\u600e\u4e48\u542c\u89c1\u4e24\u4e2a\u5973\u5b69\u5b50\u5728\u8fc7\u9053\u91cc\u8bf4\u8bdd\uff0c\u8c01\u8ddf\u8c01\u554a\uff1f\u4e0d\u662f\u8bf4\u597d\u4e86\u4e0d\u8bb8\u5e26\u5973\u5b69\u5b50\u8fdb\u6765\u778e\u641e\u7684\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\uff01\u201d\u5f20\u606a\u6307\u7740\u4e86\u5ba2\u623f\uff0c\u201c\u82cf\u4e00\u5a77\u8ddf\u4f55\u5f26\uff0c\u6628\u5929\u5728\u7f51\u5427\u91cc\u71ac\u901a\u5bb5\uff0c\u6211\u56de\u6765\u65f6\u770b\u5979\u4eec\u5b9e\u5728\u9068\u4e0d\u5230\u5929\u4eae\uff0c\u624d\u8d77\u607b\u60af\u4e4b\u5fc3\uff0c\u8981\u4e0d\u4f60\u6765\u5c06\u5979\u4eec\u8d76\u8d70\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e24\u4e2a\u6b7b\u4e2b\u5934\uff0c\u90a3\u5979\u4eec\u80af\u5b9a\u6ca1\u6709\u544a\u8bc9\u4f60\u5979\u4eec\u6628\u5929\u6652\u7684\u88ab\u5b50\u5fd8\u6536\u7ed9\u4eba\u5bb6\u6367\u8d70\u4e86\u3002\u6211\u8ba9\u5979\u4eec\u8fc7\u6765\u7761\u5979\u4eec\u8fd8\u8bf4\u8fd9\u623f\u5b50\u6709\u53ea\u82b1\u5fc3\u5927\u8272\u72fc\uff0c\u5b81\u53ef\u5728\u7f51\u5427\u91cc\u7761\u4e5f\u6bd4\u5728\u8fd9\u623f\u5b50\u7761\u5b89\u5168\uff0c\u5475\u5475\uff0c\u4e34\u4e86\u5012\u8ba9\u90a3\u53ea\u82b1\u5fc3\u5927\u8272\u72fc\u4eb2\u81ea\u9886\u56de\u6765\u4e86\u2026\u2026\u4f60\u5e2e\u6211\u5230\u5979\u4eec\u623f\u95f4\u8e39\u4e24\u811a\uff0c\u8d76\u60c5\u8001\u5a18\u6574\u5929\u4ee5\u8eab\u9972\u864e\u554a\u3002\u201d\u5b59\u9759\u6aac\u6307\u4f7f\u7740\u5f20\u606a\u53bb\u8e39\u82cf\u4e00\u5a77\u3001\u4f55\u5f26\u7684\u623f\u95f4\u95e8\u3002</p><p>\u3000\u3000\u82cf\u4e00\u5a77\u5012\u4e5f\u7f62\u4e86\uff0c\u4e0e\u5510\u5a67\u3001\u9648\u5983\u84c9\u662f\u95fa\u53cb\uff0c\u4f55\u5f26\u8fd9\u59ae\u5b50\u7684\u7ef0\u53f7\u5c31\u662f\u201c\u7978\u6c34\u201d\uff0c\u7fdf\u4e39\u9752\u53c8\u62ac\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u8fd9\u5bb6\u4f19\u6628\u5929\u4ece\u9752\u5c71\u516c\u5bd3\u56de\u53bb\u90fd\u5df2\u7ecf\u51cc\u6668\u4e00\u4e24\u70b9\u4e86\uff0c\u8fd8\u4e0d\u5fd8\u201c\u52fe\u642d\u201d\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u53c8\u5c06\u89c6\u7ebf\u79fb\u5230\u522b\u5904\u53bb\uff0c\u7aef\u8d77\u684c\u4e0a\u7684\u5496\u5561\uff0c\u60a0\u7136\u81ea\u5f97\u7684\u559d\u8d77\u6765\uff0c\u5e78\u4e8f\u6628\u5929\u6ca1\u6709\u7cca\u91cc\u7cca\u6d82\u7684\u5f7b\u5e95\u5931\u8eab\u4e8e\u8fd9\u5bb6\u4f19\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u82cf\u4e00\u5a77\u3001\u4f55\u5f26\u7684\u623f\u95e8\u91cc\u91cd\u91cd\u7684\u6376\u4e86\u4e24\u4e0b\uff0c\u8f6c\u8eab\u8fdb\u4e86\u623f\u95f4\u5f00\u59cb\u4e00\u5929\u7684\u5de5\u4f5c\uff0c\u5904\u7406\u6628\u5929\u672a\u6765\u5f97\u53ca\u5904\u7406\u7684\u5de5\u4f5c\u90ae\u4ef6\uff0c\u5510\u5a67\u3001\u8bb8\u601d\u7684\u90ae\u4ef6\u8981\u9996\u5148\u56de\u590d\uff0c\u4e2d\u5348\u4e4b\u524d\u665a\u6674\u8fd8\u8981\u518d\u56de\u5efa\u90ba\uff0c\u8981\u907f\u514d\u5728\u7fdf\u4e39\u9752\u9762\u524d\u9732\u4e86\u9a6c\u811a\uff0c\u8fd9\u51e0\u5929\u8fd8\u662f\u5c11\u63a5\u89e6\u7684\u597d\uff0c\u60f3\u7740\u7b49\u7fdf\u4e39\u9752\u67e5\u5b8c\u8d44\u6599\u56de\u6765\u4e4b\u540e\u7528\u4ec0\u4e48\u501f\u53e3\u518d\u5c06\u5979\u6253\u53d1\u8d70\u3002</p><p>\u3000\u3000\u56fe\u4e66\u9986\u6570\u5b57\u5316\u5de5\u7a0b\u79bb\u5efa\u6210\u8fd8\u6709\u4e00\u6bb5\u65f6\u95f4\uff0c\u7ed9\u4e86\u4e00\u4e2a\u76f8\u5f53\u5bbd\u7684\u8303\u56f4\uff0c\u67e5\u8be2\u8d44\u6599\u5c31\u6210\u4e86\u529b\u6c14\u6d3b\uff0c\u9664\u4e86\u5085\u4fca\uff0c\u8fd8\u4ece\u6a61\u6811\u56ed\u4e34\u65f6\u8c03\u4e86\u4e00\u540d\u79d8\u4e66\u8fc7\u6765\uff0c\u52a0\u4e0a\u9648\u5983\u84c9\u4eba\u5f53\u65f6\u5728\u56fe\u4e66\u9986\u91cc\u5e2e\u5fd9\uff0c\u56db\u4e2a\u4eba\u5fd9\u4e86\u534a\u5929\uff0c\u590d\u5370\u4e86\u8fd1\u767e\u4efd\u8d44\u6599\u3002\u6700\u540e\u7fdf\u4e39\u9752\u4e0e\u9648\u5983\u84c9\u4e24\u4eba\u6367\u56de\u6765\uff0c\u4e24\u4eba\u5404\u6367\u539a\u539a\u7684\u4e00\u53e0\uff0c\u7d2f\u5f97\u6c14\u5598\u5401\u5401\uff0c\u8fdb\u623f\u95e8\u9047\u5230\u5b59\u9759\u6aac\uff0c\u5b59\u9759\u6aac\u770b\u7740\u5979\u4eec\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u771f\u7ed9\u90a3\u5bb6\u4f19\u5f53\u79d8\u4e66\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u672c\u6765\u5c31\u662f\u8fd9\u4e2a\u547d\u3002\u201d\u7fdf\u4e39\u9752\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u8ba9\u5b59\u9759\u6aac\u5e2e\u5979\u6367\u4e00\u4f1a\u513f\u2026\u2026</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4ee5\u4e3a\u7fdf\u4e39\u9752\u4f4f\u8fdb\u6765\u662f\u7eaf\u7cb9\u8131\u5c97\u6765\u5c31\u8bfb\u4e1c\u5927\u7684\u5de5\u5546\u7ba1\u7406\u7855\u58eb\uff0c\u6ca1\u6709\u4eba\u544a\u8bc9\u5979\u7fdf\u4e39\u9752\u5c31\u662f\u8fc7\u6765\u7ed9\u5f20\u606a\u5f53\u52a9\u624b\u7684\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u8bf4\u9053\uff1a\u201c\u6211\u5728\u56fe\u4e66\u9986\u770b\u5230\u7fdf\u59d0\u90a3\u4e48\u8f9b\u82e6\uff0c\u624d\u642d\u4e00\u628a\u624b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\uff0c\u590d\u5370\u4e86\u8fd9\u4e48\u591a\u8d44\u6599\u56de\u6765\u2026\u2026\u201d\u5f20\u606a\u542c\u5230\u697c\u4e0b\u7684\u58f0\u97f3\uff0c\u5c31\u8d70\u51fa\u623f\u95f4\uff0c\u770b\u7740\u7fdf\u4e39\u9752\u8ddf\u9648\u5983\u84c9\u6367\u7740\u8d44\u6599\u8981\u4e0a\u697c\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u5c31\u653e\u697c\u4e0b\u7684\u684c\u4e0a\uff0c\u6211\u4e0b\u6765\u770b\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4e0d\u6e05\u695a\u5f20\u606a\u4e3a\u4ec0\u4e48\u7a81\u7136\u8981\u770b\u8fd9\u4e9b\u8d44\u6599\uff0c\u5c06\u8d44\u6599\u5806\u5728\u5ba2\u5385\u7684\u684c\u4e0a\uff0c\u770b\u7740\u5f20\u606a\u8d70\u4e0b\u697c\u68af\uff0c\u7591\u60d1\u7684\u8bf4\u9053\uff1a\u201c\u5927\u6982\u7684\u770b\u4e86\u770b\uff0c\u80fd\u67e5\u8be2\u5230\u7684\u516c\u5f00\u8d44\u6599\u5c31\u6709\u4e0a\u5343\u4efd\uff0c\u627e\u4e86\u8fd9\u4e9b\u63d0\u7eb2\u5951\u9886\u7684\u590d\u5370\u56de\u6765\uff0c\u8fd9\u4e5f\u4e0d\u5230\u5341\u5206\u4e4b\u4e00\uff0c\u4f60\u786e\u5b9a\u90fd\u8981\u4eb2\u81ea\u770b\u8fc7\u53bb\uff1f\u53e6\u5916\uff0c\u6211\u5df2\u7ecf\u5e2e\u4f60\u8054\u7cfb\u4e86\u91d1\u5c71\u77ff\u4e1a\u5927\u5b66\u4e0e\u6c5f\u5357\u77ff\u4e1a\u7814\u7a76\u6240\u5728\u7a00\u571f\u9886\u57df\u6700\u5177\u6743\u5a01\u7684\u4e24\u540d\u4e13\u5b9c\u2026\u2026\u82e5\u6709\u9700\u8981\u53ef\u4ee5\u968f\u65f6\u6d3e\u8f66\u5c06\u4ed6\u4eec\u63a5\u5230\u5efa\u90ba\u6765\uff0c\u9664\u4e86\u4ed6\u4eec\u4fe9\u4eba\u4e4b\u5916\uff0c\u91d1\u5c71\u77ff\u5927\u8fd8\u6709\u4e00\u4f4d\u7a00\u571f\u4e13\u5bb6\u4eca\u5929\u6b63\u5728\u4e1c\u5927\u8bbf\u95ee\uff0c\u6709\u9700\u8981\u7684\u8bdd\uff0c\u6211\u53ef\u4ee5\u53bb\u8054\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u539a\u539a\u4e00\u53e0\u8d44\u6599\u63a8\u5230\u7fdf\u4e39\u9752\u7684\u9762\u524d\uff1a\u201c\u8c01\u8bf4\u662f\u6211\u8981\u90fd\u770b\u8fc7\u53bb\u7684\uff1f\u4f60\u5148\u770b\uff0c\u505a\u4efd\u6458\u5f55\u7ed9\u6211\uff0c\u7136\u540e\u5199\u4e00\u4efd\u5173\u4e8e\u56fd\u5185\u7a00\u571f\u77ff\u4ea7\u5f00\u91c7\u3001\u52a0\u5de5\u4e0e\u5de5\u4e1a\u5e94\u7528\u7684\u73b0\u72b6\u4e0e\u672a\u6765\u53d1\u5c55\u8d8b\u52bf\u53ca\u5e94\u6ce8\u610f\u4e8b\u9879\u7684\u62a5\u544a\u7ed9\u6211\uff0c\u81f3\u4e8e\u8fd9\u4efd\u62a5\u544a\u662f\u4f60\u5199\u7684\uff0c\u8fd8\u662f\u77ff\u5927\u7684\u4e13\u5bb6\u5199\u7684\uff0c\u6211\u4e0d\u7ba1\uff0c\u4f46\u662f\u8981\u5feb\uff0c\u4e0d\u8981\u62d6\u6211\u7684\u65f6\u95f4\uff0c\u201d\u4ed6\u53ea\u4ece\u6700\u4e0a\u9762\u62ff\u4e86\u4e24\u4efd\u8584\u8584\u7684\u8d44\u6599\u5939\u5728\u814b\u4e0b\uff0c\u201c\u6211\u5148\u5403\u4e2d\u996d\u53bb\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u7fdf\u4e39\u9752\u770b\u7740\u5f20\u606a\u8d70\u51fa\u53bb\uff0c\u771f\u662f\u5934\u75bc\u554a\uff0c\u8ddf\u8bfb\u5de5\u5546\u7ba1\u7406\u7855\u58eb\u73ed\u7684\u7559\u5802\u4f5c\u4e1a\u4f3c\u7684\uff0c\u5149\u590d\u5370\u8fc7\u6765\u7684\u8d44\u6599\u8981\u901a\u8bfb\u4e00\u904d\uff0c\u66f4\u4e0d\u8981\u8bf4\u5199\u62a5\u544a\u4e86\uff0c\u6839\u672c\u5c31\u4e0d\u662f\u4e00\u4e24\u5929\u80fd\u5b8c\u6210\u7684\u4e8b\u60c5\uff0c\u8fd9\u5bb6\u4f19\u660e\u6446\u7740\u505a\u8d3c\u5fc3\u865a\u554a\uff01</p><p>\u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u54ea\u91cc\u5f97\u7f6a\u4ed6\u4e86\uff1f\u201d\u5b59\u9759\u6aac\u7591\u60d1\u7684\u770b\u7740\u7fdf\u4e39\u9752\uff0c\u201c\u600e\u4e48\u611f\u89c9\u4f60\u4eec\u4eca\u5929\u6709\u70b9\u4e0d\u5bf9\u52b2\u554a\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6ca1\u60f3\u5230\u5b59\u9759\u6aac\u80fd\u770b\u51fa\u5f02\u5e38\uff0c\u5fc3\u91cc\u4e00\u865a\uff0c\u611f\u89c9\u8138\u6709\u4e9b\u5fae\u70ed\uff0c\u773c\u795e\u95ea\u5230\u684c\u4e0a\u7684\u8d44\u6599\u4e0a\uff0c\u773c\u7738\u5b50\u8f6c\u56de\u6765\u5c31\u6062\u590d\u6b63\u5e38\uff0c\u53c8\u53f9\u4e86\u4e00\u53e3\u6c14\u5c06\u5c34\u5c2c\u63a9\u53bb\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8c01\u77e5\u9053\u554a\uff0c\u8bf4\u4e0d\u5b9a\u771f\u6709\u54ea\u91cc\u5f97\u7f6a\u8fd9\u5bb6\u4f19\u4e86\uff0c\u8c01\u8ba9\u6211\u5728\u4ed6\u624b\u4e0b\u6253\u5de5\u5462\uff1f\u6709\u6c14\u4e5f\u53ea\u80fd\u5fcd\u7740\uff0c\u4f60\u4eec\u5e2e\u6211\u5c06\u8d44\u6599\u642c\u56de\u6211\u5c4b\u5b50\u91cc\uff0c\u4e2d\u5348\u6211\u8bf7\u4f60\u4eec\u4fe9\u5403\u996d\u3002\u201d\u62c9\u7740\u5b59\u9759\u6aac\u3001\u9648\u5983\u84c9\u53c8\u4e00\u5e76\u5c06\u4e00\u5927\u53e0\u8d44\u6599\u6367\u5230\u4e5d\u697c\u53bb\uff0c\u5979\u5728\u9752\u5e74\u516c\u5bd3\u7684\u623f\u5b50\u5728\u8499\u4e50\u4ed6\u4eec\u697c\u4e0b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u7167\u60ef\u4f8b\u53bb\u6559\u5de5\u98df\u5802\u5403\u4e2d\u996d\uff0c\u90a3\u91cc\u7684\u996d\u83dc\u6bd4\u5b66\u751f\u98df\u5802\u53ef\u53e3\u591a\u4e86\uff0c\u5230\u6559\u5de5\u98df\u5802\u4e4b\u524d\uff0c\u8fd8\u7279\u610f\u5230\u4e09\u98df\u5802\u524d\u7684\u6d77\u62a5\u5899\u627e\u90a3\u5f20\u5468\u747e\u73ba\u4e0b\u5348\u505a\u4e13\u9898\u62a5\u544a\u7684\u6d77\u62a5\uff0c\u8ddf\u7535\u5b50\u5546\u52a1\u7684\u5173\u7cfb\u4e0d\u5927\uff0c\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u5468\u747e\u73ba\u4e00\u4eba\u7684\u8bb2\u5ea7\uff0c\u800c\u662f\u4e00\u4e2a\u5173\u4e8e\u65b0\u6750\u6599\u4ea7\u4e1a\u53d1\u5c55\u7684\u4e13\u9898\u8bb2\u5ea7\u3002</p><p>\u3000\u3000\u7a00\u571f\u6750\u6599\u662f\u65b0\u6750\u6599\u7684\u4e00\u4e2a\u91cd\u8981\u5206\u652f\uff0c\u5f20\u606a\u770b\u5230\u6d77\u62a5\u4e0a\u6709\u5c06\u7a00\u571f\u6750\u6599\u4f5c\u4e3a\u4e00\u4e2a\u5355\u72ec\u7684\u4e13\u9898\u5217\u51fa\u6765\uff0c\u53ef\u80fd\u4f1a\u5355\u72ec\u505a\u62a5\u544a\u3002</p><p>\u3000\u3000\u7535\u5b50\u53ca\u5fae\u7535\u5b50\u5de5\u4e1a\u7684\u65b0\u6750\u6599\u7814\u53d1\u662f\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u57fa\u7840\u7814\u7a76\u7684\u4e00\u4e2a\u91cd\u8981\u5206\u652f\uff0ci19\u624b\u673a\u7684\u84dd\u5c4f\u6280\u672f\u53ef\u4ee5\u8bf4\u662f\u6cf0\u82af\u5149\u7535\u6280\u672f\u7814\u53d1\u9662\u5728\u53d1\u5149\u65b0\u6750\u6599\u9886\u57df\u7684\u4e00\u9879\u91cd\u5927\u7a81\u7834\uff0c\u9526\u6e56\u65d7\u4e0b\u8fd8\u6709\u591a\u9879\u65b0\u6750\u6599\u57fa\u7840\u7814\u7a76\u4e0e\u7a00\u58eb\u91d1\u5c5e\u6709\u5173\u3002\u8c01\u4e5f\u65e0\u6cd5\u60f3\u8c61\uff0c\u5728\u672a\u6765\u7684\u5341\u5e74\u65f6\u95f4\u91cc\uff0c\u7531\u4e8e\u5931\u63a7\u7684\u5f00\u53d1\u4e0e\u591a\u5934\u51fa\u53e3\uff0c\u81f4\u4f7f\u56fd\u5185\u7684\u7a00\u571f\u77ff\u4ea7\u8d44\u6e90\u635f\u8017\u8fd1\u534a\uff0c\u4f7f\u56fd\u4eba\u6376\u80f8\u987f\u8db3\u4e3a\u4e4b\u627c\u8155\uff0c\u5f20\u606a\u4e5f\u56e0\u6b64\u5bf9\u56fd\u5185\u7a00\u58eb\u4ea7\u4e1a\u7565\u6709\u4e86\u89e3\u3002\u7531\u4e8e\u4e5d\u516b\u5e74\uff0c\u7edd\u5927\u591a\u6570\u7684\u8584\u819c\u6db2\u6676\u5c4f\u4e13\u5229\u6280\u672f\u8fd8\u5904\u4e8e\u65e5\u97e9\u7b49\u56fd\u4e25\u683c\u4fdd\u5bc6\u7684\u72b6\u6001\uff0c\u4f46\u662f\u62e5\u6709\u5341\u591a\u5e74\u540e\u8bb0\u5fc6\u7684\u5f20\u606a\u5374\u6e05\u695a\u7a00\u571f\u4e2d\u7684\u94df\u91d1\u5c5e\u662f\u8584\u819c\u6db2\u6676\u5c4f\u751f\u4ea7\u4e0d\u53ef\u6216\u7f3a\u7684\u539f\u6599\u2014\u2014\u56fd\u5185\u7684\u94df\u91d1\u5c5e\u8d44\u6e90\u5360\u636e\u7edd\u5bf9\u7684\u5784\u65ad\u5730\u4f4d\uff0c\u4ee4\u4eba\u75db\u60dc\u7684\u662f\u7eb7\u62e5\u800c\u4e0a\u7684\u65e0\u5e8f\u4e0e\u5931\u63a7\u5f00\u53d1\uff0c\u51e0\u4e4e\u4ee5\u6210\u672c\u4ef7\u5c06\u56fd\u5185\u7684\u94df\u91d1\u5c5e\u77ff\u5356\u7a7a\u7ed9\u65e5\u97e9\u7b49\u56fd\u3002</p></article>"}}}]);
